insert into bases(id, parent_id, created_at, updated_at, name)
values  (2, 1, null, null, '4. Шаҳар ва қишлоқ аҳоли пунктлари умумреспублика миқёсидаги жойлаштириш тизими ва унинг таркибига кирувчи вилоятлар, туманлар, қишлоқ хўжалик корхоналарининг жойлаштириш тизимлари, шунингдек минтақавий жойлаштириш тизимларининг таркибий қисмлари сифатида лойиҳалаштирилиши лозим. Бунда жойлаштириш тизимлари учун ягона бўлган ижтимоий, ишлаб чиқариш, муҳандислик-транспорт ва бошқа инфратузилмалар, шунингдек жойлаштириш тизимларининг маркази ёки кичик маркази вазифасини бажарувчи аҳоли пунктининг таъсир доирасидаги истиқболли ривожланаётган меҳнат, маданий-маиший ва рекрацион алоқалар шаклланишини ҳисобга олиш зарур.'),
        (3, 1, null, null, '5*. Турли ҳудудий даражалар учун жойлаштириш тизимлари маркази вазифасини бажарувчи-аҳоли пунктларини таъсир доиралари Ўзбекистон Республикаси ҳудудидаги жойлаштиришнинг Бош схемаси, Ўзбекистон Республикаси, Қорақалпоғистон Республикаси, вилоятлар ва туманлар (туманлар гуруҳи) ҳудудини режалаштириш схема ва лойиҳаларининг маълумотлари асосида қабул қилиниши лозим. Барча тизимлар учун аҳоли, ишлаб чиқариш ва табиий шароитлар ҳудудларни функционал макон сифатида ташкил этишни комплекс равишда бошқарадиган асосий ресурс омиллар сифатида хизмат қилишлари лозим.'),
        (4, 1, null, null, '5.1. Аҳоли пунктлари тизимининг шаклланиш хусусиятлари Республиканинг турли минтақаларида аҳоли жойлашуви ривожланишининг тарихан шаклланганлиги ва табиий шароитлари билан белгиланади: аҳолиси зич жойлашган ва яхши ривожланган урбанистик тузилмалар ҳамда аҳолининг кенг диапазонли жойлашувига эга, аввалдан ўзлаштирилганлиги ва аҳоли пунктлари тақсимланишининг нисбатан бир текисда бўлган ҳудудлари (Тошкент, Самарқанд, Бухоро вилоятлари, Фарғона водийси ва бошқ.); қулай табиий-иқлимий минтақалар ва коммуникацион каркаслар бўғинида жамланган майда аҳоли пунктларидан иборат Хоразм ва Қорақалпоғистондаги қадимдан суғорилиб келинувчи ҳудудлар; Фарғона водийсининг марказий қисми, Қарши, Жиззах чўллари ва Мирзачўлдаги қишлоқ ва шаҳар аҳоли пунктлари анча тарқоқ жойлашган янги суғориладиган ерлар; дарёларининг воҳаларида катта бўлмаган қишлоқ аҳоли пунктлари жойлашган тоғли ҳудудлар; кичик қишлоқ аҳоли пунктлари ва яйловларга эга бўлган дашт-чўл ҳудудлар'),
        (5, 1, null, null, '5.2. Қадимдан суғориладиган ҳудудлардаги лойиҳалаштириш ишларида қишлоқ агломерациялари, яъни бир нечта қишлоқ аҳоли ШНҚ 2.07.01-03 9-бет пунктларининг битта аҳоли пунктига қўшилиб кетиши бўйича шаклланган дастлабки шартлар ҳисобга олиниши зарур. Саноати ва шаҳарлари жадал ривожланган минтақаларда шаҳар агломерацияларининг шаклланиш тенденцияларини ҳисобга олиш лозим. Шаҳарлар таркибига қўшилиб кетган қишлоқларни ҳам киритиш зарур.'),
        (6, 1, null, null, '5.3. Жойлаштириш тизимини шакллантириш жойлаштиришнинг бош схемаси (ЖБС), ҳудудий ташкиллаштиришнинг комплекс бош схемаси (ҲТКБС) томонидан белгиланиши, бу тизимларни деталлаштириш эса - вилоят ва туманларни режалаштириш схемалари ва лойиҳаларида акс этиши лозим. Ушбу ишланмалар асосида шаҳар ва посёлкаларининг бош режалари ишлаб чиқилиши лозим. Шаҳар аҳоли пунктларининг бош режаси ишлаб чиқилишидан 5 йилдан ортиқ вақт илгари бажарилган муайян ҳудуднинг схемаси ва лойиҳаси мавжуд бўлган тақдирда, режалаштираётган ташкилотлар, вазирликлар ва идоралар, илмий тадқиқот ва лойиҳа институтларининг маълумотлари асосида унинг истиқболли ривожланишини аниқлаштириб олиш зарур.'),
        (7, 1, null, null, '5.4. Аҳоли пунктларини лойиҳалаштиришда уларнинг жойлаштириш тизимидаги бугунги кундаги ва келажакда тутадиган ўрнини ҳисобга олиш лозим. Ана шу тизим асосида мазкур аҳоли пунктларининг юксакроқ даражадаги жойлаштириш тизими марказлари ҳамда лойиҳалаштирилаётган аҳоли пунктларнинг таъсир зонасига кирадиган манзилгоҳ ва ҳудудлар билан ишлаб чиқариш, меҳнат, маданий-маиший ва рекрацион алоқаларининг ривожланиши белгиланиши зарур. Турли даражадаги жойлаштириш тизимлари марказлари вазифасини бажарадиган аҳоли пунктларининг таъсир зоналарининг ўлчамлари туманларни режалаштириш схемалари ва лойиҳалари бўйича ишланмалар асосида қабул қилиниши лозим.'),
        (1375, 136, '2024-05-29 17:30:50', '2024-05-29 17:30:50', '1. Ushbu Nizom O‘zbekiston Respublikasi Qurilish va uy-joy kommunal xo‘jaligi vazirligi (keyingi o‘rinlarda — Vazirlik)'),
        (57, 5, null, null, '29. Мавзелар ва даҳалар ҳудудига транспортда кириб бориш, мавзелар ҳудудида жойлашган турар жой уйлари гуруҳларига, хизмат кўрсатиш муассасалари ва корхона'),
        (8, 1, null, null, '5.5. Шаҳар ва қишлоқ аҳоли пунктларини режалаштириш лойиҳаларини ишлаб чиқишда уларнинг буйсунганлиги, маъмурий-хўжалик аҳамияти, жойлаштириш тизимларида тутган ўрни ва аҳамияти ҳисобга олиниши лозим: минтақавий марказ – Тошкент; вилоятлараро субминтақавий марказлар; ГСНМ маркази ҳисобланган вилоятлар марказлари; шаҳарлар, шаҳар ва қишлоқ посёлкалари – маҳаллий жойлаштириш тизимлари маркази ҳисобланган қишлоқ маъмурий туманларининг марказлари; шаҳарлар, шаҳар ва қишлоқ посёлкалари – дам олиш марказлари, саноат ва транспорт марказлари, шунингдек қишлоқ посёлкалари - жойлаштиришнинг маҳаллий тизимлари таркибига кирувчи ва жойлаштиришнинг дастлабки бўғи-ни ҳисобланувчи қишлоқ хўжалик корхоналарининг марказий посёлка-лари.'),
        (9, 1, null, null, '5.6. Шаҳарлар – ГСНМ марказларида марказ аҳолисидан ташқари бутун гуруҳ тизими аҳолисига ҳам хизмат кўрсатадиган йирик саноат корхоналари, транспорт объектлари ва ижтимоий-маданий-маиший муассасаларни жойлаштириш лозим. 10-бет. ШНҚ 2.07.01-03 Жойлаштиришнинг маҳаллий тизими марказида қишлоқ хўжалик маҳсулотлари қайта ишланишини таъминлайдиган ва мазкур туман, ГСНМ марказларида жойлашган корхоналар филиаллари, шунингдек жойлаштиришнинг маҳаллий тизими аҳолисига ижтимоий-маданий-маиший хизмат кўрсатувчи муассасалар эҳтиёжини қондиришга хизмат қиладиган саноат корхоналари жойлаштирилиши лозим. Қишлоқ хўжалик корхоналарининг марказий посёлкаларида ишлаб чиқариш-таъмирлаш ва хўжаликларнинг омбор объектларини ҳамда қишлоқ хўжалик корхонасининг бутун аҳолисига мавсумий хизмат кўрсатадиган муассасаларни жойлаштириш лозим, бу ерда шунингдек, агросаноат комплекси корхоналари ва ГСНМ марказлари ҳамда маҳаллий жойлаштириш тизимларида жойлашган корхоналарнинг филиаллари ҳам жойлашиши мумкин.'),
        (10, 1, null, null, '5.7. Маҳаллий жойлаштириш тизимлари учун марказ билан ижтимоий-маданий-маиший алоқа боғлашни 1,5 соатлик, ГСНМ марказлари, вилоятлараро ва минтақавий марказлар учун эса - 2 соатлик қатнаш масофасида ўрнатиш тавсия этилади. Қатналадиган меҳнат миграцион алоқалар: маҳаллий тизимлар учун 45 дақиқадан, ГСНМ учун 60 дақиқадан ошмаслигини ҳисобга олган ҳолда ташкил қилиниши лозим.'),
        (11, 1, null, null, '5.8. Суғориладиган минтақада аҳоли пунктлари тармоғининг ўта зич бўлган шароитида аҳоли сонини ҳисоблашда тизимларнинг марказларидаги бошқа аҳоли пунктлари билан ҳар кунги меҳнат алоқаларини ҳисобга олиш зарур. Жойлаштиришнинг маҳаллий тизимлари марказлари учун тахминий ҳисобкитоб қилишда ишга келаётганлар сонини шаҳар аҳолисини ташкил этадиган гуруҳ сонининг 5-15 % атрофида, ГСНМ марказлари учун эса 5-10 % миқдорида қабул қилиш тавсия этилади. Шаҳар, шаҳар посёлкасининг меъморий - режавий тузилмасини ҳал қилишда ёндош қишлоқ посёлкалари билан ўзаро алоқаларни ҳисобга олиш, қишлоқ аҳоли пунктлари тузилмасини ҳал қилишда эса уларнинг қишлоқ агломерациясига қўшилиб кетишини назарда тутиш зарур.'),
        (12, 1, null, null, '5.9. Манзилгоҳларнинг истиқболли ривожланиши қуйидаги вилоятлараро жойлаштириш тизимларини шакллантиришга асосланиши лозим: Тошкент (Тошкент, Сирдарё вилоятлари); Фарғона (Андижон, Наманган ва Фарғона вилоятлари); Бухоро (Бухоро ва Навоий вилоятлари); Самарқанд (Самарқанд ва Жиззах вилоятлари); Қашқадарё (Қашқадарё ва Сурхондарё вилоятлари); Қуйи Амударё (Қорақалпоғистон ва Хоразм вилояти). Вилоятлараро тизимларнинг марказларида (Тошкент, Фарғона, Бухоро, Самарқанд, Қарши ва Нукус шаҳарларида) лойиҳалаштириш ишлари бажаришда бутун минтақага хизмат кўрсатадиган илм-фан, маданият марказлари, олий ўқув юртлари, хўжалик ташкилотлари, акцеонерлик жамиятлари, трестлар, ШНҚ 2.07.01-03 11-бет банклар барпо этиш кўзда тутилиши лозим.'),
        (23, 1, null, null, '12*. Шаҳар ва қишлоқ аҳоли пунктлари режавий тузилмасини функционал зоналарнинг ихчам жойлашуви ва ўзаро алоқаларини, ҳудуднинг оқилона бўлиниши, ҳудуддан унинг шаҳарсозлик қимматлилигига боғлиқ ҳолда самарали фойдаланиш, меъморийшаҳарсозлик анъаналари, табиийиқлимий, ландшафт, миллиймаиший ва бошқа ҳудудий хусусиятларни биргаликда ҳисобга олиш, атроф муҳитни, маданий мерос объектларини ва фуқаро муҳофазаси талабларининг техник-муҳандислик тадбирларини таъминлаган ҳолда шакллантириш лозим.'),
        (124, 13, null, null, '75.4. Жамоат транспортида (транспортни кутиш вақтини ҳисобламаганда) шаҳар ва туман паркларига етиб олиш вақти 20 дақиқадан ошмаслиги зарур.'),
        (13, 1, null, null, '6*. Жойлаштириш тизимлари марказлари бўлган ўта йирик, йирик ва катта шаҳарлар ҳудудини ривожлантириш ва қурилишини режалаштириш лойиҳаларини шаҳар билан ягона хўжалик-иқтисодий, муҳандислик-техник, табиий-экологик ва шаҳарсозлик-режалаштириш мажмуасини ҳосил қилувчи шаҳар атрофлари ҳудуди билан биргаликда кўриб чиқиш керак. Ўта йирик, йирик ва катта шаҳарларга ёндош ҳудудлар қишлоқ хўжалик ер-мулклари, ландшафтлирекрацион зоналар, кичик аҳоли пунктлари ва бошқа объектларни ўз ичига олувчи кўкаламзорлаштирилган минтақа сифатида режалаштирилиши керак. Шу билан бирга, мос тарзда, меҳнат захира ларини ҳисоблаш ва шаҳар кичик тизимларининг ривожланиш кўрсаткичларини аниқлаш марказ-шаҳар атрофидаги аҳоли пунктлар билан ҳар кунги меҳнат ва маданий-маиший алоқаларни ҳисобга олинган ҳолда, 16-иловага мувофиқ меҳнат ресурслари баланси асосида амалга оширилиши керак.'),
        (14, 1, null, null, '7. Аҳоли пунктлари ҳудудининг ривожланиши ва қурилишини режалаштириш лойиҳаларини меъёрланувчи ривожланиш кўрсаткичларини танлашни аниқлашга имкон берувчи лойиҳадан аввалги илмий-тадқиқот ишлари асосида амалга ошириш мақсадга мувофиқ бўлади'),
        (15, 1, null, null, '8*. Шаҳар ва қишлоқ аҳоли пунктлари ҳисобланган муддат учун аҳолининг лойиҳавий сонига боғлиқ равишда 1-жадвалга мувофиқ гуруҳларга бўлинади, бунда бош режалар ишлаб чиқиш бўйича қурилишнинг биринчи навбати 5-7 йил, ҳисобий муддати эса - 20-25 йил қилиб белгиланади.'),
        (16, 1, null, null, '9.* Аҳолининг лойиҳавий муддат учун ҳисобланган сони аҳоли пунктининг истиқболли ривожланиши тўғрисидаги маълумотлар асосида, аҳолининг табиий ва механик тарзда ортиши бўйича демографик прогноз ва маятникли 12-бет. ШНҚ 2.07.01-03 миграцияни ҳисобга олинган ҳолда аниқланиши лозим'),
        (17, 1, null, null, '6. УЙ-ЖОЙ ҚУРИЛИШИНИ РИВОЖЛАНТИРИШ 6.1. Замонавий бозор муносабатлари шароитида қурилишнинг ҳар хил турларини: қўрғонлар, кам қаватли, зич жойлаштирилган, текис ҳудудларда ва мураккаб рельефли участкаларда қуриладиган уй-жой тузилмаларини қўллаш лозим, бунда уй-жой қурилиши қулай уй-жой муҳитини шакллантиришнинг кўп турдаги талабларига (шаҳарсозлик, демографик, табиий-иқлимий, эстетик) жавоб бериши керак.'),
        (18, 1, null, null, '7. ХИЗМАТ КЎРСАТИШ МУАССАСАЛАРИ ВА КОРХОНАЛАРИ ТАРМОҒИНИ ТАШКИЛ ҚИЛИШ 7.1. Ижтимоий ва маданий-маиший хизмат кўрсатиш муассасалари ва корхоналари тармоғини ташкил этилишини ижтимоий хизмат кўрсатишнинг қарор топган тизимларига мувофиқ тарзда, яшаш жойида ижтимоий хизмат кўрсатиш билан таъминланганлик даражасини ҳисобга олиб лойиҳалаштириш зарур.'),
        (19, 1, null, null, '8. ХЎЖАЛИК ИЧИДАГИ ТРАНСПОРТ АЛОҚАЛАРИ 8.1. қишлоқ хўжалик корхонаси ичидаги транспорт алоқаларини жойлаштиришнинг умумий тизимида энг кам вақт ва иқтисодий харажатлар сарфлаб юкларни ва йўловчиларни ташишни таъминлайдиган ягона тизим сифатида лойиҳалаштириш керак бўлади'),
        (20, 1, null, null, '9. МУҲАНДИСЛИК УСКУНАЛАРИ 9.1. Аҳоли сони, иқлим, геологик ва бошқа шарт-шароитлардан қатъи назар ҳамма посёлкаларда зарур санитария-гигиена шарт-шароитларини, аҳолининг меҳнат қилиши ва дам олиши учун юксак даражадаги қулайликларни таъминлайдиган муҳандислик ускуналари ва ободонлаштиришни назарда тутиш лозим.'),
        (21, 1, null, null, '10*. Шаҳар ва қишлоқ аҳоли пунктлари ривожлантирилиши учун ҳудуд меъморий-режавий ечимларнинг техник-иқтисодий, санитариягигиеник кўрсаткичлар, ёқилғиэнергетик, сув, ҳудудий захира лар, атроф муҳит ҳолатининг вариантларини таққослаш асосида, табиий ва бошқа шароитларнинг келажак-даги ўзгариши бўйича прогнозларни, ушбу ҳудудлардан оқилона функционал фойдаланиш имкониятини ҳисобга олган ҳолда танланиши лозим. Янги шаҳар ва бошқа аҳоли пунктларини қуриш, мавжудларини реконструкция қилиш учун ҳудудларни танлаш аҳолининг ишлаш, яшаш ва дам олиш жойларини оқилона жойлаштириш имкониятларини ҳисобга олган ҳолда, ҳудудларнинг табиий ва бошқа шароитларини ўрганиш ва таҳлил қилиш, вариантларнинг техник иқтисодий, санитария-гигиеник ва меъморий-режавий кўрсаткичларини таққослаш асосида амалга ошириш зарур. Янгидан қурилаётган саноат корхоналари меҳнаткашларини жойлаштириш учун ҳудуд танлаш ушбу корхоналар учун ҳудуд танлаш билан бир вақтда амалга ошириш зарур. Янгидан қурилаётган шаҳар ва бошқа аҳоли пунктлари учун, ҳамда мавжудларини реконструкция қилиш учун ҳудудларни қишлоқ хўжалик мақсадида фойдаланил-майдиган ёки қишлоқ хўжалиги учун яроқсиз бўлган ерлардан ёки қишлоқ хўжалиги учун сифати ёмон бўлган, фойдаланилган ҳолларда эса ўзлаштириш учун махсус муҳандислик тадбирларини ўтказиш талаб этадиган ерлардан танлаш лозим. Шаҳар ва бошқа аҳоли пункти учун белгиланган чегараларда жойлашган, аммо қурилиш объектлари мавжуд бўлмаган ерлар биринчи навбатда ўзлаштирилиши лозим. Суғориладиган ва захи қочирилган ерлар ва шудгорларда, кўп йиллик мевали дарахтлари ва узумзорлари бор бўлган ер майдонларида, шунингдек сувни муҳофазаловчи, ҳимоя мақсадларида ўтқазилган ва бошқа турдаги ўрмонлар билан банд ерларда янги шаҳарларни ва бошқа аҳоли пунктлари, саноат корхоналари, иншоотлар, бинолар ва коммуникацияларни жойлаштириш, мавжудларини реконструкция қилишга фақат истисно тариқасида, кўрсатилган ерларнинг Ер кодексига мувофиқ истеъмолдан чиқарилиши фақат Ўзбекистон Республикаси Олий Мажлиси қарори бўйича амалга оширилишини ҳисобга олган ҳолда, рухсат этилади. Шаҳарлар ва бошқа аҳоли пунктлари ҳудудларида қуриладиган, саноат корхоналари, бинолар ва иншоотларнинг майдон ўлчамлари минимал зарур катталикда, иморатлар қурилишининг зичлик меъёрларига риоя қилинган ҳолда қабул қилиниши керак. Фойдали қазилмалари мавжуд майдонларда иморат қуриш учун ер участкаларини ажратиб берилиши давлат тоғ-кон назорат органлари билан келишилган ҳолда амалга оширилади. ШНҚ 2.07.01-03 13-бет Бинолар, иншоотлар ва коммуникацияларни жойлаштириш қуйидаги ҳолларда рухсат этилмайди: - шахталар ва фойдали қазилмаларни бойитиш комбинатларининг тоғ жинслари ағдармалари мавжуд бўлган хавфли зоналарида; - ўпирилишлар, қор кўчкилари ва сел оқимлари, сув омборларидан фавқулодда сув чиқарилиши ва сув босиш хавфи мавжуд бўлган зоналарда; - сув таъминоти манбалари санитария муҳофазаси доираларининг биринчи зонасида; - лойиҳаланаётган объектлар курортлардаги даволаш воситаларини ишлатиш билан бевосита боғлиқ бўлмаган ҳолларда, курортлар санитария муҳофазаси доираси зонасида; - ҳимоя ва санитария-гигиеник функцияларни бажарувчи ва аҳоли дам олувчи масканлар ҳисобланган ўрмонлар, ўрмонли парклар ва бошқа дарахтзорлар эгаллаган шаҳарларнинг яшил зоналари ерларида; - санитария-гигиеник хизматлар кўрсатувчи ташкилотлар белгилаган муддатлари ўтмагунга қадар органик ва радиактив чиқиндилар билан ифлосланган ерларда; - табиий мажмуаларга талофат етказиши ёки илмий ва маданий қийматга эга бўлган табиий объектлар сақланишига хавфи мавжуд бўлган ҳолларда қўриқхоналар ва уларнинг муҳофаза қилинувчи зоналарида бино, иншоотлар ҳамда коммуникацияларни жойлаштириш тақиқланади. - ёдгорликларни сақлаш бўйича тегишли ташкилотларнинг рухсатисиз бинолар, иншоотлар ва коммуникацияларни тарихий ва маданий мерос ёдгорликларини ҳимоя қилиш зоналарида жойлаштириш тақиқланади; - кўчмас тарихий ва маданий ёдгорликларни бузиш, кўчириш, ўзгартириш тақиқланади. Фақат алоҳида ҳоллардагина Ўзбекистон Республикаси Вазирлар Маҳкамаси рухсати билан бу қоидага ўзгартириш киритилиши мумкин.'),
        (22, 1, null, null, '11*. Ҳудудлардан функционал фойдаланиш устиворлигини ҳисобга олиб, аҳоли пунктининг ҳудуди турар-жой, ишлаб чиқариш ва ландшафт-рекреация қисмларга бўлинади. Турар-жой ҳудуди турар жой фонди, жамоат бино ва иншоотлари, шу жумладан илмий-тадқиқот институтлари ва уларнинг мажмуалари, кўчалар, майдонлар, парклар ва бошқа умумий фойдаланиш жойлари, шунингдек, санитарияиҳота зоналарини яратишни талаб этмайдиган баъзи коммунал ва саноат объектларини жойлаштириш учун мўлжалланган. Ишлаб чиқариш ҳудуди саноат корхоналари ва улар билан боғлиқ бўлган илмий муассаса-ларнинг тажриба ишлаб чиқариш объектлари билан биргаликдаги мажмуалари, коммунал-омбор объектлари, ташқи транспорт иншоотлари, шаҳардан ташқари ва шаҳар атрофи автомобиль йўлларини, муҳандислик иншоот-лари ва тармоқларини, шунингдек, ишчиларга хизмат кўрсатиш муассасалари ва корхоналарини жойлаштириш учун мўлжалланган. Қишлоқ аҳоли пунктларидаги ишлаб чиқариш ҳудуди қишлоқ 14-бет. ШНҚ 2.07.01-03 хўжалик машиналарини таъмирлаш устахоналари ва гаражлари, уруғ ва ўғит омборлари, қишлоқ хўжалик маҳсулотлари ва чорва емлари омборлари, чорвачилик фермалари ҳамда қишлоқ хўжалик маҳсулотларини қайта ишлаш ва сақлашга мўлжалланган объектларни жойлаштириш учун мўлжалланган. Ландшафт – рекреация ҳудуди кўкаламзорлаштирилган ва сув билан таъминланган очиқ умумий фойдаланиш майдонлари тизимидан, шунингдек, кўчатзор ва санитарияиҳота зоналаридан иборат бўлади. Мазкур ҳудудлар чегарасида турли функционал мақсадли зоналар ажратилади: турар-жой қурилиши, жамоатчилик марказлари, саноат ва коммунал-омбор, илмий ва илмийишлаб чиқариш, ташқи транспорт, оммавий дам олиш ва курорт зоналари, муҳофаза этиладиган ландшафтлар ва бошқалар.'),
        (24, 1, null, null, '13*. Ҳар бир қишлоқ хўжалиги корхонаси ва қишлоқ фуқаролари йиғини ҳудудининг режалаштириш ва ривожлантириш лойиҳаси ШНҚ 2.07.04-06 “Қишлоқ хўжалиги корхоналари ҳудудларини меъморийрежавий ташкиллаштириш” меърий ҳужжатига мувофиқ ишлаб чиқилиши лозим.'),
        (149, 14, null, null, '88*. Бульварларни (сайр кўчаларини), одатда, асосий пиёда ҳаракати йўналишларида барпо этиш кўзда тутилиши керак'),
        (25, 3, null, null, '14*. Шаҳар аҳоли пунктларининг турар-жой ҳудудини режалаштириш тузилмасини жамоат марказлари, турар-жой бинолари  қурилиши, кўча-йўл тармоғи, кўкаламзорлаштирилган умумий фойдаланиш ҳудудлари, меҳнат қилиш жойлари зоналарининг ўзаро боғлиқ жойлашувини ҳисобга олган  ҳолда, шунингдек аҳоли пунктининг  катталиги ва ҳудудининг табиий  хусусиятларига асосланган ҳолда, унинг умумий режавий тузилмасига  боғлаб шакллантириш керак. Аҳоли истиқомат қилувчи  турар-жой ҳудудига бўлган эҳтиёжни олдиндан аниқлаш учун 1000 кишига  мўлжалланган йирик-лаштирилган  кўрсаткичларни қабул қилиш керак:  турар-жой бинолари қурилишининг  ўртача қаватлилиги 3 қаватгача  бўлган шаҳарларда - ер  участкаларисиз иморат қуриш учун  10-15 га ва ер участкалари билан иморат қуриш учун 20-25 га; 4 дан 9  қаватгача – 8 га.  Фавқулодда табиий-иқлимий  шароитларга эга бўлган ҳудудлар  (чўл, тоғли жойлар) учун мазкур  кўрсаткичлар 30 % гача ўзгартирилиши мумкин.'),
        (26, 3, null, null, '15*. Шаҳар аҳоли пунктларида аҳоли истиқомат қилувчи турар-жой ҳудудларининг қуйидаги тузил'),
        (28, 4, null, null, '16. Ўта йирик, йирик ва катта шаҳарлар жамоат марказлари тизими таркибига умумшаҳар маркази, режалаштириш туманлари-нинг марказлари, турар-жой туман'),
        (29, 4, null, null, '17*. Бўлакланган турар-жой ҳудудлари тузилмасига эга бўлган йирик ва катта шаҳарларда умум'),
        (30, 4, null, null, '18. Аҳолига хизмат кўрсатиш марказлари тизимини шакллан'),
        (31, 4, null, null, '19*. Қурилишнинг 1-навбати учун жамоат марказлари участка'),
        (32, 4, null, null, '19.1. Ижтимоий-амалий объект'),
        (33, 4, null, null, '19.2. Кўп функцияли ижтимоий-амалий бинолари мавжуд зоналарда ушбу функцияларни амалга оширишга тўсқинлик қилмайдиган ҳажмда кўп хонадонли турар-жой иморатларини жойлаштиришга ҳам рухсат этилади. Изоҳлар: 1. Ижтимоий-амалий бино'),
        (34, 4, null, null, '19.3. Ҳовлили турар-жой бино'),
        (35, 5, null, null, '20*. Истиқомат қилиш муҳити деганида, чегараси доирасида инсон'),
        (36, 5, null, null, '20.1. Турар-жой қурилиши зо'),
        (37, 5, null, null, '20.2. Турар-жой ҳудудларининг ўлчамларини аниқлашда турар-жой дастурларининг босқичлар бўйича амалга оширилишидан келиб чиқиш зарур. Турар-жой фонди ва унинг тузилмаси аҳолининг оилавий таркиби, унинг даромад даражаси, ҳар бир оилани алоҳида хонадон ёки уй билан таъминлаш заруриятидан келиб чиққан ҳолда мавжуд ва истиқболдаги уй-жой билан таъмин'),
        (38, 5, null, null, '20.3. Турар-жой зоналарининг ҳудудлари турар-жой тузилмала'),
        (39, 5, null, null, '20.4. Турар-жой бинолари қурилган зоналарни режавий таш'),
        (40, 5, null, null, '20.5. Турар-жой бинолари қурилишининг ҳисобий кўрсат'),
        (41, 5, null, null, '20.6. Шахсий турар-жой бино'),
        (42, 5, null, null, '20.7. Турар жой биноларини реконструкция қилишда, одатда, мавжуд капитал турар-жой ва жа'),
        (43, 5, null, null, '20.8. Кўп хонадонли бинолар'),
        (1376, 136, '2024-05-29 17:30:50', '2024-05-29 17:30:50', '2. Vazirlik O‘zbekiston Respublikasida qurilish va uy-joy kommunal xo‘jaligi sohasida yagona davlat siyosatini amalga oshiradigan maxsus vakolatli respublika ijro etuvchi hokimiyat organi hisoblanadi.'),
        (46, 5, null, null, 'бўлмаган; яшаш хоналарида деразалари  бўлган ушбу биноларнинг ён  томонлари учун - 20-25 м дан кам  бўлмаган. Изоҳ. Реконструкция ва бошқа мураккаб шаҳарсозлик шароитларда  кўрсатилган масофалар инсоляция, ёри'),
        (48, 5, null, null, '21.3. Шаҳарларнинг шахсий турар-жой бинолари жойлашган туманларида турар-жой зоналари чегарасида жойлаштириладиган омборхона гуруҳларининг ҳар қайсиси 30 блокдан ортиқ бўлмас'),
        (49, 5, null, null, '21.4. Кўп хонадонли бинолари мавжуд турар-жой зоналарида мавзенинг кўкаламлаштирилганган ҳудудлари майдонлари (мактаб ва мактабгача болалар муассасалари участкаларини ҳисобламаганда), одатда, мавзе ҳудуди майдонининг 25 фоизидан кам бўлмаслиги зарур. Кийим-кечак қуритиш майдон'),
        (50, 5, null, null, '22. Баланд қаватли турар-жой биноларининг (9-12 қаватли) қури'),
        (51, 5, null, null, '23*. Турар-жой туманлари ва мавзелар ҳудудида турар-жой фондининг ҳисобий зичлиги 3- ва 4- жадвалларга мувофиқ қабул қилиниши лозим. Ҳудудларнинг экологик аҳво'),
        (52, 5, null, null, '24*. Олдиндан таркиб топган капитал турар-жой қурилиши устивор бўлган туманларни қайта қуришда таянч қурилманинг бузи'),
        (53, 5, null, null, '25*. Турар жой қурилишини шакллантиришда ижтимоий хизмат кўрсатиш объектлари мажмуаси билан бирлаштирилувчи турар-жой бирликлари - маҳаллаларни ажра'),
        (54, 5, null, null, '26*. Турар жой ва жамоат бино'),
        (58, 5, null, null, '30. Магистраллараро ҳудуд (мавзе)ни лойиҳалашда турли мақсадлардаги умумий фойдаланиш майдончаларини жойлаштиришни кўзда тутиш лозим, уларнинг ўлчамлари ва улардан турар жой ва жамоат биноларигача бўлган масофалар 5-жадвалга кўра қабул қилиниши керак.'),
        (59, 6, null, null, '31. Аҳоли жойлашган ҳудуднинг катталиги, аҳоли сони, оиланинг ўртача катталиги, томорқа участкаларининг майдони, қишлоқ аҳоли пунктининг режавий тузилмаси катталиги ва хусусиятлари, ландшафт – иқлим шароитлари турига боғлиқ ҳолда қабул қилиниши лозим.
'),
        (60, 7, null, null, '32. Қишлоқ аҳоли пунктлари жамоат марказларининг тизимини 28-бет. ШНҚ 2.07.01-03 туманларни режалаштириш схемалари ва лойиҳалари ҳамда қишлоқ хўжалиги корхоналари ҳудудининг меъморий-режавий ташкиллаштирилиши схемалари билан уйғунлаштирган ҳолда, ШНҚ 2.07.04-06 “Қишлоқ хўжалиги корхоналари ҳудудларини меъморий режалаштириш ташкил қилиш” га мувофиқ, қуйидаги таркибда ишлаб чиқиш лозим: қишлоқ аҳоли пунктининг жамоат маркази; маҳалла марказлари. Қишлоқ жойлаштириш тизими марказлари ролини бажарадиган қишлоқ аҳоли пунктларидаги жамоат маркази аҳоли пунктлараро хизмат кўрсатиш маркази вазифа-сини ҳам бажаради'),
        (61, 7, null, null, '33. Жамоат марказларининг тузилиши ва таркиби қишлоқ аҳоли пунктининг катталиги, унинг хизмат кўрсатиш тизимидаги роли, шунингдек, қишлоқ аҳоли пунктиннинг режавий тузилмаси хусусиятларига мувофиқ аниқланиши лозим. Хизмат кўрсатиш объектларининг ҳисоб кўрсаткичлари VII бўлимга мувофиқ қабул қилиниши лозим'),
        (62, 7, null, null, '34*. Узунчоқ ёки бўлинган шаклга эга қишлоқ аҳоли пунктларида, ҳисоб-китобларга мувофиқ, қўшимча равишда хизмат кўрсатиш кичик марказларини кўзда тутиш мумкин. Йириклаштирилган кўрсаткичлар учун жамоат марказининг ҳудудий катталиги (парк ва текис сиртдаги спорт иншоотларисиз) киши бошига 8-12 м 2 ҳисобидан аниқланиши керак (каттароқ кўрсаткичлар - кичик аҳоли пунктлари учун)'),
        (63, 7, null, null, '35*. Жамоат марказининг режавий ҳал этилиши тежамкорлигини ошириш учун марказнинг қурилмаларида биноларнинг йириклаштирилган гуруҳли турларидан фойдаланиш лозим. Ногиронлар ва аҳолининг кам ҳаракатланувчи гуруҳларининг турар-жой ҳудудида бўлиши учун қулай шароитларни яратишда ШНҚ 2.07.02-07 “Инсонларнинг ҳаёти ва фаолияти муҳитини ногиронлар эҳтиёжлари ва аҳолининг кам ҳаракатланувчи гуруҳларини ҳисобга олган ҳолда лойиҳалаш” талабларини амал қилиш лозим.'),
        (64, 8, null, null, '36*. Турар-жой ҳудудининг асосий таркибий қисмлари сифатида қуйидагилар қабул қилиниши керак: ўлчами Ўзбекистон Республикаси Ер кодексига асосланган лойиҳа топшириғи билан аниқланадиган ҳовлили уй; бир неча турар-жой гуруҳларидан таркиб топган, “маҳалла” кўринишида шаклланадиган турар-жой мажмуаси. Қишлоқ аҳоли пунктлари турар жой қурилиши қисмларининг таркиби, ўлчамлари ва меъморийрежавий ташкил этилиши оилаларнинг ўзаро қўшничилик мулоқотига оид функционал-маиший жараёнлар билан белгилаб берилади. Ногиронлар ва аҳолининг кам ҳаракатланувчи гуруҳларининг истиқомати учун қулай шароитларни таъминловчи элементлар ШНҚ 2.07.02-07 “Инсонларнинг ҳаёти ва фаолияти муҳитини ногиронлар эҳтиёжлари ва аҳоли-- нинг кам ҳаракатланувчи гуруҳ- ШНҚ 2.07.01-03 29-бет ларини ҳисобга олган ҳолда лойиҳалаш”га мувофиқ аниқланади'),
        (65, 8, null, null, '37. Турар-жой мажмуаларининг жамоатчилик ҳудудлари катталигини 7-жадвал бўйича қабул қилиш лозим'),
        (66, 8, null, null, '38. Қишлоқ аҳоли пунктининг аҳоли жойлашган ҳудудини бир суткада 20 мингдан ортиқ автомобиль қатновчи I-тоифали автомобиль йўллари кесиб ўтмаслиги, шунингдек, II ва III тоифали йўллар ҳамда қишлоқ хўжалиги машиналари транзит ўтиши ва мол ҳайдаб ўтиш учун мўлжалланган йўллар ҳам кесиб ўтмаслиги лозим'),
        (67, 8, null, null, '39. Қишлоқ аҳоли пунктларининг қурилиши учун асосан ҳовли туридаги кам қаватли 1-2 хонадонли турар жой уйлари кўзда тутилиши лозим'),
        (68, 8, null, null, '40*. Томорқали участканинг режавий тузилиши оила ривожланиши ва унинг тузилиши ўзгаришларини ҳисобга олиб, турар жой уйлари ва хўжалик биноларининг келажакдаги ўсиш имкониятини таъминлаши лозим. Ҳайвонлар ва паррандалар учун мўлжалланган қурилмалардан турар-жойнинг истиқомат хоналари ойналаригача масофа 15 м дан кам бўлмаслиги лозим. Ушбу оралиқ санитария-гигиена талабларига боғлиқ равишда камайтирилиши мумкин.'),
        (1574, 119, '2024-07-03 11:16:15', '2024-07-03 11:16:15', '16.1. So‘rovnomada noto‘g‘ri ma’lumotlar ko‘rsatilgan'),
        (69, 8, null, null, '41. Шаҳар аҳоли пунктларининг ишлаб чиқариш ҳудудлари саноат корхоналарини умумий ишлаб чиқариш инфратузилмаси ва ёрдамчи ишлаб чиқаришлар асосида бирлаштирадиган ишлаб чиқариш зоналари кўринишида шакллантириш лозим. Ишлаб чиқариш зоналари ишлаб чиқариш-технологик, транспорт, санитария-гигиеник ва функционал талабларга мувофиқ ҳолда меҳнаткашларнинг умумий сони 30 минг киши бўлганда шакллантирилиши зарур. Меҳнаткашларнинг кўпроқ сони учун тегишли асослар бўлганда йўл қўйилиши мумкин.'),
        (70, 8, null, null, '42*. Саноат зоналарининг режалаштирилиши ва қурилиши лойиҳалари ва схемаларида тегишли ишлаб чиқариш соҳаларининг истиқболли ривожланиши бўйича прогнозларни ҳисобга олган ҳолда уларнинг ривожланиши навбатлилигини кўзда тутиш керак'),
        (71, 8, null, null, '42.1. Ишлаб чиқариш қурилиши функционал зоналари, муҳандислик ва транспорт инфратузилмалари таркибига қуйидагиларни киритиш мумкин: коммунал зоналар - коммунал ва омборхона объектлари, уй-жойкоммунал хўжалиги объектлари, 30-бет. ШНҚ 2.07.01-03 транспорт объектлари, улгуржи савдо объектлари жойлашган зоналар; саноат зоналари - атрофмуҳитга таъсир меъёрлари ҳар хил бўлган ишлаб чиқариш объектлари, шунингдек темир йўлнинг кириш йўллари жойлашган ва одатда, эни 50 метрдан катта бўлган санитарияҳимоя зоналари ташкил этилишини талаб қиладиган зоналар; ишлаб чиқаришнинг бошқа турларига оид, муҳандислик ва транспорт инфратузилмалари (илмий-ишлаб чиқариш зоналари, технопарклар). Ишлаб чиқариш зоналарида ушбу зонада жойлашган корхоналарга хизмат кўрсатувчи аварияқутқарув хизмати объектларининг бинолари ва иншоотлари, зона ҳудудида жойлашган объектлар фаолиятини таъминлаш билан боғлиқ бўлган муҳандислик ва транспорт инфратузилмалари объектлари, ишлаб чиқаришда банд бўлган аҳолига хизмат кўрсатувчи объектларни ва бошқа турдаги объектларни жойлаштиришга рухсат этилади. Изоҳлар: 1. Ишлаб чиқариш зоналари ҳудудида корхоналар ва бошқа объектларни жойлаштириш ва қайта тиклашда улардан фойдаланиш жараёни хавфсизлигини таъминловчи чораларни назарда тутиш, шунингдек, корхоналарнинг биронтасида авария содир бўлган ҳолда теварак-атрофдаги туташган жойлардаги аҳолини хавфли таъсиридан ҳимоя қилишни ва бошқа корхоналарнинг ишлаш хавфсизлигини таъминлаш бўйича чораларни назарда тутиш лозим. Ишлаб чиқариш ва бошқа объектларнинг хавфлилик даражаси техник регламентларга мувофиқ қонунларда ўрнатилган тартиб бўйича аниқланади. 2. Тарихий ва маданий ёдгорликлари ҳисобланувчи ва ишлаб чиқариш корхоналарини реконструкция қилишда тарихий меросни сақлашга қаратилган чораларни кўзда тутмоқ зарур.'),
        (72, 8, null, null, '42.2. Ишлаб чиқариш бино ва иншоотлари мавжуд функционал зоналар ва корхоналарнинг санитария-ҳимоя зоналари чегараларида ишлаб чиқариш билан боғлиқ бўлмаган турар-жой уйлари, меҳмонхоналар, ётоқхоналар, боғдала ҳовли қурилмаларини, мактабгача ва умумтаълим муассасалари, спорт иншоотлари, бошқа жамоат биноларини жойлаштиришга йўл қўйилмайди. Турар-жой ва саноат зоналарини, турар-жой ҳудудлари ва саноат корхоналарини навбатманавбат жойлаширишига ҳам йўл қўйилмайди. Санитария-ҳимоя зоналари рекреация мақсадлари ва қишлоқ хўжалик маҳсулотларини ишлаб чиқариш учун фойдаланилмаслиги лозим. Изоҳ. Корхоналарнинг санитарияҳимоя зоналари участкалари корхоналар ҳудуди таркибига қўшилмайди ва шу зоналар ҳудудида қурилишига рухсат этилган объектларни жойлаштиришга йўл қўйилади. Корхоналарга туташ жойлардаги ифлосланган ҳудудларнинг атроф-муҳити муҳофазаси билан боғлиқ бўлган соғломлаштириш, санитария-гигиеник, ҳамда қурилиш ишларига оид бошқа тадбирлар, шунингдек санитария-ҳимоя зонасининг ободонлаштирилиши атроф-муҳитга зарарли ташланмалар (чиқиндилар) чиқарувчи корхоналар ҳисобидан амалга оширилади'),
        (73, 8, null, null, '42.3. Шаҳарларнинг ишлаб чиқариш зонаси таркибида саноат корхоналари, жумладан, ишлаб чиқаришнинг санитария таснифига боғлиқ ҳолда жойлашган тоғ-кон, кимё ва металлургия саноати ШНҚ 2.07.01-03 31-бет корхоналарини жойлаштириш учун мўлжалланган саноат зонаси, илмийишлаб чиқариш зонаси ва коммуналомборхона зоналари шаклланиши мумкин. Санитария-ҳимоя зонаси 100 метрдан кам бўлган озиқ-овқат, тиббий, фармацевтика ва саноатнинг бошқа соҳаларига мансуб корхоналарни зарарли ишлаб чиқариш ҳисобланувчи металлургия, кимё, нефть кимёси ва саноатнинг шунга ўхшаш тармоқлари ҳудудида, шунингдек санитария-ҳимоя зоналарида жойлаштирмаслик лозим'),
        (74, 8, null, null, '42.4. Саноат зоналарини функционал-режавий ташкил этиш мавзелар (қизил чизиқлар чегарасида) кўринишида бўлиб, уларнинг ичидаги асосий ва ёрдамчи ишлаб чиқариш корхоналари ўзларининг жойлашиши, юк обороти ва транспорт тури, шунингдек қурилиш навбатига қараб ҳамда санитариягигиеник ва ёнғинга қарши талабларни ҳисобга олган ҳолда жойлаштирилиши назарда тутилади. Тоғ-кон саноати (очиқ конлар, тўпланган тоғ жинслари, шлактўплагичлар ва ҳ.к.) билан боғлиқ ҳудудлар шаҳар (посёлка) ҳудудлари чегарасидан ташқарида, улардан санитария-ҳимоя зоналарини ва шамол йўналишини ҳисобга олган ҳолда жойлаштирилиши зарур. Изоҳ. Саноат корхонаси участкасининг меъёрий ўлчами унинг қурилмалари майдонининг амалдаги меъёрий зичлиги кўрсаткичи нисбатига тенг қилиб қабул қилинади'),
        (75, 8, null, null, '42.5. Корхоналар ва бошқа объектларни жойлаштиришда экологик меъёрлар ва қоидаларни, шунингдек ер ости сувларини муҳофаза қилиш тўғрисидаги низом талабларини ҳисобга олган ҳолда ерларнинг, ер усти ва ер ости сувларининг, ҳовузлар сув йиғгичлари юзаларининг, атмосфера ҳавосининг ифлосланишига йўл қўймаслик бўйича чораларни назарда тутиш лозим'),
        (76, 8, null, null, '42.6. Санитария-ҳимоя зоналарининг ўлчамлари корхоналар, иншоотлар ва бошқа объектларнинг санитария таснифини ҳисобга олган ҳолда белгиланади. Санитария-ҳимоя зонаси кенглигининг етарлилигини саноат корхоналари чиқиндилари таркибидаги зарарли моддаларнинг атмосферага тарқалиш ҳисоби билан, шунингдек ушбу меъёрларнинг 1 қисми талабларини ҳисобга олган ҳолда тасдиқлаш зарур.'),
        (331, 33, null, null, '236. Бир техник зонада ётқизиш чуқурлиги ва қувур материаллари тахминан бир хил қувурлар ётқизилиши керак.'),
        (1377, 136, '2024-05-29 17:30:50', '2024-05-29 17:30:50', '3. Vazirlik o‘z faoliyatida Vazirlar Mahkamasiga bo‘ysunadi.'),
        (77, 8, null, null, '42.7. Портловчи моддалар ва улар асосида ишланган маҳсулотлар (ташкилотлар, қурол-яроғ омборхоналари, базалар, ПМ омборхоналари) тайёрловчи ва сақловчи объектлар учун тақиқланган (хавфли) зоналарни ва майдонларни назарда тутиш лозим. Бу зоналар ва майдонларнинг ўлчамлари кўрсатилган объектлар тасарруфида турган ижро этувчи ҳокимият ташкилотларининг махсус меъёрий ҳужжатларида белгиланади. Тақиқланган (хавфли) зоналарни турар жой, жамоат ва ишлаб чиқариш бинолари ва иншоотлари билан қуриш тақиқланади. Айрим зарурий ҳолларда бинолар, иншоотлар ва бошқа объектларнинг тақиқланган (хавфли) зона ҳудудида қурилиши бу зона ёки ишлаб чиқариш тасарруфида турган ташкилот билан келишилган ҳолда амалга оширилиши мумкин.'),
        (78, 8, null, null, '42.8. Илмий-ишлаб чиқариш зоналари таркибида илмий ва илмий хизмат кўрсатиш муассасаларини тажрибавий ишлаб чиқаришлар ва улар билан боғлиқ бўлган олий ва ўрта ўқув юртларини, меҳмонхоналарни, хизмат кўрсатиш муассасалари ва корхоналарини, шунингдек муҳандислик ва транспорт коммуникацияларини ва иншоотларини жойлаштириш зарур. Илмий-ишлаб чиқариш зоналари таркиби ва айрим ИТИ ва тажрибавий ишлаб чиқаришларнинг жойлашиши талаблари атроф-муҳитга таъмири омилларини ҳисобга олган ҳолда аниқлаш лозим. Илмий ишлаб чиқариш зоналарида санитария-ҳимоя зонаси 50 метрдан ортиқ кенгликни талаб қилмайдиган тажрибавий ишлаб чиқаришни жойлаштиришда қурилмаларнинг аралаш зонаси турида шакллантирилган турар-жой биноларини жойлаштиришга йўл қўйилади.'),
        (79, 8, null, null, '42.9. Илмий муассасаларнинг ер участкаларининг ўлчамлари (биноларнинг ҳар қаватдаги 1000 м 2 умумий майдонига), гектар (га) ҳисобида ортиғи билан қуйидагича қабул қилиш зарур: табиий ва техникавий фанлар - 0,12-0,17 ижтимоий фанлар - 0,08-0,1 Изоҳлар: 1. Бу кўрсаткичлар алоҳида шароитлар ва ишлаш тартибини талаб қилувчи объектларга (ботаника боғларига, обсерваторияларга, элементар заррачаларни тезлашгичларига ва бошқаларга) жорий этилмайди. 2. Келтирилган меъёрларга тажриба майдонлари, полигонлар, захира ҳудудлари, санитария-ҳимоя зоналари кирмайди. 3. Кўрсаткичларнинг кичиги энг йирик ва йирик шаҳарлар учун ва қайта тиклаш шароитлари учун қабул қилиш лозим.'),
        (80, 8, null, null, '42.10. Илмий муассасалар участкалари ичида бинолар, майдонлар, кўкаламзорлар, йўллар, йўлаклар ва автотўхташ жойлари билан эгалланган ҳудудлар ажратилади. Бунда бинолар ва иншоотлар участка майдонининг 50 фоиздан ортиқ бўлмаган, спорт майдончалари ва кўкаламлаштирилган ҳудудлар 10 фоиздан кам бўлмаган, йўллар ва йўлаклар 15 фоиздан 25 фоизгача ҳудудни эгаллашлари керак. Автотўхташ жойларига бўлган талаб илмий муассасалар бинолари тасарруфидаги умумий майдоннинг 1 автомобилга 25 м 2 бўлган меъёр-дан келиб чиқиб, битта автомобиль учун участка майдони 25 м 2 га тенг қилиб қабул қилинади.'),
        (81, 8, null, null, '42.11. Коммунал-омборхона зоналарида (майдонларида) озиқовқат (озиқ-овқат, гўшт ва сут) саноатини, умумтовар (озиқ-овқат ва озиқ-овқатдан ташқари), ихтисослашган омборхоналарни (музхона, картошка, сабзоват, мева сақланадиган жой), шаҳар аҳолисига коммунал, транспорт ва маиший хизмат кўрсатиш корхоналарни жойлаштириш зарур'),
        (82, 8, null, null, '42.12. Омборхоналарни жойлаштиришда ер ости бўшлиқларидан максимал тарзда фойдаланиш керак. Ишлатиб бўлинган тоғ-кон ер ости лаҳимлари ва ер ости участкаларида объектларни жойлаштиришга яроқли бўлган жойлар мавжуд бўлган тақдирда ташқи таъсирларга чидамлилиги ва ишлашининг ишончлилигини таъминланишини талаб қилувчи озиқ-овқат ва саноат товарлар зарур ҳужжатлар сақланадиган жойлар, тақсимловчи ШНҚ 2.07.01-03 33-бет музхоналар ва бошқа объектлар қурилишини амалга оширишга йўл қўйилади. Объектларни жойлаштириш фойдали қазилмаларни қазиб олиш билан боғлиқ бўлмаган ер ости маконларидан фойдаланиш мақсадидаги аниқ белгиланган меъёрий ҳужжатлар талабларига мувофиқ амалга ошириш зарур.'),
        (83, 8, null, null, '42.13. Аҳоли пунктларига хизмат кўрсатиш учун мўлжалланган омборхоналар биноларининг майдони ва ҳажми, уларнинг ер участкалари ўлчамлари шаҳарсозлик меъёрлари ёки ҳисоблаш асосида аниқланади. Картошка, сабзоват ва мева сақланадиган жойларнинг санитарияҳимоя зонаси ўлчамлари 50 метрдан кам бўлмаган ҳолда қабул қилиш лозим'),
        (385, 34, null, null, '290. Оқизиш каналлари, жарликлар ва эски ўзанларга ёндош тор қирғоқ бўйларида қувурсиз (қатлам, призмали) дренажлар билан бирга чизиқли дренажлар кўзда тутилиши керак'),
        (84, 8, null, null, '42.14. Қишлоқ хўжалик ишлаб чиқаришини ташкил этишда турар жой ва ижтимоий-амалий зоналарини ишлаб чиқариш мажмуаларининг салбий таъсиридан, шунингдек, бу мажмуаларнинг ўзларини, агар улар озиқ-овқат маҳсулотларини ишлаб чиқариш билан боғлиқ бўлса, бошқа ишлаб чиқариш, транспорт ва коммунал иншоотлари ифлосланишидан ва зарарли таъсиридан ҳимоя қилиш бўйича чораларни назарда тутиш зарур. Ер тупроғи, ер усти ва ер ости сувлари, юзаки сув тўпланадиган жойлар, сув ҳавзалари ва атмосфера ҳавоси ифлосланишига йўл қўймаслик чоралари санитария меъёрларига мувофиқ бўлиши керак'),
        (85, 8, null, null, '42.15. Шаҳарнинг саноат зонаси (тумани) лойиҳавий режасида фуқаро муҳофазасининг муҳандисликтехник тадбирлари бўлими ШНК 1.03.11-07 “Ўзбекистон Республикасида шаҳарлар ва бошқа аҳоли пунктларининг бош режасида фуқаро муҳофазасининг муҳандислик-техник тадбирлари бўлимининг таркиби, ишлаб чиқиш тартиби, тегишли идоралар билан келишиш ва тасдиқлашга доир йўриқнома” мувофиқ ишлаб чиқиш зарур.'),
        (86, 8, null, null, '43*. Айрим янги саноат корхоналарини саноат ва турар-жой қурилишини жойлаштириш учун қулай шароитга эга кичик ва ўрта шаҳарларда ёки янги барпо этиладиган шаҳар аҳоли пунктларида жойлаштириш кўзда тутилиши керак. Йирик шаҳарларда янги саноат корхоналари ва бошқа шаҳар аҳамиятидаги объектлар қурилиши чекланади, саноат зоналарини қайта қуришда технологик бўғимлари ва ёпиқ (чиқиндисиз) мажмуалар қисмлари сифатида зарур бўлган корхоналар ва объектлар бундан мустасно. Маданий мерос объектларига эга Тошкент шаҳри, шаҳарсозлик фаолияти алоҳида тартибга солинувчи объектларга киритилган аҳоли пунктларида янги саноат корхоналари, тажриба базалари, транспорт ва омбор корхоналарининг қури-лиши ва бу турдаги мавжуд корхоналарнинг янги қурилиш ҳисобидан кенгайтирилиши тақиқланади, Ўзбекистон Республикаси ҳукуматининг тегишли қарорларида махсус белгиланган ҳоллар, коммунал мақсадлардаги корхоналар ва объектлар, шунингдек, турар жойфуқаро қурилиши эҳтиёжларини қондирувчи объектлар бундан мустасно'),
        (87, 8, null, null, '44. Янги ва қайта қурилаётган ишлаб чиқариш зоналари таркибида 34-бет. ШНҚ 2.07.01-03 имкони борича табиий захира лар, хом ашё, материалларнинг тўлиқ ва мажмуий ишлатилишини таъминловчи, атроф муҳитга зарарли таъсири бўлмаган ёки анча камайтирилган ишлаб чиқаришларнинг мажмуалари шаклланишини кўзда тутиш лозим.'),
        (88, 8, null, null, '45. Аҳоли пунктларининг янги ишлаб чиқариш зоналарини жойлаштириш ва амалдагиларини ривожлантириш учун жой танлашда қуйидагиларни ҳисобга олиш лозим: геологик шароитлар; сейсмик микрорайонлаштириш, ер ости чучук сувларининг тозалигини ҳимоя этиш зоналари, мўлжалдаги корхоналар ва ишлаб чиқаришларнинг туси ва турлари; аҳоли пунктининг умумий режавий тузилмасига мувофиқлиги; транспорт хизмат кўрсатишининг қулайлиги; мавжуд корхоналар билан ишлаб чиқариш ва технологик алоқаларнинг оқилона ташкил этилиши; техник-иқтисодий, санитариягигиеник кўрсаткичлар, ёқилғиэнергетик, сув ва ҳудудий захиралар, атроф муҳит ҳолатининг меъморийрежавий ечимлари турларини қиёслаш асосида оқилона функционал фойдаланиш; табиий ва бошқа шароитларнинг келажакдаги ўзгариши тахминлари'),
        (89, 8, null, null, '46. Мавжуд корхоналар таркибида бошқа йўналишдаги янги ишлаб чиқаришлар ва кичик корхо-наларни жойлаштириш уларнинг санитария зарар даражаси асосий ишлаб чиқариш санитария зарар даражасидан ошиб кетмаганда ва экологик шароит ёмонлашувига олиб келмаганда мумкин бўлади. Мавжуд корхоналар таркибида бошқа йўналишдаги янги ишлаб чиқаришлар ва кичик корхоналарни жойлаштириш архитектура, санитария инспекцияси ва табиатни муҳофаза этиш идоралари, шунингдек, аҳоли пунктининг бош режасини ишлаб чиқувчи билан келишилиши лозим'),
        (90, 9, null, null, '47. Саноат корхоналари, одатда, саноат зоналари ҳудудида, умумий ёрдамчи ишлаб чиқаришлари ёки инфратузилма объектлари бўлган корхоналар гуруҳлари тарки-бида, қишлоқ аҳоли пунктларида эса ишлаб чиқариш зоналари таркибида, уларнинг ўзаро таъсирини ҳисобга олиб жойлаштириш лозим.'),
        (208, 27, null, null, '124. Йирик ва ўта йирик шаҳар атрофлари зоналарида транзит поездларни ўтказиш учун айланма темир йўллар кўзда тутилиб, уларда умумтармоқ аҳамиятидаги сортловчи ва юк бекатларини жойлаштириш лозим. Техник-иқтисодий асослари бўлганда темир йўллардан шаҳар ичи ва шаҳар атрофидаги алоқа-лардан фойдаланиш кўзда тутилади.'),
        (91, 9, null, null, '48. Саноат зоналарини жойлаштиришда меҳнаткашларнинг йўл вақтлари энг кам бўлиши учун уларнинг турар жой районлари билан оқилона ўзаро таъсирини таъминлаш керак. Саноат зоналари ҳудудларининг ўлчамлари ва фойдаланиш интенсивлиги даражаси уларнинг аҳоли пункти тузилишида жойлашуви шароитларига ва унинг ҳудудидаги турли участкалар шаҳарсозлик қийматига боғлиқ ҳолда, корхоналарнинг соҳавий хусусиятлари, уларнинг жойлашувига санитариягигиеник ва ёнғинга қарши талабларни, юк айланиши ва транспорт турлари, шунингдек, қурилиш навбатларини ҳисобга олиб, ихчам жойлашув ва ер ости майдонларидан фойдаланишни кўзда тутиб, қабул қилиниши лозим. Бунда корхоналар ходимларига ва саноат зонаси атрофида жойлашган обод ҳудуд аҳолисига барча турдаги хизмат кўрсатишнинг ўзаро боғлиқ тизимини шакллантириш лозим.'),
        (92, 9, null, null, '49. Саноат корхоналари ва бошқа ишлаб чиқариш объектларининг майдонлари, хизмат кўрсатиш муассасалари ва корхоналари эгаллаган ҳудуд, одатда, саноат зонаси ҳудудидан камида 70% ни ташкил этиши керак. Изоҳ. Саноат зонаси ҳудудининг эгалланганлиги фоизларда аниқланади, у саноат корхоналари ва улар билан боғлиқ объектларнинг тегишли чегаралар ичидаги майдонлари, шунингдек, хизмат кўрсатиш муассасалари, жумладан, темир йўл бекатлари эгаллаган майдон киритилгандаги йиғиндисининг аҳоли пунктининг бош режасида белгиланган саноат зонасининг умумий ҳудудига нисбатан белгиланади. Эгалланган ҳудудларга корхона майдонидаги лойиҳалаштириш топшириғига мувофиқ белгиланган захира участкалари кириши керак'),
        (93, 9, null, null, '50. Саноат корхоналари учун участкалар майдонларининг дастлабки ҳисобланиши техник-иқтисодий асослашлар ёки амалдаги ўхшаш корхоналар бўйича нисбий кўрсаткичлар қўлланиб ўтказилган техник-иқтисодий ҳисоблар билан белгиланади. Саноат корхоналарига ажратиладиган участкалар майдони бу корхоналарни режалаштириш лойиҳалари билан, саноат корхоналари бош режаларини ишлаб чиқишга қўйиладиган талабларга мувофиқ белгиланади'),
        (94, 9, null, null, '51*. Турар-жой ҳудуд ичида атроф муҳит ифлосланиши манбаи бўлмайдиган; нисбатан кўп меҳнат талаб этадиган ва кам захира талаб этадиган, ёнғиндан хавфсиз ва портлашдан хавфсиз ишлаб чиқариш жараёнли; белгиланган нормалардан ортиқ шовқин бермайдиган; кириб бориш темир йўлларини талаб этмайдиган саноат корхоналарини жойлаштиришга йўл қўйилади. Бунда саноат корхонаси участкаси чегараларидан турар жой уйлари, болалар мактабгача тарбия муассасаларигача, умумтаълим мактаблари, соғлиқни сақлаш ва дам олиш муассасаларигача бўлган масофа камида 50 м деб қабул қилиниши керак. Турар жой қурилиши доирасида жойлашган корхонанинг атроф муҳитга зарарли таъсирини йўқ қилиш имкони бўлмаганда корхонанинг қувватини камайтириш, унинг соҳасини ўзгартириш ёки уни турар жой қурилишидан ташқарига ўтказилиши кўзда тутилиши лозим.'),
        (95, 9, null, null, '52. Санитария-ҳимоя зоналари зарарли чиқиндиларни тозалаш ва зарарсизлантириш ҳамда шовқин даражасини пасайтиришга оид барча техник ва технологик чоралар кўрилганидан сўнг зарарли моддалар концентрациясининг ва шовқиннинг аҳоли жойлашган ҳудудда йўл қўйиладиган энг юқори даражалари таъминланмайдиган ҳолда кўзда тутилиши керак. Бундай зоналарнинг ўлчамлари корхоналар чиқиндиларида бўлган зарарли моддаларнинг атмосфера ҳавосидаги концентрациясини ҳисоблаш Методикаси ОНД-86 га мувофиқ ҳолда, шунингдек, шовқиндан ҳимоя талаблар ва бошқа талабларни ҳисобга олиб белгиланиши керак'),
        (96, 9, null, null, '53*. Санитария-ҳимоя зонасида турар жой уйлари, болаларнинг мактабгача тарбия муассасалари, умумтаълим мактаблари, соғлиқ сақлаш ва дам олиш муассасалари, спорт иншоотлари, боғлар, парклар, боғдорчилик ширкатлари жойлаштирилишига йўл қўйилмайди'),
        (97, 9, null, null, '54. Санитария-ҳимоя зоналарининг энг кам кўкаламзорлаштириш майдони бу ҳудудда пайдо бўлувчи чиқиндилар турини ҳисобга олиб, ҳисоблаш йўли билан белгиланиши лозим. Ҳисоб-китоблар учун маълумотлар бўлмаганда энг кам кўкаламзорлаштириш майдони зонанинг энига боғлиқ ҳолда, фоиз билан қабул қилинади: 300 м гача - 60 301 – 1000 м - 50 1001 – 3000 м - 40 Аҳоли жойлашган ҳудуд томонидан эни камида 50 м ли, зона эни 100 м гача бўлганда эса камида 20 м ли дарахт-бутазор бўлиши кўзда тутилади'),
        (533, 55, null, null, '11.8. Йўллар, чорвачилик фермалари қурилиши, қаттиқ чиқитларни утиллаштиришда табиатни муҳофаза қилиш тадбирларини назарда тутиш керак бўлади.'),
        (98, 9, null, null, '55. Корхоналарнинг ахлатхоналари, шағал ва шох-шабба сақланадиган жойлари, сарқит ва чиқинди ўраларини қуришга фақат бу чиқиндиларни қайта ишлаш мумкин бўлмаганда йўл қўйилади, бунда саноат районлари ва тармоқлари учун одатда марказлашган (гуруҳли) ахлатхоналар қуриш кўзда тутилади. Улар учун участкалар корхоналардан ҳамда ер ости сувларининг санитария ҳимояси зоналарининг II минтақасидан ташқарида ажратилади, бунда ифлослантирувчи моддаларнинг тупроққа ва сизот сувларига тушишига йўл қўймайдиган чоралар кўрилиши ҳамда тегишли назорат идоралари тасдиқлаган санитария нормалари ва хавфсизлик қоидаларига риоя қилиниши лозим. Кўмир, сланец, маргимуш, қўрғошин, симоб ва бошқа ёнувчи ҳамда заҳарли моддалар бўлган ахлатхоналар турар жой ва жамоат бинолари ва иншоотларидан ҳисоблаб аниқланган ва санитария-эпидемиологик хизмат идоралари билан келишилган масофада жойлаштирилиши лозим.'),
        (99, 11, null, null, '56*. Коммунал-омбор зоналари ҳудудида: озиқ-овқат саноати корхоналари; умумтовар (озиқ-овқат ва ноозиқ-овқат) ва ихтисослашган омборлар (совутгичлар, картошка, сабзавот ва мева омборлари), коммунал транспорт ва аҳолига маиший хизмат кўрсатиш корхоналари жойлаштирилиши керак. Аҳолига кундалик бевосита хизмат кўрсатиш билан боғлиқ бўлмаган омборлар мажмуа тизимини, одатда, аҳоли пунктларидан ташқарида транспорт тугунларига, ташқи, асосан, темир йўл транспорт тармоғига яқин жойда шакллантириш лозим. Аҳоли пунктлари ва уларнинг яшил зоналаридан ташқарида, шаҳар атрофининг айрим ажратилган омбор районларида санитария талаб-лари, ёнғинга қарши ва махсус қоидаларга риоя қилган ҳолда дав-лат захира лари омборлари, нефт ва нефт маҳсулотларининг 1-гуруҳ омборлари, нефть ва нефть маҳсулотларининг тушириб-ортиш омборлари, суюлтирилган газ омборлари, кучли таъсир қиладиган заҳарли моддаларнинг таянч омборлари, озиқ-овқат, ем-хашак ва саноат хом ашёлари, таянч омборлари, портловчи моддалар омборлари ўрмон ва қурилиш ашёларининг таянч омборлари алоҳида жойлаштириш кўзда тутилади.'),
        (100, 11, null, null, '57. Кичик шаҳарлар ва қишлоқ аҳоли пунктлари учун бир гуруҳ аҳоли пунктларига хизмат кўрсатувчи марказлаштирилган омборлар кўзда тутилиб, бундай омборлар кўпроқ туман марказларида ёки бекат ёнидаги аҳоли пунктларда жойлаштирилади'),
        (101, 11, null, null, '58. Ўта йирик, йирик ва катта шаҳарларда коммунал-омбор зоналарини алоҳида-алоҳида, турар жой ва саноат районлари билан транспорт алоқаларини таъминлашни ҳисобга олиб жойлаштириш керак. Коммунал-омбор зоналаридаги омбор объектлари ва коммунал корхоналар ихтисослашган гуруҳлар қолида, асосан бирлашган кўп қаватли уйларда ҳамда товарларни сақлашга қўйиладиган талабларга риоя қилиб, уларнинг ўзаро таъсирини ҳисобга олиб жойлаштирилиши керак.'),
        (102, 11, null, null, '59*. Аҳоли пунктларини қайта қуришда турар-жой ҳудудида жойлашган ҳамда санитария ва ёнғинга қарши нормалар талабларига, шунингдек, турар-жой туманининг режавий тузилмасига жавоб бермайдиган омбор ва коммунал объектлар коммунал-омбор зоналарига кўчирилиши керак.'),
        (103, 11, null, null, '60. Омборлар ва базалар жойлашуви учун ажратиладиган участкалар майдонини ҳисоблаш техникиқтисодий асослаш ёки амалдаги ўхшаш корхоналарга оид техникиқтисодий ҳисоблар билан белгиланади. Аҳолига хизмат кўрсатиш учун мўлжалланган коммунал-омбор зоналари ер участкаларининг майдони ўта йирик ва йирик шаҳарларда кўп қаватли омборлар қурилишини ҳисобга олиб бир кишига 2 м 2 ва бошқа шаҳар аҳоли пунктларида 2,5 м 2 деб олиниши мумкин. Умумтовар ва ихтисослашган омборлар участкаларининг тахминий ўлчамларини 10-иловага кўра қабул қилиш тавсия этилади'),
        (104, 11, null, null, '61. Қишлоқ аҳоли пунктларининг ишлаб чиқариш зоналарида, уларнинг ишлаб чиқариш ихтисосига мувофиқ, қишлоқ хўжалик корхоналарини ҳамда қишлоқ хўжалик маҳсулотларини бирламчи, тўлиқ ва мажмуий қайта ишлаш корхоналар гуруҳлари ёки айрим кичик корхоналарни жойлаштириш лозим. Демографик таркиби, бандлик даражаси, маҳаллий анъаналарни ҳисобга олган ҳолда қишлоқ аҳоли пунктларида ҳам доимий, ҳам мавсумий тусдаги саноат корхоналарининг ихтисослашган филиалларини кўзда тутиш керак.'),
        (105, 11, null, null, '62. Қишлоқ хўжалик ва бошқа қайта ишлаш корхоналари, бинолар ва иншоотларни қишлоқ аҳоли пунктларининг ишлаб чиқариш зонасида жойлаштиришда уларнинг орасида технологик лойиҳалашнинг санитария, ветеринар ва ёнғинга қарши талаблари ва нормаларидан келиб чиқиб, шунингдек, ШНҚ 2.07.04-06 “Қишлоқ хўжалик корхоналари ҳудудларини меъморий режалаштириш ва ташкил қилиш” ни ҳисобга олиб, йўл қўйиладиган энг кичик масофалар белгиланиши зарур.'),
        (219, 27, null, null, '135. Соҳил базалари ҳамда спорт клублари ва айрим фуқароларга тегишли кичик кемалар туриш жойлари шаҳар атрофида, аҳоли пунктларида эса аҳоли жойлашган ҳудуд ва оммавий дам олиш зоналаридан ташқарида жойлаштирилиши керак'),
        (106, 11, null, null, '63. Қишлоқ аҳоли пунктларининг ишлаб чиқариш зоналарига кирувчи электр узатиш, алоқа тармоқлари ва маҳаллий аҳамиятдаги бошқа симли иншоотлари алмашлаб экиш далалари четидан, йўллар ёҳаларида, ўрмон йўллари, мавжуд  трассалар бўйлаб, тегишли меъёрий ва қонуний ҳужжатлар билан белгиланган масофаларда, қишлоқ хўжалик экинзорлари билан банд бўлмаган жойлардан коммуникацияларгача бемалол ета олиш таъминланиб жойлаштирилиши лозим.'),
        (107, 11, null, null, '"64*. Қишлоқ аҳоли пунктларининг ишлаб чиқариш зонаси, одатда, умумий тармоқ темир ёки автомобиль йўллари билан айрим участкаларга бўлинмаслиги керак. Қишлоқ аҳоли пунктлари ишлаб чиқариш зоналарининг ажра-тилган участкаларида санитария-ҳимоя зоналари ҳисоб бўйича 300 м дан ортиқ бўлган корхоналар ва объектларни жойлаштириш керак. '),
        (108, 11, null, null, '64.1. Чорвачилик мажмуалари ва фермалари ҳудудида ҳамда уларнинг санитария-ҳимоя зоналарида қишлоқ хўжалик маҳсулотларини қайта ишловчи корхоналарни, овқатланиш объектларини ва уларга тенглаштирилган объектларни жойлаштиришга йўл қўйилмайди. '),
        (109, 11, null, null, '64.2. Қишлоқ хўжалик корхоналарини ва бошқа объектларни жойлаштиришда тупроқнинг, ер усти ва ер ости сувларининг, юзаки сув тўплаш жойларининг сув ҳавзалари ва атмосфера ҳавосининг ифлосланишига қарши чораларни назарда тутиш зарур."'),
        (110, 12, null, null, '65*. Рекреация ҳудудларининг ривожланишини аҳоли пунктлари тизимларининг режавий тузилмасидан, мавжуд табиий захиралардан оқилона фойдаланишдан келиб чиқиб, уларни ўзлаштириш муддатларига боғлиқ бўлмаган ҳолда кўзда тутиш лозим. Бадиий ва маънавий қийматга молик, табиий манзаралари сақланганлиги даражаси юқори бўлган ҳудудларда миллий ва табиий парклар барпо этиш лозим. Миллий ва табиий паркларнинг меъморий-ҳажмий тузилмаси уларнинг ҳудудидан илмий, маданий-маърифий ва дам олиш мақсадларида фойдаланишни кўзда тутиши лозим ва одатда, таркибида қўриқхона-рекреация, рекреация ва хўжалик зоналари ажратилади.'),
        (111, 12, null, null, '66. Рекреация мақсадлари учун ҳудуд танлаш, рекреация ҳудудини функционал зоналаштириш ва меъморий-режавий ташкил этиш учун асос бўлувчи табиий захираларни мажмуий баҳолаш асосида ўтказилиши керак. Баҳолаш курорт захиралари, манзаралар, санитария-гигиеник хусусиятлар, муҳандислик-қурилиш шароитлари, ҳудудларнинг мавжуд фойдаланилишини таҳлил қилиш асосида амалга оширилади. Рекреация ҳудудларининг чегаралари қуйидагиларни: табиий тўсиқлар (тоғ тизмалари, дарёлар, кўллар ва ҳоказо); давлат табиий паркларини, қўриқхоналар зоналарини, заказникларни ташкил этиш учун яроқли табиий ҳудудларнинг киритилишини ҳисобга олиб белгиланади. Рекреация ҳудудларига бўлган эҳтиёж ҳисоблари ва турли рекреация зоналарида бир дам олувчи учун майдон нормаси жадвал бўйи ча қабул қилиниши лозим'),
        (112, 12, null, null, '67. Кам сувли ҳудудларда дам олиш жойларини ташкил этиш учун мавжуд сув омборларининг соҳилларидан фойдаланиш, шунингдек дам олишда фойдаланиш учун сунъий ҳавзалар яратиш лозим. Улар аҳоли пунктлари тизимларининг асосий шаҳарлари-марказларидан 10-15 км доирасида жойлаштирилиши керак. Сунъий ҳавзалар соҳилларида дам олиш зоналарининг тузилиши шу объектлардан фойдаланиш талабларига жавоб бериши ва ернинг рекультивацияси, саёз жой-лар ва ботқоқликларни йўқ қилиш, сув ҳавзаларини ободонлаштириш ва табиий муҳитни асраш тадбирларини амалга оширишни кўзда тутиши лозим'),
        (113, 12, null, null, '68. Табиий ва сунъий сув ҳавзалари асосида жойлаштириладиган пляжлар майдони бир кишига 5 м 2 ҳисобидан, ихтисослаштирилган муолажа пляжлари ҳудудининг катталиги бир кишига камида 8 м 2 ҳисобидан қабул қилиниши керак. Пляжларда бир йўла мижозларнинг сони пляжларнинг бир вақтнинг ўзида хизмати коэффициентларини ҳисобга олиб ҳисобланади: санаторийлар - 0,6 – 0,8; дам олиш муассасалари - 0,7– 0,9; саломатлик оромгоҳлари - 0,2– 0,4; мустақил дам олувчилар - 0,5. Соҳилларда ташкил этиладиган пляжлар сиғимини оширишга сув алмашуви шартларига риоя қилган ҳолда сунъий бурунлар, қўлтиқлар ва ярим оролларни ташкил этиш йўли билан эришилади.'),
        (233, 28, null, null, '149. Тартибга солинадиган ҳаракат чорраҳаларига яқин йўл қатнов қисмини тўхташ чизиғидан камида 50 м масофадан 1-2 ҳаракат йўлига кенгайтириш зарур.'),
        (1576, 119, '2024-07-03 11:16:15', '2024-07-03 11:16:15', '16.3. Bino va inshootga bo‘lgan huquqning davlat ro‘yxatidan o‘tkazilmaganligi'),
        (1378, 136, '2024-05-29 17:30:50', '2024-05-29 17:30:50', '4. Vazirlik o‘z faoliyatida O‘zbekiston Respublikasining Konstitutsiyasi va qonunlari'),
        (114, 12, null, null, '69. Ҳар қандай турдаги рекреация муассасалар участкалари чегарасидан: турар жой уйларигача - камида 500 м, қайта қуриш шароитида - камида 100 м; коммунал хўжалик муассасалари ва объектлари, базалар, омборлар ва ҳоказоларгача - камида 500 м; I, II, III тоифадаги автомобиль йўлларигача - камида 500 м, IV тоифа - камида 200 м; боғдорчилик ширкатлари ва дала ҳовлиларгача – 300 м масофа бўлиши керак. Изоҳ. Мураккаб рельеф шароитларида кўрсатилган масофалар лойиҳалаш топшириғига мувофиқ қисқартирилади.'),
        (115, 12, null, null, '70. Тоғли рекреацион туманларнинг функционал тузилиши муайян табиий-шаҳарсозлик вазияти, тик зоналаштириш ва ҳоказолар, шунингдек, ҳудуддан фойдаланишнинг мўлжалдаги йўналиши ва 2-иловани ҳисобга олган ҳолда барпо этилиши керак. Тоғ шароитларида дам олиш жойларини барпо этишда функционал зоналар ҳудудлари ва йўл қўйиладиган рекреацион юкламаси нисбатининг тахминий кўрсаткичлари 9-жадвал бўйича қабул қилиниши керак'),
        (116, 12, null, null, '71*. Рекреация жойларининг ҳудудида барча дам олувчиларнинг, жумладан, ногиронлар ва аҳолининг кам ҳаракатланувчи гуруҳлари, шунингдек, доимий аҳолининг эҳтиёжларини қондирувчи ҳамда очиқ ва ёпиқ элементлардан иборат маданий-маиший хизмат кўрсатиш муассасалари тизимини шакллантириш кўзда тутилиши лозим. Ёпиқ тармоқ мунтазам дам олиш муассасаларида ташкил этилиб, санаторийлар, дам олиш муассасалари ва меҳмонхоналарини лойиҳалаштириш талабларига мувофиқ ҳисобланади. Рекреация ҳудудларидаги очиқ хизмат кўрсатиш тармоғини 10- жадвалга кўра лойиҳалаштириш лозим'),
        (117, 12, null, null, '72. Рекреация ҳудудларида аҳоли пунктлари тизимининг асосий магистраллари билан қулай алоқалари бўлган ягона транспорт ва пиёда йўллар тизимини яратиш керак. Йўл трассалари асосий дам олиш оқимларини тақсимловчи бош йўллар ва айрим рекреация муассасаларига хизмат кўрсатувчи маҳаллий йўлларга бўлиниши лозим. Транзит транспорт автомобиль ва темир йўллари дам олиш жойларини кесиб ўтмай, уларнинг яқинидан ўтиши шарт. Дам олиш ҳудудидан транспорт магистралларининг ўтказилиши мажбурий бўлганда уни чуқурлаштиришни ёки ер ости ўтишлари ва осма кўприклар қурилишини кўзда тутиш лозим.'),
        (118, 12, null, null, '73. Дам олиш ҳудуди анча катта бўлганда чегараланган ҳаракат тезлиги бўлган ички автомобиль йўлларини ва йўл-сўқмоқ тармоғини кўзда тутиш керак, бунда йўл қисмлари қуйидаги кенгликда бўлади, м: шоссе йўллари - 9; бош йўлкалар - 7; бош йўллар - 5; иккинчи даражали хиёбонлар - 2,0- 2,5; сўқмоқлар - 1,2-2,0.'),
        (119, 13, null, null, '74*. Шаҳар аҳоли пунктлари ландшафт-рекреация тизимини шаҳар атрофи ҳудудларининг режавий тузилмаси ечими билан уйғунликда лойиҳалаштириш керак. Шаҳар атрофи рекреация зоналарига транспортда етиб олиш вақти 2,5 соатдан ошмаслиги керак.'),
        (120, 13, null, null, '75*. Шаҳар атрофи ландшафтли- рекреация зонаси ичида қуйидагилар жойлаштирилади: шаҳар ташқарисидаги парклар, боғлар, кўчатзор ва дарахтзорларнинг бошқа турлари; дам олиш уйлари, пансионатлар, мотель ва кемпинглар, пляжлар, жисмоний тарбия ва спорт иншоотлари, сайёҳлик, ов ва балиқчилик базалари, спорт-соғломлаштириш оромгоҳлари ва болалар мактабгача тарбия муассасаларининг дала ҳовли боғлари; даволаш-муолажа муассасалари (тегишли табиий-даволаш омиллари мавжуд бўлганда); қариялар ва ногиронлар учун интернат-уйлар; жамоавий боғлар ва дала ҳовлибоғлар.'),
        (121, 13, null, null, '75.1. Рекреацияга оид функционал зоналар таркибига дам олиш, туризм, жисмоний тарбия ва спорт машғулотлари учун мўлжалланган дарахтзорлар, хиёбонлар, парклар, шаҳар боғлари, ҳовузлар, кўллар, сув омборлари, пляжлар эгаллаган ҳудудларни киритиш мумкин.'),
        (122, 13, null, null, '75.2. Шаҳарлар ва қишлоқ аҳоли пунктлари чизиғи ичида алоҳида аҳамиятга эга табиатни муҳофаза қилувчи, илмий, тарихий-маданий, эстетик, дам олиш, соғломлаштириш ШНҚ 2.07.01-03 43-бет ва бошқа алоҳида қийматга эга бўлган ер участкаларини ўзида мужассам этган алоҳида муҳофазаланувчи табиат ҳудудлари зоналарини ажратиш мумкин.'),
        (123, 13, null, null, '75.3. Рекреация зоналари ва алоҳида муҳофазаланувчи табиий зоналар ҳудудларида дам олиш, соғломлаштириш ва табиатни муҳофаза қилишга оид объектларини ишлатиш билан бевосита боғлиқ бўлмаган саноат, коммунал-омборхона ва бошқа объектларнинг янгисини қуриш ва мавжудларини кенгайтириш тақиқланади.'),
        (1184, 120, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '4. Ruxsatnoma 2 yil muddatga beriladi, agarda ruxsatnoma talablari 2 yil muddatda bajarilmasa buyurtmachining so‘roviga asosan ruxsatnoma bergan organ tomonidan qaytadan ko‘rib chiqiladi.'),
        (125, 13, null, null, '75.5. Дам олиш аҳамиятига эга зоналари таркибида ҳайвонот боғларини жойлаштириш кўзда тутиш лозим. Ҳайвонот боғларининг чегараларидан турар жой ва жамоат биноларигача бўлган масофа маҳаллий соғлиқни сақлаш идоралари билан келишилган ҳолда, шамол йўналишини ҳисобга олиб 100 м дан кам бўлмаган (шамолга рўпара томондан ва шамолга тескари томондан икки марта кўп) миқдорда белгиланади'),
        (126, 13, null, null, '75.6. Парклар ва боғларни жойлаштиришда мавжуд ўсимликли ва сув ҳавзали участкаларни имкони борича сақлаш зарур. Парклар, боғлар ва хиёбонлар ҳудудининг майдонларига ҳисобида камида қўйидагича қабул қилиш лозим: шаҳар парклари -1 5, режавий туманлар парклари - 10, турар жой туманлари боғлари - 3, хиёбонлар - 0,5. Қайта тиклаш шароитларида кўрсатилган элементларнинг майдо-нини камайтиришга йўл қўйилади.'),
        (127, 13, null, null, '75.7. Дам олиш аҳамиятига эга зоналарда жойлашган аҳолининг қисқа муддатли оммавий дам олиш объектларини жойлаштирув тартиби бу зоналарига жамоат транспортида етиб олишни ҳисобга олиб, одатда, кўпи билан 1,5 соатни назарда тутиш зарур'),
        (128, 13, null, null, '75.8. Қисқа муддатда оммавий дам олиш объектлари кейинги ҳолларда дам олиш зоналари ҳудудларининг ўлчамлари 1 дам олувчига камида 500-1000 м 2 ҳисобидан келиб чиқиб қабул қилиш лозим, шу жумладан фаол дам олиш турлари учун унинг кўп фойдаланадиган қисми битта дам олувчига камида 100 м2 ташкил этиши керак. Қисқа муддатли дам олиш зонасининг айрим участкаларининг майдонини камида 50 га, чўл ва ярим чўл жойларда камида 30 га миқдорида қабул қилиш зарур.'),
        (129, 13, null, null, '75.9. Алоҳида муҳофазаланадиган табиат ҳудудлари таркибида табиий шифобахш омилларга, энг қулай микроиқлимий, ландшафт ва санитария-гигиеник шароитларга эга бўлган ерларда даволаш-соғломлаштириш участкаларини ажратиш мумкин. Курортлар ҳудудларида курорт зонасини шакллантирувчи санаторий-курорт ва соғломлаштириш муассасаларини, дам олиш ва туризм муассасаларини, даволанувчилар ва дам олувчиларга хизмат кўрсатиш муассасалари ва корхоналари, курорт паркларини ва бошқа умумий фойдаланиш яшил ҳудудларни, пляжларни жойлаш-тириш лозим'),
        (130, 13, null, null, '75.10. Курорт зоналарининг умумий фойдаланиш яшил ҳудуд- 44-бет. ШНҚ 2.07.01-03 ларининг ўлчамлари санаторийкурорт ва соғломлаштириш муассасаларида битта жой учун 100 м 2 ҳисобидан келиб чиққан ҳолда аниқланади'),
        (131, 13, null, null, '76*. Аҳолининг ҳар хил турдаги ва шакллардаги дам олишга бўлган меъёрий эҳтиёжининг меъёрий ҳисоби 11-жадвалга кўра қабул қилиш ҳисоблаш тавсия этилади.'),
        (132, 13, null, null, '77. Шаҳар ва қишлоқ аҳоли пунктларида, одатда, кўкаламзорлаштирилган ҳудудлар ва бошқа очиқ майдонларнинг узлуксиз тизимини кўзда тутиш зарур. Шаҳар ва қишлоқ аҳоли пунктлари аҳоли жойлашган ҳудудида жойлашадиган умумий фойдаланиш кўкаламзор ҳудудлари: парклар, боғлар, хиёбонлар, сайр кўчаларининг майдонини 83-84 ва 86-87 ШНҚ 2.07.01-03 45-бет бандлар талабларига мувофиқ олиниши керак.'),
        (133, 13, null, null, '78. Умумий фойдаланиш кўкаламзор ҳудудларининг бир вақтдаги мижозлари сони ҳисоби одам/га қуйидагилардан ошмаслиги лозим: шаҳар парклари учун – 150-200; турар жой туманлари парки учун – 100; дам олиш зоналари парклари ва гидропарклар учун – 50-70; сунъий ўрмонзорлар ва табиий манзара участкалари учун - 1-10.'),
        (134, 13, null, null, '79. Ўта йирик, йирик ва катта шаҳарларда, кичик шаҳар ва қишлоқ аҳоли пунктлари - туман марказларида, шаҳар ва туман миқёсидаги парклар билан бир қаторда, шунингдек, қишлоқлараро зоналар ва қисқа муддатли дам олиш марказларида ихтисослаштирилган болалар парклари (спорт, кўргазма, ҳайвонот ва бошқа) ва ботаника боғлари жойлаштирилиши мумкин, уларнинг катталиги лойиҳалаштириш топшириғига биноан олиниши лозим'),
        (136, 14, null, null, '80*. Шаҳар аҳоли пунктларидаги турар-жой ҳудудларининг яшил кўчатзорларини лойиҳалаштириш уларнинг ҳудудлари ривожланиши ва қурилишини режалаштириш лойиҳаларининг меъморий-режавий ва муҳандислик масалаларининг бутун мажмуини ҳисобга олган ҳолда, бошқа ҳудудлар ва шаҳар атрофи зоналарининг кўкаламзорлаштирилишининг ечимлари билан уйғунликда амалга оширилиши керак. Яшил кўкаламзорларнинг қуйидаги таснифига риоя қилиш лозим: - умумий фойдаланиш кўкаламзорлари; - фойдаланиши чекланган кўкаламзорлар; - махсус мақсадли кўкаламзорлар.'),
        (1577, 119, '2024-07-03 11:16:15', '2024-07-03 11:16:15', '16.4. Loyiha tegishli litsenziyasi bo‘lgan loyiha tashkiloti tomonidan ishlab chiqilmaganligi'),
        (137, 14, null, null, '81*. Қулай ва чекланган-қулай шароитларда жойлашган ўта йирик, йирик ва катта шаҳарларда бульварлар ва суғориш каналлари ёқасидаги кўкаламлашган ҳудудлар билан ягона тизимга бирлашган шаҳар, туман парклари ва турар жой тумани боғлари кўринишидаги умумий фойдаланиш кўкаламзорларининг ривожланган тизимини яратиш лозим. Ноқулай ландшафт-иқлим шароитларида хиёбонлар сонини, пиёда йўлаклар ва бульварлар узунлигини ошириш йўли билан парклар майдонини камайтиришга йўл қўйилади.'),
        (138, 14, null, null, '81.1. Курорт зоналарида ва дам олиш зоналарида жойлашган пляжлар ҳудудининг ўлчамлари битта мижоз учун м2 ҳисобида камида қуйидагича қабул қилиш зарур: дарё ва кўл..................................8 дарё ва кўл (болалар учун).......4 Қишлоқ хўжалиги учун фойдаланишга яроқли бўлган ерлардаги курорт зонаси ташқарисида жойлашган дарё ва кўл пляжлари ўлчамлари битта мижоз учун 5 м 2 ҳисобидан келиб чиқиб қабул қилиш лозим. Ҳаракати чекланган даволанувчилар учун махсус даволаш пляжлари ҳудудининг ўлчамлари битта мижоз учун 8-12 м2 ҳисобидан келиб чиқиб қабул қилинади'),
        (139, 14, null, null, '81.2. Янги лойиҳаланаётган санаторий-курорт ва соғломлаштириш муассасалари ер участкалари чегарасидан бўлган масофа м ҳисо- 46-бет. ШНҚ 2.07.01-03 бида камида қуйидагича қабул қилиш зарур: турар жой ва жамоат қурилмаларигача (курорт ва дам зонасига хизмат кўрсатишга алоқадор бўлмаган), коммунал хўжалик объектлари ва омборхоналаргача (қайта тиклаш шароитида камида 100 м)...........................500 умумий тармоқдаги темир йўлларигача.......................500 тоифали автомобил йўлларигача: IA, IБ…..................................500 IВ, II, III.................................300 IV...........................................200 V..........................................50 боғ-дала ҳовли қурилмаларигача ..............................................300. Давлат маҳаллий ва ўзгача тоифадаги алоҳида муҳофазаланадиган табиат ҳудудлари қонун йўли билан аниқланади. Алоҳида муҳофазаланадиган табиат ҳудудларининг ўлчамлари ва фойдаланиш тартиби қонун йўли билан белгиланади. Парклар, ўрмонли парклар, шаҳар ўрмонлари ҳудудида бир вақтдаги мижозлар ҳисобий сони, одам/га тарзида, кўпи билан қуйидагича қабул қилиш лозим: шаҳар парклари учун............100 дам олиш зонаси парклари учун..........................................70 курорт парклари учун...........50 ўрмон парклари учун (ўтлоқ парклари, сувли парклар) ...................................................10 шаҳар ўрмонлари учун….....1-3. Мижозлар сони бир вақтнинг ўзида 10-15 одам/га бўлганда уларнинг ҳаракатини ташкил этиш учун сўқмоқ-йўл тармоғини, ўрмон ялангликлари чеккаларида эса тупроқни ҳимояловчи кўчатларни экишни; мижозлар сони бир вақтнинг ўзида 50 одам/га ва ундан ортиқ бўлганда ўрмон ландшафтини паркга айлантириш чораларини назарда тутиш керак.'),
        (140, 14, null, null, '81.3. Шаҳар ва туман аҳамиятига эга парклари бўлган ўта йирик ва катта шаҳарларда ўлчамлари лойиҳа топшириғи бўйича қабул қилиниши зарур бўлган ихтисослашган болалар спорт, кўргазма, зоология ва бошқа парклар, ботаника боғлари назарда тутиш керак. Болалар паркининг тахминий ўлчамлари майдончаларни ва спорт иншоотларини ҳам қўшганда 0,5 м 2 /одам ҳисобидан қабул қилишга йўл қўйилади.'),
        (141, 14, null, null, '81.4. Эстетик ва маданий қимматга эга юқори даражада сақланган табиий ландшафтлар ҳудудларида миллий ва табиат паркларни шакллантириш зарур.'),
        (142, 14, null, null, '81.5. Миллий ва табиат паркларини меъморий-ҳажмий ташкил этишда уларнинг ҳудудларини, одатда, қўриқхона, қўриқхонарекреация, рекреация ва хўжалик зоналарига ажратиш билан бирга илмий, маданий-маърифий ва рекреация мақсадларида фойдаланишни назарда тутиш лозим. Парклар ва боғларни жойлаштиришда мавжуд дарахтзорлар ва сув ҳавзаларини имкони борича сақлаб қолиш зарур.'),
        (143, 14, null, null, '82. Шаҳар аҳоли пунктлари катталигини ва кўкаламзорлар майдони нормасини 12-жадвал маълумотларига кўра қабул қилиш тавсия этилади.'),
        (144, 14, null, null, '83*. Турли табиат шароитлари ҳисобга олинган шаҳар аҳоли пунктларининг ҳар хил турларидаги қурилиш элементлари бўйича умумий фойдаланиш кўкаламзорлари нормасини ҳисоблаш ва тақсимлашда 13-жадвал маълумотларидан фойдаланиш лозим.'),
        (145, 14, null, null, '84. Мавзедаги кўкаламзорларни 15-жадвал бўйича лойиҳалаштириш лозим.'),
        (146, 14, null, null, '85. Қишлоқ аҳоли пунктларидаги кўкаламзорларни лойиҳалашда табиий-иқлим шароитларини ҳудуднинг режавий тузилмаси ва уларни оқилона жойлаштиришни таъминлаш билан уйғунликда ҳисобга олиш лозим. Умумий фойдаланиш кўкаламзорлаштиришининг нормаларини 16- жадвал бўйича қабул қилиш керак.'),
        (147, 14, null, null, '86*. Паркларни маҳаллий (хўжалик ичидаги, хўжаликлараро ва ҳоказо) жойлаштириш тизимларида хизмат кўрсатиш марказлари вазифасини бажарадиган қишлоқ аҳоли пунктларида лойиҳалаштириш керак. Паркдаги турли зоналар таркиби ва катталиги лойиҳалаш топшириғига ва ШНҚ 2.07.02-07 “Инсонларнинг ҳаёти ва фаолияти муҳитини ногиронлар эҳтиёжлари ва аҳолининг кам ҳаракатланувчи гуруҳларини ҳисобга олган ҳолда лойиҳалаш” га кўра белгиланади.'),
        (148, 14, null, null, '87*. Қишлоқ аҳоли пунктларини лойиҳалашда кўкаламзорлар нормаси элементлар бўйича қайта тақсимланиши мумкин. Кўкаламзорлардан оқилона фойдаланиш мақсадида уларни марказни шакллантирувчи жамоат бинолари олдидаги хиёбонларда, турар жой гуруҳлари ва тузилмалари марказларида жойлаштирилади.'),
        (1379, 136, '2024-05-29 17:30:50', '2024-05-29 17:30:50', '5. Vazirlikning o‘z vakolatlari doirasida qabul qilgan qarorlari davlat organlari va tashkilotlari'),
        (150, 14, null, null, '89*. Кучли шамоллар, чанг тўзонлари, гармселлар ва ҳоказоларга мойил саҳро ва қўриқ ер ҳудудларида лойиҳалаштириладиган қишлоқ аҳоли пунктлари учун шамол- 50-бет. ШНҚ 2.07.01-03 нинг устивор йўналиши томонидан 50 м кенгликдаги иҳота дарахтзорлари барпо этилиши кўзда тутилиши зарур; бундай иҳотани яратиш мумкин бўлмаганда иқлимнинг салбий омилларини юмшатувчи махсус режалаштириш усуллари ва бино турларини ишлаб чиқишни кўзда тутиш керак'),
        (151, 14, null, null, '90. Аҳоли пунктларининг бинолари ва иншоотларидан ҳамда муҳандислик ободонлаштириш объектларидан бута ва дарахтларгача бўлган масофани 17-жадвалга кўра қабул қилиш лозим.'),
        (152, 14, null, null, '91. Чўл ва дашт районларида жойлашган аҳоли пунктлари учун шамолларнинг устивор йўналишини ҳисобга олиб, 304-бандга мувофиқ санитария-ҳимоя иҳота дарахтзорларини кўзда тутиш лозим бўлади'),
        (153, 14, null, null, '92.* Шаҳар атрофи ва ландшафтли-рекреация зоналарини режалаштиришда X ва XI бўлим тавсияларига мувофиқ ҳудудни муҳандислик тайёрлаш ва табиатни муҳофаза қилиш бўйича тадбирлар мажмуасини ўтказиш кўзда тутиш зарур'),
        (154, 14, null, null, '93.* Шаҳар аҳоли пунктлари шаҳар атрофи зоналарида шаҳар ва қишлоқ аҳоли пунктлари гуруҳини экиш материаллари билан таъминлашни ҳисобга олиб дарахт ва бута ўсимликларининг ҳамда гулоранжерея хўжаликлари кўчатхоналарини ташкил этиш лозим. Кўчатхоналарнинг майдони ва сони лойиҳалаштириш топшириғига кўра белгиланиши лозим. Кўчатхоналар майдони аҳолининг умумий фойдаланиш кўкаламзор ҳудудлари билан таъминланганлиги даражаси, санитария-иҳота зоналари катталиги, жамоа боғлари ва дала ҳовлили посёлкаларининг ривожланишига қараб 3-5 м 2 /киши ҳисобидан қабул қилиниши тавсия этилади. Гул – оранжерия хўжаликларининг умумий майдонини 0,4 м 2 /киши ҳисобидан қабул қилиш'),
        (155, 14, null, null, '93.1. Оммавий тарзда қисқа муддатли дам олиш учун мўлжалланган зоналарини санаторийлардан, болалар ва ўсмирлар учун дам олиш оромгоҳларидан, мактабгача санатория-соғломлаштириш муассасаларидан, боғдорчилик ширкатларидан, умумтармоқ автомобиль йўлларидан камида 500 м, дам олиш уйларидан эса камида 300 м масофада жойлаштириш зарур.'),
        (156, 14, null, null, '93.2. Ўрмон парклари, дам олиш зоналари ва курорт зоналари чегарасида автомобиллар тўхташ жойи ўлчамлари лойиҳа топшириғига асосан, агар маълумотлар йўқ бўлса, у ҳолда 9-илова бўйича аниқланади'),
        (157, 14, null, null, '93.3. Курорт зонаси табиий даволаш омилларига, ҳамда энг қулай микроиқлим, ландшафт ва санитария-гигиеник шароитларга эга бўлган ҳудудларда жойлашиши керак. Унинг ичида санаториякурорт ва соғломлаштириш муассасаларини, дам олиш ва туризм муассасаларини, умумкурорт марказини, курорт паркларини ва бошқа умумий фойдаланиш кўкаламзор ҳудудларни, пляжларни ўз ичига олган даволанувчиларга ва дам олувчиларга хизмат кўрсатиш муассасалари ва корхоналар шакллантириган жамоат марказларини жойлаштириш лозим.'),
        (158, 14, null, null, '93.4. Курорт зонаси учун ҳудудида рухсат этилган даражадаги шовқин бўлган узоқ муддатли санаторий-курорт муассасаларини ва энг камида 100 м кўкаламзор зонаси билан катталар учун муассасалардан алоҳида ажратилган болалар санатория-курорт ва соғломлаштириш муассасаларини жойлаштиришни; даволанувчиларга ва дам олувчиларга хизмат кўрсатиш билан боғлиқ бўлмаган саноат ва коммунал-омборхона объектларини, тураржой ва жамоат биноларини олиб чиқишни; транспорт ҳаракатини ва тўғри ўтувчи транспорт оқимини мустасно қилишни назарда тутиш зарур. Санатория-курорт ва соғломлаштириш муассасаларининг хизмат кўрсатувчи ходимлари яшайдиган турар жой биноларини курорт зоналаридан ташқари, ишлаш жойигача бориш учун зарур бўлган вақти сарфи 30 дақиқа атрофида бўлишини таъминлашни назарда тутиш лозим.'),
        (159, 14, null, null, '93.5. Курорт зонаси чегарасида жойлашган бир хилдаги ва ихтисослиги бўйича яқин бўлган санаториякурорт ва соғломлаштириш муассасаларини, одатда, тиббий, маданиймаиший ва хўжалик бўйича хизмат марказини таъминловчи ва ягона меъморий-ҳажмий ечимга эга бўлган мажмуаларга бирлаштириш зарур.'),
        (254, 28, null, null, '170. Қурилган ҳудудларда ер усти оммавий йўловчи транспорти йўналишларининг зичлиги йўловчи оқимларининг интенсивлигига боғлиқ ҳолда, одатда, 1,5-2,5 км/км2 қабул қилинади. Йирик ва ўта йирик шаҳарларнинг марказий туманларида бу тармоқнинг зичлигини 4,5 км/км2 гача ошириш мумкин.'),
        (1380, 136, '2024-05-29 17:30:50', '2024-05-29 17:30:50', '6. Vazirlik'),
        (160, 15, null, null, '94.* Хизмат кўрсатиш муассасалари ва корхоналари уларнинг турар-жой ва иш жойларига яқинлиги ҳамда жамоат йўловчи транспорти тармоғи билан уйғунликдаги хизмат кўрсатиш марказларини шакллантиришни ҳисобга олган ҳолда жойлаштирилади. Хизмат кўрсатиш муассасалари ва корхоналарининг сиғимини аҳоли маданий-маиший эҳтиёжларини қондиришнинг реал имкониятларини ҳисобга олиб ҳисоблаш керак. Ҳисоблар учун ижтимоий меъёрий таъминланишнинг кўрсаткичларидан фойдаланиш лозим. Хизмат кўрсатиш муассаса ва корхоналари ер участкаларининг майдонлари 3-иловада берилган. Изоҳлар:1. Хизмат кўрсатиш муассасалари ва корхоналарини ҳисоблашнинг меъёрий кўрсаткичлари муайян шароитлар учун лойиҳалаш топшириғига кўра аниқланиши керак. 2. Келтирилган ҳисоб нормалари олий ўқув юртлари, саноат корхоналари ва бошқа меҳнат қилиш жойлари ҳудудидаги хизмат кўрсатиш муассасалари ва корхоналариларни лойиҳалаштиришга тегишли эмас. 3. Мазкур бўлимда кўрсатилмаган хизмат кўрсатиш муассасалари ва корхоналарининг жойлашуви, сиғими ва ер участкалари майдони лойиҳалаштириш топшириғига кўра аниқланиши керак. 4. Тарихий ва курорт аҳоли пунктларининг хизмат кўрсатиш тармоқларини ҳисоблашда дам олишга ва даволанишга келувчи сайёҳларнинг сонини ҳисобга олиш лозим'),
        (161, 15, null, null, '95.* Турар жой бинолари қурилган ҳудудларда жойлаштириладиган оммавий маданиймаиший хизмат кўрсатиш муассасалари 18-жадвалга мувофиқ хизмат кўрсатиш доираларидаги пиёда етиб бориш яқинлигида бўлиши керак ва бунда ШНК 2.07.02-07 “Инсонларнинг ҳаёти ва фаолияти муҳитини ногиронлар эҳтиёжлари ва аҳолининг кам ҳаракатланувчи гуруҳларини ҳисобга олган ҳолда лойиҳалаш” талабларини ҳам ҳисобга олиш зарур. Қишлоқ жойларда якка-якка жойлашган оммавий талабга эга хизмат кўрсатиш объектларини марказ ҳисобланган аҳоли пунктларида, уларга 30-дақиқалик транспорт яқинлигида жойлашган аҳоли пунктларида истиқомат қилувчи аҳоли интилишини ҳам ҳисобга олиб жойлаштириш керак.'),
        (162, 15, null, null, '96. Хизмат кўрсатиш муассасалари ва корхоналарининг бинолари ва ер участкалари чегарасидан кўчаларнинг қизил чизиқлари ва бошқа биноларгача энг кам масофалари 19- жадвал* бўйича олиниши керак.'),
        (163, 15, null, null, '97.* Болалар мактабгача тарбия муассасаларининг сиғимларини маҳаллий халқ таълими идоралари белгилайдиган болаларнинг ушбу муассасалар билан қамраб олиниши кўрсаткичлари, соғломлаштириш ва махсус муассасалар учун эса соғлиқ сақлаш идоралари белгилайдиган кўрсаткичларга мувофиқ ҳисобланиши лозим. Тахминий ҳисоблар учун 20-жадвал маълумотларидан фойдаланиш мумкин. Болалар мактабгача тарбия муассасаларининг тармоғига, шунингдек, идораларга қарашли муассасалар, ширкат ва хусусий ясли-боғчалар ҳам кириши мумкин.'),
        (164, 15, null, null, '98. Соғломлаштириш ва махсус болалар муассасалари соғлиқ сақлаш идораларининг топшириғи бўйича жойлаштирилади.'),
        (165, 16, null, null, '99*. Мактаблар, гимназиялар, лицейлар, коллежларнинг ҳар хил турлари ва ҳоказоларни ичига олувчи умумтаълим ўқув юртлари болаларнинг у ёки бу таълим босқичи ўқув муассасалари билан қамраб олиниши кўрсаткичлари асосида ҳисобланиши керак. Умум-таълим мактаблари, ўқув-ишлаб чиқариш комбинатлари, мактабинтернатлардаги жойлар сони ва ер участкаларининг катталиги 21- жадвалга кўра олиниши керак.'),
        (166, 16, null, null, '99.1. Умумтаълим мактаблари синфларининг меъёрий тўлалиги кўпи билан 35 ўқувчидан (келажакда эса кўпи билан 30 ўқувчидан) иборат бўлган 9 йиллик мактаб ўқитишига умумтаълим мактаблари, ўқувишлаб чиқариш комбинатлари, мактаб-интернатлардаги жойлар сони ва ер участкаларининг катталиги 21-жадвал* ва МКМ-01-04* “Общеобразовательные школы и школы-интернаты”, ШНҚ 4.13.51-04 “Общеобразовательные школы и пришколные интернаты”'),
        (167, 16, null, null, '100. Умумтаълим ихтисослаштирилган мактаблар (математика, тиллар), гимназиялар, лицейлар, коллежлар, шунингдек, ширкат ва хусусий ўқув муассасаларининг сиғимлари жойларнинг умумий сони ҳисобидан, уларни жойлаштириш эса лойиҳалаштириш топшириғи бўйича қабул қилинади.'),
        (168, 16, null, null, '100.1. Мактабларга етиб бориш меъёрларини таъминлашнинг иложи бўлмаганда мактаб-интернатлар ва мактаб қошида интернатлар (ётоқхоналар) ташкил этиш зарур. Мактаб-интернатлар ва мактаб ёнидаги интернатлар ҳажми аниқ шароитлар бўйича лойиҳалаш топшириғида белгиланади.'),
        (169, 16, null, null, '100.2. Квартал ичи қурилиши шароитида яшил дарахтзорлар ўтқазишни ҳисобга олиб мактаб биносини квартал ичи йўлагидан 6 м гача масофада жойлаштиришга йўл қўйилади.'),
        (170, 16, null, null, '100.3. Мактабни капитал таъмирлашда ва қайта қуришда мавжуд биноларни сақлаш ва мослаштириш сабабини асослашни тузиш, ўқувчилар сони эса уларнинг сиғимлигини ҳисобга олган ҳолда аниқлаш, қурилишга ажратилган ҳудуднинг чекланганлик шароитида эса спорт майдончаларини ва кўкаламзорларини кичрайтириш ҳисобидан ШНК 2.07.01-03 57-бет мактаб бинолари участкаларини 20% ортиқ бўлмаган ҳолда ва лойиҳалаш топшириғига мувофиқ қисқартиришга йўл қўйилади'),
        (386, 34, null, null, '291. Дренаж тизимларини ҳисоблаш ва лойиҳалаш ишлари махсус тадқиқотларга асосланган ҳолда ҚМҚ 1.02.07-97 «Қурилиш учун муҳандислик тадқиқотлари»га мувофиқ амалга оширилиши зарур.'),
        (171, 16, null, null, '100.4. Шаҳар посёлкалари мактаблари учун маҳаллий шароитдан келиб чиққан ҳолда ўғил болалар ва қиз болалар учун ҳовлида қўл ювиш жойи бўлган ва алоҳида турган ўрали ҳожатхоналарни назарда тутиш мумкин. Овқатланиш блоки, ахборот ресурслари маркази ва фаоллар зали биноларининг соддалашган таркибини, назарда тутишга рухсат этилади, катта бўлмаган спорт залли мактаблар учун эса йўриқчи ва анжомлар хоналарини қўшишга йўл қўйилади'),
        (172, 16, null, null, '100.5. Мактабдан ташқари муассасалар сиғими 22-жадвалга кўра ҳисобланиши керак; ўзлаштириш навбати ва участкалар майдони маҳаллий шароитларга кўра қабул қилиниши керак. Мактабдан ташқари муассасаларни болаларга асосан турар жойига пиёда яқинликда хизмат кўрсатишни ҳисобга олиб жойлаштириш керак. Йирик мактаблар негизида болалар маданий-қордиқ ва жисмоний-соғломлаштириш марказларини яратиш керак Ёш техниклар, сайёҳлар ва табиатчилар станциялари, одатда, шаҳар аҳамиятидаги болалар қордиқ марказлари таркибида жойлаштирилади. Қишлоқ жойларида мактабдан ташқари муассасалар элементлари аҳоли пунктларининг мактаблари ва клублари таркибида кўзда тутилиши лозим. Марказий аҳоли пунктларида аҳоли пунктлари гуруҳи ҳисобидан болалар ҳордиқ марказлари кўзда тутилиши керак. '),
        (173, 17, null, null, '102. Даволаш-профилактик муассасалари сиғими ва участкалари катталигининг меъёрий ҳисоб кўрсаткичлари соғлиқ сақлаш идоралари маълумотларга кўра аниқланиши керак. Мўлжал ҳисоб кўрсаткичлари 23-жадвал* бўйича қабул қилиниши керак. Аҳоли пунктлари учун пунктлараро алоқаларни ҳисобга олган ҳолда шифохона ва поликлиникалар учун фарқлама ҳисоб кўрсаткичлари 24-жадвалда келтирилган. Турли тоифа дорихоналари хизмат кўрсатиладиган аҳоли сони ҳисобидан 25-жадвалга кўра олиниши керак'),
        (174, 17, null, null, '102.1. Даволаш муассасаларини тасдиқлаш бош режа ва аҳоли пунктининг мукаммал режасига мувофиқ унинг аҳоли жойлашган, кўкаламлаштирилган ёки шаҳар атрофии функционал зоналарининг санитария-гигиеник талаблари бўйича энг қулай бўлган ерларига жойлаштириш зарур.'),
        (175, 17, null, null, '102.2. Йирик ихтисослашган касалхоналар (руҳий, сил касаллиги, юқумли касаллик, онкологик, теританосил касаллиги ва бошқалар) ёки мажмуалар аҳоли жойлашган ҳудуддан камида 500 м масофани сақлаган ҳолда имкони борича кўкаламлашган массивлари бор бўлган шаҳар атрофи зонасида ёки чекка ерларда жойлаштириш лозим.'),
        (176, 17, null, null, '102.3. Участка танлашда атрофдаги санитария ҳолатни ва шамолнинг устивор йўналишини ҳисобга олиш зарур. Касалхона муассасаларини аввал ахлатхона, ахлат ва нажаслардан тозалаш майдонлари, мол-ҳайвонлар кўмилган жойлар, қабристонлар ва ҳ.к., шунингдек, органик, кимёвий ва бошқа чиқиндилар учун фойдаланадиган участкаларда жойлаштириш қатъиян ман этилади.'),
        (177, 17, null, null, '102.4. Даволаш муассаси ҳудудидан шаҳар аҳамиятига эга магистраль муҳандислик коммуникацияларини (сув таъминоти, оқова, иссиқлик таъминоти, электр таъминоти) ўтказишга йўл қўйилмайди. Аҳоли пунктининг турар-жой зонасига касалхона ва ҳомиладорларга тиббий ёрдам кўрсатувчи муассасаларни жойлаштиришда даволаш-профилактика муассасалари биноларининг қаватлилигини ҳисобга олган ҳолда даволаш ва палатали бинолари қурилмалар қизил чизиғидан 30 м дан ва турар жойлардан 30-50 м яқин бўлмаган ҳолда жойлаштириш лозим.'),
        (178, 17, null, null, '102.5. Даволаш муассасалари ҳу-дудларига қаттиқ қопламали қулай кириш йўлларини қуриш зарур. Ички ўтиш йўллари ва пиёдалар йўлаклари ҳам шунга ўхшаш қаттиқ қопламага эга бўлиши керак. Касалхона участкасида даволаш бинолари ва бошқа бинолар оралиғидаги санитария узилишлар қуёш тушиши, ёритиш, шамоллатиш, шовқиндан ва чангдан ҳимоя қилиш-нинг ва ҳ.к. оптимал шароитларини таъминлаш зарур. Аҳолининг касалхона билан қулай транспорт алоқасини назарда тутиш лозим. Даволаш муассасалари бинолари олти қаватдан юқори қилиб лойиҳаламаслик зарур. Аҳолиси 10 минг киши ва ундан кўп бўлган ШНК 2.07.01-03 63-бет шаҳарларда поликлиникалар мўлжалланиши лозим. Палатали бўлимлари бўлган касалхона биноларидан, туғруқхоналар биноларидан ва стационарли диспансерлардан қурилмалар қизил чизиғигача, шунингдек турар жой уйларигача бўлган масофа камида 30 м, касалхона даволашдиагностика биноларидан, шунингдек поликлиникалар, аёллар маслаҳатхоналари ва стационарсиз диспансерлардан эса камида 15 м масофада бўлиши зарур.'),
        (179, 17, null, null, '102.6. Касалхоналар ва стационарли диспансерлар участкаларидаги қурилиш зичлиги 12-15% атрофида бўлиши керак. Қолган ҳудудлар ободонлаштирилган ва кўкаламзорлаштирилган бўлиши зарур. Кўкаламзор ва майсазорларга ажратилган майдон участка умумий майдонининг камида 60% ташкил этиши лозим. Боғ-парк зонасининг майдони бир койкага камида 25 м 2 бўлиши керак. Касалхоналар ва поликлиникалар участкаларининг периметри бўйлаб эни, мос равишда, 10 ва 15 м бўлган икки қатор қилиб ўтказилган юқори танали дарахтларни ва бутазорларни назарда тутиш лозим.'),
        (180, 19, null, null, '103. Жисмоний тарбия-спорт иншоотлари аҳоли пунктларининг барча турар-жой ҳудудларида, шунингдек, шаҳар атрофидаги яшил зоналарда жойлаштирилиши керак. Жисмоний тарбия-спорт иншоотлари жойлашуви, мўлжалланган ҳисоб кўрсаткичлари, участкаларининг майдони 26-жадвалда келтирилган. Тармоқнинг ҳисоб кўрсаткичлари маҳаллий шароитлар хусусиятларини ҳисобга олиб амалга оширилиши керак.'),
        (181, 19, null, null, '104. Маданият ва санъат муассасаларини, одатда, уларнинг ижтимоий-шаҳарсозлик аҳамиятини ва жамоат марказининг меъморий қиёфасини шакллантиришдаги ролини ҳисобга олиб, жамоат марказларида жойлаштириш керак. Уларнинг жойлашуви, ҳисобий кўрсаткичлари ва участка майдонлари 27-жадвалга мувофиқ қабул қилинади.'),
        (182, 19, null, null, '105. 10 минг кишигача бўлган аҳоли пунктларида клубларнинг ҳажми ва участкаларини пунктлараро вазифалари ҳисобидан (30- дақиқалик яқинлигида) маҳаллий шароитларга кўра (лойиҳалаш топшириқлари бўйича) аниқланиши керак. Мўлжал ҳисоблари учун қабул қилиш мумкин: аҳоли пункт - мижоз жойлари, минг киши сони 1,0 гача 300 1 дан 2,0 гача 250-200 ШНК 2.07.01-03 67-бет 2 дан 5,0 гача 200-190 5 дан 10,0 гача 190-140 10 дан 20,0 140-80 106. Қишлоқ ахоли пунктларида кутубхоналарни, одатда, клублар таркибида, лойиҳалаш топшириқларига кўра жойлаштириш керак;'),
        (183, 19, null, null, '106. Қишлоқ ахоли пунктларида кутубхоналарни, одатда, клублар таркибида, лойиҳалаш топшириқларига кўра жойлаштириш керак; тахминий ҳисобий кўрсаткичи – 30- дақиқалик яқинликда 1 минг кишига 3-5 минг сақлаш бирлиги ва 2-5 ўқувчи жойи (кичик аҳоли пунктлари учун каттароқ ҳисобий кўрсаткичи олинсин).'),
        (184, 20, null, null, '107. Савдо, умумий овқатланиш ва маиший хизмат кўрсатиш корхоналарининг ҳисоб кўрсаткичлари ва участка ўлчамлари 28-жадвал* бўйича олиниши лозим. Савдомаиший хизмат кўрсатиш корхоналар тармоғига турли идораларга қарашли, шу жумладан давлат ва хусусий корхоналар киради. Ҳисобий кўрсаткичлар белгиланган тартибда маҳаллий шароитларни ҳисобга олиниб аниқланиши керак. Савдо, умумий овқатланиш ва маиший хизмат кўрсатиш корхоналарини, одатда, мажмуаларга бирлаштириб, улардан фойдаланиш тезлигига қараб шаҳар ва қишлоқ аҳоли пунктлари таркибий қисмлари жамоат марказлари ичида жойлаштириш керак'),
        (185, 20, null, null, '107.1. Бозор мажмуаларини, дўконларини ва иморатларини шаҳар ва қишлоқ аҳоли пунктлари аҳолисига бир хил хизмат кўрсатишни таъминлашни ҳамда қулай транспорт алоқалари билан боғлаш- 70-бет. ШНҚ 2.07.01-03 ни ҳисобга олиб жойлаштириш зарур'),
        (186, 20, null, null, '107.2. Шаҳарлар ва қишлоқ аҳоли пунктларининг бош режасини лойиҳалашда ва қайта тиклашда Ўзбекистон Республикаси Давлат архитектура ва қурилиш қўмитасининг 2008 йил 2-январь 1-сон буйруғи билан тасдиқланган «Ўзбекистон Республикаси шаҳарлари ва қишлоқ аҳоли пунктларида бозорларни лойиҳалаштириш бўйича тавсияномалар” талабларига амал қилиш лозим.'),
        (187, 20, null, null, '107.3. Катта бўлмаган бозорлар маҳсулотларни бевосита истеъмолчиларга чаканалаб сотишни ҳисобга олган ҳолда шаҳарнинг марказий туманларига жойлаштириш тавсия этилади. Қувват 10-30 савдо ўрнига эга бўлган жуда кичик бозорларни (бозор павильонларини) маҳалла марказларига ва аҳолиси 5 минг кишигача бўлган аҳоли пунктларида жойлаштириш зарур. Қуввати 50-100 савдо ўрнига эга бўлган ва тўрт хил савдодан (қишлоқ хўжалик маҳсулоти бўлган деҳқон; озиқ-овқат маҳсулотлари; саноат моллари ва хўжалик мол-лари) ташкил топган микроту-манларда 2 км гача ва аҳолиси 5 мингдан 25 минггача бўлган турар-жой массивларида ажратилган ҳолда алоҳида-алоҳида жойлаштириш лозим. Қуввати 150-400 савдо ўрни ва 5-7 км хизмат кўрсатиш радиусига эга 100-150 минг кишига мўлжалланган бозор бирлашмалари ва бозор мажмуаларини (қишлоқ хўжалик маҳсулотлари бўлган деҳқон; озиқовқат; буюм; хўжалик моллари) ажратилган ҳолда алоҳида-алоҳида жойлаштириш зарур'),
        (188, 20, null, null, '107.4. Қишлоқ хўжалик маҳсулотларини сотиш жойларини экологик ва гигиеник мулоҳазасига кўра ёпиқ майдончаларда ва расталарда, қолганларини эса павильонларда жойлаштириш лозим. Деҳқон бозорлари сотиш расталари саноат товарлари ва хўжалик моллари магазинлари билан тўлдирилиши ҳамда улар орқали асосий ва иккинчи даражали магистраль кўчалар ўтказиш йўл қўйилмайди. Кичик ва ўрта бозорларда саноат товарлари учун алоҳида сотиш ўринларини жойлаштиришга йўл қўйилади.'),
        (189, 20, null, null, '107.5. Қуввати 600-1200 савдо ўрнига эга катта деҳқон ва буюм бозорларини катта шаҳарларда жойлаштириш зарур. Зарур қурилиш материалларини, автомобиль эҳтиёт қисмларини, паррандаларни сотиш бўйича ихтисослашган аралаш ўртача бозорларни шаҳар чеккасига жойлаштиришга йўл қўйилади. Автомобиль ларни, қурилиш материалларини, ҳайвонларни сотиш бўйича катта бозорлар шаҳар аҳолиси жойлашган ҳудуддан ташқарида, иккинчи даражали кириш йўлларидаги алоҳида ҳудудларга жойлаштириш лозим.'),
        (190, 22, null, null, '108.* Бошқариш, молия ташкилотлари ва муассасалари ҳамда алоқа корхоналари, одатда, шаҳар-лар, режалаштириш, турар жой ва саноат районларининг жамоат марказларида жойлаштирилиши керак. Мазкур муассасалар ер участка- ШНК 2.07.01-03 71-бет ларининг ҳисоб кўрсаткичлари ва ўлчамлари 29-жадвалга кўра олиниши лозим'),
        (191, 22, null, null, '109. Коммунал хўжалик корхоналари ер участкаларининг жойлашуви, ҳисоб кўрсаткичлари ва ўлчамларини 30-жадвал бўйича қабул қилиш лозим. Ўт ўчириш деполарини ёнғин муҳофазаси ҳудудий идоралари топшириғи билан ҳисоблаш ва жойлаштириш керак. Ҳисоб кўрсаткичларини 30-жадвал бўйича олиш керак. Хизмат кўрсатиш доирасини умумий фойдаланиш йўллари бўйича ўлчаб 3000 м деб олиш керак. Умумий турдаги меҳмонхоналар ва мотелларни лойиҳалаш топшириғи бўйича ҳисоблаш ва жойлаштириш керак.'),
        (192, 23, null, null, '110. Санаторий-курорт, соғломлаштириш, дам олиш ва сайёҳлик муассасаларининг сиғими тегишли идоралар берган маълумотлар асосида ва лойиҳалаш топшириғи бўйича ҳисобланади. Мўлжалий кўрсаткичлар 31-жадвал бўйича олиниши зарур.'),
        (193, 23, null, null, '111. Санаторий-курорт, саломатлик, дам олиш ва сайёҳлик муассасалари учун жой танлашда қуйидагиларни ҳисобга олиш керак: табиий даволаш омиллари (санаторий-курорт муассасалари учун); табиий шароитлар хусусиятлари, жумладан маданий омиллар, шунингдек, сайёҳлик ва сайр учун қизиқ бўлган объектлар мавжудлиги; санитария-гигиеник шароитлар; ижтимоий инфратузилманинг зарур қисмлари билан таъминлаш талаблари.'),
        (194, 24, null, null, '112*. Олий, ўрта махсус ва касбҳунар ўқув юртлари аҳоли пунктларида махсус ишланмалар бўйича тегишли вазирликлар ва идоралар топшириғига кўра жойлаштирилади. Ўқув юртларининг ўқитувчилар ва хизмат кўрсатиш ходимлари шаҳар ташкил этувчи гуруҳга киради'),
        (195, 24, null, null, '112.1. Ўқитиш муддати 3-йиллик бўлган ўрта махсус ва касб-ҳунар ўқув юртлари 9-йиллик умумтаълим мактабларини тугатган ўқувчиларнинг ўрта таълимини тугатиши билан бир вақтнинг ўзида академик лицейларда ихтисослашган, чуқурлаштирилган таълимни ва ҳар хил йўналишдаги коллежларда касб-ҳунар таълимини олишга мўлжалланган'),
        (196, 24, null, null, '112.2. Ўрта махсус ўқув юртларидаги ўринларнинг ҳисобий сони умумтаълим мактабларининг 9-синфини тугатган ўқувчиларни 100% қамраб олишга бўлган келажакдаги талабларга мувофиқ мос келиши зарур. Биринчи навбатда камида 75% қамраб олишни назарда тутиш лозим (демографик ҳолатни ҳисобга олиб, 15-17 ёшли ўсмирларни қам-раб олишни ҳисоблаганда). Ўқув юртларининг ўқитувчилар ва хизмат кўрсатиш ходимлари шаҳар ташкил этувчи гуруҳга киради. Ҳисоблашларда ИКН-1-05 “Средние специальные учебные заведения – академические лицеи и профес-сиональные колледжи” ва ШНК 4.13.52-04 «Академические лицеи и профессиональные колледжи» меъёрий ҳужжатларига амал қилиш зарур.'),
        (197, 24, null, null, '113*. Турли йўналишдаги ўқув юртлари учун ер участкалари майдони ўқувчи ва талабалар сонига боғлиқ ҳолда 32-жадвалда келтирилган. Ўрта махсус ўқув юртлари, академик лицейлар ва касб-ҳунар коллежларининг участкалари қайта қуриш зоналари шароитида 20 % га, олий ўқув юртлари, асосан, гуманитар йўналишдаги - 30-40 % га камайтирилиши мумкин. Қишлоқ хўжалиги йўналишидаги коллежларнинг ўқув хўжаликлари (тажриба участкалари) ўқув биноларидан узоқда ва 50 га гача, лекин камида 20 га майдонда жойлаштириш мумкин.'),
        (198, 25, null, null, '114. Қариялар, уруш ва меҳнат фахрийлари, курси-аравада ҳаракатланувчи ногиронлар учун интернатуйлар сиғимини ҳисоблаш ижтимоий таъминот идоралари маълумотлари, физиологик нуқтаи назарда заиф бўлган ногиронлар ва руҳий хаста (психоневрологик) беморлар учун интернат-уйлар – соғлиқ сақлаш идоралари маълумотлари асосида олиб борилиши керак. Муассасаларнинг сиғими, жойлашуви ва участкалар ўлчами лойиҳалаш топшириқлари билан белгиланади. Мўлжалий ҳисоб кўрсаткичлари ва участка ўлчамлари 33-жадвал бўйича қабул қилинади.
'),
        (199, 26, null, null, '115. Жойлаштириш тизимлари шаҳар марказларида хизмат кўрсатиш муассасалари ва корхоналари ҳажмини ҳисоблашда уларнинг таъсир доирасида жойлашган кичик шаҳар ва қишлоқ пунктларидан мойил аҳолини қўшимча ҳисобга олиш керак: ўта йирик, йирик ёки катта шаҳарда – 2 соатгача; кичик ва ўрта шаҳарда – 1 соатгача. Мўлжалий ҳисоблар учун 34- жадвал маълумотларидан фойдаланиш мумкин.'),
        (200, 26, null, null, '116. Аҳоли пунктлари кўчалари, оммавий йўловчи транспорти, ташқи транспорт иншоотлари, транспорт воситаларига хизмат кўрсатиш иншоотлари тармоқларини жойлаштириш тизимида энг кам вақт ва иқтисодий сарфлар билан юк ва йўловчиларни ташиш имконини таъминлайдиган ягона тизим шаклида кўзда тутиш лозим'),
        (201, 26, null, null, '117.* Аҳоли пунктининг транспорт тизимини лойиҳалашда унинг босқичма-босқич, транспорт тизимининг яхлитлигини ва аҳоли пункти ривожланишининг ҳар бир босқичида кутиладиган транспорт ва йўловчилар оқимларини ўтказишни таъминлайдиган ривожланишини кўзда тутиш мумкин. Қабул қилинган транспорт тизими тубдан қайта қурилмаганда ҳам аҳоли пунктининг ҳисоб муддатидан кейинги ўсиши имконини таъминлаши керак'),
        (202, 26, null, null, '118. Транспорт тизими келажакдаги кўча ҳаракати оқимлари ва йўловчилар оқимлари ҳақидаги ҳисоб маълумотлари асосида лойиҳаланиши керак.'),
        (203, 26, null, null, '119*. 100 минг кишидан ортиқ шаҳар аҳоли пунктлари учун кўча ҳаракати оқимлари ва йўловчилар оқимлари миқдори ҳисоблар асосида аниқланади. Магистраллар схемасини танлаш, кўчалар ўлчамини ва кўндаланг кесими элементларини белгилаш, оммавий йўловчилар транспорти умумий схемасини ва айрим турларини танлаш турларини таққослаш асосида ўтказилиши керак'),
        (204, 26, null, null, '120*. Кўча ҳаракати оқимлари, автомобиль сақлаш жойлари сони, шунингдек, шаҳарга бошқа аҳоли пунктларидан келаётган автомобиллар сонини маҳаллий давлат идоралари маълумотига кўра ҳисобланиши лозим. Аҳолиси 100 минг кишидан ортиқ бўлган шаҳар аҳоли пунктларида юк автомобиллари сони ва уларнинг кўчаларда ҳаракатининг кўплиги ҳисобланиб белгиланади. Камроқ аҳолиси бўлган шаҳар аҳоли пунктлари учун юк автомобиллари сони 1000 кишига 25-40 автомобиль нормасидан келиб чиқиб белгиланади.'),
        (205, 26, null, null, '121*. Яшаш жойидан иш жойигача бориш учун сарфланадиган вақт 90% меҳнаткашлар учун (бир томонга) қуйидагидан ошмаслиги зарур, дақ; қишлоқ аҳоли пунктлари учун – 30; сони қуйидагича бўлган шаҳар аҳоли пунктлари учун: 100 минг кишигача - 30; 101 дан 250 минг кишигача - 35; 251 дан 500 минг кишигача - 40; 501 дан 1000 минг кишигача - 45. 1000 минг кишидан ортиқ бўлган шаҳар аҳоли пунктлари ва агломерациялар учун энг кўп вақт сарфлари 55 дақиқагача бўлиши керак.'),
        (206, 27, null, null, '122*. Темир йўл, автомобиль ва ҳаво транспортининг йўловчи вокзалларини аҳоли пунктининг маркази билан, вокзаллараро, турар жой ва саноат зоналари билан ва ташқи автомобиль йўллари билан транспорт алоқаларини таъминлаган ҳолда жойлаштириш керак. Аэропортларида йўловчи ўтиши йилига 2 млн. дан ошадиган шаҳар аҳоли пунктларида, шунингдек, жойлаштириш тизимлари марказларида аэропортлар бирлашмасини барпо этганда, аэровокзаллар қурилишини кўзда тутиш лозим.'),
        (207, 27, null, null, '123. Темир йўл умумий тармоғининг янги сортловчи бекатларини шаҳар аҳоли пунктларидан ташқарида, янги техник йўловчи бекатлари, захирадаги кўчма таркиб корхоналари, юк бекатлари, контейнер майдончалари ва юклар узоқ сақланадиган янги омбор бинолари – аҳоли жойлашган ҳудуд ташқарисида жойлаштириш лозим. Аҳоли жойлашган ҳудудлар ичида бўлган юк бекатлари ҳудудида жойлашган узоқ вақт сақланадиган тахланмайдиган юклар омборлари ва майдончалари коммунал-омбор зоналарига кўчирилиши лозим.'),
        (1578, 119, '2024-07-03 11:16:15', '2024-07-03 11:16:15', '16.5. Binolar va inshootlarning mustahkamligi, zilzilabardoshliligiga ta’sir qiluvchi yuk ko‘taruvchi qismlarining o‘zgarishi nazarda tutilganligi'),
        (209, 27, null, null, '125*. Темир йўлларнинг ҳар хил сатҳларда кесишувини 1, II тоифадаги йўллар учун – аҳоли пунктларидан ташқарида, III, IV тоифадаги йўллар учун – аҳоли жойлашган ҳудудлардан ташқарида кўзда тутилиши керак. Аҳоли пунктларининг ҳудудида темир йўлларнинг кўчалар ва автомобил йўллари, шунингдек оммавий электр йўловчи транспорти йўналишлари билан бир сатҳда кесишувлари ШНҚ 2.05.02-07 «Автомобиль йўллари» ва ҚМҚ 2.05.09-97 «Трамвай ва троллейбус йўналишлари» талабларига мувофиқ кўзда тутилади.'),
        (210, 27, null, null, '126*. Турар жой қурилмалари темир йўлдан, четки темир йўл ўқидан бошлаб, 100 м кенгликдаги санитария-ҳимоя зонаси билан ажратилиши керак. ҚМҚ 2.01.08-96 «Шовқиндан ҳимояланиш» талабларига мувофиқ махсус шовқиндан ҳимоя тадбирлари таъминланганда санитария-ҳимоя зонасининг эни кўпи билан 50 м га камайтирилиши мумкин. Сараловчи бекатлардан турар жой қурилмаларигача масофалар юк айланиши, ташиладиган юкларнинг портлаш хавфи, шунингдек, йўл қуйиладиган шовқин ва вибрациялар даражасини ҳисобга олган ҳолда ҳисоблаш асосида қабул қилинади. Темир йўлдан боғ участкаларигача бўлган санитария-ҳимоя зонасининг эни камида 50 м бўлиши керак.'),
        (211, 27, null, null, '127. Санитария-ҳимоя зонасида, темир йўл тармоқланишидан ташқарида автомобиль йўллари, гаражлар, автомобиллар тўхташ жойлари, автомобилларга хизмат кўрсатиш корхоналари, омборлар ва коммунал-маиший муассасаларини жойлаштириш мумкин. Санитарияҳимоя зонаси майдонининг камида 50% кўкаламзорлаштирилиши керак'),
        (212, 27, null, null, '128*. Умумий тармоқнинг I, II ва III тоифадаги автомобиль йўллари, аҳоли пунктларини айланиб ўтадиган қилиб лойиҳалаш зарур. Ушбу йўллар ер тўшамаси ёқасидан қурилмаларгача масофалар ер ёни концентрацияларни (РД 118.0027714. 19-92 «Табиат муҳофазаси. Табиий муҳитни ифлослантирувчи чиқиндиларни ташлаш ва ахлатларни жойлаштириш учун тўловларни белгилаш бўйича услубий кўрсатмалар», Ўзбекистон Республикаси Давтабиатқўмитаси; «Автомобиль транспортидан зарарли моддалар чиқарилишини ҳисоблаш бўйича услубий кўрсатмалар», А.И.Воейков номли ГГО, 1984) ва шовқин даражаларини (мазкур ҳужжатнинг «Шовқин, вибрациялар, электромагнит майдонлар ва радиациядан сақланиш» бўлими), ҳисоблаш асосида олиниши керак, лекин у камида: қурилмалар чизиғигача – 100 м, боғдорчилик ширкатларигача – 50 м, III тоифали йўллар учун мувофиқ равишда 50 ва 25 м га тенг қабул қилиниши лозим. Қурилишларни автомобиллар шовқини ва чиқинди газларидан сақлаш учун йўл ёқалаб камида 10 м кенгликдаги дарахтзор яратилиши кўзда тутилади. Умумий тармоқ йўллари аҳоли пунктлари орқали ўтганда уларни шаҳар ёки қишлоқ кўчалари деб ҳисоблаб, ушбу нормалар талабларини ҳисобга олиб лойиҳалаштириш зарур'),
        (213, 27, null, null, '129*. Аэродромлар ва вертолётдромларни ҚМҚ 2.05.08-97 «Аэродромлар»га мувофиқ аҳоли жойлашган ҳудуд ва аҳолининг оммавий дам олиш зоналаридан учишларнинг хавфсизлигини ва аҳоли жойлашган ҳудуд учун санитария нормалари билан белгиланган мумкин бўлган  авиация шовқини ва электромагнит нурланиши даражаларини таъминлайдиган масофада жойлаштириш лозим. Кўрсатилган талабларга, шунингдек амалдаги аэропортлар зонасида янги аҳоли жойлашган ҳудудларни ва оммавий дам олиш зоналарини жойлаштиришда ҳам риоя қилиш лозим.'),
        (214, 27, null, null, '130. Аэродромлар чегараларидан аҳоли жойлашган ҳудудгача масофалар, уларнинг келажакдаги ривожланишини кўзда тутиб, 35- жадвал бўйича қабул қилиниши керак.'),
        (215, 27, null, null, '131. Учишларнинг хавфсизлигини таъминлаш мақсадида аэродромлар районларида объектларнинг жойлашуви аэродромлар қарашли бўлган корхоналар ва ташкилотлар билан келишилиши керак (7-илова)'),
        (216, 27, null, null, '132. Дарё портлари аҳоли жойлашган ҳудуддан ташқарида, турар жой қурилмаларидан камида 100 м масофада жойлаштирилиши керак. Янги дарё портларининг ихтисослашган районлари чегарасидан турар жой қурилмаларигача масофалар олиниши камида: қайта юклаш ва чанг қилувчи юкларни сақлаш жойлари чегарасидан – 300 м; енгил ёнувчан ва ёнилғи суюқлик сиғимлари ва қуйиш-олиш қурилмалари бўлган омборлардан, тоифасига қараб қуйидагича белгиланади: I – 200 м II ва III - 100 м'),
        (217, 27, null, null, '133. Юк туманларининг соҳил кенглиги қуйидагилардан ошмаслиги керак: дарё портлари учун – 300 м, пристанлар учун – 150 м, оммавий юкларни қайта юклаш ва навигациялараро сақлаш учун мўлжалланган ихтисослашган дарё портлари учун – 400 м. Тегишли асослашда ҳудуднинг мазкурнинг эни оширилиши мумкин.'),
        (218, 27, null, null, '134*. Тез ёнувчан ва ёнилғи суюқликлар омборларини дарё порти туманлари хўжалик-ичимлик сувини олиш фильтрлари, турар жой қурилмалари, аҳоли оммавий оромгоҳлари, пристанлар, дарё вокзаллари, кемалар туриш жойлари, гидроэлектростанциялар, саноат корхоналари ва кўприклардан камида 500 м қуйироқда жойлаштириш зарур. Уларнинг бу объектлардан тепароқда, I тоифа омборлари учун камида 5000 м, II ва III тоифадагилар учун эса – 3000 м масофада жойлашувига йўл қўйилади.'),
        (220, 28, null, null, '136. Аҳоли пунктлари кўчалари тизимининг режавий ечимлари аҳоли пунктларининг барча функционал зоналари орасида қулай алоқалар, марказий ва тарихий зоналаридан автомобиллар транзит оқимларини четлатиш, юк ва енгил автомобиллар оқимлари ажратилишини таъминлаши лозим. Кўча тармоғи шаҳарга ташқи автомобиль йўлларидан қулай киришни таъминлаши керак.'),
        (221, 28, null, null, '137. Магистраль кўча тармоғининг ўртача зичлиги аҳоли пункти қурилмалари ҳудудида кўрилаётган ҳудуднинг чегарасидан ўтувчи кўчалар узунлигини ҳисобга олиб, 2,2-2,4 км/км2 доирасида бўлиши керак.'),
        (222, 28, null, null, '138. Қайта қурилаётган аҳоли пунктлари марказий қисмларининг капитал бинолар қурилган кўчаларнинг қатнов қисми тор бўлганда, параллель кўчалар орасида масофа 350 м дан ошмаганда, бир томонлама ҳаракат кўзда тутилади. Қайта қуриш шароитида фақат оммавий йўловчи транспорти ва пиёдаларни ўтказиш мўлжалланган кўчаларни ҳам барпо этиш мумкин.'),
        (223, 28, null, null, '139*. Кўчалар ва йўллар тоифалари ҳаракати интенсивлиги, транспорт қатновининг тезлиги ва таркиби, шунингдек функционал вазифасига мувофиқ қабул қилинади. Аҳоли пунктлари кўча-йўл тармоғининг таснифи ва асосий таърифлари 36- ва 37-жадвалда берилган. Режа ва кўндаланг кесими элементлари ШНҚ 2.05.02-07 «Автомобиль йўллари»га мувофиқ қўлланилади. Мураккаб рельеф ёки қайта қуриш шароитида ҳаракатнинг ҳисобланган тезлигини пасайтириш ва шаҳар кўча ва йўллари режаси ва кўндаланг кесими таърифини 38- жадвалга кўра олиш мумкин бўлади. Қишлоқ аҳоли пунктлари кўча ва йўллари таснифини 39-жадвалга мувофиқ олиш керак'),
        (224, 28, null, null, '140*. Турли транспорт воситалари ҳаракатининг интенсивлигини ҳисоблашда уларни қуйидаги коэффициентларни қўллаш билан бир ҳисоб автомобилига келтириш лозим: енгил автомобиллар - 1 юк автомобиллари, юк кўтариши, т: 2,0 гача - 1,5 2,0 дан 5 гача - 2,0 5 дан 8 гача - 2,5 8 дан ортиқ - 3,5 автопоездлар – ШНҚ 2.05.02-07 «Автомобиль йўллари»га мувофиқ автобуслар – 2,5 троллейбуслар – 3,0 улама автобуслар и троллейбуслар, трамвайлар – 4,0 мотоцикл, мопедлар – 0,5'),
        (225, 28, null, null, '141. Кўча қатнов қисмида бир ҳаракат йўлагининг транспорт воситаларини ўтказиш имконияти транспорт турлари, ҳаракатнинг ҳисобланган тезлиги, узунасига қиялиги, ҳаракат йўллари сони, транспорт воситалари бир ҳаракат йўлагидан иккинчисига чап ёки ўнг бурилиш учун ўтиши тезлигига боғлиқ ҳисобланади. Илк ҳисоб-китоблар учун кўча қатнов қисми бир йўлагининг ўтказиш имкониятлари турли сатҳларда кесишганда - 1200-1500 келтирилган авт/соат, бир сатҳида - 750-850 келтирилган авт/соат миқдорида қабул қилинади.'),
        (226, 28, null, null, '142. Тўхтовсиз ҳаракат магистраллари ва юк йўллари енгил ва юк автомобилларининг катта оқимлари ўтишига мўлжалланган. Уларни қуриш зарурати кўча ҳаракати оқимларини ҳисоблаш билан асосланади'),
        (227, 28, null, null, '143. Аҳоли пунктларида тўхтовсиз ҳаракат магистраль кўчалари энг кучли транспорт оқимлари йўналишларида кўзда тутиш керак. Тўхтовсиз ҳаракат магистраллари йўлини аҳоли пункти марказидан четда лойиҳалаштириш зарур'),
        (228, 28, null, null, '144. Тўхтовсиз ҳаракат магистралларида марказий қатнов қисмлари ва бир томонлама ҳаракатли маҳаллий ўтишларнинг барпо этилишини кўзда тутиш керак. Тўхтовсиз ҳаракат магистральлари ўтишларининг эни аҳоли пункти ичида кўпи билан 11,25 м, аҳоли пунктидан ташқарида - 7,5 м ни ташкил этади.'),
        (229, 28, null, null, '145.Тўхтовсиз ҳаракат магистраллари қатнов қисми четидан турар жой қурилмаларигача масофани камида 50 м деб олиш лозим. Шовқин пасайтирувчи қурилмалардан фойдаланганда бу масофа 25 м гача камайтирилиши мумкин.'),
        (230, 28, null, null, '146. Тўхтовсиз ҳаракат магистраллари марказий қатнов қисмларининг бошқа умумшаҳар аҳамиятидаги магистраллар ва йўллар билан, шунингдек темир йўллар билан барча кесишувлари турли сатҳларда, камида 1500-2000 м масофада жойлашиши керак.'),
        (231, 28, null, null, '147. Умумшаҳар аҳамиятидаги ҳаракати тартибга солинадиган магистраль кўчаларнинг бошқа транспорт йўллари билан кесишуви одатда бир сатҳда кўзда тутилади. Тегишли техник-иқтисодик асослаш бўлганда бу кесишувларда турли сатҳлардаги чорраҳалар ҳам қурили-ши мумкин.'),
        (232, 28, null, null, '148. Умумшаҳар аҳамиятидаги магистраль кўчаларнинг бошқа кўчалар билан кесишуви бир-биридан камида 500 м масофада бўлиши мумкин. Тор кўчалар умумшаҳар аҳамиятидаги магистраль кўчаларга чорраҳадан камида 100 м масофада туташуви мумкин.'),
        (234, 28, null, null, '150*. Кўча ва йўл чорраҳаларида, шунингдек, пиёдаларнинг ўтиш жойларида кўриниш учбурчакларини кўзда тутиш лозим. Тенг ёнли «транспорт-транспорт» кўриниш учбурчаги томонларининг ўлчами транспортнинг тезлиги 40, 60 ва 80 км/соат бўлганда мос ҳолда камида, м: доирасида доимий ва кўчма буюмлар (дўконлар, реклама фургонлари, кичик меъморий шакллар), жумладан, дарахтлар ва 0,5 м дан баланд буталар жойлашуви ман этилган зона учун: 15, 30 ва 45; бинолар ва бошқа капитал иншоотлар жойлашуви тақиқланган зона учун – 25, 40 ва 65. «Пиёда-транспорт» тўғри бурчакли кўриниш учбурчаги томонлари ўлчамларини транспорт тезлиги 40 км/соат бўлганда 8х40, 60 км/соат бўлганда эса - 10х50 м қилиб олиниши керак.'),
        (235, 28, null, null, '151*. Аҳоли пунктлари кўчайўлларининг ер тўшамаси ён-атроф ҳудуднинг ва таянч иморатларнинг 88-бет. ШНҚ 2.07.01-03 баландлик қийматларини ҳисобга олиб лойиҳаланиши керак. Ер тўшамаси ва йўл қопламаларининг конструкцияларини ШМҚ 2.05.02-07 «Автомобиль йўллари» га мувофиқ лойиҳалаштириш керак'),
        (236, 28, null, null, '152. Транспортнинг бир томонлама бир ёки икки йўлли ҳаракати бўлган кўча ва йўлларнинг қатнов қисми 750 м гача бўлган радиусли горизонтал эгриликлар участкаларида 40-жадвалга мувофиқ кенгайтирилиши керак.'),
        (237, 28, null, null, '153*. Икки тасмали йўлларнинг узунасига нишаби 40 % дан ортиқ ва узунлиги 300 м дан ортиқ бўлган участкалардаги кўтарилишларда қўшимча йўлакни кўзда тутиш лозим. Икки тасмали йўлдан ўтиш ва унга қайтиш узунлиги камида 70 м бўлиши керак.'),
        (238, 28, null, null, '154. Берк кўчалар қатнов қисмларини айланиш радиуси камида 15 м бўлган майдончалар ёки 20 х 20 м ўлчамли айланиш майдончалари билан тугаши лозим. Айланиш майдончаларида оммавий транспорт охирги пунктлари ва автотранспорт тўхташ жойларини ташкил этиш мумкин эмас.'),
        (239, 28, null, null, '155. Микротуман, (мавзе) ва даҳалар ҳудудига кириб боришлар, шунингдек, биноларга тўппа-тўғри борадиган ўтиш йўллари бир-биридан камида 300 м масофада, қайта қурилаётган туманлардаги периметрик қурилмаларда – камида 180 м масофада кўзда тутилиши керак. Тор кўчалар ва пиёда йўлаклар турар жой ва жамоат бинолари деворларидан камида 5 м узоқда жойлаштирилиши лозим.'),
        (240, 28, null, null, '156. Кўп қаватли қурилмалар зонасида 3 минг кишидан ортиқ аҳолиси бўлган турар жой бинолари гуруҳига олиб борувчи эни 5,5 м икки ҳаракат йўлаги ва эни 1,5 м тротуарлари бўлган тор кўчалар қабул қилиниши лозим. Бир томонлама ҳалқа транспорт ҳаракати бўлган ва узунлиги кўпи билан 300 м бўлган тор кўчаларнинг тротуарлари бўлган ҳолда эни 3,5 м бир ҳаракат йўлаги қилиб қабул қилинишига йўл қўйилмайди. Бир йўлакли тор кўчаларда кўпи билан 100 м оралиқ билан эни 6 м ва узунлиги 15 м бўлган айрилиш майдончаларини кўзда тутиш зарур.'),
        (241, 28, null, null, '157. Узунлиги 150 м дан ошмаган боши берк тор кўчалар тротуар билан бирлаштирилган ҳолда, камида эни 3,5 м қилиб қабул қилиниши, боши берк тор кўчалар ўлчамлари 12х12 м бўлган айланиш майдончалари ёки йўл ўқи бўйича камида 20 м радиусли ҳалқа билан тугаши зарур.'),
        (242, 28, null, null, '158. Турар жой ва жамоат биноларига ўт ўчириш машиналарининг етиш йўлларини 1-иловага мувофиқ лойиҳалаш керак.'),
        (243, 28, null, null, '159. Кўча ва йўллар қатнов қисмларининг қайрилиши радиуслари тротуарлар ва ажратувчи йўллар четидан камида 15 м деб олиниши керак. Қайта қурилаётган шаҳарларда олдиндан шаклланган қури- ШНК 2.07.01-03 89-бет лишда ушбу радиусни 5 м гача камайтиришга йўл қўйилади.'),
        (244, 28, null, null, '160. Тўхтовсиз ҳаракат магистралларида эни 4 м ли марказий ажратувчи йўллар кўзда тутилади. Ажратувчи тўсин (иҳота тўсиғи) бўлган марказий ажратиш йўлнинг эни тўхтовсиз ҳаракат магистралларида 2 м гача камайтирилиши мумкин. Эни 5 м дан кам бўлган марказий ажратиш йўлида ҳаракат хавфсизлигини таъминлашга алоқаси бўлмаган иншоотларни ўрнатиш мумкин эмас.'),
        (255, 28, null, null, '171. Оммавий йўловчи транспорти оддий турларининг ташиш имкониятлари йўловчи салонининг бўш майдони тўлиши 4 киши/м2 , тезюрар турлари учун – 3 киши/м2 нормаси билан аниқланади. Оммавий йўловчи транспорти ҳар хил турларининг қатнов тезлиги ва ташиш имкониятлари 41-жадвалга мувофиқ олиниши керак'),
        (332, 33, null, null, '237. Муҳандислик тармоқлари жойлашувчи техник зоналарда бу тармоқлар кузатув қудуқлари, камеералари ва коверларидан камида 3 м масофада бўлиш шарти билан пиёда йўлкалар, аҳолининг мустақил машғулотлари учун спорт майдонча-лари ва болалар майдончаларини жойлаштириш мумкин'),
        (245, 28, null, null, '161. Тротуарлар пиёда қатнов қисмининг эни пиёда ҳаракатнинг 0,75 м бўлган бир йўл қаторига қолдиқсиз баробарлигида қабул қилиниши лозим. Тротуарлар пиёда қисми энига дўконча, тўхташ бекатлари, ўриндиқлар ва ҳоказоларни жойлаштириш учун зарур майдонлар киритилмайди. Тротуарларнинг эни 37-, 38- ва 39-жадвалларга мувофиқ олиниши керак. Асослашлари бўлганда тротуар эни оширилиши мумкин, лекин икки баробаригача бўлган миқдорда. Қайта қуриш шароитида бир қаватли қурилишда турар жой кўчалари ва тор кўчаларнинг кўндаланг кесимида тротуарни бино пойдевори бўйлаб кетган йўлак билан қўшиб юборишга йўл қўйилади. Кўча қатнов қисмидан пиёда ўтишларнинг эни камида 4 м қабул қилиниши керак.'),
        (246, 28, null, null, '162. Қизил чизиклар ёқалаб жойлашган тротуарларда ҳаракат йўлагини ўтказиш имконияти 700 пиёда/соат, кўча қатнов қисмидан бир сатҳдаги пиёда ўтишда – 1200 пиёда/соат, ер ости пиёда ўтишларда - 1500 пиёда/соат миқдорида қабул қилинади'),
        (247, 28, null, null, '163*. Тротуарларнинг узунасига нишаблиги уларнинг нишабли қисми узунлиги 30 м дан ошмаганда кўпи билан 60%, тоғ шароитларида эса - кўпи билан 80 % бўлиши мумкин. Катта нишабларда ва улар узун бўлганда зиналар қуриш кўзда тутилиши лозим. Тротуарлар болалар ва ногиронлар аравачаларининг ҳаракатини ШНҚ 2.07.02.07 - “Инсонларнинг ҳаёти ва фаолияти муҳитини ногиронлар эҳтиёжлари ва аҳолининг кам ҳаракатланувчи гуруҳларини ҳисобга олган ҳолда лойиҳалаш” талабларига мувофиқ таъминлаши зарур.'),
        (248, 28, null, null, '164. Тўхтовсиз ҳаракат магистралларининг марказий қатнов қисмларидан пиёда ўтишларни одатда турли сатҳларда барпо этиш лозим'),
        (249, 28, null, null, '165. Пиёда ер ости йўлакларнинг энг кам эни 3 м, бир томонлама зиналар эни – 2,25 м (ер ости йўлакнинг иккала охирида ҳар бирининг эни 2,25 м бўлган иккита зина қурилиши шарти билан) қабул қилинади. Барча пиёда йўлакларда болалар ва ногиронлар учун эни камида 1 м бўлган тушиш жойлари кўзда туттилиши лозим. Пиёда ер ости йўлакларида маданий-маиший хизмат кўрсатиш объектларини жойлаштириш ёнғин хавфсизлиги қоидаларига қатъий риоя қилган ҳолда ва Ўзбекистон Республикаси ИИВ ЁМБ ва МХХ билан келишилган ҳолда йўл қўйилади.'),
        (250, 28, null, null, '166. Туман аҳамиятидаги мАгистраль кўчаларда, ажратиш йўллари билан ажратилган велосипед йўлакларини кўзда тутиш мумкин. 90-бет. ШНҚ 2.07.01-03 Аҳолининг оммавий дам олиши зоналарида ва бошқа кўкаламзорлаштирилган ҳудудларда велосипед йўлаклари бошқа кўча, йўллар ва пиёда ҳаракатидан алоҳида қилиб кўзда тутилиши керак. Велосипед йўлаклари бир томонлама ва икки томонлама ҳаракатли қилиб барпо этилади, бунда велойўлак четидан энг кам хавфсизлик масофаси, м: йўл қатнов қисмигача, транс-- порт иншоотлари таянчлари, дарахтларгача - 0,75; тротуарларгача – 0,50; автомобиллар тўхташ жойлари ва оммавий транспорт бекатларигача - 1,50 м бўлиши керак. Изоҳ. Кўчаларнинг қатнов қисми четидан велосипед тасмаларини жуфт чизиқ билан кўрсатиб барпо этиш мумкин. Велосипед йўлагининг эни транспорт оқими йўналишидаги ҳаракатда камида 1,2 м ва қарама-қарши ҳаракатда камида 1,5 м бўлиши керак. Тротуар ёнида барпо этиладиган велосипед тасмасининг эни камида 1,0 м бўлиши керак. Велосипед йўлининг четидан хавфсизлик масофаси камида, ўтиш йўлигача – 1,0 м, тротуаргача – 0,5 м бўлиши керак.'),
        (251, 28, null, null, '167. Турар жой, жамоат ва дам олиш ҳудудларида механик бўлган ногиронлар аравачалари ўта оладиган пиёда йўллар кўзда тутилиши керак.'),
        (252, 28, null, null, '168. Аҳолиси 100 минг кишидан кўп бўлган шаҳар аҳоли пунктлари учун оммавий йўловчи транспорти турлари ва йўналишлари ҳисоблар асосида танланади. Қурилишнинг биринчи навбатидаги мўлжалий ҳисоблар учун транспорт ҳаракати бирлигини 100 дан 250 гача минг киши аҳолиси бўлган шаҳар аҳоли пунктлари учун 300-350 та, 250-500 минг киши аҳолиси учун – йилига бир кишига 350- 425 сафар деб қабул қилиш керак. Ҳисоблаш муддатига транспорт ҳаракати катталигини мос равишда бир йилда кишига 350-425 ва 425- 470 сафар деб қабул қилинади'),
        (253, 28, null, null, '169. Аҳолиси 10 минг киши-гача бўлган ва ҳовлиси бўлган якка тартибдаги иморатлари мавжуд аҳоли пунктларида яшаш жойларидан ишхонагача масофа 1500 м дан ошмаганда, шаҳар ичи оммавий йўловчи транспорти кўзда тутилмаслиги мумкин.'),
        (273, 29, null, null, '186.2. Шахсий турар жой қурилмалари ҳудудларида ва шаҳар (посёлка)нинг ҳудудида ТХС жойлаштиришда санитария-гигиеник нормаларига, экологик ва ёнғинга қарши талабларига риоя қилиш лозим.'),
        (256, 28, null, null, '172*. Яшаш жойи ёки ишхонадан оммавий йўловчи транспортининг энг яқин бекатигача пиёда етишнинг узоқлиги 400 м дан ошмаслиги керак. Ўта йирик, йирик ва катта шаҳарларнинг якка тартибдаги иморатлари мавжуд ҳудудларда бу катталик 600 м гача, ўрта ва кичик шаҳарларда эса – 800 м гача оширилиши мумкин. Ўта йирик ва йирик шаҳарларнинг умумшаҳар марказида оммавий йўловчи транспорти бекатларигача пиёда етишнинг узоқлиги 250 м, оммавий дам олиш ва спорт зоналарида – 800 м дан ошмаслиги керак.. Мураккаб рельеф шароитида бу миқдор босиб ўтиладиган пастбаландликнинг 10 метри учун 50 м га қисқартирилиши керак.'),
        (257, 28, null, null, '172.1. Қариялар ва ногиронлар учун хонадонлари бўлган махсус турар жой уйларининг ногиронлар учун мўлжалланганлари экологик жиҳатдан қулай шароитли (зарарли шовқин таъсири бўлган ва табиат муҳити ифлосланган зоналар ташқарисида), оммавий транспорт бекатларига ва ижтимоий инфратузилмалар элементларига қулай етишишини кўпи билан 150-200 м қилиб қилиб ҳисобга олган ҳолда қабул қилиш лозим.'),
        (258, 28, null, null, '173. Шаҳар транспортининг ер усти турлари учун бекатлар орасидаги масофа 600 м дан, тезюрар йўналиш автобуслари учун 800-1200 м дан, метрополитен учун 1000-2000 м дан, электр темир йўл учун 1500- 2000 м дан ошмаслиги керак.'),
        (259, 28, null, null, '174. Тўхтовсиз ҳаракат магистралларида оммавий йўловчи транспортининг йўналишларини ташкил этиш фақат маҳаллий тор кўчалар бўйича рухсат этилади. Марказий қатнов қисмларидан эса тезюрар автобуслар йўналишларини фақат бекатлари марказий қатнов қисмларидан четда, ҳаракат тезлашуви ва пасайиши учун ўтиш йўллари бўлган махсус чўнтакларда барпо этилганида ташкил этиш мумкин. Йўлларнинг эни 42-жадвал бўйича олиниши керак. Ҳаракатнинг тезлашуви ва пасайиши учун йўлларни асосий қатнов қисмларидан эни камида 0,75 м бўлган айирувчи чизиқ ёки белги билан ажратиш зарур.'),
        (260, 28, null, null, '175. Трамвай йўллари асосан алоҳида тўшамада жойлаштирилиши керак. Икки йўлли алоҳида трамвай тўшамасининг эни бутун трамвай йўналиши давомида майдончаларини ҳисобга олганда камида 10 м, бир йўлли – 3,8 м деб қабул қилиниши керак. Трамвайга чиқиш майдончасининг узунлиги поезднинг ҳисоб узунлигидан 5 м ортиқ, эни эса камида 1,5 м катталикда қабул қилиниши керак.'),
        (261, 28, null, null, '176. Автобус ва троллейбусларнинг тўхташ жойларини чорраҳадан сўнг камида 20 м масофада жойлаштириш лозим. Бир томонга йўналишларнинг тўхташ жойларини чорраҳанинг бир тарафида, майда донабой сотиш учун кўпи билан 3,0 м2 майдонли савдо павильони жойлашиши эҳти-молини ҳисобга олиб жойлаштири-лиши зарур. Бир чиқиш майдончасининг узунлигини бир томонга йўналишлар учун 20 м, бир неча томон йўналишлари учун 30 м ва ундан ортиқ деб қабул қилиш керак. Чиқиш майдончаларининг энг кам эни 1,5 м бўлиши лозим. Тўхташ ва чиқиш майдончалари кўча қатнов қисмини торайтириш ҳисобига қурилишига йўл қўйилмайди. Оммавий транспортнинг тўхташ жойлари ШНҚ 2.07.02.07 - “Инсонларнинг ҳаёти ва фаолияти муҳитини ногиронлар эҳтиёжлари ва аҳолининг кам ҳаракатланувчи гуруҳларини ҳисобга олган ҳолда лойиҳалаш” 3.23 ва 3.24 пунктлари талабларига мувофиқ лойиҳаланиши керак.'),
        (262, 28, null, null, '177. Ўта йирик ва йирик шаҳарларда шаҳар транспортининг охирги бекатларини ташкил этиш учун нозимлар дўкончалари, ҳайдовчилар учун дам олиш хоналари, кўчма таркибининг вақтинча туриши учун майдончалар ва чиқиш майдончалари билан жиҳозланган, майдони 1- 1,5 га бўлган махсус майдончалар кўзда тутилиши керак. Бундай охирги бекатларда шаҳар оммавий йўловчи транспортининг камида 90% йўналишлари тамомланиши керак'),
        (263, 28, null, null, '178. Саёз сатҳда жойлашган метрополитен йўллари бўйлаб (ер сиртидан рельс тепасигача кўпи билан 12 м) эни одатда 40 м бўлган техник зона кўзда тутилиб, унинг ичида капитал бино, иншоотларнинг қурилиши ва ер ости муҳандислик тармоқларининг жойлаштирилишига метрополитенни лойиҳалаштирувчи ташкилот билан келишилган ҳолда йўл қўйилади'),
        (274, 29, null, null, '186.3. Ҳар хил турдаги машиналарни ювиш қурилмаларини жойлаштиришда ҳам юқоридагиларга ўхшаш талабларга риоя этиш зарур. Ювиш қурилмалари учун майдонлар 1 машинага 30-35 м2 ўлчамда қабул қилинади'),
        (369, 34, null, null, '274. Суғориш сувининг сифатига талаблар унинг минераллашганлик даражаси билан аниқланади, тузлар таркиби 1-2 г/л бўлишига рухсат этилади, бундан камроқ миқдорларни такрор шўрланишига ёрдам берувчи гидрогеологик шароитларда қабул қилиш зарурдир'),
        (264, 29, null, null, '179*. Фуқароларга тегишли 100 % автомобилларни доимий сақлаш жойларини уларнинг эгалари яшаш жойидан кўпи билан 800 м масофада кўзда тутиш лозим. Қайта қуриш жойлари ва шаҳарларнинг тарихий зоналарида бу масофа 1500 м гача оширилиши мумкин. Ногиронларга қарашли транспорт воситаларини сақлаш учун бокс туридаги гаражлар ногирон яшаш жойидан кўпи билан ШНК 2.07.01-03 93-бет 200 м пиёда етиш масофасида кўзда тутилиши керак. Ногиронлар учун машиналар тўхташ жойлари ШНҚ 2.07.02.07- “Инсонларнинг ҳаёти ва фаолияти муҳитини ногиронлар эҳтиёжлари ва аҳолининг кам ҳаракатланувчи гуруҳларини ҳисобга олган ҳолда лойиҳалаш” 3.25 ва 3.26 пунктлари талабларига мувофиқ лойиҳаланиши керак'),
        (265, 29, null, null, '180. Сақлашни ташкил этиш учун қурилишнинг биринчи навбатида кутилган автомобиллар сонини очиқ тўхташ жойларида жойлаштиришга зарур бўлган ҳудудни ажратиш керак. Ҳисоб муддатида ва ундан кейинги даврда ҳисоб муддатига кутиладиган автомобиллар сонини жойлаштириш учун етарли бўлган сиғимдаги ер ости ва кўп қаватли гаражлар ажратилган ҳудудда кўзда тутилади. Ногиронлар (мавзе) лойиҳасида мажбурий тартибда жорий автомобиллаштириш даражаси ва мазкур нормалар талабларини ҳисобга олган ҳолда автомобилларни сақлаш жойлари кўзда тутилиши керак. Микротуманда (мавзеда) жойлаштирилган гараж ва автотўхташ жойларининг ҳудудлари микротуманнинг (мавзенинг) зичлигига киради. Қайта қурилаётган шаҳарларда шахсий транспортни тўхтатиш-сақлаш масалаларини ҳал этиш учун учун махсус лойиҳалар бажарилиши керак.'),
        (266, 29, null, null, '181. Енгил автомобиллар гаражи ва автотўхташ жойлари ер участкаларининг майдонларини уларнинг қаватлилигига боғлиқ ҳолда бир машина-жой учун м2 да олиш керак: гаражлар учун: 1-қаватли- 30 2-қаватли - 20 3-қаватли - 14 4-қаватли - 12 5-қаватли - 10 ер усти тўхташ жойлари учун - 25 кўкаламзорларни ҳисобга олган ҳолда ер усти тўхташ жойлари учун – 35.'),
        (267, 29, null, null, '182*. Гаражларга кириш ва улардан чиқишгача энг кам масофалар: магистраль кўчалар чорраҳаларидан – 50 м, маҳаллий аҳамиятидаги кўчалардан – 20 м, оммавий йўловчи транспорти бекатларидан – 3 м бўлиши керак. Ер ости гаражларига кириш ва улардан чиқишлар турар жой, жамоат бинолари деразаларидан ҳамда болалар ва даволаш муассасалари участкаларидан камида 15 м узоқликда бўлиши керак. Ер ости гаражларининг шамоллатиш шахталари ҚМҚ 2.04.05-97 «Иситиш, шамоллатиш ва кондиционирлаш»га мувофиқ кўзда тутилиши керак. Ногиронларнинг транспорт воситаларини сақлаш учун тўхташ жойлари-гаражлари яшаш жойларидан узоғи билан 20 м масофада жойлаштириш зарур'),
        (268, 29, null, null, '183*. Турар жой уйлари ёнида енгил автомобилларнинг вақтинча сақлаш очиқ тўхташ жойлари фуқароларга тегишли автомобиллар сонининг 15 % ҳисобидан кўзда тутилиши керак. Жамоат ва дам олиш марказларида тўхташ жойлари ҳисобланишининг нормалари 43-жадвалга кўра қабул қилиниши керак.'),
        (269, 29, null, null, '184. Вақтинча сақлаш тўхташ жойларидан вокзаллар йўловчи хоналари ва темир йўл бекатларигача, савдо марказлари ва универмагларгача бўлган масофа кўпи билан 150 м, бошқа объектлар учун – кўпи билан 300 м бўлиши керак.'),
        (270, 29, null, null, '185*. Автомобилларнинг вақтинча ва доимий сақлаш учун мўлжалланган очиқ тўхташ жойлари ва гаражлари, техник хизмат кўрсатиш станцияларидан турар жой уйлари ва жамоат бинолари деразаларигача, болалар ва даволаш муассасалари участкаларигача бўлган масофалар камида 44-жадвалда келтирилган каби қабул қилиниши керак. Автомобиллар тўхташ жойлари учун очиқ майдончалар шундай йиғиб жойлаштирилиши керакки, уларнинг сиғими камида 20 машина-жой бўлсин'),
        (271, 29, null, null, '186*. Техник хизмат кўрсатиш станцияларини 200 енгил автомашинага 1 пост ҳисобидан, уларнинг ер участкаси майдонини (га) станция учун қуйидагича қабул қилиб лойиҳалаш зарур: 3-5 пост учун -0,3-0,5 10 пост учун - 1,0 15 « « - 1,5 25 « « - 2,0 40 « « - 3,5'),
        (272, 29, null, null, '186.1. ТХС (техник хизмат кўрсатиш станцияси) шаҳар (посёлка) бош режаси асосида, марказий ва бош кўчаларда барпо этишни тақиқланган ҳолда магистраль кўчалар бўйлаб бир-биридан камида 1,5- 2,0 км масофада жойлаштириш зарур. Йирик ТХС шаҳарлар ва посёлкаларнинг коммунал-омбор ва саноат зоналарида жойлашиши керак. ТХС ички режасини шундай ечиш керакки, унда ТХС боксларидан чиқишга ёки постлар сонига боғлиқ бўлмаган ҳолда уларнинг магистраль кўчалар томонидан кўринишига йўл қўйилмаслиги керак.'),
        (275, 29, null, null, '187*. Аҳоли пунктларида автомобилларга ёнилғи қуйиш станцияларини (АЁҚС) турар жой ва жамоат қурилмалари даҳаларидан режавий ажратилган ер участкаларида, умумий эҳтиёжни ҳисобга олиб аҳоли пунктининг автомобиллашуви даражаси, ҳаракат интенсивлиги ва истеъмол талабига боғлиқ ҳолда жойлаштириш керак. Муайян жойлашув учун АЁҚС турини танлаш унинг қуввати ва технлогик ечимларига боғлиқ ҳолда, 4-иловада келтирилган таснифга кўра, шунингдек, шаҳарсозлик чекловлар ва табиат муҳофазасига оид қонунчилик талабларини ҳисобга олиб ўтказилиши керак. АЁҚС лар санитария-гигиеник, иқтисодий, ёнғинга қарши ва бошқа меъёрий талабларга риоя қилиш шарти билан ҳайдовчилар ва транспорт воситаларига сервис хизмат кўрсатиш: чакана савдо, тез овқатланиш, автомобилларга техник хизмат кўрсатиш, ювиш ва мойлаш хоналари ва алоҳида объектлари бўлган автоёнилғи қуйиш мажмуалари (АЁҚМ) каби лойиҳаланиши ҳам мумкин. Бу объектларнинг таркиби ва уларнинг жойлашувига талаблар ёнғин назорати идоралари билан келишилган лойиҳалаш топшириғига кўра қабул қилиниши керак.'),
        (276, 29, null, null, '188. АЁҚС 1200 енгил автомобилга бир ёнилғи-қуйиш колон- ШНК 2.07.01-03 97-бет каси ҳисобидан лойиҳалаштирилиш керак. АЁҚС қурилиши учун ер участкаси майдони (га) қуйидагича қабул қилинади: 2 колонка учун - 0,1 5 « « - 0.2 7 « « - 0,3 9 « « - 0,35 11 « « - 0,4 '),
        (277, 29, null, null, '189. Транспорт воситаларига хизмат кўрсатиш пунктлари бўлган АЁҚС (автомобилларга техник хизмат кўрсатиш, ювиш, мойлаш) фақат саноат ва коммунал-омбор зоналари кўча-йўллари бўйлаб, уларнинг ҳудудида ва аҳоли пунктларидан чиқишда жойлаштирилиши лозим. Бу АЁҚС ларни аҳоли жойлашган ҳудудлар ва дам олиш зоналари ичида жойлаштириш тақиқланади. Транспорт воситаларига техник хизмат кўрсатиш пунктлари бўлмаган АЁҚС ларни жойлаштиришга талаблар ҳам худди шундай қабул қилиниши керак'),
        (278, 29, null, null, '190*. АЁҚС лар: йирик шаҳарларда умумшаҳар ва район аҳамиятидаги магистраль кўчалар бўйлаб, ўрта ва кичик шаҳарларда - магистраль кўча-йўллар бўйлаб, шунингдек, саноат ва коммуналомбор зоналари кўчалари бўйлаб ва уларнинг ҳудудларида транспорт ҳаракати йўналишларидан, шунингдек ёқилғининг хили ва тавсифига боғлиқ бўлмаган ҳолда бир-биридан камида 2,5-3,0 км масофада жойлаштирилиши керак. АЁҚС ёки АГҚС ларни қайта тиклашда ушбу объектларни лойиҳалашдаги амалдаги нормалар ҳисобга олиниши зарур. Амалдаги ёнғинга қарши ва санитария-экологик нормаларга риоя қилиш имкони бўлмаган ҳолларда шаҳар (посёлка) чегарасида тегишли ҳудуд ажратилиши билан объект ёпилиши лозим. АЁҚС (АГҚС) атрофидаги ёнғинга қарши санитария-ҳимоя зонаси қандайдир бошқа иншоотлар барпо этиш ҳуқуқи бўлмаган ушбу АЁҚС (АГҚС) эгалар маблағлари ҳисобига ободонлаштирилиши ва кўкаламзорлаштирилиши зарур. АЁҚС ларни пиёда юриш кўчаларида ва квартал (даҳа) ичи тор кўчаларида жойлашуви тақиқланади.'),
        (279, 29, null, null, '191*. АЁҚС қурилиши учун ажратилган ер участкалари кўчаларнинг қизил чизиғи орқасида ёки қисман уларнинг чегараси ичида (агар шаҳарсозлик ҳужжатларида бу ҳудуд кўча ўтиш йўлининг кейинчалик кенгайиши кўзда тутилмаганда) жойлаштирилади. Қизил чизиқлар доирасида ер участкаларини ажратиб, АЁҚСларни жойлаштириш фақат вақтинча, белгиланган тартибда келиши ва тасдиқлаш шарти билан рухсат этилади'),
        (280, 29, null, null, '192*. АЁҚС ларни жойлаштириш уларнинг қурилиши учун тегишли шаҳарсозлик ҳужжатлари билан кўзда тутилган ер участкаларида олиб борилиши керак. Бошқа ҳолларда, бу ҳужжатлар бўлмаганда ёки уларнинг амал қилиш муддати тугаганда АЁҚС ер участкасини танлаш ва жойлашувини келишиш жорий қонунчилик талабларига мувофиқ, белгиланган тартибда келишилган ва тасдиқланган объектнинг жойлашуви шаҳарсозлик асосланиши асосида амалга оширилади.'),
        (297, 29, null, null, '203. Сув, иссиқлик, газ, электр ва б. таъминоти манбаларини танлаш, белгиланган муддатларда сув, электр энергияси, газ ва ҳоказоларнинг ҳисоб миқдорини олиш имкониятини тасдиқлайдиган манфаатдор ташкилотлар билан келишилган бўлиши керак.'),
        (298, 29, null, null, '204. Сув таъминоти ва оқова тармоғи, иссиқлик, газ ва электр таъминоти, алоқа тизимлари ва бошқа тизимларни ҳисоблаш, жумладан, манбалар доираси, иншоотлар жойлашуви тегишли тизимларни лойиҳалаш бўйича ҚМҚ бўлимлари талабига мувофиқ бўлиши керак'),
        (370, 34, null, null, '275. Чўмилишга мўлжалланган сув ҳавзаларини тўлдирувчи сув сифати санитария талаблари билан тартибга солинади.'),
        (281, 29, null, null, '193*. АЁҚС сиғимлари ер ости ёки ер устида жойлашиши мумкин. 200 минг киши ва ундан кўп аҳолиси бўлган шаҳарларнинг марказий зич қурилган ҳудудларида янги АЁҚС ларнинг фақат кам қувватли, “А” ва “Б” турдаги сиғим-ларнинг ер ости жойлашувидаги, техник хизмат кўрсатиш пунктлари бўлмаган ҳамда қўлланиши мумкин-лиги белгиланган тартибда давлат назорати идораларининг эксперт хулосаси билан тасдиқланган ёнғиндан хавфсиз технология ва экологик хавфсиз жиҳозларни қўллаган ҳолда (5- ва 6-илова) қурилишига рухсат этилади. Аҳоли пунктлари ичида “В” туридаги АЁҚС жойлаштирилишида суюқ ёнилғини сақлаш учун ер усти сиғимларининг энг кўп биргаликдаги ҳажми қўлланиши мумкинлиги белгиланган тартибда давлат назорати идораларининг эксперт хулосаси билан тасдиқланган ёнғиндан хавфсиз технологик ва экологик хавфсиз жиҳозларни қўллаган ҳолда (5- ва 6-илова) 80 куб. м дан ошмаслиги керак. Бунда ҳар бир сиғимнинг алоҳида ҳажми 20 куб. м дан ошмаслиги керак. “А” ва “Б” турлардаги катта қувватли АЁҚС ларни саноат ва коммунал зоналарида, объектларнинг санитария-ҳимоя зоналарида улардан фойдаланишнинг қонунчиликда белгиланган тартибига мувофиқ жойлаштириш лозим. АЁҚС иншоотларидаги портлашдан хавфли қурилмалар ва ифлосланиш манбаларидан энг яқинидан – ер усти ёнилғи сиғимлари девори ва ёнилғи қуйиш колонкалари (ЁҚК) корпусидан, технологик қудуқлар, ер ости сиғимларнинг нафас олиш қурилмаларидан, аварияли сиғимлар ва тозалаш иншоотларининг тортиб олувчи шамоллатиш шахталари, ёнилғини сиғимларга қуйиш тармоғидан: турар жой ва жамоат биноларининг ташқи деворларигача; ҳовлили, дала ҳовли ва боғ уйлари, болалар мактабгача муассасалари, умумтаълим мактаблари, стационар даволаш - профилактик муассасалари, санаторийлар, санаторий-профилакторийлар, умумий ва махсус турдаги интернат-уйлар, дам олиш муассасалари, жисмоний-спорт ва жисмоний-саломатлик мажмуалари участкаларининг четигача, шунингдек, ўйин, жисмоний тарбия ва спорт, дам олиш майдончаларигача ва кишиларнинг оммавий йиғилиш жойларигача бўлган масофалар ШНҚ 2.09.20-08 “Автомобилларга ёқилғи қуйиш станциялари” норматив ҳужжатига биноан ҳисобланади. “Г” турдаги кичик ва ўрта қувватли контейнерли АЁҚСларнинг қўлланишига автохўжаликлар, саноат корхоналар участкаларида санитария оралиқлари, ёнғинга қарши масофаларга ва табиат муҳофазаси қонунчилиги талабларига ҚМҚ 2.09.14-97 «Нефть ва нефть маҳсулотлари омборлари» га мувофиқ риоя қилинганда йўл қўйилади. АЁҚС дан ён-атрофдаги иншоотларгача бўлган санитария оралиқларнинг камайтирилишига ушбу ҳудудда келиб чиқиши геологик ва техноген каби хавфли ҳолатлар (тектоник, сейсмик, ўпирилиш, сел, карст ҳолатлари ва ер усти шаклининг бошқа ўзгаришлари, сизот сувлар тошиши, тошқинлар) мавжуд бўлганда йўл қуйилмайди.'),
        (282, 29, null, null, '194*. Янги АЁҚС ларни жойлаштириш ва мавжудларини қайта қуриш АЁҚС иншоотларидан энг яқиндагисидан яқин атрофдаги бинолар, иншоотлар ва муҳандислик тармоқларигача бўлган масофа ШНҚ 2.09.20-08 “Автомобилларга ёқилғи қуйиш станциялари” норма-тив ҳужжатларга мувофиқ, сани-тария оралиқлари ва ёнғинга қарши масофаларга риоя қилиб, йўл ҳаракати хавфсизлиги қоидаларини ҳисобга олиб амалга ошириш зарур. Аввал амалда қўлланилган ШНҚ 2.07.01.03 “Шаҳар ва қишлоқ аҳоли пунктлари ҳудудларини ривожлантириш ва қурилишини режалаштириш”да келтирилган 45- ва 46- жадваллар шу муносабат билан норматив ҳужжатдан чиқарилган.'),
        (283, 29, null, null, '194.1. АЁҚС ва АГҚС ҳудудига участка танлашда қуйидаги талабларга амал қилиш лозим: шаҳарлар, аҳоли пунктлари ҳудудларида, магистраль ва иккинчи даражали кўчалар бўйлаб фақат I ва II тоифали кўчма таркибига эга автомобилларга ёқилғи қуйиш учун мўлжалланган АЁҚС (АГҚС)ларни жойлаштиришга йўл қўйилади; сиғими 10 дан 40 м 2 гача бўлган резервуарлари ва сони 2 дан 4 гача ёқилғи қуйиш колонкаларига бўлган АЁҚС (АГҚС)лар ўртасидаги масофа 2,5-3,0 км дан кам бўлмаслиги зарур; АЁҚС участкаси чегарасидан турар жой ва жамоат бинолари деворларигача узилишлар камида 25 м. АЁҚС ҳудудидаги портлаш ҳавфи бўлган технологик қурилмалар ва жиҳозлардан жамоат ва турар жой бинолари ва иншоотлари деворларигача бўлган узилишлар эса ШНҚ 2.9.20-08 “Автомобилларга ёқилғи қуйиш станциялари” 3-илова 1- жадвали талабларига мувофиқ бўлиши керак. Ушбу АЁҚС лардаги ёқилғи сақлаш резервуарлари ер остида жойлашган бўлиши лозим; Умумий сиғими 60 м 3 гача бўлган ер устидаги бир деворли резервуарлари бўлган “А” туридаги контейнерли АЁҚС лар ва 100 м 3 гача бўлган ер устидаги бир деворли резервуарлари бўлган “А” туридаги модулли АЁҚС лар аҳоли жойлашган ҳудудлардан ташқарида, саноат ва омборхона зоналарида ёки шаҳарлар ва аҳоли жойлаштириш зарур; ушбу АЁҚС лардан унга тегишли бўлмаган объектларигача бўлган масофалар ШНҚ 2.9.20-08 “Автомобилларга ёқилғи қуйиш станциялари” 3-илова 1-жадвалига мувофиқ қабул қилиниши лозим; шаҳарлар ва аҳоли сони 50 минг ва ундан ортиқ бўлган йирик аҳоли пунктлари ҳудудларида ва чегарасида кўп ёқилғи АЁҚС ларни жойлаштиришга йўл қўйилмайди; АЁҚС ва АГҚС ҳудудларини ҳар хил турдаги озиқ-овқат ва буюм магазинлари билан бирга қўшмаслик зарур. Объект ҳудуди ичида ёнғинга қарши нормаларга риоя қилган 100-бет. ШНҚ 2.07.01-03 шароитда майда автомобиль эҳтиёт қисмларини сотиш бўйича катта бўлмаган магазинларни жойлаштиришга йўл қўйилади; шаҳарнинг марказий қисмида ВСН 01-89 бўйича кўчма таркибли III ва IV тоифали автомобилларга ёқилғи қуйиш учун АЁҚС ларни жойлаштирмаслик лозим; кичик аҳоли пунктларида ва ҳаракат интенсивлиги кичик бўлган йўлларда кичик АЁҚС ларни автомобилларга техник хизмат кўрсатиш билан бирлаштирилишини кўзда тутишга йўл қўйилади.'),
        (284, 29, null, null, '194.2. АЁҚС ларни йўлларнинг чорраҳаларида бурчакли қилиб жойлаштиришга рухсат берилмайди. Шундай жойлаштириш зарурияти бўлганда эса, АЁҚС га кириш ва чиқиш жойлари чорраҳа қизил чизиқлари кесишган жойдан камида 100 м нарида жойлашмоғи лозим. Автомобиль йўлларида ёқилғи қуйиш станцияларини йўлнинг иккала томонига шундай жойлаштириш керакки, бунда келаётган ва кетаётган автомобилларнинг ҳаракати тўхтамаслиги зарур.'),
        (285, 29, null, null, '194.3. АГҚС ларни аҳоли жойлашган ҳудудларга жойлаштирганда турар жой ва жамоат биноларидан бўлган масофа ШНҚ 2.9.20-08 “Автомобилларга ёқилғи қуйиш станциялари” 3-илова 2-жадвали талабларига биноан қабул қилинади. АГҚС ва қурилмалар орасидаги бўш жойлар ёнғинга қарши хавфсизлик талаблари майсазорлар ва қалин ўтказилмаган дарахтларни қўллаган ҳолда кўкаламзорлаштирилиши лозим'),
        (286, 29, null, null, '194.4. АЁҚС ларни жойлаштиришда атроф-муҳитнинг экологик ҳолатини ҳисобга олиш зарур: автоцистерналардан ёқилғини тўкишдаги ва автомобиллар бакига ёқилғи қуйишдаги таралар, шунингдек АЁҚС ҳудуди атмосфера ёғинлари туфайли ҳосил бўлган нефть маҳсулотлари ифлосликлари атрофдаги қурилмаларнинг ички муҳитига кирмаслиги зарур.'),
        (287, 29, null, null, '194.5. АЁҚС ва АГҚС ларни лойиҳалашда ШНҚ 2.9.20-08 “Автомобилларга ёқилғи қуйиш станциялари” га қатъий риоя қилиш керак.'),
        (288, 29, null, null, '194.6. АЁҚС ва АГҚС лар бош режаларини лойиҳалаганда уларни ИИБ ЁХБ, ДСЭНМ, Давлат ёнғин хавфсизлиги назорати, Давлат Табиат қўмитаси, Тошкент шаҳри, Қорақалпоғистон Республикаси ва вилоятлар ЁКХШТ (автойўл) идоралари билан келишилади.'),
        (371, 34, null, null, '276. Очиқ суғориш тармоғи қисмларини чўкувчан тупроқларда жойлаштиришда ҚМҚ 2.06.03-97 «Суғориш тизимлари. Лойиҳалаш нормалари» талабларига; ёмғирлаштириш тармоғини ўрнатишда хўжалик-ичимлик сув қувурлари нормаларига амал қилинади.'),
        (289, 29, null, null, '195*. АЁҚС жиҳозларидан атроф бинолар ва иншоотларгача тозалиқ оралиқлари ўлчами атмосфера ҳавосининг кимёвий ва товуш ифлосланиши ҳисобларига кўра, лекин камида 50 м белгиланади. “А” ва “Б” туридаги кичик ва ўрта қувватли АЁҚС лар учун бу жиҳозлардан хизмат кўрсатилаётган автотранспорт воситаларигача санитария оралиқлари белгиланган тартибда давлат назорати идораларининг эксперт хулосаси билан тасдиқланган ёнғиндан хавфсиз технологик ва экологик хавфсиз жиҳозларни қўллаган ҳолда (5- ва 6- илова) камайтирилиши мумкин, лекин кичик АЁҚС учун 25 м ва ўрта АЁҚС учун 40 м гача. Бунда ифлослантирувчи моддалар атмосфера ҳавосига чиқишининг ҳисоб кўрсаткичлари унинг негиз даражасини ҳисобга олган ҳолда гигиеник меъёрлари ва экологик хавфсизлик ШНК 2.07.01-03 101-бет меъёрларидан ошмаслиги керак. Санитария оралиқлари камайтирилиши давлат санитария-эпидемиологик назорати, давлат ёнғин хавфсизлиги назорати идоралари ва Давлат Табиат қўмитаси билан белгиланган тартибда келишилади'),
        (290, 29, null, null, '196*. Кўча-йўлларнинг бўйлама нишаби 40 % дан ортиқ ва режада эгрилик радиуслари 250 м дан кичик бўлган жойларида АЁҚС жойлаштириш мумкин эмас. АЁҚС ер усти иншоотлари қатнов қисмининг четигача масофа ШНҚ 2.9.20-08 “Автомобилларга ёқилғи қуйиш станциялари” 3-илова 1-жадвал талабларига мос равишда қабул қилиш лозим. Ҳар бир йўналишда 1-2 ҳаракат йўлакли йўлларда АЁҚС га кириш транспорт воситалари йиғиладиган, асосий ҳаракат йўлагига тенг энли, эни 30 м тенг қўшимча йўлак барпо этилиши керак, лекин у АЁҚС га киришдан аввал 50 м ва ундан чиқишда 15 м узунликда бўлиши керак. Ўтиш қисми асосий энидан қўшимча йиғилиш йўлагига ўтишнинг узунлиги камида 15 м бўлиши керак. 1 ҳаракат йўлагига 300 авт/соатдан ошмайдиган ҳаракат оқими бўлган кўчаларда жойлашган АЁҚС лар учун йиғилиш йўлагини кичик АЁҚС ларда 30 м ва ўрта АЁҚС ларда 40 м гача қисқартириш мумкин.'),
        (291, 29, null, null, '197. АЁҚС ҳудуди қатнов қисмидан хавфсизлик оролчаси билан ажратилади, унинг эни транспорт тўсиғи, тротуар жойлашувининг шартларига кўра белгиланади. АЁҚС ҳудудига кириш ва чиқиш алоҳида, ҳар бирининг эни камида 4,2 м ва эгрилик радиуси камида 10 м бўлиши лозим. Кириш ва чиқиш бирга қурилганда, улар орасида эни камида 1 м бўлган, қатнов қисмидан 0,1 м кўтарилган хавфсизлик орол-часи кўзда тутилиши керак.'),
        (292, 29, null, null, '198. АЁҚС ва АЁҚМ ҳудудларида ёнилғи сиғимларининг ёнида автоцистерналар учун икки томони очиқ ўтиш билан таъминланган қуйиш майдончалари кўзда тутилиши керак.'),
        (293, 29, null, null, '199*. АЁҚС ҳудудидан кириш ва чиқишдан энг кам масофалар: магистраль кўча билан кесишиш чорраҳасигача (унинг қатнов қисмининг яқинроқ чети) - 100 м; маҳаллий аҳамиятдаги кўча ёки тор кўча билан чорраҳагача (унинг қатнов қисмининг энг яқин қисми) - 35 м; ишлаб чиқариш ва турар жой уйларининг деразаси, умумтаълим мактаблари, болалар мактабгача тарбия ва даволаш муассасалари участкалари, дам олиш майдонларигача – ШНҚ 2.9.20-08 “Автомобилларга ёқилғи қуйиш станциялари” 3-иловадаги 1-жадвал талабларига мос ҳолда қабул қилинади'),
        (294, 29, null, null, '200. 187-199-бандларда баён қилинган талаблар ҳам янги, ҳам қайта қурилаётган АЁҚШ ва АЁҚМ ларга тегишли бўлади.'),
        (295, 29, null, null, '201. Идора енгил ва юк автомобиллари, автокорхона автомобиллари гаражлари, трамвай ва троллейбус деполари шаҳарларнинг саноат зоналарида жойлаштирилиши керак. Участкаларнинг тавсия қилинган майдони 47-жадвалда берилган.'),
        (296, 29, null, null, '202. Шаҳар ва қишлоқ аҳоли пунктларида аҳолиси сони, иқлимий, жуғрофий ва бошқа шароитларидан қатъий назар зарур санитариягигиеник шароитларни ва аҳолининг меҳнати, маиший турмуши ва дам олиши учун қулайликларнинг юксак даражасини таъминловчи муҳандислик жиҳозланиш ва ободонлаштиришни кўзда тутиш лозим. Муҳандислик жиҳозланиши мажмуий тарзда, одатда, турар жой, саноат ва бошқа туманларга хизмат кўрсатиш учун сув таъминоти, оқова тармоғи, электр, иссиқлик ва газ таъминоти, алоқа ва бошқа муҳандислик тизимларини мужассамлантириб лойиҳаланиши лозим. Тегишли техник-иқтисодий асосланишда яқиндаги шаҳар ва қишлоқ аҳоли пунктлари эҳтиёжларини таъминлаш учун туман сув, иссиқлик таъминоти, оқова тармоғи тизим- ШНК 2.07.01-03 103-бет лари ва бошқалар тузилишини кўзда тутиш зарур.'),
        (1579, 119, '2024-07-03 11:16:15', '2024-07-03 11:16:15', '16.6. Bino va inshoot fasadi eskiz loyihasining qonunchilik talablariga, shu jumladan, shaharsozlik normalari va qoidalariga mos kelmasligi'),
        (1580, 119, '2024-07-03 11:16:15', '2024-07-03 11:16:15', '16.7. Vakolatli organga ariza beruvchi tomonidan obyektni o‘rganish uchun imkoniyat yaratilmaganligi'),
        (299, 30, null, null, '205. Сувни тозалаш станциялари учун ер участкалари майдони уларнинг унумига қараб, минг м3 /сут, лойиҳага кўра, лекин кўпи билан, га: 0,8 гача 1 0,8 дан 12 гача 2 12 « 32 « 3 32 « 80 « 4 80 « 125 « 5 125 « 250 « 12 250 « 400 « 18 400 « 800 « 24 Изоҳлар: 1. Ер ости манбаларидан сув олиш майдонлари гидрогеологик изланишлар материалларига кўра ҳисоблар асосида олинади. 2. Ер участка майдонлари кейинчалик иш чизмаларига кўра аниқланади.'),
        (300, 30, null, null, '206. Оқова тармоғи тозалаш иншоотлари учун ер участкалари майдони 47-жадвалда кўрсатилганидан ортиқ қабул қилинмаслиги лозим.'),
        (301, 30, null, null, '207. Марказлаштирилган оқова тизими бўлмаганда барча вақтинча ечимлар марказлашган оқованинг умумий схемаси қисмлари бўлиши керак. Қўрғонча қурилишда замин шароитларини ҳисобга олиб септиклар қурилиши тавсия этилади ёки санитария-эпидемиологик хизматининг маҳаллий идоралари билан келишиб оқова станциялари кўзда 104-бет. ШНҚ 2.07.01-03 тутилиши керак. Оқова станциялари ва уларнинг санитария-ҳимоя зоналари учун ажратиладиган ер участкалари ўлчамлари 51-жадвал бўйича ва ҚМҚ 2.04.03-97 «Оқова тармоғи. Ташқи тармоқ ва иншоотлар»га мувофиқ олиниши керак.'),
        (302, 30, null, null, '208. Сув таъминоти манбалари ва сув қувурлари иншоотларининг санитария муҳофазаси ва санитарияҳимоя зоналари ҚМҚ 2.04.02-97 «Сув таъминоти. Ташқи тармоқ ва иншоотлар», оқова тармоқ иншоотларидан санитария-ҳимоя зоналари эса ҚМҚ 2.04.03-97 «Оқова тармоқ. Ташқи тармоқ ва иншоотлар» талабларига мувофиқ қабул қилиниши керак'),
        (303, 30, null, null, '209. Аҳоли пунктларининг бош режаси лойиҳасини тузишда келтирилган шакл бўйича сув баланси тузилиши лозим. (47-жадвал)'),
        (304, 31, null, null, '210. Қаттиқ ва суюқ маиший ахлатларни зарарсизлантириш усулларини техник-иқтисодий ҳисоблар, жойнинг табиий-иқтисодий шароитлари асосида, аҳоли пунктлари гуруҳига боғлиқ ҳолда танлаш лозим. Йирик ва ўта йирик шаҳарлар учун маиший ахлатларни ва тозалаш иншоотлари қуйқаларини саноат қайта ишлаш корхоналарини кўзда тутиш тавсия этилади. Маиший ахлатлар йиғилиши нормаларини 48-жадвал тавсияларига мувофиқ қабул қилиш мумкин.'),
        (305, 31, null, null, '211. Маиший ахлатларни ташиш, зарарсизлантириш ва қайта ишлаш корхоналари ва иншоотларининг ҳамда баъзи оқова иншоотларининг ер участкалари ва санитария-ҳимоя зоналари майдонини 49-жадвал бўйича қабул қилиш керак'),
        (307, 32, null, null, '212. Электроэнергияси сарфи, иссиқлик, газ ва электр таъминоти манбаи қувватига бўлган талаб қуйидагича аниқланади: саноат ва қишлоқ хўжалик корхоналари учун – ишлаётган корхоналар буюртмалари, янги, қайта қурилаётган ёки ўхшаш корхоналар лойиҳалари, шунингдек маҳаллий шароитларни ҳисобга олиб йириклаштирилган кўрсаткичлар бўйича; хўжалик-маиший ва коммунал эҳтиёжлар учун ҚМҚ 2.04.07-99 “Иссиқлик тармоқлари” ва ҚМҚ 2.04.08-96 “Газ таъминоти. Лойиҳалаш нормалари”га мувофиқ. Электр истеъмолининг йириклаштирилган кўрсаткичларини 52- жадвалга мувофиқ қабул қилиш мумкин'),
        (308, 32, null, null, '213*. Кучланиши 11 кВ ва ундан юқори бўлган ҳаво электр узатиш линиялари аҳоли жойлашган ҳудуддан ташқарида ўтказилиши лозим. Ўта йирик ва йирик шаҳарларнинг обод ҳудудларида пасайтирувчи кичик станцияларга кучланиши 110 кВ ва ундан юқори электр узатиш линиялари кабель линиялари орқали тегишли йўлаклар яратиш шартида ўтказилиши лозим. Умумий энергетик тизимларга кирувчи электр узатиш линиялари саноат зоналари ҳамда қишлоқ хўжалик корхоналари ишлаб чиқариш зоналари ҳудудида жойлашуви мумкин эмас. Шаҳарларни қайта қуришда кучланиши 35-110 кВ ва ундан юқори бўлган электр узатиш линияларини аҳоли жойлашган ҳудудлардан ташқарига чиқариш ёки ҳаво линияларини кабель линияларига алмаштириш, бўшаган ҳудудларда турар жой ва жамоатчилик биноларини қуриш мақсадга мувофиқ бўлганда эса, мавжуд чуқур киришли пасайтирувчи очиқ кичик станцияларини ёпиқлари билан алмаштиришни кўзда тутиш лозим.'),
        (309, 32, null, null, '214. Шаҳар аҳоли пунктлари аҳоли жойлашган ҳудудида 4 қаватли ва ва ундан баланд бинолар қурилиши жойларида 20 кВ гача кучланишли электр тармоқлар, шунингдек, курорт мажмуалари ҳудудида барча кучланишлар тармоқлари, одатда, кабель линиялари билан кўзда тутилиши керак. Ташқи ёритиш тармоғининг ҳаво линияларига йўл қўйилади.'),
        (538, 56, null, null, '11.13. Атроф муҳитни ифлослантирувчи асосий манбаларнинг таъсир кўрсатиши характерига доир йириклаштирилган кўрсаткичлар, турли мақсаддаги корхоналарнинг ифлослантирувчи моддаларга квоталари E-иловада келтирилган.'),
        (310, 32, null, null, '215. Трансформатор қуввати 16 минг кВа ва юқорироқ бўлган пасайтириш станциялари ва аҳоли жойлашган ҳудудларда жойлаштириладиган ҳаво линияларининг кабелларга ўтиш пунктлари, курорт мажмуалари ҳудудида эса барча трансформатор кичик станциялари ва тақсимлаш мосламалари ёпиқ турда бўлиши лозим. Ушбу ҳудуд-ларда очиқ кичик станцияларни қўл-лаш лойиҳада асосланиши лозим. Кичик станциялар ва ҳаво линия-ларининг кабельга ўтиши пункт-ларига киришда кабель ва ҳаво линиялари кириши ва чиқиши учун техник йўллар кўзда тутилиши лозим.'),
        (311, 32, null, null, '216. Ёпиқ пасайтириш кичик станциялари ҳамда 110-220 кВ кучланишли комплект ва тақсимлаш мосламалари учун ер майдони 0,8 га бўлиши, ҳаво линияларининг кабель линияларига ўтиш пунктлари майдони эса 0,1 га дан ошмаслиги зарур.'),
        (312, 32, null, null, '217. Алоҳида турадиган тақсимлаш пунктларини ва ҳар бири 1000 кВа қувватига эга бўлган кўпи билан 2 та трансформаторли 6-20 кВ кучланишли кичик станцияларни жойлаштиришда ва шовқиндан сақлаш чораларини кўришда уларнинг турар жой ва жамоат биноларидан узоқлиги камида 10 м, даволаш профилактика муассасалари биносигача камида 25 м бўлиши лозим. Тақсимлаш пунктларини ва 10 кВ кучланишгача металл трансфор- 108-бет. ШНҚ 2.07.01-03 матор кичик станциялари атрофини тўсиқ билан ўраш лозим. Мактаблар ва болалар муассасалари ҳудудларида тақсимлаш пунктлари ва трансформатор кичик станцияларини жойлаштириш мумкин эмас'),
        (313, 32, null, null, '218. Аҳоли пунктларининг иссиқлик таъминотини тасдиқланган иссиқлик таъминоти схемаларига мувофиқ кўзда тутиш зарур. Иссиқлик ва совуқ таъминоти тизимларини танлашда асосий таъсир этувчи омилларнинг барчаси, жумладан техник-иқтисодий, энергетик, шаҳарсозлик, ижтимоий ва экологик омиллар ҳисобга олиниши зарур'),
        (314, 32, null, null, '219 Бир ва икки қаватли қурилмалар ҳудудларида иссиқлик таъминоти схемаси бўлмаганда, марказий иссиқлик таъминоти тизимларини жамоат ва турар жой бинолари гуруҳи учун қозонхоналар шаклида кўзда тутиш мумкин. Шаҳар аҳоли пунктларида тегишли техник-иқтисодий асослаш билан ҚМҚ 2.04.05-97 «Иситиш, шамоллатиш ва кондиционерлаш» талабларига мувофиқ марказлаштирилган совуқ таъминоти тизимларини кўзда тутиш керак'),
        (315, 32, null, null, '220. Турар жой ҳудудларида жойлаштириладиган алоҳида иситиш қозонхоналари учун ер майдони ўлчамлари 53-жадвалга мувофиқ кўзда тутилиши лозим.'),
        (316, 32, null, null, '221. Газ тўлдирувчи станциялар (ГТС) ер участкалари майдони уларнинг унумига боғлиқ, лойиҳага кўра, лекин кўпи билан унумли станцияларга гектар ҳисобидан қабул қилинади: 10 минг т/йил 6 20 минг т/йил 7 30 минг т/йил 8 ГТС дан турли мақсадли бино ва иншоотларгача бўлган масофани ҚМҚ 2.04.08-96 “Газ таъминоти. Лойиҳалаш нормалари” га мувофиқ қабул қилиш лозим.'),
        (317, 32, null, null, '222. Газ тўлдириш станциялари (ГТС) ва оралиқ баллон омборлари ер участкалари майдонлари 0,6 га дан ошмаслиги лозим. Улардан турли мақсадли бино ва иншоот-ларгача бўлган масофани ҚМҚ 02.04.08-96 “Газ таъминоти. Лойиҳа-лаш нормалари” га мувофиқ қабул қилиш лозим.'),
        (318, 32, null, null, '223. Алоқа радиоэшиттириш ва телевидение ёнғин ва қўриқлаш сигнализацияси муҳандислик ускуналари тизимларини диспетчеризациялаш корхоналари, бино ва иншоотларини жойлаштириш белгиланган тартибда тасдиқланган меъёрий ҳужжатлар талабларига мувофиқ амалга оширилиши лозим. Радио уйлари ва телемарказлар биноларини жойлаштиришда халал берувчи манбалардан минимал узоқлашиш талаблари кўрсатилган 52-жадвалга амал қилиш лозим.'),
        (319, 33, null, null, '224. Муҳандислик тармоқлари жойлашуви қуйидагиларни таъминлаши керак: ҳам ер усти, ҳам ер ости маконидан оқилона фойдаланиш; қурилишнинг энг кам қиймати, муҳандислик тармоқларининг сақланиши ва ишончли ишлаши, улардан фойдаланишнинг қулай ва хавфсизлиги; транспорт ҳаракати учун энг кам тўсқинлик, йўл қопламалари ва кўкаламзорларнинг сақланиши, аҳолининг нормал турмуш шароити'),
        (329, 33, null, null, '234. Кўчаларда ва тор кўчаларда техник зоналарни жойлаштиришда уларнинг йўналишини ҳисобга олиш керак. Техник зоналар, одатда, кўчаларнинг жанубий ва ғарбий томонларида жойла-шиши керак, бу уларнинг бошқа томонидан жануб ва ғарбга қаратилган бинолар олд қисмига соя тушишини таъминлайдиган қалин кўкаламзор йўлларини барпо этиш имконини беради.'),
        (1581, 119, '2024-07-03 11:16:15', '2024-07-03 11:16:15', '16.8. Taqiqda turgan obyekt'),
        (1582, 119, '2024-07-03 11:16:15', '2024-07-03 11:16:15', '16.9. Bino va inshootlarni ulushdorlaridan birining roziligi mavjud emasligi'),
        (320, 33, null, null, '225. Аҳоли пунктининг бош режаси қурилишнинг 1-нчи навбати лойиҳаси, саноат тармоғини режалаштириш лойиҳаси турар жой туманининг батафсил лойиҳаси ва микротуман (мавзе, даҳа) лойиҳасини ишлаб чиқишда лойиҳалар таркибига кирувчи муҳандислик тармоқлари схемасида муҳандислик тармоқлари жойлашадиган техник зоналар қуйидаги мақсадларда кўрсатилиши лозим: магистраль йўллар тизими ва муҳандислик тармоқлари тизимининг режавий ажратилиши; муҳандислик тармоқларининг қурилиш ва фойдаланиш сарфларининг камайишини таъминлайдиган юқори ихчамлиги; зарур ер ости маконини келгусидаги ётқизишлар учун захира қилиш; муҳандислик тармоқлари тизимини турар жой тумани ва микротуман (мавзе, даҳа)нинг меъморий - режавий ечими билан узвий боғлаш; ШНК 2.07.01-03 111-бет кўкаламзорларни ҳам муҳандислик тармоқларини қуриш даврида, ҳам фойдаланишда сақлаб қолиш'),
        (321, 33, null, null, '226. Тармоқларни қуриш харажатларини камайтириш мақсадида магистраллараро ҳудуд ичида техник зоналарни жойлаштириш рухсат этилади.'),
        (322, 33, null, null, '227. Муҳандислик тармоқларининг техник зоналари одатда, буталар ва юзаки илдизли кам қимматли дарахтлар ўтказилган майдонлар шаклида жойлаштирилади. Техник зоналарни тротуарлар билан қўшиб юборишга рухсат этилади'),
        (323, 33, null, null, '228. Техник зоналар эни келгусидаги ётқизишлар ҳисобга олинган муҳандислик тармоқлари миқдори билан белгиланади.'),
        (324, 33, null, null, '229. Мавзе, даҳа тармоқлари мавзе (даҳа) қурилишининг ташқи четида эмас, балки, қуйидаги мақсадларда қурилиш ичида юкланиш ўқларига яқинроқ жойлаштирилиши керак: мавзе (даҳа) тармоқлари узунлигини, шунингдек уйларга кириш ва уланишларни қисқартириш; иссиқ-совуқ пунктлари, тортиш ва ҳайдаш станциялари, газ тақсимлаш пунктлари, трансформатор кичик станциялари, телефон шкафлари ва бошқаларни битта бинода – махсус муҳандислик тармоғида жойлаштириш учун қулай шароитларни юзага келтириш; мавзе (даҳа) тармоқларини умумий траншеяларда, бинолар техник ер ости ва ертўлаларда жойлаштириш учун қулай шароитларни юзага келтириш; тармоқлардан фойдаланиш учун шароитларни яратиш; кўчаларда микротуман (мавзе) муҳандислик тармоқлари миқдорини, шаҳар муҳандислик тармоқларини ва кўкаламзорларни жойлаштириш шароитларини яхшилаш мақсадларида қисқартириш'),
        (325, 33, null, null, '230. Мавзе (даҳа) қурилмасини лойиҳалашда оқова, тарновлар ва дренажларнинг аниқ схемасини қўйиш имконини, шунингдек бинолар қурилишининг мустақил навбатини тузиш учун муҳандислик ускуналари (иссиқ-совуқ пунктлари, насос станциялари ва бошқалар) ва тармоқларнинг микротуман (мавзе, даҳа)га киришларини рельеф бўйича паст томондан жойлаштириш зарур.'),
        (326, 33, null, null, '231. Бевосита биноларга ёндош участкаларнинг ободонлаштирилиши яхшиланиши учун: бинолар остидаги техник бўлимлар ва ертўлалардан (сейсмик ҳудуд ва тупроқ туридан қатъи назар) транзит иссиқлик тармоқлари ва сув қувурларини ажратилган бўлинмаларда жойлаштирган ҳолда ўтказиш учун максимал фойдаланиш; иссиқлик тармоғи ва сув қувурларининг кириши, шунингдек, оқова сувларининг чиқиши, асосан, биноларнинг ён чет қисмидан ўтказилиши; газ қувурларининг кириши биноларнинг ҳовли томонидан ўтказилиши; алоқа кабель тармоқлари (қувурларда) биноларнинг ён қопламаси тагида, электр кабеллар – бевосита ён қопламалар бўйлаб жойлаштирилиши; муҳандислик тармоқларининг умумий ҳандақда ва зич қурилиш районларида, иқтисодий асосланганда эса – умумий коллекторларда ётқизилишини кўзда тутиш лозим. Турар жой бинолари, мактаблар ва болалар боғчалари ертўлаларида 112-бет. ШНҚ 2.07.01-03 0,4 6 ва 10 кВ кучланишли кабельли электр линияларини ўтказиш ва паст вольтли шчитларни ўрнатиш мумкин эмас.'),
        (327, 33, null, null, '232. Мураккаб замин шароитларида (лёссли, чўкувчи, сизот сувларининг юқори сатҳи) қурилиш участкаларида ва мураккаб режавий шароитларда ер усти ва ердан кўтарилган иссиқлик тармоқларини ўтказиш мумкин.'),
        (328, 33, null, null, '233. Турар-жой ҳудудлар ичида иссиқлик тармоқларининг каналсиз ётқизилиши тақиқланади. Унга саноат зоналари ҳудудида унинг йўллар қатнов қисмлари ва тротуарлардан ташқарида жойлашуви шарти билан рухсат берилади.'),
        (330, 33, null, null, '235. Микротуман (мавзе) техник зоналари схемасини ечишда қиймати ва иссиқлик таъминоти ҳамда рельеф шароитига кўра оқова тармоқларини «белгиловчи тармоқлари» бўлади.'),
        (333, 33, null, null, '238. Мавзедаги ер ости муҳандислик тармоқларининг энг оқилона схемасини танлаш тармоқларни ётқизишнинг турли вариантлари техник-иқтисодий ҳисобларни қиёслаш асосида аниқланиши керак. Микротуман (мавзе, даҳа) қурилмаси режавий ечимлари турларининг муҳандислик тармоқлари узунлиги ва ихчамлиги нуқтаи назаридан самарадорлиги 1000 м 2 умумий майдондаги тармоқлар зичлиги кўрсаткичлари билан белгиланади, улар одатда 55-жадвалда келтирилган кўрсаткичлардан ошмаслиги лозим.'),
        (334, 33, null, null, '239. Ер ости тармоқларининг кўчанинг кўндаланг кесимида жойлашуви уларнинг лойиҳаланаётган туманда транспорт ва пиёда ер ости йўллар, эстакадалар, метрополитен йўналишлари ва ҳоказоларни ҳисобга олиб жойлашуви схемалари ассосида аниқланади. Микротуман (мавзе) муҳандислик тармоқларини янги турар жой қурилиши ҳудудларида магистраль кўчаларнинг қизил чизиғи ичида жойлаштириш тақиқланади'),
        (335, 33, null, null, '240. Умумшаҳар ва туман аҳамиятидаги магистраль кўчаларда бевосита кўчага хизмат кўрсатувчи ва уларсиз шаҳар транспортининг нормал ишлаши мумкин бўлмаган тармоқларни (шаҳар электр транспорти кабельи, кўча ёритиш кабельи, кўча ҳаракатини тартибга солиш кабельи, тарновлар ва ҳоказо), шунингдек, транзит сув қувурлари, иссиқлик тармоқлари, газ қувурлари, оқова суви коллекторлари ва махсус мақсадли тармоқларни жойлаштириш мумкин.'),
        (336, 33, null, null, '241. Кўчанинг кўндаланг кесимида ер ости муҳандислик тармоқларини қизил чизиқдан тахминан қуйидаги тартибда жойлаштириш тавсия этилади: телефон канал тармоғи, электрокабеллар, иссиқлик тармоғи, газ қувурлари, сув қувурлари, оқова тармоғи, тарнов; бунда тармоқларнинг тик ҳолати уларга эркин уланишни таъминлаши керак.'),
        (337, 33, null, null, '242. Ёритиш кабеллари ёритиш устунлари ёнида транспорт кабельлари эса (трамвай, троллейбус) кўчанинг қатнов қисми четида ётқизилади.'),
        (338, 33, null, null, '243. Оқова тармоғи ва сув қувури одатда кўкаламзор зонасида жойлашади.'),
        (339, 33, null, null, '244. Кабель тармоқлари, одатда, тротуарлардан бирининг тагида жойлашиши керак. Кўчанинг иккинчи томонидаги тротуар иссиқлик тармоғини жойлаштириш ёки келгусидаги ётқизишлар учун қўлланади'),
        (340, 33, null, null, '245. Иссиқлик қувури, сув қувури ва кўп сонли кабелларни бир вақтда ётқизишда, ривожланган ер ости хўжалиги бўлган шаҳар магистралларини қайта қуришда, кўчанинг кўндаланг кесимида тармоқларни алоҳида ётқизиш учун жой бўлмаганда, магистраль кўчалар ва темир йўллар билан кесишганда муҳандислик тармоқларини умумий ҳандақларда ётқизиш кўзда тутилиши керак. Трансформатор кичик станциялари ва АТС лардан чиқишда, шунингдек кўп сонли алоқа электр кабелларини ётқизиш мўлжалланаётган кўчаларда, одатда, кабель ҳандақлари лойиҳаланиши лозим. Муҳандислик тармоқларини бевосита тупроқда ётқизишда техник жиҳатдан мақсадга мувофиқ бўлган барча ҳолатларда қувурларни умумий ҳандақда биргаликда ётқизишни кўзда тутиш лозим. Ёнувчи суюқликлар ва газлар қувурлари ва кучланиш кабелларини биргаликда ётқизиш тақиқланади'),
        (341, 33, null, null, '246. Муҳандислик тармоқлари орасида, шунингдек, улардан бинолар ва иншоотлар пойдеворигача масофалар бу тармоқлардаги камеералар, қудуқлар ва бошқа қурилмалар ўлчами ва жойлашуви, монтаж ва таъмир шароитларидан келиб чиқиб энг кам миқдорда белгиланиши керак.'),
        (342, 33, null, null, '247.* Энг яқин ер ости муҳандислик тармоқларидан бино ва иншоотларгача текис масофалар (ёруғда) одатда кўпи билан 56- жадвалда кўрсатилгандек килинади. 56- ва 57-жадвалдаги масофалар иншоотлар хавфсизлиги ва ишончлилиги талабларини таъминловчи тегишли техник тадбирлар бажарилганида камайтирилиши мумкин.'),
        (343, 33, null, null, '248. Қўшни ер ости муҳандислик тармоқлари орасида текис масофа уларнинг параллель жойлашувида, одатда, 55-жадвалдагилардан ортиқ қабул қилинмайди'),
        (344, 33, null, null, '249. Муҳандислик тармоқлари ўзаро кесишганда энг кам тик масофалар 56-жадвал бўйича қабул қилинади'),
        (345, 33, null, null, '250. Иссиқлик тармоғи канали ирригация тармоғи билан кесишганда иссиқлик таъминоти ва ирригация лойиҳаларида иссиқлик каналига сув ҳамда қуйқа киришини бартараф этувчи чора-тадбирларни амалга ошириш кўзда тутилиши лозим.'),
        (1583, 119, '2024-07-03 11:16:15', '2024-07-03 11:16:15', '16.10. Davlat ro‘yxatidan o‘tkazuvchi organda ushbu yer uchastkasi qonunchilik hujjatlarida belgilangan tartibda olib qo‘yilganligi haqidagi ma’lumotlarning mavjudligi'),
        (346, 33, null, null, '251. Газ қувурлари турли мақсадли каналлар ва туннеллар билан кесишганда уларни ушбу қурилмалар устида ёки остида канал ёки туннелнинг ташқи деворларидан икки томонига 2 м чиқадиган ғилофларда жойлаштирилиши лозим. Босими 0,6 МПа (6 кгс/см2 ) гача бўлган газ қувурларини турли мақсадли туннеллар орасидан ғилофда ўтказишга рухсат этилади.'),
        (347, 33, null, null, '252. Қувурларнинг темир йўл ва трамвай йўллари, автомобиль йўллари билан кесишиши, одатда, 90° бурчакда кўзда тутилиши лозим. Айрим ҳолларда, тегишли тарзда асосланган ҳолда кесишиш бурчагини 45° гача камайтириш мумкин. Газ қувурлари ва иссиқлик тармоқларидан остряклар, крестовиналар учи ва сўрувчи кабелларнинг рельсларга уланиш жойларигача бўлган масофа трамвай йўллари учун - камида 3 м, темир йўллар учун - 10 м бўлиши лозим.'),
        (348, 33, null, null, '253*. Бевосита ерга ётқизиладиган кабель линияларининг электрлаштирилган рельсли транспорт билан кесишиш жойлари йўл ўқига нисбатан 75-90° бурчак остида кўзда тутилиши лозим. Кесишиш жойлари остряклар, крестовиналар охири ва сўрувчи кабелларнинг рельсларга уланиш жойларигача бўлган масофа трамвай йўллари учун - камида 3 м, темир йўллар учун - 10 м бўлиши лозим. Кабель линияси ҳаво линиясига ўтган тақдирда кўтарма таглиги ёки темир йўл ёки автомобиль йўли тўшамаси четидан камида 3,5 м юзага чиқиши лозим. Темир йўл ва трамвай йўллари билан кесишиш жойларида рельс таглиги ва телефон канализацияси орасидаги тик масофа камида 0,5 м бўлиши лозим'),
        (349, 33, null, null, '254*. Метрополитен иншоотларининг муҳандислик тармоқлари ШНК 2.07.01-03 117-бет билан кесишиши 90° бурчак остида кўзда тутилиши лозим, қайта қуриш шароитида кесишиш бурчагини 60° гача камайтиришга рухсат этилади. Муҳандислик тармоқларининг метрополитен станция иншоотлари билан кесишиши мумкин эмас. Кесишиш участкаларида қувурлар бир томонга нишаб бўлиши ва ҳимоя конструкциялари (пўлат ғилофлар, монолит бетон ёки темирбетон каналлар, ҳандақлар, туннеллар) ичида бўлиши лозим. Метрополитен иншоотлари ташқи қопламасидан ҳимоя конструкциялари охиригача масофа ҳар бир томонга камида 10 м бўлиши, қоплама ёки рельс таглиги (ер усти линияларида) ва ҳимоя конструкцияларигача тик масофа камида 1 м бўлиши лозим. Газ қувурларини тоннеллар тагида ўтказиш мумкин эмас. Изоҳ: Метрополитен иншоотлари билан кесишиш жойларида босимли қувурлар пўлатдан ясалган ва кесишиш участкасининг икки томонидан сув чиқариш қурилмали қудуқлар ҳамда тиргак арматура ўрнатилган бўлиши лозим'),
        (350, 33, null, null, '255. Ер ости муҳандислик тармоқлари йўловчилар ўтиш жойлари билан кесишганда қувурларни туннеллар остидан, электр ва алоқа кабелларини туннеллар устидан ўтказиш лозим'),
        (351, 33, null, null, '256. Саноат корхоналари ва омборларини таъминлаш учун тез ёнувчи ва ёнилғи суюқликлари, шунингдек суюлтирилган газ қувурларини обод ҳудуддан ўтказишга рухсат этилмайди. Магистраль қувурларни аҳоли пунктлари ҳудудидан ташқарида ҚМҚ 2.05.06-96 «Магистраль қувурлар» га мувофиқ ётқизиш лозим. Аҳоли пунктлари ҳудудидан нефть маҳсулотлари қувурларини ўтказишда ҚМҚ 2.05.12-97 «Шаҳарлар ва бошқа аҳоли пунктлари ҳудудидан ўтказиладиган нефть маҳсулотлари қувурлари» талабларига амал килиш лозим.'),
        (352, 33, null, null, '257. Аҳоли яшаш пунктларининг муҳандислик тайёргарлиги истиқболли қурилиш ҳудудларини шаҳарсозлик жиҳатдан ўзлаштирилишини таъминлаш, қурилиш ва унинг узоқ муддатли барҳарорлиги учун энг қулай шароитларни яратишдан, қурилиш учун хавфли ҳисобланган физик-геологик (ер кўчиши, силжиш, чўкиш, суффозия), гидрологик ва гидрогеологик жараёнлар ривожланишини бартараф этишдан иборат.'),
        (353, 33, null, null, '258. Ҳудуднинг муҳандислик тайёргарлигига оид тадбирлар таркиби аҳоли пунктларининг бош режаларини ишлаб чиқишдан камида 1 йил олдин тузиладиган иқлим тавсифномалари, физик-геологик, гидрогеологик тадқиқотлар ва радиоэкологик текширув билан белгиланади.'),
        (372, 34, null, null, '277. Суғориш тизими иншоотларини ҚМҚ 2.02.02.-98 «Гидротехник иншоотлар асослари», ҚМҚ 2.05.03-97 «Кўприклар ва қувурлар», ҚМҚ 2.06.01-97 «Гидротехник иншоотлар. Асосий лойиҳалаш қоидалари» ва ҚМҚ 2.06.03-97 «Суғориш тизимлари. Лойиҳалаш нормалари» талабларига мувофиқ лойиҳалаш лозим'),
        (1575, 119, '2024-07-03 11:16:15', '2024-07-03 11:16:15', '16.2. Loyihada bino va inshootning hajmi va maydoni o‘zgarganligi'),
        (1584, 119, '2024-07-03 11:19:05', '2024-07-03 11:19:05', '16.11. Bino va inshootning avariya holatida ekanligi'),
        (1585, 120, '2024-07-03 11:20:45', '2024-07-03 11:20:45', '19.1. So‘rovnomada noto‘g‘ri ma’lumotlar ko‘rsatilganligi'),
        (354, 33, null, null, '259. Муҳандислик тайёргарлик тадбирларини қуйидаги турларга ажратиш лозим: Бутун ҳудудга тааллуқли умумий чоралар: кўкаламзорларни сунъий суғориш; сувни тўсиб олиб кетиш, табиий ёки сунъий гидрографик тармоқни меъёрга солиш, қирғоқбўйини белгилаш ва ободонлаштириш; антисейсмик тадбирлар; тик режалаштириш. Алоҳида аҳоли пунктлари ҳудудларига тегишли махсус чоралар: сизот сувларининг сатҳи юқори бўлган ҳудудларда уларнинг сатҳини пасайтириш; ер кўчиши, силжиши ва турли мақсадли сув оқимларининг қирғоқларини емирилишдан ҳимоя қилиш; сел оқимлари ва тошқин сувлари босиб кетишидан ҳимоя қилиш; ер чўкишига қарши тадбирлар.'),
        (355, 34, null, null, '260. Суғориш тизимлари суғориш, сув чиқариш-декоратив ва микроиқлимий вазифаларни ўзида жамлаши, қаттиқ қопламаларни намлаш, ювиш ва сунъий сув ҳавзаларини оқар сув билан таъминлаши лозим'),
        (356, 34, null, null, '261. Суғориш тизимининг бир вақтнинг ўзидаги харажати қуйидаги формула асосида аниқланади: Q тиз= Q суғ + Q оқар + Q сеп + Q ҳав, бу ерда, Qсуғ – кўчатзорларни суғоришга бериладиган сарфи; Qоқар – тармоқ оқими ва тўлишини яратиш учун зарур бўлган сув сарфи; Qсеп – қаттиқ қопламаларга сепиш ва ювиш учун сарф; Qҳав – ҳавзалар оқарлигига сарфи.'),
        (357, 34, null, null, '262. Суғоришга доир сув сарфини суғориш тартиби, ҳудуд баланси, ҳудуднинг айрим қисмларини кўкаламзорлаштириш фоизига мувофиқ суғориш нормалари асосида аниқлаш лозим. Суғориш нормалари яшил майдонларнинг дендрологик ШНК 2.07.01-03 121-бет таркиби ҳамда гидромодуль ҳудудлаштириш асосида қабул қилинади'),
        (358, 34, null, null, '263. Қаттиқ қопламаларни ювиш ва намлашдаги сув сарфи суткасига 1 м 2 қопламага 1 л ҳисобидан келиб чиққан ҳолда аниқланади.'),
        (359, 34, null, null, '264. Чуқурлиги камида 1,5 м бўлган ҳавзаларда сув сарфи барча сув миқдорининг алмашувидан келиб чиққан ҳолда 3 гектаргача бўлган майдонда - чўмилиш мавсумида тўрт марта, 3 га дан ошиқ майдонда эса - мавсумда уч марта аниқланади'),
        (360, 34, null, null, '265. Тармоқ оқарлигига сарфлар қуйидагича аниқланади: тақсимлаш тармоғи бўйича – бош сарфининг камида 1/3 қисми; суғориш тармоғи бўйича – тасарруф ҳудудига тақсимлаш тар-моғидан бериладиган оқимлар сони ва қуйқа бермайдиган тезликларда энг секин тўлиши бўйича.'),
        (361, 34, null, null, '266. Кўкаламзорларни суғо-риш усуллари аҳоли пунктларининг сув билан таъминланганлиги, таснифи ва улар ҳудудларининг функционал зоналаштиришини ҳисобга олиб қабул қилиниши керак.'),
        (362, 34, null, null, '267. Юқори ободонлаштириш талаблари ва кўп ер ости коммуникациялари бўлган ўта йирик, йирик ва катта шаҳарларда бетонли ариқлар тармоғини лойиҳалаш зарур.'),
        (363, 34, null, null, '268. Кам қаватли қурилмалар устун бўлган ўрта ва кичик шаҳарлар, туман марказлари ва қўрғонча туридаги қурилишли қишлоқ аҳоли пунктлари нишаблар ва ҳудуд шароитларига кўра ҳам бетонли, ҳам тупроқ ичидаги очиқ тармоқ билан суғорилиши мумкин.'),
        (364, 34, null, null, '269. Аҳоли пунктлари марказларининг ва шаҳар шакллантирувчи магистраллар бўйлаб қурилмаларнинг ҳудудини очиқ тармоқлардан сув тиндириб ёки сизот сув олиш қудуғидан оладиган стационар босимли тармоқларидан ёмғирлатиб суғориш афзал.'),
        (365, 34, null, null, '270. Маданий мерос объектлари (тарихий қурилиш) бўлган аҳоли пунктларида суғориш анъанавий усуллар билан ва маданий мерос объектлари (тарихий қурилма) ва алоҳидаги ёдгорликларнинг муҳофазасига талабларни ҳисобга олиб кўзда тутилиши керак.'),
        (366, 34, null, null, '271. Сув қуйиш” орқали суғоришга фақатгина махсус мақсадли кўкаламзор ҳудудларда - шамол ва шовқиндан сақлаш учун барпо этилган иҳоталарда, сув олиш иншоотларини санитария-муҳофазаси зоналари, корхоналар ва коммунал тузилмаларнинг санитария-ҳимоя зоналарида рухсат этилади.'),
        (367, 34, null, null, '272. Сув таъминоти етарли бўлмаган ҳудудларда сув сарфини анча камайтириш имконини берувчи суғориш усуллари (томчилаб суғо-риш, боши берк жўяк-ариқлар бўйи-ча инфильтрация усулида суғориш) ни назарда тутиш тавсия этилади'),
        (368, 34, null, null, '273. Аҳоли пунктларини суғоришда сув манбалари сифатида ер усти сувлари (каналлар, дарёлар, кўллар, сув омборлари), ер ости - сизот сувлари ва оқова тизимининг қум фильтрларида тозаланган оқова сувларидан фойдаланилади. Сув таъ-миноти тахчил ҳудудларда кузги-қишки ва эрта баҳорги юзаки ёғин сувлари тўпланадиган ҳовузларини, булоқ сувлари каптажи (сувни қувур ичига олиш)ни назарда тутиш лозим.'),
        (373, 34, null, null, '278. Магистраль гидрографик тармоқни тартибга солиш ва қисман қайта трассалашни фақатгина ҳудудни ўзлаштириш учун янада қулай шароит яратиш, тармоқдан фойдаланиш шароитларини яхшилаш, энг кам қирғоқбўйи майдонларини барпо этиш учун кўзда тутилади.'),
        (374, 34, null, null, '279. Қирғоқбўйи майдони ўлчамлари сув омборлари ва бошқа сув ҳавзалари, дарё ва магистраль канал ва ҳандақларнинг сув муҳофазаси зоналари тўғрисидаги Низом билан белгиланади.'),
        (375, 34, null, null, '280. Юза оқовасини ташкил этиш ва янги ўзлаштирилаётган ерларни ёмғир, қор ва ёндош ҳудудларни суғоришдан ортиқча сувлари тошишидан ҳимоя қилиш аҳоли пунктининг суғориш тармоғи билан ва аҳоли пунктларидан ўтувчи транзит гидрографик тармоғининг иш тартиби билан уйғунлашган бўлиши лозим.'),
        (376, 34, null, null, '281*. Алоҳида ҳандақнинг бутун ҳавзаси ҳудудидан ёмғир оқовасини ҳисоблаш ёмғирнинг энг кўп интенсивлиги усулида ҚМҚ 2.04.03- 97 «Оқова. Ташқи тармоқлар ва иншоотлар» га мувофиқ, кичик (маҳаллий) сув йиғиш майдонлари учун эса ШНҚ 2.05.02-07 «Автомобиль йўллари» ва идора нормаларига мувофиқ бўлиши лозим'),
        (377, 34, null, null, '282. Ҳандақ тармогига юза сувни буриш, одатда, очиқ ариқлар суғориш тармоғи билан унинг жала оқовасини ўтказиш имкониятини ҳисоблаб текшириб кўзда тутилади. ўта йирик, йирик ва катта шаҳар аҳоли пунктлари марказлари зоналарида, шунингдек саноат корхоналари ҳудудларида, шаҳар шакллантирувчи магистралларнинг жадал қурилиши ҳудудларида, ёпиқ қувурли жала тармоғи ёки металл панжаралар билан ёпилган лотоклар кўзда тутилиши мумкин'),
        (378, 34, null, null, '283. Магистраль кўчалар ва майдонларда очиқ жала тармоғи қатнов қисмига ёнма-ён, ёпиқ жала тармоғи – кўчатзор зонасида, қатнов қисмининг чети бўйлаб жала қабул қилувчи қудуқлар қурилади'),
        (379, 34, null, null, '284. Ўта йирик, йирик ва катта шаҳар аҳоли пунктлари ҳудудида фақат жала ва дренаж оқовасини қабул қилувчи ҳандақ тармоғи ёпиқ ШНК 2.07.01-03 123-бет бўлиши лозим. ўрта ва кичик шаҳар аҳоли пунктлари ҳудудида ҳандақ тармоғи очиқ бўлиши мумкин, тармоқ бўйлаб қирғоқ тасмаси белгиланади. Ёпиқ ҳандақлар «қуруқ» даврнинг суғориш тармоғининг охирги қолдиқларидан иборат сарфларини ўтказишига ҳисоб-китоб орқали текшириб туриш лозим. Қуйқа берувчи тезликлар юзага келганда ҳандақнинг бош қисмига ювиш оқимларини юборишни кўзда тутиш керак'),
        (380, 34, null, null, '285. Суғориш тизимларининг очиқ оқизиб юбориш ва транзит каналларини жала ва транзит сарфлариниг биргаликда ўтказишга ҳисоблаб текшириш лозим'),
        (381, 34, null, null, '286. Гидрографик тармоққа (дарёлар, анҳорлар) оқизишдан аввал жала оқовасини ҚМҚ 2.04.03-97 «Оқова. Ташқи тармоқлар ва иншоотлар»га мувофиқ тозалаш лозим. Очиқ ёки ёпиқ манбалардан сув олишда, шунингдек, суғориш ва оқова сувларини гидрографик тармоққа оқизишда сув хўжалиги органлари билан келишиш лозим.'),
        (382, 34, null, null, '287. Ҳудудни тик режалаштириш, одатда, ер массаларининг нолли баланс мувозанати билан белгиланади (жарликларни кўмиш, сув босадиган ва тошадиган ҳудудларга ер солиш бундан мустасно). У жала оқовасини тупроқ эрозиясига йўл қўймайдиган тезликда оқизиб юборилишини таъминлаши лозим. Ер сатҳи нишабликларини тупроқ хоссаларига кўра 59-жадвал бўйича қабул қилиш керак. 59-жадвал Тупроқлар Сатҳ нишаблари, %, ёғинларнинг йиллик миқдорига кўра, мм 150 гача 151- 300 301- 450 450 ва ортиқ Лойли 2-50 2-50 3-50 3-50 Қумли 2-30 10-30 20-30 30 Лёсс, майда қум 2-10 3-10 5-10 10 II турдаги чўкувчан тупроқлар 2-10 3-10 5-10 5-10 Тупроқ-ўсимлик қатлами вақтинча сақлаб, кейинчалик фойдаланилиши лозим'),
        (383, 34, null, null, '288. Сизот сувларнинг даражаси юқори участкаларда уни пасайтириш зарур қуритиш нормасини таъминлайдиган ёпиқ дренажлар қурилиши орқали кўзда тутилади. Кичик шаҳар ва қишлоқ аҳоли пунктлари бундан мустасно, уларда қуритишнинг зарур меъёри жала сувлари ҳандақлари билан қўшилган ҳандақ тармоғи ёрдамида таъминланади'),
        (384, 34, null, null, '289. Дренаж турини муҳандислик-геологик ва гидрогеологик тадқиқотлар асосида танлаш лозим. Сув чиқарилган тошлоқ, қумлоқ, қумлоқтупроқли ерларда чиқарилган сувлардан (улар яроқли бўлган ҳолларда) суғориш ва техник эҳтиёжлар учун фойдаланиш билан тик ёки биргаликдаги дренажни кўзда тутиш зарур. Сувни чиқариш лаёқати паст бўлган қумлоқ ва соғтупроқ участкаларда ўзи оқар ёки мажбурий оқизиладиган текислик дренаж кўзда тутилади'),
        (387, 35, null, null, '292. Барча турдаги қурилишлар учун антисейсмик тадбирларни ҚМҚ 2.01.03-96 «Сейсмик район-ларда қурилиш» га мувофиқ ишлаб чиқиш лозим. Аҳоли пунктларининг бош режаларини ишлаб чиқишда Ўзбекистон Республикаси Фанлар Академиясининг Сейсмология институти томонидан ишлаб чиқилган микросейсморайонлаштириш хариталарига ва Давархитектқурилиш қўмитаси томонидан тасдиқланган “Шаҳарсозлик, режалаштириш ва жойлаштириш учун Ўзбекистон Республикаси ҳудудини тупроқ шароитларига кўра районлаштириш схемаси”га амал қилиш лозим'),
        (388, 35, null, null, '293. Сейсмик даража ижобий ўсишини келтириб чиқарувчи омиллар (геодинамик жараёнлар, сув босиши, сизот сувларининг астасекин нураши ва бошқалар) сабабли мураккаблашган ҳудудлар қурилишдан чиқарилиши керак, ҳудудий захиралар танг бўлган ҳолда бу омиллар тегишли тадбирларни амалга ошириш орқали нейтралланиши лозим.'),
        (389, 35, null, null, '294. Қирғоқлари эрозия жараёнлари (ўпирилиш, жарликлар ҳосил бўлиши) кучайган очиқ сув оқимлари бўйича қирғоқларни мустаҳкамлаш тадбирлари ҳудуддан функционал фойдаланиш билан узвий боғланиши лозим. Сарфлари фақат жала сувлардан ташкил топадиган очиқ сув оқимларининг туби бўйлаб қувур ва дренаж ҳандақларини ўрнатиб қисман ёки бутунлай ер билан тўлдиришга, ҳудуддан кўкаламзорлаштириш, кўп поғонали транспорт иншоотлари, коммунал қурилмалар, савдо дўконлари барпо этишга рухсат этилади.'),
        (390, 35, null, null, '295. Қирғоқни муҳофаза қилиш тадбирларини лойиҳалаштириш ҚМҚ 2.06.01-97 “Гидротехник иншоотлар. Асосий лойиҳалаш қоидалари», ҚМҚ 2.06.04-97 «Гидротехник иншоотларга тушадиган юк оғирлиги ва таъсирлар (тўлқин, муз, кемалар таъсири)»га биноан амалга оширилиши лозим'),
        (391, 35, null, null, '296. Сел хавфи бор ҳудудларда селдан ҳимоя тадбирлари аҳоли пунктлари бош режаларини ишлаб чиқишда сел оқимларидан сақланиш минтақавий схемалари билан узвий боғлиқ ҳолда кўзда тутилиши зарур. Иқтисодий асослашда умумий тусдаги тадбирлар (селга қарши тўғонлар қуриш, сой ва сувсиз сойликларда қўшимча сиғимлар яратиш, сел оқимларини ҳосил қиладиган ҳудудни дарахтзорга айлантириш), ҳамда муайян аҳоли пунктини ҳимоя қилишдан иборат хусусий чоралар (очиқ сув оқимларини кенгайтириш, аҳоли пунктининг юқори томонидаги ҳудудни марзалар билан ўраб мустаҳкамлаш, очиқ сув оқимларида селга қарши дамбалар қуриш) қабул қилиниши мумкин'),
        (392, 35, null, null, '297. Дарёнинг қуйи поғоналарида жойлашган аҳоли пунктларини тошқин сувлар босишидан  ҳимоя қилиш ҳудудни сизот сувлари даражасидан камида 0,5 метрга кўтарилгунча ер билан ёки ўтириндидан тўлдириш, ёки ҚМҚ 2.06.05-98 «Тупроқ материалларидан барпо этиладиган дамбалар» ва ҚМҚ 2.06.01-97 «Гидротехник иншоотлар. Асосий лойиҳалаш қоидалари»га мувофиқ дамбалар қуришни кўзда тутиш лозим'),
        (393, 35, null, null, '298. Тошқин сувларининг ҳисоб горизонти сифатида қурилиб бўлган ёки қурилиши мўлжалланган ҳудудлар учун - сувнинг 100 йилда бир марта такрорланувчи энг юқори даражаси ва парк ва текислик иншоотлари ҳудудлари учун 10 йилда такрорланувчи юқори даражаси қабул қилинади'),
        (394, 35, null, null, '299. Чўкувчанликка қарши тадбирлар муҳандислик-геологик туманлаштириш харитасига асосан тупроқлар чўкувчанлик турини ҳисобга олган ҳолда белгиланади'),
        (395, 36, null, null, '300. Шаҳарсозлик лойиҳалашнинг барча босқичларида атроф муҳитни муҳофаза қилиш ва табиий захиралардан оқилона фойдаланишга доир мажмуавий тадбирлар 8- иловага мувофиқ «Атроф-муҳитга таъсир тўғрисидаги баёнот лойиҳаси» таркибида амалга оширилиши лозим.'),
        (411, 37, null, null, '316. Юза ва ер ости сувларини соғломлаштириш тадбирлари уларнинг ифлосланиш манбаларини йўқ қилиш, аҳоли пунктларининг муҳандислик таъминоти, аҳоли пунктлари ҳудудида ҳавзалар соҳилида қурилиш қоидаларига риоя қилишни кўзда тутиши керак.'),
        (412, 37, null, null, '317. Тупроқлар ва ўсимликларнинг муҳофазаси ифлослантирувчилар таркиби, ифлосланиш жараёнларининг тезлиги, манзара шароитлари ва тупроқ қатлами хусусиятларига боғлиқ ҳолда мажмуий амалга ошириш керак.'),
        (413, 37, null, null, '318. Бўлимни ишлаб чиқишда тупроққа атмосфера ёғинлари, қўнувчи чанг ва аэрозоллар билан саноат чиқиндиларининг кириши, тупроқнинг газсимон бирикмаларни бевосита сингдириб, уларнинг ўсимликлар ютиб олишини ҳисобга олиш ҳамда экологик жиқатдан эқтимолий хавфли объектлар атрофида чанг-газ тутиб қолувчи дарахтзорлар билан санитария-ҳимоя зоналарини барпо этишни кўзда тутиш керак.'),
        (396, 36, null, null, '301. Атроф муҳитни муҳофаза қилиш тадбирлари тизимига қуйидагилар киради: Шаҳарсозлик тадбирлари: табиий каркас сақланиши ва ривожланиши (сув оқимлари ва улар бўйлаб кўкаламзорлаштириш, дарахтзорлар); ҳудуднинг режавий ташкилоти схемалари ва лойиҳаларини ишлаб чиқишда дарёлар, сойлар, каналлар, сув омборлари бўйидаги сув муҳофазаси зоналари, шунингдек курорт захира ларининг қатъий тартибдаги санитария зоналари ва ер ости сувлари конлари шаклланиши зоналаридан функционал фойдаланиш талабларини таъминлаш; ўта йирик ва йирик шаҳарлар атрофида яшил зоналарни барпо этиш; амалдаги экологик ва шаҳарсозлик қонунчилигига зид ҳолда жойлашган ишлаб чиқариш турлари ихтисосини ўзгартириш ё бошқа жойга кўчириш; йўл-транспорт тизимини ривожлантириш ва қайта қуриш. Муҳандислик-техник тадбирлар: саноат ва энергетика объектларни қайта қуриш, чиқиндисиз технологияларни жорий этиш; муҳандислик инфратузилмаси ва коммунал хўжаликни юқори технологик даражада ривожлантириш; шаҳарлар ва бошқа аҳоли пунктларини муҳандислик ускуналари билан тўлиқ ҳажмда таъминлаш.'),
        (397, 36, null, null, '302. Табиий каркасни сақлаш ва унинг атроф ҳудудга фаол таъсирини таъминлаш мақсадида табиий каркас ва у таъсир кўрсатаётган ҳудуд майдонлари нисбатини 1:3 даражасида сақлаш лозим.'),
        (398, 36, null, null, '303. Шаҳарларнинг атроф яшил зоналари дам олиш, санитария- гигиеник ва хўжалик мақсадларида фойдаланилиши керак. Яшил зоналарда уларнинг ўз экологик, санитария-гигиеник ва дам олиш вазифаларини бажаришига салбий таъсир этувчи хўжалик фаолияти тақиқланади.'),
        (399, 36, null, null, '304*. Чўл ва ярим чўл зоналарида жойлашган шаҳар ва қишлоқ аҳоли пунктлари атрофида шамолчангдан ҳимоя қилувчи дарахтзорлар барпо этиш, эрозияга учраётган тепаликлар, жар ва сойликларни кўкаламзорлаштириш чораларини кўзда тутиш лозим. Ҳимоя иҳотазорлари эни камида: ўта йирик ва йирик шаҳарлар учун – 500 м, катта ва ўрта шаҳарлар – 100 м, кичик шаҳар ва қишлоқ аҳоли пунктларида - 50 м бўлиши керак'),
        (400, 36, null, null, '305. Шаҳарлар ва қишлоқ аҳоли пунктларини қуриш ва ривожлантириш учун ҳудудлар асосан қишлоқ хўжалиги учун яроқсиз ерлардан иборат бўлиши лозим. Суғориладиган ва қуритилган ерларда, шудгор майдонларда, кўп йиллик мевали дарахтлар ва узумзорлар мавжуд бўлган ер участкаларида, шунингдек сувни муҳофаза қилиш, шамол-чангдан сақловчи дарахтзор майдонларда қурилишга фавқулодда ҳоллардагина рухсат этилади, баҳолаш кўрсаткичлари юқори бўлган ерларда эса - тақиқланади. Қурилиш ман этилган ерлар рўйхати маҳаллий ҳокимият органлари томонидан белгиланади.'),
        (401, 36, null, null, '306. Фойдали қазилмалар конлари чегаралари ичида аҳоли пунктлари, саноат зоналари ва бошқа халқ хўжалиги объектларини лойиҳалаштириш ва қуриш тақиқланади. Фойдали қазилмалар жойлашган майдонларда иморатлар қуриш ва ер ости иншоотлари барпо этишга фавқулодда ҳоллардагина, фойдали қазилмаларни қазиб олишга тўсқинлик юзага келмаслиги ва қурилишнинг иқтисодий мақсадга мувофиқлиги исботланиши шарти билан давлат ер ости бойликларини бошқариш ва тоғ-кон назорати идораларининг рухсати билангина йўл қуйилиши мумкин. Фойдали қазилмаларни қазиб олиш ва қайта ишлаш, шунингдек бошқа хўжалик фаолияти оқибатида бузилган ерлар тикланиши лозим.'),
        (402, 36, null, null, '307. Қуйидаги ерларда бинолар, иншоотлар ва магистраль коммуникациялар барпо этишга рухсат этилмайди: қўриқхоналар, ботаника боғлари, дендрологик парклар ва сувни муҳофаза қилиш зоналарида агар қуриладиган объектлар уларга хизмат қилишга мўлжалланмаган бўлса, қурилиши мумкин эмас; шаҳарларнинг яшил майдонларида қуриладиган объектлар дам олиш, спорт ёки шаҳар атрофи хўжалигига хизмат кўрсатишга мўлжалланмаган бўлса, қурилиш ишлари тақиқланади; гидрометеорологик станцияларни ҳимоя қилиш зоналарида; қуриладиган объектлар сув манбаларидан фойдаланишга боғлиқ бўлмаса, сув таъминоти манбалари санитария муҳофазаси биринчи зонасида ва сув қувури иншоотлари майдончаларида; қуриладиган объектлар курортлар табиий шифобахш воситаларидан фойдаланиш билан боғлиқ бўлмаса курортларнинг санитария муҳофазаси биринчи зонасида; радиоактив чиқиндилар билан ифлосланган ҳудудларда (Ўзбекистон Республикаси Санитария-назорат идоралари белгилаган муддат тугагунча); кўмир, сланец шахталари ва руда бойитиш фабрикаларининг чиқинди тупроқ чиқариш зоналарининг хавфли жойларида, ер кўчадиган, сел оқимлари юзага келадиган, қор кўчкиси содир бўладиган ва органик чиқиндилар билан ифлосланган ер участкаларида; тўғонлар ёки дамба бузилиши натижасида эҳтимолий офатли тошқин зоналарида (офатли тошқин зонаси – тошқин 1,5 м ва чуқурроқ бўлиб, бинолар ва иншоотлар бузилиши, инсонлар ўлими, корхоналар ускуналарининг ишдан чиқишига олиб келиши мумкин бўлган ҳудуддир); сейсмик районларда фаол ёриқларга бевосита ёндош зоналарда; магистраль қувурларнинг ҳимоя зоналарида.'),
        (415, 38, null, null, '319. Турар жой қурилишларида шовқин манбалари автомобиль ва темир йўл транспорти, темир йўл транспорти объектлари, фуқаролик авиацияси самолётлари ва вертолётлар, коммунал-транспорт ва саноат корхоналари, очиқ трансформатор кичик станциялари, насос қурилмалари ва иссиқхоналар бўлади.'),
        (403, 37, null, null, '308*. Аҳоли жойлашган ҳудудларни атмосфера ҳавосини ифлослантириш манбалари бўлган, шунингдек юқори ёнғин хавфи мавжуд ишлаб чиқариш корхоналарига нисбатан шамолга рўпара тарафдан (кўпроқ эсувчи шамол йўналиши учун) жойлаштириш керак. Атмосфера ҳавосининг алоҳида тозалигини талаб этувчи корхоналарни атмосфера ҳавосининг ифлосланиши манбалари бўлган қўшни корхоналарга нисбатан устун шамол йўналишида жойлаштириш керак эмас. Чорвачилик, паррандачилик ва қайвонотчилик корхоналари, заҳарли кимёвий моддалар, биопрепаратлар, ўғит сақлаш омборлари ва бошқа ёнғин-портлашдан хавфли бўлган омбор ва ишлаб чиқаришлар, ветеринария муассасалари, чиқиндиларни қайта ишлаш объект ва корхоналари, иссиқхоналари, тозалаш иншоотлари, очиқ турдаги гўнг омборлари обод ҳудуд ва ишлаб чиқариш зонасининг бошқа корхона ва объектларига нисбатан шамолдан пана тарафида (устун шамол йўналиши учун) жорий меъёрий ҳужжатларга мувофиқ жойлаштирилиши лозим.'),
        (404, 37, null, null, '309. Табиий муҳитнинг замонавий ва келажакдаги ҳолатини баҳолашда манзара таркибий қисмларининг тикланиш қобилиятини ва ҳудудларга антропоген таъсир манбалари тақсимланишининг ўзига хослигини ҳисобга олиш керак'),
        (405, 37, null, null, '310. Атмосфера ҳавосидаги зарарли моддалар концентрациялари майдонини ҳисоблаб аниқлаш лозим. Энг кўп концентрацияларнинг ҳисоб майдони муаммоли зоналар (энг юқори йўл қўйиладиган нормалардан ошувчи) нуқтаи назаридан таҳлил қилинади. Оғир вазиятли участкалар ва болалар, даволаш, дам олиш ва профилактик муассасалар жойлашиш жойлари майдалаб (10 м ҳисоб қадами билан) чиқиндилар салбий таъсири чегарасини аниқлаш учун кўриб чиқилиши лозим'),
        (406, 37, null, null, '311. Концентрацияларни ҳисоблаш учун бирламчи маълумот сифатида ҳудудни ифлослантиришга энг кўп қисса қўшувчи асосий саноат корхоналари чиқиндиларининг кўрсаткичларидан фойдаланиш лозим (Давлат Табиат қўмитасининг маҳаллий идоралари билан келишув бўйича). Ҳисобларда ҳам барқарор, ҳам кўчма манбалар чиқиндиларини ҳисобга олиш керак. Чиқиндилар миқдори Ўзбекистон Республикаси Давлат Табиат қўмитаси тасдиқлаган дастурларни қўллаб ҳисобланади.'),
        (407, 37, null, null, '312. Ҳисоб натижаларини муҳит экологик ҳолатини меъёрга солиш тадбирларини ишлаб чиқиш учун у ёки бу манбаларни ифлосланиш даражасига қўшган ҳиссасига қараб фарқлаб кўриб чиқиш лозим. Изоҳ. Шаҳарсозлик ва ташкилиймаъмурий тадбирлар технологик ва муҳандис-техник тадбирлар билан бирга ишлаб чиқилади, уларга: ишлаб чиқариш майдонларида жойлашган чиқинди манбалари ва турар жой қурилишининг жойи ўзгартирилиши; санитария-ҳимоя зоналарини ташкил этиш; саноат зоналарини ташкил этиш; магистралларни қайта қуриш, айланма йўллар, тез ҳаракатли магистралларни ташкил этиш киради'),
        (408, 37, null, null, '313. Табиат муҳофазаси тадбирларини ишлаб чиқишда сифатли ер ости суви конлари мавжудлигини ҳисобга олиш лозим. Чучук ер ости суви конлари бўлган зоналарда шаҳарсозлик фаолияти тегишли ташкилотлар (Давлат табиат қўмитаси ва Ўзбекгидрогеология) билан келишилиши керак'),
        (409, 37, null, null, '314. Юзадаги ва ер ости сувлари муҳофазаси сув оқимлари, сизот сувлар ва кўрилаётган ҳудуд-даги уларнинг ифлосланиши манбаларининг замонавий аҳволи Сув муҳофазаси зоналари тўғрисидаги Низом асосида қурилиб, Ўзбекистон Республикаси Вазирлар Мақкамасининг 07.04.1992 даги №174-сонли Қарорига мувофиқ амалга оширилиши лозим'),
        (410, 37, null, null, '315. Шаҳарсозлик ҳужжатларини ишлаб чиқишда сув муҳофазаси зоналарининг энг кам эни: Сув омборлари учун – сувнинг энг кам таянч сатҳида қирғоқ белгисидан, бошқа ҳавзалар учун – сувнинг кўп йиллик ўртача сатҳи-дан: катта сув омборлари ва бошқа ҳавзалар атрофида - (ҳажми 1,1 дан 10 км3 гача) - 300-500 м; ўрта сув омборлари ва бошқа ҳавзалар атрофида (ҳажми 0,6 дан 1 км3 гача) – 200-300 м; кичик сув омборлари ва бошқа ҳавзалар атрофида (ҳажми 0,2 дан 0,5 км3 гача) – 100-200 м; жуда кичик сув омборлари ва бошқа ҳавзалар атрофида (ҳажми 0,1 км3 дан кичик) – 100 м гача бўлади. дарёлар учун – ўртача кўп йиллик сув сатҳидан: катта дарёларда (сув сарфи 100 м 3 /сек дан кўп) – 300-500 м; ўрта дарёларда (сув сарфи 5 дан 100 м3 /сек гача) – 100-300 м; кичик дарёларда (сув сарфи 2 дан 5 м3 /сек гача) – 50-100 м; ШНК 2.07.01-03 129-бет жуда кичик дарёларда, сойларда (сув сарфи 2 м3 /сек гача) – 50 м гача. Дарёлар, кўл ва сув омборлари сув муҳофазаси зоналарида тақиқланади: қаттиқ маиший ахлат ва қайта ишланмаган саноат чиқиндилари учун полигонлар, заҳарли кимёвий моддалар, ўгитлар ва ёнилғи-мойлаш материаллари омборлари, заҳарли кимёвий моддаларни аппаратурага солиш майдончалари, чорвачилик мажмуалари ва фермалар, дафн этиш жойларининг жойлашуви; табиат муҳофазаси ва давлат санитария-эпидемиологик назорат идоралари билан келишилмасдан ишлаб чиқариш ва ижтимоий соқа янги объектларини қуриш ва мавжудларини кенгайтириш. Дарё, сойлар ва сув омборлари соҳилларидаги сув муҳофазаси зоналари ичида қирғоқ бўйи ажратилиб, унда хўжалик фаолияти кескин чекланади ва бирор саноат-фуқаро иморатлари, турар жой, ижтимоиймаиший ва маданий объектларнинг қурилиши тақиқланади. Сув омборлари қирғоқ бўйларининг энг кам эни ёндош ерлар турлари ва ён бағирлар тиклигига боғлиқ, ҳавзалар ҳажмини ҳисобга олиб белгиланади: шудгорда ва кўп йиллик дарахтзорда ён бағирлар тиклиги 3 градусгача бўлса - 35 м гача, 3 дан 8 градусгача – 35-40 м; барча ҳолларда қирғоқ бўйи эни камида 20 м белгиланади. Дарё ва сойларнинг қирғоқбўйи эни: шудгор ва кўп йиллик экинзорда ён бағирлар тиклиги 3 градусгача бўлса - 35-55 м, 3 градусдан ортиқ – 55-100 м; пичанзор ва яйловларда ён бағирлар тиклиги 3 градусгача бўлса – 25-35 м, 3 градусдан ортиқ – 35-50 м; ўрмон майдонлари ва дарахтбутазорларида ён бағирлар тиклиги 3 градусгача бўлса – 35-55 м, 3 градусдан ошиқ – 55-100м белгиланади. Энг катта қийматлари энг кўп эрозия бўлаётган тупроқларга тегишлидир.'),
        (429, 38, null, null, '329.4. Шаҳарсозлик ҳужжатларини тайёрлашда “Маданий мерос объектларига туташиб турган ҳудудлар қурилиши бўйича Регламент” ишланади.'),
        (416, 38, null, null, '320. Турар жой ва жамоат бинолари ҳамда уларга ёндош ҳудудлар учун йўл қўйиладиган шовқин даражалари «Турар жой ва жамоат бинолари ва турар жой қурилиши ҳудудида йўл қўйиладиган шовқиннинг санитария нормалари», шунингдек ҚМҚ 2.01.08-96 «Шовқиндан ҳимоя қилиш» бўйича олинади.'),
        (417, 38, null, null, '321. Авиация шовқинининг йўл қўйиладиган даражалари ГОСТ 22283-88 «Авиация шовқини. Турар жой қурилишида шовқиннинг йўл қўйиладиган даражалари ва уларни ўлчаш усуллари» га мувофиқ белгиланади.'),
        (418, 38, null, null, '322. Шаҳарлар ва бошқа аҳоли пунктлари кўчалар ва йўлларида транспорт оқимларининг шовқин тавсифлари ГОСТ 20444-85 «Шовқин. Транспорт оқимлари. Шовқин тавсифларини ўлчаш усуллари» га мувофиқ ўлчаш зарур.'),
        (419, 38, null, null, '323. Транспорт оқимлари, рельсли транспорт, коммуналтранспорт объектлари ва бошқа шовқин манбаларидан товуш эквивалент даражалари - LАэкв, дБА бош режа босқичида 9-илованинг 1-6-жадваллари бўйича аниқланади.'),
        (420, 38, null, null, '324. Аэродромларда ва аэропортларда самолётлар учиши ва қўнишида товушнинг ҳисобланадиган энг кўп ва эквивалент даражалари «Фуқаролик авиацияси аэропортларининг атрофларида турар жой қурилишининг шовқин шароитлари юзасидан чекланиш зоналари белгиланиши бўйича тавсиялар»га мувофиқ белгиланади, СССР Давлат қурилиш қўмитаси, НИИСФ, М., 1987 .'),
        (421, 38, null, null, '325. Товуш даражасининг дарахтзорлари иҳоталари билан дБА даги LАкўк пасайтирилиши 9-илованинг 7-жадвалига кўра олиниши керак.'),
        (422, 38, null, null, '326. Турар жой биноларида вибрацияларнинг йўл қўйиладиган даражалари ҚМҚ 2.05.04-97 «Метрополитенлар» ва «Турар жой уйларида йўл қўйиладиган вибрациялар санитария нормалари» № 1304-75 га мувофиқ келиши лозим.'),
        (423, 38, null, null, '327. Радиотелевизион узатиш станциялари, фуқаролик авиациясининг радиолокация станциялари, юқори кучланишли электр узатиш ҳаво линияларини жойлаштиришда № 0096-00-сонли «Аҳолини радиотехник объектлар яратувчи электрмагнит майдонлардан ҳимоя қилишнинг вақтинча санитария нормалари ва қоидалари», № 2284-81-сонли «Электрмагнит майдонининг даражасини аниқлаш ва фуқаролик авиацияси ОВЧ-, УВЧ- ва СВЧ-радиотехник воситаларини жойлаштиришга гигиеник талабларни аниқлаш бўйича услубий кўрсатмалар» ҳамда «Электр қурилмалар ўрнатиш қоидалари» (ПУЭ) га асосланиш лозим. Радиациядан ҳимоя қилишни таъминлашда Ўзбекистон Республикаси Давлат Геология қўмитаси «Қизилтепагеология» Давлат геологик корхонаси ўтказадиган шаҳарлар ва бошқа аҳоли пунктларининг ШНК 2.07.01-03 131-бет радиоэкологик тадқиқоти натижаларини ҳисобга олиш лозим.'),
        (424, 38, null, null, '328. Радиоактив моддаларни ишлаб чиқаришда, ишлов беришда, қўллаш, сақлаш, ўзгартириш, зарарсизлантириш ва кўмилишида радиацион хавфсизлик таъминланиши «Радиацион хавфсизлик нормалари» (НРБ 76/87) ва «Радиоактив мода-лар ва ионлаштирувчи нурланишларнинг бошқа манбалари билан ишлашнинг асосий санитария нормалари» (ОСП 72/87) га мувофиқ амалга оширилади.'),
        (425, 38, null, null, '329*. Аҳоли пунктлари ҳудудини ривожлантириш ва қурилмаларини режалаштириш лойиҳаларида маданий мерос объектларини муҳофаза қилиш ва фойдаланиш талабларига риоя қилиш лозим. Бунда шаҳарсозлик фаолиятини алоҳида тартибга солишни чегаралари (маданий мерос объектларини муҳофаза қилиш зоналари, қурилишнинг тартибга солиниши ва табиий манзарани муҳофаза қилиш зоналари) белгиланиши керак. Маданий мерос объектларини муҳофаза қилиш зоналари алоҳида бинолар ва иншоотлар, уларнинг ансамбллари ва мажмуалари, шунингдек бошқа қимматбаҳо тарихий-маданий шаҳарсозлик мажмуалари ва элементлари учун кўзда тутилади.'),
        (426, 38, null, null, '329.1. Шаҳарасозлик лойиҳалаштиришнинг барча босқичларида “Маданий мерос объектларини муҳофаза қилиш ва улардан фойдаланиш лойиҳаси” таркибининг тадбирлар мажмуаси 11-иловага мувофиқ кўзда тутилиши лозим'),
        (427, 38, null, null, '329.2. Жаҳон маданий мероси рўйхатига киритилган Бухоро, Самарқанд, Шахрисабз, хўжалик ва бошқа турдаги фаолият маданий мерос объектларини гуруҳли муҳофаза қилиш ва қурилмаларнинг бадиий ўзига хослигини сақлаш бўйича шаҳарсозлик чораларини ҳисобга олиб амалга ошириш зарур.'),
        (428, 38, null, null, '329.3. Таркибида умумдавлат ва маҳаллий аҳамиятга эга маданий мерос объектлари бўлган шаҳарлар ва аҳоли пунктларидаги шаҳарсозлик фаолияти Ўзбекистон Республикаси қонунларига биноан алоҳида тартибга солинади'),
        (430, 38, null, null, '330*. Маданий мерос объектларининг (шаҳар-қўриқхоналар, тарихий ансамбллар, ландшафт мажмуалари, алоҳида жойлашган архитектура, халқ меъморчилиги, этнография, археология ёдгорликлари, қадимги қабристонлар (некрополлар), мақбаралар, (алоҳида турган қабрлар) шаҳарсозлик муҳофазаси “Маданий мерос объектларига туташиб турган ҳудудлар қурилиши бўйича Регламент”га мувофиқ амалга оширилиши лозим. Регламент барча ҳудудий даражаларда (республика, минтақа, агло- 132-бет. ШНҚ 2.07.01-03 мерация, шаҳар ва қишлоқ аҳоли пунктлари, шунингдек, аҳоли пунктларидан ташқаридаги объектлар), меъморий-шаҳарсозлик лойиҳалаштиришнинг барча босқичларида амалга оширилиши ва маданий меросни сақлашнинг узоқ муддатли дастурларида изчил ечилиши зарур'),
        (431, 38, null, null, '331. Тарихий аҳоли пунктларини мажмуий қайта қуришда тарихий зоналарнинг ижтимоий ўзгартирилишига оид бўлган ва шу зоналарда яшовчи аҳоли манфаатларини, миллий маданиятнинг маънавий ва моддий бойликларини сақлаш ва ривожлантиришга интилишини акс эттирувчи дастурларни ишлаб чиқиш керак.'),
        (432, 38, null, null, '332*. Аҳоли пунктлари ҳудудларининг ривожланиш ва қурилмаларини режалаштириш лойиҳаларида маданий мерос объектларининг бузилиши, кўчирилиши ёки ҳолатида ўзгаришларни кўзда тутиши мумкин эмас. Фавқулодда ҳолларда уларниг ҳолатини ўзгартириш бўйича таклифлар маданий меросни муҳофаза қилиш давлат идоралари билан олдиндан келишилгандан сўнг Ўзбекистон Республикаси Вазирлар Маҳкамасига кўриб чиқиш учун тақдим этиш керак. Олдиндан таркиб топган қурилмалар ҳудудларида қимматли тарихий муҳитини шаҳарсозлик муҳофаза қилиш мажмуий қуриш-таъмирлаш усуллари билан таъминланиши керак, шу билан бир вақтнинг ўзида меъморий ва маданий қиймати бўлган биноларни қайта таъмирлаш бўйича ишлар, мавжуд биноларни қайта қуриш, замонавийлаштириш ва капитал таъмирлаш ишлари, муҳит характерини бузмайдиган ҳолда танланган янги қурилиш ишлари, ҳудуднинг муҳандислик жиҳозланиши ва ободонлаштирилиши ишларини кўзда тутиш ва олиб бориш зарур. Анъанавий режалаштириш тузилмаларини ва халқ меъморчилиги элементларини сақлаш билан бирга тарихий шаклланган каркасни сақлаш ва ривожлантириш, ҳамда анъанавий турар жой қурилмаларини регенерация қилиш (қайта яроқли ҳолатини тиклаш) маданий мерос объектларини муҳофаза қилиш ва улардан оқилона фойдала-ниш буйича меъморий-шаҳарсозлик тадбирлари тизимига киритилиши лозим.'),
        (433, 38, null, null, '333*. Маданий мерос объектларидан транспорт ва муҳандислик коммуникацияларигача бўлган масофалар камида қўйидагича бўлиши лозим: тез ва тўхтовсиз ҳаракат магистралларининг қатнов қисмлари, саёз қурилган метрополитен линияларигача мураккаб рельеф шароитида - 100 м; текис рельефда - 50 м; сув қувурлари, оқова тармоғи ва иссиқлик таъминоти тармоқларигача (тақсимловчиларидан ташқари)-15 м; бошқа ер ости муҳандислик тармоқларигача - 5 м. Қайта қуриш шароитларида муҳандислик тармоқларигача ушбу масофалари камайтирилиши мумкин, лекин улар камида: сув олиб борувчи тармоқларгача – 5 м; сув олиб бормайдиган тармоқларгача – 2 м бўлиши лозим. Бунда қурилиш ишларини амалга оширишда махсус техник тадбирларни ўтказишни таъминлаш лозим. Маданий мерос объектларидан суғориладиган дарахтзор ва кўкаламзорлаштириш тизимларигача бўлган масофалар камида қуйидагича бўлиши зарур: - мураккаб рельеф шароитида - 50м; - текис рельефда - 100м'),
        (434, 38, null, null, '334*. Алоҳида тартибга солинадиган шаҳарсозлик фаолияти объектларига ва умумдавлат ва маҳаллий аҳамиятга эга маданий мерос объектларини муҳофаза қилиш ва қайта тиклаш билан боғлиқ бўлган шаҳарсозлик лойиҳалаштиришидан аввал тарихий зоналарнинг уларни аҳоли пунктининг тизимига киритиш учун истиқболий сақланиши ёки ўзгартирилишини белгилаб берувчи лойиҳадан аввалги илмий-асосланган тадқиқотлар ўтказилиши керак.'),
        (436, 39, null, null, '1.5. Ушбу ШНҚ ни ишлаб чиқишда Ўзбекистон Республикасининг қуйидаги ижтимоий-иқтисодий ривожланиш тенденциялар ҳисобга олинди: бозор иқтисодиётига ўтиш, қишлоқ хўжалиги корхоналари мустақил хўжалик юритишининг ривожланиши, бошқарувнинг марказлашмаган тизимга ўтказилиши, мулкчиликнинг турли шаклларининг жорий этилиши; кўп тармоқли иқтисодиёт ва бозор муносабатларининг ривожланиши, қишлоқ хўжалиги ишлаб чиқаришини хилма-хил шаклларининг мавжудлиги аҳоли жойлаштирилиши ҳам турлича шаклда (кичик қишлоқлардан йирик қишлоқ аҳоли пунктларигача) бўлишини тақозо этади ва ҚФЙнинг бутун ҳудудида аҳолининг яшаши учун қулай шарт-шароитларнинг нормативларга мувофиқ таъминланишини талаб қилади.
'),
        (458, 39, null, null, '5.3. Аҳоли сонининг ҳисоб-китоби қурилишнинг биринчи 5-7 йиллик навбатини ажратиб кўрсатган ҳолда 10-15 йиллик муддат учун бажариш лозим. Аҳоли сонининг ҳисоб-китоби, аҳоли пунктлари ҳудудларини ривожланириш, турар жой-фуқаролик ва ишлаб чиқариш қурилиши ҳажмларини белгилаш учун асос бўлиб ҳисобланади.'),
        (1586, 120, '2024-07-03 11:20:45', '2024-07-03 11:20:45', '19.2. Bino va inshootning ixtisoslashishini o‘zgartirish, qayta qurish, rekonstruksiya qilish hamda o‘z hududida qo‘shimcha bino va inshootlarni qurish qonunchilik hujjatlari talablariga, shuningdek, shaharsozlik normalari va qoidalariga hamda sanitariya qoidalari va normalariga muvofiq emasligi'),
        (1587, 120, '2024-07-03 11:20:45', '2024-07-03 11:20:45', '19.3. Obyektni o‘rganish uchun vakolatli organga imkoniyat yaratilmaganligi'),
        (437, 39, null, null, '1.6. ҚФЙ объектларини ёки қишлоқ хўжалиги корхонасини ривожлантиришнинг меъёрлаштирилиши қуйидаги энг муҳим принципларга асосланган: ҚФЙ ёки қишлоқ хўжалиги корхонаси ҳудудини аҳолининг яшаш шароитлари яхшиланишини назарда тутувчи мақсадга мувофиқ равишда функционал зоналаштириш, қишлоқ хўжалиги ишлаб чиқаришининг янада ривожлантириш, табиий, инсон ва моддий ресурслардан самарали фойдаланиш шартида атроф муҳитни муҳофаза қилиш, рекрацион хизматни ташкил қилиш ва мавжуд ресурсларни ривожлантириш каби чоратадбирларни кўзда тутувчи комплекс ривожлантирилиши; турли даражалардаги элементлардан иборат жойлаштириш тизимини ташкил этувчи барча аҳоли пунктларининг ўзаро боғлиқ ҳолда ривожлантирилишини рағбатлантириш; жойнинг табиий рельефи, ирригация тизими ва транспорт-пиёда алоқаларини максимал тарзда сақлаб қолиш; барча қишлоқ аҳоли пунктларини, улардаги яшаш қулайликларини оширишга қаратилган кўп омилли комплекс ривожлантиришни таъминлаш.
'),
        (438, 39, null, null, '1.7. Қуйидагилар лойиҳалаштириш нормаларига табақалаштирилган тарздаги ёндошувнинг услубий негизи ҳисобланади: аҳоли пунктларини лойиҳалашни жойлаштириш тизимларининг ҳозирги ва истиқболдаги ривожланиши билан боғлиқ тарзда олиб боришга имкон берадиган жойлаштириш схемалари; аҳоли пунктларини аҳолининг лойиҳадаги сони ва уларнинг шаҳарсозлик нуқтаи назарида ривожлантириш режимига асосланган таснифланиши; Ўзбекистон Респрубликаси ҳудудининг минтақаларни ландшафт ва иқлимнинг ўхшаш шароитларига кўра ажратадиган ҳамда уларни ҳисобга олиш ва нохуш омилларни режалаштириш воситалари (кўкаламлаштириш, сув билан таъминлаш ва шу кабилар) ёрдамида яхшилашга доир тегишли тавсияларга эга бўлган ландшафт-иқлимли районлаштирилиши; йўл тармоғининг қишлоқ фуқа-ролари йиғини ҳудуди тузилмасини тўғри шакллантириш ва транспорт алоқаларини ташкил қилишга имкон берадиган таснифлаш;
'),
        (439, 39, null, null, '1.8. Ҳисоб-китобларда зарур ахборотнинг тўлиқ бўлиши учун ушбу меъёрий ҳужжатнинг айрим бўлимларига, манбаларга тегишли ҳаволалар қилинган ҳолда, бошқа меъёрий ҳужжатларнинг жадваллари киритилди.'),
        (440, 39, null, null, '2.1. ҚФЙ ни турли ҳудудий даражалар тизими, яъни: қишлоқ хўжалиги ишлаб чиқаришининг ўз чегаралари доирасида, маъмурий қишлоқ хўжалик тумани, вилояти ёки унинг бир қисми, минтақа доирасида ва, ниҳоят, Республика агросаноат комплекси доирасида ташкил этилишидан иборат агросаноат комплексининг элементи сифатида лойиҳалаштириш лозим.'),
        (441, 39, null, null, '2.2. Қишлоқ хўжалигидаги ишлаб чиқариш фаолиятини ташкил этишнинг ўзига хос хусусиятлари минтақанинг тарихий қарор топган ва табиий шарт-шароитлари асосида, ижтимоий-иқтисодий ривожланишнинг умумреспублика прогнозини ҳисобга олган ҳолда, қуйидаги асосий масалаларни ҳал қилиб туриб белгиланади: ШНҚ 2.07.04-19 7 ишлаб чиқариш объектларини ҚФЙ ёки қишлоқ хўжалиги корхонаси ҳудудида жойлаштириш; хўжалик ичидаги транспорт ва муҳандислик инфратузилмасини жойлаштириш.'),
        (442, 39, null, null, '2.3. ҚФЙ ҳудудида жойлашган фермер хўжаликларининг ердан ва сувдан фойда-ланиш шароитлари, хўжаликларнинг ихтисослашуви, ишлаб чиқариш ҳажмлари, жойлаштириш тизими ва йўл алоқаларини ўрганиш асосида белгиланган чегаралари лойиҳада ҳисобга олиниши лозим.'),
        (443, 39, null, null, '2.4. Фермер хўжаликлари, чорва-чилик ва бошқа ишлаб чиқариш объектларини улардаги қишлоқ хўжалиги ерлари (кўп йиллик мевали дарахтзорлар, узумзорлар, суғориладиган ва лалми ерлар, яйлов ва пичанзорлар)нинг майдонлари ва ҳудудларини белгилаган ҳолда жойлаштириш режаси ҚФЙ ҳудудларини ташкил қилиш бўйича лойиҳавий таклифларни ишлаб чиқиш учун асос сифатида қўлланилиши лозим'),
        (459, 39, null, null, '6.1. Замонавий бозор муносабатлари шароитида қурилишнинг ҳар хил турларини: қўрғонлар, кам қаватли, зич жойлаштирилган, текис ҳудудларда ва мураккаб рельефли участкаларда қуриладиган уй-жой тузилмаларини қўллаш лозим, бунда уй-жой қурилиши қулай уй-жой муҳитини шакллантиришнинг кўп турдаги талабларига (шаҳарсозлик, демографик, табиий-иқлимий, эстетик) жавоб бериши керак. Қишлоқдаги манзилгоҳларда уй-жой қурилишининг элементлари таркиби, ўлчамлари ва меъморий-режавий ташкил этилиши оилаларнинг функционал-маиший жараёнлари, жамоанинг қўшничилик муносабатлари (маҳалла) ва ижтимоий уюшмаларнинг бошқа турлари билан белгиланади'),
        (444, 39, null, null, '3.1. Ишлаб чиқариш объектларининг жойлаштирилиши ҚФЙ аҳолисининг ишлаб чиқаришга оид фаолиятини пухта иқтисодий таҳлил этиш асосида ва қабул қилинган жойлаштириш тизимига мувофиқ амалга оширилиши лозим. Ишлаб чиқаришга мўлжалланган объектларнинг режавий тузилмаси хўжалик юритишнинг ўзига хос хусусиятларини акс эттириши ва ишлаб чиқариш кучларини янада ривожлантиришга ва аҳоли фаровонлигини оширишга қаратилган барча тадбирларнинг муваффақиятли бажарилишига кўмаклашиши зарур. Ишлаб чиқариш объектларини қишлоқ аҳоли пунктларининг аҳолиси учун пиёда етиб олиш радиусида, йўллар, сув ва электр таъминоти тармоқлари ёнида, маҳаллий шароитлар (иқлим, жойнинг рельефи, шамолларнинг асосий йўналишлари, очиқ сув ҳавзаларининг мавжудлиги, ер ости сувларидан фойдаланиш имконияти ва бошқалар)ни ҳисобга олган ҳолда жойлаштириш лозим. Ишлаб чиқаришга мўлжалланган объектларнинг қуввати ва ихтисослашуви қишлоқ хўжалиги корхонасининг хўжалик фаолияти йўналишларини (пахтачилик, ғаллакорлик, шоликорлик, боғдорчилик, чорвачилик ва бошқ.) ҳисобга олган ҳолда белгиланади.'),
        (445, 39, null, null, '3.2. Қуйидагилар ишлаб чиқариш объектлари ҳисобланади: қишлоқ хўжалиги маҳсулотларини қайта ишлайдиган корхоналар; қишлоқ хўжалиги техникаси ва автотранспортини таъмирлаш, техник хизмат кўрсатиш ва сақлаш корхоналари; қишлоқ хўжалиги маҳсулотлари омборхоналари ва сақланадиган жойлар; иссиқхоналар ва иссиқхона-парник хўжаликлари; чорвачилик, паррандачилик, ҳайвонотчилик фермалари, озуқа тайёрлаш пунктлари, ҳовузчилик (балиқчилик) хўжаликлари; ШНҚ 2.07.04-19 8 ветеринария муассасалари; халқ истеъмоли товарларини ишлаб чиқарувчи кичик корхоналар, халқ хунармандчилиги устахоналари'),
        (446, 39, null, null, '3.3. Атроф муҳитга зарарли чиқитларни ва ифлослантирувчи моддаларни чиқариш манбалари ҳисобланган технологик жараёнларга эга ишлаб чиқариш объектлари аҳоли истиқомат қилувчи ҳудудлардан санитария ҳимоялаш минтақалари (зоналари) билан ажратилиши лозим.'),
        (447, 39, null, null, '3.4. Ишлаб чиқариш объектларини қуриш учун участкаларни ажратишда аҳоли пунктларини сани-тария ҳимоялаш зоналарини шамоллар йўналишини инобатга олган ҳолда яратилишини (2-жадвал), шунингдек лойиҳалашнинг ветеринария, ёнғинга қарши ва технологик меъёрларини ҳисобга олиш керак бўлади. Санитария-муҳофаза зоналари ердан фойдаланишдан чиқариб ташланмайди ва қишлоқ хўжалиги ишлаб чиқариши эҳтиёжлари учун фойдаланилиши керак. Санитария-муҳофазалаш зонасида турар жой бинолари, мактабгача ва мактаб ёшидаги болалар учун таълим муассасалари, соғлиқни сақлаш ва дам олиш муассасалари, спорт иншоотлари, боғлар, хиёбонлар, боғдорчилик ширкатлари ва полизларни жойлаштиришга йўл қўйилмайди. Чорвачилик фермалари ва комплекслари қурилиши учун ер участкалари майдонлари боғланиб боқиладиган фермалар учун бир бош қора молга – 100-150 м2 ; боғланмасдан боқиладиган фермалар учун – 125-175 м2 ҳисобидан келиб чиқиб ҳисобланади.'),
        (448, 39, null, null, '4.1. Қишлоқ аҳоли пунктлари жойлаштириш бўйича ягона тизимнинг элементлари ҳисобланиб, қарор топган ҳудудий тузилмалар асосида шакллантирилади: маъмурий туман; қишлоқ (маҳалла (МФЙ), овул (ОФЙ)) фуқаролари йиғини (ҚФЙ); қишлоқ аҳоли пунктлари.'),
        (449, 39, null, null, '4.2. Жойлаштиришнинг оқилона тизими ишлаб чиқариш кучларини ривожлантириш истиқболларига ва қишлоқ хўжалиги ишлаб чиқаришининг қабул қилинган ташкилий тузилмасига жавоб бериши керак. Бунда жойлаштириш тизими учун ягона бўлган ижтимоий, ишлаб чиқариш, муҳандислик-транспорт ва бошқа инфратузилмаларнинг шакллантирилиши, шунингдек, қишлоқ хўжалик корхонаси доирасида истиқболда ривожлантириладиган меҳнат, маданий-маиший ва рекреацион алоқалар ҳисобга олиниши лозим.'),
        (450, 39, null, null, '4.3. Қишлоқ аҳоли пунктлари аҳолининг лойиҳавий сонига боғлиқ равишда 3-жадвалга мувофиқ гуруҳларга бўлинади.'),
        (495, 44, null, null, '9.2. Муҳандислик коммуникацияларининг жойлаштирилишида қуйидагиларни таъминлаши керак: ер устидаги ва ер остидаги кенгликдан оқилона фойдаланиш; муҳандислик тармоқлари қурилишининг энг кам қийматли бўлиши, сақланиши ва ишончли амал қилиши, улардан фойдаланишнинг қулайлиги ва хавфсизлиги; транспорт ҳаракати учун тўсқинликларнинг жуда кам бўлиши, йўл қопламалари, яшил экинзорларнинг сақланиши, аҳоли турмуши учун нормал шарт-шароитлар.'),
        (1588, 120, '2024-07-03 11:20:45', '2024-07-03 11:20:45', '19.4. Davlat ro‘yxatidan o‘tkazuvchi organda ushbu ko‘chmas mulk obyektida nizolar borligidan dalolat beruvchi sud hujjatlarining mavjudligi'),
        (451, 39, null, null, '4.4. ҚФЙ ҳудудида жойлаштириш юзасидан лойиҳавий таклифларни ишлаб чиқишда қуйидагиларни амалга ошириш зарур: жойлаштиришнинг тарихий қарор топган тизимининг ижобий жиҳатларини ва камчиликларини ва у билан боғлиқ асосий элементларни, ҳудуднинг замонавий режалаштирилишини ва хўжалик жиҳатдан ташкил этилишини ҳисобга олиш; меҳнаткашларнинг иш жойларига қатнаши учун энг кам вақт сарфланашини, шунингдек, аҳолига маданий-маиший хизмат кўрсатишни ШНҚ 2.07.04-19 11 ривожлантириш ва улар дам олиши учун қулай шарт-шароитларни таъминлаш; аҳолининг истиқболда турар жой билан таъминланганлиги ўсишини ҳисобга олиш; қишлоқ аҳоли пунктларини режалаштириш, қуриш, муҳандислик ускуналари билан жиҳозлаш ва ободонлаштиришнинг юқори сифатли даражасини сақлаб қолган ҳолда уларни қуриш ва фойдаланиш қийматини камайтириш учун зарур шарт-шароитларни таъминлаш; амалдаги санитария-гигиена, табиатни муҳофаза қилиш ва бошқа меъёрлар ҳамда талабларга риоя қилиш.'),
        (452, 39, null, null, '4.5. Аҳоли пунктларини ривожлантириш истиқболлари қишлоқ хўжалиги туманининг ихтисослашуви, вилоятлар ҳудудларини режалаштириш схемалари ва туманларни режалаштириш лойиҳаларини ҳисобга олиб туриб, агросаноат комплексини шакллантириш билан уйғунлашган тарзда ушбу туманнинг иқтисодий ривожланиши прогнозлари асосида белгиланиши лозим.'),
        (453, 39, null, null, '4.6. Қишлоқ аҳоли пунктларидаги хизмат кўрсатиш корхоналари ва муассасаларининг тармоғи барча қишлоқ ва овуллар, иш жойлари ва дам олиш зоналарини қамраб оладиган ягона тизимдан иборат бўлиши керак.'),
        (454, 39, null, null, '4.7. Қишлоқ аҳоли пунктларининг фазовий-режавий тузилмасини шакллантиришнинг умумий принциплари Ўзбекистон Республикаси ҳудудининг иқлимга доир районлаштиришни (саҳролар, саҳролар қуршовидаги воҳалар, тоғ этакларидаги воҳалар, янгидан ўзлаштирилаётган ерлар ва тоғлар) ҳисобга олган ҳолда ШНҚ 2.01.01 га мувофиқ ҳал қилиниши лозим.'),
        (455, 39, null, null, '4.8. Қишлоқ аҳоли пунктларининг режавий тузилмасини шакллантиришда функционал зоналарнинг компакт жойлаштирилиши ва ўзаро боғлиқ бўлишини, ҳудуднинг жамоат марказлари, муҳандисликтранспорт инфратузилмаси билан уйғун тарзда оқилона районлаштирилишини, ҳудуднинг шаҳарсозлик нуқтаи назарида аҳамиятини ҳисобга олган ҳолда ундан самарали фойдаланишни, архитектура-шаҳарсозлик анъаналари, табиий-иқлим, ландшафт, миллий-маиший ва бошқа маҳаллий ўзига хос хусусиятларнинг комплекс тарзда ҳисобга олинишини таъминлаш лозим бўлади.'),
        (456, 39, null, null, '5.1. ҚФЙ аҳолиси ижтимоий ишлаб чиқаришда иштирок этиши ва меҳнат фаолиятининг турига қараб қуйидаги гуруҳларга бўлинади: шаҳар ташкил қилувчи, хизмат кўрсатувчи ва фаолият кўрсатмайдиган. Шаҳар ташкил қилувчи аҳамиятга эга корхоналар, муассасалар ва ташкилотлар сирасига қуйидагилар киради: қишлоқ хўжалиги ва саноат корхоналари, шу жумладан, барча енгил, озиқ-овқат ва маҳаллий саноат корхоналари, шунингдек, омборлар ва моддийтехника таъминоти базалари; қурилиш ташкилотлари; ташқи транспорт корхоналари ва муассасалари; туман ва вилоят, аҳамиятига эга бўлган хизмат кўрсатиш муассасалари ва корхоналари. Аҳолининг хизмат кўрсатувчи гуруҳига қуйидагилар киради: шу ҚФЙ худдудида яшовчи, аҳолига хизмат кўрсатувчи муассасалар ва корхоналарнинг ишчилари. Ўзи фаолият кўрсатмайдиган кишилар гуруҳларига эса – болалар, ўқувчилар ва нафақахўрлар киради.'),
        (457, 39, null, null, '5.2. Аҳолининг лойиҳадаги сонини ҚФЙ бўйича умуман ҳисоблаб чиқиб, кейинчалик аҳоли пунктлари бўйича тақсимлаш зарур. Янгидан ташкил этилаётган қишлоқ аҳоли пунктларидаги аҳоли сонини ҳисоблаш учун кадрларнинг меҳнат баланси услуби қўлланилиб, кейинчалик, бозор иқтисодиётига (кичик корхоналар, бизнес соҳаси ва ҳоказо) ўтилишини ҳисобга олган ҳолда бир қатор параметрлар (аҳолининг табиий кўпайиши, ёшларнинг ўзгариб бориши) бўйича текширилади. Аввалдан мавжуд аҳоли пунктларидаги аҳоли сонини ҳисоблаш учун аҳолини табиий кўпайишига асосланган статистик услуб қўлланилади. Бундай услуб қишлоқ хўжалигида иш жойлари танқислигига қарамай аҳоли миграцияси назарда тутилмаган ҳоллардагина аниқ натижалар беради. Аҳоли сонини меҳнат балансига асосланган ҳисоб-китоб қилиш услубиёти D-иловада келтирилган.'),
        (1589, 120, '2024-07-03 11:20:45', '2024-07-03 11:20:45', '19.5. Vakolatli organ tomonidan ko‘chmas mulk obyektiga taqiq, xatlov yoki garovga (ipotekaga) qo‘yilganligi'),
        (1590, 120, '2024-07-03 11:20:45', '2024-07-03 11:20:45', '19.6. Loyihalashtirish nazarda tutilgan qurilish-montaj ishlari ruxsatnoma olgunga qadar o‘zboshimchalik bilan boshlanganligi'),
        (460, 39, null, null, '6.2. Уй-жой қурилиши ҳажмлари аҳолининг ҳисобланган сонидан келиб чиқиб, ҳар бир қишлоқ аҳоли пункти бўйича уй-жой фондининг ҳозирги ҳолатини ҳисобга олган ҳолда белгиланади ва биринчи навбатда қуриладиган уй-жой бинолари номенклатураси таклиф қилинади. Турар жой ҳудудлари ва уй-жой фонди ҳозирги ҳолатини баҳолаш қуйидаги жиҳатларга тегишли бўлади: турар жой ҳудудларининг санитария-гигиеник хусусиятлари; турар жойнинг анъанавий типи ва қурилиши принциплари (ўзига хос миллий-маиший хусусиятлари, маданий анъаналарнинг хилма-хиллиги, қишлоқ жойлардаги қурилишнинг моддий-техник базаси, муҳандислик таъминотининг даражаси); томорқа ер участкасининг мавжудлиги.'),
        (461, 39, null, null, '6.3. Турар жой биноси юқори эстетик талабларга, миллий анъаналарга, турли контингентларни уларнинг маиший эҳтиёжларини ҳисобга олиб жойлаштиришнинг демографик талабларига мувофиқ бўлиши лозим. Такомиллашган намунавий лойиҳалар бўйича турар жой биноларининг қурилиши афзалроқ ҳисобланади Томорқа ер участкасида битта оила учун иморат қуриш учун ер майдонининг ўлчами якка тартибда уй-жой қуриш тўғрисидаги Низомга асосан белгиланади.'),
        (462, 39, null, null, '6.4. Ўзбекистоннинг минтақавий шарт-шароитлари билан шартланган ва турар жой фонди шаклланишига таъсир кўрсатадиган барча талаблар ривожланаётган бозор иқтисодиёти ва турар жойга мулкчиликнинг ҳар хил турлари билан комплекс тарзда кўриб чиқилиши лозим. Ўз участкасида якка тартибда меҳнат фаолиятини юритиш имкониятларига эга турар жой биноларининг турлари тавсия қилинади: уста хунарманднинг уйи (сопол, ёғочтахта, металл, ипгазлама, гилам ва шу кабилар); кичик ва олисда жойлашган қишлоқлардаги бошланғич мактаб ўқитувчисининг уйи; тиббий пункт ва дорихонали эга шифокорнинг уйи; оилавий болалар боғчаси бўлган уй; новвойхона, чойхона, дўкон, маиший хизматлар кўрсатиш пунктига эга уй; фермернинг уйи ва ҳоказо.'),
        (463, 39, null, null, '6.5. Турар жой ва жамоат бинолари ўртасидаги масофани инсоляция ва аэрация, асосий турар жой хоналари ва томорқа участкаларининг кўздан панароқ бўлишини ҳисобга олиб, шунингдек, ёнғин хавфсизлиги талаблари (ШНҚ 2.07.01, ШНҚ 2.08.01, ШНК 2.09.01) га мувофиқ тарзда минимал қилиб қабул қилиш лозим.'),
        (464, 39, null, null, '7.1. Ижтимоий ва маданий-маиший хизмат кўрсатиш муассасалари ва корхоналари тармоғини ташкил этилишини ижтимоий хизмат кўрсатишнинг қарор топган тизимларига мувофиқ тарзда, яшаш жойида ижтимоий хизмат кўрсатиш билан таъминланганлик даражасини ҳисобга олиб лойиҳалаштириш зарур.'),
        (465, 39, null, null, '7.2. Ижтимоий ва маданий-маиший хизмат кўрсатиш тизимининг ташкил этилиши қуйидагиларни таъминлаши керак: хизматларни олиш учун маблағ, куч ва вақтни энг кам сарфлаган ҳолда хизмат кўрсатишнинг оммавий ва якка тартибдаги турларининг тўлиқ комплексини; шаҳарсозлик ривожланишининг ҳар бир босқичида хизмат кўрсатиш даражасини изчил тарзда секин-аста оширилиб боришини; жамоат ва хусусий хизмат кўрсатишнинг янги усуллари, шакллари ва турларини жорий этиш ва уларни янада такомиллаштириш. Маданий-маиший хизмат кўрсатиш муассасалари тармоғининг лойиҳалаштирилиши қуйидаги шаҳарсозлик омилларини ҳисобга олиб бажарилиши лозим: ҚФЙ нинг ҳудуди жойлашган туманнинг халқ хўжалигидаги ихтисослашуви; қишлоқ хўжалигидаги ишлаб чиқаришнинг ривожланиши ва ихтисослашувнинг асосий йўналишлари; шаҳар ташкил қилувчи базанинг хусусиятлари; ҚФЙ ҳудудининг режавий тузилмаси ва функционал зоналаштирилиши; ташқи ва ички транспорт алоқалари ва муҳандислик инфратузилма тизими; аҳолининг зичлиги, ишлаб чиқариш, меҳнат ва маданий-маиший алоқалар туси; аҳоли пунктининг жойлаштириш ва манзилгоҳлараро хизмат кўрсатиш тизимидаги миқёси ва аҳамияти, аҳоли пунктини ривожлантириш истиқболлари. ҳар хил тоифадаги тизимларнинг марказларига вақт оралиғида транспортда етиб бориш имкониятлари; хизмат кўрсатиш бўйича кўчма воситалар тармоғининг кенг жорий қилиниши.'),
        (466, 39, null, null, '7.3. Маданий-маиший хизмат кўрсатиш муассасалари ва корхоналари тармоғининг ташкил этилиши қуйи-дагилар бўйича таклифларни ўз ичига олиши лозим: ҚФЙ нинг бутун ҳудудида мактабгача ва мактаб таълими муассасалари тармоғининг ташкил қилиниши; ҚФЙ аҳолисига тиббий хизмат кўрсатиш тизимининг ташкил этилиши; ҚФЙ аҳолисига маданий-маърифий ва савдо-маиший хизмат кўрсатиш тармоғининг ташкил этилиши; ҚФЙ аҳолисига рекреацион хизмат кўрсатилиши; ҚФЙ нинг ҳудудида маъмурий ва жамоат ташкилотларининг жойлаштирилиши.'),
        (467, 40, null, null, '7.4. Қишлоқдаги мулкчилик шаклларининг хилма-хиллиги ишлаб чиқариш фаолияти усулларини ва бунинг оқибатида яшаш шартшароитларини ўзгартирди. Аҳоли пунктларини марказлаштириш ва йириклаштириш тенденциясига қарама-қарши ўлароқ қишлоқлар, фермер хўжаликлари қўрғонлари ва шу кабиларнинг тарқоқ ва сийраклашган тарзда жойлашиши тобора кенг тус олмоқда. Мактабгача ва мактаб таълими муассасалари яшаш ва ишлаш жойларига максимал яқинлаштириб жойлаштирилиши лозимлиги сабабли кам сиғимли, боғча-яслиларининг гуруҳлари ва мактабларнинг синф хоналаридаги болалар сони камайтирилган таълим муассасаларига эҳтиёж пайдо бўлмоқда.'),
        (468, 40, null, null, '7.5. Болалар боғчалари-яслиларидаги ўринлар сонини муайян посёлка аҳолисининг демографик тузилишига қараб белгилаш керак. Мактабгача таълим муассасалари тармоқлари қуйидагиларни ўз ичига олади: умумий типдаги болалар боғчалари-яслилари, соғломлаштириш ва ихтисослашган, идоравий, ширкат шаклидаги ва хусусий муассасалар. Аҳолисининг сони 1000 кишигача бўлган аҳоли пунктларида кам сиғимли болалар боғчалари-яслиларини жойлаштириш кўпроқ мақсадга мувофиқдир. Соғломлаштириш ва ихтисослашган болалар боғчаларияслиларини аҳолисининг сони 3000 дан ортиқ бўлган йирик ва катта қишлоқ аҳоли пунктларида жойлаштириш мақсадга мувофиқ. Мактабгача таълим муассасалари билан таъминланганлик даражасини 4-жадвалга мувофиқ қабул қилиш лозим.'),
        (469, 40, null, null, '7.6. Мактабгача таълим муассасалари учун ер участкаси майдони сиғимга қараб қабул қилиниши керак: 25 ўрингача – ҳар бир болага 50 кв.м. 90 ўрингача – ҳар бир болага 40 кв.м. 90 ўриндан кўп бўлганида – ҳар бир болага 35 кв.м.'),
        (470, 40, null, null, '7.7. Таълим тизимини Ўзбекистон Республикасининг “Таълим тўғрисида”ги қонунига биноан, кўриб чиқилаётган ҚФЙ ёки қишлоқ хўжалик корхонаси ҳудудида аҳолини жойлаштирилиши хусусиятини ҳисобга олган ҳолда ташкил қилиш керак.'),
        (471, 40, null, null, '7.8. Умумтаълим ўқув муассасалари мактаб ёшидаги болаларнинг таълимнинг у ёки бошқа босқичи билан қамраб олинганлигига оид кўрсаткичлар асосида ҳисоблаб чиқилиши лозим. Ўринлар сони ва ер участкасининг ўлчамлари 5-жадвал бўйича қабул қилинади. Умумтаълим ва ихтисослашган мактаблар, гимназиялар, лицейлар, коллежларнинг сиғими ўринларнинг умумий сонига, уларнинг жойлаштирилиши эса лойиҳалаштириш топшириғига асосан қабул қилинади. Ўзбекистон Республикаси вилоятларидаги аҳолининг ёш бўйича тузилмаси D-иловада берилган. Мактабгача таълим ва мактаб таълими муассасалари ҳажмларининг ҳисоб-китоблари бўйича мисол 6-жадвалда келтирилган.'),
        (472, 41, null, null, '7.9. Соғлиқни сақлаш муассасаларини қишлоқ аҳоли пунктларининг аҳоли яшайдиган ҳудудларида, санитария-гигиена шарт-шароитларига кўра энг қулай ер участкаларида, аҳолининг яшаш ва ишлаш жойларига максимал яқинлаштирилишини ҳисобга олган ҳолда жойлаштириш лозим.'),
        (473, 41, null, null, '7.10. ҚФЙ нинг бутун аҳолиси тиббий ёрдам билан тўлиқ таъминланиши керак, туман марказида ёки вилоят марказида ёхуд шаҳар типидаги йирик посёлкаларда жойлаштириладиган касалликларнинг турлари бўйича (юқумли, онкология ва ҳоказо) махсус касалхона ва клиникалар бундан мустасно.'),
        (474, 41, null, null, '7.11. ҚФЙ аҳолисига тиббий хизмат кўрсатилиши қуйидаги принципга кўра ташкил қилиниши лозим: 1) Касалликларни даволаш ва уларнинг профилактикасини ўз ичига оладиган кундалик тиббий хизмат кўрсатиш. Бундай муассасалар пиёда етиб борадиган узоқликда, яъни деярли барча қишлоқ аҳоли пунктларида бўлиши лозим. 2) Енгил ва оғир касалликларнинг диагностикаси ва даволанишини ўз ичига оладиган даврий тиббий хизмат кўрсатиш. Бундай муассасалар аҳолиси 1000 кишидан ортиқ бўлган қишлоқ аҳоли пунктларида ва транспортда 30 дақиқа ичида етиб бориладиган тарзда жойлаштирилади, уларда ҳам катталар, ҳам болалар бўлимлари бўлиши керак. 3) Оғир ва сурункали хасталиклар билан оғриган касалларга эпизодик хизмат кўрсатилиши (юқумли, онкологик, сил ва шу каби касалликларни даволашга мўлжалланган ихтисослашган даволаш муассасалари). Бундай муассасалар туман ва вилоятлар марказларида ҳамда республика пойтахтида жойлаштирилади'),
        (475, 41, null, null, '7.12. Тиббиёт муассасалари учун ажратиладиган ер участкаларининг ўлчамлари ШНҚ 2.07.01 га биноан ёки лойиҳалаштир топшириғига кўра қабул қилинади.'),
        (476, 41, null, null, '7.13. Аҳолиси 500 кишигача бўлган қишлоқларда тиббий пунктнинг шифокорнинг турар жой биносида жойлаштирилишига йўл қўйилади: аҳолиси 1000 дан 2000 кишигача бўлган қишлоқ аҳоли пуктларида кенг профилдаги тиббиёт ходимлари ва дорихонаси мавжуд қишлоқ врачлик пунктлари ташкил этилади, олис қишлоқлар аҳолисига маслаҳатлар берилиши, уларни даволаш учун шу ерда ётқизилиши ҳам назарда тутилиши керак; аҳолисининг умумий сони 3000 кишидан ортиқ бўлган аҳоли пунктлари гуруҳлари учун таркибида поликлиника, туғруқхона, лаборатория ва дорихона бўлган участка касалхоналарини назарда тутиш лозим; болаларнинг саломатлигини мустаҳкамлаш мақсадида ҚФЙ ҳудудида иқлими ва табиий шароитларига кўра қулай зоналарда соғломлаштириш комплексларини, катта ёшдаги аҳоли учун эса дам олиш уйлари ва профилакторийларни назарда тутиш лозим; асосий қишлоқ аҳоли пунктларида далада ишлайдиган аҳоли ўсимликшунослик, боғдорчилик, чорвачилик ва муҳандислик инфратузилмаси ходимларига ҳам профилактик, ҳам тез ёрдам кўрсата оладиган “мобил тиббий пунктлар” ташкил қилиниши назарда тутилиши керак. ҚФЙ ҳудудида ветеринария хизматини, ҳар бир чорвачилик корхонаси қошида лаборатория ва дорихонаси бўлган ветеринария шифохонасини назарда тутиш лозим. Тиббий муассасаларнинг аҳоли сонига боғлиқ равишда тавсия этиладиган номенклатураси 7-жадвалда келтирилган. Тиббиёт муассасаларини лойиҳалаштириш СанҚваМ №0292 га мувофиқ бўлиши лозим.'),
        (1591, 120, '2024-07-03 11:20:45', '2024-07-03 11:20:45', '19.7. Davlat ro‘yxatidan o‘tkazuvchi organda ushbu yer uchastkasi qonunchilik hujjatlarida belgilangan tartibda olib qo‘yilganligi haqidagi ma’lumotlarning mavjudligi'),
        (477, 42, null, null, '7.14. ҚФЙ аҳолисига маданий-маърифий ва савдо-маиший хизматларни кўрсатувчи корхоналар ва муассасалар тармоғининг жойлаштирилиши мавжуд объектларнинг бу каби объектларга эҳтиёжни ҳисобга олиб туриб қилинган таҳлили асосида, ўзига хос миллий хусусиятлар ва қарор топган анъаналарни инобатга олган ҳолда ҳал қилиниши лозим. Қишлоқ аҳоли пунктларида яшайдиган аҳоли сонидан қатъи назар аҳолини бундай хизматлар билан тўлиқ қамраб олиниши, шунингдек ШНҚ 2.07.02 талабларига мувофиқ ногиронлар учун шарт-шароитлар яратилиши керак. Хизмат кўрсатиш корхоналари пиёда ва транспортда етиб бориш мумкин бўлган радиус чегарасида 8-жадвалга биноан жойлаштирилиши керак. Маданий-маиший хизмат кўрсатиш тармоғини ташкил этиш учун стационар бинолардан ташқари кўчма воситаларни ва мавсумий фойдаланиладиган иншоотларни ҳам назарда тутиш ва улар учун тегишли майдонларни ажратиш зарур.'),
        (478, 42, null, null, '7.15. Хизмат кўрсатиш тармоғини лойиҳалаштириш қуйидаги тизимга кўра бажарилиши лозим: 1) кундалик хизмат кўрсатиш (маҳалла маркази, дўкон, чойхона, сартарошхона, кийим-кечак ва пойафзалларни таъмирлаш, дорихона, дам олиш ва жисмоний тарбия-соғломлаштириш масканлари ва ҳоказолар). Бундай муассасалар пиёда етиб олиш доирасида бўлиши ва, фақат сийрак жойлаштириш тизими мавжуд бўлган ҳоллардагина, транспортда 30 дақиқа ичида етиб олиш мумкин бўлган жойда жойлаштирилишига йўл қўйилади. Хизмат кўрсатишнинг I-босқичли объектлари 9-жадвалда келтирилган. 2) даврий, вақти-вақти билан хизмат кўрсатиш. Бундай объектлар (маданият уйлари, дам олиш марказлари, универмаг, бозорлар, ресторанлар, маиший хизмат кўрсатиш комбинатлари, кимёвий тозалаш пунктлари, кир ювиш пунктлари, ҳаммомлар ва бошқалар) катта ва йирик қишлоқ аҳоли пунктларида жойлаштирилади. 3) чекланган, танлаб олинадиган хизматларни кўрсатувчи объектлар туманлар, вилоятлар марказларида ва Республика пойтахтида  жойлаштирилади. Улар сирасига қуйидагилар киради: театрлар, кино-концерт заллари, цирклар, музейлар, лекторийлар, кўргазма заллари, универсал спорт мажмуалари, йирик савдо комплекслари, олий (люкс) тоифадаги маиший хизмат кўрсатиш корхоналари ва шу кабилар.'),
        (479, 42, null, null, '7.16. Савдо объектларини ҚФЙ ёки қишлоқ хўжалик корхонасининг барча қишлоқ аҳоли пунктларидаги бўлган эҳтиёжни ҳисобга олган ҳолда товарларни тақсимланишига қараб жойлаштириш лозим.'),
        (480, 42, null, null, '7.17. Умумий овқатланиш объектларини аҳолининг реал эҳтиёж-ларини ҳисоб олган ҳолда икки асосий гуруҳ бўйича лойиҳалаштириш керак: оммавий хизмат кўрсатиш корхоналари (аҳолининг кундалик эҳтиёжларини қондириш учун мўл-жалланади ва ҳар бир аҳоли пунктида 5-10 дақиқада пиёда етиб олиш мумкин бўлган жойда жойлаштирилади). Улар мулкчилик шакллари бўйича давлатга қарашли, ширкат ва хусусий корхоналар бўлиши мумкин; танлаб хизмат кўрсатиладиган корхоналар (нафақат овқатланиш, балки дам олиш ва кўнгил очиш учун мўлжалланади - ресторанлар, барлар, кафе ва бошқалар).'),
        (481, 42, null, null, '7.18. Коммунал ҳамда маиший хизмат кўрсатиш корхоналари ва муассасалари ҳар бир аҳоли пунктидаги аҳолини энг зарур хизматлар билан таъминлашни ҳисобга олиб 30 дақиқада пиёда етиб олиш мумкин бўлган жойда жойлаштирилади. Аҳоли сони кам бўлган (1000 кишигача) қишлоқларда маиший хизматларни уйлар қошида (этикдўз, тикувчи, маиший асбобларни таъмирлаш бўйича уста ва шу кабиларнинг уйларида) ёки маҳалла марказида (маиший хизматлар блоки) ташкил этилиши тавсия қилинади. Маиший хизматлар блокини бошқа муассасалар (савдо объектлари, меҳмонхона, ҳаммом ва шу кабилар) билан бирлаштириш ҳам мумкин. Хизмат кўрсатишнинг юқорироқ даражасидаги объектлар билан таъминлаш аҳоли пунктларининг гуруҳлари учун назарда тутилиши лозим. Ўт ўчирувчилар депосини ШНҚ 2.07.01 нинг 30-жадвалига мувофиқ ҳисоблаган ҳолда ташкил қилиш лозим. Ўт ўчирувчилари депосини хизмат кўрсатиш радиуси – 3 км, агар радиус каттароқ бўлса, у ҳолда 1 та автомашинага мўлжалланган ўт ўчириш постини ташкил қилиш кўзда тутилиши лозим.'),
        (482, 43, null, null, '7.19. Рекреацион хизмат кўрсатиш тизимининг ривожлантирилишини аҳоли пунктларини режалаштириш тузилмаси, дам олиш ҳудудини функционал зоналаштириш ва меъморий-режавий ташкиллаштириш учун асос бўлиб хизмат қилувчи мавжуд табиий ресурслардан оқилона фойдаланишга асосланган ҳолда назарда тутиш лозим.'),
        (483, 43, null, null, '7.20. Рекреацион хизмат кўрсатиш тизимига кирувчи объектлар, вазифалари ва мақсадларидан келиб чиққан ҳолда, қуйидаги объектларга бўлиниши керак: кундалик хизмат кўрсатиш объектлари; болалар учун ўйин майдончалари, катта ёшдаги аҳоли дам олиши учун майдончалар, маҳалла марказлари участкалари, посёлка парклари, хиёбонлари, шунингдек, дарёлар, табиий ва сунъий сув ҳавзалари ёнидаги пляжлар. Бундай объектлар аҳоли пиёда (30 дақиқагача) етиб олиши мумкин бўлган радиусда жойлаштирилади. Рекреацион участкаларнинг ўлчамлари ШНҚ 2.07.01 нинг 15-жадвалига асосан белгиланади; ҳафта охирида даврий равиш-даги қисқа муддатли (1-2 кун) дам олиш. Бундай дам олиш турини ташкил қилиш учун табиий ёки сунъий барпо этиладиган ва табиий компонентларга (сув, яшил ўсимликлар, рельеф, юксак эстетик хусусиятларга) бой бўлган, аҳоли пунктидан транспортда 1-1,5 соат ичида етиб олиш мумкин бўлган махсус ҳудудлар ажратилиши лозим; қисқа муддатли дам олиш тизимини ишлаб чиқишда дам олиш муассасаларида аҳолининг камида 42% ўринлар билан таъминланиши ҳисобга олиниши зарур; катта ёшдаги аҳолининг ҳар йилги меҳнат таътили ва мактаб ёшидаги болаларнинг ёзги таътиллари вақтида эпизодик, узоқ муддатли дам олиши учун мўлжалланган дам олиш масканлари – санаториялар, дам олиш уйлари, пансионатлар, туристик базалар, мотеллар, болаларнинг соғломлаштириш муассасалари, болалар муассасаларининг ёзги боғ ҳовлилари. ҚФЙ ҳудудида тавсифномаси туманларни режалаштириш лойиҳасида кўрсатиладиган туман, вилоят ёки республика аҳамиятига эга қимматли рекреацион ресурслар мавжуд бўлса, улардан мазкур ҚФЙ ҳудудида яшайдиган аҳолининг дам олиши учун фойдаланиш имкониятини белгилаш зарур.'),
        (484, 43, null, null, '7.21. Спорт бинолари ва иншоотлари тармоғи жисмоний тарбия талабларини қондириш учун зарур бўлган ҳажмда, ҚФЙнинг бутун аҳолисига қуйидагича принципга кўра хизмат кўрсатилишини ҳисобга олиб туриб лойиҳалаштирилиши лозим: ҳар куни спорт билан шуғулланиш учун мактаб ўқувчилари ва катта ёшдаги аҳоли спорт ўйинлари ва машғулотлари билан шуғулланиши учун мўлжалланган майдончалар, улар 5-10 дақиқа ичида пиёда етиб олиш радиусида (турар жой гуруҳлари ичида, мактаб қошида, маҳалла маркази ёнидаги рекрацион мажмуада) жойлаштирилиши керак. катта ёшдаги аҳоли ва болаларнинг мустақил ва мураббий раҳбарлигида умумий жисмоний тайёргарлик гуруҳлари (саломатлик гуруҳлари)да ва спорт секцияларида мунтазам равишда шуғулланиш учун. тизимли тарздаги машғулотлар учун мўлжалланган спорт маркази 30 дақиқа ичида пиёда етиб бориш радиусида жойлаштирилиши лозим. Спорт иншоотларини лойиҳалаштиришда улардан кўп мақсадларда фойдаланиш принципига асосланиш зарур.'),
        (485, 44, null, null, '7.22. Маъмурий бинолар, молия, алоқа муассасалари, жамоатчилик ташкилотлари, жамоат тартиби бўлинмалари, қишлоқ кенгашлари (диний муассасалар бундан мустасно) қишлоқ аҳоли пунктининг ижтимоий маркази ҳудудида жойлаштирилиши ва маъмурий-ижтимоий хизматлар тўлиқ ҳажмда кўрсатилишини таъминлаши керак. Бинолар имкони борича бирлаштирилиши керак. Жойлаштиришнинг ҳажми ва манзили ҳар бир муайян ҳолат учун лойиҳалаштириш топшириғига биноан белгиланади. Бинолар ва муассасаларнинг тавсия қилинадиган рўйхати, уларнинг сиғими ва ҚФЙнинг аҳоли пунктлари бўйича жойлаштирилиши 10 – 13-жадвалларда келтирилган'),
        (486, 44, null, null, '8.1. қишлоқ хўжалик корхонаси ичидаги транспорт алоқаларини жойлаштиришнинг умумий тизимида энг кам вақт ва иқтисодий харажатлар сарфлаб юкларни ва йўловчиларни ташишни таъминлайдиган ягона тизим сифатида лойиҳалаштириш керак бўлади.'),
        (487, 44, null, null, '8.2. Йўл тармоғининг лойиҳалаштирилиши йўлтранспорт тармоғининг ҳозирги аҳволи ва хўжалик ичида транспорт алоқаларининг ташкил этилишини ҳисобга олиб амалга оширилиши лозим.'),
        (488, 44, null, null, '8.3. Хўжалик ичидаги йўллар уларнинг функционал мақсадига қараб аниқ табақалаштирилган бўлиши керак. қишлоқ хўжалик техникасини ўтказишга мўлжалланган йўллар селитеб ҳудудлар чеккасидан лойиҳалаштирилиши лозим.'),
        (489, 44, null, null, '8.4. ҲМРТ лойиҳасида йўллар қурилишининг навбатлилигини уни хўжалик ҳудудидаги бошқа реконструкциялаш тадбирлари навбатлилиги билан уйғунлаштирган тарзда назарда тутиш лозим. Транспорт тармоғини қуриш ва реконструкциялаш юзасидан мўлжалланган ишларнинг навбатлилиги хўжаликда мўлжалланаётган ишлаб чиқаришни ривожлантиришнинг навбатлилиги ва жойлаштириш тизимини яхшилашга доир тадбирлар билан уйғунлаштирилиши керак.'),
        (490, 44, null, null, '8.5. Қишлоқ хўжалик корхонаси ҳудудидан четга чиқиб кетадиган трассалар аралаш хўжаликлар транспорт тизимлари билан уйунлаштирилиши лозим. Йўл тармоғи амалдаги меъерий ҳужжатларга мувофиқ лойиҳалаштирилиши керак. Автомобиль йўлларини лойиҳалаштириш бўйича асосий техник меъёрлар 14-жадвалда келтирилган. Қишлоқ поселкаларидаги кўчалар ва йўлларнинг ҳисоб-китоб параметрлари 15-жадвалда келтирилган.'),
        (491, 44, null, null, '8.6. Яшаш жойидан меҳнат қиладиган жойгача етиб олиш учун сарфланадиган вақт 30 минутдан ошмаслиги керак.'),
        (492, 44, null, null, '8.7. I, II ва III классдаги умумий тармоқ автомобиль йўллари, одатда, посёлкаларни четлаб ўтадиган қилиб лойиҳалаштирилади. Йўлнинг тупроқли чети билан уй-жой иморати ўртасидаги масофа 100 м лик (I ва II классдаги йўллар) ва 50 м лик (III классдаги йўллар) қилиб қабул қилиниши лозим.'),
        (493, 44, null, null, '8.8. Кўчалар ва йўллар тоифаларини ҳаракатнинг интенсивлиги, тезлиги, шунингдек, функционал мақсадига қараб қабул қилиш керак. Тупроқли қатлам ва йўлга тўшамаларни ШНК 2.05.02 га биноан лойиҳалаштириш зарур'),
        (494, 44, null, null, '9.1. Аҳоли сони, иқлим, геологик ва бошқа шарт-шароитлардан қатъи назар ҳамма посёлкаларда зарур санитария-гигиена шарт-шароитларини, аҳолининг меҳнат қилиши ва дам олиши учун юксак даражадаги қулайликларни таъминлайдиган муҳандислик ускуналари ва ободонлаштиришни назарда тутиш лозим.'),
        (1592, 120, '2024-07-03 11:20:45', '2024-07-03 11:20:45', '19.8. Ko‘chmas mulkka bo‘lgan huquqlar davlat ro‘yxatidan o‘tkazilmaganligi'),
        (1593, 121, '2024-07-03 11:24:18', '2024-07-03 11:24:18', '21.1. So‘rovnomada qayd etilgan ma’lumotlarning haqqoniy emasligi'),
        (496, 44, null, null, '9.3. Сув таъминоти, иссиқлик билан таъминлаш, алоқа ва газ таъминоти манбалари ва бошқаларнинг танланиши белгиланган муддатларда ҳисобкитобдаги миқдордаги сув, иссиқлик энергияси, газ ва шу кабиларнинг олиниши мумкинлигини тасдиқлайдиган ташкилотлар билан келишилиши лозим. Етиб бориш қийин бўлган туманларда (тоғларда, чўлларда жойлашган, яйловлардаги чорвачилик ва шу кабилар) жойлашган кичик аҳоли пунктларида (100-500 киши) муҳандислик таъминотининг локал тизимларидан фойдаланиш мумкин. Бундай аҳоли пунктларида фавқулодда ҳоллардагина олиб келинадиган сув билан таъминлашга, иссиқ сув билан таъминлаш учун эса қуёш энергиясидан фойдаланишга йўл қўйилади'),
        (497, 44, null, null, '9.4. Муҳандислик таъминоти тизимларининг ҳисоб-китоби, шу жумладан, манбаларнинг танланиши тегишли тизимларни лойиҳалаштириш бўйича ШНКнинг боблари талабларига мувофиқ амалга оширилиши керак.'),
        (498, 45, null, null, '9.5. Сув таъминоти тизимларининг ҳисоб-китоби, сув олинадиган иншоотларнинг жойлаштирилиши, шунингдек, ҳисоб-китобдаги сув сарфхаражатлари, шу жумладан, ёнғинга қарши кураш эҳтиёжлари учун сув харажатининг белгиланиши сув таъминотининг ташқаридаги тармоқлари ва иншоотларини лойиҳалаштириш бўйича ШНКнинг боби талабларига мувофиқ амалга оширилиши керак. ШНК 2.04.02.'),
        (499, 45, null, null, '9.6. ҲМРТ лойиҳасида сув таъминоти ва канализациянинг истеъмолчиларнинг сифатли ичимлик суви билан таъминланиши ва манбаларнинг оқова сувлар билан ифлосланишининг олдини олиш зарурлигини ҳисобга оладиган масалаларининг биргаликда ишлаб чиқилиши мажбурий шарт ҳисобланади.'),
        (500, 45, null, null, '9.7. Йирик аҳоли пунктлари ва ишлаб чиқариш объектлари сув таъминоти ва канализациянинг марказлаштирилган тизимларига эга бўлиши лозим. Кичик аҳоли пунктлари, шунингдек, алоҳида жойлашган чорвачилик фермалари, ёзги лагерлар, қўйчилик фермалари, қорамоллар сақланадиган жойлар ва шу кабилар ишлаб чиқариш эҳтиёжлари, ёнғинларни ўчириш ва бошқа мақсадлар учун очиқ сув манбаларидан фойдаланган ҳолдаги ичимлик ва ишлаб чиқариш сув таъминотининг алоҳида тизимларига эга бўлиши мумкин.'),
        (501, 45, null, null, '9.8. Сувни тозалаш станциялари, канализациянинг тозаловчи иншоотлари учун ер участкаларининг майдонлари ШНК 2.04.02 га мувофиқ қабул қилиниши керак, канализациянинг тозаловчи иншоотлари учун эса ШНК 2.04.03 га мувофиқ қабул қилиниши керак.'),
        (502, 45, null, null, '9.9. Лойиҳада қишлоқ хўжалик корхонаси доирасидаги ер ости ва ер устидаги сув ресурсларини, уларга боғланиб турган ҳудудларни аниқлаш ва посёлкалар, ишлаб чиқариш ва чорвачилик объектларининг сув билан таъминланиши, шунингдек, ерларни суғориш, ерларга сув чиқариш ва ерларнинг захини қочириш учун сув ресурсларидан оқилона фойдаланишга доир техник тадбирларни белгилаш мақсадида сув ресурсларидан комплекс фойдаланиш масалаларини сувдан фойдаланувчилар томонидан сув ресурсларига қўйиладиган талабларни ҳисобга олиб ҳал қилиш зарур. Сув хўжалигини ташкил этиш юзасидан қабул қилинган қарорлар ер устидаги ва ер остидаги сувларнинг ифлосланиш ҳамда захираларнинг қуриб қолишидан ҳимоя қилишни назарда тутиши лозим'),
        (503, 46, null, null, '9.10. Қишлоқ хўжалик корхонасини электр энергияси билан таъминлаш лойиҳаси қуйидаги масалаларни ҳал этиши зарур: қишлоқ хўжалик корхонаси доирасидаги барча электр энергиясини истеъмол қилувчиларни электр энергияси билан таъминлаш манбаларининг эҳтиёжга доир қувватларини белгилаш; таянч таъминловчи подстанциялар жойлаштириладиган жойларни белгилаш, бундай подстанцияларни электр энергияси энг кўп тўпланадиган марказларда электр узатиш линияларини олиб келиш қулайлигини ҳисобга олиб жойлаштириш лозим бўлади'),
        (504, 46, null, null, '9.11. Юқори кучланишли электр тармоқлари ҳаводан тортиб олиб келиниши назарда тутилиши керак. қўрғонлар, фермалар ва бошқа ишлаб чиқариш марказлари ҳудудидаги паст кучланишли электр энергиясини тақсимловчи тармоқлар ҳам ҳаводан тортиб олиб келиниши назарда тутилиши лозим, айрим ҳоллардагина (мактаблар, болалар боғчалари) кабелли тармоқлар назарда тутилиши мумкин.'),
        (551, 60, null, null, '4.20. Қурилиш амалиётига қурилиш конструкциясини тадбиқ этиш чоғида, уларни ёнғинбардошлик даражаси аниқланмаган бўлса ёки ҳисоблаш йўли билан ёки стандарт оловли синов асосида аниқ ёнғин хавфлилик синфига киритиш имкони бўлмаса, бинонинг натуравий фрагментларини оловли синовдан ўтказиш лозим.'),
        (553, 61, null, null, '4.21. Бино ва бино қисми – хонасининг қисми ёки хона гуруҳлари функционал ёнғин хавфи буйича бир бирига боғлиқ ҳолда ва уларни кўллаш усулига кўра ҳамда улардаги одамларни хавфсизлигига таҳдид солувчи холатлар, яъни уларни ёшини, жисмоний ҳолатини, уйқу вақтидалигини асосий функционал контингент тури ва миқдорини ҳисобга олган ҳолда синфларга бўлинади: Ф 1. Одамларни доимий яшаши ва вақтинчалик (шу жумладан кечаю-кундуз) бўлиши учун (бу бино- ШНҚ 2.01.02-04 12-бет лар хоналаридан, одатда кечаю-кундуз фойдаланилади, булардаги одамлар контингенти турли ёшда ва жисмоний холатда бўлишлари мумкин, бу биноларга ётоқхоналари бўлиши хос). Ф 1.1. Болалар мактабгача муассасалари, қариялар ва ногиронлар уйлари, касалхоналар, мактаб-интернатлар ёток бинолари ва болалар муассасалари. Ф 1.2. Меҳмонхоналар, ётокхоналар, санаторий ва умумий турдаги дам олиш уйлари, кемпинглар, отеллар ва пансионатлар ёток корпуслари. Ф 1.3. Кўп хонадонли яшаш жойлар. Ф 1.4. Бир хонадонли, шу жумладан бириктирилган яшаш жойлар. Ф 2. Томоша ва маданий-маърифий муассалари (бу биноларда асосий хоналар, маълум вақт давомида ташриф қилувчиларининг оммавий келиб кетиши билан ўзига хос). Ф 2.1. Театрлар, кинотеатрлар, концерт заллар, клублар, истирохат боғлари, спорт иғгшоатлари минбарлари билан, кутубхоналар ва бошқа муассасалар, ёпиқ биноларда ташриф буюрувчилар учун хисобли ўриндиқлар сони билан . Ф 2.2. Музейлар, кўргазмалар, рақс заллари ва бошқа шунга ўхшаш ёпиқ хоналардаги муассасалар. Ф 2.3. Ф 2.1. да кўрсатилган муассасалар очиқ ҳавода. Ф 2.4. Ф 2.2. да кўрсатилган муассасалар очиқ ҳавода. Ф 3. Аҳолига хизмат кўрсатиш корхоналари (бу корхоналарнинг хоналарида ишловчиларга караганда келувчилар сони кўплиги билан ўзига хос). Ф 3.1. Савдо корхоналари. Ф 3.2. Умумий овқатланиш корхоналари. Ф 3.3. Вокзаллар, аэропортлар, Ф 3.4. Шифохона ва амбулаторияяар. Ф 3.5. Маиший ва коммунал хизмат кўрсатиш корхоналари бинолари (почта, омонат кассалари, транспорт агентликлари, хукуқий маслахатхоналари, нотариал идоралар, кир ювиш, тикув ательелари, пойафзал ва кийим таъмирлаш хоналари, кимёвий тозалаш, сартарошхона ва бошқа шунга ўхшаш, шу жумладан ретуал ва диний муассасалар)га ташриф этувчилар учун ўриндиқлар сони ҳисобланмаган ҳолда. Ф 3.6. Томошабинлар учун минбарлар, маиший хоналар ва хаммоми мавжуд бўлмаган жисмоний тарбия ва соғломлаштириш муассасалари ҳамда спорт машғулотларини ўтказиш муассасалари. Ф 4. Ўқув юртлари, илмий ва лойиҳалаш ташкилотлари, бошқарув муассасалари (бу бинолар хоналари бир кеча-кундузнинг маълум вақт давомида фойдаланилади ва уларда, одатда, доимий равишда махаллий шароитларга кўниккан белгиланган ёшдаги ва жисмоний холатдаги контингент одамлар бўлади). Ф 4.1. Мактаблар, мактабгача ёшдаги муассасалар, махсус ўрта ўқув юртлар, коллежлар, лицейлар. Ф 4.2. Олий ўқув юртлари, малака ошириш муассасалари, мактабгача ёшдаги муассасалари. Ф 4.3. Бошқарув органлари муассасалари, лойиҳа-конструкторлик ташкилотлари. ахборот ва тахририятнашр ташкилотлари, илмий-қидирув ташкилотлари, банклар, идоралар, офислар, Ф 4.4. Ёнғин ўчириш депоси. ШНҚ 2.01.02-04 13-бет Ф 5. Ишлаб чиқариш ва омборхона бинолари, иншоатлари ва хоналари (бу турдаги хоналар учун ишловчилар контингенти доимий равишда мавжудлиги билан тавсифланади, шу жумладан кеча-кундуз давомида). Ф 5.1. Ишлаб чиқариш бино ва корхоналари, ишлаб чиқариш ва лаборатория хоналари, устахоналар. Ф 5.2. Омборхона бино ва иншоатлари, техник хизмат кўрсатиш ва таъмирлаш ишлари олиб борилмайдиган автомобилларнинг турар жойлари, архивлар, омборхона бинолари. Ф 5.3. Қишлоқ хўжалик бинолари. Портлаб-ёниш ва ёнғин хавфига эга ишлаб чиқариш ва омборхона бинолари, хоналари бунда улардаги мавжуд портлаш ва ёнғин хусусиятига эга моддалар ва ашёларнинг миқдоридан келиб чиқиб ҳамда уларда жойлашган ишлаб чиқариш хавфлиги уларда мавжуд портлашга хавфли хусусиятли моддаларнинг ва ашёларнинг технологик жараёнларни ҳисобга олган ҳолда НПБ 105-95 га асосан тоифаларга бўлинади. Ф1, Ф2, ФЗ, ва Ф4 таснифи биноларда жойлашган ишлаб чиқариш ва омбор хоналари, шу жумладан, лаборатория хоналари ва устахоналар Ф5 таснифига тааллуклидир.'),
        (505, 46, null, null, '9.12. Электр таъминоти манбаларининг эҳтиёжга доир қувватларини белгилаш хўжаликдаги барча электр энергияси истеъмолчиларининг жойлашган жойларини ҳисобга олиб уларга тўғри келадиган электр энергияси қувватларини ҳисоблаш натижалари асосида амалга оширилиши зарур. Айрим истеъмолчиларнинг электр энергияси қувватларининг йириклаштирилган меъёрлари 16 ва 17-жадвалларда келтирилган.'),
        (506, 47, null, null, '9.13. Бориш қийин бўлган туманларда ва яйловларда жойлашган аҳолиси 100-500 кишидан иборат бўлган посёлкалар учун давлатнинг электр энергияси билан таъминлаш тизимлари бўлмаган ҳолларда дизель электр станциялари ва шамол энергиясидан фойдаланиш йўли билан локал электр таъминоти манбаларини қўллашга йўл қўйилади.'),
        (507, 47, null, null, '9.14. қишлоқ посёлкаларининг селитеб ҳудудларини ва умумий тармоқдаги автомобиль йўлларининг ташқаридан ёритилишини лойиҳалаш ШНҚ 2.01.05, ШНҚ 2.04.18 ва ШНҚ 2.04.17 га мувофиқ амалга оширилиши керак. Қишлоқ хўжалик объектлари учун электр тармоқларининг лойиҳалаш Электр ускуналарининг тузилиши қоидаларига (“Ўздавэнергоназорат” агентлигининг 2004 йил 13 февралдаги 84-сонли буйруғи билан тасдиқланган) мувофиқ бажарилиши лозим. Саноат зонасининг ташқаридаги ёритиш қурилмаларини лойиҳалашда “қишлоқ хўжалик корхоналари, бинолари ва иншоотларини ёритишнинг тармоққа доир” меъёрлардан фойдаланиш керак бўлади.'),
        (508, 48, null, null, '9.15. Корхоналар, бинолар, иншоотлар ва аҳоли пунктларининг иссиқлик таъминоти тизимларини лойиҳалаштиришда марказлаштирилган ва марказлаштирилмаган иссиқлик таъминоти тизимларини қўлланиш соҳасини, иссиқлик манбалари турлари, тизимлар схемалари ва типлари, иссиқлик тармоқлари конструкциясини танлаш билан боғлиқ масалалар комплекси ишлар ҳажмлари ва қурилишнинг навбатлилигини белгилаган ҳолда, шунингдек, улардан фойдаланишга доир таклифлар тақдим қилиниб ҳал этилиши лозим. Асосий диққат-эътибор атроф муҳитни ҳимоя қилиш, капитал қўйилмалар, ёнилқи-энергетика, моддий ресурслар ва меҳнат харажатларини тежашга қаратилган қарорларга қаратилиши керак. Иссиқлик таъминоти тизимларини минтақа (вилоят, туман) нинг ёнилқи-энергетика баланси тузилиши ва электр ҳамда газ таъминоти тизимларини ривожлантиришни ҳисобга олган ҳолда лойиҳалаштириш зарур. қўрғон типидаги тураржойлар, одатда, иссиқлик таъминоти якка тартибдаги манбаларидан лойиҳалаштирилади.'),
        (509, 48, null, null, '9.16. қишлоқ хўжалик корхонаси объектларининг иссиқлик таъминоти масалаларини ҳал этиш учун ёқилғининг энг тежамли турларини ва барча истеъмолчиларнинг унга бўлган эҳтиёжини, бундай эҳтиёжни қўшни хўжаликлар ва туманнинг ёқилғи-энергетика баланслари билан уйғунлаштирилган тарзда маҳаллий ёқилғи ресурслари ҳисобига қоплаш имконияти белгиланиши зарур. Газ, термал сувлар тарзидаги табиий иссиқлик манбаларига эга туманларда улардан истеъмолчиларнинг иссиқлик таъминоти учун фойдаланишни назарда тутиш лозим. қуйидагилар тегишли равишда техник-иқтисодий асослангани ҳолда ёнилғи сифатида назарда тутилиши мумкин: тошкўмир, йирик ва ўртача класслардаги навли кўмир, суюлтирилган газ, дизель ёқилғиси, керосин, қуёш энергияси, электр энергияси ва ёқилғининг бошқа турлари. Маиший эҳтиёжлар учун мазутнинг қўлланилиши техник сабабларга кўра инкор этилади, ундан фақат қозонхонадан марказлаштирилган тартибдаги иссиқлик таъминотида фойдаланиш мумкин.'),
        (510, 48, null, null, '9.17. Қишлоқ хўжалик корхонаси объектлари учун иситиш, вентиляция ва ҳавони тозалаш, шунингдек, марказлаштирилган тартибдаги иссиқ сув таъминотига иссиқлик сарфи ШНКнинг иссиқлик тармоқларини лойиҳалаштиришга доир бобига биноан қабул қилиниши лозим. 18, 19-жадвалларга мувофиқ иссиқлик сарфининг йириклаштирилган кўрсаткичларини қабул қилишга ҳам йўл қўйилади.'),
        (517, 51, null, null, '10.3. Вертикал режалаштириш ер устидаги сувларни қочириш бўйича сув оқизиладиган иншоотларни, суғориш тизими каналлари ва иншоотларини, ободонлаштиришнинг муҳандислик тармоқларини, транспорт тармоқлари ва иншоотларни, тураржой, маданий-маиший ва ишлаб чиқариш объектларини жойлаштириш ва фойдаланиш учун энг қулай шарт-шароитларни таъминлаши лозим. Аҳоли пунктларининг вертикал режалаштирилишини аҳоли пунктларига қўшилиб кетган далаларнинг вертикал режалаштирилиши билан уйғун тарзда ҳал этиш зарур, ШНҚ 2.07.01 га мувофиқ.
'),
        (511, 49, null, null, '9.18. қишлоқ хўжалик корхонаси объектларининг газ таъминоти табиий ва суюлтирилган газ билан амалга оширилиши мумкин. Аҳоли пунктларининг газ таъминоти уч усулда: бир босқичли, икки босқичли ва уч босқичли усулда амалга оширилади. Одатда, асосан икки босқичли тизимдан – ўртача (ёки юқори) ва паст босимли газ ўтказгичли тизимлардан фойдаланилади. Уч босқичли тизим – (юқори – ўртача – паст босимли газ ўтказгичлар) алоҳида ҳолларда қўлланилиши мумкин. Суюлтирилган газ билан газлаштириш бориш қийин бўлган ва олис аҳоли пунктлари учун туманлардаги газ тўлдириш станцияларидан посёлкаларгача махсус автомашиналарда баллонларнинг (одатда – Vқ27л) етказиб берилиши йўли билан тавсия қилинади. Суюлтирилган газ меъёри – 1 ойлик муддатга 1 хўжалик учун 3 баллон миқдорида ҳисобланади. Газ таъминоти масалаларини ҳал этиш учун у ёки бошқа газ турининг қўлланилиши ҳар бир аниқ ҳолда техник-иқтисодий мақсадга мувофиқликни ҳисобга олиб ҳал этилади'),
        (512, 49, null, null, '9.19. Хўжалик-маиший ва коммунал эҳтиёжлар, саноат ва қишлоқ хўжалик корхоналари учун газ сарфи ШНҚ 2.04.08 нинг газ таъминотини лойиҳалаштиришга доир бобига мувофиқ қабул қилиниши керак.'),
        (513, 50, null, null, '9.20. ҲМРТ лойиҳасида алоқа воситаларини (телефонлаштириш ва радиолаштиришни) таъминлаш ва ривожлантириш бўйича қуйидагиларни амалга ошириш керак бўлади: кўриб чиқилаётган хўжаликнинг биринчи навбатдаги ва ҳисобкитобдаги муддатга алоқа воситаларига эҳтиёжини ва телефонлаштириш ҳамда радиолаштириш тизимини ташкил этиш тамойилларини белгилаш; ШНҚ 2.07.04-19 39 қишлоқ хўжалик корхоналари ва аҳоли пунктларини алоқа воситалари билан таъминлаш юзасидан бутун ҳудудда асосий иншоотлар ва линияларнинг оқилона жойлаштирилишини асослаш; телефонлаштириш ва радиолаштириш тармоғларини қуришда алоқа линияларининг илгари қарор топган йўналишларидан максимал фойдаланишга интилиш лозим; хўжаликни телефонлаштириш масаласини ҳал этишда алоқа тизимларини барпо қилишнинг ҳудудий-ишлаб чиқариш тамойилига риоя қилиш мақсадга мувофиқ бўлиб, бунда асосий станциялар бириктириладиган линиялар орқали қишлоқ хўжалик корхонасининг марказий посёлкаларида жойлашган охирги ёки узелли АТСлари билан боғланади'),
        (514, 50, null, null, '9.21. Ҳудуднинг радиолаштирилишини ҳал этишда ҳар бир белгиланган зона марказида қуввати ҳамма аҳоли пунктларини ва зонадаги ишлаб чиқариш объектларини радиолаштириш учун етарли бўлган радиоузел жойлашадиган тармоғлар қурилиши тамойилига риоя қилиш керак бўлади. Ҳамма объектлар зона маркази билан радиотрансляция фидер линиялари орқали боғланиши керак. Радиолаштиришнинг зонали тизими мабодо хўжаликдаги барча объектларни бир радиоузелдан сифатли эшиттириш билан таъминлашнинг техник имконияти бўлмагани ҳолда қўлланилиши мумкин. қишлоқдаги телефонлаштириш ва радиолаштириш тармоқларини барпо этишнинг тавсия қилинадиган тузилмалари 20, 21-жадвалларда келтирилган.'),
        (515, 50, null, null, '10.1. «Қишлоқ хужалик корхонаси худудини муҳандислик тайёргарлиги» қисми ҲМРТ лойиҳаси таркибида бажарилади, бунда албатта лойиҳалаштириш топшириғи ҳамда топогеодезик, физико-геологик, гидрогеологик изланишлар ва лойиҳалашти-рилиётган худудни тупроқмелиоратив карталари бўлиши шарт. Қишлоқ хўжалик корхонаси ҳудудининг муҳандислик тайёргарлигига доир тадбирларни ишлаб чиқишда кўриб чиқилаётган корхона ва унинг ташқарисидаги доирадаги халқ хўжалиги барча тармоқларининг талабларини ҳисобга олиш керак бўлади. Тадбирлар таркиби иқлимга оид тавсифнома, физик-геологик ва гидрогеологик изланишлар, радиоэкологик текширувни ҳисобга олиб белгиланади.'),
        (516, 50, null, null, '10.2. Ҳудуднинг муҳандислик тайёргарлигига доир тадбирларга қуйидагилар киради: ҳудуднинг вертикал режалаштирилиши; сунъий суғориш; сувни қочириш, табиий ёки сунъий гидрографик тармоқнинг тартибга солиниши; сейсмикликка қарши ва ер кўчишига қарши тадбирлар; сизот сувлар даражасининг пасайтирилиши; ўпирилиш, емирилиш ва дарё ҳамда каналлар қирғоқларининг бузилишидан ҳимоя қилиш; сел оқимлари ва сув тошқинларидан ҳимоя қилиш.'),
        (554, 61, null, null, '5.1. Мазкур бўлим талаблари одамларни ўз вақтида ва узлуксиз эвакуация қилишга йўналтирилган: - ёнғиннинг хавфли омиллари таъсирига йўлиқиши мумкин бўлган одамларни қутқаришга; - одамларни эвакуация йўлларида ёнғиннинг хавфли омиллари таъсиридан ҳимоялаш;'),
        (518, 52, null, null, '10.4. Суғориш тизими иншоотлар, бинолар ва қурилмаларнинг табиий намлаштириш етарли бўлмаган шароитда суғориладиган массивнинг тупроқ қатлами сувнинг илдизларга етиб боришини, сувларда оптимал тузланиш режими бўлишини, қишлоқ хўжалик экинларидан юксак ва барқарор ҳосил олинишини таъминлайдиган ўзаро боғлиқ комплексини ўз ичига олиши лозим.'),
        (519, 52, null, null, '10.5. Суғориш тармоғи қуйидагиларнинг таъминланишини ҳисобга олиб лойиҳалаштирилиши керак: ШНҚ 2.07.04-19 41 ҳисоб-китобдаги эҳтиёжларни суғориш учун сувнинг ўз вақтида етказиб берилиши; ерлардан фойдаланиш ва тизимнинг фойдали амал қилишининг оптимал коэффициентларини. Суғориш тармоғи жойнинг рельефи, муҳандислик-геологик шартшароитлар билан уйғун тарзда лойиҳалаштириш лозим.'),
        (520, 52, null, null, '10.6. қишлоқ хўжалик экинларининг сувни истеъмол қилиши миқдорини ўсимликшуносликдаги хўжалик фаолиятининг йўналиши, экинлар таркиби ва иқлим, тупроқ ҳамда гидрогеологик шарт-шароитлар, суғориш техникаси усулларига қараб суғориш режими асосида қабул қилиш лозим.'),
        (521, 52, null, null, '10.7. Суғориш тармоғини қувурўтказгичлар шаклида ёпиқ ёки каналлар ҳамда лотоклар шаклида очиқ ҳолида лойиҳалаштириш зарур. Суғориш тармоғининг оптимал конструкциясини танлаш тармоқ вариантларининг техник-иқтисодий кўрсаткичларини солиштириш асосида амалга оширилиши лозим'),
        (522, 52, null, null, '10.8. Ортиқча сувлар (ер устидаги ёки сизот сувлар) мавжуд бўлганида суғориш каналларидан ташқари сувни йиғиб оладиган ва чиқариб ташлайдиган ҳамда коллектор-дренаж тармоқларини ҳам лойиҳалаштириш зарур. Суғориш ва қуритиш талаб қилинадиган ерлар майдонлари тупроқларнинг мелиоратив хариталари ва агроиқтисодий ишланмалар маълумотлари асосида белгиланади. Мунтазам равишда суғориш учун сувнинг тахминий сарфи 22-жадвалда келтирилган маълумотларга кўра белгиланиши мумкин.'),
        (523, 52, null, null, '10.9. Дренаж тизимини ҳисоб-китоб қилиш ва лойиҳалаштириш ШНҚ 1.02.07-15 ва ШНҚ 2.06.15 га биноан муҳандислик қидирувлар асосида амалга оширилиши керак.'),
        (524, 53, null, null, '10.10. Иқлимга доир параметрларни ва физик-геологик маълумотларни ШНҚ 2.01.01 га мувофиқ қабул қилиш керак: қуёш радиацияси; ёғингарчиликлар, ҳарорат ва яхлашнинг чуқурлиги; соз тупроқларнинг кўчувлари; дефляцион ва шамол таъсиридаги жараёнларнинг тарқалиши; ҳудудларни сув босиши; кўчкилар ва ўпирилиш жараёнларининг тарқалиши, сейсмик туманлар, кўчки тушиш хавфи бўлган ва сейсмик туманлар.'),
        (525, 53, null, null, '10.11. Хавфли геологик жараёнлардан ҳимоя қилишга доир тадбирлар ШНҚ 2.01.03, ШНК 2.01.15 га ва бошқа меъёрий ҳужжатларга биноан назарда тутилиши керак. Селдан ҳимоя қилиш тадбирлари сел оқимларидан ҳимоя қилишнинг минтақавий схемалари билан уйғун тарзда назарда тутилиши лозим. Қирғоқларида нураш жараёнлари (кўчкилар, жарликлар вужудга келиши) юз берадиган очиқ сув оқимлари юзасидан қирғоқларни мустаҳкамлайдиган тадбирлар назарда тутилиши лозим.'),
        (526, 53, null, null, '11.1. ҲМРТ лойиҳасига кўра атроф муҳитни муҳофаза қилиш ва антропоген босим барқарор ошиб борадиган шароитда жойлашган табиий ресурслардан оқилона фойдаланиш бўйича комплекс тадбирлар: шаҳарсозлик, муҳандислик-техник, технологик ва ташкилий-маъмурий тадбирлар назарда тутилиши лозим.'),
        (527, 53, null, null, '11.2. Атроф муҳитнинг ифлосланиши бўйича танланган ва ҳудудга нисбатан бир тизимга бирлаштирилган кўрсаткичларнинг комплекс таҳлили табиий муҳитнинг аҳволини ва унинг ўзгариши динамикасини баҳолашга имкон беради. Атроф муҳитга таъсирни баҳолаш (АТБ) бўлими вужудга келган экологик вазиятнинг сабабларини аниқлашга даъват қилинган. АТБ бўлимини “АТБни ўтказиш тартиби ҳақидаги йўриқнома” РҲ 118.0027714.24-93 га мувофиқ бажариш лозим. АТБ бўлими асосида атроф муҳитни муҳофаза қилишга доир тадбирларни ишлаб чиқиш зарур'),
        (530, 55, null, null, '11.5. Атмосфера ҳавосининг муҳофазаси тураржойлар қурилган туманларда зарарли моддалар концентрациясининг меъёрий ҳужжатларга мувофиқ чекланишига асосланиши лозим. Янги посёлкаларни қуриш ва мавжудларини ривожлантириш худудлари Ўзбекистон Республикасининг ер тўғрисидаги қонунларига мувофиқ қишлоқ хўжалик ишлаб чиқариши учун яроқсиз бўлган ерларда назарда тутилиши керак. Қурилишларнинг суғриладиган ва қуритилган ерларда, шудгорда, кўп йиллик мевали дарахтлар банд қилган ерларда, шунингдек, сувдан ҳимоя қиладиган яшил дарахтзорлар банд қилган ерларда жойлаштирилишига фавқулодда ҳолларда йўл қўйилади. қурилиш тақиқланадиган ерлар рўйхати маҳаллий бошқарув органлари (вилоят ва туман ҳокимликлари) томонидан белгиланади. Қуйидагилар атмосфера ҳавосини ҳимоя қилиш тадбирларига киради: қишлоқ хўжалик корхонаси ҳудудини зоналаштиришни такомиллаштириш (тураржойлар, саноат, коммунал-омборхоналар, қишлоқ хўжалик далалари, транспорт), санитария-ҳимоялаш зоналарининг қурилиши; транспорт воситаларида ички ёниш тизимини такомиллаштириш; тозалашнинг турли хилларидан фойдаланиб технологик ва вентиляция чиқитларини тозалаш; ифлосланган чиқитларни тарқалиши радиусини ҳисобга олиб катта баландликларга чиқариб юбориш'),
        (528, 53, null, null, '11.3. қишлоқ хўжалик корхонасининг “Атроф муҳитни муҳофаза қилиш ва яхшилаш” бўлимини ишлаб чиқишда қуйидагилар мажбурий тартибда ҳисобга олиниши лозим: табиий ландшафтнинг тавсифномаси (жойнинг рельефи, ҳудуднинг табиий қуритилиши тизими, тупроқ, ўсимлик ва ҳайвонот дунёси); ҳудуднинг фонга оид микроиқлим шарт-шароитлари, ер юзасидаги сувларнинг (сув ҳавзалари, очиқ сув оқимларининг) ифлосланганлиги, тупроқ ва ўсимликлар, ер юзасидаги сувларни йиғиб олиш, тозалаш ва чиқариб ташлашнинг ташкил этилиши, атмосферанинг ер юзасидаги қатламидаги ҳавонинг ифлосланганлигининг тавсифномаси; электромагнит фони ва радиациянинг асосий манбалари (телемарказлар, ретрансляторлар, радиолокаторлар, радиостанциялар, саноат қурилмалари, электр станциялари, электр узатиш линиялари) тавсифномаси; қаттиқ чиқитлар ва чиқиндилардан тозалашни ташкил этиш; умуман аҳолининг ва айрим ёш таркибларининг касалланиши.'),
        (529, 54, null, null, '11.4. Ер усти ва ер ости сувларининг муҳофазаси Ўзбекистон Республикаси “Сув ва сувдан фойдаланиш тўғрисида” қонунига мувофиқ белгиланган муҳофаза қилинадиган сув зоналари талаблари доирасида амалга оширилиши лозим. Қуйидагилар сув ресурсларини ҳимоя қилишга доир асосий масалалар ҳисобланади: асосий ишлаб чиқариш технологиясини такомиллаштириш; сув таъминоти тизимларини такомиллаштириш (тўлиқ оборотли сув таъминотини, сувдан такроран фойдаланиш схемаларини жорий этиш, технологик эҳтиёжлар учун хўжалик-ичимлик суви истеъмол қилинишини қисқаририш ёки тўлиқ барҳам бериш, барча технологик жараёнларда сув сарфини автоматик назорат қилиш, сувни истеъмол қилишнинг техник асосланган меъёрлари ва нормативларини жорий қилиш); канализация ва оқова сувларнинг тозаланиши тизимини такомиллаштириш (ҳатто аварияли ҳолатларда ҳам сувларнинг сув ҳавзаларига оқиб тушишига йўл қўймайдиган сувларни чиқариб ташлаш усулларининг жорий этилиши, тозалаш жараёнларининг автоматик назорат қилиниши ва тартибга солиб турилиши, оқова сувлардаги ифлослантирувчи аралашмалар миқдорларини белгилаш услубларини такомиллаштириш, агрессив суюқ моддаларнинг коррозия ва абразивли таъсирига чидамли ускуналар ва сув қувурларининг қўлланилиши); ер ости сувларини ҳимоя қилишга доир тадбирлар (ҳудуднинг режалаштириш белгиларини сунъий кўтариш, ҳимоялаш гидроизоляцияси, дамбаларни тиклаш, сувни кам ўтказадиган тупроқ ва материаллар билан тўсиқлаш, ишлаб чиқариш чиқитлари ва тайёр маҳсулотларнинг лозим даражада омборларда тахлаб қўйилиши, сувни ўтказмайдиган экранлар ва тўсиқларни барпо этиш, ер ости сувларидан фойдаланишнинг ҳисобга олиниши). Муҳофаза қилинадиган сув зоналарида хўжалик фаолиятининг ўрнатилган тартибига риоя қилиш.
'),
        (531, 55, null, null, '11.6. Табиатни муҳофаза қилиш тадбирларини амалга оширишда фойдали қазилмаларнинг жойлашувини ва сифатли ер ости сувлари мавжудлигини ҳисобга олиш лозим'),
        (532, 55, null, null, '11.7. Тупроқ ва ўсимликларни ҳимоя қилишни ифлослантирувчилар таркиби, саноат чиқитларининг атмосферада қолдиқлар билан тушишига қараб, тупроқнинг газга ўхшаган бирикмаларни бевосита шимиб юбориши ва ўсимликларнинг сўриб олишини ҳисобга олиб амалга ошириш зарур.'),
        (534, 55, null, null, '11.9. Тупроқнинг бузилишлар ва ифлосланишдан ҳимоя қилинишини назарда тутиш лозим (сув эрозияси, шамол эрозияси, тупроқнинг шўрланиши, ер остидаги муҳандислик коммуникациялари, нефть ва газ сақланадиган жойларнинг жойлаштирилиши; қишлоқ хўжалик маҳсулотларини сақлаш ва қайта ишлаш корхоналари; қишлоқ хўжалик техникасини таъмирлаш корхоналари; пахтачилик далалари, кам заҳарли препаратлардан фойдаланиш, маъданли ўғитларни ёпиқ тарада ташиш, ўғитлантиришнинг авиация воситаларини қўлланишни чеклаш; қурилиш материаллари ишлаб чиқариш корхоналари, пахта тозалаш заводлари). ШНҚ 2.07.04-19 46 Тупроқни ҳимоя қилишга доир тадбирларни тупроқни ҳимоя қилишга доир қонунлар ва Ўзбекистон Республикаси Соғлиқни сақлаш вазирлиги тасдиқлаган санитария меъёрларига ҳамда ерлардан турлича фойдаланишда тупроқнинг тупроқдаги ифлослантирувчи моддалар ПДКнинг тасдиқланган меъёрларидан ортиқча ифлослантирилишининг олдини олишни таъминлайдиган бошқа идоралар талабларига мувофиқ назарда тутиш лозим.'),
        (535, 56, null, null, '11.10. Тураржой ва жамоатчилик бинолари ва улар атрофидаги ҳудудлар учун йўл қўйилиши мумкин бўлган шовқин даражалари, ташқаридаги шовқиннинг асосий манбаларининг шовқинга доир тавсифномалари, шовқинни камайтиришнинг меъморчилик режалаштириш ва қурилишакустика воситаларининг акустик самарадорлигини ҳисоблаш услубиёти ҳамда уларни лойиҳалаштириш юзасидан асосий талаблар ҚМҚ 2.01.08 га мувафиқ қабул қилинади.'),
        (536, 56, null, null, '11.11. Радиотехника объектлари (радиостанциялар, радиотелевизион, етказиб берувчи ва радиолокацион станциялар), ҳаводаги юқори кучланишли электр узатиш линиялари ва электромагнит энергияси чиқарадиган бошқа объектларни жойлаштиришда аҳолини электромагнит майдони таъсиридан ҳимоя қилишнинг вақтинчалик санитария меъёрлари ва қоидалари, электр қурилмаларини (ПУЭ) қуриш қоидаларига амал қилиш керак бўлади.'),
        (537, 56, null, null, '11.12. Радиоактив моддалар ва бошқа қувватлантирувчи нурланиш манбаларини ишлаб чиқариш, ишлаш, қайта ишлаш, қўллаш, сақлаш, ташиш, зарарсизлантириш ва кўмиб ташлашда радиация хавфсизлигининг таъминланиши радиация хавфсизлиги меъёрлари (НРБ 76/87) ва радиоактив моддалар билан ишлашнинг асосий санитария қоидалари (ОСП-78/87) га мувофиқ амалга оширилиши керак.'),
        (540, 57, null, null, '4.9. Қурилиш конструкциялари ёнғинга бардошлилик ва ёнғинга хавфлилиги билан тавсифланади. Ёнғинга бардошлилик кўрсаткич бўлиб ёнғинга бардошлик чегараси хисобланади, конструкциянинг ёнғинга хавфлилигини унинг ёнғинга хавфлилик синфи тавсифлайди'),
        (541, 57, null, null, '4.10. Қурилиш ашёлари, маҳсулот ва конструкцияларнинг ёнғинга бардошлик чегараси муайян қурилма учун меъёрланган бир ёки кетма кет бир неча чегара холатини белгиловчи аломатлари пайдо бўлиш вақти (дақиқада) билан ўрнатилади: - кўтариб туриш кобилиятини йўқотиши (R); - бутунликни йўқотиши (Е); - иссиқлик ўтказмаслик қобилиятини йўқотиши (I). Қурилиш конструкцияларининг ёнғинга бардошлик чегараси ва уларнинг шартли белгилари Ўз РСТ 30247.0-94 бўйича ўрнатилади. Бунда деразалар ёнғинга бардошлик чегараси фақат Е аломатини бошланиш вақти бўйича аниқланади'),
        (542, 57, null, null, '4.11. Ёнғинга хавфлилиги бўйича қурилиш ашёлари, маҳсулот ва конструкциялари тўрт синфга бўлинади: К0 (ёнғинга хавфли бўлмаган); К1 (кам ёнғинга хавфли бўлган); К2 (мўтадил ёнғинга хавфли бўлган); К3 (ёнғинга хавфли бўлган); Қурилиш конструкцияларининг ёнғин хавфи бўйича синфи ГОСТ 30403 бўйича ўрнатилади.'),
        (543, 58, null, null, '4.12. Ёнғинга карши тўсиклар ёнғин ўчоғи мавжуд хонадан ёки отсекдан ёнғинни ва ёнғин маҳсулотларни бошка хоналарга тарқалишини олдини олиш учун мўлжалланган. Ёнғинга қарши тўсиқларга ёнғинга қарши деворлар, оралиқ тўсиқлар, қаватлар ва оралиқ ёпмалар киради.'),
        (555, 61, null, null, '5.2. Одамларни мустақил ташкиллаштирилган ҳолда ёнғинни хавфли омиллари мавжуд хонадан ташқарига чикиш ҳарақати эвакуация жараёни хисо&ианади. Аҳолининг кам ҳаракатланиши гуруҳига кирувчи одамларнинг хизмат ҳам эвакуация хисобланади. Эвакуация – эвакуация чиқиш йўллари орқали амалга оширилади.'),
        (544, 58, null, null, '4.13. Ёнғинга қарши тўсиклар ёнғинга бардошлик ва ёнғин хавфи билан тавсифланади, ёнғинга қарши тўсиқларнинг ёнғинга бардошлиги уларнинг унсурларини ёнғин бардошлилиги билан аниқланади: - тўсувчи кисм; - тўсиқнинг турғунлигини таъминловчи қурилмалар; - уларнинг таянадиган конструкциялари; - уларнинг ўзаро маҳкамлаш тугунлари. Ёғинга қарши тўсикнинг турғунлиги таъминловчи қурилмалар, улар таянадиган конструкциялар ва уларни ўзаро маҳкамлаш тугунларнинг ёнғинга бардошлик чегаралари R аломати бўйича ёнғинга қарши тўсиқнинг тўсувчи қисмига талаб этиладиган ёнғинга бардошлик чегарасидан кам бўлмаслиги лозим. Ёнғинга қарши тўсикнинг ёнғин хавфи, унинг турғунлигини таъминловчи курилмалар, улар таянадиган конструкциялар ва уларни ўзаро маҳкамлаш тугунларининг ёнғин хавфи билан аниқланади.'),
        (545, 58, null, null, '4.14. Ёнғинга қарши тўсиқлар уларни тўсувчи қисмлари ёнғинга бардошлилиги боғлиқ ҳолда турлари 1-жадвалга, ёнғинга қарши тўсиқлардаги бўшлиқларни тўлдириш 2- жадвалга, ёнғинга қарши тўсиқлардаги бўшлиқларда назарда тутиладиган тамбур-шлюзлар 3-жадвалга асосан бўлинади. Тамбур-шлюзларнинг тўсиқлари ва том ёпмалари ёнғинга қарши бўлиши керак. 1 турдаги ёнғинга қарши тўсиклар КО синфли бўлиши лозим. Алоҳида келишилган ҳолларда К1 синфли 2-4 турдаги ёнғинга қарши тўсиқлар қўлланишига рухсат берилади.'),
        (546, 59, null, null, '4.15. Эвакуация мақсадида фойда-ланиладиган зина ва зина катаклари, қуйидаги зиналар турига бўлинади; 1 -ички зина катакларида; 2 -ички, очиқ; 3 -ташқи, очиқ Оддий зина катаклари турлари: Л1 - ҳар бир каватдаги ташқи де-ворда ойнавандли ёки очиқ туйнук би-лан; Л2 - ойнаванд ёки очиқ туйнук орқали табиий ёритиладиган Тутун қопламайдиган зина ка-таклари турлари: Н1 - ташқи ҳаво зонаси орқали очиқ ўтиш имкоци бўлган қаватдан зина катагига кириш, бунда ҳаво зонаси оркали утиш тутун қопламасига таъминланган бўлиши лозим; Н2 - ёнғин вактида ҳаво босими мавжуд зина катагига; Н3 - қаватдан зина катагига ҳаво босими мавжуд тамбур-шлюз орқали кириш билан.'),
        (547, 59, null, null, '4.16. Ўт ўчириш ва қутқариш ишларини таъминлаш учун ёнғин зиналари турлари назарда тутилади: П1 -тип; П2 -қиялиги 6:1 дан кўп бўлмаган зинапоя.'),
        (560, 61, null, null, '5.7. Биноларни тутунга қарши ҳимояси ҚМҚ 2.04.05-96 га биноан бажарилиши лозим. Бунда ёнғин вақтида тутунга қарши ҳимоя воситалари (тутун чиқариш копқоклари, тўсиклар ҳаво босими ҳосил килиш ва тутун тортиш вентиляторлари) саклаш учун, автомат тарзда ишга тушиши керак. Ёнғинни билдирмок тартиби хозирги меъёрий хужжатлар талабларига караб бажармоклиги лозим.'),
        (548, 60, null, null, '4.17. Бинолар, ҳамда биноларнинг Ёнғинга қарши деворлар билан ажратилган қисмлари: - ёнғин отсеклари (кейинчалик, бинолар), - ёнғин бардошлик даражаси, курилмавий таснифи ва функциявий ёнғин хавфи бўйича бўлинади. Ёнғин отсекларига ажратиш учун 1- турдаги ёнғинга қарши деворлар қўлланилади. Бинонинг ёнғин бардошлик даражаси унинг курилиш конструкцияларни ёнғин бардошлиги билан аниқланади. Бинонинг конструктив ёнғин хавфлилик синфи курилиш конструкцияларини ёнғиннинг таракқий этишида ва унинг хавфли омилларини пайдо бўлишида иштирок этиш даражаси билан аниқланади. Бино ва унинг қисмларини функционал ёнғин хавфлилик синфи, уларнинг бажарадиган вазифаси ва уларда жойлаштириладиган технологик жараёнларнинг хусусиятлари билан аникланади.'),
        (549, 60, null, null, '4.18. Бинолар ва ёнғин отсеклари ёнғин бардошлик даражаси бўйича 4-чи жадвалга биноан бўлинади. Биноларнинг юк кўтарувчи унсурларига, ёнғин вақтида умумий турғунлигини ва геометрик ўзгармаслигини таъминлайдиган юк кўтарувчи деворлар, устунлар, боғланишлар, бикрлик диафрагмалари, ораёпма конструкциялари (равоқлар, фермалар, тўсинлар, плиталар) киради. Бўшлиқларни (эшиклар, дарвоза, дераза ва люклар) тўлдирувчиларининг ёнғин бардошлик чегараси меъёрланмайди, махсус эслатиб ўтилган холатлар ва ёнғинга қарши тўсиқлардаги бўшлиқлар тўлдирувчилари бундан мустасно. Жадвалда келтирилган R 15 (RE 15, REI 15) конструкцияларининг энг кичик ёнғин бардошлик чегараси талаб этиладиган ҳолларда, ҳақиқий ёнғин бардошлик чегарасидан қатъий назар ҳимояланган пўлат конструкцияларини қўллашга йўл қўйилади, фақат бинонинг юк кўтарувчи унсурларини ёнғин бардошлик чегараси, синов натижалари буйича R 8 дан кам бўлмаслигини ташкил этган ҳоллар бундан мустасно.'),
        (550, 60, null, null, '4.19. Бино ва ёнғин отсеклари конструктив ёнғин хавфи буйича. Биноларнинг тўсувчи қурилмаларидаги бўшликлар тўлдирувчилар (эшиклар, дарбозалар, деразалар ва люклар) ёнғин хавфи меъёрланмайди, махсус эслатиб ўтиладиган ҳоллар бундан мустасно'),
        (1594, 121, '2024-07-03 11:24:18', '2024-07-03 11:24:18', '21.2. tegishli ravishda tuman (shahar) qurilish bo‘limining bino va inshootni rekonstruksiya qilish hamda o‘z hududida qo‘shimcha bino va inshootni qurishga ruxsatnomasi o‘z kuchini yo‘qotganligi yoki bekor qilinganligi;'),
        (556, 61, null, null, '5.3. Одамларга ёнғиннинг хавфли омиллари таъсир этганда ёки, шундай таъсир хавфи бевосита таҳдид солганда мажбурий ҳаракатланиши, кутқаришга киради. Қутқариш ишлари ҳам ёнғин ўчириш бўлинмалари ёрдамида ёки махсус ўргатилган ходимлар томонидан, шу жумладан, махсус қутқарув воситаларидан фойдаланилган холда эвакуация ва авария чиқишлари оркали мустакил амалга оширилади..'),
        (557, 61, null, null, '5.4. Одамларни эвакуация йўлларида ҳимоя қилиш хажмий-режавий, иқтисодий, конструктив, мухандислик-техник ва ташкилий тадбирлар мажмуаси билан таъминланади. Хона миқёсида эвакуация йўллари, шу хонадан эвакуация чиқиш йўллари орқали, уларда ёнғин ўчириш ва тутунга қарши ҳимоя воситалари хисобга олма-ган ҳолда одамларни бехатар эвакуация килиши таъминлаши лозим. Хоналар ташкарисида эвакуация йўлларини химояси одамларни бехатар эвакуация қилиш шароитидан келиб чиқиб, эвакуация йўлига чикадиган хоналарнинг функционал ёнғин хавфи эвакуация қилинувчилар сони, бинонинг ёнғин-бардошлик даражаси ва конструктив ёнғин хавфлик синфи, каватдан ва бутун бинодан эвакуация чикиш сонини хисобга олган ҳолда назарда тутиш керак. Хоналар ва хоналардан ташқаридаги эвакуация йўлларидаги конструкцияларнинг юза қатламлари учун кўлланиладиган (пардозлов ва қопламалар) қурилиш ашёларининг ёнғин хавфи, хона ва биионинг функционал ёнғинга хавфи ва эвакуация йўлларини ҳимоялаш бўйича бошқа тадбирларни ҳисобга олган ҳолда чекланиши лозим.'),
        (558, 61, null, null, '5.5. Қуйидаги 5.9.бандга мос келмаган одамларни қутқаришга мўлжал-ланган тадбирлар ва воситалар, ҳамда чиқишлар, барча хоналар ва бинолардан эвакуация жараёнини ташкил этишда ва лойиҳалашда эътиборга олинмайди.'),
        (559, 61, null, null, '5.6. А ва Б тоифадаги Ф5 синфга мансуб хоналарни бир вақтни 50 дан ортиқ одам тўпланиши учун мўлжалланган хона остида, ҳамда ертўла ва цокол каватларда жойлаштиришга йўл кўйилмайди. Ф1.1, Ф1.2, ва Ф1.3. синфга мансуб хоналарни ертўла ва цокол каватларида жойлаштиришга йўл кўйилмайди.'),
        (561, 61, null, null, '5.8. Ёнғин вактида одамлар хавфсизлиги таминлаш бўйича тадбирларнинг самарадорлиги хисоблаш йўли билан бахоланиши мумкин.'),
        (562, 62, null, null, '5.9. Чиқиш йўллари эвакуация йўли бўлиб хисобланади, агар улар олиб чиқса: а) 1 қават хонасидан ташкарига - бевосита; - йўлак орқали; - вестибюл орқали (фойе); - зина катаги оркали; - йўлак ва вестибюл (фойе) орқали; - йўлак ва зина катаги орқали. б) биринчидан ташқари, исталган қаватдаги хонасидан: - бевосита зина катагига ёки 3- турдаги зинага; - бевосита зина катагига ёки 3- турдаги зинага олиб чиқувчи йўлакка; - бевосита зина катагига ёки 3- турдаги зинага чиқиши бўлган хол (фойе)га. в) а) ва б) да кўрсатилган чиқиш йўли билан таъминланган, шу қаватдаги қўшни хонага (А ва Б тоифадаги Ф5 синфига мансуб хонадан ташқари) А ва Б тоифадаги хонага чиқиш, эвакуация хисобланишига йўл қўйилади, агар у юқорида эелатилган А ва Б тоифадаги хоналарга хизмат кўрсатиш учун мўлжалланган, доимий ишчи ўринларисиз техник хонадан олиб чикса. Ертўла ва цокол қаватдан эвакуация хисобланадиган чиқиш йўлларини, одатда, бинонинг умумий зина катакларидан алохида, бевосита ташқарига чиқиши назарда тутилиши лозим. Йўл қўйилади: ер тўладан эвакуацион чиқиш йўлларини умумий зина катаклари орқали, ташқарига алоҳида чиқиши йўли назарда тутилишига, агар зина катаги бошқа қисмлардан 1-турдаги ёнғинга қарши тўсик девор билан ажратилган бўлса: - В, Г ва Д тоифадаги хоналар жойлашган ертўла ва цокол қаватлардаги эвакуация жойлари, Ф5 синфига мансуб биноларнинг биринчи қаватида жойлашган Г, Д хоналарга ва вестибюл оркали назарда тутилган бўлса ва 6.29 талабномага риоя килиши зарур: - Ф2, Ф3 ва Ф4 синфига мансуб биноларнинг ертўла ва цокол қаватларида жойлашган фойе, ечиниш хоналари, чекиш хоналари ва санитария қисмларидан эвакуация чиқиш жойлари, алохида 2- турдаги зиналар бўйича биринчи қаватдаги вестибюл орқали назарда тутилган бўлса; - хоналардан эвакуация чиқиш йўллари 2-турдаги зинага, йўлак орқали (фойе, вестибюл) бу зинага олиб борувчи бўлиши керак, лекин меъёрий хужжатларнинг талабларида кўрсатилган бўлса. - бинодан, ертўла ва цокол қаватдан бевосита ташқарига чикиш жойини тамбур билан жихозлашга.'),
        (563, 62, null, null, '5.10. Эвакуация чиқиш йўллари бўлиб хисобланмайди, агар уларцинг бўшлиқларида сурилувчи ва кўтарилиб-тушувчи эшиклар ва темир иўл ҳаракатли таркиби учун дарвозалар, аГшанувчи эшиклар ва турникетлар урнатилган бўлса. Темир йўл ҳаракатли таркиб учун мўлжаданган дарвозалар ўзмдаги отилувчи эшиклар эвакуация ''''яқиш йўллари хисобланиши мумкин.'),
        (564, 62, null, null, '5.11. Хоналардан, каватлардак ва бинолардан эвакуация чиқиш иўлларини сони ва умумий кенглиги, улар орқали эвакуация қилиниши эҳтимоли бўлган одамларнинг максимал сонига ва одамлар мавжуд бўлиши мумкин бўлган (ишчи жойидан) энг яқин эвакуация чиқиш жойигача бўлган энг узоқ масофага боғлиқ. Турли функционал ёнғин хавфига эга ва ёнғинга қарши тўсиклар билан ажратилган бино қисмлари мустакил эвакуация чикиш йўллари билан таъминланиши шарт.'),
        (592, 64, null, null, '5.38. Л1 турдаги зина катаклари, баландлиги 28 м гача бўлган, ҳамма вазифавий ёнғинга хавфли синфга мансуб биноларда назарда тутилиши мумкин; бунда Ф5 синфга мансуб А ва Б тоифали биноларда, А ва Б тоифадаги хоналардаги қаватлар аро йўлакларга чиқиш жойлари доимий ҳаво босими танбур шлюз орқали кўзда тутилади'),
        (565, 62, null, null, '5.12. Иккитадан кам бўлмаган эвакуация чиқиш йўллари бўлиши керак: - бир вақтни ўзида 10 дан ортик одамни бўлиши мўлжалланган Ф1.1. синфга мансуб хоналардан, сменада ишловчиларни энг кўп сони 5 кишидан ортиқ бўлган А ва Б тоифадаги Ф5 синфга мансуб, В тоифадаги хонадан: - 25 кишидан кўп ёки майдони 1000 кв.м. ортиқ хонадан; - бир вақтни ўзида 6 тадан 15 та одам бўлишига мўлжалланган ертўла ва цокол қаватдаги хоналардан бунда иккитадан битта чиқиш 5.20 «д» банд талабига биноан назарда тутишга йўл қўйилади; - бир вақтда 50 дан ортиқ одам бўлишига мўлжалланган хоналардан; - жихозларга хизмат кўрсатиш учун мўлжалланган Ф5 синфига мансуб хоналардаги очиқ жавонлар ва майдончалардан, бунда ярус пол майдони - 100 кв.м. дан ортик бўлганда - А ва Б тоифадаги хоналар учун ва 400 кв.м. ортиқ бўлганда - иккала тоифадаги хоналар учун. Икки қаватда жойлашган Ф1.3. синфга мансуб хоналар (хонадонлар), юқори қават 15 м дан баланд жойлашганда, ҳар бир қаватдан эвакуация чиқиш жойига эга бўлиши лозим.'),
        (566, 62, null, null, '5.13. Қаватлари камида икки эвакуация чиқиш жойларига эга бўлиши керак: - Ф1.1; Ф1.2; Ф2.1; Ф2.2; ФЗ; Ф4. синфга мансуб бинолар; - қаватдаги квартиралар умумий майдонидан келиб чиқиб Ф1.3. синфга мансуб бинолар, секция турдаги бинолар учун секция қаватида – 500 кв.м. дан ортиқ бўлса; майдони кам бўлганда (қаватдан битта эвакуация чиқиш йўли бўлганда) 15 метрдан ортик, баландликда жойлашган ҳар бир квартира 5.20 банд бўйича эвакуация чиқиш жойидан ташқари, авария холати чиқиш жойига эга бўлиши керак; - А ва Б тоифаси мавжуд Ф5 синфига мансуб бинолар, бунда ишловчилар сони энг кўп сменада 5 тадан ортиқ одамни ташқил қилса, В тоифада 25 та одамни ташкил қилса; Ертўла ва цокол қаватлари майдони 300 кв. м. дан ортиқ бўлганда, ёки бир вактни ўзида 15 тадан ортиқ одам мавжуд бўлиши учун мўлжалланганда 2 тадан кам бўлмаган эвакуация чиқиш йўлларига эга бўлиши керак. Баландлиги 15 метрдан ортиқ бўлмаган Ф1.2; Ф 3 ва Ф 4.3. синфига мансуб ва майдони 300 кв. метрдан ортиқ бўлмаган, ҳамда одам сони 20 тадан ортиқ бўлмаган ҳамда зина катагига чиқувчи 2-турдаги эшик билан (2-чи жадвал бўйича) жихозлаган бино қаватларидан (ёки қаватнинг бошқа қисмидан ёнғинга қарши тўсиқ билан ажратилган қават қисмидан) битта эвакуация чиқиш йўли ўрнатишга рухсат берилади. Қўлланиш: 1. Бинонинг баландлиги юқори қаватнинг жойлаштириш жойидан баландлиги деб, тепадаги техник қаватнинг хисобга олмаган ҳолатда ҳисобланади. 2. Қаватнинг баландлиги ёнғин машиналар юрган юзидан ташқари девордаги деразанинг очиқ бўлган ҳолатдаги чегарасигача масофаси хисобланади.'),
        (567, 62, null, null, '5.14. Қават иккитадан кам бўлмаган эвакуация чиқиш жойига эга бўлиши керак, агар қаватда 2-тадан кам бўлмаган эвакуация чиқиш йўлига эга бўлувчи хона жойлашган бўлса. Бинодан эвакуация чиқиш жойлари ва йўлларнинг сони, бинонинг исталган қаватидаги эвакуация чиқиш жойлари сонидан кам бўлмаслиги керак.'),
        (568, 62, null, null, '5.15. Икки ва ундан ортик эвакуация чиқиш йўллари мавжуд бўлса улар таркатилган ҳолда жойланиши керак. Икки эвакуация чиқиш жойлари ўрнатилган бўлса, у ҳолда уларнинг ҳар бири хонада, қаватда ёки бинода мавжуд бўлган ҳамма одамларни хавфсиз эвакуация қилинишини таъминлаши лозим. Барча узоқ масофали эвакуация чиқиш жойларидан энг кичик «L» узунлиги пастда кўрсатилган тенгламалар ёрдамида топилади:'),
        (1595, 121, '2024-07-03 11:24:18', '2024-07-03 11:24:18', '21.3. “E-auksion” yagona elektron savdo maydonchasida o‘tkazilgan elektron auksion natijalari haqidagi bayonnoma o‘z kuchini yo‘qotganligi yoki bekor qilinganligi;'),
        (569, 62, null, null, '5.16. Эвакуация чиқиш жойлари баландлиги 1.9 м дан, эни эса кам бўлмаслиги керак: - 1.2 м дан - Ф1.1 синфга мансуб хонадан эвакуация қилинадиганлар сони 15 кишидан ортиқ бўлганда, бошқа вазифавий ёнғинга хавфли хона ва бинолардан, Ф1.3 синфга мансублари 50 кишидан ортиқ бўлганда бундан мустасно. - 0.8м дан – бўлган ҳамма ҳолларда. Зина катаклари ташқи тарафдаги эшиклари ва зина катакларидан вестибюлдан чиқувчи эшиклар эни хисоб бўйича, ёки зинапоя эни 5.29 бандда белгиланган зинапоя энидан кам бўлмаслиги лозим. Барча ҳолатларда ҳам эвакуация чиқиш йўлларнинг эни шундай бўлиши керакки, очиқ жой ёки эшик орқали ўтувчи эвакуация йўлини геометриясини ҳисобга олган ҳолда. одам ётган замбилни бемалол олиб ўтиш мумкин бўлсин.'),
        (570, 62, null, null, '5.17. Эвакуация чиқиш жойларидаги эшиклар ва эвакуация йўлидаги бошқа эшиклар бинодан чиқиш йўналиши бўйича очилиши лозим. Эшикларни очилиш йўналиши меъёрланмайди: а) Ф1.3 ва Ф1.4 – синфга мансуб хоналар учун; б) бир вақтда 15 дан ортиқ киши тўпланмайдиган хоналар учун, А ва Б тоифадаги хоналар бундан мустасно; в) сатхи 200 кв. м. дан ортиқ бўлмаган, доимий ишчи ўрни бўлмаган омборхоналар учун; г) санитария қисмлари учун; д) 3-турдаги зина майдончасига чиқиш учун; е) шимолий қурилиш иқлимли минтақасида жойлашган бинолар ташқи эшиклари учун'),
        (585, 64, null, null, '5.31.3ина майдонлари эни, зинапоя энидан кам бўлмаслиги, кенгайтирилган эшикли лифтларга чи- ШНҚ 2.01.02-04 21-бет қиш олдида – зинапоя эни ва лифт эшиги энининг ярми йиғиндисидан, аммо 1,6 м дан кам бўлмаслиги бўлиши шарт. Тўғри зинапояларда ора майдончалар эни 1м дан кам бўлмаслиги лозим. Зина катагига чиқувчи эшиклар очиқ ҳолда зина майдонларини ва зина пояларни энини камайтирмаслиги керак.'),
        (614, 64, null, null, '6.16. Биноларни ёнғин бўлимларига ажратувчи, ёнғинга қарши деворлар, бинонинг бор бўйича кўтарилиши ва ёнғин ўчоғи томонида бино қурилмаси қулаб тушган чоғида, ёнмаён жойлашган ёнғин бўлинмасига ёнғин тарқалмаслигини таъминлаши лозим.'),
        (571, 62, null, null, '5.18. Қаватлараро йўлаклардан, холлардан, фойелардан, вестибюллардан ва зина катакларидан эвакуация чиқиш йўллари эшиклари, уларни калитсиз эркин очилишига ҳалақит берувчи тўсиқлар бўлмаслиги лозим. Баландлиги 15 м. дан ортиқ биноларда хоналардан ташқари бу эшиклар очилмаган ёки қалин ойнадан бўлиши керак. Зина катакларида эшиклар ўзўзидан ёпилиши учун мосламалар ва зичлагичларига эга бўлиши шарт. Ташқарига ёки хоналарга олиб борадиган эшиклар зина катаклар томонидан ўз-ўзидан ёпиладиган ва зичлагичлар учун асбоблар бўлиши шарт эмас. Умумий йўлакларга олиб чиқувчи зина катаклари эшиклари, лифт холлари эшиклари, доимий ҳаво босими бўлган шлюз-тамбур эшиклари ўз ўзидан ёпилиши учун мосламалар ва зичлагичларга эга бўлиши лозим, ёнғин вақтида ҳаво босими бўлган тамбур-шлюз эшиклари ва мажбурий тутунга қарши ҳимояли хоналар эшиклари, эса уларни ўт тушган вақтда ёпилиши ШНҚ 2.01.02-04 18-бет учун автоматикага эга бўлиши керак'),
        (572, 62, null, null, '5.19. Эвакуация чиқиш жойлари, талабларга жавоб бермайдиган чиқишларга авариявий сифатида каралиши мумкин ва ёнғин вақтида одамлар хавфсизлигини ошириш учун мўлжалланиши мумкин. Ўт тушган чоғда авария чиқиш жойлари эвакуация вақтида ҳисобга олинмайди.'),
        (573, 62, null, null, '5.20. Авария чиқиш жойларига киради: а) эшик ёки люк ўлчамлари ва зина бўлиши «д» банд бўйича; б) очиқ балконга ёки лоджияга эни 1.2 м дан кам бўлмаган, балкон ёнидан, дераза бўшлиғигача (ойнаванд эшик) туйнуксиз пойдеворли ёки ойнаванд бўшлиқлар ораси эни 1.6м дан кам бўлмаган (лоджияга) чиқувчи чиқиш жойлари; в) эни 0.6 м дан кам бўлмаган Ф1.3. синфга мансуб бинонинг туташ бўлимига очиқ ўтиш жойига ёки ҳаво минтақаси орқали туташ ёнғин бўлинмасига олиб чиқиш жойи; г) балкон ва лоджияларни қаватма-қават боғловчи ташқи зина билан жиҳозланган, балкон ва лоджияга чиқиш жойи; д) пол сатхи – 4,5 метрдан паст ва + 5.0 м дан баланд бўлмаган, ўлчамлари 0.75 х 1.5м дан кам бўлмаган дераза ёки эшик орқали, ҳамда ўлчамлари 0,6хО,8м дан кам бўлмаган люк орқали, бунда чиқиш хонадаги люк-зина орқали, бевосита хонадан ташқарига чиқиш жойлари; бу зиналарнинг қиялиги меъёрланмайди; е) ўлчамлари ва зиналари I бўйича, дераза, эшик ёки люк орқали, ёнғинбардошлилиги I ва II даражали С0 ва С1 синфга мансуб бино томига чиқиш.'),
        (1242, 121, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '28. Ariza beruvchi davlat organlari va boshqa tashkilotlar xodimlarining harakatlari (harakatsizligi) yuzasidan qonunchilik hujjatlarida belgilangan tartibda shikoyat qilishi mumkin.'),
        (574, 62, null, null, '5.21. Фақат мухандислик тармоқлари ўтказиш учун мўлжалланган техник қаватлардан, ўлчамлари 0,75x1,5 м дан кам бўлмаган эшиклар орқали, ҳамда 0,6хО,8 м дан кам бўлмаган люклар орқали эвакуация чиқиш жойларисиз, авария чиқиш жойлари назарда тутилишига йўл қўйилади. Техник қават майдони 300 м2 гача бўлганда бир чиқиш жойи мўлжалланишига рухсат этилади, кейинги ҳар бир, тўла ёки тўлиқсиз 2000 м2 майдонга, яна камида бир чиқиш жойи кўзда тутилиши лозим. Техник қаватларда эвакуация чиқиш жойлари 1,8 м дан кам бўлиши мумкин эмас. Бу чиқиш жойларини техник ертўлаларда, бинодан чиқиш жойларидан алоҳида бўлиши ва бевосита ташқарига олиб чиқиши лозим.'),
        (575, 63, null, null, '5.22. Эвакуация йўллари ҚМҚ2.01.05-98 талабларига мувофиқ ёритилиши керак.'),
        (576, 63, null, null, '5.23. Хонанинг энг узоқ нуқтасидан, Ф5 синфга мансуб бинолар учун энг узоқда жойлашган иш ўрнидан, энг яқин эвакуация чиқиш жойигача йўл қўйиладиган эвакуация йўли ўки бўйича ўлчанадиган чегаравий масофа бино ва хонанинг портлаш ёнғин хавфлилиги тоифаси вазифавий ёнғинга хавфлилиги, эвакуация қилинадиганлар сони, бинонинг курилмавий ёнғинга хавфлилиги синфга мансублиги ва ёнғинбардошлик даражасига боғлиқ. 2-турдаги зина бўйича эвакуация йўлини узунлигини унинг учланган баландигача тенг қилиб олиш керак.'),
        (577, 63, null, null, '5.24. Эвакуация йўлларини 5.9- ни ҳисобга олган ҳолда назарда тутилиши лозим; улар тортлов эскалаторлар, ҳамда қуйидаги участкаларни ўз ичига олмаслиги керак; - лифт шахталаридан чиқиладиган йўлаклар орқали ёки лифт холлари ва лифт олдидан тамбурлар орқали, агар лифтлар шахталарини тўсиб турувчи курилмалари, ёнғинга қарши тўсиқларга қўйиладиган талабларга жавоб бермаса; - зина катаклари йўлакнинг бир қисми бўлганда, зина катаклари орқали ўтиш жойини ва хона орқали, унинг ичида 2-чи турдаги зина жойлаштирилган бўлса, лекин эвакуация жараёнга эга бўлмаса; - бир жойи бўйича, фойдаланиладиган том ёки махсус жиҳозланган том қисми бундан мустасно; - иккидан ортиқ қаватли ярусли боғловчи ҳамда ертўла ва пойпеш қаватидан олиб борувчи 5.9 да кўрсатилган ҳолатлар бундан мустасно, 2-турдаги зиналар.'),
        (578, 63, null, null, '5.25. Ҳамма тоифадаги ёнғинбардошлик ва қурилмавий ёнғинга хавфлилик синфига мансуб V даражали ёнғинбардош бинолардан ташқари, биноларда эвакуация йўлида қуйидагидан, ортиқ юқори ёнғинга хавфли ашёлар қўлланишига йўл қўйилмайди: - Г1, В1, Д2, Т2-синфи деворларини пардозлаш ва вестибюл, зина катаклари, лифт холлари осма шифтларини тўлдириш учун; - Г2, ДЗ, ТЗ - ёки Г2, ВЗ, Д2, Т2 - деворларни, шифтларни пардозлаш ва умумий йўлаклар, холлар ва фойелар осма шифтларини тўлдириш учун; - В2, РП2, ДЗ, Т2 - умумий йўлакларда, холларда ва фойе поллари қопламаси учун. Енгил алангаланадиган суюқликлар ишлаб чиқариладиган, қўлланадиган ёки сақланадиган А, Б ва В1 тоифадаги Ф5 синфга мансуб хоналарда полларни ёнмайдаган ашёлардан ёки Г1 ёнувчанлик гурухидаги ашёлардан тайёрланиши лозим. Хоналарда ва эвакуация йўлида осма шифтлар сирти ёнмайдиган ашёлардаги тайёрланиши керак.'),
        (579, 63, null, null, '5.26. Умумий йўлакларда, 5.9. бандда кўрсатилган бўйича, меъёрларда махсус эслаб ўтилган ҳоллар бундан мустасно, 2 метрдан паст баландликда деворлар сатҳида газ қувурлари ва ёнувчи суюқликлар қувр ўтгазгичлари, шунингдек бириктирма шкафлар, коммуникация ва ёнғин жўмраклари учун шкафлардан ташқари, жихозлар жойлаштиришга йўл қўйилмайди. 60 метрдан ортиқ узунликдаги умумий йўлакларни 2-турдаги ёнғинга қарши узунлиги давлат меъёрий хужжатлари билан аниқланадиган, лекин 60 метрдан ошмайдиган пойдеворлар билан участкаларга бўлиниши лозим. Хоналардан йўлак томонга очиладиган эшиклар учун эвакуация йўлларни эни йўлак энига қараганда камайтириш шарт. - эшик энининг ярмига, эшиклар бир томондан жойлаштирилган бўлса; - эшиклар эни масофасига эга бўлса, эшиклар икки томонидан жойлаштирилган бўлса, лекин бу талабнома ҳар қаватли бўлган йўлаклар (холлар) Ф1.3. синфдаги биноларда бўлганда ва ҳар бир хонадан зина катагига чиқиш йўли бўлганда бу қоида бўлиши шарт эмас.'),
        (603, 64, null, null, '6.5. Бинода ёнғинга қарши тўсиқлар билан ажратилган турли вазифавий ёнғинга хавфлилигига эга қисмлар бўлса, бу қисмларнинг ҳар бири вазифавий ёнгин хавфига мувофиқ келувчи бинога қўйилади-ган ёнғинга қарши, талабларга жавоб бериши лозим. Бинонинг ёнғинга қарши ҳимоя тизимини танлашда, бутунлай бинонинг вазифавий ёнгинга хавфлилиги, бино қисмларининг турли вазифавий ёнгин хавфлилигида юқори бўлиши мумкинлигини олиш лозим'),
        (580, 63, null, null, '5.27. Эвакуация йўллари уфқий участкаларнинг соф баландлиги 2 метрдан кам бўлмаслиги лозим, эвакуация йўллари уфқий участкалари ва пандуслар эни кам бўлмаслиги лозим: - 1,2 м – Ф1 синфга мансуб хоналардан 15 дан кўп киши, бошқа синфга мансуб вазифавий ёнғинга хавфли хоналардан 50 дан ортиқ киши эвакуация қилиши мумкин бўлган умумий йўллар учун; - 0,7 м – якка ишчи ўринларига жойлари учун; - 1.0 м – ҳамма бошқа холларда. Исталган вазиятда эвакуация йўлларини эни, унинг геометриясини ҳисобга олган ҳолда, одам ётган замбилни бемалол олиб ўтиш мумкин бўлиши керак.'),
        (581, 63, null, null, '5.28. Эвакуация йўлида полда 45 см дан кам бўлмаган баландлик ўзгаришига, эшик остоналари, бундан мустасно, йўл қўйилмайди. Баландлик ўзгарадиган жойда 3 тадан кам бўлмаган поғонали зина ёки қиялиги 16 дан ортиқ бўлмаган намдус назарда тутиши керак. Зина баландлиги 15 см дан ортиқ бўлганда тутқичли тўсиқ кўзда тутиш лозим. Эвакуация йўлларида айланма зиналар ва қўшимча зина олди пиллапоялари, ҳамда зина ва зина катагида турли эндаги ва баландликдаги поғоналар ўрнатишига йўл қўйилмайди.'),
        (583, 64, null, null, '5.29. Эвакуация учун мўлжалланган зина, шу жумладан зина катаклари жойлашган зиналар эни ундан исталган эвакуация чиқиши жойи (эшик) энидан кам бўлмаслиги, лекин одатда: а) 1,35м дан – Ф1.1 синфга мансуб бино учун; б) 1,2м дан – биринчи қаватдан ташқари, исталган қаватда мавжуд одамлар сони 200 кишидан кўп бўлган бинолар учун; в) 0,7м дан – якка иш жойларига олиб борувчи зиналар учун; г) 0,9м дан – қолган ҳамма ҳоллар учун кам бўлмаслиги керак.'),
        (584, 64, null, null, '5.30. Эвакуация йўлларида зина қиялиги, одатда 1:1 дан ортиқ; поғона эни, одатда 25 см дан баландлиги эса 22 см дан ортиқ бўлмаслиги лозим. Якка ишчи ўринларига ўтиш жойлари учун зиналар қиялиги 2% гача кўпайтириш мумкин. Эгри чизиқли тантанавор зиналар тор қисмида поғоналар энини 22 см гача, фақат умумий иш ўринлари 15 кишидан кам бўлган хоналар (А ва Б тоифадаги Ф5) синфга мансуб хоналардан ташқари) олиб борувчи зиналар поғоналари эни 12 см гача камайтиришга йўл қўйилади. Учинчи турдаги зиналар одатда К1 синфга мансуб, ёнғинбардошлик чегараси RЕI-30 дан кам бўлмаган деворларнинг туйнуксиз (ёруғлик тушиш бўшлиқларисиз) қисмида ёнмайдиган ашёлардан тайёрланиши лозим. Бу этажлар эвакуация чиқиш жойлари сатхидан 1,2 м баландликда тўсиқли майдонларга эга бўлиши ва даража бўшлиғидан 1м дан кам бўлмаган масофада жойлашиши лозим.'),
        (586, 64, null, null, '5.32. Зина катакларида газ қувурлари ва ёнувчи суюқликлар қувур ўтгазгичларини, тиркаш шкафларни, коммуникация ва ёнғин жўмраклари учун шкафлардан ташқари, электр кабеллари ва симлари (йўлаклар зина катакларни ёритиш учун электр ўтказгичлар бундан мустасно) исталган хоналарни жипслаштириб қуриш, юк лифтлари ва юк кўтаргичларидан чиқиш жойларини назарда тутиш, ҳамда девор сатҳида поғона ва зина майдончаси юзасидан 2,2 м гача баландликда, бўртиб чиқиб турувчи жиҳозлар жойлаштиришга йўл қўйилмайди. Баландлиги 28 метрдаги бўлган биноларда одатий зина катакларида ахлат ўтказгичлар ва хоналарини ёритиш учун электр ўтказгичлар назарда тутишга йўл қўйилади. Оддий зина катакларнинг ҳажмида фақат сақлаш хоналарни жойлаштиришга рухсат берилади. Биринчи ёки ертўла қаватнинг зина катагининг остида иситиш, сув ўлчагич, электр асбобларни текшириш учун хоналарни жойлашткриш лозим.'),
        (587, 64, null, null, '5.33. Зина катакларидан тутун қопламайдиганларидан ташқари 1- қаватда пастга тушмайдиган иккидан кўп бўлмаган лифт жойлашишига рухсат этилади. Тутун қопламайдиган зина катакларида биринчи қаватдан пастга тушмайдиган, лифт шахталарини тўсувчи қурилмалари ёнмайдиган ашёлардан бўлган, иккитадан кўп бўлмаган лифт жойлаштиришга йўл қўйилади. Бинолар ташқарида жойлаштирилган лифт қудуқлари фақат ёнмайдиган конструкциялардан тўсувчи курилмаларини ишлатиш мумкин ва уларнинг ёнғинга бардошлик чегараси меъёрий қоидаларга тенг бўлиши шарт эмас. Тутун қопламайдиган зина катакларида фақат иситиш жиҳозлари жойлаштириш мумкин.'),
        (604, 64, null, null, '6.6. Ф 5 синфларга мансуб биноларга А ва Б тоифадаги хоналар бу технологик талаблар билан йўл қўйилса, ташқи девор олдида, кўп қаватли биноларда эса, юқори қаватларда жойлаштириш лозим.'),
        (605, 64, null, null, '6.7. Ёритувчи ва пойпеш қаватларда ёнувчан газ ва суюқликлар ҳамда енгил алангаланувчи ашёлар, махсус эслатиб ўтиладиган ҳоллар бундан мустасно, ишлатиладиган ёки сақланадиган хоналар жойлаштиришга йўл кўйилмайди.'),
        (588, 64, null, null, '5.34. Зина катаклари бевосита ташкарига бино атрофидаги худудга чиқиши керак. Зина катакларидан, шунингдек вестибюлга чиқиш мавжуд бўлса, у ҳолда вестибюл йўлаклари ва ёнидаги хоналардан 1-турдаги ёнғинга қарши пойдеворлар билан ажратилган бўлиши лозим. Иккита зина катакларидан эвакуация чиқиш жойлар бўлганда ва улар умумий вестибюл томондан чиқиш жойи орқали бевосита ташқарига чиқиши лозим. Н1-турдаги зина катаклари фақат, бевосита ташқарига чиқиш жойига эга бўлиш керак.'),
        (589, 64, null, null, '5.35. Зина катаклари Л2 турдаги зиналардан катаклар бундан мустасно, одатда, ташқи деворда, ҳар бир қаватда юзаси 1.2 м2 дан кам бўлмаган ёриқлик тушувчи бўшлиқларига эга бўлиши лозим. Биноларда эвакуация учун мўлжалланган, 50% дан кўп бўлмаган ички зина катакларини ёруғлик тушувчи бўшлиқларсиз назарда тутилишига йўл қўйилади: ШНҚ 2.01.02-04 22-бет - Ф2,ФЗ ва Ф4 синфга мансуб – Н2 тоифали ёки НЗ ёнғин чоғида ҳаво босими; - Ф5 синфга мансуб В тоифадаги баландлиги 28м гача, Г ва Д тоифадагиларда бино баландлигидан қатъий назар, НЗ тоифадаги ёнғин чоғида ҳаво босими. Л2 турдаги зина катаклари томёпмада 4м2 дан кам бўлмаган ёруғлик тушувчи бўшлиқларга, зинапоялар оралиғида эни 0,7м дан кам бўлмаган масофага ёки бинонинг бор баландлиги бўйича шарқий кесимида юзаси 2м2 дан кам бўлмаган ёруғлик шахтасига эга бўлиши лозим.'),
        (590, 64, null, null, '5.36. Н2 ва НЗ туридаги зина катаклари тутунга қарши ҳимояси ҚМҚ 2.04.05-97 меъёрий хужжатларига биноан назарда тутилиши лозим. Зарур бўлса Н2 турдаги зина катакларини 1-чи турдаги туйнуксиз ёнғинга қарши пойдеворлар билан баландлиги бўйича қисмларга бўлиш лозим, бунда қисмларга ўзаро ўтиш жойлари зина катаги хажмидан ташқарида бўлади. Н2 турдаги зина катакларида ойналар очилмайдиган бўлиши лозим.'),
        (591, 64, null, null, '5.37. Н1 турдаги тутун қопламайдиган зина катакларига ташқи ҳаво минтақаси орқали олиб берувчи ўтиш жойининг тутун қопламаслиги унинг қурилмавий ва хажмий-режавий ечимлари билан таъминланиши лозим: бу ўтиш жойлари очиқ бўлиши лозим, Бинонинг ташқари девори бошқа деворгача тарқалиши 135 градусдан кам бўлган ҳолда, яқин турадиган ташқари деворгача горизонтал масофаси ички бурчакгача 4м дан кам бўлиши мумкин эмас ва бу масофа ташқари деворнинг устун миқдоригача камайтириш мумкин, лекин бу талабнома 135 градусли ички бурчакларда жойлаштирилган ўтишларга ва деворнинг устуни 1,2м дан кўп бўлмаган ҳолда ўтиши зарур эмас. Зина катаклари эшик бўшлиғи ва энг яқиндаги дераза орасидаги эни – 2м дан кам бўлмаслиги лозим. Бинонинг ички бурчакларида жойлашмаслиги керак ва 1,2м дан кам бўлмаган энга ва 1,2м баландликдаги тўсиққа эга бўлиши лозим; ташқи ҳаво минтақасида эшиклар орасидаги девор эни 1,2м дан кам бўлмаслиги керак.'),
        (593, 64, null, null, '5.39. Л2 турдаги зина катаклари I, II ва III ўтга чидамлик даражали биноларда ва конструктив ёнғин хавфли синфлари С0, С1 бўлган ҳолатда ундан ташқари Ф1,Ф2,ФЗ ва Ф4 функционал ёнғин хавфли бўйича ва баландлиги 9м дан ошмаган бўлса жойлаштирилиши лозим. Ёнғинда тепадаги ёруғлик тушунчаси автоматик равишда очиладиган бўлса, бинонинг баландлигини 12м гача кўпайтириш мумкин, лекин бино Ф 1.3. синфида бўлиши шарт ва автоматик ёнғин сигнализацияси ёки ёнғин тўғрисида хабар берадиган асбобларнинг бўлиши лозим.Фақат : - Ф2, ФЗ, ва Ф4 синфдаги биноларда бу зиналар 50% кўп бўлиши мумкин эмас, қолганлардан ташқари деворларида ҳар бир қаватда ёритувчи ойналар бўлиши керак; - Ф1.3. синфдаги секцияли биноларда ҳар 4 м. баландликда жойлаштирилган хонада а 5.20. банд бўйича авария чиқиш жойи назарда тутилиши лозим'),
        (606, 64, null, null, '6.8. Бинокорлик қурилмалари ёнғинни хуфия тарқалишига имкон бермаслиги лозим'),
        (607, 64, null, null, '6.9. Бинокорлик қурилмалари маҳкамланиш тугунлари ёнғинбардошлиги, қурилманинг талаб этиладиган ўз ёнғинбардошлигидан кам бўлмаслиги керак'),
        (608, 64, null, null, '6.10. Зал хоналарида полнинг нишаблигини ташкил этувчи қурилмалар 4 ва 5 жадвалларда қаватлараро ораёпмалар учун ўрнатилган талабларга мувофиқ бўлиши лозим.'),
        (609, 64, null, null, '6.11. Меъёрланган ёнғинбардошлик ва ёнғинга хавфли тўсувчи курилмаларни кабеллар ва қувур ўтгазгичлар кесиб ўтиш тугунлари қурилмаларнинг ёнгин техникавий талаб этиладиган кўрсатгичларини пасайтирмаслиги керак'),
        (1596, 121, '2024-07-03 11:24:18', '2024-07-03 11:24:18', '21.4. yirik investitsiya loyihasini amalga oshirish uchun Vazirlar Mahkamasining qarori o‘z kuchini yo‘qotganligi yoki bekor qilinganligi.'),
        (1597, 122, '2024-07-03 11:26:13', '2024-07-03 11:26:13', '19.1. So‘rovnomada noto‘g‘ri ma’lumotlar ko‘rsatilganligi'),
        (594, 64, null, null, '5.40. Баландлиги 28 м дан ортиқроқ биноларда, ҳамда Ф5 синфга мансуб, А ва Б тоифадаги биноларда, одатда Н1 турдаги, тутун қопламайдиган зина катаклари на-зарда тутилиши керак. Йўл қўйилади: - Ф 1.1, Ф 1.2 ,Ф 2, Ф 3 ва Ф 4 синфларга мансуб, йўлакли турли 50% ортиқ бўлмаган Н2 ёки Н3 турдаги зина катакларини назарда тутишга ва ёнғинда ҳавонинг босими бўлиши шарт; - Ф 1.3 синфдаги йўлак турдаги би-ноларда 50% кўп бўлмаган зина катак-лари Н2 турдаги бўлиши лозим; - Ф5 синфга мансуб А ва Б тоифадаги биноларда табиий ёруғлик ва дои-мий ҳаво босимли Н2 ва Н3 зина катакларини назарда тутишга; - Ф5 синфга мансуб, В тоифадаги биноларда Н2 ёки Н3 ёнғин чоғида ҳаво босими зина катакларини назарда тутишга; - Ф5 синфга мансуб Г ва Д тоифадаги биноларда Н2 ёки Н3 ёнғин чоғида ҳаво босимли зина катаклари, ҳамда Л1 турдаги зина катакларини, уларни баландлиги бўйича ҳар 20 метрда ёнғинга қарши туйнуксиз пойдеворлар билан бўлинган ва зина катагининг бир қисмидан бошқасига, зина катаги хажми-даги ташқарида ўтиш жойи билан назар-да тутишга.'),
        (595, 64, null, null, '5.41. Тутун копламайдиган чина катакли биноларда умумий йўлакларни, вестибюлларни, холлар ва фойеларни тутунга қарши ҳимоясини назарда тутиш лозим.'),
        (596, 64, null, null, '5.42. I ва II даражали ёнғинбардош СО синфга мансуб биноларда, вестибюлдан иккинчи қаватгача 6.30 талабла-рини ҳисобга олган ҳолда, иккинчи турдаги зиналар назарда тутишга йўл қўйилади'),
        (597, 64, null, null, '5.43. Баландлиги 28 м дан ортиқ бўлмаган, курилмавий ёнғин хавфлилиги Ф1.2, Ф2, Ф3, Ф4 синфларга мансуб, I ва II ёнғинбардошлилик тоифасига ва СО қурилмавий ёнғин хавфлилиги синфга мансуб меъёрлар билан талаб этиладиган эвакуация зина катаклари мавжуд ва 6.31. талаблари риоя этилган биноларда 2 дан ортиқ қаватни бирлаштирувчи, 2 турга мансуб зиналар кўлланишга йўл қўйилади.'),
        (598, 64, null, null, '5.44. Эскалаторни 2-турдаги зиналар учун ўрнатилган талабларга мувофиқ назарда тутиш лозим.'),
        (599, 64, null, null, '6.1. Ёнғин таркалишининг олдини олишта ёниш майдони, кучайиши (жадаллашуви) ни ва давомийлигини чекловчи тадбирлар оркали эришилади. Уларга қуйидагилар киради: - ёнғиннинг хавфли омилларини хона, хоналараро, турли вазифавий ёнғин хавфлилигига мансуб хоналар гуруҳи, қаватлар ва секциялар, ёнғинли бўлинмалар, ҳамда бинолар аро бўйлаб тарқалишига тўсқинлик қилувчи қурилмавий ва хажмийрежавий ечимлар;  бинолар курилмалари шу жумладан том, тарзлар ва пардозлаш ва қопламалари, хоналар ва эвакуация йўллари, юза қатламларида фойдаланиладиган қурилиш материалларининг ёнғинга хавфлилигини чеклаш; - хона ва биноларнинг технологик портлаш ёнғинга хавфлилиги пасайтириш; - бирламчи, шу жумладан автоматик ва ташиб келтирувчи ўт ўчириш воситаларни мавжудлиги; - сигнализация ва ёнғин ҳақида хабар бериш; - ёнғинда эвакуация тартибини бошқарма қилиш'),
        (600, 64, null, null, '6.2. Биноларнинг ўт ўчириш қайси бўлган қисмлари (техник хоналар ва қаватлар, ертўла ва сопол қаватлар ва биноларнинг бошка қисмлари) ни ёпиш майдони, жадаллашуви ва давомийлигини чеклашга каратилган қўшимча воситалар билан жихозланиши лозим.'),
        (601, 64, null, null, '6.3. Ёнғин тарқалишини олдини олишга қаратилган тадбирлар самарадорлигини ёнғин бевосита ва билвосита зарарини чеклаш бўйича 4 бўлим талаблари асосидаги техникиқтисодий ҳисоблар бўйича ҳисоблашга йўл қўйилади.'),
        (1598, 122, '2024-07-03 11:26:13', '2024-07-03 11:26:13', '19.2. tegishli litsenziyaga ega bo‘lmasdan loyiha-smeta hujjatlari ishlab chiqilganligi'),
        (1599, 122, '2024-07-03 11:26:13', '2024-07-03 11:26:13', '19.3. bino va inshoot qismlari rangining atrofdagi bino va inshootlar arxitektura ko‘rinishiga va tabiiy landshaft arxitekturasiga uyg‘unlashtirilmaganligi'),
        (602, 64, null, null, '6.4. Турли вазифавий ёнғинга хавфлилик синфига мансуб, бино қисмлари ва хоналар ўзаро бир биридан меъёрланган ёнғинбардошлик чегарали ва қурилмавий ёнғинга хавфлилик синфига мансуб тўсувчи қурилмалар ёки ёнғинга қарши тўсиқлар билан ажратилган бўлиши лозим. Бундай тўсувчи қурилмаларга ва ёнғинга қарши тўсиқлар турига, хоналарнинг вазифавий ёнғинга хавфлилиги, снгин юкининг катталиги, бинонинг ёнғинбардошлик даражаси ва қурилмавий ёнғин хавфлилиги синф;а мансублиги ҳисобга олинган ҳолда талаблар қўйилади.'),
        (646, 64, null, null, '7.13.Ўт ўчириш сув ўтказгичини ва бошқа доимий ўт ўчириш воситаларини биноларнинг ёнғин бардошлик даражаси, курилмавий ва вазифавий ёнғинга хавфлигидан, вақтинчалик ёнғин юкини катталиги ва ёнғин портлаш хавфлигидан келиб чиқиб назарда тутиш керак'),
        (610, 64, null, null, '6.12. Қурилмаларнинг очиқ сатхига суркалган махсус ёнғин ҳимоя қоплама ва шимдиргичлар, курилмаларни пардозлашга қўйиладиган талабларга мувофиқ бўлиши шарт. Бу қолламалар ва шимдиргичлар техникавий хужжатларида уларнинг ўзгариш даврийлиги ёки фойдаланига шароитидан келиб чиқиб қайта тикланиши кўрсатилиши лозим. Уларни даврий ўзгартириш ёки кайта тиклаш имгониятини истисно этувчи жойларда махсус ёнғин ҳимоя копламалари ва шимдиргичлар қўлланилишига йўл кўйилмайди'),
        (611, 64, null, null, '6.13. Ашёларнинг ёнғинга хавфлилигини камайтириш учун қўлланиладиган ёнғинга қарши воситаларни самдрадорлиги 4-чи бўлимда бинокорлик курилмаларини ёнғинбардошлилик чегарасини аниқлаш учун ўрнатиладиган синов воситасида баҳоланилиши лозим. Қурилмаларни ёнғинга хавфлилигини кўтариш учун қўлланиладиган ёнғинга қарши воситаларни самарадорлиги 4-чи бўлимда қурилиш қурилмаларини ёнғин бардошлик чегарасини аниқлаш учун ўрнатилган синов воситаси баҳоланилиши лозим. Метал қурилмаларнинг юк кўтариш қобилияти аниқланаётганда ёнғинга қарши ҳимоя воситалари, ҳисобга олинмайдиган самарадорлигини статик юксиз 1,7 м дан кам бўлмаган ўлчовдаги баландликкача кичрайтирилган устунлар моделларини ёки 2,8 м. дан кам бўлмаган оралиқдаги тўсинлар моделларини таққослаш синовлари йўли билан баҳолашга йўл қўйилади.'),
        (612, 64, null, null, '6.14. Томёпма ва ораёпмалар ёнғинбардошлилик чегарасини ошириш учун кўлланиладиган осма шифтлар ёнғинга хавфлилиги бўйича бу томёпма ва ораёпмаларга кўйиладиган талабларга мувофиқ бўлиши лозим, Осма шифтли хоналардаги ёнғинга қарши пойдеворлар уларнинг юқорисидаги бўшлиқни бўлиб туриши керак. Осма шифтлар юкорисидаги бўшлиқда ёнувчан газлар, чанг ҳаво аралашмаси, суюқликлар ва ашёлар ташишга мўлжалланган каналлар ва қувур ўтгазгичлар ўрнатилишига йўл қўйилмайди. А ва Б тоифадаги хоналарда осма шифтлар назарда тутишга йўл кўйилмайди. Ёнғинга қарши тўсиқлар томёпма ва ораёпмалар бўлган хоналарда уларнинг орасидаги бўш жойи бўлиши лозим.'),
        (613, 64, null, null, '6.15. Ёнғинга қарши тўсиклар бинонинг тўсиб турувчи деворлар билан туташув жойида, шу жумладан, бинонинг шакли ўзгарадиган жойда, бу тўсиқлардан четлаб ўтиб, ёнғин таркалмаслигини таъминловчи тадбирлар назарда тутилиши лозим'),
        (615, 64, null, null, '6.17. Ёнғинга қарши деворлар пойдеворга ёки пойдевор тўсинларга тарқалиши, бинонинг бор буйича кўтарилиши, ҳамма қурилмалар ва қаватларни кесиб ўтиши лозим. Ёнгинга қарши деворлар таянчга ўрнатилган бўлса, таянч конструкцияларнинг ёнғинбардошлик чегараси деворларнинг ёнғинбардошлик чегарасидан паст бўлмаслиги керак. Ёнғинга карши деворлар бино ва иншоатнинг ёнмайдиган ашёлардан тайёрлаб, сўнг қурилмасига бевосита ўрнатишга йўл қўйилади.'),
        (616, 64, null, null, '6.18. Ёнғинга қарши деворлар томдан ташқарида кўтариб туриши лозим: 60 см. дан кам бўлмаган масофага, агар чордоқли ёки чордоқсиз томёпмаларининг бирон бир унсури ёнувчан ашёлардан тайёрланган бўлса ГЗ, Г4 гуруҳлар бўйича, том бундан мустасно; 30 см. дан кам бўлмаган масофага, агар чордоқли ёки чордоқсиз томёпма унсурлари қалин ёнувчан ашёлардан тайёрланган бўлса Г1, Г2 гуруҳлар бўйича, том бундан мустасно. Агар чордоқли ёки чордоқсиз томёпма ҳамма унсурлари ёнмайдиган ашёлардан (НГ) тайёрланган бўлса, ёнғинга қарши девор томдан кўтарилиб турмаслиги мумкин, том бундан мустасно.'),
        (617, 64, null, null, '6.19. Ташки деворлари ёнувчан ёки қийин ёнувчан ашёлардан ГЗ, Г4 гуруҳлар орқали фойдаланиб бунёд этилган бинолар ёнғинга қарши деворлари бу деворларни кесиб ўтиши ҳамда деворнинг ташқи сатҳидан 30 см. дан кам бўлмаган масофада бўртиб чиқиб туриши лозим. Ташқи деворлари ёнмайдиган ашёлардан тасмасимон қилиб барпо этилса, ёнғинга қарши деворлар ойналарни бўлиб туриши лозим. Бунда ёнғинга қарши девор, девор сатхидан ташқарига бўртиб чиқмаслиги мумкин.'),
        (618, 64, null, null, '6.20. Бинони ёнғин қисмларига ажратилишида, баландроқ ва кенгроқ бўлган қисм девор ёнғинга қарши бўлиши лозим. Ёнғинга қарши девор қисмида ёнғинбардошлиги меъёрлаштирилмайдиган дераза, эшик ва дарвозалар ўранатилишига йўл қўйилади, туташган қисмларининг томидан тиклик бўйича 8 м. дан кам бўлмаган ва девордан уфқ бўйича 4 м. дан кам бўлмаган масофада.'),
        (647, 64, null, null, '7.14. Биноларнинг ёнғинга қарши сув таъминоти тизимига ўт ўчирувчи бўлинмалар ва уларнинг жиҳозлари учун доимий етишиш имконияти таъминланган бўлиши лозим.'),
        (619, 64, null, null, '6.21. Ёнғинга қарши деворларда шамоллатиш ва тутун ўтказиш каналлари ўрнатишга йўл қўйилади, қачонки улар ўрнатилган жойида ёнғинга қарши девор каналининг ҳар қайси томонида ёнғинбардошлик чегараси деворнииг меъёрлаштирилган қийматидан кам бўлмаган ҳолатда.'),
        (620, 64, null, null, '6.22. Ёнғинга қарши девор ёки ёнғинга қарши тўсиқларни бинонинг бир қисми бошқаси билан бурчак остида тутатиш жойида ўрнатишда, ташқи деворларда жойлашган бўшлиқлар энг яхши қирралари орасидаги масофа уфқ бўйича 4 метрдан кам бўлмасин, ёнғинга қарши толнинг деворга ёки тўсиққа бурчак остида туташувчи девор, гулқош ва чизиқлари 4 м. узунликда ёнмайдиган ашёлардан тайёрланиши зарур. Кўрсатилган бўшлиқлар орасидаги масофа 4 м.бўлганда, ёнгинга қарши 2-чи турдаги эшик ёки дераза билан тўлдириши лозим.'),
        (621, 64, null, null, '6.23. Ёнғин чоғида ёнғинга қарши тўсиқлардаги бўшликлар (дарчалар), одатда, ёпиқ бўлиши лозим. Ёнғинга қарши деворларда деразалар очилмайдиган бўлиши ШНҚ 2.01.02-04 27-бет керак. Эшиклар, дарвозалар, люклар, қопқоқчалар ўз-ўзидан ёпилиб қолиши учун мосламаларга ва зичлагичларга эга бўлиши керак. Очиқ ҳолда фойдаланилиши мумкин бўлган эшиклар, дарвозалар, люклар, қопқоқчалар ёнғинда уларни автоматик ёниб қолишини таъминлайдигани билан жиҳозланган бўлиши лозим.'),
        (622, 64, null, null, '6.24. Ёнғинга қарши тўсиқларда, лифт шахталари бундан мустасно, дарчалар умумий майдони улар майдонининг 25% дан ошмаслиги керак. Ёнғинга қарши тўсиқлар дарчалари тўлдиргичлари 4.14. талабларига ва мазкур бўлим талабларига жавоб бериши керак. А ва Б тоифадаги бошқа тоифадаги хоналардан, йўлаклардан, зина катакларидан лифт хоналаридан ажратиб турувчи ёнғинга қарши тўсикларда, меъёрий ҳужжатларига мувофиқ доимий ҳаво босими тамбур-шлюзлар ўрнатишга йўл қўйилмайди. Икки ва кўпроқ сонли хоналарга умумий тамбур-шлюз кўйилиши мумкин эмас.'),
        (640, 64, null, null, '7.7. Томнинг баландлиги 1м. дан ортиқ ўзгариш жойида (шу жумладан томга нур-аэрацион фонарларни кўтариш учун) одатда ёнғин зиналари кўзда тутилиши керак. Том баландлиги 10м. дан ортиқ ўзгарса агар томнинг ҳар бир 100 кв.м. дан ортиқ майдон участкаси 7.3. талабларига жавоб берадиган томга хусусий чиқиш жойига эга бўлса, ёки 7.3 бўйича аниқланадиган томнинг пастки участкаси 10 м. дан ошмаса, ёнғин зиналари назарда тутилмайди'),
        (1600, 122, '2024-07-03 11:26:13', '2024-07-03 11:26:13', '19.4. bino va inshoot qismlaridagi yaxlit ranglar va shakllar (parapet, pilyastr, deraza, eshik va hokazo) mazkur bino va inshootlarning funksional vazifasiga yoki umumiy arxitektura ko‘rinishiga mos kelmasligi'),
        (623, 64, null, null, '6.25. А ва Б тоифадаги хоналарни бошқа хоналардан ёки эшиклар, дарвозалар, люклар ва қопқоқчалардан ажратиб турувчи ёнувчан қарши тўсиқларда тамбур-шлюз ўрнатиш имкони бўлмаса ва тоифадаги хоналарни бошқа хоналардан ажратиб турувчи ёнғинга қарши тўсиқларда, қўшни қаватларга ва хоналарга ёнғинни тарқалишини ва ёнувчан газлар, енгил алангаланувчи буғлар ва ёнувчан суюқликлар, чанглар, толаларни ўтиб портлаш хавфини келтириб чиқаришга қодир йўналиши олдини олиш бўйича мажмуавий тадбирлар назарда гутилиши лозим. Бу тадбирлар самарадорлиги асосланган бўлиши шарт. Ёнғинга қарши эшик ёки дарвозалар билан ёпиб бўлмайдиган В, Г ва Д тоифадаги ёнма-ён хоналар билан алоқа қилиш учун, ёнғинга қарши тўсиқ бўшлиқ-дарчаларида; автоматик ўт-ўчириш воситалари билан жихозланган очиқ тамбурлар назарда тутишга йўл қўйилади. Бу тамбурларнинг тўсиб турувчи қурилмалари ёнғинга қарши бўлиши лозим.'),
        (624, 64, null, null, '6.26. Ёнгинга қарши тўсиқлар дарчалари тўлдиргичлари, одатда, ёнмайдиган ашёлардан тайёрланади, Эшиклар, дарвозалар. люклар ва қопқоқчаларни 4мм дан кам бўлмаган қалинликда ёнмайдиган ашёлар билан ҳимояланган, ёниш гурухи ГЗ дан кам бўлмаган ашёлар қўллаш билан тайёрлашга йўл қўйилади. Ёнувчан газлар, суюқлар ва ашёлар қўлланилмайдиган ва сақламайдиган ҳамда ёнувчан чанглар пайдо бўлувчи жараёнлар билан алоқадор бўлмаган хоналар томонидаги ёнғинга қарши тўсиқлардаги тамбур-шлюз, эшиклари, эшиклар дарвозалар ва люклар калинлиги 4 мм дан кам бўлмаган ва бўшлиқсиз ёниш гурухи ГЗ ашёлардан тайёрланишига йўл қўйилади.'),
        (675, 67, null, null, '3.29.Участкаларда ёнғинни ўчириш машиналари учун (участканинг турли тарафидан) иккита йўл мўлжалланиши керак. Махсус муассаса биноси атрофида деворлардан 5 м узоқликда ёнғинни ўчириш машиналари учун йўлкаларни кўзда тутиш керак.'),
        (625, 64, null, null, '6.27. 1-тоифадаги ёнғинга қарши деворлар ва ораёпмаларни, ёнувчан газлар, чанг-ҳаво аралашмаси, суюқликлар, мода ва ашёларни ташиш учун каналлар. шахталар ва қувур ўтказгичларни ўтишига йўл қўйилмайди. Юқорида кўрсатилганлардан фарқланувчи муҳитни ташиш учун каналлар, шахталар ва қувур ўтказгичлар бундай ёнғинга қарши тўсиқларни кесиб ўтиш жойида, каналлар, шахталар ва қувур ўтказгичлар бўйича ёниш маҳсулотларини тарқалишини бартараф этувчи автоматик мосламалар назарда тутилиши лозим'),
        (626, 64, null, null, '6.28. Лифт шахталари (5.33-да кўрсатилганлардан ташқари) ва лифтларнинг машина бўлинмалари хоналари (томда жойлашганларидан ташқари), хамда коммуникациялар ўтказиш учун каналлар, шахталар, тахмончалар тўсувчи қурилмалари 1-турдаги ёнғинга қарши пойдеворлар ва 3-турдаги ораёпмаларга қўйиладиган талаблар мувофик бўлиши керак. Юқорида кўрсатилган лифт шахталари тўсиқларда ёнғинга қарши эшиклар ўрнатиш имконияти бўлмаса, 1-турдаги ёнғинга қарши пойдеворли ва 3-турдаги ораёпмали тамбурлар ёки холлар ёки ёнғин чоғида лифт шахталари эшикларни автоматик равишда ёниб куювчи экран назарда тутилиши лозим. Бундай жараёнлар ёнмайдиган ашёлардан тайёрланиши ва уларнинг ёнғибардошлик чегараси ЕI 45 дан кам бўлмаслиги зарур. Чиқиш жойида ёнғин чоғида ҳаво босими тамбур-шлюзга эга бўлмаган, тутун қопламайдиган зина катакли биноларда тахталарида автоматик тутунга қарши ҳимоя назарда тутиш лозим. Ахлат ўтказгичлар тақаси ёнмайдиган ашёлардан тайёрланиши лозим.'),
        (627, 64, null, null, '6.29. Технология талаблари бўйича биноларда ҳамма синфлар ичида, лекин Ф1.3. – да эмас, ертўла ва цокол қаватлар биринчи қават билан алоқа бўлганда зиналар ўрнатиши шарт. Бу зиналар эвакуация вақтида ҳисобга олиш керак эмас, фақат 5.9.-да кўрсатилган бўлмаса. Бу зиналар ёнғинга қарши тўсиқлар 1-турдаги ва тамбур-шлюз ёрдамида ажратилиши лозим. Агар цокол ва ертўла хоналарда ёнғинга хавфли моддалар ёки ашёлар сақланадиган бўлса, тамбур-шлюзда керакли ҳаво босими бўлиши шарт. Ф5 синфдаги биноларда зиналар ертўладаги ёки цокол хоналардан Г ва Д категорияли бўлган ҳолда, тепадаги кўрсатилган ажратиш тўсиқлар ишлатиши зарур эмас.'),
        (628, 64, null, null, '6.30. Вестибюлдан иккинчи қаватга олиб борувчи 2-турдаги зина ўрнатилса, вестибюл йўлаклардан ва ёнма-ён хоналардан 1-турдаги ёнғинга қарши пойдеворлар билан ажратилган бўлиши лозим.'),
        (629, 64, null, null, '6.31. 5.43-да тутилган 2-турдаги зина жойлашган хона, унга туташувчи йўлаклардан ва бошқа хоналардан 1-турдаги ёнғинга қардги пойдевор билан ажратилиши лозим. Иккинчи турдаги зина жойлашган вақтда ёнғинга қарши пойдевор билан ажрамаслигига йўл қўйилади: - бинода бутунлай автоматик ўт ўчириш ўрнатилса; - бинонинг баландлиги 9 метрдан ва қаватнинг майдони 300кв.м. дан кўп бўлмаган ҳолда.'),
        (630, 64, null, null, '6.32. Ертўла ва цокол қаватда лифт рўпарасида ёнғин чоғида ҳаво босими 1-турдаги тамбур-шлюз ўрнатиши назарда тутилиши лозим. Агар цокол ва ертўла хоналарда ёнғинга хавфли моддалар ёки ашёлар сақланадиган бўлса, тамбуршлюзда ёнғин пайдо бўлганда керакли ҳаво босими бўлиши шарт. 6.33. Бино ва ёнғин бўлинмаларини ўлчамларини, ҳамда бинолар орасидаги масофани тўплашда, улар- ШНҚ 2.01.02-04 29-бет нинг ёнғинга бардошлигидан, қурилмавий ва вазифавий ёнғинга хавфлилигидан келиб чиқади, ёнғин юки каталигидан, ҳамда ёнғинга қарши қўлланилаётган ҳимоя воситаларини самарадорлигини, ўт ўчириш хизмати қарши қўлланилаётган ҳимоя воситаларини самарадорлигини, ўт ўчириш.'),
        (631, 64, null, null, '6.33. Хизмати мавжудлиги ва олислигини уларнинг қуролланганлигини, ёнғиннинг эҳтимоли иқтисодий ва экологик оқибатларини ҳисобга олган ҳолда ишлаб чиқиш лозим'),
        (632, 64, null, null, '6.34. Фойдаланиш жараёнида ёнғинга қарши ҳимоя мухандислик воситаларнинг ишга яроқлилиги таъминланиши лозим'),
        (633, 64, null, null, '6.35. Автоматик ўт ўчиришни ва ёнғин сигнализациясини давлат меъёрий хужжатларига мувофиқ равишда назарда тутиш керак.'),
        (644, 64, null, null, '7.11.Том нишоблиги 12% гача бўлган, баландлиги пештоқгача ёки ташқи девор юқорисига 10 дан ортиқ биноларда, ҳамда том нишоббошлиги 12% дан ортиқ, баландлиги пештоқгача 7м. дан ортиқ биноларда ГОСТ 257722 га мувофиқ томда мувофиқ тўсиқ назарда тутилиши лозим. Бино баландлигидан қатъий назар, шу стандарт талабларига мувофиқ, фойдаланиладиган иссиқ томларда, балконларда, лоджияларда ташқи галлереяларда, очиқ ташқи зиналарда, зинапояларда ва майдончаларда тўсиқ назарда тутилиши лозим'),
        (634, 64, null, null, '7.1. Эхтимолли ёнғинни ўчириш ва қутқарув ишларини олиб бориш, қурилмавий, хажмий-режавий, мухандислик-техникавий ва ташкилий тадбирлар орқали таъминланади: Уларга куйидагилар киради: а) вазифавий ўтиш йўллари ва олиб келувчи йўллар билан бирга ўт ўчириш техникаси ўтиши учун ва олиб келувчи йўллар ёки махсус йўллар жиҳозланиши; б) ташқи ёнғин зиналари жиҳозланиши ва ўт ўчириш бўлинмалари ходимларини ва ўт ўчириш техникасини бино қаватларига ва томига олиб чиқишининг бошқа усуллари билан таъминлаш, шу жумладан ўт ўчириш бўлинмаларини ташиш режасига эга портлар ўрнатилиши; в) ёнғинга қарши сув ўтказгичлар, шу жумладан хўжалик сув ўтказгичлари билан бирга ёки махсусини ўрнатиш, зарур бўлса қувурлардан ва ёнғин сиғимлари (резервуарлар) ўрнатиш; г) бино ичида, ўт ўчириш бўлинмалари ўтиш йўлини тутунга қарши ҳимоялаш керак; д) зарур ҳолларда биноларни одамларни якка ёки оммавий тартибда қутқариш воситалари билан жиҳозлаш; е) аҳоли яшаш манзилгоҳи ёки объект худудида уларнинг ҳаракат доирасида жойлашган объектдаги ўт ўчириш шароитига мувофиқ ёнғин техникаси билан таъминланган зарур сонига эга шахсий ёнғикдан саклаш бўлинмасини жойлаштириш. Бу тадбирларни танлаш биноларнинг ёнғинга бардошлик даражаси қурилмавий ва вазифавий ёнғин хавсизлиги синфига мансублигига боғлиқ.'),
        (635, 64, null, null, '7.2. Асосий ва махсус ўт ўчириш маҳсулотлари учун ўтиш йўлларини, давлат меъёрий хужжатлари талабларига мувофик равишда назарда тутиш лозим.'),
        (636, 64, null, null, '7.3. Том карнизи ёки ташқи девор тепасигача (пойдевор), баландлиги 10 м. ва ортиқ учун томга зина катакларидан бевосита ёки чордоқ орқали иситилувчи ундан мустасно, ҳамда 3 турдаги зиналар бўйича ёки ташқи ўт ўчириш зиналари бўйича чиқиш жойлари назарда тутилиши лозим. Томга чиқиш жойлари сони ва уларнинг жойланишини бинонинг вазифавий ёнғинга хавфлилиги ва ўлчамларидан келиб чиқиб назарда ШНҚ 2.01.02-04 30-бет тутилиш лозим, лекин 1 чиқиш жойидан кам эмас: - чордоқли томёпмавий бино узунлиги ҳар бир тўлиқ ва нотўлиқ 100м., чордоқсиз томёпмавий Ф1, Ф2, ФЗ ва Ф4 синфга мансуб бинолар учун, бино томининг ҳар бир тўлиқ ва нотўлик 1000 кв.м майдонига 1 дан кам бўлмаган чиқиш жойи; - ёнғинга карши зиналар бўйича 200м дан сўнг Ф5 синфга мансуб бинолар айланаси бўйича. Назарда тутилишига йўл қўйилмайди: - агар бинонинг эни 150м. дан ошмаса бош тарзига қарама-қарши томонда ёнғинга қарши сув ўтказгич линияси мавжуд бўлса бинонинг бош тарзида ўт ўчириш зиналарни; - том ёпмаси 100м. дан ёпмайдиган бир қаватли бинолар томига чиқиш жойини.'),
        (637, 64, null, null, '7.4. Бино чордоқларида томга ўлчамлари 0,6x0,8м. дан кам бўлмаган эшиклар, люклар ёки деразалар орқали доимий зиналар билан жиҳозланган жойлари назарда тутилиши лозим, лекин Ф1.4. синфидаги бинолардан ташқари. Том ёки чордоққа зина катакларидан чиқиш жойлари, чиқиш олдида майдончаси бор, зинапоялар бўйича, ўлчамлари 0,75x1,5 м. дан кам бўлмаган 2 турдаги ёнғинга қарши эшиклар орқали назарда тутилиши керак. Баландлиги 15м. гача бўлган Ф1, Ф2, ФЗ ва Ф4 синфга мансуб биноларда чордоқ ёки томга зина катакларида 0,6x0,8м. ўлчамли 2 турдаги ёнғинга қарши ток орқали, маҳкамланган пўлат нарвонлар бўйича чиқиш жойлари жиҳозланишига йўл қўйилади.'),
        (638, 64, null, null, '7.5. Техник қаватларда, шу жумладан техник тоқ хоналарда ва техник чордоқларда ўтиш жойлари баландлиги 1,8м дан кам бўлмаслиги, чордоқларда бутун бино бўйлаб – 1,6м. дан кам бўлмаслиги лозим. Бу ўтиш жойлари эни 1,2м. дан кам бўлмаслиги керак. Айрим буюклардан ортиқ бўлмаган масофада ўтиш баландлигини 1,2м. гача энини эса 0,3м гача камайтиришга йўл қўйилади.'),
        (639, 64, null, null, '7.6. Мансубдорли биноларда тўсиб турувчи қурилмаларда чордоқлар бўшлиғи назарда тутилиши лозим.'),
        (680, 68, null, null, '4.3. Ногиронлар ва аҳолининг камҳаракатли гуруҳи бино, иншоотлар қаватларидаги эвакуация чиқиш жойларига имкон қадар яқин жойланиши керак. Охири берк йўлакка чиқадиган эшикдан қаватдаги эвакуация чиқиш жойигача масофа 15 м дан ошмаслиги керак.'),
        (641, 64, null, null, '7.8. 10м. дан то 20м. гача баландликка кўтарилиши учун ва томнинг баландлиги 1м. дан то 20 м. гача ўзгариш жойида Ш турдаги ёнғин зиналари 20м. дан баландроққа кўтариш учун ва баландлик 20м. дан ортиқ ўзгарувчи жойларда П2 турдаги зиналар кўлланиши лозим. Ёнғин зиналари ёнмайдиган ашёлардан тайёрланиши, деразадан 1м. дан якинроқка жойлашмаслиги ва улардан ўт ўчирувчи бўлинмалар фойдаланишга ҳисобланган бўлиши керак'),
        (642, 64, null, null, '7.9. Зиналар-поялар орасида ва зинапоя тўсиғи тутгичлари орасида, режада соф этиш 75мм. дан кам бўлмаган тирқиш қолдирилиши лозим.'),
        (643, 64, null, null, '7.10. Баландлиги 5м. дан ортиқ Ф1.1. синфга мансуб биноларнинг ҳар бир ёнғин бўлинмасида баландлиги 28м. дан ортиқ ҳамма вазифани ёнғинга хавфлилик синфига мансуб биноларда, тўсувчи қурилмалари RЕI120 ёнғинга бардошлик чегарасига эга ёнғинга қарши тўсиқлар талабларига жавоб берувчи, Е60 ёнғин бардошлик чегарасига эга ёнғинга қарши эшикли шахталарда жойлашган «ўт ўчириш бўлинмаларни ташиши» режимига эга лифтлар назарда тутилиши лозим'),
        (645, 64, null, null, '7.12.Ўт ўчириш депосини, давлат меъёрий хужжатлари тартибларига мувофиқ худудларда жойлаштириш керак.'),
        (648, 65, null, null, '3.1. Ногиронлар ва аҳолининг камҳаракатли гуруҳлари учун қурилмалар ҳудудида қулай шароитларни яратиш ШНҚ 2.07.01 ва мазкур лойиҳалаш меъёрлари талабларига мувофиқ ишлаб чиқилган лойиҳаларни амалга ошириш орқали таъминланади. Тураржой ва жамоат зоналари ҳудудларини лойиҳалашда ноқулай табиийиқлимий таъсирларни камайтириш, офтоб уриши, ортиқча қуёш радиацияси ва чанг-тўзонли шамоллардан ҳимоя қилишга имкон берадиган талаб ва тавсияларга комплекс риоя қилиш, ҳудудни юқори савияда ободонлаштириш, кўкламзорлаштириш, сув келтириш, транспорт бекатлари, хизмат кўрсатиш объектлари, автомобиль тўхташ жойларига қулай етиш учун пиёдалар йўлкаларининг энг қисқа йўналишларини рационал ташкил этишни назарда тутиш керак. Кўрсатилган талаблар аҳолининг барча табақалари, жумладан, ногиронлар ва камҳаракатланувчи гуруҳлар учун нормал яшаш шароитларини таъминлашда асосий шарт ҳисобланади.'),
        (649, 65, null, null, '3.2. Транспорт ва пиёдалар йўлкаларининг параметрлари ва қурилишига оид талаблар ШНҚ 2.07.01 га мувофиқ қабул қилинади; ногиронлар ҳаракат-ланиши учун қулай шароитларни таъминлайдиган талаблар мазкур лойиҳалаш меъёрлари матнида келтирилган.'),
        (650, 65, null, null, '3.3. Тураржой қурилишида бирламчи ва кундалик маданий-маиший шахобчалар (дам олиш ва жисмоний тарбия машғулотлари учун майдонча, болалар учун ўйин майдончаси, маҳалла маркази, сабзавот бозори ва б.)га қулай етиб ШНҚ 2.07.02-07 7-бет боришни таъминлаши керак; хизмат кўрсатиш шахобчаларига етиб бориш радиуслари ШНҚ 2.07.01 га мувофиқ қабул қилинади.'),
        (651, 65, null, null, '3.4. Ногиронлар ва қариялар учун кўп квартирали махсус уйлар, ногиронларга мўлжалланган махсус уйлар қулай экологик шароитлар (шовқин-сурон ва табиий муҳит ифлосланишининг зарарли таъсиридан ҳоли)да, жамоат транспорти бекатлари ва ижтимоий инфратузилма шахобчаларига қулай етиб боришни ҳисобга олган ҳолда жойлаштирилиши керак; уларга пиёда етиб бориш учун масофа 150-200 м дан кўп бўлмаслиги тавсия этилади'),
        (652, 65, null, null, '3.5. Жамоат транспорти бекатлари, автомобиль тўхташ жойлари ва оммавий ташриф иншоотларидан даволаш-профилактика объектлари, дорихоналар, савдо муассасалари, хизмат кўрсатиш комплекслари, бозорлар, маданий-кўнгил очар объектлар ва спорт иншоотлари, автостанциялар, автовокзаллар, темир йўл вокзаллари, аэропортларгача бўлган масофа ногиронлар учун қисқа - қои-дага кўра 150 м дан кўп бўлмаслиги ва қулай бўлиши керак. Жамоатчилик автомобиль тўхташ жойларида ногиронларнинг автомашиналари учун тўхташ жойлари барпо этишга бўлган талабларни 3.24-3.26-бандларидан олиш мумкин'),
        (653, 65, null, null, '3.6. Курси-аравачадаги ногиронлар ҳаракатланадиган йўлнинг кенглиги 1.6 м дан кам бўлмаслиги керак; қарама-қарши йўналишли йўлларда курси-аравачадаги ногиронлар ҳаракатланиши учун йўлнинг кенглиги, қоидага кўра, 2,0 м дан кам бўлмаслиги керак. Пиёдалар, жумладан, ногиронлар ҳаракатланадиган серқатнов жойларда йўлнинг кенглиги 2,4 м дан кам бўлмаслиги тавсия этилади; шаҳар марказларида эса 3,6 м дан кам бўлмаслиги керак. Кенглиги 2,0 м дан кам бўлган йўлларда ҳар 60м да айлана майдонлар барпо этилиши керак. Курси-аравачадаги ногиронлар асосий ҳаракатланиш йўлидан четга чиқиши учун тротуар ёки пиёдалар йўлкасининг меъёрий кенглиги торайтирилишига йўл қўйилмайди. Изоҳ: Махсус муассасалар атрофидаги ҳаракатланиш йўлларининг параметрлари лойиҳалаштиришга берилган топшириқларда қўйилган қўшимча талабларни ҳисобга олган ҳолда қабул қилинади.'),
        (654, 65, null, null, '3.7. Курси-аравачадаги ногиронлар ҳаракатланиш йўллари бўйлама қиялиги 5% дан ошмаслиги керак. Бино ва иншоотлар олдидаги тротуардан тушувчи йўлакчани, шунингдек, йўлакчанинг горизонтал майдонча (1,5 м дан кам бўлмаган)лари бўлган йўлнинг 10-12 м масофада 10% бўйлама қияликка рухсат берилади. Ҳаракатланиш йўлининг кўндаланг қиялигини 1-2 % атрофида қабул қилиш керак'),
        (691, 69, null, null, '4.14. Автовокзаллар ва темир йўл вокзалларининг пассажир платформаси (перрон)га олиб чиқувчи тоннелларида пандусларнинг кенглиги 1,5 м бўлиши назарда тутилиши керак, тутқичларнинг баландлиги 0,8 - 0,9 м бўлиши керак. Пандуснинг юзаси қаттиқ ва ғадир-будир (сирпанишнинг олдини олувчи) бўлиши керак. Пандуснинг қиялиги 1:10.'),
        (655, 65, null, null, '3.8. Газон ва кўкаламзорлаштирилган майдончалар атрофидаги тротуар ва пиёдалар йўлкалари ердан 5-7 см баланд бўлиши ва баландлиги 4-5 см, эни 10 см бўлган бордюр, борт тошлари билан тўсилиши керак. Пиёдалар йўлаклари ва тротуарлар транспорт ҳаракати билан тўқнашадиган жойларда бортли тўсиқлар баландлиги 2 см дан баланд бўлмаслиги керак, «тўсиқсиз» технология қўлланилиши тавсия этилади.'),
        (656, 65, null, null, '3.9. Маҳаллий жойларда пиёдалар йўлкаси 2,0 м дан, транспорт ҳаракатланадиган кўчаларда 2,4-3,0 м дан кам бўлмаслиги керак. Кўчанинг ҳаракатланиш қисмидан кесиб ўтиш жойларида ҳавфсизлик жойи, қоидага кўра, 2 м дан кам бўлмаслиги керак. Кесиб ўтиш жойи 0,6 м дан кам бўлмаган тўсиққа эга бўлиши керак.'),
        (657, 65, null, null, '3.10. Кесиб ўтиш жойларини барпо этишда пиёдалар учун светофорлар 1,7 м дан ошмаган баландликда жойлаштирилиши керак. Светофор билан бир қаторда кўриш қобилияти заиф ва кўр одамлар ҳавфсизлигини таъминлаш учун махсус синхрон товуш сигналлари қўлланилиши керак.'),
        (658, 65, null, null, '3.11. Рельеф қияликларида очиқ зинапоялар кенглиги қоидага кўра, 1,6 м, ундаги зиначалар кенглиги 0,4 м дан кам бўлмаслиги, зиначалар баландлиги 0,12 м дан ошмаслиги керак; бир зинапоя маршида зиначалар сони 3 дан кам бўлмаслиги керак; баландлик бундан паст бўлса, пандуслар қўлланилиши ке-рак. Бир маршдаги зиначалар шакли, зиначаларнинг кенглиги ва баландлиги бир хил бўлиши керак. Зиначаларнинг кўндаланг қиялиги 1-2 % атрофида бўли-ши керак. Ҳаракатланиш хавфсизлигини таъминлаш учун зинапоя зиначаларининг қопламаси ғадир-будир бўлиши керак; зиначаларнинг қопламаси текис тарашланган тошдан, сирпанчиқ қилиб ишланишига йўл қўйилмайди. Зиналарга махсус контраст буёқлар қўлланилиши, шунингдек, зинапоялар кечки вақт ёрити-ши керак. Зинапоялар 0,15 - 0,20 м баландликдаги, бортларга эга бўлиши ва 0,7-0,9 м баландликда тутқичлари (мактабгача ёшдаги болалар учун 0,5м баландликда) бўлиши керак. Тутқичнинг узунлиги зинапоя маршидан 0,03 м узунроқ бўлиши керак. Тутқичлар тўғритўртбурчак ёки думалоқ шакл (0,05 - 0,04 м диаметр)да бунёд этилади. Эни 2,5м дан кенг бўлган зинапоялар узунасига тутқичлар билан бўлини-ши керак. Зинапоя остидаги бўшлиқ 2,1 м дан кам бўлганида, 0,7 м баландликдаги тўсиқ билан чегараланиши керак. Ҳаракатланиш йўлларида зинапоялар билан бирга пандуслар қўлланилиши керак; курси-аравачадаги ногиронлар учун махсус кўтарувчи мосламалардан фойдаланишга йўл қўйилади.'),
        (659, 65, null, null, '3.12. Пиёдалар йўлкасининг горизонтал қияликлари оралиғида пандуслар қурилишини кўзда тутиш керак. Пандус ва уларнинг тўсиқлари НГ гуруҳига кирувчи ёнғинга чидамли материаллардан бўлиши керак. Ногирон аравачаси ҳаракатланиши учун пандуснинг кенглиги қоидага кўра, 1,0 м дан кам бўлмаслиги керак; баъзи жойларда 0,9 м бўлишига йул қўйилади. Пандус ҳар бир маршининг кўтарилиш баландлиги 0,8 м дан ошмаслиги керак, бунда пандуснинг бўйлама қиялиги 1:12 нисбатда, кўндаланг қиялиги эса 1:50 нисбатда бўлиши керак; пандус узунлигининг қияликка боғлиқлиги 4- иловада келтирилган. Пандуслар чети 0,15 - 0,20 м баландликдаги бортларга ва 0,7-0,9 м баландликда тутқичлар (мактабгача ёшдаги болалар учун 0,5 м)га эга бўлиши керак; тутқичларнинг узунлиги пандус узунлигидан 0,03м узунроқ бўлиши керак. Тутқич айлана диаметри 0,04 - 0,05 м ни ташкил этиши керак. Ҳар бир кўтарилиш боши ва охирида пандус кенглигидан кам бўлмаган ва узунлиги 1,5 м дан кам бўлмаган горизонтал майдонча аравачанинг қийинчиликсиз бурилишига имкон бериши керак; бунда майдончанинг «чуқурлиги» 1,5 м дан кам бўлмаслиги, майдончанинг кенглиги эса иккита марш кенглиги билан баробар олиниши керак.'),
        (660, 65, null, null, '3.13. «Винтли» пандусларнинг бунёд этилиши оддий пандус барпо этишнинг имкони бўлмаган фавқулоддаги ҳолатлардагина рухсат этилади. «Винтли» пандуслардаги горизонтал майдончаларнинг ички радиуси 2 м дан кам бўлмаслиги керак. «Винтли» пандус диаметрининг қияликка боғлиқлигини 6-иловага асосланиб қабул қилиш тавсия этилади.'),
        (661, 65, null, null, '3.14. Пиёдалар ерости ўтиш жойларида курси-аравачадаги ногиронлар, шунингдек, болалар аравачаси ҳаракатланиши учун 0,9 - 1,0 м кенгликдаги пандуслар барпо этилиши керак, бунда пандуслар 3.12 банд бўйича кўзда тутилиш лозим. Ерости ўтиш жойларидаги зинапоялар кенглиги 1,8 м дан кам бўлмаслиги ва 3.15. банд талабларига жавоб бериши керак. Изоҳ: Йирик транспорт объектларидаги пиёдалар тоннелларида пандуслар 1,5 м дан кам бўлмаган кенгликда (юк аравачаларининг ҳаракатланишини ҳисобга олган ҳолда) лойиҳалаштирилади; пандуснинг қиялиги 1:10.'),
        (662, 65, null, null, '3.16. Камҳаракат фуқаролар ва ногиронлар ҳаракатланадиган йўлларда турникет ва айланма эшиклар қўлланилишига йўл қўйилмайди'),
        (663, 65, null, null, '3.17. Жамоат жойлари ва тураржойлардаги тротуар ва пиёдалар йўлаклар бўйлаб ҳар 200м да ўриндиқлар ва ичиш учун сув фонтанчалари барпо этилиши назарда тутилиши керак.'),
        (664, 65, null, null, '3.18. Жамоат жойлари ва тураржойлардаги дам олиш майдончалари кўкаламзорлаштириш, сув келтириш, ташқи ёритиш, соябон айвончалар, ўриндиқлар, ичимлик учун сув фонтанчалари каби ободонлаштиришнинг юқори савиясига эга бўлиши керак.'),
        (665, 65, null, null, '3.19. Пиёдалар йўлкаси, тротуарлар ва пандусларни қоплашда қўлтиқтаёқ ва курси-аравачадаги ногиронлар ҳаракатланишини қийинлаштирадиган сочма ва йирик таркибли материаллардан қўлланилишига йўл қўйилмайди. Бетон плитали қопламалар текис бўлиши, плиталар орасидаги чоклар қалинлиги, қои-дага кўра, 1,0 см дан ошмаслиги керак.'),
        (742, 77, null, null, '6.6.Курси-аравачадаги ногиронлар ташриф буюрадиган жамоат биноларидаги ўчиргич ва розеткалар полдан 0,8 м баландликда бўлиши назарда тути-лиши керак. Психоневрологик интернатлар ва ногирон болалар учун ўқув-тар-бия муассасаларида розеткаларни 1,8 м баландликда жойлаштириб, ҳимоя ускуналари билан жиҳозлаш керак'),
        (666, 65, null, null, '3.20. Ногиронлар ва аҳолининг камҳаракатли гурухлари ҳаракатланадиган йўлларда визуал ва товушли маълумотлар учун қурилмалар назарда тутилиши керак. Визуал маълумот пиёда ҳаракатланадиган сатҳдан 0,9 - 1,7 м баланд-ликда жойлаштирилиши керак. Транспорт йўлларини кесиб ўтиш жойлари ва жамоатчилик бинолари, иншоотларига киришда товушли сигнализация ўрнатилиши назарда тутилиши керак.'),
        (667, 65, null, null, '3.21. Ҳаракатланиш йўлида жойлашган таянч конструкция ва қурилмалар: маълумот тахталари, почта қутилари, таксофонлар ва бошқа осилиб турадиган қурилма ва конструкциялар ҳаракатланиш параметрларини, шунингдек, курсиаравача ҳаракатланиш маконини қисқартирмаслиги керак. Пиёдалар йўлкаси сатҳидан пастки қисми 0,7-2,1 м баландликда бўлган объектлар вертикал конструкциясининг юзасидан 10 см дан, алоҳида таянчларга қурилган бўлса, 30 см дан ортиқ туртиб чиқмаслиги керак. Туртиб чиқадиган элементларнинг ҳажми ортиқ бўлганда, бу объектлар остидаги бўшлиқларни баландлиги 5 см бўлган бордюр тошлари билан чегаралаш ёки 0,7 м баландликдаги тўсиқларни қўллаш керак. Жароҳатлардан ҳимояни таъминлаш учун осма қурилмалар ясси бўлиши керак.'),
        (668, 65, null, null, '3.22. Кўриш қобилияти заиф одамлар учун таксофонлар ва бошқа махсус қурилмалар горизонтал юзага рельефли қопламаларни қўллаб ўрнатилиши керак.'),
        (669, 65, null, null, '3.23. Жамоат бинолари ва иншоотлари, савдо ва кўргазма комплекслари ва бошқа кириш зоналарини сигнализация ва навбатчи ходимларни чақириш қурилмалари, назорат-қўриқлаш ускуналари билан жиҳозлаш тавсия этилади.'),
        (670, 66, null, null, '3.24. Жамоат транспорти бекатларида курси-аравачалар учун тротуар тарафдан ва кўчанинг қатнов тарафидан кириш йўлкаларини лойиҳалаштирили-ши керак. Жамоат транспорти бекатларининг майдончаси 1-2 та рақамли жамоат транспорти тўхтайдиган бўлса, кенглиги 1,8 м, узунлиги 12 м дан кам бўлмаслиги керак, 3-4 та рақамли жамоат транспорти тўхтайдиган бўлса, кенглиги 2,4 м, узунлиги 24 м дан кам бўлмаслиги керак. Транспорт бекати майдончасининг баландлиги кўчанинг қатнов қисмидан 0,20 м дан ошмаслиги керак.'),
        (671, 66, null, null, '3.25. Ногиронлар ва камҳаракатланувчи фуқароларни ташувчи махсус транспортлар учун бекатлар ҳамда ногиронларнинг шахсий автотранспорти учун жойлар бино ёки иншоотнинг кириш қисмига яқин, қоидага кўра, 100 м дан узоқ бўлмаган масофада мўлжалланиши керак.'),
        (672, 66, null, null, '3.26. Тураржойларда ногиронлар автомашиналарини қўйиш учун майдончалар уй-жойнинг кириш қисмидан 100 м дан узоқ бўлмаслиги керак (50-25 м бўлиши тавсия этилади). Ногирон автомобилини қўядиган майдончада махсус белгилар билан белгиланиши керак. Тураржойлардаги автомобиль тўхташ жойларидаги ўринларнинг 2,5 - 3% ногиронлар автомашиналари учун мўлжалланиши керак. Ногиронлар транспорт воситаларини сақлаш учун, гаражлар тураржойдан 200 м дан узоқ бўлмаслиги керак'),
        (673, 66, null, null, '3.27. Жамоат бинолари ва иншоотлари олдидаги очиқ индивидуал автомобиль тўхташ жойларидаги ўринларнинг 10% ногиронлар автомашинаси учун мўлжалланиши керак. Автомобиль тўхташ жойидаги мазкур ўринлар халқаро амалиётда қабул қилинган махсус белгилар билан белгиланиши керак. Ногиронларга хизмат кўрсатувчи махсус муассасалар олдидаги автомобиль тўхташ жойларидаги махсус ўринларнинг миқдори 25% дан кам бўлмаслиги керак.'),
        (674, 67, null, null, '3.28. Хизмат кўрсатувчи махсус муассасаларнинг ер участкалари майдони тавсия қилинган 3-иловага асосланиб мўлжалланиши керак. Ер участкасида эксплуатация учун нормал шароитни таъминлайдиган барча бинолар ва зарур иншоотлар жойланиши керак. Участканинг зоналаштирилишини таъминлаш керак ва қуйидаги зоналарга ажратиш керак: - кўкаламзорлаштирилган (гулзор), маълумот тахталари жойлаштирилган кириш зонаси; - тинч дам олиш ва сайр қилиш зонаси (ёритиш ва ободонлаштириш элементлари билан); - жисмоний тарбия-спорт иншоотлари рекреацион зонаси; - боғ-томорқа зонаси (ҳаваскорлик машғулотлари ва меҳнат терапияси учун); - хўжалик ва техник иншоотлар, ҳовли ҳожатхоналари ва ахлат контейнерлари жойлашган хўжалик зонаси. Махсус ўқув-тарбия муассасалари ер участкаларини ташкил этиш лойиҳалашга қўйилган махсус талабларни ҳисобга олган ҳолда амалга оширилади. Ўқув-тарбия муассасалари таркибида турли ёшдаги болалар учун ўйин майдончалари, жисмоний тарбия машғулотлари ва реабилитацияси учун жисмоний тарбия-спорт ва ўқув майдончалари назарда тутилиши керак.'),
        (676, 67, null, null, '3.30. Участкалар тўсиқларга эга бўлиши керак. Тўсиқнинг тури, баландлиги лойиҳалашга берилган топшириқда белгиланади. Ақлий ривожланиши заиф болалар ўқув-тарбия муассаси, неврология ихтисосидаги интернатлар учун тўсиқларнинг баландлиги 1,7 - 2,0 м атрофида бўлиши керак. Участкаларда ҳаракатланиш йўллари, пиёдалар йўлкалари, зинапоялар, пандуслар мазкур ҳужжат талабларига мувофиқ назарда тутилиши керак. Қариялар ва ногиронларга хизмат кўрсатиш махсус муассасалари участкаларини ободонлаштириш ва жиҳозлаш лойиҳалашга берилган топшириқдаги технологик талабларни ҳисобга олиб назарда тутилиши керак. Турмуш шароити қулай муҳитни яратиш, қоидага кўра, ободонлаштиришнинг юқори савияси ва жиҳозлар билан таъминланганлиги билан белгиланади.'),
        (677, 67, null, null, '3.31. Ногирон ва қарияларга хизмат кўрсатувчи махсус муассасалар (ШНҚ 2.07.01га мувофиқ) транспорт магистраллари, экологик зарарли ишлаб чиқариш, электромагнит нурланиш нуқталари, аэропорт ва б. дан меъёрий узоқликда, экология жиҳатдан имкон қадар қулай зоналарда жойлаштирилиши керак. Қариялар ва ногиронларга хизмат кўрсатиш муассасалари шаҳар яқинида, қишлоқ жойларда жойлаштирилганда ёнғинни ўчириш депосигача бўлган масофа 3 км дан узоқ бўлмаслиги таъминланиши керак.'),
        (678, 68, null, null, '4.1. Бино ва иншоотларнинг лойиҳалари ШНҚ 2.01.02, ШНҚ 2.08.02 ва мазкур ҳужжат талабларига мувофиқ, ногиронлар ва камҳаракатли фуқароларнинг ҳавфсизлигини таъминлаши керак. Тураржой корпуслари (блоклари)ни лойиҳалашда қўшимча тарзда ШНҚ 2.08.01 талабларини ҳам ҳисобга олиш керак. Лойиҳалашда бинолар ва иншоотларнинг ёнғин ҳавфсизлиги бўйича таснифланишини ва ШНҚ 2.01.02 нинг тегишли талабларини ҳисобга олиш керак. Ёнгин ҳавфсизлигини таъминлашнинг қатъий талабларини ўз ичига олган Ф 1.1. синфига мактабгача бўлган, шунингдек, ногирон болалар учун муассасалар, ногирон болалар учун мактаб-интернатларнинг ўқув-тураржой блоклари (секциялар), ногиронлар ва психоневрологик касаллар учун уй-интернатлар, ҳамма турдаги даволаш стационарлари, ногиронлар ва қариялар учун реабилитация ва ҳудудий ижтимоий марказларнинг тураржой блоклари киради. Мазкур бино ёки бино блоклари 2 қаватдан баланд бўлмаслиги, оловбардошлиги II даражадан паст бўлмаслиги керак.'),
        (679, 68, null, null, '4.2. Бино ва иншоотларнинг қаватларидан эвакуация чиқиш жойлари сони, зинапоялар тури ШНҚ 2.01.02, ШНҚ 2.08.01 ва ШНҚ 2.08.02 талабларини ҳисобга олиб танланиши керак'),
        (681, 68, null, null, '4.4. Томоша заллари ва спорт-кўнгилочар иншоотларида ногиронлар учун ўринлар бевосита эвакуация чиқишига олиб борувчи йўлакларга яқин жойлашиши керак; ногиронлар ўрнини жойлаштиришда эвакуация пайтида бошқа томошабинлар эвакуация йўллари билан тўқнашмаслик имконини назарда тутиш тавсия этилади. Томоша, спорт-кўнгилочар залларидаги ногиронлар ўрнидан эвакуация чиқишигача масофа 25 м дан ошмаслиги керак, бунда йўлакнинг кенглиги 1,2 м дан кам бўлмаслиги керак. Курси-аравача йўлининг кенглиги 1,6 м дан кам бўлмаслиги керак. Умумовқатланиш муассасалари залларида ногиронлар учун ўринлар чиқиш жойига яқинлигини ҳисобга олиб жойлаштирилиши керак.'),
        (682, 68, null, null, '4.5. Эвакуация йўлларининг кенглиги (соф ҳолатда) қуйидагилардан кам бўлмаслиги керак, м: - 15 кишигача бўлган хонанинг эшикларидан - 1,2; - 35 кишигача бўлган ўқув хоналари эшикларидан - 0,9; - қолган вазиятларда тирқиш ва эшиклардан, хоналардан ўтиш жойидан - 1,8; - жамоат бинолари йўлакларидан - 1,8; - лоджия ёки балкон орқали ўтадиган йўллардан - 1,5.'),
        (683, 68, null, null, '4.6. Юқори қаватлардан эвакуация йўли бўлиб хизмат қиладиган зинапоя ва пандуслар ташқарига чиқиш эшиклари билан бевосита боғланган бўлиши керак; зинапоя ва пандусларнинг кўрсаткичлари ва қияликлари ШНҚ 2.01.02, ШНҚ 2.08.01 ва ШНҚ 2.08.02 га мувофиқ бўлиши керак'),
        (684, 68, null, null, '4.7. Эвакуация йўллари конструкцияси ёнғиндан хавфсиз КО синфига оид бўлиши керак, уларнинг оловбардошлик даражаси ва пардозлов-оқлаш материаллари ШНҚ 2.01.02 ва бошқа амалдаги меъёрлар талабларига мос бўлиши керак'),
        (685, 68, null, null, '4.8. Ногиронлар ва камҳаракатли фуқаролар эвакуацияси учун ажратилган меъёрий вақтга риоя қилиш мумкин бўлмаган ҳолларда, уларни қутқариш учун биноларда жисмоний имкониятлари чекланган одамлар ёнғинни ўчириш бўлимлари етиб келгунича кутиб турадиган ёнғиндан ҳавфсиз зоналарни назарда тутиш керак. Ногиронлар ва камҳаракатли фуқаролар энг узоқ хоналардан ёнгиндан ҳавфсиз зона эшигигача бўлган масофа эвакуацияга ажратилган вақт ичида етиболадиган даражада бўлиши керак'),
        (686, 68, null, null, '4.9. Ёнғиндан ҳавфсиз зоналарни вертикал коммуникациялар яқинида ёки Н1 туридаги тутун кирмайдиган зинапояга ёҳуд тегишли талабларга мос келиб КО синфига оид тўсувчи конструкцияга эга пандусли хонага чиқиш йўлларини ягона тугун сифатида лойиҳалаш керак. Ёнғиндан ҳавфсиз зона бошқа хоналар ва туташ йўлаклардан ёнгинга қарши тусиқлар билан ажратилган бўлиши керак, бунда оловбардошликнинг даражалари қуйидагича бўлиши керак: деворлар - RЕI 90, тўсиқлар - REI 60, дераза ва эшиклар - ЕI 60 нинг биринчи тури. Ёнғиндан ҳавфсиз зона таркибига ложия ёки балконга туташ майдонча (кенглиги 1,5 м дан кам бўлмаган) ҳам киритилиши мумкин. Ёнғиндан ҳавфсиз зоналарнинг конструкциялари ёнғиндан ҳавфсиз КО синфига оид бўлиши керак, пардозлов-оқлаш ва қопловчи материаллари эса ШНҚ 2.01.01 талабларига мос келиши керак. Ёнғиндан ҳавфсиз зоналарнинг эшиклари ўз-ўзидан зич ёпилувчи, ёнғиндан ҳавфсиз бўлиши керак. Ёнғиндан ҳавфсиз зонага тутун кирмайдиган бўлиши керак. Ёнғин пайтида битта эвакуация эшиги очиқ бўлганида ортиқча 20 Па босим бўлиши таъминланиши керак. Ёнғиндан ҳавфсиз зоналарга чиқиш имконияти бўлган лифт шахталарида КМК 2.04.05 талабларини ҳисобга олиб, ҳавонинг кириш мосламалари ўрнатилиши керак'),
        (687, 68, null, null, '4.10 Ёнгиндан ҳавфсиз зонанинг майдони қаватдаги ногиронлар сони ва бир ногиронга 2,5 - 2,65 м2 (курси-аравачанинг бурила олиши ва уни кузатувчи шахснинг сиғишини ҳам) ҳисобга олган ҳолда қабул қилиниши керак.'),
        (688, 68, null, null, '4.11. Махсус бинолардаги девор ва шифтлар НГ гуруҳига кирувчи ёнмайдиган пардозлов-оқлаш материалларидан бўлиши керак, суст ёнувчи Г1 материалларига ҳам йўл берилади. Суриладиган тўсиқлар икки тарафидан НГ гуруҳига оид ёнмайдиган материаллар билан ҳимояланган бўлиши керак. Тутун чиқариб осон ёниб кетувчи, ҳавфли заҳар чиқарувчи пол қопламаларини қўллашга йўл қўйилмайди. Ўзбекистон Республикаси Соғлиқни сақлаш вазирлиги рухсат этган материаллар қўлланилиши керак.'),
        (697, 69, null, null, '4.20. Эшиклар, зинапоя ва пандусларга чиқиш жойларида, шунингдек, коммуникация йўлларининг бурилишларидан олдинги участкаларнинг поллари 0,6 м масофада ажралиб турувчи рельефли бўйоқли юзага эга бўлиши керак. Шунингдек, зинапоялар, пандуслар ва йўналишнинг ўзгаришини огоҳлантирувчи ёнувчи кўрсаткичлар (ёнувчи чироқлар) ва товуш сигналларини назарда тутиш керак.'),
        (705, 70, null, null, '4.28. Курси-аравачадаги ногиронларни кўтариш учун лифтларнинг кабиналари (соф ҳолатда) қуйидаги кўрсаткичларга эга бўлиши керак: кенглиги 1,1 м, чуқурлиги 1,4 м; эшик кенглиги 0,9 м дан кам бўлмаслиги керак. Лифтлар чироқ ва товуш сигнализациялари, автоматлаштирилган бошқарув тизимлари ва тутунга қарши ҳимоя билан жиҳозланган бўлиши керак. Ертўла ва цокол қаватдаги лифт эшиклари олдида тамбур-шлюз мосламаси зарур.'),
        (689, 69, null, null, '4.12. Бино ва иншоотларга кириш (ер сатҳидан, шунингдек, ногиронлар учун имкони бўлган ҳар бир ўтиш, шунингдек, ерости ўтиш жойлари) курсиаравачадаги ногиронларга етиш ва уларни эвакуация қилиш учун пандуслар билан жиҳозланиши керак. Кириш зоналарида ташқи зинапоя ва пандусларни 3.11. ва 3.12 бандлар-даги талабларга мувофиқ лойиҳалаш керак. Зинапоя ва пандуслар тутқичлар билан жиҳозланиши керак. Пандуслар барпо этилишидаги қийинчиликлар (меъёрий қияликлар кўпроқ) бўлганида, цокол қаватнинг нисбатан баланд бўлганида курси-аравачадаги ногиронлар учун махсус кўтарувчи мосламалар назарда тутилиши мумкин. Кириш зонасидаги кенглиги 2,5 м ва ундан ортиқ бўлган очиқ зинапоялар қўшимча ажратувчи тутқичларга эга бўлиши керак. Бинонинг кириш зонаси 1,6 м дан кам бўлмаган кириш майдончаси ва ёғингарчиликдан ҳимоя қилувчи айвончани ўз ичига олиши керак. Кириш зонаси сигнализация ва ходимларни чақириш мосламалари билан жиҳозланиши керак. Кириш зоналаридаги қопламалар юзаси қаттиқ, ҳўл бўлмайдиган ва сирпанчиқ бўлмаслиги керак; кўндаланг қиялиғи 1-2% атрофида бўлиши керак.'),
        (690, 69, null, null, '4.13. Тамбурлар 1,8 м дан кам бўлмаган «чуқурлик»да, кенглиги 2,2 м дан кам бўлмаслиги керак. Кириш майдончалари ёки тамбурлар полидаги дренаж ва сув йиғувчи панжаралар пол қопламаси остидан ўрнатилиши керак; панжаралар 1,5 см дан ортиқ бўлмаган майда тешикли бўлиши керак.'),
        (1243, 121, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '29. Ushbu Reglament talablari buzilishida aybdor bo‘lgan shaxslar qonunchilik hujjatlarida belgilangan tartibda javobgar bo‘ladi.'),
        (692, 69, null, null, '4.15 Жамоат бинолари ва иншоотларининг ўтиш жойлари, йўлаклари ва галереялари одамларни бинодан тезда эвакуация қилиш талабларига, шунингдек секин ҳаракат қилувчи ногиронлар ва камҳаракатланувчи (қўлтиқтаёқли, таянч билан, курси-аравачадаги) фуқароларнинг қулай ҳаракатланиши учун талабларга мос келиши керак. Йўлаклар ва ўтиш жойларининг кенглиги 4.5- бандга мувофиқ қабул қилинади). Ҳаракатланиш йўлида учрайдигаи тепаликдан қияликларда пандуслар (4.23-бандга мувофиқ) барпо этилиши керак. Рекреация йўлаклари, кутиш жойлари бўлган йўлаклар, кулуарлар ва ўтиш жойларининг кенглиги 2,4 м дан кам бўлмаслиги керак (ШНҚ 2.08.02 га мувофиқ).'),
        (693, 69, null, null, '4.16. Ҳаракатланиш йўлларида қалинлиги 1,3 см дан ортиқ бўлган тукли гиламларнинг қўлланилишига йўл қўйилмайди. Ҳаракатланиш йўлларидаги гилам қопламалари, айниқса, чокли жойлар, турли қопламалар тўқнашган жойларда, пишиқ мустаҳкамланган бўлиши керак.'),
        (694, 69, null, null, '4.17.Ўқув кабинетларидаги кўргазма тахтаси ва ўқув столлари олдинги қаторидаги ўтиш жойларининг оралиғи қоидага кўра, 1,8м дан кам бўлмаслиги керак. Кўргазма столи бўлган лабораториялар. ва катта аудиторияларда кўргазма тахтаси ва ўқув столларининг олдинги қатори орасидаги масофа 2,55 м дан кам бўлмаслиги керак. Жиҳозлари ва мебеллари бўлган маъмурий хонага ўтиш жойининг кенглиги қоидага кўра, 1,5 м дан кам бўлмаслиги, иккинчи даражали ўтиш жойларининг кенглиги 1,2 м дан кам бўлмаслиги керак:'),
        (695, 69, null, null, '4.18. Курси-аравачадаги ногиронларнинг мустақил равишда 90 - 180° га бурила олиши учун зонанинг диаметри 1,4 м дан кам бўлмаслиги керак. Хизмат кўрсатиш жойларидаги столлар, пештахталар, деворий асбоблар ва мосламалар атрофидаги бўш жойлар 0,9x1,5 м дан кам бўлмаслиги керак. Курси-аравачадаги ногиронлар эшикни «ўзидан ичкарига» бемалол очиши учун «ичкари» 1,2 м дан кам бўлмаслиги, «ичкаридан ўзига» очганида эса 1,5 м дан кам бўлмаслиги керак (жойнинг кенглиги 1,5 м дан кам бўдмагнида).'),
        (696, 69, null, null, '4.19. Эшиклар ва девордаги тирқишлар шунингдек, хонадан ва йўлаклардан зинапояга чиқиш йўлакларининг кенглиги 0,9м дан кам бўлмаслиги керак. Очиқ тирқиш кесакисининг кенглиги 1 м дан ошиб кетганида, тирқишнинг кенглигини коммуникация тирқиши бўйича ҳисобланади, лекин 1,2 м дан кам бўлмаслиги керак. Эшикларнинг пастки кесакиси - остоналар бўлмаслиги керак.'),
        (750, 77, null, null, '6.14. Кўриш қобилияти заиф ногиронлар иш жойида 500-1500 ЛК гача маҳаллий ва умумий ёритишнинг комбинациялашган тизимини қўллаш керак (бажариладиган ишнинг турига қараб). Умумий ёритишнинг даражаси қоидага кўра, 500 ЛК дан кам бўлмаслиги керак'),
        (698, 69, null, null, '4.21. Бинолар ичидаги зинапоялар ва пандуслар ШНҚ 2.01.02 ва ШНҚ 2.08.02 талабларига мувофиқ бўлиши керак. Зинапоялар маршининг кенглиги 1,35 м дан кам бўлмаслиги керак. Кенглиги 2,5м дан ортиқ зинапоялар ажратувчи тутқичларга эга бўлиши керак. Бир марш миқёсидаги зиначалар шакли (геометрияси), кенглиги ва баландлиги бир хил бўлиши керак. Очиқ зинапоялардаги биринчи марш зиначаларининг режадаги кўриниши зиначаларнинг меъёрдаги кенглигини сақлаган ҳолда ўзгартирилишига йўл қўйилади. Зинапоя зиначаларининг кенглиги 0,3 м дан кам бўлмаслиги, зиначаларнинг баландлиги эса 0,15 м дан баланд бўлмаслиги керак. Зинапоянинг қиялиги 1:2 дан ошмаслиги керак. Зинапояларнинг зиначалари яхлит, текис, ғадир-будир қопламали бўлиши керак. Зиначаларнинг қирралари 5 м радиусдан кам бўлмаган яссиликда бўли-ши керак. Зинапоянинг деворга туташмаган четки тарафи 0,15 - 0,20 см баландликдаги бортларга эга бўлиши керак'),
        (699, 69, null, null, '4.22. 0,45м дан баданд бўлган зинапоялар, пандуслар, қияликларда икки тарафдан тутқичли тўсиқлар ўрнатилиши керак. Пандусларнинг тутқичи 0,7 - 0,9 м баландликда, зинапояларнинг тутқичи 0,9 м баландлиқда (мактабгача муассасаларда 0,5 м баландликда) бўлиши назарда тутилади. Тутқичларнинг бандлари зинапоянинг ички тарафидан бутун узунлик бўйлаб яхлит бўлиши керак. Тутқичнинг охирги қисми марш ёки пандус қиялигидан 0,3 м га узунроқ бўлиши керак. Маршнинг ташқи тарафи, тутқичларнинг юқори ёки ён тарафларининг юзасига қаватларнинг рельефли белгилари кўрсатилиши назарда тутилади (рақамлар қоидага кўра, 1,5x1,0 см, рельефнинг баландлиги 0,2 см дан кам бўлмаслиги керак).'),
        (700, 69, null, null, '4.23. 8% дан ошмаган қияликда бир пандус маршининг максимал баландлиги 0,8 м дан ошмаслиги керак. Ҳаракатланиш йўлларидаги поллар баландлигининг тушиши 0,2 м ва ундан камроқ бўлганида пандуснинг қиялиги 10% га оширилишига йўл қўйилади. Винтли пандусларнинг қўлланилишига фавқулоддаги ҳолатлардагина йўл қўйилади. Пандуснинг кенглиги 1,0 м дан кам бўлмаслиги керак (истисно ҳолатларда 0,9 м га йўл қўйилади). Тўғри ҳаракатланиш йўллари ёки бурилишлардаги горизонтал майдончалар 1,5 м дан кам бўлмаслиги керак. Хасса ёки оёқ сирпаниб кетишининг олдини олиш учун пандус маршларининг четлари, шунингдек, 0,45 м дан ортиқ баландлик қиялигида горизонтал юзанинг четларида 15-20 см баландликдаги бортларни назарда тутиш керак. Пандусни кўтариб турувчи конструкциялар оловбардошлиги R 60 дан кам бўлмаган НГ гуруҳига кирувчи ёнмас материаллардан ишланиши керак; пандусларнинг тўсиқ конструкциялари оловбардошлилиги R 120 дан кам бўлмаслиги керак.'),
        (701, 69, null, null, '4.24. Ногиронлар ва камҳаракатли фуқароларнинг ҳаракатланиш йўлларида турникет ва айланувчи эшикларни қўллашга йўл қўйилмайди.«Очиқ» ва «ёпик» ҳолатини фиксация қиладиган ва бир тарафга очиладиган илмоқли эшиклар, шунингдек, автоматик ёпиладиган (5с дан ортиқ вақт ичидан ёпиладиган) эшикларни қўллаш керак.'),
        (702, 69, null, null, '4.25. Кўринмас ойнаванд эшик ва тўсиқлар синмайдиган материаллардан қилиниши керак. Эшикларнинг кўринмас ойнаванд юзасида баландлиги 10 см дан кам бўлмаган, кенглиги 20 см дан кам бўлмаган маркировка назарда тутилади, бунда ҳаракатланиш йўли сатҳидан 1,2м дан кам бўлмаслиги, 1,5 м дан ошмаслиги керак.'),
        (703, 69, null, null, '4.26. Ногиронлар кирадиган ташқи эшикларнинг юзаси шаффоф, синмайдиган материалдан ишланган кўриш панелларини ўрнатилишини назарда тутиш керак. Эшик юзасининг пастки қисми полдан 0,3 м баландликда урилишларга қарши полоса билан ҳимояланиши керак.'),
        (704, 70, null, null, '4.27.Бинолар ва иншоотларда лифтлар ўрнатилиши ШНҚ 2.08.01 ва ШНҚ 2.08.02 ва лифт холлари кўрсаткичларига бўлган талабларга мувофиқ бўлиши керак. Курси-аравачадаги ногиронлар икки ва ундан юқори қаватлардаги зал, хоналар, шунингдек, меҳнат билан банд бўлган ишчи жойларига етиб олишларини таъминлаш учун лифтлар ва кўтариш мосламаларини назарда тутиши керак. Курси-аравачани бинонинг қаватларига кўтариш воситаларини танлаш лойиҳалашга берилган топшириқда кўрсатилади (ЎзНЖ органлари билан келишилган ҳолда). Лифт ва кўтариш мосламаларидан ногиронлар жойлашган хоналаргача масофа 50 м дан кўп бўлмаслиги керак. Бинода лифт бўлмаганида курси-аравачадаги ногиронлар учун кўтариш мосламалари (платформалар) ўрнатилиши назарда тутилиши керак. Кўтариш мосламаларидан чиқиш жойи ногиронлар ташриф буюрадиган ва яшайдиган хоналари жойлашган қаватлар сатҳи билан баробар бўлиши керак.'),
        (706, 71, null, null, '4.29. Жамоат биноларининг курси-аравачадаги ногиронларга хизмат кўрсатиш махсус зоналари қоидага кўра, мазкур бино умумий сиғимининг 5% ёки ташриф буюрувчилар умумий сонининг 5% ҳисобида мўлжалланиши керак (лойиҳалашга берилган топшириқда кўрсаткичлар аниқ берилади). Бино ёки иншоотнинг ҳар бир қаватида ногиронлар учун қулай жойда хизмат кўрсатиш ёки хоналарга қандай ўтиш ҳақида маълумотлар зонаси, шунингдек, 2-3 кишилик дам олиш зонаси ва бир ёки :икки курси-аравача учун туриш жойи назарда тутилиши керак. Аудитория, ўқув хоналари, томоша заллари, кутубхоналар, ошхона-ресторан ва кафе залларида курси-аравачадаги ногиронлар учун ўринлар, хона ва бинодан тез ва қулай эвакуацияни ҳисобга олиб жойлаштирилиши керак, бу нарса, қоидага кўра, бино қаватлари ва заллардан камида иккита эвакуация чиқишига етиб боришни таъминлайди. Киши сиғими 35 кишидан кўп бўлмаган ўқув хоналари (лабораториялар, синфлар, кабинетлар), кутубхона залларида ногиронларнинг ўрни чиқиш жойига яқин биринчи қаторларда жойлаштирилиши керак, иккинчи чиқиш жойи бўлмаслигига йўл қўйилади. 50 ва ундан ортиқ кишилик аудиториялар ва томоша залларида курсиаравачадаги ногиронлар учун ўринлар залдан иккита чиқиш жойига яқинроқ, майдончанинг горизонтал полли қисмида жойлаштирилиши керак. 800 ва ундан ортиқ ўриндиқли залларда курси-аравачадаги ногиронлар тарқоқ ҳолда, ҳар бир эвакуация чиқиш жойида учтадан ортиқ жойлаштирилмаслиги керак. Изоҳ: Ҳар бир аудитория, ўқув хонасида курси-аравачадаги ногиронлар учун ўринлар миқдори 3 тадан кам бўлмаслиги керак'),
        (707, 71, null, null, '4.30. Эстрада ёки саҳна олдида, шунингдек, залнинг охирида эвакуация чиқиш жойларида курси-аравачадаги ногиронларни тўсиқсиз эвакуация қилиш учун бўш ўтиш зоналари ёки майдончаларнинг кенглиги 1,8 м дан кам бўлмаслиги керак. Аудиториялар, амфитеатрлар, томоша залларида ногирон-томошабинлар ўринлари ёки зоналарида ҳимоя буфер тўсиқларини назарда тутиш мумкин'),
        (708, 71, null, null, '4.31. Фиксация этилган ўриндиқлар сиғими 50 кишидан кўп бўлган аудиториялар, маъруза ва томоша залларида эшитиш қобилияти заиф шахслар учун махсус ўринлар (умумий сиғимнинг 4%) товуш манбаидан 10 м узоқ бўлмаган масофада жойлаштирилиши керак ва индивидуал эшитиш тизимлари (асбоблари) билан жиҳозланиши керак. Бу ўринлар эстрада, саҳна, «имо-ишора» тилига ўгирувчи таржимон кафедраси яхши кўриниб турадиган зонада жойлаштирилиши керак.'),
        (709, 71, null, null, '4.32. Музей ва кўргазма залларида курси-аравачадаги ногиронлар экспозициялар бўйлаб тўсиқсиз ҳаракатланиши учун шароит яратилиши керак. Асосий йўлнинг кенглиги, қоидага кўра, 2,0 м.дан кам бўлмаслиги керак, ташриф буюрувчиларнинг оқими кўп бўлган залларда 2,4 м дан кам бўлмаслиги керак. Эвакуация чиқиш жойларининг яқинида ногиронларнинг маълумот олиши ва дам олиши учун махсус зоналарни назарда тутиш зарур (ҳар бирининг бўш майдони 1,5x1,5 м дан кам бўлмаслиги керак).'),
        (710, 71, null, null, '4.33. Рўйхатга олиш муассасалари, банклар, меҳмонхоналар, чипталар сотувчи ва хизматлар тўловини амалга оширувчи кассалар, маъмурий муассасалари, савдо-маиший корхоналарида барьерлар, пештахталар, столлар ва бошқа хизмат кўрсатувчи мосламалар курси-аравачадаги ташриф буюрувчилар учун полдан 0,8 м дан ортиқ бўлмаган баландликда бўлиши керак. Интерьерлар, жиҳозларни танлаш ва жойлаштиришни лойиҳалашда куриаравачадаги ташриф буюрувчилар етишиши қулай параметрларни ҳисобга олиш тавсия этилади, шунингдек: - тўғридан-тўғри яқинлашувда полдан 1,2 м дан ошмаслиги ва 0,4 м дан кам бўлмаслиги; - ташриф буюрувчининг ён томонида жойлашган махсус мосламалар зонаси 1,4 м дан баланд, 0,3 м дан паст бўлмаслиги керак.'),
        (711, 71, null, null, '4.34. Спорт иншоотлари, спорт ва тренажёр заллари, бассейнларда ногиронларнинг жисмоний имкониятларини ҳисобга олган машғулотлар учун шароитлар назарда тутилиши керак. Бассейнларни лойиҳалашда 5.11. банд талаблари назарда тутилиши керак. Спорт заллари ва бошқа спорт иншоотларида ечиниш хоналарида қуйидагилар назарда тутилиши керак: - кийим-кечак, қўлтиқтаёқ ёки протезлар учун 1,3 м дан баланд бўлмаган жавонли индивидуал шкафлар; - ечиниш учун кенглиги 0,7 м дан кам бўлмаган, баландлиги 0,5м, узун-лиги 3 м бўлган ўриндиқлар; - ечиниб-кийиниш учун кабиналар (ўлчамлари1,8x1,8 м дан кам бўлмаган); - курси-аравачаларни вақтинчалик сақлаш учун жойлар (1,5x1,5 м). Кабиналар ва очиқ ечиниш жойлари (ўриндиқлар)га кенглиги 1,6м дан кам бўлмаган ўтиш жойларидан қулай етиб бориш таъминланиши керак. Ечиниш учун индивидуал кабиналар бир сменада шуғулланувчи иккита ногирон учун камида битта ҳисобида бўлиши керак.'),
        (712, 72, null, null, '4.35. Ногиронлар ва қариялар тураржойлари ШНҚ 2.08.01га мувофиқ лойиҳаланиши керак. Жамоат биноларидаги тураржойлар, шунингдек, меҳмонхоналар, дам олиш муассасалари, ногиронлар яшаши учун мўлжалланган санаторийлар, қуйидаги талабларни ҳисобга олиб жойлаштирилиши: - имкон қадар биринчи қаватда бўлиши; - ногироннинг тураржойидан эвакуация чиқиш жойигача бўлган масофа 50 м дан ошмаслиги керак; - бино ёки комплекс таркибидаги ижтимоий хизматлар, ташрифхоналарга етиб боришнинг қулайлигини таъминлаш (ногиронлар талабига жавоб берувчи жиҳозларнинг қўлланилишини ҳисобга олган ҳолда) керак. Тураржойларни лойиҳалашда курси-аравачадаги ногиронларнинг талаблари билан бир қаторда кўриш ва эшитиш қобилияти заиф ногиронларнинг ҳам талаблари ҳисобга олиниб, маълумот сигнализацияларининг турли мосламалари (товушли ёки чироқли)ни қўллаш назарда тутилиши керак. Меҳмонхоналар, мотеллар, кемпинглар, пансионатлар ва санаторийларда, қоидага кўра, ўринларнинг 5-8%да курси-аравачадаги ногиронларнинг жойлаштирилиши назарда тутилиши тавсия этилади (ўринларнинг миқдори лойиҳалашга берилган топшириқнинг аниқ шартлари билан белгиланади).'),
        (751, 77, null, null, '6.15. Ёз даврида шамоллатиш, вентиляция ёрдамида хоналарда қулай микроиқлим яратиш имкони бўлмаганда хоналарда маиший совутгичлар қўлланилиши мумкин.'),
        (713, 72, null, null, '4.36. Курси-аравачадаги бир ногирон яшаши учун тураржой майдони 14 м2 дан кам бўлмаслиги, икки киши, шунингдек, бири ногирон бўлса, 16 м2 дан кам бўлмаслиги, курси-аравачадаги икки ногирон.учун 18-20 м2 бўлиши назарда тутилади. Тураржойнинг кириш қисми курси-аравача қулай кириши ва уни қўйиш (сақлаш) учун кенглиги 1,6 м дан кам бўлмаслиги керак, кириш қисмининг майдончаси 4 м2 дан кам бўлмаслиги керак. Курси-аравачадаги ногирон яшайдиган тураржойдаги аралаш санузел хонаси 2,2x2,2 дан кам бўлмаслиги керак.'),
        (714, 72, null, null, '4.37. Ногиронлар ва қариялар учун уй-интернатлардаги тураржой ва ёрдамчи хоналар ШНҚ 2.08.01 га мувофиқ лойиҳалаш керак.'),
        (715, 73, null, null, '4.38. Курси-аравачадаги ногироннинг индивидуал-меҳнат фаолияти ва машғулотлар учун хона ёки иш кабинети майдони 12м2 дан кам бўлмаслиги керак 14-16 м2 бўлиши тавсия этилади.'),
        (724, 74, null, null, '4.47. Душ билан бирга жойлашган санузел 2,2х2,2м дан кам бўлмаслиги керак. Индивидуал шкафлар (полдан 1,3м дан ортиқ бўлмаган баландликдаги жавонли) ногиронлар қулай етиб борадиган қилиб жойланиши керак, шкаф олдида курси-аравача бемалол ҳаракатланиши учун 1,5х1,5м ли зона назарда тутилиши керак. Шкафлар ҳажми 0,4x0,5 м дан кам бўлмаслиги керак. Кийимлар илмоқларда очиқ сақланадиган шароитда улар полдан 1,2 - 1,3 м баландликда бўлиши керак. Ечиниш ва кийим ечиш хоналарида ювингич ва оёқ ванналарини жойлаштириш тавсия этилади.'),
        (1294, 123, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '24. Ko‘chirma qurilish-montaj (rekonstruksiya) ishlarini boshlash uchun obyektni ro‘yxatdan o‘tkazgan kunning o‘zida “Shaffof qurilish” milliy axborot tizimida avtomatik ravishda hisobga olib boriladi.'),
        (716, 73, null, null, '4.39. Аҳолини ижтимоий ҳимоялаш ва меҳнат бандлиги бўйича давлат дастурларига мувофиқ маъмурий муассасалар ва корхоналарда ногиронлар учун иш жойлари назарда тутилиши керак. Ногиронлар учун иш жойларининг миқдори, уларнинг умумий ёки махсус хоналарда, алоҳида участкаларда жойлашуви ва хизмат қўрсатиш шароитлари лойиҳалашга берилган ЎзНЖ органлари билан келишилган топшириқда назарда тутилади. Хоналар ва жиҳозлар саломатлик учун ҳавфсиз ва иш фаолиятини самарали ташкил этиш талабларига, фавқулоддаги ҳолатларда эвакуация қилиш талабларига мувофиқ келиши керак. Эшитиш, кўриш қобилияти заиф, таянч-ҳаракат аппарати касалликлари билан хасталанган ногиронлар (курси-аравачадаги ногиронлар) ишлаши учун технологик топшириқ шароитлари ҳисобга олинган махсус жиҳозлар назарда тутилади.'),
        (717, 73, null, null, '4.40. Маъмурий ва ишлаб чиқариш жойларида иш майдони ускуналарнинг жойлашуви ва ўтиш жойларининг меъёрий параметрларини ҳисобга олган ҳолда лойиҳалашга берилган топшириқда белгиланади.'),
        (718, 73, null, null, '4.41. Ногиронларнинг иш жойидан ҳожатхона, ювиниш хонаси, душхоналар, сув ичиш мосламалари, қисқа вақтли дам олиш хоналаригача бўлган масофа, қоидага кўра, 50м дан ошмаслиги керак. Қўл ювиш мосламаси бевосита ишлаб чиқариш хоналарида бўлиши тавсия этилади. Корхона участкаси (ҳудуди)да иш жойлари ва санблокларпинг тарқоқ жойлашувида юқорида қайд этилган масофа 75-100 м бўлиши мумкин.'),
        (719, 73, null, null, '4.42. Ишловчи ногиронларга санитар-маиший хизмат кўрсатиш ШНҚ 2.08.02 ва КМК 2.08.04 ва мазкур меъёрий ҳужжат, корхоналарда меҳнат қиладиганлар учун эса КМК 2.09.04 талабларини ҳисобга олиб таъминланиши керак. Курси-аравачадаги ногиронларнинг дам олиш жойлари ва ошхона залларига етиб боришларида қийинчиликлар бўлганида иш жойларининг яқинида ҳар бир ногирон учун 2,5 м2 (лекин 14 м2 кам бўлмаган) дам олиш ва овқатланиш хонасини назарда тутиш керак.'),
        (720, 74, null, null, '4.43. Жамоат ҳожатхоналарида, шунингдек, жамоат бинолари ва иншоотлари ичида жойлашган ҳожатхоналарда, курси-аравачадаги ногиронлар учун махсус кабиналар назарда тутилиши керак; кабинага кириш кенглиги 1,5м дан кам бўлмаслиги керак, кабина олдида курси-аравача бурилиши учун диаметри 1,4 м дан кам бўлмаган бўш жой назарда тутилиши керак. Курси-аравачадаги ногиронлар ва кўриш қобилияти заиф ногиронлар ишлайдиган биноларда ногиронлар учун ҳожатхона кабиналари ҳар бир қаватда иш жойидан 50 м дан кам узоқликда бўлиши керак. Ногиронлар учун ҳожатхона ёки кабина эшиклари кенглиги 90 см дан кам бўлмаслиги ва ташқарига очилиши керак. Бунда ногирон эвакуация йўлига тўсиқларсиз чиқиши таъминланиши керак'),
        (721, 74, null, null, '4.44. Курси-аравачадаги ногирон учун ҳожатхона кабинаси қуйидагилардан кам бўлмаслиги керак: кенглиги - 1,65 м, ичкариси - 1,8 м. Кабинада унитаз билан ёнма-ён курси-аравача учун жой, штанга ва тутқичлар конструкциясини назарда тутиш керак. Ювингич жойлашган ҳожатхона 1,65x2,2 м дан.кам бўлмаслиги керак.'),
        (722, 74, null, null, '4.45. Санитар-гигиеник хоналарда ногиронлар учун махсус жиҳозланган кабина ва санитар ускуналар курси-аравачадаги 5 та ногиронга 1 та унитаз ва 1та ювингич ҳисобида назарда тутилиши керак. Жамоат душхоналари таркибида курси-аравачадаги ногирон учун камида битта махсус кабина назарда тутилиши керак. Кабина олдида курси-аравача кира олиши ва бурила олиши учун зона еки майдонча назарда тутилиши керак (айланиш диаметри 1,4 м). Душ кабинасига бевосита кийим ечиш хонасидан кирилиши керак (эшик ташқарига - кийим ечиш жойихонага очилиши керак).'),
        (723, 74, null, null, '4.46. Санитар-маиший хоналарда ногиронлар фойдаланадиган кабиналарнинг геометрик кўрсаткичлари қуйидагилардан кам бўлмаслиги керак (м): - ёпиқ душ кабиналари, ечиниш кабиналари - 1,8 х 1,8; - ўзаро очиқ ўтиш жойли душ кабиналари - 1,2 x 0,9; - ҳожатхона кабиналари - 1,8x1,65; - аёллар шахсий гигиена хонаси - 1,8x2,6; - кийим ечиш хонасида (ўриндиқда) ечиниш жойи - 0,6x1,5. Кабина қаторлари орасидаги бўш жойларнинг кенглиги 1,8 м бўлиши керак.'),
        (725, 74, null, null, '4.49. Қаторлар орасидаги ўтиш жойи қуйидагилардан кам бўлмаслиги керак, м: - ҳожатхона ва очиқ ёки ёпиқ душхоналар - 1,8; - писсуар ва ювингич - 2,4; - ечиниш учун ўриндиқли кийим ечиш жойи ва шкафлар - 1,8'),
        (726, 75, null, null, '5.1. Ногиронлар ва қариялар учун уй-интернатларнинг киши сиғими ижтимоий таъминот органлари топшириғига асосан, қоидага кўра, 50 дан 200-300 ўрингача (ижтимоий, иқтисодий ва технологик асосларни ҳисобга олган ҳолда) белгиланади. Уй-интернат бинолари лойиҳалашда мазкур меъёрий ҳужжат билан бир қаторда ШНҚ 2.08.01 ва бошқа амалдаги -меъёрлардан фойдаланиш зарур. Уй-интернатларни лойиҳалашда қуйидаги хона гуруҳлари назарда тутилади: - тураржой учун (шунингдек, доимий назорат ва парваришга муҳтож бўлмаган шахслар учун; доимий парвариш ва медицина назоратига муҳтож шахслар учун доимий парвариш ва медицина назоратида бўлган ётиб қолган шахс-лар учун); - медицина хизмати ва даволаш-тиклаш, реабилитация-соғломлаштириш учун.'),
        (727, 75, null, null, '5.2. Тураржой бўлимларининг таркиби ва майдони - яшаш хоналари, санузеллар, маиший хоналар, ва бошқа ёрдамчи хоналар ШНҚ 2.08.01 га мувофиқ бўлиши керак. Турли мақсаддаги жамоат хоналарининг (медицина, маданий-маиший, маъмурий) майдони тавсия этилаётган 50-300 ўринли уй-интернатларнинг таркиби ШНҚ 2.08.01 да келтирилган кўрсаткичларни ҳисобга олган ҳолда лойиҳалашга берилган топшириқда кўрсатилади. Бунда маданий-маиший хизмат кўрсатиш ва меҳнат фаолиятини ўз ичига олган инсон фаолияти зоналарида мазкур лойиҳалаш меъёрларида келтирилган талаблар ҳисобга олиниши керак. Ётиб қолган беморларни назорат қилиш хоналари, медицина ва маиший хизмат кўрсатиш хоналари, озиқ-овқат блоклари, ошхоналар биринчи қаватда жойлашиши керак. Ошхона блоклари асосий бинодан алоҳида жойлаштирилишига йўл қўйилади; уларга ёпиқ ўтиш жойлари (шунингдек, иситиладиган)ни барпо этиш зарурати лойиҳалашга берилган топшириқда белгиланади. Ошхона заллари икки сменали хизмат кўрсатишга мўлжалланиши мумкин. Ошхона залининг майдони курси-аравачадаги ногиронлар қулай жойлашиши учун 1 ўринга 1,6 м дан кам бўлмаслиги керак. Озиқ-овқат блоки технологик ҳисоб-китобларга кўра, тайёрланадиган таомлар ҳажмига қараб ишлаб чиқариш ва ёрдамчи хоналарни ўз ичига олиши керак.'),
        (728, 75, null, null, '5.3. Ногирон болалар учун махсус мактаб-интернатлар жисмоний ва ақлий ривожланиши заиф болалар учун ўқув-тарбия муассасаларига қўйилган махсус меъёрий талабларни ҳисобга олиб лойиҳаланиши керак. Хоналарнинг киши сиғими, таркиби ва майдони лойиҳалашга берилган топшириқда белгиланади. Мактаб-интернатлар, қоидага кўра, жисмоний ва ақлий ривожланишида турли камчиликлари бўлган болаларни ўқитиш ва тарбиялашнинг ўзига хослигини ҳисобга олиб лойиҳалаштирилади; махсус мактаб-интернатларнинг қуйидаги асосий турлари ажратилади: I - заиф кўрувчи ва кўр болалар учун; II - заиф эшитувчи ва нутқий ривожланиши заиф кар болалар учун; III - таянч-ҳаракат аппарати шикастланган болалар учун; IV - ақли заиф болалар (олигофрен) учун. Махсус мактаб-интернатлар ижтимоий-педагогик,талабларга мувофиқ болаларни 7 ёшдан 18 ёшгача умумий таълим мактаб дастурлари (9-йиллик ёки 12 йиллик) буйича ўқитиш ва реабилитацияси учун мўлжалланган; бунда синфларнинг тўлиши 12 ўқувчи, ақли заиф болалар учун мактабларда 16 ўқувчи бўлиши қабул қилинган. Мактаб-интернатларнинг умумий қиши сиғими, қоидага кўра, 300 тарбияланувчидан ортиқ бўлмаслиги керак. Махсус мактаб-интернатлар турли ёшдаги болалар учун ўқув-тураржой секциялари ва умуммактаб хоналари, табиий фанлар лабораториялари, фан кабинетлари, спорт ва тренажёр заллари, меҳнат таълими устахоналари, актив заллари, кутубхоналар, ошхоналар, маъмурий-хужалик, медицина ва реабилитация хоналари асосида шаклланади'),
        (729, 75, null, null, '5.4. Кичик ва ўрта синфлар учун ўқув-тураржой секциялари (блоклари) таркибига тураржой хоналари, 24-32 ўқувчини кўзда тутувчи синф хоналари, рекреацион хоналари ва маиший мақсаддаги хоналар киради; қайд этилган секциялар алоҳида вестибюлли ки-риш жойига эга бўлиши керак. Тураржой ёки ётоқ хоналари майдонлари 12 м2 дан кам бўлмаслиги, қоидага кўра, 18 м2 дан ошмаслиги керак, яъни, 3-4 та тарбияланувчини жойлаштириш учун ҳар бир тарбияланувчига 4-6 м2 бўлиши назарда тутилади. Ётоқ хоналари ёрдамчи ва санитар-маиший хоналарни ўз ичига олувчи турар жой гурухлари таркибида жойлаштирилади. Ҳар бир ўқув-тураржой секциясида меҳмонхона-теле-хол ва ўйин хонаси, тарбиячи хонаси, кийим ечиш жойи-вестибюл, маиший хизмат хонаси, кийимкечак ва инвентар омборлари бўлиши назарда тутилади. 12-16 ўқувчига мўлжалланган ўқув синфлари 40 м майдонга эга бўлиши керак. 8-12 синфларда ўқийдиган катта ёшдаги болалар учун ётоқхоналар (6 м 2 одам ҳисобида), санитар-маиший ва ёрдамчи хоналарни ўз ичига олувчи тураржой секциялари ёки блоклари назарда тутилади; синфлар ва ўйин хоналари назарда тутилмайди (чунки катта ёшдаги болалар машғулотлар учун умумий мактаб кабинетлари, лабораториялар, актив ва спорт заллари ва б. дан фойдаланадилар).'),
        (730, 75, null, null, '5.5. Махсус мактаб-интернатларда умуммактаб ўқув хоналарининг таркиби лойиҳалашга берилган топшириқда реабилитация ва соғломлаштириш методикаси ва ўқув дастурлари назарда тутилади. Табиий фанлар лаборатория хоналари ва фан кабинетлари 50 м2 майдонга эга бўлиши керак, кўр болалар мактаб-интернатида эса 60 м2 . Спорт заллари 1х18м ёки 12x24 м, қўшимча тарзда теренажёр заллари 36-60 м 2 бўлиши назарда тутилади. Актив заллари бир пайтнинг ўзида тарбияланувчиларнинг 60-70% сиғиши назарда тутилиши, майдони, қоидага кўра, 100 м2 дан кам бўлмаслиги керак. Кўр болалар учун мактаб-интернатларда ва таянч-ҳаракат аппарати шикастлан-ган болалар учун мактаб-интернатларда залда битта ўрин учун 1,6 м 2 жой мўлжалланиши керак. Кутубхоналар 40 м2 дан кам бўлмаган қироатхоналарга эга бўлиши керак. Ошхоналарда икки сменали хизмат кўрсатишга йўл қўйилади, залда бир ўринга 1,0 - 1,2 м2 , кўр болалар ва таянч-ҳаракат аппарати шикастланган болалар мактаб-интернатларида 1,6 м 2 дан кам бўлмаган майдон таъминланади. Озиқ -овқат блоки технологик ҳисоб-китобларга кўра, тайёрланадиган таомлар ҳажмига қараб лойиҳаланиши керак (амалдаги меъёрларга асосан).'),
        (731, 76, null, null, '5.6. Ногиронлар ва қарияларга яшаш жойида ижтимоий хизмат кўрсатишга мўлжалланган ҳудудий марказлар ижтимоий таъминот органлари топширигига кўра лойиҳаланади. Ҳудудий марказ таркибига беморларнинг кундузги ташрифлари учун хоналар, шунингдек, вақтинчалик (йўлланмада кўрсатилган муддат оралиғида) тураржой, медицина ва соғломлаштириш хизмати хоналари киради. Умумий киши сиғими (кундузги ташриф хоналари ва йўлланма мудда-тида вақтинчалик тураржой) 50-200 ўрин ҳисобида мўлжалланади. Ҳудудий марказларда, қоидага кўра, қуйидагилар назарда тутилади: - рўйхатхона-маълумотхона жойлашган вестибюл; - вақтинчалик тураржойлар (1-2 кишилик), ташриф буюрувчи беморлар учун кундузги дам олиш палаталари, ёрдамчи хоналар; ҳар бир тураржой гуруҳи 25-30 киши учун назарда тутилади; - медицина хоналари (врач кабинети - 12 м2 , муолажа хонаси - 14 м2 , тиш врачи кабинети - 14 м2 ); - даволаш-соғломлаштириш жисмоний-тарбия учун-хоналар (50-72 м2 ), ечиниш, душ ва ҳожатхоналар; - бўш вақтларни ташкил этиш хоналари (телекўрсатувларни томоша қилиш учун холлар, стол ўйинлари, кутубхона-қироатхона); - меҳнат терапияси ва реабилитация учун устахоналар; - хизмат ва хўжалик-маиший хоналари; - ижтимоий марказ фаолият кўрсатаётган ҳудуддаги ногирон ва қарияларга уйида хизмат кўрсатиш хоналари. Ҳудудий марказ таркибида протезларга буюртма бериш, уларни улчаб кўриш ва тўғрилаб бериш хизматлари (устахоналар) ҳам назарда тутилиши керак. Ҳудудий марказ киши сиғими, таркиби ва майдони ҳисоб-китоби лойиҳалашга берилган топшириқда шаҳарсозлик шароитларига мувофиқ белгиланади. Ҳудудий марказ биноларини лойиҳалашда шунингдек, ШНҚ 2.08.01 ва ШНҚ 2.08.02 талаблари ҳам назарда тутилиши керак. Ногиронларнинг ҳудудий марказ қаватларидаги хоналарга етиб келиши, яшаш фаолияти зоналари (тураржой хоналари, ижтимоий-маданий хизмат, меҳнат реабититацияси хоналари)ни яратиш мазкур меъёрий ҳужжатнинг 4- бўлими талабларига мувофиқ таъминланиши керак'),
        (741, 77, null, null, '6.5. Бино ичида ногиронлар фойдаланадиган эшикларни очиб-ёпиш учун мослама ва ускуналари, горизонтал тутқичлар, автоматларнинг ричаглари, кранлари ва бошқарув кнопкалари, чипта берувчи савдо автоматларининг тирқишлари ва бошқа ускуналарни полдан 1,1 м дан кўп бўлмаган ва 0,85 м дан кам ШНҚ 2.07.02-07 27-бет бўлмаган баландликда ва хона ён деворларидан ёки бошқа вертикал юзадан 0,4 м дан кам масофада ўрнатилиши керак.'),
        (732, 76, null, null, '5.7. Кундузги ташриф учун яшаш хоналарининг майдони 12-20 м2 , ҳар 1 ўринга 6-8 м2 ҳисобида бўлиши, курси-аравачадаги ногиронлар учун бир кишилик хоналар 12-14 м2 , икки кишили хоналар 18-20 м2 бўлиши керак. Кундузги ташриф хоналарида тамбур (шлюз)ларда ювингичлари бўлган эркак ва аёллар ҳожатхоналари назарда тутилади. Санитар мосламалари қуйидаги ҳисобда олинади: 5-7 кишига 1 та унитаз, 7 кишига 1 та ювиниш жоми, 15 кишига 1 душ хонаси. Ҳожатхоналарда курси-аравачадаги ногиронлар учун кабиналар назарда тутилиши керак; кабина 9-иловада берилган чизмалар асосидаги тутқичлар билан жиҳозланиши керак. Ювингичлар жойлашган ҳожатхона 1,65x2,2 м бўлиши керак. Аёллар шахсий гигиена хонаси аёллар санузели таркибида бўлиши керак, кабина 1,8x2,6 м бўлиши керак (жиҳозлар: унитаз, биде, қўл ювиш мосламаси).'),
        (733, 76, null, null, '5.8. Уйда хизмат кўрсатиш бўлими таркибида қуйидагиларни назарда тутиш керак: диспетчер хонаси (10-12м2 ), ювингич жойлашган маҳсулотларни сақлаш хонаси (8м2 ), ифлосланган ва тоза кийимлар сақланадиган хона, 5-10 ходимга мўлжалланган хона (кийим ечиш жойи билан), душлар жойлашган санузел хонаси.'),
        (734, 77, null, null, '5.9. Ногиронларни реабилитация қилиш махсус муассасалари ижтимоий реабилитация дастурларига мувофиқ ташкил этилади; хоналарнинг таркиби ва майдони лойиҳалашга берилган топшириқда белгиланади. Реабилитация муассалари таркибида тураржой хоналари (йўлланмада курсатилган муддат ичида) ва даволаш-соғломлаштириш хоналари, ижтимоийпсихологик ва касбий-меҳнат реабилитация учун хоналарни ўз ичига олиши мумкин. Реабилитация маркази хоналари, жумладан кундузги ташриф учун яшаш, маданий-маиший, даволаш соғломлаштириш хоналарини лойиҳалаш мазкур нормаларнинг 4-бўлими, ШНҚ 2.08.01 ва ШНҚ.2.08.02 талабларига мувофиқ амалга оширилади. Реабилитация марказини физиотерапия, сув ва балчиқли даволаш бўлимлари бўлган санаторий-курорт муассасаси сифатида шакллантирилганда қўшимча тарзда КМК 2.08.03 талабларини ҳам назарда тутиш керак. Реабилитация муассасаларида ҳаммом-сауналар, спорт заллари ва сузиш бассейнларини лойиҳалашда курси-аравачадаги ногиронлар ва кўриши заиф ногиронлар учун махсус жиҳозларни назарда тутиш керак. Ечиниш, душ хоналарига етиб бориш мазкур меъёрий ҳужжат бўйича назарда тутилиши керак.'),
        (735, 77, null, null, '5.11. Бассейнлар амалдаги нормалар бўйича лойиҳаланади. Периметри бўйича ванналар ёпиқ бассейнлар учун 2 м дан, очиқ бассейнлар учун 2,4 м дан кам бўлмаган айланма сув йўлакларига эга бўлиши керак. Старт тумбочкалари 26-бет ШНҚ 2.07.02-07 томонидан сув йўлакчасининг кенглиги 3,6 м дан кам бўлмаслиги керак. Соғломлаштириш машғулотлари учун бассейннинг чуқурлиги саёз жойларда 0,6-0, 8 м дан, чуқур жойларида 1,4-1,6 м гача ўзгарувчан бўлиши керак. Тушириш ва кўтариш (механик) мосламалари лойиҳалашга берилган топшириқда назарда тутилиши керак. Бассейн ванналари деворлари бўйлаб сув сатҳидан 0,1 м баландликда ва сув сатҳидан 0,3 м пастда тутқичлар бўлиши назарда тутилиши керак. Бассейн залининг деворлари бўйлаб 0,9 м баландликда, болалар учун бассейнларда эса 0,6 м баландликда тутқичлар бўлиши керак. Очиқ бассейн ванналари периметри бўйича айланма йўлак полидан 0,4 м баландликда бортларга эга бўлиши, зарурият бўлганида эса айланма сув йўлаклари полидан 0,6 м баландликда қўшимча тўсиқлар бўлиши керак.'),
        (736, 77, null, null, '5.12. Ўқув хоналари ва устахоналарнинг майдонлари гуруҳлар билан тўлиши, жиҳозлар жойлашуви ва ўтиш жойларининг меъёрларига қараб белгиланади; ўқув хонасининг майдони 36 м 2 дан кам бўлмаслиги, устахона майдони бир иш ўрни учун 6 м2 дан, жами 36 м2 кам бўлмаслиги керак. Устахоналарда қўшимча ёрдамчи хоналар (усталар хонаси, асбоб-ускуналар учун хона, қазноқ, санузеллар) назарда тутилиши керак.'),
        (737, 77, null, null, '6.1. Бино ва иншоотларнинг муҳандислик, шунингдек, махсус мақсадларга йўналтирилган ускуналари, лойиҳалашнинг амалдаги меъёрларига мувофиқ яратилиши керак.'),
        (738, 77, null, null, '6.2. Лойиҳаларда қўлланилган асбоб-ускуналар ва приборлар давлат санитария-эпидемиологик хизматлари органларининг сертификатларига эга бўлиши керак.'),
        (739, 77, null, null, '6.3. Ногиронлар ва камҳаракат фуқаролар ташриф буюрадиган бино ва иншоотларда кўп куч талаб этмайдиган ва курси-аравачадаги ногиронлар учун ноқулайликлар яратмайдиган эшикни очиб-ёпадиган мосламалар қўлланилиши керак. Осон бошқариладиган, шунингдек, эшикни автоматик очадиган мослама ва ускуналардан фойдаланиш керак. Сурилувчи эшиклар юзасининг икки тарафидан очиш учун қулай бўлган тутқичлар назарда тутилиши керак.'),
        (740, 77, null, null, '6.4. Ташриф буюрувчилар кириши ман этилган техник ҳоналар (шунингдек, бойлер, венткамера, трансформатор узели ва б.)нинг эшикларига эркин равишда ёки тасодифан кириб қолишни истисно қилувчи тамбалар қўйилиши керак. Бундай хоналар эшикларининг тамбалари сигнализация билан, заиф кўрувчи ва заиф эшитувчи ногиронлар учун огохлантиришнинг тактил белгилари билан жиҳозланиши керак'),
        (1449, 137, '2024-05-29 17:31:43', '2024-05-29 17:31:43', '12. Kengash majlisiga unda ko‘rib chiqilayotgan masalalarning ahamiyatiga ko‘ra davlat va xo‘jalik boshqaruvi organlari, loyiha-qidiruv va boshqa tashkilotlar rahbarlari va mutaxassislari taklif etilishi mumkin.'),
        (743, 77, null, null, '6.7. Ногиронлар ва камҳаракат фуқаролар ташриф буюрадиган жамоат бинолари, ногиронлар учун иш жойлари бўлган ишлаб чиқариш хоналари ёнғин ҳақида хабар берувчи тизимга уланган синхрон товуш ва чироқ сигнализацияси ускуналари билан жиҳозланиши керак. Ёнғин ҳақида хабар берувчи товуш сигнализацияси учун товуш кўтарилиши максимал 5 ДБА бўлган хоналарда 30 с давомида 20 ДБА дан кам бўлмаган товуш даражасини таъминлайдиган мосламаларни қўллаш керак.'),
        (744, 77, null, null, '6.8. Жамоат биноларининг вестибюллари, холлар, фойе ва йўлакларида кўриш қобилияти заиф ташриф буюрувчилар учун телефон-автоматлар ва эшитиш камчилиги бўлган ташриф буюрувчилар учун текстофон каби товушли информаторлар назарда тутилиши керак. Эшитиши заиф бўлган ногиронлар учун табло, яхши кўриниб турувчи кўрсаткичлар, чироқ сигнализацияларида маълумотлар такрорланишини назарда тутиш керак'),
        (745, 77, null, null, '6.9. Хавф-хатар тўғрисида информация воситалари тизими ва сигнализацияси комплекс бўлиб, ногиронлар ва камҳаракатли фуқароларнинг турли тоифаси ташриф буюрадиган хоналарда визуал, товуш, тактил информациялар назарда тутилиши керак. Информация воситалари бино ва комплекс ичида бир хил бўлиб, амалдаги меъёр ва стандартларга жавоб бериши керак.'),
        (746, 77, null, null, '6.10. Ногиронлар бўладиган зона ва хоналар, аввало оммавий жойлар, шунингдек, кириш хоналари ва ҳаракатланиш йўлларида, информация воситалари тизими информациянинг узлуксизлиги, ташриф объекти ёки жойни сўзсиз таниш ва йўналтирилишини таъминлаши керак. Информация воситалари фавқулоддаги ҳолатларда ҳавф-хатар ҳақида хабар бериши, хизматлар ва уларнинг турларига етиб бориш ва фойдаланишга йўналтириши керак'),
        (747, 77, null, null, '6.11. Визуал информация контраст фонда 1,5-1,7 м баландликда жойлаштирилиши керак. Визуал информация интерернинг бадиий ечими билан боғлик бўлиши керак.'),
        (748, 77, null, null, '6.12. Ногирон ёлғиз қолиши мумкин бўлган бинонинг ёпиқ жойлари (лифтлар ва лифт холлари, турли мақсаддаги хоналар, тамбурлар, ҳожатхоиа кабиналари ва ҳ.з.) да «хавф-хатар» тугмачаси ёки деспетчер (навбатчи) билан гаплашиш мосламасини назарда тутиш керак. Барча махсус муассаларда ходимларни чақириш учун сигнализация тизими назарда тутилиши керак.'),
        (749, 77, null, null, '6.13. Ногиронлар ва камҳаракатли фуқаролар бўладиган хона ва коммуникацияларнинг ёритилишини меъёрий кўрсаткичлардан бир даражага ошириш керак.  Қўшни зоналар ёки хоналар ёритилишидаги фарқ 1:4 нисбатдан ошмаслиги керак. Жамоат ҳожатхоналарида, шунингдек, кабиналарда, авария ҳолатида ёритилиши назарда тутилиши керак'),
        (752, 77, null, null, '6.16. Хўжалик ва истеъмол учун бериладиган совуқ ва иссиқ сувнинг сифати О’z DST 950-2000 стандартларига мувофиқ келиши керак. Ногиронлар, қариялар, ногирон болалар учун санитария бўлмаларининг смесителлари термостатик регуляторли бўлиши керак. Мактабгача ва кичик мактаб ёшидаги ногирон болалар, психоневрологик интернатларда арматура тарқатгич орқали душ ва ювингичларга келадиган иссиқ сувнинг ҳарорати 37°С дан ошмаслиги керак; ногиронлар ва қариялар, ногирон ўсмирлар учун 50°С дан ошмаслиги керак. Сув тарқатиш арматураси сифатида ричагли ва босимли кранлар, шунингдек, автоматик мосламалардан фойдаланиш тавсия этилади.'),
        (753, 77, null, null, '6.17. Жамоат ва тураржой биноларида ёнғин сигнализацияси нафақат товуш сигналлари, балки заиф эшитувчи ва карлар учун чироқли сигналларни қабул қилишини ҳисобга олган ҳолада лойиҳалаш керак. Ногиронлар учун тураржойлар ёнғин ҳақида автоном хабар берувчи мосламалар билан жиҳозланиши керак.'),
        (754, 77, null, null, '6.18. Сигнализация мосламаларининг миқдори ва жойлашуви лойиҳага берилиш топшириқда белгиланади. Домофонлар ва киритишни назорат қилувчи мосламалар товушли ҳамда чироқли сигнализацияли бўлиши керак.'),
        (1450, 137, '2024-05-29 17:31:43', '2024-05-29 17:31:43', '13. O‘zbekiston Respublikasi Qurilish va uy-joy kommunal xo‘jaligi vazirligi Kengashning ishchi organi (keyingi o‘rinlarda — ishchi organ) hisoblanadi.'),
        (1158, 119, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '1. Ushbu Reglament yuridik va jismoniy shaxslarga (keyingi o‘rinlarda — ariza beruvchilar) tegishli bo‘lgan bino va inshootlarning tashqi ko‘rinishini o‘zgartirishni kelishish tartibini belgilaydi.'),
        (1225, 121, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '11. Davlat xizmati ko‘rsatilgani uchun bazaviy hisoblash miqdorining bir baravari miqdorida yig‘im undiriladi.
Davlat xizmati ko‘rsatilishi uchun YIDXP orqali murojaat etilganda ushbu bandning birinchi xatboshisida ko‘rsatilgan yig‘im summasining 90 foizi undiriladi.
Davlati xizmati ko‘rsatilgani uchun yig‘im to‘langanligini tasdiqlash elektron tarzda amalga oshiriladi.'),
        (950, 106, '2023-06-20 11:08:41', '2023-06-20 11:08:41', '26-modda. Qurilishni nazorat qilish
Qurilishni nazorat qilish shaharsozlik faoliyati subyektlari tomonidan qurilishga tayyorgarlik ko‘rish, shaharsozlik hujjatlarini ishlab chiqish, qurilish-montaj, ishga tushirish-sozlash ishlarini bajarish, obyektni foydalanishga qabul qilib olish davrida shaharsozlik to‘g‘risidagi qonun hujjatlariga hamda shaharsozlik normalari va qoidalari talablariga rioya etilishini ta’minlash maqsadida amalga oshiriladi.
Shaharsozlik faoliyati obyektlarini qurish chog‘ida quyidagilarning amalga oshirilishi ta’minlanishi kerak:
qurilish ustidan davlat nazorati;
buyurtmachining texnik nazorati;
ishlab chiquvchining mualliflik nazorati;
pudratchining ichki nazorati.'),
        (1244, 122, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '1. Ushbu Reglament yuridik va jismoniy shaxslar yoki ularning buyurtmasiga (shartnomaga) muvofiq loyiha tashkilotlari (keyingi o‘rinlarda — ariza beruvchilar) tomonidan berilgan so‘rovnomaga asosan yer maydoni hajmidan qat’i nazar, barcha turdagi binolar, inshootlar, yakka tartibdagi uy-joylar, ko‘p kvartirali uylar, suv ta’minoti, kanalizatsiya va oqova suvlarni tozalash tizimlari, gidrotexnika inshootlari va boshqa obyektlarning loyiha-smeta hujjatlarining arxitektura qismini (keyingi o‘rinlarda — loyiha-smeta hujjatlari) ko‘rib chiqish va kelishish tartibini belgilaydi.'),
        (925, 102, '2023-06-20 11:08:41', '2023-06-20 11:08:41', '1-modda. Ushbu Kodeksning maqsadi
Ushbu Kodeksning maqsadi shaharsozlik faoliyati sohasidagi munosabatlarni tartibga solishdan iborat.'),
        (926, 102, '2023-06-20 11:08:41', '2023-06-20 11:08:41', '2-modda. Shaharsozlik to‘g‘risidagi qonun hujjatlari
Shaharsozlik to‘g‘risidagi qonun hujjatlari ushbu Kodeks va boshqa qonun hujjatlaridan iboratdir.
Agar O‘zbekiston Respublikasining xalqaro shartnomasida O‘zbekiston Respublikasining shaharsozlik to‘g‘risidagi qonun hujjatlarida nazarda tutilganidan boshqacha qoidalar belgilangan bo‘lsa, xalqaro shartnoma qoidalari qo‘llaniladi.'),
        (927, 102, '2023-06-20 11:08:41', '2023-06-20 11:08:41', '3-modda. Asosiy tushunchalar
Ushbu Kodeksda quyidagi asosiy tushunchalar qo‘llaniladi:
aholi punkti — huquqiy maqom, nom va chegara bilan belgilangan O‘zbekiston Respublikasi ma’muriy-hududiy birligi (shahar, shaharcha, qishloq, ovul);
aholi punktining bosh rejasi — aholi punktini hududiy rivojlantirishning asosiy yo‘nalishlarini hamda fuqarolarning qulay hayot faoliyati muhitini kompleks shakllantirish shart-sharoitlarini belgilaydigan shaharsozlik hujjati;
aholi punktlariaro hududlar — ikki va undan ortiq aholi punkti oralig‘idagi aholi punktlari chegaralaridan tashqaridagi hududlar;
aholi punktining chegara chizig‘i — aholi punkti yerlarining ularni yer fondining boshqa toifalaridan ajratib turadigan, shaharsozlik hujjatlarida belgilangan tashqi chegarasi;
aholini joylashtirish tizimi — mavjud aholi punktlarini rivojlantirish hamda yangi aholi punktlarini barpo etish yo‘li bilan aholini tegishli hududga tartibga solib boriladigan tarzda joylashtirishning shaharsozlik hujjatlari bilan belgilanadigan asosiy yo‘nalishlari;
bino — funksional maqsadiga qarab odamlar yashashi yoki bo‘lishiga va har xil turdagi ishlab chiqarish jarayonlarini bajarishga mo‘ljallangan, tayanch, to‘sma yoki har ikkala maqsadga xizmat qiluvchi konstruksiyalardan iborat qurilish tizimi;
buyurtmachi — obyektlarni qurish, rekonstruksiya qilish, ta’mirlash va buzishning amalga oshirilishini ta’minlovchi, shuningdek shu maqsadlarda loyiha-qidiruv, ekspertlik, qurilish-montaj va nazorat ishlarini bajarish uchun tegishli tashkilotlar bilan shartnomalar tuzuvchi jismoniy yoki yuridik shaxs;
zonalashtirish — hududni rivojlantirishni shaharsozlik jihatidan rejalashtirish chog‘ida shaharsozlikning foydalanish turlarini hamda bu turlardan foydalanishga doir cheklovlarni belgilagan holda, hududning funksional maqsadga ko‘ra bo‘linishi;
inshoot — har xil turdagi ishlab chiqarish jarayonlarini bajarish, moddiy qimmatliklarni joylashtirish va saqlash, odamlarning vaqtincha bo‘lishi (harakatlanishi), shuningdek uskunalar yoki kommunikatsiyalarni joylashtirish (o‘tkazish, ulash) uchun mo‘ljallangan, tayanch, to‘sma yoki har ikkala maqsadga xizmat qiluvchi konstruksiyalardan iborat hajmiy, yassi yoki chiziq tarzidagi qurilish tizimi;
ishlab chiqarish obyektlari — odamlar va texnologik asbob-uskunalar ishlashi uchun zarur shart-sharoitlar ta’minlanadigan ishlab chiqarishni joylashtirish uchun mo‘ljallangan binolar hamda inshootlar;
qizil chiziqlar — shaharsozlik hujjatlarida belgilanadigan, mavzelarning, kichik dahalarning va rejalashtirish tuzilmasi boshqa elementlarining hududlarini aholi punktlarining ko‘chalaridan, tor ko‘chalaridan va maydonlaridan ajratib turuvchi chegaralar;
noyob va alohida muhim obyektlar — alohida yoki birgalikda o‘ziga xos, betakror hajmiy-rejaviy, konstruktiv, muhandislik yoki texnologik yechimlari hamda arxitektura g‘oyasi bilan, yuqori texnik darajadagi yutuqlar va me’morchilikni o‘zida mujassam etgan binolar, inshootlar va ularning majmualari;
reklama va axborot obyektlari (konstruksiyalari) — tashqi reklamani joylashtirish (tarqatish) uchun maxsus ishlab chiqilgan va qo‘llaniladigan vositalar, shuningdek aholiga axborot yetkazish vazifalarini bajaruvchi texnik vositalar (konstruksiyalar);
turar joy obyektlari — odamlar yashashi uchun mo‘ljallangan binolar, doimiy yashash uchun uy-joylar (xonadonlar), shuningdek vaqtincha yashash uchun yotoqxonalar, mehmonxonalar, maktab-internatlar, qariyalar uylari;
urbanizatsiya — aholining ijtimoiy-kasbiy va demografik tuzilishida ijobiy o‘zgarishlarga, uning turmush tarzi hamda madaniyati sifat jihatidan yangi darajaga ko‘tarilishiga ko‘maklashuvchi, jamiyat va iqtisodiyotni rivojlantirishda shaharlarning rolini oshirish, shuningdek shahar infratuzilmasini shakllantirish jarayoni. Urbanizatsiya, qoida tariqasida, shahar atrofini shahar hududiga kiritish, bir nechta aholi punktlarini yagona makonga birlashtirish bilan birga olib boriladi;
fuqaroviy obyektlar — madaniyat, jismoniy tarbiya, sport, ilm-fan, ta’lim, sog‘liqni saqlash, savdo, umumiy ovqatlanish, maishiy xizmat ko‘rsatish muassasalari va tashkilotlarini, bank-moliya muassasalarini, qurilish-kommunal tashkilotlarini, aloqa hamda transport korxonalarini joylashtirish uchun mo‘ljallangan binolar va inshootlar;
shaharsozlik — aholi punktlarini, aholi punktlariaro hududlarni rejalashtirish hamda qurishning ijtimoiy-iqtisodiy, qurilish-texnika, arxitektura-badiiy yechimlari yig‘indisini sanitariya qoidalari, normalari va gigiyena normativlarini hisobga olgan holda ta’minlovchi nazariyasi hamda amaliyoti;
shaharsozlik reglamenti — hududlarni va aholi punktlarini qurishning belgilangan qoidalari, shaharsozlik faoliyatini amalga oshirish chog‘ida aholi punktlarining hamda aholi punktlariaro hududlarning yer uchastkalaridan va boshqa ko‘chmas mulk obyektlaridan foydalanish ko‘rsatkichlari hamda turlarining yig‘indisi;
shaharsozlik faoliyati — davlat organlarining, fuqarolar o‘zini o‘zi boshqarish organlarining, yuridik va jismoniy shaxslarning hududlarni va aholi punktlarini rivojlantirishni shaharsozlik jihatidan rejalashtirish, yer uchastkalaridan foydalanish turlarini belgilash, qurilish materiallari va buyumlarini ishlab chiqarish, binolarni, inshootlarni hamda ularning majmualarini shaxs, jamiyat va davlat manfaatlarini, shuningdek mazkur hududlar hamda aholi punktlarining milliy, tarixiy-madaniy, ekologik, tabiiy o‘ziga xos xususiyatlarini inobatga olgan holda loyihalashtirish, qurish va rekonstruksiya qilish sohasidagi faoliyati;
shaharsozlik hujjatlari — hududlarni va aholi punktlarini rivojlantirishni shaharsozlik jihatidan rejalashtirish to‘g‘risidagi hamda ularni qurish haqidagi tasdiqlangan hujjatlar;
yakka tartibdagi uy-joy — yakka tartibda uy-joy qurish va tomorqa xo‘jaligini yuritish uchun mo‘ljallangan bir yer uchastkasida joylashgan bir yoki bir nechta turar-joy binolari va ularga taalluqli bo‘lgan tomorqa-xo‘jalik imoratlari va hovli inshootlari.'),
        (928, 102, '2023-06-20 11:08:41', '2023-06-20 11:08:41', '4-modda. Shaharsozlik faoliyatining asosiy prinsiplari
Shaharsozlik faoliyatining asosiy prinsiplari quyidagilardan iborat:
shaharsozlik sohasida fuqarolar, jamiyat va davlat manfaatlarini hisobga olgan holda rivojlantirishni shaharsozlik jihatidan rejalashtirish asosida O‘zbekiston Respublikasi hududini va uning qismlarini barqaror rivojlantirish;
hayotiy faoliyat uchun qulay muhit yaratish, fuqarolar salomatligini muhofaza qilish, atrof-muhitga, tabiiy resurslarga va madaniy merosga ehtiyotkorlik bilan munosabatda bo‘lish;
binolar va inshootlar konstruksiyalarining xavfsizligi va ishonchliligi, ularning seysmik chidamliligi, yong‘in xavfsizligi va energiya samaradorligiga qo‘yiladigan talablarga rioya qilgan holda tasdiqlangan shaharsozlik hujjatlari asosida shaharsozlik faoliyatini amalga oshirish;
shaharsozlik faoliyati va qurilish ishlab chiqarishida eng yaxshi amaliyotlarni joriy etish, qurilish sifatini oshirish;
hududlarni rivojlantirishni shaharsozlik jihatidan rejalashtirish jarayonida jamoatchilikning samarali ishtirokini ta’minlash.'),
        (929, 102, '2023-06-20 11:08:41', '2023-06-20 11:08:41', '5-modda. Shaharsozlik sohasida raqobat muhitini yaratish
Shaharsozlik faoliyati subyektlari o‘rtasidagi raqobat muhiti:
ishlar (tovarlar, xizmatlar) bozorida monopol faoliyatni, shu jumladan raqobatni cheklashga olib keladigan yoki olib kelishi mumkin bo‘lgan faoliyatni cheklash;
shaharsozlik faoliyati subyektlari to‘g‘risidagi ma’lumotlarning qulay elektron bazalarini yaratish va yaratilgan asosda ularning faoliyatini baholash reytinglarini joriy etish;
shaharsozlik faoliyatining ochiqligi va shaffofligini ta’minlash;
davlat xaridlari to‘g‘risidagi qonun hujjatlari talablariga rioya etilishini ta’minlash yo‘li bilan yaratiladi.'),
        (957, 106, '2023-06-20 11:08:41', '2023-06-20 11:08:41', '33-modda. Qurilish materiallariga, buyumlariga va konstruksiyalariga qo‘yiladigan talablar, ularni shaharsozlik hujjatlariga kiritish shartlari
Qurilish materiallari, buyumlari va konstruksiyalari texnik jihatdan tartibga solish sohasidagi normativ hujjatlar talablariga javob berishi kerak.
Shaharsozlik hujjatlarida qurilish materiallari, buyumlari va konstruksiyalarining xavfsizlik ko‘rsatkichlari qayd etiladi.
Shaharsozlik hujjatlarida qurilish materiallari, buyumlari va konstruksiyalari qurilishning texnik-iqtisodiy asoslari hamda loyiha yechimlarining talablaridan kelib chiqqan holda tanlab olinadi.'),
        (1241, 121, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '27. Adliya vazirligi, uning hududiy boshqarmalari va Davlat xizmatlari markazlari ushbu Reglament talablari ijrosi bo‘yicha davlat organlari va boshqa tashkilotlarning faoliyati ustidan, shu jumladan, YIDXP orqali, doimiy nazoratni va monitoringni amalga oshiradi.'),
        (930, 102, '2023-06-20 11:08:41', '2023-06-20 11:08:41', '6-modda. Shaharsozlik normalari va qoidalari
Shaharsozlik normalari va qoidalari shaharsozlikning asosi bo‘lib, ular shaharsozlik faoliyati subyektlari tomonidan bajarilishi majburiydir. Shaharsozlik normalari va qoidalari texnik jihatdan tartibga solish sohasidagi normativ hujjatlar bo‘lib:
hududlarni rivojlantirishni va qurishni shaharsozlik jihatidan rejalashtirish;
muhandislik qidiruvlari;
loyihalashtirish, shu jumladan texnologik loyihalashtirish;
obyektlarni qurishni, rekonstruksiya qilishni, mukammal va joriy ta’mirlashni, shuningdek buzishni amalga oshirish;
obyektlardan foydalanish, ularni ta’mirlash yoki qayta tiklash (ishlarni tashkil etish, yuritish va qabul qilib olish);
obyektlarning muhandislik tizimlarini o‘rnatish;
obyektlarning qiymatini belgilash;
qurilish materiallari, buyumlari va konstruksiyalarini ishlab chiqarish hamda ulardan foydalanish chog‘idagi majburiy talablarni belgilaydi.
O‘zbekiston Respublikasining geologik, tabiiy-iqlimiy, seysmologik hamda boshqa o‘ziga xos xususiyatlariga moslashtirilgan shaharsozlik faoliyati sohasidagi xalqaro va xorijiy normativ-texnik hujjatlardan foydalanishga yo‘l qo‘yiladi.
Shaharsozlik normalari va qoidalari to‘g‘risidagi axborot shaharsozlik faoliyati sohasidagi maxsus vakolatli davlat organining rasmiy veb-saytiga joylashtiriladi.
Shaharsozlik normalari va qoidalari chegaradosh yer uchastkalari, reklama va axborot obyektlari (konstruksiyalari) hamda boshqa ko‘chmas mulk obyektlari mulkdorlarining, egalarining va ulardan foydalanuvchilarning, shuningdek shaharsozlik faoliyatini amalga oshirishda qaysi yuridik va jismoniy shaxslarning manfaatlariga daxl qilinishi mumkin bo‘lsa, o‘sha yuridik va jismoniy shaxslarning huquqlari masalalari yuzasidan davlat organlari tomonidan qarorlar qabul qilinishi uchun asos bo‘ladi.'),
        (931, 102, '2023-06-20 11:08:41', '2023-06-20 11:08:41', '7-modda. Idoraviy qurilish normalari
Shaharsozlik faoliyatini amalga oshirish chog‘ida idoraviy qurilish normalaridan foydalanilishi mumkin.
Vazirliklar, davlat qo‘mitalari va idoralar bir vazirlik, davlat qo‘mitasi va idora doirasida qo‘llanilishi mumkin bo‘lgan idoraviy qurilish normalarini o‘z vakolatlari doirasida ishlab chiqishi, tasdiqlashi, qayta ko‘rib chiqishi yoki bekor qilishi mumkin.
Idoraviy qurilish normalari shaharsozlik to‘g‘risidagi qonun hujjatlariga, shaharsozlik normalari va qoidalariga zid bo‘lmasligi kerak.
Idoraviy qurilish normalari tasdiqlanishidan oldin shaharsozlik faoliyati sohasidagi maxsus vakolatli davlat organi bilan kelishib olinadi.'),
        (932, 102, '2023-06-20 11:08:41', '2023-06-20 11:08:41', '8-modda. Yuridik va jismoniy shaxslarning, jamiyat va davlatning shaharsozlik sohasidagi manfaatlari
Yuridik va jismoniy shaxslarning shaharsozlik faoliyatini amalga oshirish bilan bog‘liq manfaatlari ularning shaharsozlik sohasidagi manfaatlari jumlasiga kiradi.
Yuridik va jismoniy shaxslarning shaharsozlik faoliyati, agar bunday faoliyat chegaradosh yer uchastkalari, reklama va axborot obyektlari (konstruksiyalari) hamda boshqa ko‘chmas mulk obyektlari mulkdorlarining, egalarining hamda ulardan foydalanuvchilarning huquqlari va qonuniy manfaatlari amalga oshirilishiga monelik qilsa, cheklanishi kerak.
Fuqarolarning qulay hayot faoliyati muhitiga bo‘lgan huquqi, xo‘jalik va boshqa faoliyatning atrof-muhitga zararli ta’sir ko‘rsatishiga yo‘l qo‘ymaslik, ekologik vaziyatni yaxshilash, aholi punktlarining hamda ularga tutash hududlarning muhandislik, transportga oid va ijtimoiy infratuzilmalarini rivojlantirish, madaniy meros obyektlarini saqlash, shuningdek shaharsozlik faoliyatining ochiqligi va shaffofligi jamiyatning shaharsozlik sohasidagi manfaatlaridir.
Aholi punktlarini va aholi punktlariaro hududlarni barqaror rivojlantirish uchun shart-sharoitlarni ta’minlash, muhandislik, transportga oid va ijtimoiy infratuzilmalar tizimlarining ishlab turishi, tabiiy resurslarni va madaniy meros obyektlarini saqlash, shuningdek manfaatlar to‘qnashuviga yo‘l qo‘ymaslik davlatning shaharsozlik sohasidagi manfaatlaridir.
Yuridik va jismoniy shaxslarning, jamiyat va davlatning shaharsozlik sohasidagi manfaatlari shaharsozlik to‘g‘risidagi qonun hujjatlari, shaharsozlik normalari hamda qoidalari, shaharsozlik hujjatlari talablariga rioya etish, shuningdek ularga rioya etilishi ustidan davlat va jamoatchilik nazoratini amalga oshirish orqali ta’minlanadi.
Agar shaharsozlik faoliyati yuridik va jismoniy shaxslar, jamiyat va davlat manfaatlariga zid bo‘lsa, bunday faoliyat tugatilishi kerak.'),
        (933, 102, '2023-06-20 11:08:41', '2023-06-20 11:08:41', '9-modda. Fuqarolarining qulay hayot faoliyati muhitiga bo‘lgan huquqi
Har bir fuqaro qulay hayot faoliyati muhitiga bo‘lgan huquqqa ega.
Shaharsozlik faoliyatini amalga oshirishda fuqarolarning qulay hayot faoliyati muhitiga bo‘lgan huquqi:
shaharsozlik faoliyatini davlat tomonidan tartibga solish;
hududlarni va aholi punktlarini rivojlantirishni shaharsozlik jihatidan rejalashtirish;
fuqarolarning shaharsozlik faoliyatini amalga oshirishda ishtirok etishi;
qurilish materiallari, buyumlari va konstruksiyalarini sertifikatlashtirish;
 shaharsozlik to‘g‘risidagi qonun hujjatlariga, shaharsozlik normalari va qoidalariga rioya etilishi ustidan davlat va jamoatchilik nazorati;
shaharsozlik to‘g‘risidagi qonun hujjatlari buzilganligi natijasida fuqarolarga yetkazilgan hamda ularning hayot faoliyati muhiti yomonlashishiga sabab bo‘lgan zararlarni kompensatsiya qilish, shuningdek fuqarolarning hayoti, sog‘lig‘i va mol-mulkiga yetkazilgan zararning o‘rnini qoplash orqali ta’minlanadi.'),
        (1245, 122, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '2. Ushbu Reglamentning amal qilishi binolar, inshootlar va boshqa obyektlarni mukammal ta’mirlashga nisbatan qo‘llanmaydi.
O‘zbekiston Respublikasining davlat sirlari bilan bog‘liq bo‘lgan obyektlarning loyiha-smeta hujjatlarini kelishish uchun bevosita to‘g‘ridan to‘g‘ri Qoraqalpog‘iston Respublikasi Qurilish vazirligi, viloyatlar va Toshkent shahar qurilish bosh boshqarmalariga murojaat qilinadi.'),
        (934, 102, '2023-06-20 11:08:41', '2023-06-20 11:08:41', '10-modda. Shaharsozlik faoliyatiga doir asosiy talablar
Shaharsozlik faoliyatiga doir asosiy talablar quyidagilardan iborat:
shaharsozlik faoliyati subyektlari tomonidan shaharsozlik to‘g‘risidagi qonun hujjatlariga, shaharsozlik normalari va qoidalariga rioya etilishi;
hududlar va aholi punktlarini tabiiy hamda texnogen xususiyatga ega favqulodda vaziyatlar ta’siridan himoya qilish;
atrof-muhitni muhofaza qilish, ekologik xavfsizlik talablariga, yong‘inga qarshi hamda sanitariya qoidalariga, normalariga va gigiyena normativlariga rioya etish;
suvni muhofaza qilish zonalari, muhandislik va transport infratuzilmasi obyektlarining muhofaza zonalari talablariga rioya etish;
madaniy meros obyektlarini va muhofaza etiladigan tabiiy hududlarni saqlash talablariga rioya etish;
aholi punktlarini aholi punktlarining tegishli turi uchun shaharsozlik normalari va qoidalarida belgilangan aholiga xizmat ko‘rsatish darajasidan kam bo‘lmagan muhandislik, transportga oid hamda ijtimoiy infratuzilmalar obyektlari, rekreatsiya va sog‘lomlashtirish maqsadlariga mo‘ljallangan obyektlar, shuningdek hududlarni obodonlashtirish obyektlari bilan ta’minlash;
nogironligi bo‘lgan shaxslarning ijtimoiy infratuzilma obyektlaridan (turar joylar, jamoat va ishlab chiqarish binolari, imoratlari va inshootlari, sog‘liqni saqlash, madaniyat, sport obyektlari va boshqa obyektlardan) moneliksiz foydalanishi uchun shart-sharoitlar yaratish;
shaharsozlik faoliyati sohasidagi qarorlarning muhokamasi va qabul qilinishida fuqarolarning o‘zini o‘zi boshqarish organlari, nodavlat notijorat tashkilotlari, ommaviy axborot vositalari va fuqarolar ishtirok etishi uchun shart-sharoitlarni ta’minlash.'),
        (935, 102, '2023-06-20 11:08:41', '2023-06-20 11:08:41', '11-modda. Yuridik va jismoniy shaxslarning shaharsozlik faoliyatini amalga oshirish chog‘idagi majburiyatlari
Shaharsozlik faoliyatini amalga oshirish chog‘ida yuridik va jismoniy shaxslar:
aholining qulay hayot faoliyati muhitini asrashi;
shaharsozlik hujjatlariga, aholi punktlarini va hududlarni qurish qoidalariga rioya etilishini ta’minlashi;
atrof-muhitni muhofaza qilish, ekologik xavfsizlik talablariga, yong‘inga qarshi hamda sanitariya qoidalariga, normalariga va gigiyena normativlariga rioya etishi;
atrof-muhitga, madaniy meros obyektlariga, shahar, qishloq landshaftlariga, muhandislik, transportga oid va ijtimoiy infratuzilmalar obyektlariga, hududlarni obodonlashtirish obyektlariga zararli ta’sir etadigan, uchinchi shaxslarning qonuniy manfaatlariga daxl etadigan hamda chegaradosh yer uchastkalari, reklama va axborot obyektlari (konstruksiyalari) hamda boshqa ko‘chmas mulk obyektlari mulkdorlarining, egalarining yoki ulardan foydalanuvchilarning huquqlari amalga oshirilishiga monelik qiladigan xatti-harakatlar sodir etmasligi;
yer uchastkalaridan va boshqa ko‘chmas mulk obyektlaridan shaharsozlik reglamentlariga rioya etgan holda foydalanishi;
shaharsozlik to‘g‘risidagi qonun hujjatlariga, shaharsozlik normalari va qoidalariga rioya etilishi ustidan nazoratni amalga oshiruvchi davlat organlarining ko‘rsatmalarini bajarishi;
davlat shaharsozlik kadastri yuritilishini va shaharsozlik faoliyati obyektlarining monitoringini amalga oshiruvchi shaharsozlik faoliyati sohasidagi maxsus vakolatli davlat organiga hamda obyektlarni texnik inventarizatsiyadan o‘tkazuvchi tashkilotlarga o‘zlariga tegishli obyektlardagi o‘zgarishlar haqida ishonchli ma’lumotlar taqdim etishi;
shaharsozlik hujjatlari materiallarini, shu jumladan kompleks muhandislik qidiruvlari materiallarini (bundan mavsumiy ishlar uchun vaqtinchalik va xo‘jalik-maishiy imoratlari, uch yuz kub metrdan oshmaydigan alohida turgan obyektlarga doir shaharsozlik hujjatlari mustasno) shaharsozlik faoliyati sohasidagi maxsus vakolatli davlat organining tegishli hududiy bo‘linmalariga taqdim etishi shart.
Shaharsozlik faoliyatini amalga oshirish chog‘ida yuridik va jismoniy shaxslarning zimmasida qonun hujjatlariga muvofiq boshqa majburiyatlar ham bo‘lishi mumkin.'),
        (936, 102, '2023-06-20 11:08:41', '2023-06-20 11:08:41', '12-modda. Aholi punktlarining turlari
Aholi punktlari shaharlarga, shaharchalarga, qishloqlar va ovullarga bo‘linadi.
Aholining soniga qarab shaharlar quyidagilarga bo‘linadi:
eng yirik — aholisining soni bir milliondan ortiq kishidan iborat;
yirik — aholisining soni ikki yuz ellik mingdan bir milliongacha kishidan iborat;
katta — aholisining soni yuz mingdan ikki yuz ellik minggacha kishidan iborat;
o‘rtacha — aholisining soni ellik mingdan yuz minggacha kishidan iborat;
kichik — aholisining soni ellik minggacha kishidan iborat bo‘lgan shaharlar.
Aholining soniga qarab shaharchalar quyidagilarga bo‘linadi:
katta — aholisining soni yigirma mingdan ortiq kishidan iborat;
o‘rtacha — aholisining soni o‘n mingdan yigirma minggacha kishidan iborat;
kichik — aholisining soni o‘n minggacha kishidan iborat bo‘lgan shaharchalar.
Aholining soniga qarab qishloqlar va ovullar quyidagilarga bo‘linadi:
yirik — aholisining soni besh mingdan ortiq kishidan iborat;
katta — aholisining soni uch mingdan besh minggacha kishidan iborat;
o‘rtacha — aholisining soni bir mingdan uch minggacha kishidan iborat;
kichik — aholisining soni bir minggacha kishidan iborat bo‘lgan qishloqlar va ovullar.
Vaqtinchalik ahamiyatga molik va aholi tarkibi doimiy bo‘lmagan hamda iqtisodiyot tegishli tarmog‘ining xizmat maqsadidagi obyektlari bo‘lgan binolar va inshootlarning guruhlari (vaxta usulida ishlovchi neftchilar, gazchilar, izlanuvchilarning shaharchalari hamda uylari), shuningdek yakka uylar (temir yo‘l nazoratchilarining, o‘rmonchilarning uylari, dala shiyponlari va boshqalar) ushbu binolar va inshootlar ma’muriy, ishlab chiqarish yoki hududiy munosabatlarda shaharsozlik faoliyatining qaysi tegishli subyektlari bilan bog‘liq bo‘lsa, o‘sha subyektlar tasarrufida bo‘ladi.
Aholi punktining turiga qarab shaharsozlik to‘g‘risidagi qonun hujjatlarida shaharsozlik hujjatlarining tarkibi, ularni ishlab chiqish va tasdiqlash tartibi belgilanadi.
 O‘zbekiston Respublikasi hududlarini va hududlarining qismlarini rivojlantirishni rejalashtirishga doir hujjatlarni ishlab chiqish, kelishish va tasdiqlash tartibi to‘g‘risidagi nizom.
Yangi aholi punktlarini joylashtirish hamda mavjudlarini rivojlantirish hududlarni ijtimoiy-iqtisodiy va investitsiyaviy rivojlantirishning uzoq muddatli prognozlariga, O‘zbekiston Respublikasi hududida aholini joylashtirishning bosh tarhiga, hududlarni rejalashtirish tarhlariga hamda tumanni (tumanlar guruhlarini) rejalashtirish loyihalariga, aholi punktlari hududlarini rivojlantirish va qurishga oid shaharsozlik hujjatlariga muvofiq amalga oshiriladi.
Aholi punktlarini tashkil etish hamda qayta tashkil etish, ularning maqomi va chegaralarini o‘zgartirish tasdiqlangan shaharsozlik hujjatlari asosida amalga oshiriladi.
 Shaharlar, posyolkalar va ovullarning mahallalarini tuzish, birlashtirish, bo‘lish va tugatish, shuningdek ularning chegaralarini belgilash va o‘zgartirish, ularga nom berish va ularning nomini o‘zgartirish tartibi to‘g‘risidagi nizom.
Aholi punktlari chegaralaridagi yerlar aholi punktlarining tasdiqlangan bosh rejalariga, shaharsozlik va yer to‘g‘risidagi qonun hujjatlariga muvofiq ulardan foydalanishning tartibga solinishini amalga oshiruvchi mahalliy davlat hokimiyati organlari tasarrufida bo‘ladi.
Aholi punktlarining yerlaridan foydalanish shartlari va tartibiga doir shaharsozlik talablari shaharsozlik to‘g‘risidagi qonun hujjatlari bilan belgilanadi.'),
        (937, 103, '2023-06-20 11:08:41', '2023-06-20 11:08:41', '13-modda. Shaharsozlik faoliyati obyektlari
O‘zbekiston Respublikasining hududi va hududining qismlari, aholi punktlarining hududlari va hududlarining qismlari, aholini joylashtirishning umumdavlat hamda hududiy tizimlari, yer uchastkalari, binolar va inshootlar, hududiy-ishlab chiqarish, shaharsozlik hamda landshaft majmualari, rekreatsiya va ishlab chiqarish zonalari, madaniy meros obyektlari hamda ularning muhofaza zonalari, aholi punktlari chegaralaridagi va aholi punktlariaro hududlardagi akvatoriyalar, muhandislik va transport kommunikatsiyalari, reklama va axborot obyektlari (konstruksiyalari) shaharsozlik faoliyati obyektlaridir.
Shaharsozlik faoliyati obyektlari uchun shaharsozlik hujjatlari ishlab chiqiladi.'),
        (938, 103, '2023-06-20 11:08:41', '2023-06-20 11:08:41', '14-modda. Shaharsozlik faoliyati subyektlari
Davlat organlari, fuqarolarning o‘zini o‘zi boshqarish organlari, yuridik va jismoniy shaxslar, shu jumladan chet ellik yuridik va jismoniy shaxslar shaharsozlik faoliyati subyektlaridir.
Shaharsozlik faoliyati subyektlari:
shaharsozlik faoliyati sohasidagi buyurtmachilar;
shaharsozlik hujjatlarini ishlab chiquvchilar;
obyektlarni qurish bo‘yicha pudratchilar;
shaharsozlik faoliyati obyektlaridan foydalanuvchilar sifatida ish yuritishi mumkin.
Shaharsozlik faoliyati subyektlari tegishli davlat organlaridan aholi punktlarini, ularning tizimlarini va hududlarini kutilayotgan rejalashtirish, qurish hamda rekonstruksiya qilish bilan bog‘liq bo‘lgan, o‘zining shaharsozlik faoliyatiga ta’sir eta oladigan qarorlar tayyorlanishi va qabul qilinishi haqida axborot olish huquqiga ega.
Shaharsozlik faoliyati subyektlari qonunchilikka muvofiq o‘ziga berilgan yer uchastkalaridan belgilangan maqsadda foydalanishi, shuningdek madaniy meros obyektlariga, tabiiy hamda sun’iy landshaftlarga zarar yetkazmasligi, aholining hayot faoliyati muhiti yomonlashishiga, shaharsozlik faoliyati boshqa subyektlarining huquqlari va qonuniy manfaatlari kamsitilishiga olib keladigan xatti-harakatlarga yo‘l qo‘ymasligi shart.'),
        (955, 106, '2023-06-20 11:08:41', '2023-06-20 11:08:41', '31-modda. Qurilish ishlarini tashkil etish va amalga oshirish
Qurilish ishlarini tashkil etish pirovard natijaga erishish uchun zarur bo‘lgan barcha tashkiliy-texnik va texnologik yechimlar qo‘llanilgan holda shaharsozlik faoliyati subyektlari tomonidan amalga oshiriladi.
Qurilish ishlari:
buyurtmachi tomonidan hududiy inspeksiya obyektda qurilish-montaj ishlari boshlanganligi to‘g‘risida xabardor qilingandan keyin;
pudratchilar tomonidan malakali muhandis-texnik xodimlarni, mutaxassislar va ishchilarni jalb etgan holda, bundan ushbu Kodeks 17-moddasining uchinchi qismida nazarda tutilgan hollar mustasno;
ekspert tashkilotlarining ijobiy xulosasi asosida tasdiqlangan shaharsozlik hujjatlariga muvofiq;
qurilishni tashkil etish va ishlarni bajarishning tasdiqlangan loyihalari majburiy ravishda mavjud bo‘lganda amalga oshirilishi kerak.'),
        (1246, 122, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '3. Loyiha-smeta hujjatlari tegishli litsenziyasi bo‘lgan loyiha tashkiloti tomonidan ishlab chiqiladi. Loyiha-smeta hujjatlarida bino va inshoot hamda yakka tartibdagi uy-joyning umumiy maydoni (kvadrat metr) ko‘rsatilishi kerak.'),
        (939, 103, '2023-06-20 11:08:41', '2023-06-20 11:08:41', '15-modda. Shaharsozlik faoliyati sohasidagi buyurtmachilar
Quyidagilar shaharsozlik faoliyati sohasidagi buyurtmachilar bo‘lishi mumkin:
davlat organlari, fuqarolarning o‘zini o‘zi boshqarish organlari, shuningdek yuridik va jismoniy shaxslar;
davlat-xususiy sheriklik asosida faoliyatini amalga oshiruvchi yuridik va jismoniy shaxslar.
Buyurtmachilar:
shaharsozlik hujjatlarini ishlab chiquvchini tanlash hamda u bilan bu hujjatlarni ishlab chiqishga doir shartnomani tuzish;
obyektlarni qurish bo‘yicha pudratchini tanlash va u bilan pudrat shartnomasini tuzish huquqiga ega.
Buyurtmachilar:
shaharsozlik to‘g‘risidagi qonun hujjatlariga, shaharsozlik normalari va qoidalariga rioya etishi;
shaharsozlik hujjatlarini ishlab chiqish uchun topshiriqlarni tayyorlashi va tasdiqlashi;
shaharsozlik hujjatlarini ishlab chiquvchilarga loyihalashtirishga doir birlamchi-ruxsat beruvchi hujjatlarni, shu jumladan arxitektura-rejalash topshirig‘ini, topografik materiallarni, muhandislik-geologik qidiruvlarning va tayanch qurilish konstruksiyalarining texnik holatini tekshiruvdan o‘tkazish materiallarini, nuqsonlarga oid dalolatnomalarni taqdim etishi;
shaharsozlik hujjatlarining ekspertizadan o‘tkazilishini ta’minlashi;
loyihalashtirish jarayoni ustidan tekshiruvni va qurilish sifati ustidan texnik nazoratni amalga oshirishi, qurilish jarayonida kamchiliklar va nuqsonlar aniqlangan taqdirda, ularni bartaraf etish yuzasidan choralar ko‘rishi;
shaharsozlik hujjatlarini ishlab chiquvchi bilan tuzilgan shartnoma asosida shaharsozlik hujjatlarini amalga oshirish bo‘yicha mualliflik nazorati amalga oshirilishini ta’minlashi;
hududiy inspeksiyalar tomonidan qurilishi tugallangan obyektni ekspluatatsiya qilish uchun ruxsatnoma berilganidan keyin foydalanishning kafolat muddati davomida aniqlangan kamchiliklar bartaraf etilishini tashkil qilishi shart.
Buyurtmachilarga, bundan asosiy faoliyat turi sifatida qurilish-montaj ishlarini olib boruvchi tashkilotlar mustasno, pudratchi bilan qurilish pudrati shartnomasini tuzmasdan mustaqil ravishda (xo‘jalik usulida) balandligi yer yuzasidan 12 metrdan va (yoki) umumiy maydoni 500 kvadrat metrdan ortiq, ikki qavatdan yuqori (tsokolni hisobga olmagan holda) bo‘lgan obyektlarni qurish, rekonstruksiya qilish va kapital ta’mirlash taqiqlanadi.
Buyurtmachilar qonun hujjatlariga muvofiq boshqa huquqlarga ham ega bo‘lishi va ularning zimmasida boshqa majburiyatlar ham bo‘lishi mumkin.'),
        (940, 103, '2023-06-20 11:08:41', '2023-06-20 11:08:41', '16-modda. Shaharsozlik hujjatlarini ishlab chiquvchilar
Tegishli litsenziyaga ega bo‘lgan yuridik shaxslar, shuningdek shaharsozlik hujjatlarini ishlab chiqish bo‘yicha faoliyatni boshlash to‘g‘risida vakolatli organni xabardor qilish asosida faoliyat yuritadigan yuridik shaxslar shaharsozlik hujjatlarini ishlab chiquvchilar (bundan buyon matnda ishlab chiquvchilar deb yuritiladi) bo‘lishi mumkin.
Ishlab chiquvchilar:
ishlab chiqiladigan shaharsozlik hujjatlarining tarkibi va hajmini qonun hujjatlariga muvofiq belgilash;
shaharsozlik hujjatlarini ishlab chiqishga doir tanlovlarda ishtirok etish;
shaharsozlik hujjatlarini amalga oshirish bo‘yicha mualliflik nazoratini olib borish;
qurilish, montaj ishlari va maxsus ishlar tasdiqlangan shaharsozlik hujjatlari hamda normativ talablardan fuqarolarning hayoti va sog‘lig‘iga tahdid soladigan, madaniy meros obyektlarining shikastlanishi hamda yo‘q qilinishi xavfini tug‘diradigan, shuningdek yuridik va jismoniy shaxslarning, jamiyat va davlatning manfaatlariga ziyon keltiradigan tarzdagi chetga chiqishlar bilan bajarilgan taqdirda, qurilish, montaj ishlarini va maxsus ishlarni to‘xtatib turish yoki tugatish haqida takliflar kiritish;
zarur hollarda, shaharsozlik hujjatlariga o‘zgartirishlar kiritish huquqiga ega.
Ishlab chiquvchilar:
shaharsozlik hujjatlarining qonun hujjatlariga muvofiq ishlab chiqilishini ta’minlashi;
o‘zlari ishlab chiqqan shaharsozlik hujjatlarini barcha manfaatdor organlar va tashkilotlar bilan kelishib olishi shart.
Ishlab chiquvchilar qonun hujjatlariga muvofiq boshqa huquqlarga ham ega bo‘lishi va ularning zimmasida boshqa majburiyatlar ham bo‘lishi mumkin.'),
        (941, 103, '2023-06-20 11:08:41', '2023-06-20 11:08:41', '17-modda. Obyektlarni qurish bo‘yicha pudratchilar
Tegishli litsenziyaga ega bo‘lgan yuridik shaxslar shuningdek jismoniy shaxslar obyektlarni qurish bo‘yicha pudratchilar (bundan buyon matnda pudratchilar deb yuritiladi) bo‘lishi mumkin.
Jismoniy shaxslar pudratchilar sifatida:
ikki qavatdan yuqori bo‘lmagan (tsokolni hisobga olmagan holda), balandligi yer yuzasidan 12 metrdan va (yoki) umumiy maydoni 500 kvadrat metrdan ortiq bo‘lmagan yakka tartibdagi uy-joylarni qurish;
mavsumiy ishlar va yaylov chorvachiligi uchun vaqtinchalik imoratlarni, xo‘jalik-maishiy binolarini qurish;
yakka tartibdagi tomorqa yer uchastkalari hududida, shuningdek jamoa bog‘dorchiligi, uzumchiligi va polizchiligini yuritish uchun ajratilgan yer uchastkalarida xo‘jalik-maishiy imoratlarini qurish;
tomorqa yer uchastkalarida va mavjud muhandislik tarmoqlarini o‘zgartirish talab etilmaydigan jamoa bog‘dorchiligi, uzumchiligi va polizchiligini yuritish uchun ajratilgan yer uchastkalarini obodonlashtirish;
umumiy maydoni 500 kvadrat metrdan ortiq bo‘lmagan, yig‘ma-qismlarga ajraladigan konstruksiyalardan quriladigan savdo obyektlari, umumiy ovqatlanish va maishiy xizmat ko‘rsatish obyektlari uchun bir qavatli bino va inshootlarni qurish;
ikkitadan ko‘p bo‘lmagan avtomashinaga mo‘ljallangan bokslardan iborat garajlarni qurish;
kichik arxitektura shakllarini qurish va hududlarni o‘rash;
ochiq sport maydonchalari, yo‘laklar qurish, bino va inshootlar atrofiga tosh yotqizish;
reklama va axborot obyektlarini (konstruksiyalarini) qurish;
mulkdorlarning shaxsiy foydalanishi uchun mo‘ljallangan texnik jihatdan murakkab bo‘lmagan imoratlarni qurish bo‘yicha ishlarni amalga oshirishi mumkin.
Pudratchilar:
binolar, inshootlar va boshqa obyektlarning yangi konstruktiv yechimlarini ishlab chiqishda ishtirok etish;
ishlab chiquvchi, shaharsozlik faoliyati sohasidagi maxsus vakolatli davlat organining tegishli hududiy bo‘linmasi va buyurtmachi bilan kelishgan holda shaharsozlik hujjatlariga binolarning, inshootlarning va boshqa obyektlarning arxitektura-badiiy qiyofasini hamda konstruktiv yechimini yomonlashtirmagan tarzda texnik-iqtisodiy ko‘rsatkichlarni yaxshilaydigan o‘zgartirishlar kiritish;
ishlab chiquvchidan zarur texnik maslahatlar olish huquqiga ega.
Pudratchilar:
qurilishni tasdiqlangan shaharsozlik hujjatlariga muvofiq amalga oshirishi;
shaharsozlik to‘g‘risidagi qonun hujjatlariga, shaharsozlik normalari va qoidalariga, shaharsozlik hujjatlarida qabul qilingan yechimlarga rioya etishi;
buyurtmachining texnik nazoratiga oid, ishlab chiquvchining mualliflik nazoratiga oid qonuniy talablarini hamda O‘zbekiston Respublikasi Qurilish vazirligi huzuridagi qurilish sohasidagi nazorat bo‘yicha hududiy inspeksiyalarning qonuniy talablarini bajarishi;
ishlab chiqarish jarayonida ishtirok etuvchi barcha xodimlarni mehnatni muhofaza qilish, texnika xavfsizligi anjomlari, jamoaviy va yakka tartibdagi himoya vositalari bilan ta’minlashi;
buyurtmachiga qurilishi tugallangan obyektda bajarilgan ishlarning tasdiqlangan shaharsozlik hujjatlari hamda shaharsozlik normalari va qoidalari talablariga muvofiqligi to‘g‘risidagi deklaratsiyani (bundan buyon matnda muvofiqlik deklaratsiyasi deb yuritiladi) taqdim etishi shart.
Pudratchilar qurilishning pirovard natijalari va pudrat shartnomasida belgilangan majburiyatlarga muvofiq shartnoma narxiga rioya etilishi uchun javobgar bo‘ladi.
Pudratchilar qonun hujjatlariga muvofiq boshqa huquqlarga ham ega bo‘lishi va ularning zimmasida boshqa majburiyatlar ham bo‘lishi mumkin.'),
        (942, 104, '2023-06-20 11:08:41', '2023-06-20 11:08:41', '18-modda. Shaharsozlik faoliyati sohasidagi davlat boshqaruvi
Shaharsozlik faoliyati sohasidagi davlat boshqaruvini O‘zbekiston Respublikasi Vazirlar Mahkamasi, shaharsozlik faoliyati sohasidagi maxsus vakolatli davlat organi va mahalliy ijro etuvchi hokimiyat organlari amalga oshiradi.
Shaharsozlik faoliyati sohasidagi maxsus vakolatli davlat organining o‘z vakolatlari doirasida qabul qilingan qarorlari barcha vazirliklar, davlat qo‘mitalari, idoralar hamda davlat boshqaruvining boshqa organlari, mahalliy ijro etuvchi hokimiyat organlari, shuningdek yuridik va jismoniy shaxslar uchun majburiydir.'),
        (943, 104, '2023-06-20 11:08:41', '2023-06-20 11:08:41', '19-modda. O‘zbekiston Respublikasi Vazirlar Mahkamasining shaharsozlik faoliyati sohasidagi vakolatlari
O‘zbekiston Respublikasi Vazirlar Mahkamasi:
shaharsozlik faoliyati sohasida yagona davlat siyosati amalga oshirilishini ta’minlaydi;
shaharsozlik sohasida kompleks davlat dasturlarini ishlab chiqadi va tasdiqlaydi hamda ularning amalga oshirilishiga ko‘maklashadi;
shaharsozlik faoliyati sohasida o‘z vakolatlari doirasida normativ-huquqiy hujjatlar qabul qiladi;
O‘zbekiston Respublikasi hududida aholini joylashtirish bosh tarhini hamda O‘zbekiston Respublikasi, Qoraqalpog‘iston Respublikasi va viloyatlar hududlarini rejalashtirish tarhlarini, umumdavlat ahamiyatiga molik alohida tartibga solinadigan shaharsozlik faoliyati obyektlarining shaharsozlik hujjatlarini, shuningdek O‘zbekiston Respublikasi hududini rivojlantirishning tarmoq tarhlarini hamda muhandislik, transportga oid va ijtimoiy infratuzilmalarni rivojlantirish loyihalarini tasdiqlaydi;
Toshkent va Nukus shaharlarining, shuningdek respublika va viloyat bo‘ysunuvidagi shaharlarning bosh rejalarini tasdiqlaydi;
shaharlar shahar atrofi zonalarining chegaralarini tasdiqlaydi;
umumdavlat ahamiyatiga molik alohida tartibga solinadigan shaharsozlik faoliyati obyektlarining chegaralarini hamda tegishli obyektlar hududlarida shaharsozlik faoliyatini tartibga solish tartibotini belgilaydi;
davlat shaharsozlik kadastrini hamda shaharsozlik faoliyati obyektlarining monitoringini yuritish tartibini belgilaydi;
umumdavlat ahamiyatiga molik obyektlarning shaharsozlik hujjatlari ishlab chiqilishini, shaharsozlik faoliyati sohasidagi ilmiy-tadqiqot ishlarini, shuningdek shaharsozlik normalari va qoidalari ishlab chiqilishini moliyalashtirish tartibini belgilaydi;
shaharsozlik faoliyati sohasidagi ayrim faoliyat turlarini litsenziyalash va shaharsozlik hujjatlarini ekspertizadan o‘tkazadigan yuridik shaxslarni akkreditatsiya qilish tartibini belgilaydi;
shaharsozlik hujjatlarining ekspertizasini tashkil etish hamda o‘tkazish tartibini belgilaydi;
shaharsozlik faoliyati sohasida davlat xaridlarini tashkil etish hamda o‘tkazish tartibini belgilaydi;
shaharsozlik to‘g‘risidagi qonun hujjatlariga rioya etilishi ustidan davlat nazoratini amalga oshirish tartibini belgilaydi;
shaharsozlik faoliyati sohasidagi davlat boshqaruvi organlarining tashkiliy tuzilmasini belgilaydi;
O‘zbekiston Respublikasi Vazirlar Mahkamasi huzuridagi Respublika arxitektura-shaharsozlik kengashi faoliyatini muvofiqlashtiradi va tashkil etadi.
O‘zbekiston Respublikasi Vazirlar Mahkamasi qonun hujjatlariga muvofiq boshqa vakolatlarni ham amalga oshirishi mumkin.'),
        (944, 104, '2023-06-20 11:08:41', '2023-06-20 11:08:41', '20-modda. Shaharsozlik faoliyati sohasidagi maxsus vakolatli davlat organi
O‘zbekiston Respublikasi Qurilish vazirligi shaharsozlik faoliyati sohasidagi maxsus vakolatli davlat organidir (bundan buyon matnda maxsus vakolatli davlat organi deb yuritiladi).
Maxsus vakolatli davlat organi:
shaharsozlik faoliyati sohasida yagona davlat siyosatini amalga oshiradi;
O‘zbekiston Respublikasi hududida aholini joylashtirish bosh tarhi ishlab chiqilishini, Qoraqalpog‘iston Respublikasi, viloyatlar (viloyatlar guruhlari) hududlarini rejalashtirish tarhlarini va umumdavlat ahamiyatiga molik alohida tartibga solinadigan shaharsozlik faoliyati obyektlarining shaharsozlik hujjatlari ishlab chiqilishi, shuningdek shaharsozlik faoliyati sohasidagi ilmiy-tadqiqot ishlari tashkil etilishini ta’minlaydi;
shaharlarning, shaharchalarning bosh rejalarini, bundan tasdiqlanishi O‘zbekiston Respublikasi Vazirlar Mahkamasining vakolatlari jumlasiga kiritilgan shaharlarning bosh rejalari mustasno, hamda Toshkent shahrining markaziy qismlarini, viloyatlarning ma’muriy markazlarini, shuningdek madaniy meros obyektlariga ega bo‘lgan shaharlarni batafsil rejalashtirish loyihalarini tasdiqlaydi;
umumdavlat ahamiyatiga molik alohida tartibga solinadigan shaharsozlik faoliyati obyektlari uchun chegaralarni belgilash haqida O‘zbekiston Respublikasi Vazirlar Mahkamasiga takliflar kiritadi;
shaharsozlik sohasidagi normativ-huquqiy hujjatlarni, shaharsozlik normalari va qoidalarini ishlab chiqadi hamda tasdiqlaydi;
vazirliklar, davlat qo‘mitalari va idoralar tomonidan taqdim etilgan idoraviy qurilish normalarini kelishib oladi;
shaharsozlik, arxitektura, loyihalashtirish va qurilish sohasida malakali kadrlarni tayyorlashga, ixtisoslashgan davlat ta’lim muassasalarida hamda nodavlat ta’lim tashkilotlarida ta’lim-tarbiya jarayonining darajasi va sifatini oshirishga, qurilish sohasida, shu jumladan xorijdagi yetakchi ilmiy-tadqiqot muassasalarida loyihachilarni hamda mutaxassislarni qayta tayyorlashga va ularning malakasini oshirishga ko‘maklashadi;
Oldingi tahrirga qarang.
shaharsozlik to‘g‘risidagi qonun hujjatlariga, shaharsozlik normalari va qoidalariga, milliy standartlarga, qurilish ishlari texnologiyalariga, qurilish materiallari va buyumlari sifatiga rioya etilishi ustidan davlat nazoratini amalga oshiradi;
(20-modda ikkinchi qismining to‘qqizinchi xatboshisi O‘zbekiston Respublikasining 2022-yil 3-noyabrdagi O‘RQ-800-sonli Qonuni tahririda — Qonunchilik ma’lumotlari milliy bazasi, 04.11.2022-y., 03/22/800/0990-son — 2023-yil 5-noyabrdan kuchga kiradi)
shaharsozlik hujjatlarini ekspertizadan o‘tkazish bo‘yicha yagona siyosatni amalga oshiradi va ekspertiza xulosalarining yagona reyestrini yuritadi;
qurilishda standartlashtirish ishlarini amalga oshiradi;
davlat shaharsozlik kadastri va shaharsozlik faoliyati obyektlarining monitoringi yuritilishini amalga oshiradi;
aholi punktlarining bosh rejalari ishlab chiqilishini tashkil etadi;
shaharsozlik hujjatlarining amalga oshirilishi yuzasidan monitoring olib borish tartibini belgilaydi;
shaharsozlik faoliyatining ayrim turlarini amalga oshirish uchun litsenziyalar beradi;
shaharsozlik hujjatlarining ekspertizasini amalga oshiruvchi yuridik shaxslarni akkreditatsiyadan o‘tkazadi;
qonun hujjatlariga muvofiq malakasi bo‘lishi majburiy bo‘lgan, loyihalashtirish va qurilish sohasidagi mutaxassislarni sertifikatlashtiradigan yuridik shaxslarni akkreditatsiyadan o‘tkazadi;
shaharsozlik faoliyati sohasida qabul qilinayotgan qarorlar to‘g‘risida aholini muntazam ravishda xabardor qiladi;
o‘z vakolatlari jumlasiga kiritilgan masalalar yuzasidan shaharsozlik faoliyati subyektlari o‘rtasidagi kelishmovchiliklarni ko‘rib chiqadi;
shaharsozlik faoliyati obyektlarini ro‘yxatga oladi;
texnik nazoratni amalga oshirish bo‘yicha buyurtmachining va mualliflik nazoratini amalga oshirish bo‘yicha ishlab chiquvchining ishlarini tekshiradi, shuningdek texnik va mualliflik nazorati sifatsiz amalga oshirilgan taqdirda, ularga nisbatan javobgarlik choralarini qo‘llash to‘g‘risida vakolatli organlarga takliflar kiritadi;
shaharsozlik sohasidagi normativ hujjatlar buzilganligi aniqlangan taqdirda, qurilish materiallari va buyumlarining ishlab chiqarilishi, realizatsiya qilinishi hamda qo‘llanilishini, shaharsozlik normalari va qoidalari hamda tasdiqlangan loyiha yechimlari talablari chidamlilik va puxtalik xususiyatlari pasayishiga sabab bo‘lgan darajada buzilganda, shuningdek binolar va inshootlarning avariya xavfi bo‘lgan, obyektlar o‘zboshimchalik bilan qurilgan taqdirda qurilish-montaj ishlarini davom ettirishni to‘xtatib turadi. Qurilish materiallari va buyumlarini ishlab chiqarishni, realizatsiya qilishni hamda qo‘llashni, qurilish-montaj ishlarini davom ettirishni tadbirkorlik subyektining faoliyatini to‘xtatib turishga olib keladigan tarzda to‘xtatib turish sud tartibida amalga oshiriladi, bundan favqulodda vaziyatlar, epidemiyalar hamda aholining hayoti va sog‘lig‘i uchun boshqa real tahdid yuzaga kelishining oldini olish bilan bog‘liq holda faoliyatni o‘n ish kunidan ko‘p bo‘lmagan muddatga to‘xtatib turish hollari mustasno;
shaharsozlik faoliyati sohasidagi huquqbuzarliklar to‘g‘risidagi ma’muriy ishlarni ko‘rib chiqadi va jarima tarzidagi ma’muriy jazo beradi;
qurilishi tugallangan obyektlarni foydalanishga qabul qilib olish bo‘yicha komissiyalar ishida ishtirok etadi.
Maxsus vakolatli davlat organi qonun hujjatlariga muvofiq boshqa vakolatlarni ham amalga oshirishi mumkin.'),
        (980, 109, '2023-06-20 11:08:42', '2023-06-20 11:08:42', '56-modda. Aholi punktlari hududlarini rivojlantirishning tarmoq tarhlari
Aholi punktlari hududlarini rivojlantirishning tarmoq tarhlarida:
energiya ta’minotini, gaz ta’minotini, aloqani, irrigatsiyani, suv ta’minotini va kanalizatsiya tarmoqlarini rivojlantirish;
transport turlarini rivojlantirish;
iqtisodiyot tarmoqlarining obyektlarini joylashtirish;
hududlarni xavfli geologik va gidrogeologik jarayonlardan himoya qilish;
aholining fuqaro muhofazasi;
atrof-muhitni muhofaza qilish;
madaniy meros obyektlarini muhofaza qilish;
hududlarni obodonlashtirish va ko‘kalamzorlashtirish;
hududlarni rivojlantirishning boshqa masalalari bo‘yicha asosiy ko‘rsatkichlar hamda rejalashtirish yechimlari belgilanadi.'),
        (956, 106, '2023-06-20 11:08:41', '2023-06-20 11:08:41', '32-modda. Obyektlarni qabul qilish va ularni foydalanishga topshirish
Qurilishi tugallangan obyektni foydalanishga qabul qilish buyurtmachi tomonidan tasdiqlangan shaharsozlik hujjatlariga muvofiq to‘liq tayyor bo‘lganda va ijro hujjatlari, muvofiqlik deklaratsiyasi, bajarilgan ishlarning tasdiqlangan shaharsozlik hujjatlariga muvofiqligi va qurilish-montaj ishlarining sifati to‘g‘risidagi xulosalar mavjud bo‘lganda amalga oshiriladi.
Davlat sirlari yoki qonun bilan qo‘riqlanadigan boshqa sir bilan bog‘liq qurilishi tugallangan obyektlarni, shuningdek davlat ahamiyatiga ega obyektlarni qabul qilish O‘zbekiston Respublikasi Vazirlar Mahkamasi tomonidan tuziladigan davlat qabul komissiyasi (bundan buyon matnda komissiya deb yuritiladi) tomonidan amalga oshiriladi. Bunda komissiya ishida buyurtmachi, ishlab chiquvchi va pudratchining vakillari majburiy tarzda ishtirok etishi kerak.
Qurilishi tugallangan obyektni qabul qilish pudratchi va buyurtmachi o‘rtasida imzolanadigan, qurilishi tugallangan obyektni topshirish-qabul qilish dalolatnomasi bilan rasmiylashtiriladi. Komissiya tuzilgan hollarda, qurilishi tugallangan obyektni topshirish-qabul qilish dalolatnomasi komissiya a’zolari, buyurtmachi, ishlab chiquvchi, pudratchi hamda texnik va mualliflik nazoratini amalga oshiruvchi shaxslar tomonidan imzolanadi.
Qurilishi tugallangan obyektni topshirish-qabul qilish dalolatnomasi shakli maxsus vakolatli davlat organi tomonidan tasdiqlanadi.
Qurilishi tugallangan obyektni qabul qilish ishtirokchilarining vazifalariga quyidagilar kiradi:
qurilishi tugallangan obyektning tayyorligini belgilash va hujjatli tasdiqlash;
bajarilgan qurilish-montaj ishlarining tasdiqlangan shaharsozlik hujjatlariga, shaharsozlik normalari va qoidalari talablariga muvofiqligini baholash;
texnologik, muhandislik yoki boshqa uskunalarni (muhandislik tizimlarini) sinovdan o‘tkazish;
obyektning ishga tushiriladigan quvvatining (sig‘imi, o‘tkazuvchanligining) shaharsozlik hujjatlarida tasdiqlangan ko‘rsatkichlarga muvofiqligini belgilash.
Tasdiqlangan loyiha yechimlari hamda shaharsozlik normalari va qoidalari buzilishlari aniqlangan taqdirda, shuningdek texnik va mualliflik nazoratini amalga oshiruvchi shaxslarning salbiy xulosalari mavjud bo‘lganda buyurtmachi (komissiya) pudratchi buzilishlarni bartaraf etgandan so‘ng obyektni foydalanishga qabul qiladi.
Qurilishi tugallangan obyektni foydalanishga topshirish hududiy inspeksiya tomonidan qurilishi tugallangan obyektdan foydalanish uchun ruxsatnoma berilishi orqali amalga oshiriladi.
Quyidagilar qurilishi tugallangan obyektdan foydalanishga ruxsatnoma berish uchun asos bo‘ladi:
qurilishi tugallangan obyektni topshirish-qabul qilish dalolatnomasi;
obyektning ijro hujjatlari to‘plami;
pudratchi tomonidan berilgan muvofiqlik deklaratsiyasi;
mualliflik nazoratini amalga oshiruvchi shaxs tomonidan berilgan bajarilgan ishlarning tasdiqlangan shaharsozlik hujjatlariga muvofiqligi to‘g‘risidagi xulosa;
texnik nazoratni amalga oshiruvchi shaxs tomonidan berilgan qurilish-montaj ishlarining sifati to‘g‘risidagi xulosa.
Hududiy inspeksiya tomonidan qurilishi tugallangan obyektdan foydalanishga ruxsatnoma berilgan sana obyekt foydalanishga topshirilgan sana hisoblanadi.
Qurilgan obyektdan qurilishi tugallangan obyektni topshirish-qabul qilish to‘g‘risidagi tasdiqlangan dalolatnomasiz va hududiy inspeksiyaning qurilishi tugallangan obyektdan foydalanish uchun ruxsatnomasisiz foydalanishga yo‘l qo‘yilmaydi.
Muvofiqlik deklaratsiyasining, qurilish-montaj ishlarining sifati va bajarilgan ishlarning tasdiqlangan shaharsozlik hujjatlariga muvofiqligini to‘g‘risidagi xulosalarning buyurtmachiga taqdim etilishi ishlab chiquvchini, pudratchini, texnik va mualliflik nazoratini amalga oshiruvchi shaxslarni obyektni loyihalashtirish, qurish, qabul qilish va foydalanishga topshirishda bajarilgan ishlar uchun javobgarlikdan ozod qilmaydi.
Buyurtmachi qurilishi tugallangan obyektni o‘z vaqtida topshirish-qabul qilish va uni foydalanishga topshirish uchun javobgar bo‘ladi.
Qurilishi tugallangan obyektni qabul qilish va uni foydalanishga topshirish bo‘yicha tartib-taomillarni o‘tkazish tartibi va muddatlari O‘zbekiston Respublikasi Vazirlar Mahkamasi tomonidan belgilanadi.'),
        (945, 104, '2023-06-20 11:08:41', '2023-06-20 11:08:41', '21-modda. Qoraqalpog‘iston Respublikasi Vazirlar Kengashining, viloyatlar va Toshkent shahar ijro etuvchi hokimiyat organlarining shaharsozlik faoliyati sohasidagi vakolatlari
Qoraqalpog‘iston Respublikasi Vazirlar Kengashi, viloyatlar va Toshkent shahar ijro etuvchi hokimiyat organlari o‘z vakolatlari doirasida:
shaharsozlik to‘g‘risidagi qonun hujjatlariga rioya etilishi ustidan davlat nazoratini amalga oshiradi, shuningdek shaharsozlik faoliyati obyektlarining but saqlanishini ta’minlaydi;
shaharlar hududlarining ijtimoiy-iqtisodiy rivojlanish ko‘rsatkichlari va istiqboldagi urbanizatsiya jarayonlari asosida aholini oqilona joylashtirish, muhandislik, transportga oid va ijtimoiy infratuzilmalarni rivojlantirish masalalarini hal qiladi;
mahalliy ahamiyatga molik obyektlarni qurishga doir shaharsozlik hujjatlarini ishlab chiqish bo‘yicha buyurtmachi vazifalarini bajaradi va bu obyektlarning moliyalashtirilishini ta’minlaydi;
shaharsozlik faoliyati obyektlarini qurish to‘g‘risida qarorlar qabul qiladi;
o‘z hududidagi shaharsozlik faoliyatini, agar bu faoliyat shaharsozlik to‘g‘risidagi qonun hujjatlari talablariga mos kelmasa, cheklaydi, to‘xtatib turadi yoki taqiqlaydi. Tadbirkorlik subyektlarining shaharsozlik faoliyatini cheklash, to‘xtatib turish va taqiqlash sud tartibida amalga oshiriladi, bundan favqulodda vaziyatlar, epidemiyalar hamda aholining hayoti va sog‘lig‘i uchun boshqa real tahdid yuzaga kelishining oldini olish bilan bog‘liq holda faoliyatni o‘n ish kunidan ko‘p bo‘lmagan muddatga cheklash, to‘xtatib turish hollari mustasno;
reklama va axborot obyektlarini (konstruksiyalarini) joylashtirish masalalariga doir shaharsozlik faoliyatini nazorat qiladi hamda rejalashtiradi, shu jumladan reklama va axborot obyektlari (konstruksiyalari) qurilishi uchun ajratiladigan yer uchastkalari ro‘yxatini shakllantiradi, ularni pasportlashtirishni amalga oshiradi;
aholi punktlarining bosh rejalarini ishlab chiqishni tashkil etadi va ularning jamoatchilik muhokamasini ta’minlaydi;
qishloq aholi punktlarining bosh rejalarini, aholi punktlari hududlarining qismlarini batafsil rejalashtirish loyihalarini (bundan buyon matnda batafsil rejalashtirish loyihasi deb yuritiladi) hamda mavzelarni, kichik dahalarni va aholi punktlari rejalashtirilgan tuzilmasining elementlarini qurish loyihalarini (bundan buyon matnda qurish loyihasi deb yuritiladi) tasdiqlash uchun tegishli hokimiyat vakillik organlariga kiritadi;
viloyatlar hududlarini rejalashtirish tarhlari, muhandislik, transportga oid va ijtimoiy infratuzilmalarni rivojlantirish, shuningdek obodonlashtirish tarhlari va loyihalari ishlab chiqilishi hamda amalga oshirilishini tashkil etadi;
tumanni (tumanlar guruhlarini) rejalashtirish loyihalarini tasdiqlash uchun tegishli vakillik organlariga kiritadi;
mahalliy ahamiyatga molik alohida tartibga solinadigan shaharsozlik faoliyati obyektlarining chegaralarini hamda tegishli obyektlar hududlarida shaharsozlik faoliyatini tartibga solish tartibotini belgilaydi;
aholi punktlarini qurish qoidalari loyihalarini tasdiqlash uchun tegishli hokimiyat vakillik organlariga kiritadi;
aholi punktlari shaharsozlik hujjatlarining, shaharsozlik faoliyati sohasidagi ilmiy tadqiqotlarning ishlab chiqilishi va moliyalashtirilishini, davlat shaharsozlik kadastri, shaharsozlik faoliyati obyektlarining monitoringi yuritilishini, kompleks muhandislik qidiruvlari o‘tkazilishini, aholi punktlarini mikroseysmik hududlashtirish xaritalari tuzilishini, shaharsozlik hujjatlari amalga oshirilishi yuzasidan monitoring olib borilishini va bu hujjatlarning ekspertizadan o‘tkazilishini ta’minlaydi;
shaharsozlik faoliyati sohasida qabul qilinayotgan qarorlar to‘g‘risida aholini muntazam ravishda xabardor qiladi;
o‘zboshimchalik bilan qurilgan imoratlarning qonun hujjatlarida belgilangan tartibda buzib tashlanishini tashkil etadi.
Qoraqalpog‘iston Respublikasi Vazirlar Kengashi, viloyatlar va Toshkent shahar ijro etuvchi hokimiyat organlari qonun hujjatlariga muvofiq boshqa vakolatlarni ham amalga oshirishi mumkin.'),
        (946, 104, '2023-06-20 11:08:41', '2023-06-20 11:08:41', '22-modda. Tumanlar (shaharlar) ijro etuvchi hokimiyat organlarining shaharsozlik faoliyati sohasidagi vakolatlari
Tumanlar (shaharlar) ijro etuvchi hokimiyat organlari:
shaharsozlik to‘g‘risidagi qonun hujjatlariga rioya etilishi ustidan nazoratni amalga oshiradi, shuningdek shaharsozlik faoliyati obyektlarining but saqlanishini ta’minlaydi;
shaharlar hududlarining ijtimoiy-iqtisodiy rivojlanish ko‘rsatkichlari va istiqboldagi urbanizatsiya jarayonlari asosida aholini oqilona joylashtirish, muhandislik, transportga oid va ijtimoiy infratuzilmalarni rivojlantirish masalalarini hal qiladi;
mahalliy ahamiyatga molik obyektlarni qurishga doir shaharsozlik hujjatlarini ishlab chiqish bo‘yicha buyurtmachi vazifalarini bajaradi va bu obyektlarning moliyalashtirilishini ta’minlaydi;
o‘z hududidagi shaharsozlik faoliyatini, agar bu faoliyat shaharsozlik to‘g‘risidagi qonun hujjatlari talablariga mos kelmasa, cheklaydi, to‘xtatib turadi yoki taqiqlaydi. Tadbirkorlik subyektlarining shaharsozlik faoliyatini cheklash, to‘xtatib turish va taqiqlash sud tartibida amalga oshiriladi, bundan favqulodda vaziyatlar, epidemiyalar hamda aholining hayoti va sog‘lig‘i uchun boshqa real tahdid yuzaga kelishining oldini olish bilan bog‘liq holda faoliyatni o‘n ish kunidan ko‘p bo‘lmagan muddatga cheklash, to‘xtatib turish hollari mustasno;
o‘zboshimchalik bilan qurilgan imoratlarni buzib tashlash to‘g‘risida sudga da’vo arizasi bilan murojaat qiladi;
tumanlarni (tumanlar guruhlarini) rejalashtirish loyihalarini, aholi punktlarining bosh rejalarini, batafsil rejalashtirish loyihalarini, qurish loyihalarini ishlab chiqishda ishtirok etadi hamda ularning amalga oshirilishini tashkil etadi;
aholi punktlarini qurish qoidalari loyihalarini tasdiqlash uchun tegishli hokimiyat vakillik organlariga kiritadi;
shaharsozlik faoliyati obyektlarining qurilishi to‘g‘risida qarorlar qabul qiladi;
reklama va axborot obyektlari (konstruksiyalari) masalalariga doir shaharsozlik faoliyatini nazorat qiladi hamda rejalashtiradi, shu jumladan reklama va axborot obyektlari (konstruksiyalari) qurilishi uchun ajratiladigan yer uchastkalari ro‘yxatini shakllantiradi, ularni pasportlashtirishni amalga oshiradi;
shaharsozlik faoliyati obyektlarining texnik holati inventarizatsiyadan o‘tkazilishini tashkil etadi;
shaharsozlik faoliyati sohasida qabul qilinayotgan qarorlar to‘g‘risida ommaviy axborot vositalari va Internet tarmog‘idagi o‘zining rasmiy veb-saytlari orqali aholini muntazam ravishda xabardor qiladi.
Ushbu modda birinchi qismining sakkizinchi xatboshisi qoidalari shaharlar tarkibiga kiruvchi tumanlarning vakillik hokimiyati organlariga nisbatan tatbiq etilmaydi.
Tumanlar (shaharlar) vakillik hokimiyati organlari qonun hujjatlariga muvofiq boshqa vakolatlarni ham amalga oshirishi mumkin.'),
        (947, 105, '2023-06-20 11:08:41', '2023-06-20 11:08:41', '23-modda. Shaharsozlik hujjatlari ekspertizasini o‘tkazish
Shaharsozlik hujjatlari ekspertizasi maxsus vakolatli davlat organining ixtisoslashtirilgan tarkibiy bo‘linmalari, bunga vakolatli bo‘lgan vazirliklar, davlat qo‘mitalari va idoralarining ixtisoslashtirilgan tarkibiy bo‘linmalari, ekspert komissiyalari va ekspert guruhlari, shuningdek akkreditatsiya qilingan yuridik shaxslar (bundan buyon matnda ekspert tashkilotlari deb yuritiladi) tomonidan o‘tkaziladi.
Alohida tartibga solinadigan shaharsozlik faoliyati obyektlarining shaharsozlik hujjatlari ekspertizasi maxsus vakolatli davlat organining ixtisoslashtirilgan tarkibiy bo‘linmalari tomonidan o‘tkaziladi.
Shaharsozlik hujjatlari shaharsozlik to‘g‘risidagi qonun hujjatlari talablariga, shu jumladan shaharsozlik, yong‘inga qarshi hamda ekologik normalar va qoidalarga, shuningdek sanitariya qoidalari va normalariga, gigiyena normativlariga muvofiqligi yuzasidan ekspertizadan o‘tkazilishi lozim.
Shaharsozlik faoliyati obyektlarini qurish uchun shaharsozlik hujjatlari zilzilaga bardoshlilik va yong‘in xavfsizligi yuzasidan majburiy ekspertizadan o‘tkazilishi lozim.
To‘g‘ridan-to‘g‘ri investitsiyalar, shu jumladan chet el investitsiyalari hisobidan amalga oshiriladigan obyektlar bo‘yicha shaharsozlik hujjatlarining smeta qismini ekspertizadan o‘tkazish talab etilmaydi.
Quyidagi obyektlarning shaharsozlik hujjatlari ekspertizasi majburiy emas:
mavsumiy ishlar uchun vaqtinchalik va maishiy binolar;
uch yuz kub metrdan ortiq bo‘lmagan alohida qurilayotgan kichik hajmdagi obyektlar;
ikki qavatdan yuqori bo‘lmagan (tsokolni hisobga olmagan holda), balandligi yer yuzasidan 12 metrdan va (yoki) umumiy maydoni 500 kvadrat metrdan ortiq bo‘lmagan yakka tartibdagi uy-joylar;
I xavf-xatar toifasiga kiradigan obyektlar;
binolarni, inshootlarni va boshqa obyektlar joriy ta’mirlanganda.
Loyiha yechimlariga o‘zgartirishlar kiritilgan taqdirda, shu jumladan qurilayotgan yoki mavjud obyektlarning konstruksiyasi o‘zgarganda (qavatlar soni o‘zgarganda, yonma-yon qurilganda, ustiga qavat qurilganda) shaharsozlik hujjatlari kiritilayotgan o‘zgartirishlar qismida qayta ekspertizadan o‘tkaziladi.
Ilgari ekspertizaning ijobiy xulosalari berilgan yakka tartibdagi va namunaviy loyihalar takroran qo‘llanilganda ekspertiza ushbu loyihalarni aniq bir joyga va shartlarga bog‘lash qismida amalga oshiriladi.
Buyurtmachi zimmasiga shaharsozlik hujjatlarining to‘liq to‘plamini ekspertizadan o‘tkazish uchun taqdim etish va ularning ishonchliligini ta’minlash majburiyati yuklanadi.
Shaharsozlik hujjatlarining ekspertizasi buyurtmachilar va ekspert tashkilotlari tomonidan tuziladigan shartnomalar asosida amalga oshiriladi, bunda xarajatlar ko‘rib chiqilayotgan loyihani ishlab chiqish qiymatiga kiritiladi.
Ekspertizaning ijobiy xulosalari (bundan davlat sirlari yoki qonun bilan muhofaza qilinadigan boshqa sirlarni o‘z ichiga olgan shaharsozlik hujjatlariga doir xulosalar mustasno) ular ekspertiza xulosalarining yagona reyestriga kiritilgan taqdirdagina haqiqiy deb tan olinadi.
Ekspertizaning ijobiy xulosalari shaharsozlik hujjatlarini kelishib olish, tasdiqlash va kelgusida amalga oshirish uchun asos bo‘ladi.
Shaharsozlik hujjatlarini ekspertizadan o‘tkazish tartibi va muddatlari O‘zbekiston Respublikasi Vazirlar Mahkamasi tomonidan belgilanadi.'),
        (948, 105, '2023-06-20 11:08:41', '2023-06-20 11:08:41', '24-modda. Ekspert tashkilotlarining vakolatlari
Ekspert tashkilotlari:
qabul qilingan arxitektura-rejalashtirish va shaharsozlik yechimlarining shaharsozlik normalari hamda qoidalariga muvofiqligini tekshiradi;
shaharsozlik normalari va qoidalarining majburiy talablariga, shu jumladan energiya jihatidan samarador hamda energiyani tejaydigan texnik, texnologik va innovatsion loyiha yechimlarini qo‘llash bo‘yicha majburiy talablariga rioya etilishini tekshiradi;
ekspertiza natijalari yuzasidan xulosalar taqdim etadi;
bajarilgan loyiha-qidiruv ishlarining sifatiga baho beradi.
Ekspert tashkilotlari qonun hujjatlariga muvofiq boshqa vakolatlarni ham amalga oshirishi mumkin.'),
        (949, 105, '2023-06-20 11:08:41', '2023-06-20 11:08:41', '25-modda. Shaharsozlik hujjatlari ekspertlarining huquqlari va majburiyatlari
Shaharsozlik hujjatlari ekspertlari:
hududlarni shaharsozlik jihatidan rejalashtirish loyihalari bo‘yicha kompleks shaharsozlik ekspertizasida ekspert komissiyalari (ekspert guruhlari) tarkibida ishtirok etishga;
buyurtmachilardan va ishlab chiquvchilardan zarur materiallarni hamda axborotni so‘rash va olishga;
shaharsozlik faoliyatining professional ixtisoslashuviga muvofiq loyihalarning alohida bo‘limlari (qismlari) bo‘yicha ekspertizani amalga oshirish, ular bo‘yicha mahalliy ekspertiza xulosalarini tuzishga. Ayrim bo‘limlar (qismlar) bo‘yicha ekspertizaning lokal xulosalari shaharsozlik hujjatlarini yaxlit tarzda tasdiqlash uchun asos hisoblanmaydi va ekspertizaning jamlanma xulosasini tuzishda foydalaniladi;
buyurtmachi ekspertiza xulosasida qayd etilgan shartlarni (talablarni) bajarmagan hollarda ekspert tashkiloti tomonidan ilgari berilgan ekspertiza xulosasini chaqirib olinishini taklif qilishga haqlidir.
Shaharsozlik hujjatlari ekspertlariga quyidagilar taqiqlanadi:
mazkur ekspert tayyorlanishida bevosita yoki bilvosita ishtirok etgan loyihalar yuzasidan yoxud er (xotin) va (yoki) boshqa yaqin qarindoshlar ishtirokida bajarilgan loyihalar yuzasidan ekspertiza o‘tkazish;
shaharsozlik faoliyatining subyektlari bilan mehnatga oid, moliyaviy jihatdan va (yoki) boshqacha tobelik munosabatlarida bo‘lish.
Shaharsozlik hujjatlari ekspertlari:
shaharsozlik hujjatlarining ko‘rib chiqilayotgan loyihalari bo‘yicha davlat sirlarining va qonun bilan qo‘riqlanadigan boshqa sirning maxfiyligiga rioya etishi hamda saqlanishini ta’minlashi;
o‘z kasbiy malakasini oshirib borishi;
kasbiy odob-axloq qoidalariga rioya etishi shart.
Shaharsozlik hujjatlari ekspertlari qonun hujjatlariga muvofiq boshqa huquqlar ham ega bo‘lishi va ularning zimmasida boshqa majburiyatlar ham bo‘lishi mumkin.
Ekspertiza sifatsiz o‘tkazilganligi hollari aniqlangan taqdirda ekspertlar va ekspert tashkilotlari qonun hujjatlariga muvofiq javobgar bo‘ladi.
Ekspertlar va ekspert tashkilotlari o‘z faoliyatida shaharsozlik faoliyati subyektlaridan mustaqildir. Shaharsozlik hujjatlarini ko‘rib chiqish va ular bo‘yicha ekspertiza xulosalarini tayyorlash vaqtida hech kim ekspertlar yoki ekspert tashkilotlari ishiga aralashishga haqli emas.'),
        (1247, 122, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '4. Obyekt egasi o‘zgarganda (begonalashtirilganda), obyektni qurish (rekonstruksiya qilish) bo‘yicha kelishilgan loyiha-smeta hujjatlari o‘zgarmasa, loyiha hujjatlari qayta kelishilishi talab etilmaydi.'),
        (951, 106, '2023-06-20 11:08:41', '2023-06-20 11:08:41', '27-modda. O‘zbekiston Respublikasi Qurilish vazirligi huzuridagi Qurilish sohasidagi nazorat inspeksiyasining va uning hududiy inspeksiyalarining vakolatlari
Shaharsozlik faoliyati sohasidagi davlat nazorati O‘zbekiston Respublikasi Qurilish vazirligi huzuridagi Qurilish sohasidagi nazorat inspeksiyasi (bundan buyon matnda Inspeksiya deb yuritiladi) va uning hududiy inspeksiyalari (bundan buyon matnda hududiy inspeksiyalar deb yuritiladi) tomonidan amalga oshiriladi.
Inspeksiya va hududiy inspeksiyalar o‘z vakolatlari doirasida:
mahalliy davlat hokimiyati organlariga va maxsus vakolatli davlat organining hududiy bo‘linmalariga shaharsozlik to‘g‘risidagi qonun hujjatlarini buzgan holda qabul qilingan qarorlarni bekor qilish haqida taqdimnomalar kiritadi;
binolar va inshootlarning zilzilabardoshliligiga ta’sir qiluvchi, shuningdek fuqaro hayoti va sog‘lig‘iga tahdid soluvchi konstruktiv qoidabuzarliklar aniqlangan hollarda qurilish-montaj ishlarini to‘xtatib turadi hamda demontaj ishlari amalga oshirilishini talab qiladi;
qonun hujjatlarida nazarda tutilgan qurilish sohasida ruxsat berish tartib-taomillariga rioya qilinmagan va aniqlangan huquqbuzarliklarni bartaraf etish bo‘yicha kamida ikkita ko‘rsatma bajarilmagan hollarda qurilish-montaj ishlarini to‘xtatib turadi va demontaj ishlarini olib borish to‘g‘risidagi da’vo bilan sudga murojaat qiladi;
yuridik va jismoniy shaxslarga shaharsozlik faoliyati sohasida aniqlangan qoidabuzarliklarni bartaraf etish bo‘yicha bajarilishi majburiy bo‘lgan ko‘rsatmalarni, ularni bajarishning shaharsozlik faoliyati obyektidagi huquqbuzarliklarni bartaraf etish uchun zarur bo‘lgan mutanosib muddatlarini, lekin ko‘pi bilan oltmish kunni belgilagan holda, beradi;
shaharsozlik faoliyatini amalga oshirishga doir litsenziyalarning hamda ruxsat beruvchi hujjatlarning amal qilishini to‘xtatib turish, tugatish yoki ularni bekor qilish to‘g‘risidagi masalani hal etish uchun tegishli davlat organlari hamda tashkilotlarga taqdimnomalar yuboradi;
shaharsozlik faoliyati obyektlarida binolar hamda inshootlarning zilzilabardoshliligiga ta’sir qiluvchi, shuningdek inson hayoti va sog‘lig‘iga tahdid soluvchi sifatsiz qurilish materiallari, konstruksiyalari hamda buyumlari qo‘llanilganligi aniqlangan hollarda, qurilish materiallarini, konstruksiyalarini va buyumlarini ishlab chiqarishni, realizatsiya qilish hamda qo‘llashni to‘xtatib turish haqida tegishli davlat organlari va tashkilotlarga taqdimnomalar yuboradi;
qurilayotgan obyektlarga moneliksiz kiradi va davlat nazorati vazifalarini amalga oshiradi;
yuridik va jismoniy shaxslar tomonidan shaharsozlik to‘g‘risidagi qonun hujjatlari, shaharsozlik normalari hamda qoidalari talablariga, qurilishda qo‘llaniladigan qurilish materiallari, konstruksiyalari va buyumlarining texnik jihatdan tartibga solish sohasidagi normativ hujjatlar talablariga muvofiq bo‘lishiga rioya etilishi ustidan, shuningdek texnik va mualliflik nazorati yuritilishi ustidan tanlab o‘tkaziladigan davlat nazoratini amalga oshiradi;
shaharsozlik to‘g‘risidagi qonun hujjatlari buzilgan taqdirda ma’muriy bayonnomalarni rasmiylashtiradi va ma’muriy huquqbuzarliklar to‘g‘risidagi ishlarni ko‘rib chiqadi;
o‘z vakolatlariga kiradigan masalalarni hal etish uchun zarur bo‘lgan materiallarni, ma’lumotlar va axborotni vazirliklar, davlat qo‘mitalari, idoralar hamda mahalliy ijro etuvchi hokimiyat organlaridan so‘raydi va oladi;
mahalliy ijro etuvchi hokimiyat organlarining, nodavlat notijorat tashkilotlarining vakillarini, ilmiy-texnik va ilmiy-tadqiqot tashkilotlarining ekspertlari hamda mutaxassislarini davlat nazorati tartibida o‘tkaziladigan tekshiruvlarga jalb etadi;
qurilish obyektlaridagi avariyalar holatlarini tekshirish bo‘yicha idoralararo komissiyalar ishida ishtirok etadi;
qurilish-montaj ishlarini amalga oshiruvchi yuridik va jismoniy shaxslarga konstruksiyalarning ayrim elementlarini, uzellar va detallarni ochish va qayta yopib qo‘yish, poydevorlar ostidagi tuproqni, qo‘llanilgan qurilish materiallarini, konstruksiyalarini, buyumlarini hamda muhandislik kommunikatsiyalarini sinash yuzasidan topshiriqlar beradi, shuningdek tegishli xulosa tayyorlash uchun ekspertlar va mutaxassislarni jalb qiladi.
Inspeksiya va hududiy inspeksiyalar qonun hujjatlariga muvofiq boshqa vakolatlarni ham amalga oshirishi mumkin.'),
        (952, 106, '2023-06-20 11:08:41', '2023-06-20 11:08:41', '28-modda. Texnik nazorat
Shaharsozlik faoliyati obyektlarini qurish va rekonstruksiya qilishda texnik nazorat buyurtmachi tomonidan texnik nazorat bo‘yicha mutaxassisni biriktirish yoki ushbu faoliyat turini amalga oshirish huquqiga ega bo‘lgan yuridik shaxsni shartnoma asosida jalb qilish yo‘li bilan amalga oshiriladi.
Texnik nazorat bo‘yicha mutaxassis qonun hujjatlarida belgilangan tartibda sertifikatlashtirilgan bo‘lishi kerak.
Texnik nazorat bo‘yicha mutaxassis:
pudratchi tomonidan tashkiliy-texnologik hujjatlar va shaharsozlik hujjatlari talablariga rioya etilishi, shuningdek qurilish obyektida qurilish-montaj ishlarining sifatining ta’minlanishi ustidan nazoratni amalga oshiradi;
yashirin ishlarni tekshirish va muhim konstruksiyalarni oraliq qabul qilish to‘g‘risidagi dalolatnomalarni rasmiylashtirishda qatnashadi;
texnik nazorat jurnalining to‘g‘ri yuritilishini ta’minlaydi;
qurilish jarayonida yo‘l qo‘yilgan qoidabuzarliklarni bartaraf etish yuzasidan pudratchilar va mualliflik nazorati bo‘yicha mutaxassislar tomonidan ijro etilishi majburiy bo‘lgan ko‘rsatmalar beradi;
konstruksiyalarning ishonchliligiga hamda binolar va inshootlarning zilzilabardoshliligiga ta’sir ko‘rsatuvchi, shuningdek inson hayoti va sog‘lig‘iga tahdid soluvchi kamchiliklar aniqlanganda, buyurtmachini hamda hududiy inspeksiyani ta’sirchan choralar ko‘rish uchun xabardor qilgan holda, qurilish-montaj ishlarini to‘xtatib turadi;
qurilish obyektidagi qurilish-montaj ishlari sifatini muntazam ravishda nazorat qiladi.
Obyektni qurish (rekonstruksiya qilish) tugallangandan so‘ng texnik nazorat bo‘yicha mutaxassis buyurtmachiga bajarilgan qurilish-montaj ishlarining sifati to‘g‘risida xulosa beradi.
Texnik nazorat bo‘yicha mutaxassis qonun hujjatlariga muvofiq boshqa vakolatlarni ham amalga oshirishi mumkin.'),
        (953, 106, '2023-06-20 11:08:41', '2023-06-20 11:08:41', '29-modda. Mualliflik nazorati
Mualliflik nazorati ishlab chiquvchi tomonidan shartnoma asosida obyektni qurishning (rekonstruksiya qilishning) butun davri davomida amalga oshiriladi. Ishlab chiquvchi mualliflik nazoratini amalga oshirish bo‘yicha o‘z vakolatlarini shartnoma asosida boshqa ishlab chiquvchiga, bu haqda buyurtmachini xabardor qilgan holda, topshirish huquqiga ega.
Mualliflik nazorati bo‘yicha mutaxassis qonun hujjatlarida belgilangan tartibda sertifikatlashtirilgan bo‘lishi kerak.
Mualliflik nazorati bo‘yicha mutaxassis:
qurilish-montaj ishlarining tasdiqlangan shaharsozlik hujjatlarida qabul qilingan loyiha yechimlariga muvofiq bajarilishini nazorat qiladi;
yashirin ishlarni ko‘rikdan o‘tkazish va muhim konstruksiyalarni oraliq qabul qilish to‘g‘risidagi dalolatnomalarni rasmiylashtirishda qatnashadi;
mualliflik nazorati jurnali to‘g‘ri yuritilishini ta’minlaydi va mualliflik nazorati jurnaliga tasdiqlangan shaharsozlik hujjatlaridan chetga chiqishlar to‘g‘risidagi yozuvlarni kiritadi, ular buyurtmachi va pudratchi tomonidan bajarilishi majburiydir;
tasdiqlangan shaharsozlik hujjatlaridan konstruksiyalarning ishonchliligiga hamda binolar va inshootlarning zilzilabardoshliligiga ta’sir ko‘rsatuvchi, shuningdek inson hayoti va sog‘lig‘iga tahdid soluvchi chetga chiqishlar aniqlanganda, buyurtmachini hamda hududiy inspeksiyani ta’sirchan choralar ko‘rish uchun xabardor qilgan holda, qurilish-montaj ishlarini to‘xtatib turadi;
qurilish obyektining tasdiqlangan shaharsozlik hujjatlariga muvofiqligi uchun javobgar bo‘ladi.
Obyektni qurish (rekonstruksiya qilish) tugallangandan so‘ng mualliflik nazorati bo‘yicha mutaxassis buyurtmachiga bajarilgan qurilish-montaj ishlarining shaharsozlik hujjatlariga muvofiqligi to‘g‘risida xulosa beradi.
Mualliflik nazorati bo‘yicha mutaxassis qonun hujjatlariga muvofiq boshqa vakolatlarni ham amalga oshirishi mumkin.'),
        (954, 106, '2023-06-20 11:08:41', '2023-06-20 11:08:41', '30-modda. Ichki nazorat
Ichki nazorat pudratchi tomonidan kamchiliklarni o‘z vaqtida aniqlash hamda ularni bartaraf etish va ularning oldi olish bo‘yicha choralar ko‘rish maqsadida amalga oshiriladi.
Ichki nazorat bo‘yicha mutaxassis qonun hujjatlarida belgilangan tartibda sertifikatlashtirilgan bo‘lishi kerak.
Ichki nazorat bo‘yicha mutaxassis:
shaharsozlik hujjatlarining shaharsozlik normalari va qoidalariga muvofiq butligi yuzasidan kirish nazoratini;
qurilish materiallarini, konstruksiyalarini, buyumlarini va uskunalarini albatta tashqi ko‘zdan kechirishdan o‘tkazgan holda, ularning texnik jihatdan tartibga solish sohasidagi normativ hujjatlar talablariga, ish chizmalariga, pasportlarga, sertifikatlarga va ularning sifatini tasdiqlovchi hujjatlarga muvofiqligi yuzasidan kirish nazoratini;
texnik va mualliflik nazorati bo‘yicha mutaxassislar ishtirokida muhim konstruksiyalarni ularning tayyor bo‘lishiga qarab qabul qilib olish yo‘li bilan qurilish jarayonlari texnologiyalariga rioya etilishiga doir operatsiyalar bo‘yicha nazoratni;
tasdiqlangan shaharsozlik hujjatlariga muvofiq qurilish-montaj ishlarini qabul qilib olish nazoratini amalga oshiradi.
Ichki nazorat bo‘yicha mutaxassis qonun hujjatlariga muvofiq boshqa vakolatlarni ham amalga oshirishi mumkin.
Qurilish-montaj ishlari sifatini nazorat qilish pudratchining qurilish laboratoriyasi yoki shartnoma asosida jalb etiladigan tashkilot tomonidan ta’minlanadi.'),
        (1260, 122, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '17. O‘zi kelib murojaat etganda Davlat xizmatlari markazlari xodimi obyektning loyiha-smeta hujjatlaridagi bino va inshootning maydoniga ko‘ra so‘rovnomani va loyiha-smeta hujjatlarini real vaqt rejimida mazkur reglament 7-bandi bilan o‘rnatilgan tartib asosida:
Kengashlarga;
bir vaqtda xulosa beruvchilar va vakolatli organga;
tuman (shahar) qurilish bo‘limlariga yuboradi.'),
        (958, 106, '2023-06-20 11:08:41', '2023-06-20 11:08:41', '34-modda. Shaharsozlik faoliyatini sug‘urta qilish
O‘zbekiston Respublikasi Davlat budjeti mablag‘lari va hukumat kafolati ostidagi kreditlar hisobiga moliyalashtiriladigan qurilish obyektlari bo‘yicha qurilish tavakkalchiliklarini majburiy sug‘urta qilish amalga oshiriladi.
Binolar, inshootlar, asbob-uskunalar, mashinalar, ularning ehtiyot qismlari, materiallar va qurilish maydonidagi hamda sug‘urta shartnomasida belgilangan qurilish va montaj predmetidan iborat bo‘lgan boshqa mol-mulk, shuningdek qurilish-montaj ishlarini amalga oshirishda uchinchi shaxslarning hayotiga, sog‘lig‘iga va mol-mulkiga zarar yetkazilganligi uchun sug‘urta qildiruvchining javobgarligi sug‘urta obyektlaridir.
Obyektlarni davlat kapital qo‘yilmalari hisobiga barpo etishda qurilish tavakkalchiliklarini sug‘urta qilish qurilish tavakkalchiliklarini majburiy sug‘urta qilish xarajatlari obyekt qurilishining umumiy qiymatiga kiritilgan holda amalga oshiriladi. Boshqa manbalar hisobiga moliyalashtiriladigan qurilish obyektlari bo‘yicha sug‘urta qilish ixtiyoriy asosda amalga oshiriladi.'),
        (959, 107, '2023-06-20 11:08:41', '2023-06-20 11:08:41', '35-modda. Shaharsozlik faoliyati sohasidagi jamoatchilik nazoratining shakllari
Shaharsozlik faoliyati sohasidagi jamoatchilik nazorati jamoatchilik muhokamasi, jamoatchilik ekspertizasi shakllarida va qonun hujjatlariga zid bo‘lmagan boshqa shakllarda amalga oshiriladi.
Jamoatchilik nazoratining natijalari bo‘yicha axborotga oid va tavsiyaviy xususiyatga ega bo‘lgan takliflarni o‘z ichiga olgan yakuniy hujjat tayyorlanadi.
Yakuniy hujjatda bayon etilgan axborot, tavsiyalar va takliflar shaharsozlik faoliyati sohasidagi davlat boshqaruvi organlari tomonidan majburiy tartibda ko‘rib chiqiladi hamda ular yuzasidan tegishli qarorlar qabul qilinadi.'),
        (960, 107, '2023-06-20 11:08:42', '2023-06-20 11:08:42', '36-modda. Shaharsozlik faoliyati sohasidagi jamoatchilik nazorati subyektlari va ularning huquqlari
Fuqarolarning o‘zini o‘zi boshqarish organlari, nodavlat notijorat tashkilotlari, ommaviy axborot vositalari, shuningdek fuqarolar shaharsozlik faoliyati sohasidagi jamoatchilik nazorati subyektlaridir, bundan jamoatchilik nazorati subyektlari buyurtmachi sifatida ishtirok etgan hollar mustasno.
Jamoatchilik nazorati subyektlari aholi punktlarining bosh rejalari to‘g‘risida ishonchli va o‘z vaqtida axborot olish huquqiga ega.
Jamoatchilik nazorati subyektlarining aholi punktlari bosh rejalarini tasdiqlash to‘g‘risidagi qarorlarning jamoatchilik muhokamasida va qabul qilinishida ishtirok etishi mahalliy ijro etuvchi hokimiyat organlari tomonidan ta’minlanadi.
Jamoatchilik nazoratini amalga oshirish uchun quyidagilar tashkil etilishi mumkin:
jamoatchilik kuzatuv komissiyalari;
jamoatchilik ekspert komissiyalari;
jamoatchilik nazorati guruhlari;
jamoatchilik nazoratining boshqa tashkiliy tuzilmalari.
Aholi punktlarining bosh rejalarini tasdiqlash to‘g‘risidagi qarorlarning jamoatchilik muhokamasi va qabul qilinishi chog‘ida jamoatchilik nazorati subyektlari quyidagi huquqlarga ega:
aholi punktlarining bosh rejalarini tasdiqlash to‘g‘risidagi qarorlarni muhokama qilish, ular yuzasidan takliflar kiritish va ularni tayyorlashda boshqa shaklda ishtirok etish;
zarur hollarda, aholi punktining bosh rejasi tasdiqlanguniga qadar qonun hujjatlarida taqiqlanmagan mablag‘lar hisobiga uni jamoatchilik ekspertizasidan o‘tkazishni tashkil etish.
Jamoatchilik nazorati subyektlari qonun hujjatlariga muvofiq boshqa huquqlarga ham ega bo‘lishi mumkin.'),
        (961, 107, '2023-06-20 11:08:42', '2023-06-20 11:08:42', '37-modda. Aholi punktlarining bosh rejalari tasdiqlanganligi to‘g‘risidagi axborot
Aholi punktlarining bosh rejalari tasdiqlanganligi to‘g‘risidagi axborot jumlasiga quyidagilar kiradi:
shaharsozlik to‘g‘risidagi qonun hujjatlari;
davlat boshqaruvi organlarining, mahalliy davlat hokimiyati organlarining aholi punktlari bosh rejalarini tasdiqlash to‘g‘risidagi qarorlari haqidagi ma’lumotlar;
aholi punktlarining bosh rejalari to‘g‘risidagi hujjatlar va ma’lumotlar.
Davlat boshqaruvi organlari, mahalliy ijro etuvchi hokimiyat organlari jamoatchilik muhokamalarining yakuniy hujjatlarini, jamoatchilik ekspertizalarining xulosalarini ommaviy axborot vositalarida va maxsus vakolatli davlat organining Internet tarmog‘idagi rasmiy veb-saytida e’lon qilish, dasturlar va shaharsozlik hujjatlarini namoyish qilishga doir tadbirlar orqali jamoatchilik nazorati subyektlarini aholi punktlarining bosh rejalari tasdiqlanganligi haqida tegishli qaror qabul qilingan paytdan e’tiboran besh kundan ko‘p bo‘lmagan muddat ichida xabardor qilishi shart.
Aholi punktlarining bosh rejalari tasdiqlanganligi to‘g‘risidagi axborot O‘zbekiston Respublikasi Vazirlar Mahkamasi, shaharsozlik faoliyati sohasidagi maxsus vakolatli davlat organi, mahalliy ijro etuvchi hokimiyat organlari tomonidan taqdim etiladi.'),
        (1274, 123, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '4. O‘zbekiston Respublikasining davlat sirlari bilan bog‘liq bo‘lgan obyektlarning qurilish-montaj ishlarini Qurilish sohasida hududiy nazorat inspeksiyalari (keyingi o‘rinlarda — hududiy inspeksiya) to‘g‘ridan to‘g‘ri ro‘yxatdan o‘tkazadi. Bunda shaharsozlik hujjatlarining ekspertizadan o‘tkazilganligi to‘g‘risidagi ijobiy xulosa hududiy inspeksiyalarga taqdim etiladi.'),
        (962, 107, '2023-06-20 11:08:42', '2023-06-20 11:08:42', '38-modda. Aholi punktlari bosh rejalarining jamoatchilik muhokamasi
Jamoatchilik nazorati subyektlari tomonidan shaharsozlik hujjatlarining yuridik va jismoniy shaxslar, jamiyat va davlat manfaatlariga muvofiqligi yuzasidan muhokama qilinishi aholi punktlari bosh rejalarining jamoatchilik muhokamasidir.
Aholi punktlari bosh rejalarining jamoatchilik muhokamasi quyidagi tartibda o‘tkaziladi:
1) muhokama boshlanishidan o‘n kun avval jamoatchilik nazorati subyektlarini jamoatchilik muhokamasini o‘tkazish muddatlari va tartibi bilan aholi punktining bosh rejasi to‘g‘risidagi axborotni taqdim etgan holda tanishtirish. Jamoatchilik nazorati subyektlarini tanishtirish:
ommaviy axborot vositalarida va maxsus vakolatli davlat organining Internet tarmog‘idagi rasmiy veb-saytida tegishli e’lonlar berish;
korxonalarning, muassasalarning va tashkilotlarning binolarida, jamoat joylarida, taxmin qilinayotgan qurilish maydonchasi hududida bannerlar (plakatlar) va tashqi reklama vositalarini o‘rnatish;
ekspozitsiyalar tashkil etish;
qonun hujjatlarida taqiqlanmagan boshqa shakllardan foydalanish orqali amalga oshiriladi;
2) jamoatchilik muhokamasini o‘tkazish, aholi punktining bosh rejasiga oid fikrlarni to‘plash va qonun hujjatlarida taqiqlanmagan boshqa shakllar.
Aholi punkti bosh rejasining jamoatchilik muhokamasini o‘tkazish davomiyligi kamida o‘n besh kundan iborat bo‘lishi kerak;
3) bildirilgan mulohazalarni qayd etish (so‘rovnomalar, audiovideoyozuv, kelishib olish varaqasi, ishtirokchilarning yozma takliflari, jamoatchilik muhokamasi yig‘ilishining bayonnomasi);
4) jamoatchilik muhokamasi yakunlari bo‘yicha xulosaning muhokama yakunlanganidan keyin o‘n besh kundan kechiktirmay jamoatchilik nazorati subyektlari tomonidan ommaviy axborot vositalarida va maxsus vakolatli davlat organining Internet tarmog‘idagi rasmiy veb-saytida e’lon qilinishi.
Aholi punktlarining bosh rejalari jamoatchilik muhokamasi natijalari hisobga olingan holda tasdiqlanadi.
Aholi punkti bosh rejasining o‘tkazilgan jamoatchilik muhokamasi materiallari bosh reja amal qiladigan butun muddat mobaynida buyurtmachida va mahalliy ijro etuvchi hokimiyat organida saqlanadi.'),
        (963, 107, '2023-06-20 11:08:42', '2023-06-20 11:08:42', '39-modda. Aholi punkti bosh rejasining jamoatchilik muhokamasini o‘tkazish tartibining buzilishlari ustidan shikoyat berish
Aholi punkti bosh rejasining jamoatchilik muhokamasini o‘tkazishning ushbu Kodeksning 38-moddasida belgilangan tartibi buzilishlari aniqlanganda, jamoatchilik nazorati subyektlari mahalliy ijro etuvchi hokimiyat organlariga “Jismoniy va yuridik shaxslarning murojaatlari to‘g‘risida”gi O‘zbekiston Respublikasi Qonuniga muvofiq shikoyat berishga haqli.
Jamoatchilik muhokamasini o‘tkazish tartibi buzilganligi to‘g‘risidagi shikoyat mahalliy ijro etuvchi hokimiyat organlari tomonidan asosli deb topilgan taqdirda takroriy muhokama o‘tkaziladi.
Aholi punktlarining bosh rejalarini tasdiqlash to‘g‘risida jamoatchilik muhokamasini o‘tkazish tartibi buzilgan holda qabul qilingan qaror ustidan sudga shikoyat berilishi mumkin.'),
        (964, 107, '2023-06-20 11:08:42', '2023-06-20 11:08:42', '40-modda. Aholi punktlari bosh rejalarining jamoatchilik ekspertizasi
Aholi punktlari bosh rejalarining jamoatchilik ekspertizasi ushbu rejalarning yuridik va jismoniy shaxslar huquqlari hamda qonuniy manfaatlariga, jamiyat va davlat manfaatlariga muvofiqligi yuzasidan o‘rganilishini hamda baholanishini nazarda tutadi.
Aholi punktlari bosh rejalarining jamoatchilik ekspertizasi jamoatchilik nazorati subyektlarining tashabbusiga ko‘ra va jamoatchilik muhokamasini o‘tkazuvchi shaharsozlik faoliyati sohasidagi davlat boshqaruvi organlariga jamoatchilik ekspertizasini o‘tkazish zaruratini asoslagan holda bergan arizasi asosida o‘tkaziladi.
Aholi punktlari bosh rejalarining jamoatchilik ekspertizasini o‘tkazishga doir ariza jamoatchilik muhokamasi o‘tkazilganidan keyin o‘n besh kun ichida shaharsozlik faoliyati sohasidagi davlat boshqaruvi organlari tomonidan qabul qilinadi.
Jamoatchilik ekspertizasi jamoatchilik asosida, jamoatchilik nazorati subyektlarining mablag‘lari, homiylik mablag‘lari yoki qonun hujjatlarida taqiqlanmagan boshqa mablag‘lar hisobidan amalga oshiriladi.
Aholi punktlari bosh rejalarining jamoatchilik ekspertizasi natijalari bo‘yicha yakuniy hujjat tayyorlanib, u shaharsozlik faoliyati sohasidagi davlat boshqaruvi organiga va buyurtmachiga taqdim etiladi.
Aholi punktlari bosh rejalarining buyurtmachilari aholi punktlari bosh rejalari jamoatchilik ekspertizasining yakuniy hujjatiga doir javoblarni ushbu hujjatni tasdiqlash to‘g‘risida uzil-kesil qaror qabul qiluvchi shaharsozlik faoliyati sohasidagi davlat boshqaruvi organiga taqdim etadi.
Aholi punktlarining bosh rejalarini tasdiqlash to‘g‘risida uzil-kesil qaror qabul qiluvchi shaharsozlik faoliyati sohasidagi davlat boshqaruvi organi o‘tkazilgan jamoatchilik muhokamasi natijalari bilan bir qatorda jamoatchilik ekspertizasining yakuniy hujjatida keltirilgan tavsiyalarni hisobga oladi.'),
        (965, 107, '2023-06-20 11:08:42', '2023-06-20 11:08:42', '41-modda. Jamoatchilik nazorati subyektlari tomonidan aholi punktining tasdiqlangan bosh rejasiga o‘zgartirishlar kiritish to‘g‘risidagi takliflar kiritilishi
Aholi punktining tasdiqlangan bosh rejasiga o‘zgartirishlar kiritish to‘g‘risida takliflar berish huquqi o‘z qonuniy manfaatlariga daxl qilinayotgan jamoatchilik nazorati subyektlariga taqdim etiladi.
Jamoatchilik nazorati subyektlarining aholi punktining tasdiqlangan bosh rejasiga o‘zgartirishlar kiritish to‘g‘risidagi takliflari faqat uni amalga oshirish boshlanguniga qadar qabul qilinadi.
Aholi punktining tasdiqlangan bosh rejasiga o‘zgartirishlar kiritish to‘g‘risidagi takliflar mahalliy ijro etuvchi hokimiyat organlariga yozma yoki elektron shaklda beriladi.
Mahalliy ijro etuvchi hokimiyat organi kelib tushgan taklifni o‘n besh kun ichida ko‘rib chiqadi. Aholi punktining bosh rejasiga oid o‘zgartirishlarning jamoatchilik muhokamasini o‘tkazish zarurati bo‘lmagan taqdirda, mahalliy ijro etuvchi hokimiyat organi arizachilarga jamoatchilik muhokamasini o‘tkazishni rad etishga doir asoslantirilgan javob yuboradi.'),
        (966, 107, '2023-06-20 11:08:42', '2023-06-20 11:08:42', '42-modda. Aholi punktining tasdiqlangan bosh rejasiga o‘zgartirishlar kiritish to‘g‘risida qarorlar qabul qilish
Aholi punktining tasdiqlangan bosh rejasiga o‘zgartirishlar kiritish to‘g‘risidagi takliflar ekspert baholash natijalari olinganidan keyin o‘n besh kunlik muddatda buyurtmachilar tomonidan arizachi yoki uning vakolatli vakili ishtirokida ko‘rib chiqiladi.
Aholi punktining tasdiqlangan bosh rejasiga o‘zgartirishlar kiritish to‘g‘risidagi takliflarni ekspert baholash buyurtmachi mablag‘lari hisobidan tegishli tashkilotlarni jalb etgan holda o‘tkaziladi.
Aholi punktining tasdiqlangan bosh rejasiga o‘zgartirishlar kiritish ishlab chiquvchi tomonidan buyurtmachining mablag‘lari hisobidan amalga oshiriladi.'),
        (967, 107, '2023-06-20 11:08:42', '2023-06-20 11:08:42', '43-modda. Aholi punktining muqobil bosh rejasini ishlab chiqish tartibi
Aholi punktining muqobil bosh rejasi aholi punktining taklif etilgan bosh rejasidan norozi bo‘lgan jamoatchilik nazorati subyektlarining tashabbusiga ko‘ra va ularning mablag‘lari hisobidan bajariladigan loyihaviy, ilmiy ishlanmadan iborat.
Aholi punkti muqobil bosh rejasining tashabbuskori mahalliy ijro etuvchi hokimiyat organiga o‘z aholi punktining bosh rejasini muqobil bosh reja deb topish to‘g‘risida yozma ariza bilan murojaat qiladi.
Mahalliy ijro etuvchi hokimiyat organi yozma arizani olganidan keyin aholi punkti bosh rejasining tayyorlik darajasini hisobga olgan holda, uni muqobil bosh reja deb topish hamda jamoatchilik ekspertizasiga va jamoatchilik muhokamalariga taqdim etish yoki uni asoslangan holda rad etish haqida qaror qabul qiladi.
Aholi punktining bosh rejasini muqobil bosh reja deb topishni rad etish to‘g‘risidagi qaror ustidan ushbu Kodeksning 39-moddasida nazarda tutilgan tartibda shikoyat qilinishi mumkin.
Aholi punktining muqobil bosh rejasini ishlab chiqish, ekspertiza qilish, kelishib olish va uning jamoatchilik muhokamasi ushbu Kodeksda belgilangan tartibda amalga oshiriladi.
Aholi punktining bosh rejasi muqobil bosh reja deb topilgan taqdirda, tashabbuskorga uni ishlab chiqish bilan bog‘liq xarajatlarning o‘rni ushbu bosh rejaning buyurtmachisi tomonidan qoplanadi.'),
        (1289, 123, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '19. Davlat xizmatlari markazlari invoys va QR-kod qo‘yilgan ko‘chirma kelib tushgandan so‘ng real vaqt rejimida bu haqida elektron tarzda ariza beruvchini xabardor qiladi.
Obyekt ro‘yxatdan o‘tkazilganligi haqidagi ko‘chirma obyektda qurilish-montaj ishlarini boshlash uchun asos bo‘ladi.'),
        (968, 108, '2023-06-20 11:08:42', '2023-06-20 11:08:42', '44-modda. Shaharsozlik hujjatlarining tarkibi
Shaharsozlik hujjatlari tarkibiga quyidagilar kiradi:
1) O‘zbekiston Respublikasining hududini va hududining qismlarini rivojlantirishni rejalashtirish to‘g‘risidagi hujjatlar:
O‘zbekiston Respublikasi hududida aholini joylashtirish bosh tarhi;
O‘zbekiston Respublikasi hududining ikki va undan ortiq viloyat hududlarini o‘z ichiga olgan qismlarini rivojlantirishni rejalashtirish tarhlari (bundan buyon matnda shaharsozlik jihatidan rejalashtirishning biriktirma tarhlari deb yuritiladi);
O‘zbekiston Respublikasi hududini rejalashtirish tarhi;
O‘zbekiston Respublikasi hududini rivojlantirishning tarmoq tarhlari;
2) O‘zbekiston Respublikasining viloyatlari hududlarini rivojlantirishni rejalashtirish to‘g‘risidagi hujjatlar:
viloyatlar va Qoraqalpog‘iston Respublikasi hududlarini rejalashtirish tarhlari;
tumanni (tumanlar guruhlarini) rejalashtirish loyihasi;
3) aholi punktlarining hududlarini rivojlantirish to‘g‘risidagi hujjatlar:
aholi punktlarining bosh rejalari;
aholi punktlari chegara chiziqlarining loyihalari;
aholi punktlari hududlarini rivojlantirishning tarmoq tarhlari;
aholi punktlarining hududlarini shaharsozlik jihatidan rivojlantirish tarhlari;
4) aholi punktlarining hududlarini qurish to‘g‘risidagi hujjatlar:
batafsil rejalashtirish loyihasi;
qurish loyihasi;
5) shaharsozlik faoliyati obyektlarining qurilishi uchun loyihaoldi va loyiha hujjatlari:
qurilishning texnik-iqtisodiy asosi;
qurilishning texnik-iqtisodiy hisob-kitoblari;
loyiha hujjatlari (ishchi loyiha, loyiha-smeta hujjatlari, ishchi hujjatlar).
Qonun hujjatlariga muvofiq shaharsozlik hujjatlari tarkibiga shaharsozlik hujjatlarining boshqa turlari ham kirishi mumkin.'),
        (969, 108, '2023-06-20 11:08:42', '2023-06-20 11:08:42', '45-modda. Shaharsozlik hujjatlarini ishlab chiqish, kelishib olish va tasdiqlash
Shaharsozlik hujjatlarini ishlab chiqish ishlab chiquvchilar tomonidan shartnoma, buyurtmachi tomonidan beriladigan loyihalashtirish topshirig‘i, boshlang‘ich-ruxsat beruvchi hujjatlar va muhandislik-texnik qidiruvlar natijalari asosida amalga oshiriladi.
Shaharsozlik hujjatlarini ishlab chiqishga doir topshiriqlar quyidagilar bilan kelishib olinishi lozim:
O‘zbekiston Respublikasi Vazirlar Mahkamasi bilan — ushbu Kodeks 44-moddasi birinchi qismining 1-bandida nazarda tutilgan shaharsozlik hujjatlari bo‘yicha;
Qoraqalpog‘iston Respublikasi Vazirlar Kengashi, viloyatlar ijro etuvchi hokimiyat organlari, tegishli vazirliklar, davlat qo‘mitalari, idoralar bilan — ushbu Kodeks 44-moddasi birinchi qismining 1 va 2-bandlarida nazarda tutilgan shaharsozlik hujjatlari bo‘yicha;
tumanlar va shaharlar ijro etuvchi hokimiyat organlari bilan — ushbu Kodeks 44-moddasi birinchi qismining 2 — 4-bandlarida nazarda tutilgan shaharsozlik hujjatlari bo‘yicha;
maxsus vakolatli davlat organi bilan — ushbu Kodeks 44-moddasi birinchi qismining 1 — 3-bandlarida, 4-bandida (ularning ishlab chiqilishi budjet tizimining budjetlar mablag‘lari hisobidan moliyalashtirilganda), 5-bandida (budjet tizimining budjetlar mablag‘lari hamda hukumat kafolati ostida moliyalashtiriladigan noyob va alohida muhim davlat ahamiyatiga molik obyektlar bo‘yicha) nazarda tutilgan shaharsozlik hujjatlari bo‘yicha;
maxsus vakolatli davlat organining hududiy bo‘linmalari bilan — ushbu Kodeks 44-moddasi birinchi qismining 4-5-bandlarida nazarda tutilgan shaharsozlik hujjatlari bo‘yicha (ularning ishlab chiqilishi budjet tizimining budjetlar mablag‘lari hisobidan moliyalashtirilganda).
Shaharsozlik hujjatlari quyidagilar bilan kelishib olinishi lozim:
ushbu Kodeks 44-moddasi birinchi qismining 1 — 3-bandlarida nazarda tutilgan shaharsozlik hujjatlari tegishli vazirliklar, davlat qo‘mitalari, idoralar va ularning hududiy bo‘linmalari, shuningdek mahalliy ijro etuvchi hokimiyat organlari bilan;
ushbu Kodeks 44-moddasi birinchi qismining 2 — 4-bandlarida nazarda tutilgan shaharsozlik hujjatlari tegishli vazirliklar, davlat qo‘mitalari, idoralarning hududiy bo‘linmalari vakillari ishtirokida Qoraqalpog‘iston Respublikasi Qurilish vazirligi, viloyatlar va Toshkent shahar qurilish bosh boshqarmalari huzuridagi hududiy arxitektura-shaharsozlik kengashlari hamda mahalliy ijro etuvchi hokimiyat organlari bilan.
Shaharsozlik hujjatlarini kelishib olish muddati bir oydan oshmasligi kerak.
O‘zbekiston Respublikasi Vazirlar Mahkamasi va maxsus vakolatli davlat organi tomonidan tasdiqlanadigan shaharsozlik hujjatlari O‘zbekiston Respublikasi Vazirlar Mahkamasi huzuridagi Respublika arxitektura-shaharsozlik kengashi bilan kelishib olinishi lozim.
Ushbu Kodeks 44-moddasi birinchi qismining 5-bandida nazarda tutilgan shaharsozlik hujjatlari sanitariya-epidemiologiya xizmatlari va davlat yong‘in nazorati organlari, ekologiya va atrof-muhitni muhofaza qilish organlari, Qoraqalpog‘iston Respublikasi Qurilish vazirligi, viloyatlar va Toshkent shahar qurilish bosh boshqarmalari bilan, qonun hujjatlarida nazarda tutilgan hollarda esa ular huzuridagi arxitektura-shaharsozlik kengashlari bilan kelishiladi.
Shaharsozlik hujjatlari ushbu Kodeksda belgilangan vakolatlarga muvofiq O‘zbekiston Respublikasi Vazirlar Mahkamasi, maxsus vakolatli davlat organi, Qoraqalpog‘iston Respublikasi Jo‘qorg‘i Kengesi, xalq deputatlari viloyatlar va Toshkent shahar Kengashlari, buyurtmachi tomonidan tasdiqlanadi.
Tasdiqlangan shaharsozlik hujjatlariga o‘zgartirishlar ushbu hujjatlarni tasdiqlagan organning yoki buyurtmachining qaroriga binoan kiritiladi.'),
        (970, 108, '2023-06-20 11:08:42', '2023-06-20 11:08:42', '46-modda. Shaharsozlik hujjatlarini ro‘yobga chiqarish
Ushbu Kodeks 44-moddasi birinchi qismining 1 — 4-bandlarida ko‘rsatilgan shaharsozlik hujjatlarini ro‘yobga chiqarish mahalliy ijro etuvchi hokimiyat organlari tomonidan ishlab chiqiladigan tasdiqlangan shaharsozlik hujjatlarini ro‘yobga chiqarish bo‘yicha kompleks dasturlar asosida amalga oshiriladi.
Tasdiqlangan shaharsozlik hujjatlarini ro‘yobga chiqarish bo‘yicha kompleks dasturlar Qoraqalpog‘iston Respublikasi Vazirlar Kengashining, viloyatlar va Toshkent shahar ijro etuvchi hokimiyat organlarining taqdimnomasiga ko‘ra O‘zbekiston Respublikasi Vazirlar Mahkamasi, Qoraqalpog‘iston Respublikasi Jo‘qorg‘i Kengesi, xalq deputatlari viloyatlar va Toshkent shahar Kengashlari tomonidan tasdiqlanadi.
Tasdiqlangan shaharsozlik hujjatlarini ro‘yobga chiqarish bo‘yicha kompleks dasturlar bajarilishining borishi Qoraqalpog‘iston Respublikasi Jo‘qorg‘i Kengesining, xalq deputatlari viloyatlar, Toshkent shahar, tumanlar va shaharlar Kengashlarining majlislarida ko‘rib chiqilishi hamda ommaviy axborot vositalarida yoritilishi lozim.'),
        (971, 108, '2023-06-20 11:08:42', '2023-06-20 11:08:42', '47-modda. Shaharsozlik hujjatlarini ishlab chiqish hamda ro‘yobga chiqarishdagi taqiqlar va cheklovlar
Ushbu Kodeks 44-moddasi birinchi qismining 5-bandida ko‘rsatilgan shaharsozlik hujjatlarini ishlab chiqish, tasdiqlash va amalga oshirishda quyidagilar taqiqlanadi:
moliyalashtirish manbalaridan qat’i nazar, obyektlarni shaharsozlik hujjatlarini bir vaqtning o‘zida (parallel ravishda) ishlab chiqqan holda qurish va rekonstruksiya qilish;
qurilishning borishiga va tasdiqlangan shaharsozlik hujjatlariga o‘zgartirishlar kiritish jarayoniga tegishli vakolatlarga ega bo‘lmagan shaxslarning aralashishi;
shaharlar va shaharchalarda qurilishi tugallangan mavzelar, ko‘p qavatli turar joy kichik dahalarining (dahalarning) hududida aholi punktlarining tasdiqlangan bosh rejalarida nazarda tutilmagan ishlab chiqarish, savdo, umumiy ovqatlanish, servis xizmati ko‘rsatish hamda bozor infratuzilmasining alohida turgan obyektlarini joylashtirish, loyihalashtirish va qurish;
ko‘p qavatli uylarni qurish uchun mo‘ljallanmagan yer uchastkalarida bunday turdagi qurilishlarni amalga oshirish;
shaharsozlik hujjatlari ekspertizasini o‘tkazmay qurilayotgan va mavjud bino va inshootlarning, shu jumladan ikki qavatdan yuqori bo‘lgan yakka tartibdagi uy-joylarning qavatlari sonini ko‘paytirish (ustki qavat qurish);
umumiy foydalaniladigan ko‘kalamzorlar hududlari va obyektlarida qurilish obyektlarini (shahar o‘rmonlari, o‘rmon-bog‘lar, istirohat bog‘lari, xiyobonlar, bog‘lar, sayilgohlar) joylashtirish, bundan obodonlashtirish, muhandislik infratuzilmasi va metropoliten obyektlari mustasno.
Obyektlarni bir vaqtning o‘zida (parallel ravishda) loyihalashtirgan holda qurish mudofaa qobiliyati va davlat xavfsizligini, aholining hayoti va sog‘lig‘i xavfsizligini ta’minlash bo‘yicha chora-tadbirlar, tabiiy va ekologik ofatlarning oqibatlarini bartaraf etish, O‘zbekiston Respublikasining xalqaro majburiyatlarini bajarish bilan bog‘liq istisno hollarda, moliyalashtirish manbalari mavjud bo‘lganda O‘zbekiston Respublikasi Prezidenti va O‘zbekiston Respublikasi Vazirlar Mahkamasining qarorlariga binoan amalga oshirilishi mumkin.'),
        (972, 108, '2023-06-20 11:08:42', '2023-06-20 11:08:42', '48-modda. Davlat shaharsozlik kadastri
Davlat shaharsozlik kadastri Davlat kadastrlarining yagona tizimiga kiradi.
Davlat shaharsozlik kadastri:
muhandislik-texnik qidiruvlarning elektron geofondini;
hududlarning, ko‘chmas mulk obyektlarining, muhandislik, transportga oid va ijtimoiy infratuzilmaning manzilli reyestrlarini;
hududlarni rivojlantirish va qurishni rejalashtirish to‘g‘risidagi tasdiqlangan shaharsozlik hujjatlari haqidagi ma’lumotlarni;
hududlarni hamda shaharsozlik faoliyati obyektlarini qurish va ulardan foydalanish bo‘yicha shaharsozlik reglamentlari to‘g‘risidagi ma’lumotlarni;
loyihalar, shaharsozlik faoliyati obyektlari va subyektlari to‘g‘risidagi ma’lumotlarni o‘z ichiga oladi.
Davlat shaharsozlik kadastrini yuritish maxsus vakolatli davlat organi va uning hududiy bo‘linmalari tomonidan amalga oshiriladi.'),
        (981, 109, '2023-06-20 11:08:42', '2023-06-20 11:08:42', '57-modda. Batafsil rejalashtirish loyihalari
Batafsil rejalashtirish loyihalari aholi punktining bosh rejasi asosida aholi punktlari hududlarining qismlari uchun ishlab chiqiladi va bu loyihalar:
qizil chiziqlarni;
yer uchastkalarini qurish va ulardan foydalanishni tartibga solish chiziqlarini;
yerga egalik qilish, yerdan foydalanish va mulkdorlarning yer uchastkalari chegaralarini;
hududlarning zonalashtirilishini va ularni qurish navbatini;
muhofaza zonalaridan foydalanish rejimini;
ko‘chalar, tor ko‘chalar, piyodalarga ajratilgan zonalarning, shuningdek transport, aloqa inshootlari va kommunikatsiyalarining, muhandislik uskunalarining hamda hududni obodonlashtirishning ko‘rsatkichlarini;
ijtimoiy va madaniy-maishiy obyektlarning tarkibi hamda joylashuvini;
qurishning hajmiy-hududiy va arxitektura-rejalashtirish yechimini, qurish zichligi va ko‘rsatkichlarini, binolar va inshootlarning necha qavatliligini, turlarini belgilaydi.
Batafsil rejalashtirish loyihasi hududlarni qurish loyihalarini ishlab chiqish uchun asos bo‘ladi.'),
        (973, 109, '2023-06-20 11:08:42', '2023-06-20 11:08:42', '49-modda. O‘zbekiston Respublikasi hududida aholini joylashtirish bosh tarhi
O‘zbekiston Respublikasi hududida aholini joylashtirish bosh tarhida quyidagilarni nazarda tutuvchi loyiha yechimlari belgilanadi:
aholini joylashtirish tizimlarini, tabiatdan foydalanishni va ishlab chiqarish kuchlarini O‘zbekiston Respublikasi hududini ijtimoiy-iqtisodiy rivojlantirish prognozlariga muvofiq rivojlantirishning asosiy qoidalari;
Qoraqalpog‘iston Respublikasi va viloyatlar hududlarida ekologik vaziyatni yaxshilash, yerlardan oqilona foydalanish va ularni muhofaza qilish, madaniy meros obyektlari hududlarini saqlash, umumdavlat ahamiyatiga molik muhandislik, transportga oid va ijtimoiy infratuzilmalarni rivojlantirish chora-tadbirlari;
aholini joylashtirish tizimlarini rivojlantirish uchun qulay hududlar;
muhofaza etiladigan tabiiy hududlar;
qishloq xo‘jaligiga va o‘rmon xo‘jaligiga mo‘ljallangan hududlar;
ekstremal tabiiy-iqlim sharoitlariga ega bo‘lgan hududlar;
tabiiy va texnogen xususiyatga ega favqulodda vaziyatlar ta’siriga duchor bo‘lgan hududlar;
foydali qazilmalar joylashgan hududlar;
o‘ziga nisbatan shaharsozlik faoliyatini alohida tartibga solish rejimlari va shaharsozlik jihatidan foydalanishning boshqa turlari hamda shaharsozlik faoliyatini amalga oshirish uchun mazkur hududlardan foydalanishga doir cheklovlar belgilangan hududlar;
O‘zbekiston Respublikasi hududini rivojlantirishga doir boshqa loyiha yechimlari.'),
        (974, 109, '2023-06-20 11:08:42', '2023-06-20 11:08:42', '50-modda. Shaharsozlik jihatidan rejalashtirishning biriktirma tarhlari
Shaharsozlik jihatidan rejalashtirishning biriktirma tarhlarida quyidagilarni nazarda tutuvchi loyiha yechimlari belgilanadi:
hududning zonalashtirilishi;
aholini joylashtirishning hududiy tizimlarini rivojlantirish chora-tadbirlari;
viloyatlararo ahamiyatga molik muhandislik, transportga oid va ijtimoiy infratuzilmalarni rivojlantirish chora-tadbirlari;
tabiatdan oqilona foydalanish chora-tadbirlari;
hududni kompleks rivojlantirish maqsadida resurslar bilan ta’minlash chora-tadbirlari;
hududni rivojlantirishga doir boshqa loyiha yechimlari.'),
        (975, 109, '2023-06-20 11:08:42', '2023-06-20 11:08:42', '51-modda. O‘zbekiston Respublikasi, viloyatlar va Qoraqalpog‘iston Respublikasi hududlarini rejalashtirish tarhlari
O‘zbekiston Respublikasi, viloyatlar va Qoraqalpog‘iston Respublikasi hududlarini rejalashtirish tarhlarida quyidagilarni nazarda tutuvchi loyiha yechimlari belgilanadi:
hududlarning zonalashtirilishi;
aholini joylashtirish tizimlarini takomillashtirishning, aholi punktlarini rivojlantirishning asosiy yo‘nalishlari;
sanoatni, qishloq xo‘jaligini, viloyatlararo hamda aholi punktlariaro ahamiyatga molik muhandislik, transportga oid va ijtimoiy infratuzilmalarni rivojlantirishning asosiy yo‘nalishlari;
aholi punktlarining va shaharlar shahar atrofi zonalarining chegaralari;
shaharsozlik vositalari orqali O‘zbekiston Respublikasi, viloyatlar va Qoraqalpog‘iston Respublikasi hududlarini tabiiy hamda texnogen xususiyatga ega favqulodda vaziyatlar ta’siridan muhofaza qilish, ekologik holatni yaxshilash, madaniy meros obyektlari hududlarini saqlash chora-tadbirlari;
O‘zbekiston Respublikasi, viloyatlar va Qoraqalpog‘iston Respublikasi hududlarini rivojlantirishga doir boshqa loyiha yechimlari.'),
        (976, 109, '2023-06-20 11:08:42', '2023-06-20 11:08:42', '52-modda. O‘zbekiston Respublikasi hududini rivojlantirishning tarmoq tarhlari
O‘zbekiston Respublikasi hududini rivojlantirishning tarmoq tarhlarida:
energiya ta’minotini, gaz ta’minotini, aloqani, irrigatsiyani, suv ta’minotini va kanalizatsiya tarmoqlarini rivojlantirish;
havo, temir yo‘l, avtomobil, daryo transportini, quvur orqali yetkazib berish transportini va boshqa transport turlarini rivojlantirish;
hududlarni xavfli geologik va gidrogeologik jarayonlardan himoya qilish;
atrof-muhitni muhofaza qilish;
iqtisodiyot tarmoqlarining ishlab chiqarish kuchlarini joylashtirish;
O‘zbekiston Respublikasi hududini rivojlantirishning boshqa masalalari bo‘yicha asosiy ko‘rsatkichlar va rejalashtirish yechimlari belgilanadi.'),
        (977, 109, '2023-06-20 11:08:42', '2023-06-20 11:08:42', '53-modda. Tumanni (tumanlar guruhlarini) rejalashtirish loyihalari
Tumanni (tumanlar guruhlarini) rejalashtirish loyihalarida quyidagilarni nazarda tutuvchi loyiha yechimlar belgilanadi:
tuman (tumanlar guruhlari) hududini ijtimoiy-iqtisodiy rivojlanish xususiyatlarini, tabiiy-iqlim sharoitlarini hamda aholi soni prognozini inobatga olgan holda rivojlantirishning asosiy yo‘nalishlari;
hududning zonalashtirilishi va rejali tuzilishi;
tuman (tumanlar guruhlari) hududini tabiiy va texnogen xususiyatga ega favqulodda vaziyatlar ta’siridan muhofaza qilish chora-tadbirlari;
aholi punktlariaro ahamiyatga molik muhandislik, transportga oid va ijtimoiy infratuzilmalarni rivojlantirish yo‘nalishlari;
aholi punktlarini rivojlantirish uchun zaxira hududlar;
yakka tartibdagi uy-joylar qurilishini joylashtirish uchun hududlar, jamoa bog‘dorchiligi, uzumchiligi va polizchiligini yuritish uchun yer uchastkalari;
aholining dam olish joylarini tashkil etish uchun hududlar;
aholi punktlarining va shaharlar shahar atrofi zonalarining chegaralari;
tuman (tumanlar guruhlari) hududini rivojlantirishga doir boshqa loyiha yechimlari.'),
        (978, 109, '2023-06-20 11:08:42', '2023-06-20 11:08:42', '54-modda. Aholi punktining bosh rejasi
Aholi punktining bosh rejasida quyidagilarni nazarda tutuvchi loyiha yechimlari belgilanadi:
aholi punkti hududini ijtimoiy-iqtisodiy rivojlanish xususiyatlarini, tabiiy-iqlim sharoitlarini hamda aholi soni prognozini inobatga olgan holda rivojlantirishning asosiy yo‘nalishlari;
hududlarning zonalashtirilishi va ularni qurish navbati;
aholi punkti hududini tabiiy va texnogen xususiyatga ega favqulodda vaziyatlar ta’siridan muhofaza qilish, muhandislik, transportga oid va ijtimoiy infratuzilmalarni rivojlantirish chora-tadbirlari;
aholi punktining qurilgan va qurilmagan hududi nisbati;
aholi punktini rivojlantirish uchun zaxira hududlar;
aholi punktining chegaralari;
aholi punkti hududini rivojlantirishga doir boshqa loyiha yechimlari.
Shaharning bosh rejasi va shahar atrofi zonasini rejalashtirish loyihasi chegaradosh hududlarning ijro etuvchi hokimiyat organlari o‘rtasidagi kelishuv asosida yagona hujjat sifatida ishlab chiqilishi mumkin.
O‘rtacha va kichik shaharchalarning, shuningdek qishloqlar va ovullarning bosh rejalarini ishlab chiqish batafsil rejalashtirish loyihalarini ishlab chiqish bilan birga yagona hujjat sifatida qo‘shib bajarilishi mumkin.
Hududida madaniy meros obyektlari bo‘lgan aholi punktining bosh rejasi ishlab chiqilayotganda aholi punktining tarixiy-arxitektura rejasi va madaniy meros obyektlarining muhofaza zonalari inobatga olinadi.
Aholi punktining bosh rejasi yigirma — yigirma besh yillik hisob-kitob muddatiga mo‘ljallanib ishlab chiqiladi. Aholi punktining bosh rejasi u to‘liq amalga oshirilguniga qadar yoki yangi bosh reja tasdiqlanguniga qadar amal qiladi.
Aholi punktining bosh rejasi mavjud bo‘lmaganda yoki uning yangi bosh rejasi ishlab chiqilayotgan davrda aholi punkti hududida joriy qurilish uchun yer uchastkasi ajratish bilan bog‘liq masalalar tuman (shahar) qurilish bo‘limining taqdimnomasiga ko‘ra Qoraqalpog‘iston Respublikasi Qurilish vazirligi, viloyatlar va Toshkent shahar qurilish bosh boshqarmalari huzuridagi hududiy arxitektura-shaharsozlik kengashlari bilan kelishib olinadi.
Aholi punktining yangi bosh rejasi ishlab chiqilayotgan davrda davlat ahamiyatiga molik obyektlarni qurish uchun yer uchastkalarini ajratishga doir materiallar ishlab chiquvchilar bilan qo‘shimcha ravishda kelishib olinishi lozim.
Aholi punktlari hududida obyektlarni bosh rejasiz va boshqa zarur shaharsozlik hujjatlarisiz loyihalashtirish hamda qurish taqiqlanadi.'),
        (979, 109, '2023-06-20 11:08:42', '2023-06-20 11:08:42', '55-modda. Aholi punktlarining chegara chiziqlari loyihalari
Aholi punktlarining chegara chiziqlari loyihalari hududlarni rejalashtirish tarhlari, tumanni (tumanlar guruhlarini) rejalashtirish loyihalari va aholi punktlarining bosh rejalari asosida ishlab chiqiladi.
Kichik shaharlarning, shuningdek shaharchalar, qishloqlar va ovullarning chegara chiziqlari loyihalari mazkur aholi punktlarining bosh rejalari tarkibida ishlab chiqilishi mumkin.'),
        (982, 109, '2023-06-20 11:08:42', '2023-06-20 11:08:42', '58-modda. Hududlarni qurish loyihalari
Hududlarni qurish loyihalari belgilangan qizil chiziqlar chegaralarida yoki yer uchastkalari chegaralarida batafsil rejalashtirishning tasdiqlangan loyihasi asosida ishlab chiqiladi.
Hududlarni qurish loyihalarida:
qurishni tartibga solish chiziqlari;
binolar va inshootlarning joylashuvi, qavatlari soni, turi hamda boshqa tavsiflari;
qurishning arxitektura yechimi;
muhandislik uskunalari, aloqa va obodonlashtirish tizimlari, shuningdek mazkur tizimlarni yer uchastkalari, massivlar, mavzelar, kichik dahalar va aholi punktlarini rejalashtirish tuzilmasining boshqa elementlari doirasidan tashqaridagi muhandislik uskunalari, aloqa hamda obodonlashtirish tizimlarining inshootlari va kommunikatsiyalariga ulash shartlari;
transport vositalari va piyodalar harakatining tashkil etilishi;
umumiy foydalanishdagi hududlar belgilanadi.
Mahalliy ijro etuvchi hokimiyat organlari hududlarni qurish loyihalariga ushbu Kodeksga, shaharsozlik normalari va qoidalariga zid bo‘lmagan, mahalliy sharoitlarga muvofiq qo‘shimcha talablarni, shu jumladan madaniy meros obyektlarini muhofaza qilish zonalarida loyihalashtirish va qurilishga, hududlarni ko‘kalamzorlashtirishga, kichik shakldagi arxitektura obyektlarini, reklamalarni, peshlavhalarni va boshqa axborotni joylashtirishga doir talablarni kiritishga haqli.'),
        (983, 109, '2023-06-20 11:08:42', '2023-06-20 11:08:42', '59-modda. Loyihaoldi hujjatlari
Loyihaoldi hujjatlari quyidagilardan iborat:
texnik-iqtisodiy asos;
texnik-iqtisodiy hisob-kitob.
Texnik-iqtisodiy asos yoki texnik-iqtisodiy hisob-kitob loyihaning umuman eng yuqori qiymatini va moliyalashtirish ehtiyoji hajmini loyihalarni moliyalashtirishning ishlab chiqilgan dastlabki shartlarini hisobga olgan holda belgilaydigan variantlarni ko‘rib chiqish asosida loyihani amalga oshirishning eng samarali texnik-texnologik va tashkiliy yechimini tanlashni asoslab beruvchi hujjatdir.'),
        (984, 109, '2023-06-20 11:08:42', '2023-06-20 11:08:42', '60-modda. Loyiha hujjatlari
Loyiha hujjatlari quyidagilardan iborat:
ishchi loyiha;
loyiha-smeta hujjatlari;
ishchi hujjatlar.
Loyiha hujjatlari hajmiy-rejali, amaliy va texnikaviy yechimlarni, binolarni, inshootlarni va ularning majmualarini qurish, rekonstruksiya qilish hamda mukammal ta’mirlashning, shuningdek obodonlashtirish ishlarining qiymatini belgilaydi.
Loyiha hujjatlari shaharsozlik normalari va qoidalariga muvofiq ishlab chiqiladi, maxsus vakolatli davlat organining tegishli hududiy bo‘linmalari, sanitariya-epidemiologiya xizmatlari, davlat yong‘in nazorati organlari, ekologiya va atrof-muhitni muhofaza qilish organlari va boshqa xizmatlar bilan kelishiladi.
Loyiha hujjatlarini ishlab chiqish, moliyalashtirish, tasdiqlash va ulardan foydalanish tartibini belgilash, shuningdek ularga o‘zgartirishlar kiritish buyurtmachining tashabbusiga ko‘ra hamda uning hisobidan amalga oshiriladi.
Obyektlar qurilishi tasdiqlangan loyiha hujjatlari asosida amalga oshiriladi.'),
        (985, 110, '2023-06-20 11:08:42', '2023-06-20 11:08:42', '61-modda. Qurish qoidalari
Qurish qoidalari tegishli hududlarda va aholi punktlarida shaharsozlik faoliyatini amalga oshirish tartibini belgilaydi.
Qurish qoidalari energiyani tejovchi, ekologik xavfsiz, ijtimoiy foydali texnologiyalarni qo‘llash va rivojlantirishni rag‘batlantirishi va buning uchun imkoniyat yaratishi kerak.
Qurish qoidalari hududni rejalashtirish tarhi va tumanni (tumanlar guruhlarini) rejalashtirish loyihalari, aholi punktining bosh rejasi hamda har bir hududning shaharsozlik reglamentlari asosida ishlab chiqiladi.
Qurish qoidalari joriy etilgunga qadar berilgan obyektlarni qurish uchun ruxsat beruvchi hujjatlar haqiqiy hisoblanadi, bundan aholi xavfsizligi talablari ta’minlanmayotgan hollar mustasno.'),
        (986, 110, '2023-06-20 11:08:42', '2023-06-20 11:08:42', '62-modda. Hududlarning zonalashtirilishi
Hududlarning zonalashtirilishi qulay hayot faoliyati muhitini ta’minlashga, hududlarni tabiiy va texnogen xususiyatga ega favqulodda vaziyatlar ta’siridan muhofaza qilishga, aholi hamda ishlab chiqarish haddan tashqari zich joylashishining, atrof-muhit ifloslanishining oldini olishga va muhofaza etiladigan tabiiy hududlardan foydalanishga, shuningdek madaniy meros obyektlarini, qishloq xo‘jaligi yerlari va o‘rmonzorlarni muhofaza qilishga qaratilgan.
Hududlardan shaharsozlik faoliyatini amalga oshirish uchun foydalanishga doir cheklovlar:
madaniy meros obyektlarining muhofaza zonalarida, qo‘riqxona zonalarida;
muhofaza etiladigan tabiiy hududlar zonalarida;
sanitariya zonalarida;
muhofaza zonalarida;
sanitariya-muhofaza zonalarida;
foydali qazilmalar joylashgan zonalarda;
tabiiy va texnogen xususiyatga ega favqulodda vaziyatlarning ta’siriga duchor bo‘lgan zonalarda;
favqulodda ekologik vaziyatlar va ekologik falokat zonalarida;
alohida (ekstremal) tabiiy-iqlim sharoitli zonalarda belgilanadi.
Hududlardan shaharsozlik faoliyatini amalga oshirish uchun foydalanishga doir cheklovlar qonun hujjatlariga muvofiq boshqa zonalarda ham belgilanishi mumkin.
Hududlarning funksional maqsadi va ulardan foydalanish jadalligi hududlardan foydalanishga doir cheklovlar hisobga olingan holda belgilanadi.
Aholi punkti hududining funksional zonalashtirilishini belgilovchi shaharsozlik hujjatlari yuridik va jismoniy shaxslarning, jamiyat va davlatning manfaatlaridan, ishlab chiqarishni modernizatsiyalash, investitsiyalar jalb qilish, rekreatsiya va madaniy zonalarni shakllantirish zaruratidan kelib chiqqan holda qayta ko‘rib chiqilishi lozim.'),
        (987, 110, '2023-06-20 11:08:42', '2023-06-20 11:08:42', '63-modda. Aholi punktlarining hududiy zonalari
Aholi punktlarining hududlarida quyidagi hududiy zonalar belgilanishi mumkin:
turar joy zonalari;
ijtimoiy-amaliy zonalar;
ishlab chiqarish zonalari;
muhandislik va transportga oid infratuzilmalar zonalari;
rekreatsiya zonalari;
qishloq xo‘jaligi maqsadlarida foydalaniladigan zonalar;
maxsus maqsadlarga mo‘ljallangan zonalar;
harbiy obyektlar va boshqa rejimli hududlar zonalari;
shahar atrofi zonalari.
Mahalliy sharoitlarga muvofiq mahalliy davlat hokimiyati organlari boshqa hududiy zonalarni belgilashi, shuningdek shaharsozlik normalari va qoidalarini hisobga olgan holda ularga yer uchastkalarini hamda boshqa obyektlarni kiritishi mumkin.
Aholi punktlari hududiy zonalarining chegaralari qizil chiziqlar, tabiiy obyektlarning tabiiy chegaralari, yuridik va jismoniy shaxslarning yer uchastkalari chegaralari inobatga olingan holda belgilanadi.
Har bir hududiy zona uchun shaharsozlik reglamenti bilan belgilangan huquqiy rejim unda joylashgan barcha yer uchastkalariga, binolarga, inshootlarga va ularning majmualariga nisbatan bir xil qo‘llaniladi.
Chegaralarida shaharsozlik faoliyati alohida tartibga solinishi lozim bo‘lgan hududiy zonalarda ayrim binolarni, inshootlarni hamda ularning majmualarini loyihalashtirish va qurishga nisbatan qo‘shimcha talablar belgilanishi mumkin.'),
        (988, 110, '2023-06-20 11:08:42', '2023-06-20 11:08:42', '64-modda. Turar joy zonalari
Turar joy zonalari ko‘p qavatli uylar, o‘rtacha va kam qavatli uylar, yakka tartibdagi uy-joylar qurish uchun mo‘ljallangan.
Turar joy zonalari jumlasiga jamoa bog‘dorchiligi, uzumchiligi va polizchiligini yuritish uchun ajratilgan, aholi punktlarining chegaralari doirasida joylashgan hududlar ham kiradi.
Turar joy zonalarida sanitariya-himoya zonalarini belgilash talab etilmaydigan hamda faoliyati atrof-muhitga zararli ta’sir ko‘rsatmaydigan (shovqin, tebranish, magnit maydonlari, radiatsiya ta’siri, tuproq, havo, suvning ifloslanishi va boshqa zararli ta’sirlar), alohida turgan, birgalikda qurilgan, birgalikda-qo‘shimcha qilib qurilgan yoki qo‘shimcha qilib qurilgan ijtimoiy va madaniy-maishiy obyektlarni, ibodatxonalarni, avtomobil transporti turargohlarini, sanoat, kommunal va omborxona obyektlarini joylashtirishga yo‘l qo‘yiladi.
Turar joy zonalarida yuzdan ortiq o‘tirish joyi bo‘lgan umumiy ovqatlanish korxonalarini, kino-videofilmlar namoyish qilinadigan statsionar va vaqtinchalik pavilonlarni, ochiq turdagi yozgi sahnalarni, kinoteatrlarni va raqs maydonchalarini joylashtirishga yo‘l qo‘yilmaydi.'),
        (989, 110, '2023-06-20 11:08:42', '2023-06-20 11:08:42', '65-modda. Ijtimoiy-amaliy zonalar
Ijtimoiy-amaliy zonalar amaliy, moliyaviy, ijtimoiy faollik markazlarini, sog‘liqni saqlash, madaniyat, savdo, umumiy ovqatlanish, maishiy xizmat ko‘rsatish obyektlarini, davlat ta’lim muassasalarini va nodavlat ta’lim tashkilotlarini, ma’muriy, ilmiy-tadqiqot muassasalarini, ibodatxonalar va inshootlarni, ijtimoiy soha obyektlarini, shuningdek avtomobil transporti turargohlarini joylashtirish uchun mo‘ljallangan.
Ijtimoiy-amaliy zonalarga joylashtirish uchun ruxsat etilgan obyektlar ro‘yxatiga uylar, mehmonxonalar, yer osti garajlari yoki ko‘p qavatli garajlar kiritilishi mumkin.'),
        (990, 110, '2023-06-20 11:08:42', '2023-06-20 11:08:42', '66-modda. Ishlab chiqarish zonalari
Ishlab chiqarish zonalarida ularning ish olib borishini ta’minlaydigan sanoat, kommunal, omborxona obyektlari, muhandislik va transportga oid infratuzilmalar obyektlari joylashtiriladi, shuningdek bunday obyektlarning sanitariya-muhofaza zonalari belgilanadi.
Sanoat, kommunal va omborxona obyektlarining sanitariya-muhofaza zonasida uylar, davlat ta’lim muassasalari, nodavlat ta’lim tashkilotlari, sog‘liqni saqlash, dam olish obyektlari, jismoniy tarbiya-sog‘lomlashtirish, sport inshootlari, jamoa bog‘dorchiligi, uzumchiligi va polizchiligini yuritish uchun yer uchastkalari joylashtirilishiga, shuningdek qishloq xo‘jaligi mahsuloti yetishtirilishiga yo‘l qo‘yilmaydi.
Ishlab chiqarish zonalarining hududlarini obodonlashtirish shu zonalarda joylashgan obyektlarning mulkdorlari tomonidan amalga oshiriladi.'),
        (991, 110, '2023-06-20 11:08:42', '2023-06-20 11:08:42', '67-modda. Muhandislik va transportga oid infratuzilmalar zonalari
Muhandislik va transportga oid infratuzilmalar zonalari havo, temir yo‘l, avtomobil, daryo transporti va quvur orqali yetkazib berish transporti, aloqa inshootlari hamda kommunikatsiyalarini va muhandislik uskunalarini joylashtirish hamda ularning ishlab turishi uchun mo‘ljallangan.
Qulay hayot faoliyati muhitiga transport, aloqa inshootlari, kommunikatsiyalari hamda muhandislik uskunalari zararli ta’sir ko‘rsatishining oldini olish bunday obyektlardan to turar joylar, ijtimoiy-amaliy, rekreatsiya zonalari hududlariga qadar zarur masofa bo‘lishiga, shuningdek shaharsozlik normalari va qoidalari, qurishning maxsus normativlari hamda qoidalari talablariga rioya etilishi orqali ta’minlanadi.
Transport, aloqa inshootlari va kommunikatsiyalarining, muhandislik uskunalarining yer uchastkalari, shuningdek ularning sanitariya-muhofaza zonalari chegaralaridagi hududlar ushbu yer uchastkalarining mulkdorlari, egalari va ulardan foydalanuvchilar tomonidan obodonlashtirilishi lozim.
Foydalanilishi fuqarolarning hayoti va sog‘lig‘iga to‘g‘ridan to‘g‘ri zararli ta’sir ko‘rsatadigan transport, aloqa inshootlari va kommunikatsiyalarini hamda muhandislik uskunalarini aholi punktlari doirasida joylashtirishga yo‘l qo‘yilmaydi.'),
        (992, 110, '2023-06-20 11:08:42', '2023-06-20 11:08:42', '68-modda. Rekreatsiya zonalari
Rekreatsiya zonalari turizmni va aholining ommaviy dam olishini tashkil etish uchun mo‘ljallangan bo‘lib, ko‘kalamzorlar hududlari va obyektlari (shahar o‘rmonlari, o‘rmon-bog‘lar, istirohat bog‘lari, xiyobonlar, bog‘lar, sayilgohlar), hayvonot bog‘lari, hovuzlar, plyajlar, sohilbo‘yi obyektlari va boshqa obyektlarni o‘z ichiga oladi.
Rekreatsiya zonalarining hududlarida rekreatsiya-sog‘lomlashtirish maqsadlariga mo‘ljallangan obyektlardan foydalanish bilan bevosita bog‘liq bo‘lmagan mavjud sanoat, kommunal, omborxona obyektlari qurilishiga va kengaytirilishiga yo‘l qo‘yilmaydi.'),
        (993, 110, '2023-06-20 11:08:42', '2023-06-20 11:08:42', '69-modda. Qishloq xo‘jaligi maqsadlarida foydalaniladigan zonalar
Aholi punktlarining chegaralari chizig‘i doirasida haydaladigan yerlar, bog‘lar, tokzorlar, polizlar, pichanzorlar, yaylovlar va boshqa yer maydonlari, shuningdek qishloq xo‘jaligiga mo‘ljallangan binolar va inshootlar egallab turgan, qishloq xo‘jaligi maqsadlarida foydalaniladigan zonalar ajratiladi. Mazkur zonalarning hududlaridan foydalanish turi aholi punktining bosh rejasiga va qurish qoidalariga muvofiq o‘zgartirilguniga qadar bu hududlardan qishloq xo‘jaligini yuritish maqsadida foydalanilishi mumkin.'),
        (994, 110, '2023-06-20 11:08:42', '2023-06-20 11:08:42', '70-modda. Maxsus maqsadlarga mo‘ljallangan zonalar
Maxsus maqsadlarga mo‘ljallangan zonalar foydalanilishi aholi punktlari hududiy zonalarining boshqa turlaridan foydalanishga to‘g‘ri kelmaydigan dafn qilish joylari, chorva mollari ko‘miladigan joylarni, maishiy chiqindilar tashlanadigan axlatxonalarni va boshqa obyektlarni joylashtirish uchun ajratiladi.
Maxsus maqsadlarga mo‘ljallangan zonalar uchun sanitariya-himoya zonalari o‘rnatiladi.
Maxsus maqsadlarga mo‘ljallangan zonalarning hududlaridan foydalanish tartibi shaharsozlik normalari va qoidalarining, maxsus normativlarning va qurish qoidalarining talablari inobatga olingan holda belgilanadi.'),
        (995, 110, '2023-06-20 11:08:42', '2023-06-20 11:08:42', '71-modda. Harbiy obyektlarning va boshqa rejimli hududlarning zonalari
Harbiy obyektlarning va boshqa rejimli hududlarning zonalari o‘ziga nisbatan alohida rejim belgilanadigan obyektlarni joylashtirish uchun mo‘ljallangan. Mazkur zonalarning hududlaridan aholi punktlari chegaralari doirasida foydalanish tartibi shaharsozlik normalari va qoidalarining, maxsus normativlarning va qurish qoidalarining talablari inobatga olingan holda O‘zbekiston Respublikasi Vazirlar Mahkamasi tomonidan belgilanadi.'),
        (996, 110, '2023-06-20 11:08:42', '2023-06-20 11:08:42', '72-modda. Shahar atrofi zonalari
Shahar atrofi zonasi shahar chegarasidan tashqaridagi, shahar bilan yagona ijtimoiy, tabiiy, xo‘jalik hududini tashkil etuvchi, ushbu shahar hududini, shahar atrofi zonasiga kiruvchi aholi punktlari hududlarini, aholi punktlariaro hududlarni (shaharni rivojlantirish uchun zaxira hududni) rivojlantirish uchun hamda sanoat, kommunal, omborxona obyektlarini, muhandislik, transportga oid infratuzilmalar obyektlarini, jamoa bog‘dorchiligi, uzumchiligi va polizchiligini yuritish uchun yer uchastkalarini, aholi dam olish joylarini joylashtirish uchun, shuningdek qishloq xo‘jaligini yuritish hamda muhofaza va sanitariya-gigiyena vazifalarini bajarish uchun mo‘ljallangan yerlarni o‘z ichiga oladi.
Shahar atrofi zonalari chegaralarini belgilash ushbu Kodeksga hamda yer to‘g‘risidagi qonun hujjatlariga muvofiq shaharsozlik va yer tuzish hujjatlari asosida amalga oshiriladi.'),
        (997, 110, '2023-06-20 11:08:42', '2023-06-20 11:08:42', '73-modda. Shahar atrofi zonasi hududidan foydalanish
Shahar atrofi zonasi hududidan foydalanish hamda uning chegaralaridagi shaharsozlik faoliyati yuridik va jismoniy shaxslarning, jamiyat va davlatning, shahar atrofi zonasiga kiruvchi aholi punktlariaro hududlarning manfaatlari inobatga olingan holda amalga oshiriladi.
Shahar atrofi zonalari hududlarining zonalashtirilishi Qoraqalpog‘iston Respublikasi va viloyatlar hududlarini rejalashtirish tarhlarida, tumanni (tumanlar guruhlarini) rejalashtirish loyihalarida, shuningdek shaharlarning o‘z shahar atrofi zonalari bilan birgalikda ishlab chiqiladigan bosh rejalarida belgilanadi.
Shahar atrofi zonalarida sanitariya-himoya va rekreatsiya vazifalarini bajaradigan hududlar ajratiladi. Ushbu hududlarda atrof-muhitga zararli ta’sir ko‘rsatadigan xo‘jalik faoliyati va boshqa xildagi faoliyat taqiqlanadi.
Shahar atrofi zonasi chegaralari doirasida binolarni, inshootlarni hamda ularning majmualarini qurish, rekonstruksiya qilish va kengaytirish shaharning ijro etuvchi hokimiyat organi bilan kelishgan holda amalga oshiriladi.'),
        (998, 111, '2023-06-20 11:08:42', '2023-06-20 11:08:42', '74-modda. Shaharsozlik faoliyatini alohida tartibga solish
Agar hududdan foydalanishning maxsus qoidalarini joriy etmay turib, shaharsozlik sohasida yuridik va jismoniy shaxslarning, jamiyat va davlatning manfaatlarini ta’minlash mumkin bo‘lmasa yoki qiyin bo‘lsa, shaharsozlik faoliyati alohida tartibga solinishi lozim.
Shaharsozlik faoliyatini alohida tartibga solish:
hududlardan foydalanishning maxsus qoidalarini joriy etish;
maxsus shaharsozlik normalari va qoidalarini joriy etish;
shaharsozlik hujjatlarini ishlab chiqishning, tasdiqlashning va amalga oshirishning alohida tartibini joriy etish;
hududlarni va aholi punktlarini zararli ta’sirlardan himoya qilish bo‘yicha maxsus tarhlar hamda loyihalarni ishlab chiqish;
shaharsozlik faoliyatini amalga oshirish uchun maxsus ruxsatnomalar berish yo‘li bilan amalga oshiriladi.'),
        (999, 111, '2023-06-20 11:08:42', '2023-06-20 11:08:42', '75-modda. Alohida tartibga solinishi lozim bo‘lgan shaharsozlik faoliyati obyektlari
Alohida tartibga solinishi lozim bo‘lgan shaharsozlik faoliyati obyektlari umumdavlat yoki mahalliy ahamiyatga molik bo‘lishi mumkin.
Shaharsozlik faoliyati quyidagi hollarda alohida tartibga solinishi lozim, agar:
hududlar hamda aholi punktlari tabiiy va texnogen xususiyatga ega favqulodda vaziyatlar ta’siriga duchor bo‘lgan bo‘lsa;
hududlar yo‘l qo‘yiladigan eng yuqori konsentratsiyalardan ortiqcha kimyoviy va biologik moddalar, zararli mikroorganizmlar, yo‘l qo‘yiladigan eng yuqori darajalardan ortiqcha miqdorlarda radioaktiv moddalar bilan ifloslangan bo‘lsa;
shaharsozlik faoliyati muhofaza etiladigan tabiiy hududlarda amalga oshirilayotgan bo‘lsa.
Alohida tartibga solinadigan shaharsozlik faoliyati obyektlari jumlasiga quyidagilar ham kiritilishi mumkin:
muhofaza etiladigan noyob landshaftlarga ega hududlar;
muhofaza etiladigan tabiiy hududlar;
kurort uchun mo‘ljallangan hududlar;
rekreatsiya uchun mo‘ljallangan hududlar;
maxsus iqtisodiy zonalarning hududlari;
kichik sanoat zonalarining hududlari;
suv resurslarining muhofaza zonalari;
Toshkent shahri hududi;
Nukus shahri hududi;
viloyatlarning ma’muriy markazlari bo‘lgan shaharlar hududlari;
Toshkent va Nukus shaharlariga, viloyatlar va tumanlarning ma’muriy markazlariga tutash zonalar;
madaniy meros obyektlari mavjud bo‘lgan aholi punktlari;
kurort aholi punktlari;
qo‘riqxona aholi punktlari;
turistik salohiyatiga ega bo‘lgan aholi punktlari;
alohida (ekstremal) tabiiy-iqlim sharoitlariga ega bo‘lgan aholi punktlari;
hayot faoliyatining alohida rejimi belgilangan aholi punktlari (harbiy obyektlar va boshqa rejimli hududlar, davlat qo‘riqxonalarining hududida hamda boshqa qo‘riqlanadigan tabiiy hududlarda joylashgan aholi punktlari).'),
        (1000, 111, '2023-06-20 11:08:42', '2023-06-20 11:08:42', '76-modda. Alohida tartibga solinadigan shaharsozlik faoliyati obyektlarini umumdavlat va mahalliy ahamiyatga molik obyektlar jumlasiga kiritish hamda ularning chegaralarini belgilash
Alohida tartibga solinadigan shaharsozlik faoliyati obyektlarini umumdavlat ahamiyatiga molik obyektlar jumlasiga kiritish, shuningdek ularning chegaralarini belgilash O‘zbekiston Respublikasi Vazirlar Mahkamasi tomonidan:
tabiiy va texnogen xususiyatga ega favqulodda vaziyatlar ta’siriga duchor bo‘lgan hududlarda — O‘zbekiston Respublikasi Favqulodda vaziyatlar vazirligining manfaatdor vazirliklar, davlat qo‘mitalari va idoralar, Qoraqalpog‘iston Respublikasi Vazirlar Kengashi, viloyatlar va Toshkent shahar ijro etuvchi hokimiyat organlari ma’lumotlari asosidagi taqdimnomasiga ko‘ra;
yo‘l qo‘yiladigan eng yuqori konsentratsiyalardan ortiqcha kimyoviy va (yoki) biologik moddalar, zararli mikroorganizmlar bilan, yo‘l qo‘yiladigan eng yuqori darajalardan ortiqcha radioaktiv moddalar bilan ifloslangan hududlarda — O‘zbekiston Respublikasi Favqulodda vaziyatlar vazirligining hamda O‘zbekiston Respublikasi Ekologiya va atrof-muhitni muhofaza qilish davlat qo‘mitasining taqdimnomalariga ko‘ra;
Toshkent shahri hududida — O‘zbekiston Respublikasi Qurilish vazirligining hamda Toshkent shahar ijro etuvchi hokimiyat organining taqdimnomalariga ko‘ra;
moddiy madaniy meros obyektlari mavjud bo‘lgan shaharlar hududlarida — O‘zbekiston Respublikasi Madaniyat vazirligining hamda O‘zbekiston Respublikasi Qurilish vazirligining taqdimnomalariga ko‘ra;
kurort shaharlar hududlarida — O‘zbekiston Respublikasi Sog‘liqni saqlash vazirligining, O‘zbekiston Kasaba uyushmalari Federatsiyasi Kengashining hamda O‘zbekiston Respublikasi Qurilish vazirligining taqdimnomalariga ko‘ra;
qo‘riqxona shaharlar hududlarida — O‘zbekiston Respublikasi Madaniyat vazirligining hamda O‘zbekiston Respublikasi Qurilish vazirligining taqdimnomalariga ko‘ra;
hayot faoliyatining alohida rejimiga ega bo‘lgan aholi punktlari (harbiy obyektlar, boshqa rejimli hududlar, davlat qo‘riqxonalaridagi va boshqa qo‘riqlanadigan tabiiy hududlardagi aholi punktlari) hududlarida — manfaatdor vazirliklarning, davlat qo‘mitalarining, idoralarning taqdimnomalariga ko‘ra;
muhofaza etiladigan tabiiy hududlarda — manfaatdor vazirliklarning, davlat qo‘mitalarining va idoralarning taqdimnomasiga ko‘ra amalga oshiriladi.
Alohida tartibga solinadigan shaharsozlik faoliyati obyektlarini mahalliy ahamiyatga molik (umumdavlat ahamiyatiga molik obyektlar jumlasiga kiritilmagan) obyektlar jumlasiga kiritish, shuningdek ularning chegaralarini belgilash O‘zbekiston Respublikasi Favqulodda vaziyatlar vazirligi tomonidan:
tabiiy va texnogen xususiyatga ega favqulodda vaziyatlar ta’siriga duchor bo‘lgan hududlarda — Qoraqalpog‘iston Respublikasi Vazirlar Kengashining, viloyatlar va Toshkent shahar ijro etuvchi hokimiyat organlarining taqdimnomalariga ko‘ra, O‘zbekiston Respublikasi Qurilish vazirligi bilan kelishilgan holda;
yo‘l qo‘yiladigan eng yuqori konsentratsiyalardan ortiqcha kimyoviy va (yoki) biologik moddalar, zararli mikroorganizmlar bilan, yo‘l qo‘yiladigan eng yuqori darajalardan ortiqcha radioaktiv moddalar bilan ifloslangan hududlarda — Qoraqalpog‘iston Respublikasi Vazirlar Kengashining, viloyatlar va Toshkent shahar ijro etuvchi hokimiyat organlarining taqdimnomalariga ko‘ra hamda O‘zbekiston Respublikasi Qurilish vazirligi, O‘zbekiston Respublikasi Sog‘liqni saqlash vazirligi va O‘zbekiston Respublikasi Ekologiya va atrof-muhitni muhofaza qilish davlat qo‘mitasi bilan kelishilgan holda amalga oshiriladi.
Alohida tartibga solinadigan shaharsozlik faoliyati obyektlarini mahalliy ahamiyatga molik (umumdavlat ahamiyatiga molik obyektlar jumlasiga kiritilmagan) obyektlar jumlasiga kiritish, shuningdek ularning chegaralarini belgilash Qoraqalpog‘iston Respublikasi Vazirlar Kengashi hamda viloyatlar va Toshkent shahar ijro etuvchi hokimiyat organlari tomonidan:
muhofaza etiladigan tabiiy hududlarda — O‘zbekiston Respublikasi Qurilish vazirligi, O‘zbekiston Respublikasi Ekologiya va atrof-muhitni muhofaza qilish davlat qo‘mitasi, manfaatdor vazirliklar, davlat qo‘mitalari va idoralar bilan kelishilgan holda;
hayot faoliyatining alohida rejimiga ega bo‘lgan aholi punktlari (harbiy obyektlar, boshqa rejimli hududlar, davlat qo‘riqxonalari hududlarida va qo‘riqlanadigan boshqa tabiiy hududlarda joylashgan aholi punktlari) hududlarida — manfaatdor vazirliklar, davlat qo‘mitalari va idoralar bilan kelishilgan holda;
Nukus shahri hamda viloyatlarning ma’muriy markazlari bo‘lgan shaharlar hududlarida, shuningdek madaniy meros obyektlari mavjud bo‘lgan shaharlar hududlarida — tegishli mahalliy ijro etuvchi hokimiyat organlarining taqdimnomasiga ko‘ra amalga oshiriladi.
Alohida tartibga solinadigan shaharsozlik faoliyati obyektlarining chegaralari aholi punktlarining hududlarini rivojlantirishni rejalashtirish va qurish to‘g‘risidagi tasdiqlangan shaharsozlik hujjatlari asosida belgilanadi hamda ma’muriy-hududiy birliklar chegaralari bilan mos kelmasligi mumkin.'),
        (1001, 111, '2023-06-20 11:08:42', '2023-06-20 11:08:42', '77-modda. Tabiiy va texnogen xususiyatga ega favqulodda vaziyatlar ta’siriga duchor bo‘lgan hududlarda shaharsozlik faoliyatini alohida tartibga solish
Yo‘l qo‘yiladigan eng yuqori konsentratsiyalardan ortiqcha kimyoviy va (yoki) biologik moddalar, zararli mikroorganizmlar bilan, yo‘l qo‘yiladigan eng yuqori darajalardan ortiqcha radioaktiv moddalar bilan ifloslangan hududlar, agar bunday hududlarda aholining yashashi va xo‘jalik faoliyatini amalga oshirish inson hayoti hamda sog‘lig‘iga tahdid tug‘dirayotgan bo‘lsa, konservatsiya qilinishi va ularga maxsus ishlov berilishi lozim. Mazkur hududlarning ifloslanish darajasiga hamda ularni sog‘lomlashtirish usuliga qarab O‘zbekiston Respublikasi Vazirlar Mahkamasi tomonidan ulardan foydalanishning alohida rejimi joriy etilishi yoki boshqa funksional maqsadi belgilanishi mumkin.
Aholi punktlari hududlarining xavfsizligi konsepsiyasini amalga oshirish maqsadida fuqaro muhofazasi talablariga, tabiiy va texnogen xususiyatga ega favqulodda vaziyatlarning oldini olish, shuningdek terrorchilik harakatlariga qarshi kurashish talablariga rioya etilgan holda shaharsozlik faoliyatini amalga oshirish chora-tadbirlari ko‘riladi.
Shaharsozlik faoliyati obyektlarida alohida tartibga solishni joriy etish muddatlari shaharsozlik faoliyati sohasidagi davlat boshqaruvi organlari tomonidan belgilanadi. Alohida tartibga solish rejimi amalda bo‘lgan davrda shaharsozlik faoliyati ushbu Kodeksning 74-moddasida nazarda tutilgan tadbirlarga muvofiq amalga oshiriladi.'),
        (1002, 111, '2023-06-20 11:08:42', '2023-06-20 11:08:42', '78-modda. Obyektlarni qurish uchun maxsus ruxsatnomalar
Obyektlar to‘g‘risidagi axborot davlat sirlariga yoki qonun bilan qo‘riqlanadigan boshqa sirga taalluqli bo‘lgan obyektlar, shuningdek alohida tartibga solinadigan shaharsozlik faoliyati obyektlarini qurish uchun maxsus ruxsatnomalar qonun hujjatlariga muvofiq beriladi.'),
        (1003, 112, '2023-06-20 11:08:42', '2023-06-20 11:08:42', '79-modda. Aholi punktlarida yer uchastkalaridan foydalanishga doir shaharsozlik talablari
Yer uchastkalaridan foydalanishga doir shaharsozlik talablari aholi punktlarining bosh rejalari, batafsil rejalashtirish loyihalari, qurish loyihalari va qoidalari bilan belgilanadi.
Mahalliy ijro etuvchi hokimiyat organlari yer uchastkalari berilayotganda yuridik va jismoniy shaxslarni yer uchastkalaridan foydalanishga doir shaharsozlik talablari to‘g‘risidagi axborot bilan ta’minlaydi.
Yuridik va jismoniy shaxslarga qurish uchun yer uchastkalari berilayotganda (realizatsiya qilinayotganda) mahalliy davlat hokimiyati organlari tomonidan yer uchastkalariga nisbatan quyidagi shaharsozlik talablari va yer uchastkasini saqlash vazifalari belgilanishi mumkin:
maxsus vakolatli davlat organining hududiy bo‘linmalari tomonidan berilgan arxitektura-rejalashtirish topshirig‘iga binoan hududni qurish loyihasini ishlab chiqish bo‘yicha;
qurilishga tutash bo‘lgan umumiy foydalanishdagi hududni obodonlashtirish bo‘yicha;
obyektlar qurilishini boshlash va tamomlash muddatlariga rioya etish bo‘yicha.
Obyekt qurilishi qurilish boshlangan sanadan e’tiboran ikki yil ichida yoki shaharsozlik hujjatlarida belgilangan muddatlarda tugallanmagan taqdirda, obyekt tugallanmagan deb hisoblanadi.
Yangi qurilish mazkur qurilish hududi aholisining tegishli xizmatlar bilan ta’minlanishi shaharsozlik normalari va qoidalarida nazarda tutilgan normalardan pasayishiga olib keladigan bo‘lsa, yuridik va jismoniy shaxslarga yer uchastkalari ular muhandislik, transportga oid va ijtimoiy infratuzilmalarning qo‘shimcha obyektlarini qurishda ulushli ishtirok etganda berilishi (realizatsiya qilinishi) mumkin.
Ushbu moddaning uchinchi qismida ko‘rsatilgan talablar va yer uchastkasini saqlash vazifalari yer uchastkasiga bo‘lgan huquqlar boshqa shaxsga o‘tgan taqdirda saqlanib qoladi.
Ushbu moddaning uchinchi, to‘rtinchi va beshinchi qismlari yakka tartibdagi uy-joy qurilishi uchun berilgan (realizatsiya qilingan) yer uchastkalarida amalga oshiriladigan qurilishga nisbatan qo‘llanilmaydi.
Yer uchastkasiga bo‘lgan huquqni tasdiqlaydigan hujjatlarda quyidagilar ko‘rsatilishi kerak:
hududiy zonaning qurish qoidalarida belgilangan kodi;
doirasida yer uchastkasi joylashgan hududiy zonaning funksional maqsadi;
yer uchastkasi yoki uning qismi madaniy meros obyektlarini muhofaza qilish zonalarining, muhofaza etiladigan tabiiy hududlarning va suvni muhofaza qilish zonalarining chegaralarida, shuningdek tabiiy hamda texnogen xususiyatga ega favqulodda vaziyatlarning ta’siriga duchor bo‘lgan hududlar va aholi punktlarining hamda foydalanilishiga qonunda cheklovlar belgilanadigan boshqa hududlarning chegaralarida joylashganligi;
yer uchastkasi chegaralarida muhandislik, transportga oid va ijtimoiy infratuzilmalar obyektlarining mavjudligi ushbu obyektlarning ko‘rsatkichlari va ulardan foydalanishga doir cheklovlar ko‘rsatilgan holda;
yer uchastkasi chegaralarida yer uchastkasining mulkdoriga, egasiga yoki bu uchastkadan foydalanuvchiga tegishli bo‘lmagan binolar va inshootlarning mavjudligi ushbu yer uchastkasida ko‘rsatib o‘tilgan obyektlar joylashganligi munosabati bilan undan foydalanishga doir cheklovlar ko‘rsatilgan holda.
Yer uchastkalari bo‘lib berilayotganda yoki ularning chegaralari va ko‘rsatkichlari o‘zgarganda qizil chiziqlarga, qurishni tartibga solish chiziqlariga hamda shaharsozlik reglamentlarining eng kichik o‘lchamdagi yer uchastkalariga doir talablariga rioya etiladi.
Yer uchastkalari hamda ularda joylashgan binolar, inshootlar va boshqa obyektlar bo‘lingan taqdirda ularning qismlari mustaqil obyektlar sifatida ishlab turishi uchun shart-sharoitlar ta’minlanishi kerak.'),
        (1004, 112, '2023-06-20 11:08:42', '2023-06-20 11:08:42', '80-modda. Obyektlarni rekonstruksiya qilish jarayonida yer uchastkalariga, bino va inshootlarga qo‘yiladigan talablar
Binolardan foydalanish sharoitlarini o‘zgartirish, ularning jismoniy eskirishi tufayli yuzaga kelgan yo‘qotishlar o‘rnini imkoni boricha to‘ldirish, binolardan foydalanishning yangi maqsadlariga erishish uchun asosiy texnik-iqtisodiy ko‘rsatkichlarni (yuklamalarni, xonalarning rejalashtirilishini, binoning qurilish hajmini va umumiy maydonini, muhandislik jihatdan jihozlanishini) o‘zgartirish bilan bog‘liq qurilish ishlari va tashkiliy-texnik tadbirlar majmui obyektlarni rekonstruksiya qilishdir.
Obyektlarni rekonstruksiya qilish, shu jumladan buzilgan binolar va inshootlar o‘rnida yer uchastkasidan foydalanish maqsadini o‘zgartirgan holda yangi obyektlar qurish uchun ruxsatnomalar aholi punktlarining rivojlanishini rejalashtirish va hududlarini qurish to‘g‘risidagi shaharsozlik hujjatlari asosida sanitariya-epidemiologiya xizmatlari, davlat yong‘in nazorati organlari bilan kelishuvga ko‘ra maxsus vakolatli davlat organining hududiy bo‘linmalari tomonidan beriladi.
Aholi punktlarining rivojlanishini rejalashtirish va hududlarini qurish to‘g‘risidagi shaharsozlik hujjatlari mavjud bo‘lmagan taqdirda ruxsatnomalar berish masalalari Qoraqalpog‘iston Respublikasi Qurilish vazirligi, viloyatlar va Toshkent shahar qurilish bosh boshqarmalari huzuridagi arxitektura-shaharsozlik kengashlari bilan kelishiladi.
Obyektlarni rekonstruksiya qilishda, shu jumladan buzilgan binolar va inshootlar o‘rnida yer uchastkasidan foydalanish maqsadini o‘zgartirgan holda yangi obyektlar qurishda buyurtmachilar zimmasiga ushbu Kodeksda nazarda tutilgan majburiyatlar yuklanadi.'),
        (1005, 112, '2023-06-20 11:08:42', '2023-06-20 11:08:42', '81-modda. Shaharsozlik faoliyatini amalga oshirishda yer uchastkalariga doir servitutlar va yer uchastkalariga bo‘lgan huquqlarni saqlash vazifalari
Shaharsozlik faoliyatini amalga oshirishda servitutlar shaharsozlik faoliyati subyektlarining manfaatlari shaharsozlik faoliyati obyektlarini qurish, rekonstruksiya qilish, ta’mirlash va ularning but saqlanishini ta’minlash bo‘yicha boshqa ehtiyojlar uchun o‘zga yer uchastkasidan cheklangan tarzda foydalanish huquqisiz ta’minlanishi mumkin bo‘lmagan taqdirda belgilanadi.
Shaharsozlik faoliyatini amalga oshirishda yer uchastkalariga doir servitutlar va yer uchastkalariga bo‘lgan huquqlarni saqlash vazifalari yer uchastkalarining mulkdorlari, egalari va bunday uchastkalardan foydalanuvchilarning majburiyatlarini hamda ularning huquqlari cheklanishini o‘z ichiga oladi va shaharsozlik hujjatlari hamda qurish qoidalari asosida belgilanadi.
Yer uchastkalariga doir servitutlarni va yer uchastkalariga bo‘lgan huquqlarni saqlash vazifalarini belgilash hamda bekor qilish tartibi yer to‘g‘risidagi qonun hujjatlarida belgilanadi.'),
        (1006, 112, '2023-06-20 11:08:42', '2023-06-20 11:08:42', '82-modda. Yer uchastkalarini kompensatsiya evaziga jamoat ehtiyojlari uchun olib qo‘yish
Jismoniy va yuridik shaxslarga tegishincha meros qilib qoldiriladigan umrbod egalik qilish, doimiy egalik qilish, doimiy foydalanish, muddatli (vaqtincha) foydalanish yoki ijara huquqi asosida tegishli bo‘lgan yer uchastkalarini kompensatsiya evaziga jamoat ehtiyojlari uchun olib qo‘yish qonunda nazarda tutilgan tartibda amalga oshiriladi.'),
        (1007, 113, '2023-06-20 11:08:42', '2023-06-20 11:08:42', '83-modda. Shaharsozlik faoliyatini moliyalashtirish
Shaharsozlik faoliyatini moliyalashtirish budjet tizimi budjetlarining mablag‘lari, buyurtmachilarning o‘z mablag‘lari va jalb qilingan mablag‘lari hisobidan, yuridik va jismoniy shaxslarning muhandislik, transportga oid va ijtimoiy infratuzilmalarni rivojlantirishda ulushli ishtirok etishi, shuningdek qonun hujjatlarida taqiqlanmagan boshqa manbalar hisobidan amalga oshiriladi.
Shaharsozlik faoliyatini moliyalashtirish quyidagi hollarda budjet tizimi budjetlarining mablag‘lari hisobidan amalga oshiriladi:
ushbu Kodeks 44-moddasi birinchi qismining 1 — 4-bandlarida nazarda tutilgan shaharsozlik hujjatlarini ishlab chiqishda, kompleks muhandislik qidiruvlarini o‘tkazishda, mikroseysmik hududlashtirish xaritalarini tuzishda va shaharsozlik hujjatlarini ekspertizadan o‘tkazishda;
shaharsozlik faoliyatiga oid ilmiy-tadqiqot ishlarini olib borishda, shaharsozlik normalari va qoidalarini ishlab chiqishda;
favqulodda ekologik vaziyatlar va ekologik falokat zonalarini, tarixiy manzilgohlarni, umumdavlat ahamiyatiga molik muhandislik, transportga oid hamda ijtimoiy infratuzilmalarni rivojlantirishning belgilangan maqsadli umumdavlat dasturlarini, boshqa maqsadli umumdavlat dasturlarini ishlab chiqish va amalga oshirishda, shuningdek davlat shaharsozlik kadastrini yuritishda.
Shaharsozlik hujjatlarini ishlab chiqishni moliyalashtirish ushbu moddaning birinchi qismida nazarda tutilgan manbalardan tashqari, Qoraqalpog‘iston Respublikasi Vazirlar Kengashi, viloyatlar va Toshkent shahar ijro etuvchi hokimiyat organlari ixtiyoriga davlat korxonalarini xususiylashtirishdan tushadigan mablag‘larning bir qismi hisobidan ham amalga oshiriladi.
Agar hududni shaharsozlik jihatidan o‘zlashtirish davlat manfaatlariga, ikki va undan ortiq viloyatning manfaatlariga yoki aholi punktlari guruhlarining manfaatlariga daxldor bo‘lsa, budjet mablag‘laridan boshqa shaharsozlik faoliyatini moliyalashtirish uchun foydalaniladi.
Qoraqalpog‘iston Respublikasi Vazirlar Kengashi, mahalliy ijro etuvchi hokimiyat organlari har yilgi qurilishlarning manzilli ro‘yxatlarini shakllantirishda aholi punktlari bosh rejalarining ishlab chiqilishini, kompleks muhandislik qidiruvlari o‘tkazilishini, aholi punktlarini mikroseysmik hududlashtirish xaritalarini, tabiatni muhofaza qilishning hududiy kompleks tarhlarini tuzishni, joriy qurish ustidan mualliflik nazorati hamda aholi punktlari bosh rejalarining amalga oshirilishi monitoringi ishlab chiquvchilar tomonidan o‘tkazilishini, shaharsozlik hujjatlari kelishib olinishi va ekspertizadan o‘tkazilishini mahalliy budjet mablag‘lari hisobidan moliyalashtirishni nazarda tutishi kerak.'),
        (1008, 113, '2023-06-20 11:08:42', '2023-06-20 11:08:42', '84-modda. Nizolarni hal etish
Shaharsozlik faoliyati sohasidagi nizolar qonun hujjatlarida belgilangan tartibda hal etiladi.'),
        (1009, 113, '2023-06-20 11:08:42', '2023-06-20 11:08:42', '85-modda. Shaharsozlik to‘g‘risidagi qonun hujjatlarini buzganlik uchun javobgarlik
Shaharsozlik to‘g‘risidagi qonun hujjatlarini buzganlikda aybdor shaxslar belgilangan tartibda javobgar bo‘ladi.'),
        (1290, 123, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '20. Ariza beruvchi invoysda ko‘rsatilgan summani invoys tushgan kundan boshlab bir oy muddatda to‘lashi shart.
Obyektda davlat qurilish nazoratini amalga oshirish uchun to‘lov qiymati to‘langanligi elektron tizim orqali tasdiqlanadi.'),
        (1291, 123, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '21. Obyektda davlat qurilish nazoratini amalga oshirishdan tushgan mablag‘ to‘liq miqdorda hududiy inspeksiyaning bank hisobvarag‘iga o‘tkaziladi.'),
        (1179, 119, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '22. Ariza beruvchi davlat organlari va boshqa tashkilotlar xodimlarining harakatlari (harakatsizligi) yuzasidan qonunchilik hujjatlarida belgilangan tartibda shikoyat qilishi mumkin.'),
        (1159, 119, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '2. Ushbu Reglamentda quyidagi asosiy tushunchalardan foydalaniladi:
bino — funksional maqsadiga qarab odamlar yashashi yoki bo‘lishiga va har xil turdagi ishlab chiqarish jarayonlarini bajarishga mo‘ljallangan, tayanch, to‘sma yoki har ikkala maqsadga xizmat qiluvchi konstruksiyalardan iborat qurilish tizimi;
inshoot — har xil turdagi ishlab chiqarish jarayonlarini bajarish, moddiy qimmatliklarni joylashtirish va saqlash, odamlarning vaqtincha bo‘lishi (harakatlanishi), shuningdek, uskunalar yoki kommunikatsiyalarni joylashtirish (o‘tkazish, ulash) uchun mo‘ljallangan, tayanch, to‘sma yoki har ikkala maqsadga xizmat qiluvchi konstruksiyalardan iborat hajmiy, yassi yoki chiziq tarzidagi qurilish tizimi;
bino va inshoot fasadining eskiz loyihasi — bino va inshoot fasadiga kiritiladigan o‘zgartirishlarning yakuniy natijasi to‘g‘risida to‘liq tasavvur beruvchi bino va inshoot fasadining rangli tasvirdagi arxitekturaviy chizmasi.'),
        (1160, 119, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '3. Ushbu Reglamentning amal qilishi bino va inshootning hajmi va maydonini o‘zgartirmasdan turib ko‘cha (ko‘chalar)dan ko‘rinadigan old va yon fasadlar (fasad maydonining 25 foizidan ortiq qismi)dagi arxitekturaviy elementlar (tsokoli, quyoshdan himoyalash elementlari, parapetlari, ustunlari, tomi, derazalari, peshayvoni, ayvonlari va shu kabilar)ning rangi, konfiguratsiyasi va o‘lchamlari o‘zgartirilishiga tatbiq etiladi.
Bino va inshootning, yakka tartibdagi uy-joyning tashqi ko‘rinishini o‘zgartirish bo‘yicha agar bino va inshoot, yakka tartibdagi uy-joy magistral ko‘chalar, xalqaro, davlat ahamiyatiga ega bo‘lgan avtomobil yo‘llari bo‘ylab joylashgan hollarda fasad maydonining 25 foizidan ortiq qismidan qat’i nazar kelishish talab etiladi.
Yakka tartibdagi uy-joylar, tumanlarning ma’muriy markazi bo‘lmagan shaharchalarda, qishloq aholi punktlari (qishloq va ovullar)da hamda aholi punktlari tashqarisida joylashgan bino va inshootlarning tashqi ko‘rinishini o‘zgartirish uchun kelishish talab etilmaydi.
Bino va inshoot fasadining eskiz loyihasi tegishli litsenziyasi bo‘lgan loyiha tashkiloti tomonidan ishlab chiqiladi.
Bino va inshootning tashqi ko‘rinishini o‘zgartirgandan so‘ng obyektni foydalanishga qabul qilish talab etilmaydi.'),
        (1161, 119, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '4. Bino va inshoot fasadining eskiz loyihasini kelishmasdan turib bino va inshoot tashqi ko‘rinishini o‘zgartirish taqiqlanadi.'),
        (1162, 119, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '5. Bino va inshootning tashqi ko‘rinishini o‘zgartirishni kelishish tartibotini amalga oshirishda bino va inshoot fasadining eskiz loyihasi tuman (shahar) qurilish bo‘limlari (keyingi o‘rinlarda — vakolatli organ) bilan kelishiladi.'),
        (1163, 119, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '6. Davlat xizmatlari markazlari va vakolatli organlar o‘rtasidagi axborot almashuv elektron raqamli imzodan (keyingi o‘rinlarda — ERI) foydalangan holda faqat elektron tarzda amalga oshiriladi.'),
        (1164, 119, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '7. Bino va inshootlarning tashqi ko‘rinishini o‘zgartirishni kelishish ushbu Reglamentga 1-ilovada keltirilgan sxemaga muvofiq amalga oshiriladi.'),
        (1165, 119, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '8. Ariza beruvchilar bino va inshoot tashqi ko‘rinishini o‘zgartirishni kelishish uchun Davlat xizmatlari markazlariga o‘zi kelib murojaat etadi yoki O‘zbekiston Respublikasi Yagona interaktiv davlat xizmatlari portalida (keyingi o‘rinlarda — YIDXP) davlat xizmatidan elektron tarzda foydalanish uchun ro‘yxatdan o‘tadi.
Bino va inshoot tashqi ko‘rinishini o‘zgartirishni kelishish uchun arizalar YIDXP orqali dam olish va bayram kunlaridan qat’iy nazar, har kuni 24 soat davomida yuborilishi mumkin.
Bunda arizalar ish vaqtidan keyin yoki dam olish va bayram kunlari kelib tushganda navbatdagi ish kuni arizalarning vakolatli organga kelib tushgan va qabul qilingan kun hisoblanadi.'),
        (1166, 119, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '9. O‘zi kelib murojaat etgan taqdirda, Davlat xizmatlari markazi xodimi ariza beruvchi nomidan, YIDXP orqali murojaat etgan taqdirda esa — ariza beruvchi mustaqil ravishda ushbu Reglamentga 2-ilovada keltirilgan shaklga muvofiq davlat xizmatidan foydalanish uchun so‘rovnomani elektron tarzda to‘ldiradi.
So‘rovnomaga bino va inshoot fasadining eskiz loyihasi elektron ko‘rinishda ilova qilinadi.
Ariza beruvchilardan ushbu bandda ko‘rsatilmagan ma’lumotlarni hamda boshqa hujjatlarni talab qilish taqiqlanadi.'),
        (1167, 119, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '10. Zarur ma’lumotlar so‘rovnomaga kiritilgandan so‘ng ariza beruvchi ma’lumotlarning to‘g‘riligini tekshiradi va imzolarni identifikatsiya qilishning tegishli vositalari (daktiloskopiya, elektron imzo, Mobil-ID va boshqalar) bilan tasdiqlaydi.'),
        (1168, 119, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '11. Davlat xizmatlari ko‘rsatish uchun bazaviy hisoblash miqdorining 50 foizi miqdorida yig‘im undiriladi.
Davlat xizmati ko‘rsatilishi uchun YIDXP orqali murojaat etilganda ushbu bandning birinchi xatboshisida ko‘rsatilgan yig‘im summasining 90 foizi undiriladi.
Davlat xizmati ko‘rsatish uchun yig‘im to‘langanligini tasdiqlash elektron tarzda amalga oshiriladi.'),
        (1180, 119, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '23. Ushbu Reglament talablari buzilishida aybdor bo‘lgan shaxslar qonunchilik hujjatlarida belgilangan tartibda javobgar bo‘ladi.'),
        (1181, 120, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '1. Ushbu Reglament yuridik va jismoniy shaxslarning (keyingi o‘rinlarda — ariza beruvchilar) bino va inshootlarning ixtisoslashishini o‘zgartirish, qayta qurish, rekonstruksiya qilish hamda o‘z hududida qo‘shimcha bino va inshootlarni qurishni loyihalashtirishga ruxsatnomalar (keyingi o‘rinlarda — ruxsatnoma) berish tartibini belgilaydi.
Ruxsatnoma qurilish-montaj ishlarini boshlash uchun asos hisoblanmaydi.'),
        (1182, 120, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '2. Ushbu Reglament qoidalari O‘zbekiston Respublikasining maqsadli dasturlariga kiritilgan obyektlar, davlat sirlari bilan bog‘liq bo‘lgan obyektlar va yakka tartibdagi uy-joylar qurilish-montaj ishlariga nisbatan tatbiq etilmaydi.'),
        (1183, 120, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '3. Ushbu Reglamentda quyidagi asosiy tushunchalardan foydalaniladi:
bino — funksional maqsadiga qarab odamlar yashashi yoki bo‘lishiga va har xil turdagi ishlab chiqarish jarayonlarini bajarishga mo‘ljallangan, tayanch, to‘sma yoki har ikkala maqsadga xizmat qiluvchi konstruksiyalardan iborat qurilish tizimi;
inshoot — har xil turdagi ishlab chiqarish jarayonlarini bajarish, moddiy qimmatliklarni joylashtirish va saqlash, odamlarning vaqtincha bo‘lishi (harakatlanishi), shuningdek, uskunalar yoki kommunikatsiyalarni joylashtirish (o‘tkazish, ulash) uchun mo‘ljallangan, tayanch, to‘sma yoki har ikkala maqsadga xizmat qiluvchi konstruksiyalardan iborat hajmiy, yassi yoki chiziq tarzidagi qurilish tizimi;
kapital bino va inshoot — obyekt barpo etilishi uchun yer uchastkasiga mulk, ijara yoki doimiy foydalanish huquqi mavjud bo‘lgan, zarur ruxsat beruvchi hujjatlari olingan hamda muqim muhandislik kommunikatsiyalari o‘tkazilgan (zarur bo‘lganda), shaharsozlik normalari va qoidalariga rioya etgan holda maxsus moslashtirilgan (chuqurlashtirilgan va boshqa holatdagi) poydevorlarga o‘rnatilgan qurilish tizimi;
vaqtinchalik qurilmalar — obyektlar quriladigan, rekonstruksiya qilinadigan va ta’mirlanadigan, dala ishlari (geodeziya, qishloq ho‘jaligi, o‘rmonchilik, baliq ko‘paytirish va boshqalar) bajariladigan mavsumiy savdo, umumiy ovqatlanish, o‘qitish va boshqa jarayonlar davrida maxsus barpo etiladigan yoki moslashtiriladigan hamda ulardan foydalanish zarurati bo‘lmaganda bartaraf etish (buzish) imkoniyati mavjud bo‘lgan kapital hisoblanmagan ishlab chiqarish, ombor sifatida ishlatiladigan bino va inshootlar;
yengil turdagi qurilmalar — yengil konstruksiyalardan iborat, har tomonlama yopiq yoki bir qismi ochiq, 0,5 metrdan chuqur bo‘lmagan holatda toshdan yoki g‘ishtdan terilgan poydevorlarga ega, to‘liq yechib olish (donalab o‘pirib olish) va ko‘chirish imkoniyati mavjud obyektlar;
magistral ko‘chalar — aholi punktining ma’muriy markazini boshqa funksional zonalari bilan bog‘lovchi, shuningdek, uning hududida umumiy foydalanishdagi transport va boshqa transport vositalari hamda piyodalar serqatnov bo‘lgan asosiy ko‘chalar;
qayta qurish — obyektdagi bino va inshootlarni to‘liq yoki qisman buzib tashlab, ularning ixtisoslashishini o‘zgartirgan yoki o‘zgartirmagan holda yangilarini qurish;
ixtisoslashishini o‘zgartirish — bino va inshootlarni yoki alohida xonalarni, shu jumladan, qurilishi yakunlanmaganlarini ularning dastlabki loyiha-smeta va kadastr hujjatlarida nazarda tutilmagan maqsadli yo‘nalishining rekonstruksiya qilingan va rekonstruksiya qilinmagan o‘zgarishi;
qo‘shimcha qurilish — yuridik va jismoniy shaxslarga tegishli yer uchastkalarining oldin qurilgan bino va inshootlardan bo‘sh qismida ularga tutashgan holda va (yoki) alohida turadigan yangi bino va inshootlarning barpo etilishi;
rekonstruksiya qilish — binolardan foydalanish sharoitlarini o‘zgartirish, ularning jismoniy eskirishi tufayli yuzaga kelgan yo‘qotishlar o‘rnini to‘ldirish, binolardan foydalanishning yangi maqsadlariga erishish uchun asosiy texnik-iqtisodiy ko‘rsatkichlarni (yuklamalarni, xonalarning rejalashtirilishini, binoning qurilish hajmini va umumiy maydonini, muhandislik jihatdan jihozlanishini) o‘zgartirish bilan bog‘liq qurilish ishlari va tashkiliy-texnik tadbirlar hamda ko‘p kvartirali uydagi joyni qayta rejalashtirish majmui;
shaharsozlik hujjatlari — hududlarni va aholi punktlarini rivojlantirishni shaharsozlik jihatidan rejalashtirish to‘g‘risidagi hamda ularni qurish haqidagi tasdiqlangan hujjatlar.'),
        (1169, 119, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '12. Yig‘imlar summasi quyidagi tartibda taqsimlanadi:
a) Davlat xizmatlari markazlariga o‘zi kelib murojaat etganda:
20 foizi — Adliya vazirligining Davlat xizmatlarini rivojlantirish jamg‘armasining shaxsiy hisobvarag‘iga o‘tkaziladi;
10 foizi — Qurilish vazirligi huzuridagi Qurilish sohasida axborot-kommunikatsiya texnologiyalarini rivojlantirish markazining shaxsiy hisobvarag‘iga o‘tkaziladi;
qolgan mablag‘lar to‘liq miqdorda Qoraqalpog‘iston Respublikasi Qurilish vazirligi, viloyatlar va Toshkent shahar qurilish bosh boshqarmalarining bank hisobvarag‘iga o‘tkaziladi;
b) YIDXP orqali murojaat etilganda:
10 foizi — Adliya vazirligining Davlat xizmatlarini rivojlantirish jamg‘armasining shaxsiy hisobvarag‘iga o‘tkaziladi;
10 foizi — Qurilish vazirligi huzuridagi Qurilish sohasida axborot-kommunikatsiya texnologiyalarini rivojlantirish markazining shaxsiy hisobvarag‘iga o‘tkaziladi;
11,25 foizi — Axborot-kommunikatsiya texnologiyalarini rivojlantirish jamg‘armasining elektron hukumat va raqamli iqtisodiyotning idoralararo loyihalarini moliyalashtirish bo‘yicha maxsus hisobvarag‘iga;
qolgan mablag‘lar to‘liq miqdorda Qoraqalpog‘iston Respublikasi Qurilish vazirligi, viloyatlar va Toshkent shahar qurilish bosh boshqarmalarining bank hisobvarag‘iga o‘tkaziladi.'),
        (1170, 119, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '13. Ariza beruvchi davlat xizmatidan foydalanishdan uni ko‘rsatishning har qanday bosqichida bosh tortish huquqiga ega.
Ariza beruvchi davlat xizmatidan foydalanishdan bosh tortganda uning so‘rovnomasi ko‘rmasdan qoldiriladi. Bunda to‘langan yig‘im summasi qaytarilmaydi.'),
        (1171, 119, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '14. O‘zi kelib murojaat etganda Davlat xizmatlari markazlari so‘rovnoma to‘ldirilgan vaqtdan boshlab real vaqt rejimida uni vakolatli organga yuboradi.
YIDXP orqali murojaat qilinganda esa so‘rovnoma avtomatik tarzda vakolatli organga yuboriladi.'),
        (1292, 123, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '22. Davlat xizmatlari markazi invoys va QR-kod qo‘yilgan ko‘chirma kelib tushganidan so‘ng real vaqt rejimida ularni ariza beruvchining elektron manziliga yuboradi va bu haqida ariza beruvchini elektron tarzda xabardor qiladi.
Davlat xizmatlari markazi xodimi ariza beruvchi o‘zi kelib murojaat qilgan taqdirda, ariza beruvchining so‘roviga ko‘ra unga invoys va ko‘chirmani elektron shaklida yoki qog‘oz shaklida taqdim etishi mumkin.'),
        (1172, 119, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '15. Vakolatli organ so‘rovnoma tushgan kundan boshlab besh ish kuni mobaynida ushbu Reglamentga 3-ilovada keltirilgan shaklga muvofiq QR-kod qo‘yilgan bino va inshootning tashqi ko‘rinishini o‘zgartirish kelishilganligi haqida xulosa beradi yoki rad etishning aniq sabablarini va qonunchilik hujjatlari normalarini ko‘rsatgan holda kelishishni rad etadi.
Vakolatli organ tomonidan rad etish normativ-huquqiy hujjatning yoki shaharsozlik normalari va qoidalarining nomi, uning qismi, bandi, kichik bandi yoki xatboshisiga asoslangan bo‘lishi talab etiladi.
Bino va inshoot tashqi ko‘rinishini o‘zgartirishni kelishish bino va inshoot fasadining eskiz loyihasini vakolatli organning ERI bilan tasdiqlash orqali amalga oshiriladi.
Vakolatli organ arizani ko‘rib chiqishi davomida, zaruratga ko‘ra, ko‘chmas mulk obyektining kadastr hujjatlarini tekshirish talab etilganda Kadastr agentligining axborot tizimi bilan integratsiya qilingan Qurilish vazirligining “Shaffof qurilish” milliy axborot tizimi orqali real vaqt rejimida ko‘chmas mulk obyektiga oid zarur ma’lumotlar va kadastr hujjatlarini mustaqil ravishda (yuklab) olishi mumkin.
Vakolatli organ joyiga borib bino va inshootni o‘rganib chiqishi butun bino va inshootni yoki ularning qismlari va ularga biriktirilgan yer uchastkasini, shuningdek, ularga tutash hudud va ular yaqinidagi bino va inshootlarni vizual ko‘rikdan (zarurat bo‘lganda, maxsus uskunalar va asboblardan foydalangan holda) o‘tkazishdan iborat bo‘ladi.
O‘rganish davomida bino va inshoot o‘zboshimchalik bilan qurilganini (loyiha-smeta hujjatlarini kelishmasdan va tegishli tartibda ekspertizadan o‘tkazmasdan turib qurilganini, obyektning loyiha-smeta hujjatlaridan chetga chiqqan holda hamda shaharsozlik normalari va qoidalarini buzgan holda qurish va rekonstruksiya qilingani) aniqlash maqsadida, bino va inshootni tekshiruvdan o‘tkazish hamda ariza beruvchi so‘rovnomasida, shuningdek, bino va inshootning devorlari rejasi, kesimi (mavjud bo‘lsa), fasad va kadastr pasporti nusxasida berilgan ma’lumotlarni bino va inshootning haqiqiy holati bilan taqqoslash amalga oshiriladi.'),
        (1174, 119, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '17. Ariza beruvchi bino va inshoot tashqi ko‘rinishini o‘zgartirishni kelishish rad etilgan kundan boshlab o‘n ish kuni mobaynida davlat xizmati ko‘rsatish uchun yig‘im to‘lamasdan ko‘rsatilgan kamchilikni bartaraf etgan holda bino va inshootning tashqi ko‘rinishini o‘zgartirishni kelishish uchun takroran murojaat qilish huquqiga ega.
Bino va inshoot tashqi ko‘rinishini o‘zgartirishni kelishishni rad etish uchun asos bo‘lgan sabablar ariza beruvchi tomonidan bartaraf qilingan taqdirda, takroran kelishish so‘rovnoma takroran tushgan kundan boshlab uch ish kunidan ortiq bo‘lmagan muddatda amalga oshiriladi.
Bino va inshoot fasadining eskiz loyihasini takroran ko‘rib chiqishda vakolatli organ tomonidan avvalgi rad etish asoslarida bayon etilmagan sabablarning keltirilishiga yo‘l qo‘yilmaydi. Oldin ko‘rsatilgan sabablar bartaraf etilganligini tasdiqlovchi ma’lumotlar bilan bog‘liq rad etish sabablari keltirilishi bundan mustasno.'),
        (1175, 119, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '18. Vakolatli organ bino va inshoot tashqi ko‘rinishini o‘zgartirishni kelishgan kunning o‘zida QR-kod qo‘yilgan bino va inshootning tashqi ko‘rinishini o‘zgartirish kelishilganligi haqidagi xulosani Davlat xizmatlari markaziga (o‘zi kelib murojaat etgan taqdirda) yoki ariza beruvchiga (elektron tarzda murojaat etgan taqdirda) YIDXP orqali yuboradi.
Davlat xizmatlari markazi xulosa olinganidan so‘ng real vaqt rejimida uni ariza beruvchining elektron pochta manziliga yuboradi va bu haqida ariza beruvchini elektron tarzda xabardor qiladi.
Davlat xizmatlari markazi xodimi ariza beruvchi o‘zi kelib murojaat qilgan taqdirda, ariza beruvchining so‘roviga ko‘ra unga xulosani elektron shaklda yoki qog‘oz shaklida taqdim etadi.'),
        (1176, 119, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '19. Davlat xizmatlari markazlari va vakolatli organlar o‘z faoliyatini amalga oshirishi natijasida olingan ariza beruvchi haqidagi ma’lumotlarning oshkor etilmasligini ta’minlashi kerak.'),
        (1177, 119, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '20. Xulosa bino va inshoot fasadining eskiz loyihasi kelishilgan kunning o‘zida “Shaffof qurilish” milliy axborot tizimida avtomatik ravishda hisobga olib boriladi.'),
        (1178, 119, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '21. Adliya vazirligi, uning hududiy boshqarmalari va Davlat xizmatlari markazlari ushbu Reglament talablari ijrosi bo‘yicha davlat organlari va boshqa tashkilotlarning faoliyati ustidan, shu jumladan, YIDXP orqali, doimiy nazoratni va monitoringni amalga oshiradilar.'),
        (1185, 120, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '5. Quyidagi hollarda rekonstruksiya qilish uchun ruxsatnoma talab etilmaydi:
binolarning mustahkamligi va zilzilabardoshliligiga ta’sir qiluvchi yuk ko‘tarib turuvchi qismlarining o‘zgarishiga olib kelmaydigan qayta rejalashtirish (ko‘p kvartirali uydagi joylarni qayta rejalashtirish bundan mustasno);
poydevor maydonini o‘zgartirmasdan yengil turdagi qurilmalar va vaqtinchalik qurilmalarni qurish va rekonstruksiya qilish.
Bunda loyiha hujjatlari tuman (shahar) qurilish bo‘limlari bilan (arxitektura rejalashtirish topshirig‘ini ishlab chiqmasdan) kelishiladi. Obyektni foydalanishga qabul qilish talab etilmaydi.'),
        (1186, 120, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '6. Bino va inshootlarning ixtisoslashishini o‘zgartirish, qayta qurish, rekonstruksiya qilish hamda o‘z hududida qo‘shimcha bino va inshootlarni qurishni tegishli ruxsatnomasiz amalga oshirish taqiqlanadi.'),
        (1187, 120, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '7. Ruxsatnoma tuman (shahar) qurilish bo‘limlari (keyingi o‘rinlarda — vakolatli organ) tomonidan beriladi.
Vakolatli organ tomonidan turar joyni noturar joy toifasiga yoki noturar joyni turar joy toifasiga o‘tkazish (rekonstruksiya qilmasdan) uchun ruxsatnoma rasmiylashtirilganda ariza beruvchining so‘roviga ko‘ra kadastr pasportini rasmiylashtirish va ko‘chmas mulkka bo‘lgan huquqlarni davlat ro‘yxatidan o‘tkazish uchun ruxsatnoma Davlat kadastrlari palatasining hududiy boshqarmasiga elektron tarzda yuboriladi (obyektning muayyan qismi toifasini o‘zgartirish bundan mustasno).
Bunda ko‘chmas mulkka bo‘lgan huquq davlat ro‘yxatidan o‘tkazilganidan so‘ng avtomatik ravishda:
davlat soliq inspeksiyasiga — obyektning toifasi o‘zgarganligi haqida soliq hisobini yuritish uchun;
muhandislik-kommunikatsiya tarmoqlarini ekspluatatsiya qiluvchi tashkilotlarga — iste’molchi bilan to‘g‘ri hisob-kitob qilinishi uchun yuboriladi.
Ruxsatnoma rasmiylashtirilgandan so‘ng ariza beruvchining ixtiyoriga ko‘ra arxitektura-rejalashtirish topshirig‘ini ishlab chiqish uchun avtomatik tarzda buyurtma berilishi mumkin.
Magistral ko‘chalar, xalqaro, davlat ahamiyatiga ega bo‘lgan avtomobil yo‘llari bo‘ylab joylashgan bino va inshoot, ko‘p qavatli va yakka tartibdagi uy-joylar fasadlarini rekonstruksiya qilish va ularni qayta qurish masalalari Qoraqalpog‘iston Respublikasi Qurilish vazirligi, viloyatlar va Toshkent shahar qurilish bosh boshqarmalari huzuridagi hududiy arxitektura-shaharsozlik kengashlari tomonidan ko‘rib chiqiladi.
Ko‘p qavatli turar joyning birinchi qavatini noturar joy toifasiga yoki noturar joyni ko‘p qavatli turar joy toifasiga o‘tkazishga ruxsatnoma berishda ko‘p xonadonli uy-joy fondidan foydalanishni nazorat qilish hududiy inspeksiyalarining tegishli ijobiy xulosalari talab etiladi.'),
        (1188, 120, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '8. Davlat xizmatlari markazlari va vakolatli organlar o‘rtasidagi axborot almashuv ERIdan foydalangan holda faqat elektron tarzda amalga oshiriladi.'),
        (1189, 120, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '9. Ruxsatnoma berish ushbu Reglamentga 1-ilovada keltirilgan sxemaga muvofiq amalga oshiriladi.'),
        (1190, 120, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '10. Quyidagilar ruxsat berishga doir talab va shartlar hisoblanadi:
a) turar joyni noturar joyga o‘tkazishda:
turar joy avariya holatida emasligi;
turar joyni boshqa shaxsga o‘tkazishga taqiq yo‘qligi yoki ushbu turar joyga nisbatan xatlov mavjud emasligi;
noturar joyga o‘tkazilayotgan turar joy birinchi qavatda joylashganligi (yakka tartibdagi uy-joylar bundan mustasno);
qonunchilik hujjatlari hamda shaharsozlik normalari va qoidalari talablariga zid ravishda binolar konstruksiyasiga ta’sir ko‘rsatuvchi o‘zgartirishlar kiritmaslik;
obyektda evakuatsiya chiqishlarining soni va joylashishining o‘zgarmasligi;
turar joy aniq maqsadli kommunal uy-joy fondiga kirmasligi;
alohida kirish joyini yaratish imkoni mavjud bo‘lishi;
ko‘zlangan faoliyat turi Vazirlar Mahkamasining 2000-yil 24-yanvardagi 22-son qarori bilan tasdiqlangan Ko‘p kvartirali uylardagi yashash uchun mo‘ljallanmagan joylardan foydalanish tartibi to‘g‘risidagi nizom ilovasiga (ko‘p kvartirali uylardagi yashash uchun mo‘ljallanmagan joylarda joylashtirishga ruxsat berilgan faoliyat turlari ro‘yxati) muvofiq faoliyat turlariga zid bo‘lmasligi;
b) noturar joyni turar joyga o‘tkazishda:
bino va inshootlar aholi punktlarining (shaharlar, shaharchalar va qishloq aholi punktlarining) yerlari toifasiga mansub yerlardagi turar joy zonalarida joylashganligi;
noturar joy avariya holatida emasligi;
turar joyga o‘tkazilayotgan xona bino yerto‘lasi yoki yarim yerto‘lasida joylashmaganligi;
turar joyga o‘tkazilayotgan xona qishloq xo‘jaligiga mo‘ljallangan yer maydonlarida joylashmaganligi;
turar joyga o‘tkazilayotgan xonani to‘g‘ridan to‘g‘ri boshidan oxirigacha yoki burchagidan shamollatishni tashkil etish imkoniyatining mavjudligi, shuningdek, yondosh turar joy yoki noturar joylardan to‘liq izolatsiyalanganligi;
xonalar shiftining (maksimum, minimum) balandligi shaharsozlik normalari va qoidalari talablariga muvofiq ekanligi;
noturar joyni boshqa shaxsga o‘tkazishga taqiq yo‘qligi yoki ushbu noturar joyga nisbatan xatlov mavjud emasligi;
zarur sanitariya-gigiyenik uzellar yaratish uchun xonalarni moslashtirish imkoniyatining mavjudligi;
markazlashtirilgan kanalizatsiya mavjud bo‘lmagan hollarda xonaga biriktiriladigan yer uchastkasining maydoni turar joy xonasidan, shuningdek, biriktiriladigan yer uchastkasiga tutash bo‘lgan boshqa mulkdorlarga tegishli turar joylardan oqova suv va hojatxona uchun mo‘ljallangan o‘ralardan sanitariya normalariga muvofiq masofada joylashganligi;
joy muhandislik va transport kommunikatsiyalari, irrigatsiya inshootlari, daryo va suv havzalari, ishlab chiqarish obyektlari, qabristonlar muhofaza zonalari, qishloq xo‘jaligi ekinlariga ishlov berish uchun zaharli kimyoviy moddalarni qo‘llash zonalari hududida joylashmaganligi;
xonaning mavsumiy va vaqtincha yashash uchun mo‘ljallanmaganligi;
shaharsozlik hujjatlariga muvofiq turar joy binolarini joylashtirish uchun mo‘ljallanmagan zonalarda joylashmaganligi;
xonaning bozorlar va boshqa savdo markazlarida, mahalliy davlat hokimiyati organlari binolarida joylashmaganligi;
to‘y va shunga o‘xshash marosimlar o‘tkazish bilan bog‘liq 100 tadan ortiq o‘tirish joyi bo‘lgan umumiy ovqatlanish obyekti, kino va videofilmlar namoyish qilinadigan statsionar va vaqtincha pavilonlar, yozgi ochiq estrada, kinoteatrlar, raqs maydonchalari, 500 o‘ringacha bo‘lgan yopiq kinoteatrlar, inson hayoti uchun xavfli bo‘lgan portlovchi, yonuvchi va kimyoviy vositalar saqlash omborxonalari, angarlar, kioskalar, bostirma toifasidagi obyekt emasligi;
ijtimoiy soha obyektlarida va unga tegishli yer maydonlarida joylashmaganligi;
v) ko‘p kvartirali uylarni qayta rejalashtirish va rekonstruksiya qilishda qo‘shnilarning yoki boshqa uchinchi shaxslarning umumiy mulkdan foydalanish bilan bog‘liq huquqlari buzilmasligi hamda ularning hayoti va sog‘lig‘i uchun xavf-xatar yuzaga kelmasligi;
g) shaharsozlik normalari va qoidalariga muvofiqligi.'),
        (1191, 120, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '11. Ariza beruvchi ruxsatnoma olish uchun Davlat xizmatlari markaziga o‘zi kelib murojaat etadi yoki YIDXPda davlat xizmatidan elektron tarzda foydalanish uchun ro‘yxatdan o‘tadi.
Ruxsatnoma olish uchun arizalar YIDXP orqali dam olish va bayram kunlaridan qat’iy nazar, har kuni 24 soat davomida yuborilishi mumkin.
Bunda arizalar ish vaqtidan keyin yoki dam olish va bayram kunlari kelib tushganda navbatdagi ish kuni arizalarning vakolatli organga kelib tushgan va qabul qilingan kun hisoblanadi.'),
        (1192, 120, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '12. O‘zi kelib murojaat etgan taqdirda, Davlat xizmatlari markazi xodimi ariza beruvchi nomidan, YIDXP orqali murojaat etgan taqdirda esa — ariza beruvchi mustaqil ravishda ushbu Reglamentga 2-ilovada keltirilgan shaklga muvofiq davlat xizmatidan foydalanish uchun so‘rovnomani elektron tarzda to‘ldiradi.
Turar joyni noturar joy toifasiga o‘tkazish nazarda tutilganda so‘rovnomaga turar joyga birgalikda egalik qiluvchilarning (agar mavjud bo‘lsa) notarial tasdiqlangan yozma roziligi ilova qilinadi.
Ariza beruvchidan ushbu bandda ko‘rsatilmagan ma’lumotlar hamda boshqa hujjatlarni talab qilish taqiqlanadi.'),
        (1193, 120, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '13. Zarur ma’lumotlar so‘rovnomaga kiritilgandan so‘ng ariza beruvchi ma’lumotlarning to‘g‘riligini tekshiradi va imzolarni identifikatsiya qilishning tegishli vositalari (ERI, daktiloskopiya, elektron imzo, Mobil-ID va boshqalar) bilan tasdiqlaydi.'),
        (1194, 120, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '14. Davlat xizmati ko‘rsatilganligi uchun bazaviy hisoblash miqdorining 50 foizi miqdorida yig‘im undiriladi.
Davlat xizmati ko‘rsatilishi uchun YIDXP orqali murojaat etilganda ushbu bandning birinchi xatboshisida ko‘rsatilgan yig‘im summasining 90 foizi undiriladi.
Yig‘im to‘langanligini tasdiqlash elektron amalga oshiriladi.'),
        (1195, 120, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '15. Yig‘im summasi quyidagi tartibda taqsimlanadi:
a) Davlat xizmatlari markazlariga o‘zi kelib murojaat etganda:
20 foizi — Adliya vazirligining Davlat xizmatlarini rivojlantirish jamg‘armasining shaxsiy hisobvarag‘iga o‘tkaziladi;
10 foizi — Qurilish vazirligi huzuridagi Qurilish sohasida axborot-kommunikatsiya texnologiyalarini rivojlantirish markazining shaxsiy hisobvarag‘iga o‘tkaziladi;
qolgan mablag‘lar to‘liq miqdorda Qoraqalpog‘iston Respublikasi Qurilish vazirligi, viloyatlar va Toshkent shahar qurilish bosh boshqarmalarining hisobvarag‘iga o‘tkaziladi;
b) YIDXP orqali murojaat etganda:
10 foizi — Adliya vazirligining Davlat xizmatlarini rivojlantirish jamg‘armasining shaxsiy hisobvarag‘iga o‘tkaziladi;
10 foizi — Qurilish vazirligi huzuridagi Qurilish sohasida axborot-kommunikatsiya texnologiyalarini rivojlantirish markazining shaxsiy hisobvarag‘iga o‘tkaziladi;
11,25 foizi — Axborot-kommunikatsiya texnologiyalarini rivojlantirish jamg‘armasining elektron hukumat va raqamli iqtisodiyotning idoralararo loyihalarini moliyalashtirish bo‘yicha maxsus hisobvarag‘iga o‘tkaziladi;
qolgan mablag‘lar to‘liq miqdorda Qoraqalpog‘iston Respublikasi Qurilish vazirligi, viloyatlar va Toshkent shahar qurilish bosh boshqarmalarining hisobvarag‘iga o‘tkaziladi.'),
        (1196, 120, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '16. Ariza beruvchi davlat xizmatidan foydalanishdan uni ko‘rsatishning har qanday bosqichida bosh tortish huquqiga ega.
Ariza beruvchi davlat xizmatidan foydalanishdan bosh tortganda uning so‘rovnomasi ko‘rmasdan qoldiriladi. Bunda to‘langan yig‘im summasi qaytarilmaydi.'),
        (1197, 120, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '17. O‘zi kelib murojaat qilganda Davlat xizmatlari markazlari so‘rovnoma to‘ldirilgan vaqtdan boshlab real vaqt rejimida uni vakolatli organga yuboradi.
YIDXP orqali murojaat qilinganda so‘rovnoma vakolatli organga avtomatik tarzda yuboriladi.'),
        (1203, 120, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '23. Turar joy zonasida yakka tartibda uy-joy qurish uchun mo‘ljallangan o‘z yer uchastkasida yoki mavjud bino va inshootni buzib, uning o‘rnida ko‘p kvartirali uy qurish masalalari Qoraqalpog‘iston Respublikasi Qurilish vazirligi, viloyatlar va Toshkent shahar qurilish bosh boshqarmalari huzuridagi hududiy arxitektura-shaharsozlik kengashlari tomonidan ko‘rib chiqiladi.'),
        (1206, 120, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '26. Turar joyni noturar joy sifatida ixtisoslashtirishga ruxsatnomani rasmiylashtirishda ariza beruvchi tomonidan rekonstruksiya qilish ishlarida va muhandislik tarmoqlari yuklama hajmini o‘zgartirish talab etilganda ushbu Reglamentga 3-ilovaga muvofiq shakl bo‘yicha ruxsatnoma rasmiylashtiriladi.
Ushbu ruxsatnomada arxitektura-rejalashtirish topshirig‘i ishlab chiqilishi, loyiha-smeta hujjatlari kelishilishi, rekonstruksiyasi tugallangan bino va inshootlarni noturar joyga kadastr pasportini qayta shakllantirish, foydalanishga qabul qilish hamda unga bo‘lgan huquqni davlat ro‘yxatidan o‘tkazish haqida talablar ko‘rsatiladi.
Turar joyni noturar joyga yoki noturar joyni turar joyga ixtisoslashishini o‘zgartirishda binoni rekonstruksiya qilish nazarda tutilmasa, vakolatli organ ushbu Reglamentga 4-ilovaga muvofiq shakl bo‘yicha ruxsatnomani rasmiylashtiradi.'),
        (1207, 120, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '27. Noturar joyni turar joyga ixtisoslashishini o‘zgartirishga ruxsatnomani rasmiylashtirishda vakolatli organ tomonidan rekonstruksiya qilish zarurati mavjudligi aniqlansa, ushbu Reglamentga 5-ilovada keltirilgan shaklga muvofiq ruxsatnoma rasmiylashtiriladi.
Ushbu ruxsatnomada uy-joyni rekonstruksiya qiladigan uy-joy mulkdorlariga yakka tartibda uy-joyni (ko‘p kvartirali uylardagi kvartiralar) rekonstruksiya qilish bo‘yicha loyiha-smeta hujjatlarini kelishish va rekonstruksiyasi ishlari yakunlangan uy-joyni foydalanishga qabul qilish haqida talablar ko‘rsatiladi.'),
        (1198, 120, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '18. Vakolatli organga so‘rovnoma tushganidan so‘ng yetti ish kuni mobaynida obyektni joyiga borib bino va inshootlarning ixtisoslashishini o‘zgartirish, qayta qurish, rekonstruksiya qilish hamda o‘z hududida qo‘shimcha bino va inshootlarni qurish imkoniyatlari mavjudligini o‘rganadi va QR-kod qo‘yilgan ruxsatnomani beradi yoki rad etishning aniq sabablarini ko‘rsatgan holda ruxsatnoma berishni rad etadi.
Vakolatli organ tomonidan rad etish normativ-huquqiy hujjatning yoki shaharsozlik normalari va qoidalarining nomi, uning qismi, bandi, kichik bandi yoki xatboshisiga asoslangan bo‘lishi talab etiladi.
Ruxsatnoma elektron ko‘rinishda rasmiylashtiriladi va vakolatli organning ERIsi bilan tasdiqlanadi.
Vakolatli organ arizani ko‘rib chiqish muddati davomida zaruratga ko‘ra ko‘chmas mulk obyektining kadastr hujjatlarini tekshirishi talab etilganda Kadastr agentligining axborot tizimi bilan integratsiya qilingan Qurilish vazirligining “Shaffof qurilish” milliy axborot tizimi orqali real vaqt rejimida ko‘chmas mulk obyektiga oid zarur ma’lumotlar va kadastr hujjatlarini mustaqil ravishda (yuklab) olishi mumkin.
Agarda, Kadastr agentligining axborot tizimi bilan integratsiya qilingan Qurilish vazirligining “Shaffof qurilish” milliy axborot tizimi orqali obyektning kadastr hujjatlari to‘g‘risidagi zaruriy ma’lumotlarni olishni imkoni bo‘lmagan (ma’lumotlar bazasida mavjud bo‘lmaganda) taqdirda, ariza vakolatli organ tomonidan hududiy kadastrlar palatasining obyekt to‘g‘risida taqdim etadigan yozma ma’lumotnomasi asosida ko‘rib chiqiladi.
Ariza beruvchi vakolatli organga obyektni o‘rganish uchun imkoniyat yaratib berishi talab etiladi.
Vakolatli organning obyektni joyiga borib o‘rganib chiqishi butun bino va inshootni yoki ularning qismlarini va ularga biriktirilgan yer uchastkasini, shuningdek, ularga tutash hududni hamda bino va inshootlarni vizual ko‘rikdan (zarurat bo‘lganda, maxsus uskunalar va asboblardan foydalangan holda) o‘tkazishdan iborat bo‘ladi.
Vakolatli organ o‘rganish davomida bino va inshoot o‘zboshimchalik bilan qurilmaligini aniqlash uchun bino va inshootni tekshiruvdan o‘tkazadi, ariza beruvchining so‘rovnomasidagi va kadastr hujjatlaridagi ma’lumotlarni bino va inshootning haqiqiy holati bilan taqqoslaydi.
Shuningdek, obyektda quyidagilar o‘rganiladi:
qurilishi rejalashtirilayotgan obyekt bosh rejaga muvofiqligi;
shaharsozlik hujjatlarisiz amalga oshirilgan qurilish-montaj ishlari mavjudligi;
muhandislik tarmoqlari yoki muhofaza zonalarida obyektni joylashtirish rejalashtirilganligi;
qurilayotgan obyekt uchun shaharsozlik talablari bo‘yicha yer maydoni yetarli ekanligi;
bino va inshootlarning ixtisoslashishini o‘zgartirish, qayta qurish, rekonstruksiya qilish hamda o‘z hududida qo‘shimcha bino va inshootlarni qurish rejalashtirilayotgan obyekt hududning shaharsozlik hujjatlarida belgilangan zonalashtirishga muvofiq joylashganligi;
qurilishi rejalashtirilayotgan obyekt uchun ajratilgan yer maydoni nizoli bo‘lgan taqdirda, mazkur nizo mazmunan hal etilganligi to‘g‘risidagi qonuniy kuchga kirgan sud organining qarori yoxud nizolashuvchi taraflarning yozma ravishda rasmiylashtirilib tasdiqlangan kelishuv bitimi mavjudligi;
sanitariya qoidalari va normalariga mos ekanligi;
qo‘shimcha bino va inshootlarni qurish rejalashtirilayotgan o‘z hududida ko‘p yillik daraxtlar mavjud bo‘lgan taqdirda qurish uchun ekologiya organlarining tegishli ijobiy xulosasi mavjudligi.'),
        (1200, 120, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '20. Ariza beruvchi ruxsatnoma berish rad etilgan kundan boshlab o‘n ish kuni mobaynida davlat xizmati ko‘rsatish uchun yig‘imni to‘lamasdan ko‘rsatilgan kamchilikni bartaraf etib ruxsatnoma olish uchun qayta murojaat qilish huquqiga ega.
Ruxsatnoma berishni rad etish uchun asos bo‘lgan sabablar ariza beruvchi tomonidan bartaraf etilgan taqdirda, ruxsatnoma so‘rovnoma takroran berilgandan so‘ng vakolatli organ tomonidan besh ish kunidan oshmagan muddatda beriladi.
Ruxsatnomani berish masalasini takroran ko‘rib chiqishda vakolatli organ tomonidan avvalgi rad etish asoslarida bayon etilmagan sabablarning keltirilishiga yo‘l qo‘yilmaydi, avval ko‘rsatilgan sabablar bartaraf etilganligini tasdiqlovchi ma’lumotlar bilan bog‘liq rad etish sabablari keltirilishi bundan mustasno.
Ruxsatnomani olish uchun qayta murojaat rad etilganda keyingi murojaatlar ushbu Reglamentga asosan umumiy tartibda ko‘rib chiqiladi.'),
        (1201, 120, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '21. Turar joy zonalarida joylashgan obyektlardagi bino va inshootlarni yoki ulardagi alohida xonalarni quyidagi faoliyat turlari uchun ixtisoslashtirishga ruxsatnoma berish taqiqlanadi:
Vazirlar Mahkamasining 2020-yil 20-avgustdagi 496-son qarori bilan Obyektlar xavf-xatarlar toifalarining tasnifiga muvofiq keltirilgan II toifaning 19.1, 22.2, 44.2, 44.3, 45.4 va 45.5-bandlarida qayd etilgan hamda III-IV toifalarga mansub obyekt turlariga;
ommaviy tadbirlar o‘tkazishga mo‘ljallangan hamda 100 tadan ortiq o‘tirish joyi bo‘lgan ochiq turdagi umumiy ovqatlanish joylarini tashkil etish, kino va videofilmlar namoyish qilish, konsert hamda ko‘ngilochar attraksionlarni tashkil etishga mo‘ljallangan faoliyat turlari.
Quyidagi faoliyat turlariga mansub obyektlar bundan mustasno (ushbu bandning uchinchi xatboshisida ko‘rsatilgan obyektlar tarkibida bo‘lmaslik sharti bilan):
sog‘liqni saqlash muassasalari obyektlari va dorixonalar;
dam olish va turizm muassasalari obyektlari;
yulduzlik darajasidan (toifasidan) qat’i nazar, mehmonxona xo‘jaligi obyektlari;
madaniy-ma’rifiy muassasalar obyektlari;
jamoat binolari va ma’muriy binolar;
ta’lim muassasalari obyektlari;
ilmiy-tadqiqot muassasalari obyektlari;
maishiy xizmat ko‘rsatish korxonalarining yil bo‘yi faoliyat yuritadigan obyektlari, kir yuvish korxonalari, kiyim tikish atelyelari, sartaroshxonalar, hammomlar va shu kabilar;
suv xo‘jaligi qurilishi, melioratsiya va irrigatsiya obyektlari;
pochtamptlar, telegraflar, saralash uzellari;
aloqa bo‘limi mavjud bo‘lgan avtomatlashtirilgan telefon stansiyalari;
qavatlar sonidan qat’i nazar, ko‘p kvartirali uylar va yotoqxonalar (yakka tartibdagi uy-joylardan iborat mahallalar hududida faqat aholi punktlarining bosh rejalari va ularning qismlarini batafsil rejalashtirish loyihalarida nazarda tutilgan holatlarda joylashtiriladi);
ushbu bandning uchinchi xatboshisida qayd etilganlardan tashqari umumiy ovqatlanish shoxobchalari, shu jumladan, restoranlar;
savdo shoxobchalari, shu jumladan, supermarket, yirik ko‘p tarmoqli va ixtisoslashtirilgan do‘konlar, savdo uylari;
yopiq suv havzalari va suv havzali jismoniy-sog‘lomlashtirish majmualari;
issiqlik ta’minoti uchun avtonom qozonxonalar;
ko‘p qavatli yer osti va yer usti avtoturargohlar.'),
        (1202, 120, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '22. Turar joy zonalarida joylashgan bino va inshootni ixtisoslashishini o‘zgartirmasdan bino va inshootni rekonstruksiya qilish, qayta qurish va o‘z hududida qo‘shimcha bino va inshootlar qurishga ruxsatnoma berishga yo‘l qo‘yiladi.'),
        (1239, 121, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '25. ART rasmiylashtirilgan kunning o‘zida “Shaffof qurilish” milliy axborot tizimida avtomatik ravishda hisobga olib boriladi.'),
        (1204, 120, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '24. Noturar joy xonalarini (yoki ularning bir qismini) ko‘p kvartirali turar joy yoki yotoqxona sifatida ishlatiladigan turar joyga vakolatli organning noturar joy xonalarini (yoki ularning bir qismini) turar joy toifasiga o‘zgartirish uchun beradigan ruxsatnomasida yondosh binolardagi yong‘inga qarshi va sanitariya masofalari, kirish yo‘llari, piyodalar uchun yo‘laklar, avtomobillar to‘xtab turish maydonchalari, sport va bolalar maydonchalari, axlat yig‘gichlar va yuvilgan kiyimlarni quritish maydonchalari hisobga olingan holda ko‘p kvartirali turar joylar yoki yotoqxonaga xizmat ko‘rsatish uchun mo‘ljallangan yer uchastkasining maydoni ko‘rsatiladi.'),
        (1205, 120, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '25. Vakolatli organ ruxsatnomani u rasmiylashtirilgan kunda Davlat xizmatlari markaziga yoki ariza beruvchiga YIDXP orqali elektron shaklda yuboradi.'),
        (1224, 121, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '10. Zarur ma’lumotlar so‘rovnomaga kiritilgandan so‘ng ariza beruvchi ma’lumotlarning to‘g‘riligini tekshiradi va imzolarni identifikatsiya qilishning tegishli vositalari (ERI, daktiloskopiya, elektron imzo, Mobil-ID va boshqalar) bilan tasdiqlaydi.'),
        (1208, 120, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '28. Kapital bino va inshootlarning, shuningdek, alohida xonalarning ixtisoslashishini o‘zgartirishga o‘ziga tegishli yer uchastkasida qo‘shimcha kapital bino va inshootlarni qurishga, mavjud bino va inshootlarni rekonstruksiya qilishga hamda ularni to‘liq yoki qisman buzgan holda o‘rnida yangi bino va inshootni qurish uchun ruxsatnomani rasmiylashtirish ushbu Reglamentga 6-ilovaga muvofiq shakl bo‘yicha amalga oshiriladi.
Agar ruxsatnomada obyektni rekonstruksiya qilmasdan faqat ixtisoslashishini o‘zgartirish ko‘rsatilgan bo‘lsa, bu kadastr organlarining hududiy filiallari tomonidan belgilangan tartibda kadastr pasportini qayta shakllantirishga asos bo‘ladi hamda ushbu Reglamentga 4-ilovaga muvofiq shakl bo‘yicha ruxsatnoma rasmiylashtiriladi.'),
        (1209, 120, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '29. Davlat xizmatlari markazi ruxsatnoma kelib tushgandan so‘ng real vaqt rejimida uni ariza beruvchining elektron manziliga yuboradi va (yoki) ariza beruvchini bu haqida elektron tarzda xabardor qiladi.
Davlat xizmatlari markazi xodimi ariza beruvchi o‘zi kelib murojaat qilgan taqdirda, ariza beruvchining so‘roviga ko‘ra unga ruxsatnomani elektron shaklda yoki qog‘oz shaklda taqdim etishi mumkin.'),
        (1210, 120, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '30. Davlat xizmatlari markazlari va vakolatli organlar o‘z faoliyatini amalga oshirishi natijasida olingan ariza beruvchi haqidagi ma’lumotlarning oshkor etilmasligini ta’minlashi kerak.'),
        (1211, 120, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '31. Ruxsatnoma rasmiylashtirilgan kunning o‘zida “Shaffof qurilish” milliy axborot tizimida avtomatik ravishda hisobga olib boriladi.'),
        (1212, 120, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '32. Adliya vazirligi, uning hududiy boshqarmalari va Davlat xizmatlari markazlari ushbu Reglament talablari ijrosi bo‘yicha davlat organlari va boshqa tashkilotlarning faoliyati ustidan, shu jumladan, YIDXP orqali, doimiy nazoratni va monitoringni amalga oshiradi.'),
        (1213, 120, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '33. Ariza beruvchi davlat organlari va boshqa tashkilotlar xodimlarining harakatlari (harakatsizligi) yuzasidan qonunchilik hujjatlarida belgilangan tartibda shikoyat qilishi mumkin.'),
        (1214, 120, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '34. Ushbu Reglament talablari buzilishida aybdor bo‘lgan shaxslar qonunchilik hujjatlarida belgilangan tartibda javobgar bo‘ladi.'),
        (1215, 121, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '1. Ushbu Reglament yuridik va jismoniy shaxslar yoki ularning buyurtmasiga (shartnomaga asosan) muvofiq loyiha tashkilotlari (keyingi o‘rinlarda — ariza beruvchilar) tomonidan berilgan so‘rovnomaga asosan shaharsozlik faoliyati obyektini qurish yoki rekonstruksiya qilishni loyihalashtirish uchun arxitektura-rejalashtirish topshirig‘ini (keyingi o‘rinlarda — ART) ishlab chiqish va taqdim etish tartibini belgilaydi.'),
        (1216, 121, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '2. Ushbu Reglament shaharsozlik hujjatlari hamda shaharsozlik normalari va qoidalarida belgilangan talablarga asosan barcha turdagi bino va inshootlar, shuningdek, muhandislik-kommunikatsiya tarmoqlari, yo‘l va yo‘l infratuzilmasi obyektlarini qurish va rekonstruksiya qilish hamda obodonlashtirish ishlarini bajarish uchun ARTni ishlab chiqish va taqdim etishga nisbatan tatbiq etiladi.
O‘zbekiston Respublikasining davlat sirlari bilan bog‘liq bo‘lgan obyektlar uchun ARTni ishlab chiqish uchun bevosita to‘g‘ridan to‘g‘ri Qoraqalpog‘iston Respublikasi Qurilish vazirligi, viloyatlar va Toshkent shahar qurilish bosh boshqarmalariga (keyingi o‘rinlarda — vakolatli organ) murojaat qilinadi.
Yakka tartibdagi uy-joyni qurish va loyiha hujjatini kelishishda ARTni ishlab chiqish va taqdim etish talab etilmaydi (ikki qavatdan yuqori, balandligi yer yuzasidan 12 metrdan va (yoki) umumiy maydoni 500 kvadrat metrdan ortiq yakka tartibdagi uy-joylar bundan mustasno).'),
        (1217, 121, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '3. ART loyihaoldi hujjat bo‘lib, yer uchastkasi yoki bino va inshootga mulk huquqini tasdiqlamaydi hamda qurilish-montaj ishlarini boshlash uchun asos hisoblanmaydi.
ART tasdiqlangan shaharsozlik hujjatlaridan, davlat hokimiyati organlarining tegishli qarorlaridan, shu jumladan, tarixiy va madaniy obidalarni, atrof-muhitni muhofaza qilish talab va shartlarini hamda obyektni aniq yer uchastkasida joylashtirishda uchinchi shaxslarning qonuniy huquqlari va manfaatlarini himoya qilishdan kelib chiquvchi o‘ziga xos shaharsozlik va arxitekturaviy shartlar hamda obyektni loyihalashtirish va qurishning alohida sharoitlari majmuasidan iborat hujjat hisoblanib, uning asosida obyekt qurilishi (rekonstruksiyasi) uchun loyiha hujjatlari ishlab chiqiladi.
ART bino yoki inshootning tashqi ko‘rinishiga, qurilishning taxminiy maydonlariga, avtoturargohlar, hududga kirish va chiqish yo‘llariga, kichik me’moriy shakllarning tuzilishi, yer uchastkasining ko‘kalamzorlashtirilishi va obodonlashtirilishiga bo‘lgan umumiy talablarni hamda muhandislik-kommunikatsiya trassalariga va muhandislik tarmoqlari o‘tkazilishiga qo‘yiladigan asosiy talablarni belgilaydi.'),
        (1218, 121, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '4. ART ishlab chiqilmasdan loyiha hujjatlarini kelishish taqiqlanadi.'),
        (1219, 121, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '5. Vakolatli organ ARTni ishlab chiqadi va taqdim etadi.'),
        (1220, 121, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '6. Davlat xizmatlari markazlari va vakolatli organlar o‘rtasida axborot almashish ERIdan foydalangan holda faqat elektron tarzda amalga oshiriladi.'),
        (1221, 121, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '7. Arxitektura-rejalashtirish topshirig‘ini ishlab chiqish va taqdim etish ushbu Reglamentga 1-ilovada keltirilgan sxemaga muvofiq amalga oshiriladi.'),
        (1222, 121, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '8. Ariza beruvchi ARTni olish uchun Davlat xizmatlari markaziga kelib murojaat etadi yoki YIDXPda davlat xizmatidan elektron shaklda foydalanish uchun ro‘yxatdan o‘tadi.
ARTni uchun arizalar YIDXP orqali dam olish va bayram kunlaridan qat’iy nazar, har kuni 24 soat davomida yuborilishi mumkin.
Bunda arizalar ish vaqtidan keyin yoki dam olish va bayram kunlari kelib tushganda navbatdagi ish kuni arizalar vakolatli organga kelib tushgan va qabul qilingan kun hisoblanadi.'),
        (1240, 121, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '26. ARTni ishlab chiqishdan tushgan mablag‘lar to‘liq miqdorda vakolatli organning bank hisobvarag‘iga o‘tkaziladi.'),
        (1223, 121, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '9. O‘zi kelib murojaat etgan taqdirda, Davlat xizmatlari markazi xodimi ariza beruvchi nomidan, YIDXP orqali murojaat etgan taqdirda esa ariza beruvchi mustaqil ravishda ushbu Reglamentga 2-ilovada keltirilgan shakl bo‘yicha davlat xizmatidan foydalanish uchun so‘rovnomani elektron shaklda to‘ldiradi.
So‘rovnomaga quyida sanab o‘tilgan hujjatlardan biri ilova qilinadi:
tegishli ravishda tuman (shahar) qurilish bo‘limining bino va inshootni rekonstruksiya qilish hamda o‘z hududida qo‘shimcha bino va inshootni qurishga ruxsatnomasi;
“E-auksion” yagona elektron savdo maydonchasida o‘tkazilgan elektron auksion natijalari haqidagi bayonnoma;
yirik investitsiya loyihasini amalga oshirish uchun Vazirlar Mahkamasining qarori.
Obyektda tashqi muhandislik-kommunikatsiya tarmoqlariga (ehtiyojga ko‘ra elektr, gaz, suv ta’minoti va oqova suv tarmoqlari) ulanish nuqtalari va ularga obyektdan ulanish trassalari o‘tkazilishi zarurati mavjud bo‘lsa, texnik shartlar so‘rovnomaga ilova qilinadi.
Ariza beruvchilardan ushbu bandda ko‘rsatilmagan ma’lumotlarni, shuningdek, boshqa hujjatlarni taqdim etishni talab qilish taqiqlanadi.'),
        (1293, 123, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '23. Davlat xizmatlari markazlari va vakolatli organlar o‘z faoliyatini amalga oshirishi natijasida olingan ariza beruvchi haqidagi ma’lumotlarning oshkor etilmasligini ta’minlashi kerak.'),
        (1226, 121, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '12. Yig‘imlar summasi quyidagi tartibda taqsimlanadi:
a) Davlat xizmatlari markazlariga o‘zi kelib murojaat etganda:
20 foizi — Adliya vazirligining Davlat xizmatlarini rivojlantirish jamg‘armasining shaxsiy hisobvarag‘iga o‘tkaziladi;
10 foizi — Qurilish vazirligi huzuridagi Qurilish sohasida axborot-kommunikatsiya texnologiyalarini rivojlantirish markazining shaxsiy hisobvarag‘iga o‘tkaziladi;
qolgan mablag‘lar to‘liq miqdorda vakolatli organning bank hisobvarag‘iga o‘tkaziladi;
b) YIDXP orqali murojaat etilganda:
10 foizi — Adliya vazirligining Davlat xizmatlarini rivojlantirish jamg‘armasining shaxsiy hisobvarag‘iga o‘tkaziladi;
11,25 foizi — Axborot-kommunikatsiya texnologiyalarini rivojlantirish jamg‘armasining elektron hukumat va raqamli iqtisodiyotning idoralararo loyihalarini moliyalashtirish bo‘yicha maxsus hisobvarag‘iga o‘tkaziladi;
10 foizi — Qurilish vazirligi huzuridagi Qurilish sohasida axborot-kommunikatsiya texnologiyalarini rivojlantirish markazining shaxsiy hisobvarag‘iga o‘tkaziladi;
qolgan mablag‘lar to‘liq miqdorda vakolatli organning bank hisobvarag‘iga o‘tkaziladi.
Ariza beruvchi ART berish rad etilgan kundan boshlab o‘n ish kuni mobaynida ARTni olish uchun yig‘imni to‘lamasdan qayta murojaat qilish huquqiga ega.'),
        (1227, 121, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '13. Davlat xizmati ko‘rsatishni rad etish uchun quyidagilar asos hisoblanadi:
so‘rovnomada noto‘g‘ri ma’lumotlar ko‘rsatilishi;
davlat xizmatlari ko‘rsatish uchun yig‘im va insvoysda ko‘rsatilgan summani o‘z vaqtida to‘lanmasligi.'),
        (1228, 121, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '14. Ariza beruvchi davlat xizmatidan foydalanishdan uni ko‘rsatishning har qanday bosqichida bosh tortish huquqiga ega.
Ariza beruvchi davlat xizmatidan foydalanishdan bosh tortganda uning so‘rovnomasi ko‘rmasdan qoldiriladi. Bunda to‘langan yig‘im summasi qaytarilmaydi.'),
        (1229, 121, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '15. O‘zi kelib murojaat qilganda Davlat xizmatlari markazlari so‘rovnoma to‘ldirilgan vaqtdan boshlab real vaqt rejimida uni vakolatli organga yuboradi.'),
        (1230, 121, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '16. Vakolatli organ so‘rovnoma tushganidan so‘ng uch ish kuni mobaynida so‘rovnomada qayd etilgan ma’lumotlarning haqqoniyligini tekshiradi va Davlat xizmatlari markaziga yoki ariza beruvchiga YIDXP orqali elektron shaklda vakolatli organ tomonidan tasdiqlangan ARTni ishlab chiqishning qiymati ko‘rsatilgan hisob-fakturani (keyingi o‘rinlarda — invoys) yuboradi yoki rad etishning aniq sabablarini va qonunchilik hujjatlari normalarini ko‘rsatgan holda uni yuborishni rad etadi.'),
        (1231, 121, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '17. Vakolatli organ zaruratga ko‘ra, obyektni joyiga borgan holda, yangi qurilish uchun ajratilgan yer uchastkasini va qo‘shimcha qurilish amalga oshirilayotgan yer uchastkasini, shuningdek, rekonstruksiya qilinishi zarur bo‘lgan obyektni o‘rganib chiqadi.
Obyektni o‘rganib chiqish obyekt yoki uning qismlari va unga biriktirilgan yer uchastkasini, shuningdek, unga tutash hududlar hamda bino va inshootlarni vizual ko‘rikdan (zarurat bo‘lganda, maxsus uskunalar va asboblardan foydalangan holda) o‘tkazishdan iborat bo‘ladi.'),
        (1232, 121, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '18. Davlat xizmatlari markazi invoys tushganidan boshlab real vaqt rejimida bu haqida ariza beruvchini elektron xabardor qiladi.
Ariza beruvchi invoys kelib tushgandan so‘ng bir oy mobaynida invoysda ko‘rsatilgan summani to‘laydi.
Invoysda ko‘rsatilgan summa to‘langanligi elektron tarzda tasdiqlanadi.'),
        (1233, 121, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '19. Vakolatli organ arizani ko‘rib chiqishi davomida zaruratga ko‘ra, ko‘chmas mulk obyektining kadastr hujjatlarini tekshirish talab etilganda Kadastr agentligining axborot tizimi bilan integratsiya qilingan Qurilish vazirligining “Shaffof qurilish” milliy axborot tizimi orqali real vaqt rejimida ko‘chmas mulk obyektiga oid zarur ma’lumotlar va kadastr hujjatlarini mustaqil ravishda (yuklab) olishi mumkin.'),
        (1234, 121, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '20. ARTni ishlab chiqish qiymati va muddatlari ushbu Reglamentga 3-ilovaga muvofiq belgilanadi.
ARTni ishlab chiqish muddati ARTni ishlab chiqish to‘lovi kelib tushgandan so‘ng hisoblanadi.'),
        (1236, 121, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '22. Invoysda ko‘rsatilgan to‘lov belgilangan muddatda amalga oshirilgandan so‘ng vakolatli organ ushbu Reglamentga 4-ilovada keltirilgan shaklga muvofiq ART ishlab chiqadi.
Bunda so‘rovnomaga texnik shartlar ilova qilingan taqdirda, yer uchastkasi (obyekt)ning amaldagi joylashuvini hisobga oluvchi muhandislik-kommunikatsiya tarmog‘iga ulanishning texnik shartlarida bayon etilgan ma’lumotlarni taqqoslagan holda obyekt tekshiruvdan o‘tkazilib, muhandislik-kommunikatsiya tarmoqlarining ulanish nuqtalarigacha o‘tkaziladigan trassalari belgilanadi.
QR-kod qo‘yilgan ART elektron shaklda rasmiylashtiriladi va vakolatli organning ERIsi bilan tasdiqlanadi.'),
        (1237, 121, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '23. ART rasmiylashtirilgan kuni vakolatli organ Davlat xizmatlari markaziga yoki YIDXP orqali ariza beruvchiga ARTni elektron shaklda yuboradi.
Davlat xizmatlari markazi ART tushgandan so‘ng real vaqt rejimida uni ariza beruvchining elektron manziliga yuboradi yoki ariza beruvchini bu haqida elektron tarzda xabardor qiladi.
Davlat xizmatlari markazi xodimi ariza beruvchi o‘zi kelib murojaat qilgan taqdirda, ariza beruvchining so‘roviga ko‘ra unga ARTni elektron shaklda yoki qog‘oz shaklda taqdim etadi.'),
        (1238, 121, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '24. Davlat xizmatlari markazlari va vakolatli organlar o‘z faoliyatini amalga oshirishi natijasida olingan ariza beruvchi haqidagi ma’lumotlarning oshkor etilmasligini ta’minlashi kerak.'),
        (1248, 122, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '5. Ikki qavatdan yuqori bo‘lmagan (tsokolni hisobga olmagan holda), balandligi yer yuzasidan 12 metrdan va (yoki) umumiy maydoni 500 kvadrat metrdan ortiq bo‘lmagan yakka tartibdagi uy-joylar va 300 metr kubdan ortiq bo‘lmagan noturar bino va inshootlarni qurish hamda rekonstruksiya qilish loyiha-smeta hujjatlarini ekspertizadan o‘tkazish va qurilish sohasida hududiy nazorat inspeksiyalaridan ro‘yxatdan o‘tkazish talab etilmaydi.
Mazkur holatlarda tuman (shahar) qurilish bo‘limlari tomonidan kelishilgan loyiha-smeta hujjatlari qurilish va rekonstruksiya qilish ishlarini boshlash uchun asos hisoblanadi.
Loyiha-smeta hujjatlarini kelishmasdan bino-inshootlarni qurish va rekonstruksiya qilish taqiqlanadi.
Uy-joylar, yordamchi-xo‘jalik qurilmalari va hovlidagi inshootlarning qurilishi shaharsozlik normalari va qoidalariga rioya etgan holda amalga oshiriladi.
Yakka tartibdagi uy-joylarni qurish uchun mo‘ljallangan yer uchastkasida bir oila a’zolari uchun mazkur uy-joyning hovlisiga mustaqil chiqishga ega ikki va undan ortiq yakka tartibdagi turar joy binosi qurishga ruxsat beriladi.'),
        (1249, 122, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '6. Yakka tartibdagi uy-joylarni qurish loyiha-smeta hujjatlari quyidagilardan iborat bo‘ladi:
er usti (uy-joy qurilishi uchun yangi ajratilgan yer uchastkalari uchun — yer osti muhandislik kommunikatsiyalari aks ettirilgan) yer uchastkasi tarhi;
yakka tartibdagi uy-joyning poydevorlari, yerto‘lalari, qavatlari, ularning qirqimlari va binoning old tomoni tarhlari;
moliyaviy smeta hisob-kitobi (kredit olishga ehtiyoj bo‘lgan taqdirda).
Bino va inshootlarning loyiha-smeta hujjatlarining arxitekturaga oid qismida bino va inshootning joylashishi aks ettirilgan loyiha rejasi, rangli fasadlari, qavatlar rejalari, qirqimlari, qurilish obyektining bosh rejasi bo‘ladi.'),
        (1250, 122, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '7. Loyiha-smeta hujjatlari:
Qoraqalpog‘iston Respublikasi Qurilish vazirligi, viloyatlar va Toshkent shahar qurilish bosh boshqarmalari huzuridagi hududiy arxitektura-shaharsozlik kengashlari (keyingi o‘rinlarda — Kengashlar) tomonidan — tarixiy zonalarda, madaniy yodgorliklarni qo‘riqlash zonalarida, Toshkent, Nukus shaharlari va viloyat markazlarida quriladigan obyektlarning hamda davlat obyektlari bo‘yicha besh ish kuni mobaynida;
Qoraqalpog‘iston Respublikasi Qurilish vazirligi, viloyatlar va Toshkent shahar qurilish bosh boshqarmalari (keyingi o‘rinlarda — vakolatli organ) tomonidan — ikki qavatdan yuqori (tsokolni hisobga olmagan holda), balandligi yer yuzasidan 12 metrdan va (yoki) umumiy maydoni 500 kvadrat metrdan ortiq bo‘lgan yakka tartibdagi uy-joylar va 300 metr kubdan ortiq bo‘lgan noturar bino va inshootlar bo‘yicha uch ish kuni mobaynida;
tuman (shahar) qurilish bo‘limlari tomonidan — ikki qavatdan yuqori bo‘lmagan (tsokolni hisobga olmagan holda), balandligi yer yuzasidan 12 metrdan va (yoki) umumiy maydoni 500 kvadrat metrdan ortiq bo‘lmagan yakka tartibdagi uy-joylar va 300 metr kubdan ortiq bo‘lmagan noturar bino va inshootlar bo‘yicha uch ish kuni mobaynida ko‘rib chiqiladi va kelishiladi.'),
        (1251, 122, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '8. Ikki qavatdan yuqori bo‘lgan (tsokolni hisobga olmagan holda), balandligi yer yuzasidan 12 metrdan va (yoki) umumiy maydoni 500 kvadrat metrdan ortiq bo‘lgan yakka tartibdagi uy-joylar va 300 metr kubdan ortiq bo‘lgan noturar bino va inshootlarni qurish hamda rekonstruksiya qilish obyektining loyiha hujjatlari majburiy ekspertizadan o‘tkaziladi hamda obyekt Qurilish vazirligi huzuridagi Qurilish sohasida nazorat inspeksiyasining hududiy inspeksiyalarida ro‘yxatdan o‘tkaziladi.'),
        (1252, 122, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '9. Davlat xizmatlari markazlari, Kengashlar, vakolatli organlar, tuman (shahar) qurilish bo‘limlari hamda sanitariya-epidemiologik xizmatning tuman (shahar) bo‘limlari va tuman (shahar) favqulodda vaziyatlar bo‘limlari (keyingi o‘rinlarda — xulosa beruvchilar) o‘rtasida axborot almashish ERIdan foydalangan holda faqat elektron amalga oshiriladi.'),
        (1253, 122, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '10. Loyiha-smeta hujjatlarini kelishish ushbu Reglamentga 1-ilovada keltirilgan sxemaga muvofiq amalga oshiriladi.'),
        (1254, 122, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '11. Ariza beruvchilar loyiha-smeta hujjatlarini kelishish uchun Davlat xizmatlari markazlariga o‘zi kelib murojaat etadi yoki O‘zbekiston Respublikasi Yagona interaktiv davlat xizmatlari portalida (keyingi o‘rinlarda — YIDXP) davlat xizmatidan elektron shaklda foydalanish uchun ro‘yxatdan o‘tadi.
Loyiha-smeta hujjatlarini kelishish uchun arizalar YIDXP orqali dam olish va bayram kunlaridan qat’iy nazar, har kuni 24 soat davomida yuborilishi mumkin.
Bunda arizalar ish vaqtidan keyin yoki dam olish va bayram kunlari tushganda navbatdagi ish kuni arizalarning vakolatli organga tushgan va qabul qilingan kun hisoblanadi.'),
        (1255, 122, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '12. O‘zi kelib murojaat etgan taqdirda, Davlat xizmatlari markazi xodimi ariza beruvchi nomidan, YIDXP orqali murojaat etgan taqdirda esa — ariza beruvchi mustaqil ravishda ushbu Reglamentga 2-ilovada keltirilgan shakl bo‘yicha davlat xizmatidan foydalanish uchun so‘rovnomani elektron shaklda to‘ldiradi.
So‘rovnomaga loyiha-smeta hujjatlarining elektron ko‘rinishdagi eskiz loyihasi PDF yoki JPEG formatda ilova qilinadi.
Ariza beruvchilardan ushbu bandda ko‘rsatilmagan ma’lumotlarni hamda boshqa hujjatlarni talab qilish taqiqlanadi.'),
        (1256, 122, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '13. Zarur ma’lumotlar so‘rovnomaga kiritilgandan so‘ng ariza beruvchi ma’lumotlarning to‘g‘riligini tekshiradi va imzolarni identifikatsiya qilishning tegishli vositalari (ERI, daktiloskopiya, elektron imzo, Mobil-ID va boshqalar) bilan tasdiqlaydi.'),
        (1257, 122, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '14. Davlat xizmatlari ko‘rsatish uchun bazaviy hisoblash miqdorining 50 foizi miqdorida yig‘im undiriladi.
Davlat xizmati ko‘rsatilishi uchun YIDXP orqali murojaat etilganda ushbu bandning birinchi xatboshisida ko‘rsatilgan yig‘im summasining 90 foizi undiriladi.
Davlat xizmati ko‘rsatish uchun yig‘im to‘langanligini tasdiqlash elektron tarzda amalga oshiriladi'),
        (1258, 122, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '15. Yig‘imlar summasi quyidagi tartibda taqsimlanadi:
a) Davlat xizmatlari markazlariga o‘zi kelib murojaat etganda:
20 foizi — Adliya vazirligining Davlat xizmatlarini rivojlantirish jamg‘armasining shaxsiy hisobvarag‘iga o‘tkaziladi;
10 foizi — Qurilish vazirligi huzuridagi Qurilish sohasida axborot-kommunikatsiya texnologiyalarini rivojlantirish markazining shaxsiy hisobvarag‘iga o‘tkaziladi;
qolgan mablag‘lar to‘liq miqdorda vakolatli organning bank hisobvarag‘iga o‘tkaziladi;
b) YIDXP orqali murojaat etilganda:
10 foizi — Adliya vazirligining Davlat xizmatlarini rivojlantirish jamg‘armasining shaxsiy hisobvarag‘iga o‘tkaziladi;
11,25 foizi — Axborot-kommunikatsiya texnologiyalarini rivojlantirish jamg‘armasining elektron hukumat va raqamli iqtisodiyotning idoralararo loyihalarini moliyalashtirish bo‘yicha maxsus hisobvarag‘iga;
10 foizi — Qurilish vazirligi huzuridagi Qurilish sohasida axborot-kommunikatsiya texnologiyalarini rivojlantirish markazining shaxsiy hisobvarag‘iga o‘tkaziladi;
qolgan mablag‘lar to‘liq miqdorda vakolatli organning bank hisobvarag‘iga o‘tkaziladi.'),
        (1259, 122, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '16. Ariza beruvchi davlat xizmatidan foydalanishdan uni ko‘rsatishning har qanday bosqichida bosh tortish huquqiga ega.
Ariza beruvchi davlat xizmatidan foydalanishdan bosh tortganda uning so‘rovnomasi ko‘rmasdan qoldiriladi. Bunda to‘langan yig‘im summasi qaytarilmaydi.'),
        (1261, 122, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '18. Xulosa beruvchilar uch ish kuni mobaynida loyiha-smeta hujjatlarining arxitektura qismini ko‘rib chiqadilar va ularga e’tirozlar bo‘lmagan holatda QR-kod qo‘yilgan xulosalarni vakolatli organga elektron shaklda yuboradilar.
Xulosa beruvchilarning barchasidan ijobiy xulosalar olingandan so‘ng vakolatli organ, Kengashlar va tuman (shahar) qurilish bo‘limlari ushbu Reglamentning 7-bandida ko‘rsatilgan muddatlarda ushbu Reglamentga 3-ilovada keltirilgan shakl bo‘yicha QR-kod qo‘yilgan loyiha-smeta hujjatlari kelishilganligi haqidagi xulosani beradi yoki rad etishning aniq asoslarini va qonunchilik hujjatlari normalarini ko‘rsatgan holda kelishishni rad etadi.
Bunda ariza beruvchiga elektron tizim orqali avtomatik tarzda QR-kod qo‘yilgan xulosa beriladi, ariza beruvchi xulosasiz faoliyat bilan shug‘ullanganlik uchun javobgarlikka tortilishi mumkin emas hamda xulosa beruvchilar, vakolatli organ, Kengashlar va tuman (shahar) qurilish bo‘limlari ehtimol tutilgan barcha oqibatlar uchun javob beradi.
Xulosa beruvchilar tomonidan ijobiy xulosa berilib, biroq vakolatli organ, Kengashlar va tuman (shahar) qurilish bo‘limlari tomonidan rad etilgan taqdirda ariza beruvchi rad etish sabablarini bartaraf etib umumiy tartibda davlat xizmatidan foydalanish uchun takroran murojaat qilganda so‘rovnoma xulosa beruvchilarga ko‘rib chiqish uchun qaytadan yuborilmaydi.'),
        (1263, 122, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '20. Loyiha-smeta hujjatlarini kelishish uni vakolatli organ, Kengashlar hamda tuman (shahar) qurilish bo‘limlarining ERIsi bilan tasdiqlash yo‘li bilan amalga oshiriladi.
Bunda arizani ko‘rib chiqish muddati davomida zaruratga ko‘ra ko‘chmas mulk obyektining kadastr hujjatlarini tekshirish talab etilganda Kadastr agentligining axborot tizimi bilan integratsiya qilingan Qurilish vazirligining “Shaffof qurilish” milliy axborot tizimi orqali real vaqt rejimida ko‘chmas mulk obyektiga oid zarur ma’lumotlar va kadastr hujjatlarini mustaqil ravishda (yuklab) olish mumkin.'),
        (1264, 122, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '21. Ariza beruvchilar loyiha-smeta hujjatlarini kelishish rad etilgandan keyin o‘n kun mobaynida davlat xizmati ko‘rsatish uchun yig‘imni to‘lamasdan loyiha-smeta hujjatlarini kelishish uchun qayta murojaat qilish huquqiga ega.
Ariza beruvchilar tomonidan loyiha-smeta hujjatlarini kelishishni rad etish uchun asos bo‘lgan sabablar bartaraf etilganda takroran kelishish:
Kengashlar tomonidan besh ish kunida;
vakolatli organ hamda tuman (shahar) qurilish bo‘limlari tomonidan ikki ish kunida amalga oshiriladi.
Loyiha-smeta hujjatlarini takroran ko‘rib chiqishda avvalgi rad etish asoslarida bayon etilmagan sabablarning keltirilishiga yo‘l qo‘yilmaydi, avval ko‘rsatilgan sabablar bartaraf etilganligini tasdiqlovchi ma’lumotlar bilan bog‘liq rad etish sabablari keltirilishi bundan mustasno.'),
        (1265, 122, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '22. Loyiha-smeta hujjatlari kelishilgan kunning o‘zida QR-kod qo‘yilgan xulosa Davlat xizmatlari markaziga yoki ariza beruvchiga YIDXP orqali yuboriladi.
Davlat xizmatlari markazi xulosa tushganidan so‘ng real vaqt rejimida uni ariza beruvchining elektron manziliga yuboradi va bu haqida ariza beruvchini elektron tarzda xabardor qiladi.
Davlat xizmatlari markazi xodimi ariza beruvchi o‘zi kelib murojaat qilgan taqdirda, ariza beruvchining so‘roviga ko‘ra unga xulosani elektron shaklda yoki qog‘oz shaklida taqdim etadi.'),
        (1266, 122, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '23. Davlat xizmatlari markazlari va vakolatli organlar o‘z faoliyatini amalga oshirishi natijasida olingan ariza beruvchi haqidagi ma’lumotlarning oshkor etilmasligini ta’minlashi kerak.'),
        (1267, 122, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '24. Xulosa loyiha-smeta hujjatlari kelishilgan kunning o‘zida “Shaffof qurilish” milliy axborot tizimida avtomatik ravishda hisobga olib boriladi.'),
        (1268, 122, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '25. Adliya vazirligi, uning hududiy boshqarmalari va Davlat xizmatlari markazlari ushbu Reglament talablari ijrosi bo‘yicha davlat organlari va boshqa tashkilotlarning faoliyati ustidan, shu jumladan, YIDXP orqali, doimiy nazoratni va monitoringni amalga oshiradi.'),
        (1269, 122, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '26. Ariza beruvchi davlat organlari va boshqa tashkilotlar xodimlarining harakatlari (harakatsizligi) yuzasidan qonunchilik hujjatlarida belgilangan tartibda shikoyat qilishi mumkin.'),
        (1270, 122, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '27. Ushbu Reglament talablari buzilishida aybdor bo‘lgan shaxslar qonunchilik hujjatlarida belgilangan tartibda javob beradilar.'),
        (1271, 123, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '1. Ushbu Reglament yuridik va jismoniy shaxslar (keyingi o‘rinlarda — ariza beruvchilar) tomonidan qurilish-montaj ishlarini boshlash uchun ro‘yxatdan o‘tkazish (keyingi o‘rinlarda — ro‘yxatdan o‘tkazish) tartibini belgilaydi.'),
        (1272, 123, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '2. Ushbu Reglamentning amal qilishi, moliyalashtirish manbalaridan qat’i nazar xavf-xatar toifasidan kelib chiqqan holda, qurilish obyektlarida jumladan, ikki qavatdan yuqori (tsokolni hisobga olmagan holda), balandligi yer yuzasidan 12 metrdan baland va (yoki) umumiy maydoni 500 kvadrat metrdan ortiq bo‘lgan yakka tartibdagi uy-joylar hamda 300 metr kubdan ortiq bo‘lgan noturar bino va inshootlarni qurish va rekonstruksiya qilishga qo‘llaniladi.'),
        (1273, 123, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '3. Ushbu Reglament talablari:
ikki qavatdan yuqori bo‘lmagan (tsokolni hisobga olmagan holda), balandligi yer yuzasidan 12 metrdan va (yoki) umumiy maydoni 500 kvadrat metrdan ortiq bo‘lmagan yakka tartibdagi uy-joylar va 300 metr kubdan ortiq bo‘lmagan noturar bino va inshootlarni qurish hamda rekonstruksiya qilish;
joriy ta’mirlash obyektlari;
mukammal ta’mirlash obyektlari (davlat budjeti mablag‘laridan moliyalashtiriladigan obyektlar bundan mustasno);
turar joy toifasidan noturar joy toifasiga rekonstruksiyasiz o‘tkazilayotgan xonalar;
mavsumiy ishlar va yaylov chorvachiligi uchun vaqtinchalik inshootlar va maishiy xonalar;
ishlayotgan muhandislik-kommunikatsiya tarmoqlarini o‘zgartirishni talab etmaydigan kichik o‘lchamdagi (300 kvadrat metrdan kam bo‘lgan) alohida qurilayotgan obyektlar;
kon kavlash ishlari;
yo‘l inshootlari (umumiy foydalanishdagi temir yo‘llar);
murakkablik toifalari tasnifiga ko‘ra I toifa — xavflilik omili past obyektlar;
texnologik resurslari ishlatib bo‘lingan va obyektning ixtisoslashishini o‘zgartirish talab etilmaydigan texnologik hamda muhandislik uskunalarini ta’mirlash va almashtirish obyektlariga nisbatan tatbiq etilmaydi.'),
        (1275, 123, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '5. Qurilish obyektini ro‘yxatdan o‘tkazmasdan qurilish ishlarini amalga oshirish taqiqlanadi.
Loyiha hujjatlarini ekspertizadan o‘tkazmasdan mavjud bino va inshootlarning, shu jumladan, yakka tartibdagi uy-joylarning qavatlari sonini ko‘paytirish hamda o‘zgartirilgan loyiha hujjatlarini ekspertizadan o‘tkazmasdan qurilayotgan bino va inshootlarning qavatlari sonini (loyiha hujjatlarini o‘zgartirish yo‘li bilan) ko‘paytirishga yo‘l qo‘yilmaydi.
Loyiha hujjatlari bo‘yicha ijobiy ekspertiza xulosasi ekspert tashkiloti tomonidan “Shaffof qurilish” milliy axborot tizimidagi “Ekspert xulosalarining yagona reyestri”ga kiritilgan taqdirda, haqiqiy deb tan olinadi. Davlat sirini tashkil etuvchi ma’lumotlarni o‘z ichiga olgan loyiha hujjatlari bo‘yicha ekspert xulosalari bundan mustasno.'),
        (1276, 123, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '6. Davlat xizmatlari markazlari va hududiy inspeksiyalar o‘rtasida axborot almashish ERIdan foydalangan holda faqat elektron tarzda amalga oshiriladi.'),
        (1277, 123, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '7. Qurilish obyektini ro‘yxatdan o‘tkazish ushbu Reglamentga 1-ilovada keltirilgan sxema bo‘yicha amalga oshiriladi.'),
        (1278, 123, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '8. Ariza beruvchilar qurilish-montaj ishlarini ro‘yxatdan o‘tkazish uchun Davlat xizmatlari markazlariga o‘zi kelib murojaat etadi yoki YIDXP davlat xizmatidan elektron shaklda foydalanish uchun ro‘yxatdan o‘tadi.
Qurilish-montaj ishlarini ro‘yxatdan o‘tkazish uchun arizalar YIDXP orqali dam olish va bayram kunlaridan qat’iy nazar, har kuni 24 soat davomida yuborilishi mumkin.
Bunda arizalar ish vaqtidan keyin yoki dam olish va bayram kunlari tushganda navbatdagi ish kuni arizalarning hududiy inspeksiyaga tushgan va qabul qilingan kun hisoblanadi.'),
        (1279, 123, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '9. O‘zi kelib murojaat etgan taqdirda, Davlat xizmatlari markazi xodimi ariza beruvchi nomidan, YIDXP orqali murojaat etgan taqdirda esa — ariza beruvchi mustaqil ravishda ushbu Reglamentga 2-ilovada keltirilgan shakl bo‘yicha davlat xizmatidan foydalanish uchun so‘rovnomani elektron shaklda to‘ldiradi.
So‘rovnomaga (yakka tartibdagi uy-joylar bundan mustasno) quyidagilar ilova qilinadi:
qurilish-montaj ishlari sifatini nazorat qilishga pudrat tashkilotining qurilish laboratoriyasi mavjudligini tasdiqlovchi yoki shartnoma asosida laboratoriya jalb etilganligi to‘g‘risidagi hujjat;
buyurtmachining texnik nazorati, loyiha tashkilotining mualliflik nazorati va pudrat tashkilotining ichki nazorati tayinlanganligi to‘g‘risidagi buyruq yoki shartnoma nusxalari;
buyurtmachining texnik nazorati, loyiha tashkilotining mualliflik nazorati va pudrat tashkilotining ichki nazorat mutaxassislarining sertifikatlari;
qurilishni tashkil etish va ishlarni bajarishning tasdiqlangan loyihalari.
So‘rovnomada qurilish ishtirokchilari to‘g‘risidagi ma’lumotlar, loyiha hujjatlari kelishilganligi to‘g‘risidagi xulosa va ijobiy yig‘ma ekspertiza xulosasining (sanasi va raqami) ma’lumotlari majburiy ko‘rsatiladi.
Hududiy inspeksiyalar ro‘yxatdan o‘tkazish uchun shaharsozlik hujjatlari va ekspertiza xulosalarini “Shaffof qurilish” milliy axborot tizimidan elektron tarzda olishi talab etiladi.'),
        (1280, 123, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '10. Ariza beruvchilardan 9-bandda ko‘rsatilmagan ma’lumotlarni hamda boshqa hujjatlarni talab qilish taqiqlanadi.'),
        (1281, 123, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '11. Ariza beruvchi zarur ma’lumotlarni so‘rovnomaga kiritgandan so‘ng uni o‘z ERI bilan tasdiqlaydi.
O‘zi kelib murojaat etgan taqdirda, so‘rovnomani ERI bilan tasdiqlash imkoniyati bo‘lmagan hollarda tegishli imzolarni identifikatsiya qilishning boshqa vositalari (daktiloskopiya, elektron imzo va boshqalar) bilan ham tasdiqlash mumkin.'),
        (1282, 123, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '12. Ro‘yxatdan o‘tkazish bo‘yicha davlat xizmatlari ko‘rsatish uchun yig‘im undirilmaydi.'),
        (1283, 123, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '13. Zarur ma’lumotlar so‘rovnomaga kiritilgandan so‘ng ariza beruvchi ma’lumotlarning to‘g‘riligini tekshiradi va imzolarni identifikatsiya qilishning tegishli vositalari (ERI, daktiloskopiya, elektron imzo, Mobil-ID va boshqalar) bilan tasdiqlaydi.'),
        (1284, 123, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '14. O‘zi kelib murojaat etganda Davlat xizmatlari markazlari so‘rovnoma to‘ldirilgan vaqtdan boshlab real vaqt rejimida uni hududiy inspeksiyaga yuboradi.
YIDXP orqali murojaat qilinganda esa so‘rovnoma avtomatik tarzda hududiy inspeksiyalarga yuboriladi.'),
        (1285, 123, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '15. Hududiy inspeksiya so‘rovnoma tushgan vaqtdan boshlab bir ish kuni mobaynida:
zarurat bo‘lganda obyektni joyiga borib o‘rganadi, qurilayotgan obyektlarga moneliksiz kiradi;
ekspertiza xulosasi “Shaffof qurilish” milliy axborot tizimiga kiritilganligini va ijobiy xulosa berilganligini tekshiradi;
“Shaffof qurilish” milliy axborot tizimi orqali qurilish-montaj ishlarini boshlash uchun obyektni ro‘yxatdan o‘tkazadi;
so‘rovnomada ko‘rsatilgan obyektning qurilish-montaj ishlari qiymatining 0,2 foizi miqdorida (qo‘shimcha qiymat solig‘isiz) ajratmani belgilagan holda invoysni shakllantiradi;
o‘z ERIsi bilan tasdiqlagan holda ushbu Reglamentga 3-ilovada keltirilgan shakl bo‘yicha obyekt ro‘yxatdan o‘tkazilganligi haqida QR-kod qo‘yilgan ko‘chirmani rasmiylashtiradi yoki uni berishni rad etishning aniq sabablarini va qonunchilik hujjatlari normalarini ko‘rsatgan holda ro‘yxatdan o‘tkazishni rad etadi.'),
        (1286, 123, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '16. Ushbu holatlarda ariza beruvchi umumiy tartibda davlat xizmatidan foydalanish uchun takroran murojaat qilishga haqli.
Takroriy murojaatni ko‘rib chiqishda hududiy inspeksiyalar tomonidan avvalgi rad etish asoslarida bayon etilmagan sabablarning keltirilishiga yo‘l qo‘yilmaydi, avval ko‘rsatilgan sabablar bartaraf etilganligini tasdiqlovchi ma’lumotlar bilan bog‘liq rad etish sabablarining keltirilishi bundan mustasno.'),
        (1287, 123, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '17. Davlat xizmati ko‘rsatishni rad etish uchun quyidagilar asos hisoblanadi:
so‘rovnomada noto‘g‘ri ma’lumotlar ko‘rsatilishi;
buyurtmachining texnik nazorati, loyiha tashkilotining mualliflik nazorati va pudrat tashkilotining ichki nazorati tayinlanganligi to‘g‘risidagi buyruq yoki shartnoma rasmiylashtirilmaganligi;
buyurtmachining texnik nazorati, loyiha tashkilotining mualliflik nazorati va pudrat tashkilotining ichki nazorat mutaxassislari sertifikatlarining mavjud emasligi;
qurilishni tashkil etish va ishlarni bajarishning tasdiqlangan loyihalari mavjud emasligi;
loyiha hujjatlari bo‘yicha ijobiy ekspertiza xulosasi ekspert tashkiloti tomonidan “Shaffof qurilish” milliy axborot tizimiga kiritilmaganligi yoki ijobiy xulosa olinmaganligi.
Hududiy inspeksiya tomonidan ro‘yxatdan o‘tkazish rad etish normativ-huquqiy hujjatning yoki shaharsozlik normalari va qoidalarining nomi, uning qismi, bandi, kichik bandi va xatboshisiga asoslangan bo‘lishi talab etiladi.
Boshqa asoslarga ko‘ra, shu jumladan, ro‘yxatdan o‘tkazish maqsadga muvofiq emas degan vaj bilan ro‘yxatdan o‘tkazishni rad etishga yo‘l qo‘yilmaydi.'),
        (1288, 123, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '18. Invoysni va ko‘chirma rasmiylashtirilgan kuni hududiy inspeksiya invoys va ko‘chirmani Davlat xizmatlari markaziga yoki ariza beruvchiga YIDXP orqali elektron shaklda bir vaqtda yuboradi.'),
        (1295, 123, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '25. Ariza yuborilgan vaqtdagi shartlar o‘zgargan taqdirda (ish bajaruvchilarining, obyektda texnik va mualliflik nazoratini amalga oshiradigan shaxslar almashishi, litsenziyaning amal qilish muddati tugashi, loyiha hamda uning qiymatidagi yechimlarning o‘zgarishi va hokazo), ariza beruvchilar o‘n kun mobaynida Davlat xizmatlari markazlari yoki YIDXP orqali hududiy inspeksiyalarga tegishli o‘zgarishlarni ko‘rsatgan holda qo‘shimcha ravishda murojaat qiladi.'),
        (1296, 123, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '26. Adliya vazirligi, uning hududiy boshqarmalari va Davlat xizmatlari markazlari ushbu Reglament talablari ijrosi bo‘yicha davlat organlari va boshqa tashkilotlarning faoliyati ustidan, shu jumladan, YIDXP orqali, doimiy nazoratni va monitoringni amalga oshiradilar.'),
        (1297, 123, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '27. Ariza beruvchi davlat organlari va boshqa tashkilotlar xodimlarining harakatlari (harakatsizligi) yuzasidan qonunchilik hujjatlarida belgilangan tartibda shikoyat qilishi mumkin.'),
        (1298, 123, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '28. Ushbu Reglament talablari buzilishida aybdor bo‘lgan shaxslar qonunchilik hujjatlarida belgilangan tartibda javob beradilar.'),
        (1299, 124, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '1. Ushbu Reglament jismoniy va yuridik shaxslarning (keyingi o‘rinlarda — ariza beruvchilar) qurilish-montaj ishlari tugallangan bino va inshootlar va yakka tartibdagi uy-joylardan foydalanish uchun ruxsatnoma (keyingi o‘rinlarda — ruxsatnoma) berish tartibini belgilaydi.
Ushbu Reglamentning amal qilishi barcha turdagi binolar, inshootlar va yakka tartibdagi uy-joylarning qurilish-montaj ishlari tugallangan obyektlariga nisbatan qo‘llaniladi.'),
        (1300, 124, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '2. Ushbu Reglament davlat sirlari yoki qonun bilan qo‘riqlanadigan boshqa sir bilan bog‘liq qurilishi tugallangan obyektlar, shuningdek, davlat ahamiyatiga ega obyektlarga nisbatan tatbiq etilmaydi.'),
        (1301, 124, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '3. Qurilish-montaj ishlari tugallangan bino va inshootlardan ruxsatnomasiz foydalanish taqiqlanadi.
Ruxsatnoma ko‘chmas mulkka bo‘lgan huquqlarni davlat ro‘yxatidan o‘tkazish uchun asos bo‘ladi.'),
        (1302, 124, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '4. Qurilish-montaj ishlari tugallangan obyektdan foydalanish uchun ruxsatnoma berish tartibotiga ko‘ra:
a) qurilish sohasida hududiy nazorat inspeksiyalari (keyingi o‘rinlarda — hududiy inspeksiya) qurilish-montaj ishlari tugallangan ikki qavatdan yuqori (tsokolni hisobga olmagan holda), balandligi yer yuzasidan 12 metrdan yuqori va (yoki) umumiy maydoni 500 kvadrat metrdan ortiq bo‘lgan yakka tartibdagi uy-joylar hamda 300 metr kubdan ortiq bo‘lgan noturar bino va inshootlardan (keyingi o‘rinlarda — II, III, IV toifalardagi obyektlar) foydalanish uchun ruxsatnoma beradilar;
b) tuman (shahar) qurilish bo‘limlari qurilish-montaj ishlari tugallangan ikki qavatdan yuqori bo‘lmagan (tsokolni hisobga olmagan holda), balandligi yer yuzasidan 12 metrdan va (yoki) umumiy maydoni 500 kvadrat metrdan ortiq bo‘lmagan yakka tartibdagi uy-joylar va 300 metr kubdan ortiq bo‘lmagan noturar bino va inshootlardan (keyingi o‘rinlarda — I toifa obyektlar) foydalanish uchun ruxsatnoma beradilar;
v) sanitariya-epidemiologiya xizmatining tuman (shahar) bo‘limi va tuman (shahar) favqulodda vaziyatlar bo‘limi (keyingi o‘rinlarda — xulosa beruvchilar) II, III, IV toifalardagi obyektlardan foydalanish bo‘yicha xulosa beradilar;
g) ijtimoiy-maishiy va madaniy maqsadlarga mo‘ljallangan qurilish obyektlarini hamda rekonstruksiya qilinayotgan obyektlarni foydalanishga qabul qilish nogironligi bo‘lgan shaxslarning jamoat birlashmalari vakillari obyektdan foydalanish uchun ruxsatnoma berishda ishtirok etadilar;
d) Davlat kadastrlari palatasining Qoraqalpog‘iston Respublikasi, viloyatlar va Toshkent shahar boshqarmalari (keyingi o‘rinlarda — ro‘yxatdan o‘tkazuvchi organ) ruxsatnomaga asosan ko‘chmas mulkka bo‘lgan huquqlarni davlat ro‘yxatidan o‘tkazadilar;
e) ko‘p xonadonli uy-joy fondidan foydalanishni nazorat qilish hududiy inspeksiyalari ko‘p kvartirali uylarni qurish (rekonstruksiya qilish) ishlarining loyiha hujjatlariga muvofiqligi yuzasidan tegishli xulosa beradilar.'),
        (1303, 124, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '5. Hududiy inspeksiya va tuman (shahar) qurilish bo‘limlari arizani ko‘rib chiqish muddati davomida, zaruratga ko‘ra ko‘chmas mulk obyektining kadastr hujjatlarini tekshirishi talab etilganda Kadastr agentligining axborot tizimi bilan integratsiya qilingan Qurilish vazirligining “Shaffof qurilish” milliy axborot tizimi orqali real vaqt rejimida ko‘chmas mulk obyektiga oid zarur ma’lumotlar va kadastr hujjatlarini mustaqil ravishda (yuklab) olishi mumkin.'),
        (1304, 124, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '6. Davlat xizmatlari markazlari va hududiy inspeksiya, tuman (shahar) qurilish bo‘limlari, xulosa beruvchilar va ro‘yxatdan o‘tkazuvchilar o‘rtasida axborot almashish ERIdan foydalangan holda faqat elektron tarzda amalga oshiriladi.'),
        (1305, 124, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '7. Ruxsatnoma olishdan oldin foydalanishga qabul qilinayotgan bino va inshootga belgilangan tartibda kadastr pasporti rasmiylashtirish talab etiladi.'),
        (1306, 124, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '8. Qurilish-montaj ishlari tugallangan obyektdan foydalanish uchun ruxsatnoma berish ushbu Reglamentga 1-ilovada keltirilgan sxemaga muvofiq amalga oshiriladi.'),
        (1307, 124, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '9. Ruxsatnoma olish uchun ariza beruvchilar Davlat xizmatlari markaziga o‘zi kelib murojaat etadi yoki YIDXP davlat xizmatidan elektron shaklda foydalanish uchun ro‘yxatdan o‘tadi.
Ruxsatnoma olish uchun arizalar YIDXP orqali dam olish va bayram kunlaridan qat’iy nazar, har kuni 24 soat davomida yuborilishi mumkin.
Bunda arizalar ish vaqtidan keyin yoki dam olish va bayram kunlari tushganda navbatdagi ish kuni arizalarning vakolatli organga kelib tushgan va qabul qilingan kun hisoblanadi.'),
        (1308, 124, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '10. O‘zi kelib murojaat etgan taqdirda, Davlat xizmatlari markazi xodimi ariza beruvchi nomidan, YIDXP orqali murojaat etgan taqdirda esa — ariza beruvchi mustaqil ravishda ushbu Reglamentga 2-ilovaga muvofiq davlat xizmatidan foydalanish uchun so‘rovnomani elektron shaklda to‘ldiradi.'),
        (1309, 124, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '11. So‘rovnomaga quyidagilar elektron shaklda ilova qilinadi:
pudrat tashkiloti va buyurtmachi o‘rtasida imzolangan qurilishi tugallangan obyektni topshirish-qabul qilish dalolatnomasi;
pudrat tashkiloti tomonidan berilgan muvofiqlik deklaratsiyasi;
mualliflik va texnik nazoratni amalga oshiruvchi shaxslar tomonidan berilgan bajarilgan ishlarning tasdiqlangan shaharsozlik hujjatlariga muvofiqligi to‘g‘risidagi xulosasi;
I toifa obyektlar bo‘yicha yuqorida sanab o‘tilgan hujjatlar ilova qilinishi talab etilmaydi.
Ariza beruvchilardan ushbu bandda ko‘rsatilmagan ma’lumotlarni va boshqa hujjatlarni taqdim etishni talab etish taqiqlanadi.'),
        (1310, 124, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '12. Ariza beruvchi zarur ma’lumotlarni so‘rovnomaga kiritgandan so‘ng ma’lumotlarning to‘g‘riligini tekshiradi va imzolarni identifikatsiya qilishning tegishli vositalari (daktiloskopiya, elektron imzo, Mobil-ID va boshqalar) bilan tasdiqlaydi.'),
        (1311, 124, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '13. Davlat xizmatlari ko‘rsatish uchun bazaviy hisoblash miqdorining 30 foizi miqdorida yig‘im undiriladi.
Davlat xizmati ko‘rsatilishi uchun YIDXP orqali murojaat etilganda ushbu bandning birinchi xatboshisida ko‘rsatilgan yig‘im summasining 90 foizi undiriladi.
Davlat xizmatini ko‘rsatish uchun yig‘im to‘langanligini tasdiqlash elektron tarzda amalga oshiriladi.'),
        (1349, 126, '2023-10-09 11:40:53', '2023-10-09 11:40:53', '3. Belgilansinki, Konsepsiyani amalga oshirish doirasida irrigatsiya-melioratsiya tarmoqlarini tiklash, rekonstruksiya qilish, suv tejaydigan texnologiyalarni joriy etish hamda suvsizlikka chidamli ekinlarni ekish orqali foydalanishga kiritish uchun investitsiyaviy shartnoma yoki davlat-xususiy sheriklik asosida 50 yilgacha bo‘lgan muddatga:
qishloq xo‘jaligida foydalanishdan chiqib ketgan sug‘oriladigan yerlar, shuningdek, lalmi va o‘rmon fondi yerlari — O‘zbekiston Respublikasi fuqarolari va qishloq xo‘jaligi korxonalariga;
yaylov, bo‘z, ko‘p yillik daraxtzorlar va boshqa yerlar — O‘zbekiston Respublikasi fuqarolari, qishloq xo‘jaligi korxonalariga, shuningdek O‘zbekiston Respublikasi rezidenti bo‘lgan investorlarga beriladi.'),
        (1312, 124, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '14. Yig‘imlar summasi quyidagi tartibda taqsimlanadi:
a) Davlat xizmatlari markazlariga o‘zi kelib murojaat etganda:
20 foizi — Adliya vazirligining Davlat xizmatlarini rivojlantirish jamg‘armasining shaxsiy hisobvarag‘iga o‘tkaziladi;
10 foizi — Qurilish vazirligi huzuridagi Qurilish sohasida axborot-kommunikatsiya texnologiyalarini rivojlantirish markazining shaxsiy hisobvarag‘iga o‘tkaziladi;
qolgan mablag‘lar to‘liq miqdorda hududiy inspeksiya (II, III, IV toifalardagi obyektlar bo‘yicha) yoki Qoraqalpog‘iston Respublikasi Qurilish vazirligi, viloyatlar va Toshkent shahar qurilish bosh boshqarmalari (I toifa obyektlar bo‘yicha)ning bank hisobvaraqlariga o‘tkaziladi;
b) YIDXP orqali murojaat etilganda:
10 foizi — Adliya vazirligining Davlat xizmatlarini rivojlantirish jamg‘armasining shaxsiy hisob varag‘iga o‘tkaziladi;
10 foizi — Qurilish vazirligi huzuridagi Qurilish sohasida axborot-kommunikatsiya texnologiyalarini rivojlantirish markazining shaxsiy hisobvarag‘iga o‘tkaziladi;
11,25 foizi — Axborot-kommunikatsiya texnologiyalarini rivojlantirish jamg‘armasining elektron hukumat va raqamli iqtisodiyotning idoralararo loyihalarini moliyalashtirish bo‘yicha maxsus hisobvarag‘iga o‘tkaziladi;
qolgan mablag‘lar to‘liq miqdorda hududiy inspeksiya (II, III, IV toifalardagi obyektlar bo‘yicha) yoki Qoraqalpog‘iston Respublikasi Qurilish vazirligi, viloyatlar va Toshkent shahar qurilish bosh boshqarmalari (I toifa obyektlar bo‘yicha)ning bank hisobvaraqlariga o‘tkaziladi.'),
        (1313, 124, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '15. Ariza beruvchi davlat xizmatidan foydalanishdan uni ko‘rsatishning har qanday bosqichida bosh tortish huquqiga ega.
Ariza beruvchi davlat xizmatidan foydalanishdan, ko‘chmas mulkka bo‘lgan huquq davlat ro‘yxatidan o‘tkazilguniga qadar, bosh tortganda uning so‘rovnomasi ko‘rmasdan qoldiriladi. Bunda to‘langan yig‘im summasi qaytarilmaydi.'),
        (1314, 124, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '16. Davlat xizmatlari markazlari xodimi so‘rovnoma to‘ldirilgan vaqtdan boshlab real vaqt rejimida obyektni kadastr pasportida ko‘rsatilgan umumiy maydoni va toifasiga ko‘ra:
II, III, IV toifalardagi obyektlar bo‘yicha xulosa beruvchilarga;
I toifa obyektlar bo‘yicha tuman (shahar) qurilish bo‘limlariga;
II, III, IV toifalardagi obyektlar bo‘yicha xulosa beruvchilarga;
ikki qavatdan yuqori, balandligi yer yuzasidan 12 metrdan va (yoki) umumiy maydoni 500 kvadrat metrdan ortiq yakka tartibdagi uy-joylar bo‘yicha so‘rovnoma faqat hududiy inspeksiyaga yuboradi.
YIDXP orqali murojaat qilinganda esa so‘rovnoma avtomatik tarzda ushbu organlarga yuboriladi.'),
        (1315, 124, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '17. Xulosa beruvchilar uch ish kuni mobaynida joyiga chiqqan holda bino va inshootlarni sanitariya-epidemiologik hamda yong‘in xavfsizligi talablariga muvofiqligi yuzasidan o‘rganib chiqadi. O‘rganish natijalari bo‘yicha asosli e’tiroz bo‘lmagan taqdirda, QR-kod qo‘yilgan xulosani hududiy inspeksiyaga elektron yuboradi.
Bino va inshoot sanitariya-epidemiologik hamda yong‘in xavfsizligi talablariga javob bermagan taqdirda, xulosa beruvchilar rad etishning aniq asoslarini va qonunchilik hujjatlari normalarini ko‘rsatgan holda xulosa berishni rad etish to‘g‘risidagi xulosani rasmiylashtirilgan kunning o‘zida Davlat xizmatlari markaziga yoki ariza beruvchiga YIDXP orqali yuboradi.'),
        (1316, 124, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '18. Xulosa beruvchilar tomonidan hududiy inspeksiyaga ijobiy xulosa kelib tushgandan so‘ng to‘rt ish kuni mobaynida hududiy inspeksiya joyiga chiqqan holda qurilish-montaj ishlari tugallangan II, III, IV toifalardagi obyektni:
qabul qilish va ularni foydalanishga topshirish shaharsozlik hujjatlari talablariga muvofiqligini;
bino va inshootlarning konstruktiv mustahkamligi, barqarorligi va ishonchliligini;
bino va inshootlardan foydalanish davrida yong‘in xavfsizligi, salomatlik va atrof-muhitni muhofaza qilish, xavfsizligini;
binolar, inshootlar va jamoat maskanlaridan imkoniyatlari cheklangan shaxslarning foydalanishi mumkinligini;
binolarning energiya tejamkorligini va ularning shovqindan himoya qilinishini;
bino va inshootlarda qayta tiklanuvchi va energiya samarador qurilish materiallaridan foydalanilganligini o‘rganadi.
O‘rganish natijalari asosida ushbu Reglamentga 3-ilovada keltirilgan shaklga muvofiq QR-kod qo‘yilgan ruxsatnomani beradi yoki rad etishning aniq sabablarini va qonunchilik hujjatlari normalarini ko‘rsatgan holda ruxsatnoma berishni rad etadi.
Ruxsatnoma elektron ko‘rinishda rasmiylashtiriladi va hududiy inspeksiyaning ERIsi bilan tasdiqlanadi.'),
        (1317, 124, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '19. Quyidagilar ruxsatnoma berishni rad etish uchun asos bo‘ladi:
so‘rovnomada noto‘g‘ri ma’lumotlar ko‘rsatilishi;
davlat xizmatlari ko‘rsatish uchun yig‘imni to‘lamaslik;
qurilish-montaj ishlari qiymati uchun to‘lanishi lozim bo‘lgan ajratmalar to‘liq to‘lanmaganligi;
kelishilgan loyiha yechimlari hamda shaharsozlik hujjatlari talablari buzilishi;
binolar va inshootlarning zilzilabardoshliligiga ta’sir qiluvchi, shuningdek, fuqarolar hayoti va sog‘lig‘iga tahdid soluvchi konstruktiv qoidabuzarliklar mavjudligi;
sifatsiz qurilish materiallari, konstruksiyalari hamda buyumlari qo‘llanilganligi;
ruxsatnoma berish uchun asos bo‘lgan hujjatlarning mavjud emasligi;
texnik va mualliflik nazoratini amalga oshiruvchi mutaxassislarning salbiy xulosalari mavjud bo‘lganda pudrat tashkiloti qurilish normalari va qoidalari bo‘yicha buzilishlarni bartaraf etmaganda;
loyiha hujjatlari bo‘yicha ijobiy ekspertiza xulosasi ekspert tashkiloti tomonidan “Shaffof qurilish” milliy axborot tizimiga kiritilmaganligi yoki ijobiy xulosa olinmaganligi;
obyekt qurilish-montaj ishlarini boshlash uchun hududiy inspeksiyadan ro‘yxatdan o‘tkazilmaganligi;
vakolatli organga obyektni o‘rganish uchun imkoniyat yaratilmaganligi;
yer uchastkasiga bo‘lgan huquq davlat ro‘yxatidan o‘tkazilmaganligi;
ro‘yxatdan o‘tkazuvchi organda ushbu ko‘chmas mulk obyektining mansubligi masalasida nizolar borligidan dalolat beruvchi sud hujjatlarining mavjudligi;
vakolatli organ tomonidan ko‘chmas mulk obyektiga taqiq, xatlov yoki garovga (ipotekaga) qo‘yilganligi (vakolatli organning rozilik xati taqdim etilsa bundan mustasno);
ro‘yxatdan o‘tkazuvchi organda ushbu yer uchastkasi qonunchilik hujjatlarida belgilangan tartibda olib qo‘yilganligi haqidagi ma’lumotlarning mavjudligi;
foydalanishga qabul qilinayotgan bino va inshootlarning kadastr yig‘ma jildi (pasporti) mavjud emasligi;
shaharsozlik normalari va qoidalariga muvofiq emasligi.
Davlat xizmatlarini ko‘rsatishni boshqa asoslarga ko‘ra rad etish qat’iyan taqiqlanadi.
Rad etish hududiy inspeksiya va xulosa beruvchilar tomonidan shaharsozlik hujjati nomi, uning qismi, bandi, kichik bandi yoki xatboshisi orqali asoslangan bo‘lishi talab etiladi.'),
        (1318, 124, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '20. Ariza beruvchi ruxsatnoma berish rad etilgan kundan boshlab o‘n ish kuni mobaynida davlat xizmati ko‘rsatish uchun yig‘imni to‘lamasdan ruxsatnomani olish uchun qayta murojaat qilish huquqiga ega.
Ruxsatnoma berishni rad etish uchun asos bo‘lgan sabablar ariza beruvchi tomonidan bartaraf etilgan taqdirda, ruxsatnoma takroran so‘rovnoma tushganidan so‘ng hududiy inspeksiya va tuman (shahar) qurilish bo‘limlari tomonidan ikki ish kuni mobaynida oshmagan muddatda ko‘rib chiqiladi.
So‘rovnomani takroran ko‘rib chiqishda hududiy inspeksiya tomonidan avvalgi rad etish asoslarida bayon etilmagan sabablarning keltirilishiga yo‘l qo‘yilmaydi, avval ko‘rsatilgan sabablar bartaraf etilganligini tasdiqlovchi ma’lumotlar bilan bog‘liq rad etish sabablari keltirilishi bundan mustasno.'),
        (1319, 124, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '21. Xulosa beruvchilar tomonidan ijobiy xulosa berilib, biroq hududiy inspeksiya tomonidan rad etilgan taqdirda, ariza beruvchi rad etish sabablarini bartaraf etib takroran murojaat qilganda, so‘rovnoma ko‘rib chiqish uchun faqat hududiy inspeksiyaga yuboriladi.'),
        (1381, 136, '2024-05-29 17:30:50', '2024-05-29 17:30:50', '7. Vazirlikning rasmiy nomi:
a) davlat tilida:
to‘liq nomi — O‘zbekiston Respublikasi Qurilish va uy-joy kommunal xo‘jaligi vazirligi;
qisqartirilgan nomi — Qurilish va uy-joy kommunal xo‘jaligi vazirligi;
b) rus tilida:
to‘liq nomi — Ministerstvo stroitelstva i jilishno-kommunalnogo xozyaystva Respubliki Uzbekistan;
qisqartirilgan nomi — Minstroykomxoz;'),
        (1382, 136, '2024-05-29 17:30:50', '2024-05-29 17:30:50', '8. Vazirlikning joylashgan manzili (pochta manzili): Toshkent shahri'),
        (1320, 124, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '22. Obyektlarni, shuningdek, yakka tartibdagi uy-joylarni bosqichma-bosqich foydalanish uchun ruxsatnoma berilishi mumkin. Obyekt dastlabki bosqichda foydalanish uchun ruxsatnoma berilgandan so‘ng uning qurilishi hamda rekonstruksiya qilinishi tugallanmagan qismlari obyektning zilzilabardoshliligini va yong‘inga chidamliligini ta’minlashi shart.
Qurilayotgan obyekt loyiha hujjatlariga muvofiq belgilangan ishlarning barchasi, xususan, bino va inshootlarning barchasi to‘liq qurilgan, pardozlash ishlari yakunlangan hamda tashqi muhandislik tarmoqlariga ulangandan so‘ng obyektdan foydalanishga ruxsatnoma berilishi mumkin.
Markaziy ko‘chalarning ikki tarafida joylashmagan yakka tartibdagi uy-joyning muayyan xona, oshxona, hojatxonasi, uyning ichki bezagi yakunlanib yashash uchun yaroqli holatga kelgandan keyin ariza beruvchining talabi asosida uy-joy muhandislik tarmoqlariga ulanmagan, alohida yordamchi-xo‘jalik imoratlarisiz, hududni obodonlashtirmasdan uyning bir qismidan foydalanishga ruxsatnoma berishga yo‘l qo‘yiladi.'),
        (1321, 124, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '23. Tuman (shahar) qurilish bo‘limi so‘rovnoma tushgandan so‘ng to‘rt ish kuni mobaynida obyektni joyiga borgan holda qonunchilik hujjatlarida belgilangan vakolatlar doirasida qurilish-montaj ishlari tugallangan I toifa obyektlarning shaharsozlik hujjatlariga muvofiqligini o‘rganib, ushbu Reglamentga 4-ilovada keltirilgan shaklga muvofiq QR-kod qo‘yilgan ruxsatnoma beradi yoki rad etishning aniq sabablarini va qonunchilik hujjatlari normalarini ko‘rsatgan holda ruxsatnoma berishni rad etadi.
Ruxsatnoma elektron ko‘rinishda rasmiylashtiriladi va tuman (shahar) qurilish bo‘limining ERIsi bilan tasdiqlanadi.'),
        (1322, 124, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '24. Hududiy inspeksiya yoki tuman (shahar) qurilish bo‘limi QR-kodli ruxsatnoma rasmiylashtirilgan kunning o‘zida avtomatik tarzda ro‘yxatdan o‘tkazuvchi organga ruxsatnomani ko‘chmas mulkka bo‘lgan huquqlarni davlat ro‘yxatidan o‘tkazish uchun yuboradi.'),
        (1323, 124, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '25. Hududiy inspeksiya yoki tuman (shahar) qurilish bo‘limi rasmiylashtirilgan QR-kodli ruxsatnoma tushgan kundan keyin ikki ish kuni mobaynida ro‘yxatdan o‘tkazuvchi organ belgilangan tartibda ko‘chmas mulkka bo‘lgan huquqlarni davlat ro‘yxatidan o‘tkazib, tegishli huquq davlat ro‘yxatidan o‘tkazilganligini tasdiqlovchi ko‘chmas mulk obyektlariga bo‘lgan huquqlarning davlat reyestridan ko‘chirmani rasmiylashtiradi.'),
        (1324, 124, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '26. Ko‘chmas mulkka bo‘lgan huquqlarni davlat ro‘yxatidan o‘tkazish uchun belgilangan yig‘im miqdori ko‘rsatilgan xabarnoma (invoys) tashkilotlarga, Markazga yoki ariza beruvchiga YIDXP orqali elektron tarzda yuboriladi.
Xabarnomada ko‘rsatilgan yig‘im to‘liq to‘langanidan so‘ng real vaqt rejimida tegishli huquqning davlat ro‘yxatidan o‘tkazilganligini tasdiqlovchi elektron shakldagi Reyestrdan ko‘chirma yuklab olish imkoni paydo bo‘ladi va ariza beruvchining so‘roviga ko‘ra unga reyestrdan ko‘chirma elektron shaklda yoki qog‘oz shaklida berilishi mumkin.'),
        (1325, 124, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '27. Davlat xizmatlari markazlari, hududiy inspeksiya, tuman (shahar) qurilish bo‘limlari, xulosa beruvchilar va ro‘yxatdan o‘tkazuvchilar o‘z faoliyatini amalga oshirishi natijasida olingan ariza beruvchi haqidagi ma’lumotlarning oshkor etilmasligini ta’minlashi kerak.'),
        (1326, 124, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '28. Hududiy inspeksiya va tuman (shahar) qurilish bo‘limlari tomonidan ruxsatnoma rasmiylashtirilgan kunning o‘zida mazkur ruxsatnoma “Shaffof qurilish” milliy axborot tizimida avtomatik tarzda hisobga olinadi.'),
        (1327, 124, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '29. Adliya vazirligi, uning hududiy boshqarmalari va Davlat xizmatlari markazlari ushbu Reglament talablari ijrosi bo‘yicha davlat organlari va boshqa tashkilotlarning faoliyati ustidan, shu jumladan, YIDXP orqali, doimiy nazoratni va monitoringni amalga oshiradilar.'),
        (1328, 124, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '30. Ariza beruvchi davlat organlari va boshqa tashkilotlar xodimlarining harakatlari (harakatsizligi) yuzasidan qonunchilik hujjatlarida belgilangan tartibda shikoyat qilishi mumkin.'),
        (1329, 124, '2023-06-20 11:11:17', '2023-06-20 11:11:17', '31. Ushbu Reglament talablari buzilishida aybdor bo‘lgan shaxslar qonunchilik hujjatlarida belgilangan tartibda javob beradilar.'),
        (1330, 125, '2023-10-09 11:37:55', '2023-10-09 11:37:59', '1. Turizm va sport vazirligi huzurida Madaniy meros agentligi (keyingi o‘rinlarda — Agentlik) hamda uning Qoraqalpog‘iston Respublikasi'),
        (1331, 125, '2023-10-09 11:37:55', '2023-10-09 11:37:59', '2. Agentlikning asosiy vazifalari etib quyidagilar belgilansin:
a) moddiy madaniy meros obyektlarini muhofaza qilish sohasida:
moddiy madaniy meros obyektlarini aniqlash, hisobga olish, muhofaza qilish, targ‘ib qilish, davlat reyestri, elektron katalogi, pasporti va davlat kadastrini yuritish, qo‘riqlanadigan tegralarini belgilash hamda sohada davlat nazoratini amalga oshirish;
moddiy madaniy meros obyektlaridan oqilona foydalanish, shu jumladan ularni davlat-xususiy sheriklik asosida samarali boshqarish, sohani raqamlashtirish va zamonaviy axborot texnologiyalarini joriy etish;
moddiy madaniy merosning ko‘chmas mulk obyektlarida asrashga doir ishlarni amalga oshirish, buyurtmachi funksiyasini bajarish, uzluksiz ravishda ilmiy-texnik nazorat qilish, tarixiy-madaniy ekspertiza va loyiha-smeta hujjatlarining tarixiy-madaniy qimmatga ta’siri bo‘yicha ekspertizasini amalga oshirish;
b) muzeylar sohasida:
milliy muzey fondining davlat katalogini yuritish, muzey ashyolari va muzey kolleksiyalarini milliy muzey fondi tarkibiga kiritish, undan chiqarish, universal huquqiy vorislik tartibida yoxud boshqa usulda bir shaxsdan ikkinchi shaxsga o‘tkazish hamda sohada davlat nazoratini amalga oshirish;
muzeylar faoliyatini muvofiqlashtirish, rivojlantirish, muzey xodimlarining malakasini oshirish, qayta tayyorlash, attestatsiyadan o‘tkazish, muzeylarning toifalarini belgilash, milliy muzey fondining ochiqligini ta’minlash va muzey ashyolari hamda muzey kolleksiyalarini o‘rganish, ommalashtirish, restavratsiya va asrashga doir ishlarni amalga oshirish;
v) alohida muhofaza qilinadigan tarixiy-madaniy hududlar sohasida:
alohida muhofaza qilinadigan tarixiy-madaniy hududlarni muhofaza qilish, ularning pasporti, boshqarish rejalari, rejimi, qo‘riqlanadigan tegralarini belgilash, ushbu hududlar va tarixiy mahallalarning tarixiy-madaniy qimmati, noyobligi, tabiiy landshafti va o‘ziga xosligini saqlab qolish;
tarixiy joylarni Umumjahon merosi ro‘yxatiga kiritishga tavsiya etish, Umumjahon madaniy va tabiiy merosini muhofaza qilish to‘g‘risidagi konvensiya talablari, YUNESKO, Umumjahon merosi markazi, Umumjahon merosi qo‘mitasi tavsiyalarining mamlakat hududida bajarilishini nazorat qilish;
g) arxeologiya sohasida:
arxeologiya tadqiqotlarini qo‘llab-quvvatlash, muvofiqlashtirish, nazorat qilish, arxeologiya tadqiqoti uchun ruxsatnoma berish, ilmiy hisobotni ekspertizadan o‘tkazish va uning saqlanishini ta’minlash;
Arxeologiya ashyolari davlat katalogini yuritish, arxeologiya ashyolarining davlatga topshirilishini tashkil qilish hamda fan, madaniyat va ta’lim muassasalariga biriktirib qo‘yish va tasarrufdan chiqarish;
arxeologiya merosi obyektlarini muhofaza qilish, halokat yoqasidagi arxeologiya obyektlarida zudlik bilan tadqiqot o‘tkazilishini ta’minlash, arxeologiya obyektlarini konservatsiyalash, muzeylashtirish hamda arxeologiya parklarini yaratish;
d) mamlakatimiz va xorijdagi madaniy boyliklar sohasida:
madaniy boyliklarni muhofaza qilish, shu jumladan ularning O‘zbekiston Respublikasi hududidan olib chiqilishi va uning hududiga olib kirilishi bo‘yicha sertifikat berish va realizatsiya qilishni muvofiqlashtirish, madaniy boyliklarni badiiy ekspertizadan o‘tkazish, ularning toifasini aniqlash, davlat reyestrini yuritish, sohada davlat nazoratini amalga oshirish;
mamlakatimiz tarixi va madaniyatiga oid xorijdagi madaniy boyliklarni aniqlash, ularning to‘liq ma’lumotlar bazasini yaratish, muntazam yangilab borish hamda asl yoki ko‘chirma nusxalari, foto va videotasvirlarini yurtimizga olib kelish;
ilmiy o‘rganish asosida olingan ilmiy ma’lumotlar va ularning tahliliy natijalariga oid bibliografik to‘plamlar, kataloglar,kitob-albomlar, axborot byulletenlari va boshqa matbaa mahsulotlarini turli tillarda nashr etish, ommaviy axborot vositalari va Internet tarmog‘ida keng yoritish, xalqaro va xorijiy tashkilotlar bilan hamkorlik qilish.'),
        (1332, 125, '2023-10-09 11:37:55', '2023-10-09 11:37:59', '3. Belgilansinki, Agentlik:
moddiy madaniy meros, muzeylar, arxeologiya, madaniy boyliklarning olib chiqilishi va olib kirilishi sohasida maxsus vakolatli davlat boshqaruv organi hisoblanadi;
o‘z vakolatlari doirasida qabul qilgan qarorlari davlat va xo‘jalik boshqaruvi organlari, mahalliy ijro etuvchi hokimiyat organlari, boshqa tashkilotlar va ularning mansabdor shaxslari, shuningdek, fuqarolar tomonidan bajarilishi majburiy hisoblanadi;
sohaga oid qonunchilik hujjatlarining buzilishiga yo‘l qo‘ymaslik yoki qonun buzilishlarini bartaraf qilish haqida tegishlicha yozma ravishda ogohlantirishlar va ijro etilishi majburiy bo‘lgan ko‘rsatmalar beradi;
qonunbuzilish holatlarini, ularga imkon beruvchi sabablar va shart-sharoitlarni bartaraf etish hamda aybdor mansabdor shaxslarga nisbatan tegishli choralar ko‘rish to‘g‘risida ko‘rib chiqilishi majburiy bo‘lgan taqdimnomalar kiritadi.'),
        (1333, 125, '2023-10-09 11:37:55', '2023-10-09 11:37:59', '4. Shunday tartib o‘rnatilsinki, unga muvofiq:
sohaga oid normativ-huquqiy hujjatlar loyihalari va moddiy madaniy merosning ko‘chmas mulk obyektlari joylashgan aholi punktlarining bosh rejalari Agentlik bilan kelishilishi majburiy hisoblanadi;
moddiy madaniy merosning ko‘chmas mulk obyektlarida, alohida muhofaza qilinadigan tarixiy-madaniy hududlarda, Umumjahon merosi ro‘yxatiga kiritilgan hududlarda va ularning qo‘riqlanadigan tegralarida obyektlarni qurish, buzish va obodonlashtirish ishlarining loyihalari Agentlik bilan majburiy tartibda kelishiladi. Bunda, Umumjahon merosi ro‘yxatiga kiritilgan hududlar (obyektlar) bo‘yicha loyihalar Agentlik tomonidan YUNESKO ishlari bo‘yicha Milliy komissiya orqali YUNESKOning Umumjahon merosi markazi bilan kelishiladi;
moddiy madaniy meros ko‘chmas mulk obyektlarining davlat reyestri Vazirlar Mahkamasi tomonidan tasdiqlanadi;
davlat ko‘chmas mulki hisoblangan moddiy madaniy merosning ko‘chmas mulk obyektlarini operativ boshqaruv huquqi asosida yoki tekin foydalanish huquqi asosida biriktirish O‘zbekiston Respublikasi Prezidenti va Vazirlar Mahkamasi qarori asosida amalga oshiriladi;
moddiy madaniy meros obyektiga yetkazilayotgan zarar haqidagi holat bo‘yicha xabardor bo‘lgan paytdan boshlab davlat nazoratining dastlabki natijalari 24 soat ichida Agentlik rasmiy sayti orqali jamoatchilikka ma’lum qilinishi shart;
asrashga doir ishlarni rejalashtirish va amalga oshirishning barcha bosqichlarini ilmiy-texnik tadqiq etish uchun har bir obyektning o‘ziga xosligi va holatidan kelib chiqib, Ilmiy-texnik tadqiqot guruhlari tuziladi, asrashga doir ishlarning loyiha-smeta hujjatlarida guruh a’zolari mehnatiga haq to‘lash nazarda tutiladi;
Davlat budjeti mablag‘lari hisobidan asrashga doir ishlar amalga oshiriladigan moddiy madaniy meros ko‘chmas mulk obyektlari manzilli ro‘yxati Ilmiy-texnik tadqiqot guruhlari va Agentlikning Ilmiy-ekspert kengashining ijobiy xulosalari asosida Bosh vazir o‘rinbosari — turizm va sport vaziri tomonidan tasdiqlanadi;
asrashga doir ishlarni amalga oshirish uchun Ilmiy-texnik tadqiqot guruhlari va Ilmiy-ekspert kengashining ijobiy xulosalariga asosan Agentlik tomonidan berilgan ruxsatnoma bo‘lishi shart, ushbu qoidaga rioya qilmagan holda berilgan ruxsatnoma haqiqiy hisoblanmaydi;
Ilmiy-ekspert kengashi va Ilmiy-texnik tadqiqot guruhlari a’zolariga davlat sud ekspertlari uchun qonunchilik hujjatlarida belgilangan huquq va majburiyatlar tatbiq etiladi. Ilmiy-ekspert kengashi xulosasi Agentlik uchun majburiy hisoblanadi.'),
        (1451, 137, '2024-05-29 17:31:43', '2024-05-29 17:31:43', '14. Ishchi organ:
Kengash faoliyatini tashkiliy-texnik jihatdan ta’minlashni amalga oshiradi;
ish rejasi loyihasini tayyorlaydi, Kengash muhokamasiga kiritadi va tasdiqlangan ish rejasi bajarilishini ta’minlaydi;
Kengash majlisiga zarur bo‘lgan materiallar tayyorlanishini ta’minlaydi va manfaatdor tashkilotlardan zarur materiallar va ekspert xulosalarini oladi;
Kengash majlisi kun tartibidagi masala bo‘yicha tahliliy materiallar tayyorlanishini tashkil etadi;
tushgan murojaatlarni ko‘rib chiqadi hamda Kengash vakolatiga kiradigan masalalar bo‘yicha qarorlar loyihalarini tayyorlaydi.'),
        (1334, 125, '2023-10-09 11:37:55', '2023-10-09 11:37:59', '5. Quyidagilar:
Turizm va sport vazirligi huzuridagi Madaniy meros agentligining tashkiliy tuzilmasi 1-ilovaga muvofiq;
Turizm va sport vazirligi huzuridagi Madaniy meros agentligi markaziy apparatining boshqaruv xodimlari umumiy cheklangan soni 65 nafardan iborat bo‘lgan tuzilmasi 2-ilovaga muvofiq tasdiqlansin.
Agentlikning Qoraqalpog‘iston Respublikasi, viloyatlar va Toshkent shahar boshqarmalari (keyingi o‘rinlarda — hududiy boshqarmalar) boshqaruv xodimlarining cheklangan soni 145 nafar etib belgilansin.
Belgilansinki, Agentlikning markaziy apparati va hududiy boshqarmalarning tuzilmalariga qo‘shimcha 30 ta shtat birligi davlat muzeylari xodimlari sonini maqbullashtirish hisobiga ajratiladi.
Bosh vazir o‘rinbosari — turizm va sport vaziri A. Abduhakimov manfaatdor vazirlik va idoralar bilan birgalikda ikki oy muddatda davlat muzeylari xodimlari sonining maqbullashtirilishini inobatga olib, davlat muzeylarining namunaviy shtatlar jadvalining belgilangan tartibda tasdiqlanishini ta’minlasin.'),
        (1335, 125, '2023-10-09 11:37:55', '2023-10-09 11:37:59', '6. Turizm va sport vaziriga:
Agentlikning markaziy apparati, hududiy boshqarmalar, Agentlik tizimidagi boshqa tashkilotlarning tuzilmasiga boshqaruv xodimlarining cheklangan umumiy soni va ish haqi fondi doirasida o‘zgartirishlar kiritish;
hududiy boshqarmalar tuzilmalari va nizomlarini tasdiqlash, shuningdek, ularda boshqaruv xodimlarining cheklangan sonini belgilash huquqi berilsin.'),
        (1336, 125, '2023-10-09 11:37:55', '2023-10-09 11:37:59', '7. Belgilansinki:
Agentlik direktorining maqomi mehnatga haq to‘lash, tibbiy va transport xizmati ko‘rsatish shart-sharoitlariga ko‘ra turizm va sport vazirining birinchi o‘rinbosariga tenglashtiriladi;
Agentlik direktori va uning o‘rinbosarlari turizm va sport vazirining taqdimnomasiga asosan Vazirlar Mahkamasi tomonidan lavozimga tayinlanadi va lavozimidan ozod etiladi;
Agentlik xodimi sifatidagi ish staji boshqa davlat organlariga, shu jumladan huquqni muhofaza qiluvchi va nazorat qiluvchi organlarga ishga o‘tish chog‘ida navbatdagi harbiy va maxsus unvonlar olish, shuningdek, harbiy va maxsus unvonlar uchun ustamalar hamda uzoq muddatli xizmat uchun qo‘shimcha haqlar hisoblash uchun ish stajiga qo‘shib hisoblanadi;
moddiy madaniy meros ko‘chmas mulk obyektlarida asrashga doir ishlarni rejalashtirish, amalga oshirish, muvofiqlashtirish va ilmiy-texnik nazorat qilish 3-ilovaga muvofiq sxema bo‘yicha amalga oshiriladi.'),
        (1337, 125, '2023-10-09 11:37:55', '2023-10-09 11:37:59', '8. Agentlikning budjetdan tashqari Madaniy meros jamg‘armasini (keyingi o‘rinlarda — Jamg‘arma) moliyalashtirish manbalari etib quyidagilar belgilansin:
Agentlik, uning hududiy boshqarmalari va davlat muzey-qo‘riqxonalariga tegishli moddiy madaniy merosning ko‘chmas mulk obyektlarini ijaraga berishdan tushgan mablag‘larning 90 foiz miqdori;
davlat mulki bo‘lgan boshqa tashkilotlarga tegishli moddiy madaniy merosning ko‘chmas mulk obyektlarini ijaraga berish va chiptalar sotilishidan tushgan pul mablag‘larining 50 foiz miqdori;
muzey-qo‘riqxonalarda chiptalar sotilishidan tushgan pul mablag‘larining 50 foiz miqdori va davlat muzeylarida chiptalar sotilishidan tushgan pul mablag‘larining 10 foiz miqdori;
madaniy boyliklarni badiiy ekspertizadan o‘tkazish, realizatsiya qilish uchun davlat xizmati ko‘rsatish, olib chiqish huquqini beruvchi sertifikat, Milliy muzey fondining davlat katalogida muzey ashyolari va muzey kolleksiyalari ro‘yxatdan o‘tkazilganligi to‘g‘risida guvohnoma berish hamda uning tarkibidan muzey ashyolari va muzey kolleksiyalarini chiqarish uchun to‘lovlar;
moddiy madaniy merosning ko‘chmas mulk obyektlarini davlat kadastriga kiritish va chiqarish, tarixiy-madaniy hududning pasportini ko‘rib chiqish va tasdiqlash, aholi punktlari bosh rejalarining loyihalarini ko‘rib chiqish va ularga xulosa berish uchun to‘lovlar;
moddiy madaniy merosning ko‘chmas mulk obyektlarini asrashga doir ishlar uchun ruxsatnoma va litsenziya berish, ilmiy-texnik nazorat qilish, shuningdek, tarixiy-madaniy va loyiha-smeta hujjatlarining tegishli ekspertizalari, restavrator-ustalarga malaka sertifikati berish uchun to‘lovlar;
moddiy madaniy merosning ko‘chmas mulk obyektlari, alohida muhofaza qilinadigan tarixiy-madaniy hududlarda, Umumjahon merosi ro‘yxatiga kiritilgan hududlar va ularning qo‘riqlanadigan tegralarida yer qazish, yer tuzish, qurilish, melioratsiya va boshqa xo‘jalik ishlarini o‘tkazishga oid loyihalar, merosga bo‘lgan ta’sirini baholash hisobotlarini ko‘rib chiqish uchun to‘lovlar;
moddiy madaniy meros obyektlarini muhofaza qilish va ulardan foydalanish qoidalarini buzganligi hamda nobud qilish, buzish yoki ularga shikast yetkazganligi uchun to‘lanadigan jarimalar, shuningdek, davlat mulki hisoblangan moddiy madaniy meros obyektlariga yetkazilgan zararni qoplash bo‘yicha mablag‘lar;
vakolatli organlar tomonidan Agentlik, uning tizimidagi tashkilotlar va davlat muzeylari, muzey-qo‘riqxonalari faoliyatini, shuningdek, moddiy madaniy meros ko‘chmas mulk obyektlarida asrashga doir ishlarni amalga oshirishni tekshirish natijalari bo‘yicha aniqlangan noqonuniy sarflangan budjet mablag‘larining, talon-toroj, rastratalar, kamomadlar summalarining Davlat budjetiga yo‘naltiriladigan qismi;
Jamg‘armaning vaqtincha bo‘sh turgan mablag‘larini joylashtirishdan olinadigan daromadlar, xalqaro moliya tashkilotlari va xorijiy davlatlarning grantlari va texnik ko‘maklashish mablag‘lari, shuningdek, qonunchilik hujjatlari bilan taqiqlanmagan boshqa tushumlar.'),
        (1338, 125, '2023-10-09 11:37:55', '2023-10-09 11:37:59', '9. Belgilansinki, Jamg‘arma mablag‘lari:
moddiy madaniy meros obyektlarini, shu jumladan arxeologiya merosi obyektlari, madaniy boyliklar hamda muzey ashyolari va kolleksiyalarini muhofaza qilish, konservatsiyalash, restavratsiya qilish, tuzatish, ta’mirlash, hozirgi zamonda foydalanishga moslashtirish, shuningdek, ular bilan bog‘liq ilmiy va ilmiy-texnik tadqiqotlar, loyihalash hamda ishlab chiqarish ishlarini amalga oshirish;
moddiy madaniy meros obyektlarining davlat kadastrini, arxeologiya ashyolari, milliy muzey fondining davlat kataloglari va pasportlarini yuritish, muzeylar faoliyati va arxeologiya tadqiqotlarini qo‘llab-quvvatlash, targ‘ibot va ommalashtirish ishlarini tashkil etish;
alohida muhofaza qilinadigan tarixiy-madaniy hududlar, Umumjahon merosi ro‘yxatiga kiritilgan hududlar va tarixiy mahallalarni muhofaza qilish;
Agentlik va hududiy boshqarmalarning moddiy-texnik bazasini mustahkamlash, axborot-kommunikatsiya texnologiyalarini joriy etish, elektron xizmatlarni rivojlantirish va raqamlashtirish, sohada huquqbuzarliklarning oldini olish, ularga samarali qarshi kurashish bo‘yicha ishlarni tashkil etish;
ekspertlarni, jumladan xorijiy mutaxassislar, Ilmiy-ekspert kengashi, Ilmiy-texnik tadqiqot guruhlari a’zolari mehnatiga haq to‘lash, jamoatchi inspektorlar, Agentlik va hududiy boshqarmalarning xodimlarini moddiy rag‘batlantirish, Agentlik faoliyati bilan bog‘liq masalalar bo‘yicha mutaxassislarni fuqarolik-huquqiy shartnoma asosida jalb etish;
qonunchilik hujjatlari bilan belgilangan Agentlik faoliyati bilan bog‘liq boshqa tadbirlarni amalga oshirish uchun sarflanadi.
Jamg‘arma mablag‘larini shakllantirish va ulardan foydalanish tartibi Vazirlar Mahkamasi tomonidan belgilanadi.'),
        (1339, 125, '2023-10-09 11:37:55', '2023-10-09 11:37:59', '10. Quyidagilar:
2021-2022-yillarda moddiy madaniy meros obyektlarini muhofaza qilish, muzeylar faoliyatini rivojlantirish va sohani raqamlashtirish bo‘yicha “yo‘l xaritasi” 4-ilovaga muvofiq;
2021-2022-yillarda moddiy madaniy meros ko‘chmas mulk obyektlarini qayta tarixiy-madaniy ekspertizadan o‘tkazish, ularning raqamlashtirilgan pasporti va katalogini ishlab chiqishning maqsadli ko‘rsatkichlari 5-ilovaga muvofiq;
2021 — 2023-yillarda moddiy madaniy meros ko‘chmas mulk obyektlarining qo‘riqlanadigan tegralarini belgilash va raqamlashtirishning maqsadli ko‘rsatkichlari 6-ilovaga muvofiq;
2021 — 2023-yillarda davlat muzeylaridagi muzey ashyolari va muzey kolleksiyalarini elektron identifikatsiya qilish maqsadli ko‘rsatkichlari 7-ilovaga muvofiq tasdiqlansin.
Belgilab qo‘yilsinki, ushbu qaror bilan tasdiqlangan maqsadli ko‘rsatkichlar 2021-yilda Jamg‘arma va madaniy meros obyektlarini asrash uchun ajratilgan Davlat budjeti mablag‘lari hisobidan amalga oshiriladi, 2022-2023-yillarda Davlat budjeti parametrlarini shakllantirishda nazarda tutiladi.'),
        (1340, 125, '2023-10-09 11:37:55', '2023-10-09 11:37:59', '11. Qoraqalpog‘iston Respublikasi Vazirlar Kengashi, viloyatlar va Toshkent shahar hokimliklari Agentlik, Fanlar akademiyasi, Qurilish vazirligi, Kadastr agentligi bilan birgalikda har yarim yil yakunida o‘z hududlarida joylashgan moddiy madaniy meros obyektlari va madaniy boyliklarni aniqlash, hisobga olish, muhofaza qilish, asrash va ulardan oqilona foydalanish, muzey ashyolari va muzey kolleksiyalarining but saqlanishi holati haqida xalq deputatlari mahalliy Kengashlariga axborot berib borsin.
Vazirlar Mahkamasi har chorakda mazkur qaror bilan tasdiqlangan “yo‘l xaritasi” va maqsadli ko‘rsatkichlarning to‘liq bajarilishini tanqidiy tahlil qilib, Agentlik, tegishli vazirlik va idoralar hamda Qoraqalpog‘iston Respublikasi Vazirlar Kengashi, viloyatlar va Toshkent shahar hokimliklari rahbarlari faoliyatiga baho berib borsin.'),
        (1341, 125, '2023-10-09 11:37:55', '2023-10-09 11:37:59', '12. Shunday tartib o‘rnatilsinki, unga muvofiq moddiy madaniy meros obyektlari va muzeylar sohasidagi davlat xizmatlari 8-ilovaga muvofiq 2023-yil 1-yanvardan boshlab elektron shaklda ko‘rsatiladi. Bunda, ushbu ro‘yxatga kiritilgan davlat xizmatlarini ko‘rsatishda:
Davlat xizmatlari markazlari va Yagona interaktiv davlat xizmatlari portali orqali yoki arizachining o‘zi bevosita Agentlikning tegishli tuzilmasiga murojaat qilish tartibi joriy etiladi. Arizachining o‘zi bevosita murojaat qilganda uning murojaati Agentlik xodimi tomonidan maxsus elektron tizimda ro‘yxatdan o‘tkazilishi shart;
davlat xizmatlari uchun to‘lovlarni elektron shaklda amalga oshirish orqali kvitansiya talab qilish bekor qilinadi;
Agentlik mas’ul xodimlari va fuqarolik-huquqiy shartnoma asosida jalb etilgan mutaxassislar, ekspertlar tomonidan tegishli ko‘chmas mulk va ko‘char ashyolar shaxsan ekspertizadan o‘tkaziladi;
ekspertiza o‘tkaziladigan manzil, vaqt va ma’lumot uchun telefon raqamlari hamda davlat xizmati ko‘rsatish natijasi haqida SMS yuborish orqali arizachi xabardor qilinadi;
davlat xizmatini ko‘rsatish natijasida beriladigan hujjatlar QR-kod (matrik shtrixli kod) qo‘yish orqali beriladi.'),
        (1342, 125, '2023-10-09 11:37:55', '2023-10-09 11:37:59', '13. Belgilansinki, moddiy madaniy meros obyektlari, muzey, sport majmualari va boshqa namoyish obyektlari (keyingi o‘rinlarda — namoyish obyektlari):
ro‘yxati O‘zbekiston Respublikasi Prezidentining 2021-yil 6-apreldagi PF-6199-son Farmoni bilan tuzilgan Turizm, sport, madaniy meros, transport logistikasi va xizmatlar sohalarida davlat-xususiy sheriklik hamda raqamlashtirishni rivojlantirish masalalari bo‘yicha muvofiqlashtiruvchi kengash tomonidan tasdiqlanadi;
ro‘yxatiga kiritilgan namoyish obyektlariga kirish-chiqish joylarida 2021-yil 1-oktabrga qadar elektron turniketlar yoki tashrif buyuruvchilarni avtomatik hisobga olishga mo‘ljallangan boshqa moslamalar “Raqamlashtirish va avtomatlashtirish markazi” DUK tomonidan, jumladan, davlat-xususiy sheriklik asosida o‘rnatilishi hamda ular tegishli yagona elektron tizimga integratsiya qilinishi shart.
2022-yil 1-yanvardan boshlab namoyish obyektlari ro‘yxatiga kiritilgan namoyish obyektlarida chiptalar savdosini tegishli yagona elektron tizimga integratsiya qilmagan holda amalga oshirish nazorat-kassa texnikasini va hisob-kitob terminallarini qo‘llash tartibini buzish deb baholanadi.'),
        (1343, 125, '2023-10-09 11:37:55', '2023-10-09 11:37:59', '14. Turizm va sport vazirligi (A.A. Abduxakimov) ikki oy muddatda:
tizimga integratsiya qilinishi lozim bo‘lgan namoyish obyektlari ro‘yxatini shakllantirsin hamda belgilangan tartibda tasdiqlangan ro‘yxatning e’lon qilinishini ta’minlasin;
qonunchilik hujjatlariga mazkur qarordan kelib chiqadigan o‘zgartirish va qo‘shimchalar to‘g‘risida Vazirlar Mahkamasiga takliflar kiritsin.'),
        (1344, 125, '2023-10-09 11:37:55', '2023-10-09 11:37:59', '15. O‘zbekiston Respublikasi Prezidentining 2018-yil 19-dekabrdagi “Moddiy madaniy meros obyektlarini muhofaza qilish sohasidagi faoliyatni tubdan takomillashtirish chora-tadbirlari to‘g‘risida”gi PQ-4068-son qarori o‘z kuchini yo‘qotgan deb hisoblansin.'),
        (1345, 125, '2023-10-09 11:37:55', '2023-10-09 11:37:59', '16. Mazkur qarorning ijrosini samarali tashkil etishga mas’ul va shaxsiy javobgar etib Turizm va sport vazirligi huzuridagi Madaniy meros agentligi direktori, Qoraqalpog‘iston Respublikasi Vazirlar Kengashi Raisi, viloyatlar va Toshkent shahar hokimlari belgilansin.
Qaror ijrosini muhokama qilib borish, ijro uchun mas’ul idoralar faoliyatini muvofiqlashtirish va nazorat qilish O‘zbekiston Respublikasi Prezidentining maslahatchisi A.A. Abduvaxitov va O‘zbekiston Respublikasi Bosh vazirining o‘rinbosari — turizm va sport vaziri A.A. Abduxakimov zimmasiga yuklansin.
Amalga oshirilayotgan chora-tadbirlar natijadorligi yuzasidan 2021-yil 1-oktabr va 2022-yil 1-iyulga qadar O‘zbekiston Respublikasi Prezidentiga axborot berilsin.'),
        (1346, 126, '2023-10-09 11:40:53', '2023-10-09 11:40:53', 'Hozirgi kunda qishloq xo‘jaligida 20 mln gektardan ortiq, shu jumladan 3,2 mln gektar sug‘oriladigan ekin yer maydonlaridan foydalanib, aholining ehtiyoji uchun oziq-ovqat mahsulotlari, iqtisodiyot tarmoqlari uchun zarur xomashyo yetishtirilmoqda.
Sug‘oriladigan maydonlarning unumdorligini oshirish, meliorativ holati va suv ta’minotini yaxshilash maqsadida davlat dasturlari doirasida keng ko‘lamli irrigatsiya va melioratsiya tadbirlari amalga oshirilmoqda.
Natijada 2008 — 2017-yillar mobaynida 1,7 mln gektardan ortiq sug‘oriladigan maydonlarning suv ta’minoti hamda 2,5 mln gektar maydonlarning meliorativ holatining yaxshilanishiga erishildi.
Biroq, global iqlim o‘zgarishi natijasida so‘nggi yillarda davriy ravishda kuzatilayotgan suv tanqisligi va ichki irrigatsiya tarmoqlarining asosiy qismi yaroqsiz holatga kelganligi sug‘oriladigan ekin yerlarining meliorativ holati yomonlashishiga va yillar davomida foydalanishdan chiqib ketishiga olib kelgan.
Respublikada yillar davomida irrigatsiya va melioratsiya holati yomonlashuvi natijasida foydalanishdan chiqib ketgan yerlarni bosqichma-bosqich qayta foydalanishga kiritish, yer osti suv zaxiralaridan samarali foydalanish, suv tejovchi texnologiyalarni joriy etish hamda ichki irrigatsiya tarmoqlarini rekonstruksiya qilish orqali suv yo‘qotilishini kamaytirish, shuningdek, bu ishlarda salohiyatli investorlar ishtirokini ta’minlash maqsadida:'),
        (1347, 126, '2023-10-09 11:40:53', '2023-10-09 11:40:53', '1. Quyidagilar:
Qishloq xo‘jaligida yer va suv resurslaridan samarali foydalanish konsepsiyasi (keyingi o‘rinlarda — Konsepsiya) 1-ilovaga muvofiq;
Qishloq xo‘jaligida yer va suv resurslaridan samarali foydalanish konsepsiyasini amalga oshirish bo‘yicha “yo‘l xaritasi” 2-ilovaga muvofiq;
2020 — 2030-yillarda qishloq xo‘jaligi yerlaridan foydalanish samaradorligini oshirish bo‘yicha amalga oshiriladigan chora-tadbirlarning prognoz ko‘rsatkichlari 3-ilovaga muvofiq;
2019 — 2022-yillar davomida qishloq xo‘jaligi yerlarida suv tejovchi texnologiyalarni joriy qilishning prognoz ko‘rsatkichlari 4-ilovaga muvofiq tasdiqlansin.'),
        (1348, 126, '2023-10-09 11:40:53', '2023-10-09 11:40:53', '2. Qishloq xo‘jaligida yer va suv resurslaridan samarali foydalanish konsepsiyasini amalga oshirish doirasida xalqaro moliya institutlari bilan hamkorlik qilish va davlat-xususiy sheriklikni joriy etish bo‘yicha ishchi guruhi (keyingi o‘rinlarda — Ishchi guruhi) 5-ilovaga muvofiq tarkibda tasdiqlansin.
Ishchi guruhi (J.A. Qo‘chqorov, A.J. Vaxabov, J.A. Xodjayev)ga:
xalqaro moliya institutlarini jalb qilgan holda eksperiment tariqasida 2019-yilda Namangan viloyatining Pop va Mingbuloq tumanlarida Konsepsiyani amalga oshirish tajribasini yaratish;
eksperiment natijalari asosida 2019-yil 1-noyabrga qadar Konsepsiyani amalga oshirish tajribasini boshqa hududlarda joriy etish, shu jumladan yer va suvdan foydalanish sohasida davlat-xususiy sheriklikning aniq shartlarini, qo‘shimcha imtiyozlar, alohida imtiyozli kredit liniyalarini hamda foydalanishga kiritilgan yer va suvdan foydalanish tartibini nazarda tutuvchi Hukumat qarori loyihasini ishlab chiqish va O‘zbekiston Respublikasi Vazirlar Mahkamasiga kiritish topshirilsin;
zaruratga ko‘ra, mazkur Farmonning 2 — 4-ilovalarida belgilangan muddatlar va hududlarni o‘zgartirish hamda ularga qo‘shimchalar kiritish huquqi berilsin.'),
        (1470, 138, '2024-05-29 17:32:26', '2024-05-29 17:32:26', '10. Kengash a’zolari:
Kengash majlislarida ko‘rib chiqiladigan masalalarni muhokama qilish va hal etishda ishtirok etadilar;
shaharsozlikning dolzarb masalalari bo‘yicha Kengashga takliflar kiritadilar;
Kengash muhokama qiladigan masalalar bo‘yicha xulosalar beradilar.
Kengash a’zolari Kengashdagi faoliyatini jamoatchilik asosida amalga oshiradilar.'),
        (1350, 126, '2023-10-09 11:40:53', '2023-10-09 11:40:53', '4. Konsepsiyani amalga oshirish doirasida yer maydonlarini foydalanishga kiritishda:
yerdan foydalanuvchilarga qishloq xo‘jaligi ekinlarini mustaqil joylashtirish, erga ishlov bermasdan ekin ekish va yerlarni ikkilamchi ijaraga berish huquqi beriladi;
mazkur yer maydonlari maqbullashtirilmaydi, yer uchastkasi yoki uning bir qismi jamoat ehtiyojlari uchun olib qo‘yilishi O‘zbekiston Respublikasining “Yer uchastkalarini kompensatsiya evaziga jamoat ehtiyojlari uchun olib qo‘yish tartib-taomillari to‘g‘risida”gi Qonunida belgilangan tartibda amalga oshiriladi;
ushbu yer maydonlarida barpo etiladigan infratuzilma obyektlarini (dala shiypon, mahsulotlar saqlanadigan omborxonalar, sug‘orish inshootlari va boshqalar) joylashtirishga ruxsat beriladi;
yerdan foydalanuvchilarning ehtiyojlari uchun olib kelinadigan va respublikada ishlab chiqarilmaydigan xom-ashyo, materiallar, texnika, asbob-uskunalar, ehtiyot qismlar Vazirlar Mahkamasi tomonidan belgilangan tartibda bojxona to‘lovlari (qo‘shilgan qiymat solig‘idan tashqari) to‘lashdan ozod qilinadi;
investitsiyaviy shartnoma yoki davlat-xususiy sheriklik to‘g‘risidagi bitimda nazarda tutilgan hollarda O‘zbekiston Respublikasi Davlat budjeti mablag‘lari hisobidan har bir gektar qishloq xo‘jaligiga mo‘ljallangan yerlarni foydalanishga kiritish bilan bog‘liq xarajatlarning 50 foizi, lekin bazaviy hisoblash miqdorining 50 barobaridan oshmagan qismi qoplab beriladi;
investitsiyaviy shartnoma yoki davlat-xususiy sheriklik to‘g‘risidagi bitimda nazarda tutilgan hollarda Tadbirkorlik faoliyatini qo‘llab-quvvatlash davlat jamg‘armasi mablag‘lari hisobidan yerlarning suv ta’minoti tizimlarini qurish, rekonstruksiya qilish uchun jalb etilgan kreditlarning bir gektar hisobiga 40 million so‘mdan oshmaydigan qismiga tijorat banklari tomonidan belgilangan foiz stavkasining 5 foizlik punkti qoplab beriladi;
yer maydonlari investitsiyaviy shartnoma yoki davlat-xususiy sheriklik to‘g‘risidagi bitim imzolanganidan so‘ng qonun hujjatlarida belgilangan tartibda ajratib beriladi.'),
        (1351, 126, '2023-10-09 11:40:53', '2023-10-09 11:40:53', '5. Shunday tartib o‘rnatilsinki, unga muvofiq:
sug‘oriladigan qishloq xo‘jaligi yerlari maxsus muhofazaga olinib, ushbu yerlarni qishloq xo‘jaligidan boshqa maqsadlar uchun, shu jumladan sanoat va fuqarolik obyektlari (binolar va inshootlar) qurilishi uchun, shuningdek, unumdor tuproq talab etilmaydigan ilg‘or texnologiyalarning (gidroponika va boshqalar) yangi usullari qo‘llaniladigan issiqxonalar qurilishi uchun ajratishga yo‘l qo‘yilmaydi;
qishloq xo‘jaligidan boshqa maqsadlarda foydalanish uchun olib qo‘yiladigan sug‘oriladigan yerlar uchun kompensatsiya to‘lovi, olib qo‘yilayotgan yer maydonlari joylashgan joylarni hisobga oluvchi koeffitsiyentlar qo‘llanilmagan holda, xuddi shunday yangi yerlarni o‘zlashtirish uchun talab etiladigan mablag‘ning 10 barobari, lalmi yerlar va sug‘orilmaydigan ko‘p yillik daraxtzorlar egallagan yerlar, pichanzorlar va yaylovlar holatini tubdan yaxshilash bilan bog‘liq xarajatlarning 20 barobari miqdorida hisoblanadi.
Bunda sug‘oriladigan yerlar uchun kompensatsiya to‘lovining 90 foizi, lalmi yerlar va sug‘orilmaydigan ko‘p yillik daraxtzorlar egallagan yerlar, pichanzorlar va yaylovlar uchun kompensatsiya to‘lovining 95 foizi Respublika budjetiga hamda mos ravishda 10 va 5 foizi Qoraqalpog‘iston Respublikasi, viloyatlar va Toshkent shahar yer resurslari va davlat kadastri boshqarmalarining maxsus hisob raqamlariga o‘tkaziladi.
Ushbu me’yor o‘zboshimchalik bilan yerlarning qishloq xo‘jaligi muomalasidan chiqishiga sabab bo‘lgan hollarda yetkazilgan zararni hisoblashda ham qo‘llaniladi hamda hisoblangan mablag‘larning 95 foizi Respublika budjetiga va 5 foizi O‘zbekiston Respublikasi Vazirlar Mahkamasi huzuridagi Agrosanoat majmui ustidan nazorat qilish inspeksiyasi hamda uning hududiy inspeksiyalarining rivojlantirish jamg‘armasiga undiriladi;
sug‘oriladigan yerlar hisobidan bog‘dorchilik, uzumchilik va boshqa ko‘p yillik daraxtzorlar hamda yengil tipdagi issiqxonalarni tashkil etish O‘zbekiston Respublikasi Qishloq xo‘jaligi vazirligi xulosasiga asosan amalga oshiriladi;
yangi fermer xo‘jaliklari va suv iste’molchilari uyushmalari suvdan foydalanish joylarida suvni boshqarish va hisobga olish vositalari bilan majburiy tarzda jihozlash sharti bilan tashkil etiladi;
yer maydonlarini foydalanishga kiritish suvni tejovchi texnologiyalardan (tomchilatib, yomg‘irlatib, tuproq ostidan tomchilab sug‘orish va boshqalardan) foydalangan holda amalga oshiriladi.'),
        (1352, 126, '2023-10-09 11:40:53', '2023-10-09 11:40:53', '6. O‘zbekiston Respublikasi Moliya vazirligi:
a) mazkur Farmonning 3-ilovasida belgilangan qishloq xo‘jaligida foydalanilmayotgan yerlarni foydalanishga kiritishda irrigatsiya va melioratsiya obyektlarini qurish va rekonstruksiya qilish uchun talab etilayotgan mablag‘lar, 2019 — 2029-yillardagi Investitsiya dasturi doirasida davlat budjetidan melioratsiya obyektlarini qurish va rekonstruksiya qilish uchun ajratiladigan mablag‘larning 5 foizi miqdorida qo‘shimcha ravishda yo‘naltirsin;
b) O‘zbekiston Respublikasi Davlat geologiya va mineral resurslar qo‘mitasining hisob-kitoblari asosida “Mineral xom-ashyo bazasini rivojlantirish va qayta to‘ldirish” Dasturida ko‘zda tutiladigan qishloq xo‘jaligi ekinlarini sug‘orishga yaroqli bo‘lgan yer osti suvlari zaxirasini aniqlash ishlarini moliyalashtirish uchun davlat budjeti hisobidan mablag‘ ajratsin;
v) Suv xo‘jaligi vazirligi va Qishloq xo‘jaligi vazirligi bilan birgalikda ikki oy muddatda:
qishloq xo‘jaligida foydalanilmayotgan yerlarni foydalanishga kiritishda irrigatsiya va melioratsiya obyektlarini qurish va rekonstruksiya qilish bilan bog‘liq xarajatlarni qoplab berish tartibini;
yer maydonlari egalarining tuproq unumdorligini saqlash va oshirishni rag‘batlantiruvchi mexanizmlarni nazarda tutgan holda tabaqalashtirilgan soliq imtiyozlarini joriy qilish bo‘yicha takliflarni O‘zbekiston Respublikasi Vazirlar Mahkamasiga kiritsin;
bir oy muddatda ilg‘or xalqaro tajribalarni inobatga olgan holda, yerdan foydalanish sohasida davlat-xususiy sheriklik to‘g‘risidagi bitimlarning namunaviy shakllarini ishlab chiqsin.'),
        (1353, 126, '2023-10-09 11:40:53', '2023-10-09 11:40:53', '7. O‘zbekiston Respublikasi Qishloq xo‘jaligi vazirligi:
a) Yer resurslari, geodeziya, kartografiya va davlat kadastri davlat qo‘mitasi bilan birgalikda 2019-yil 1-oktabrga qadar qishloq xo‘jaligiga mo‘ljallangan yer maydonlarini ikkilamchi ijaraga berishni nazarda tutuvchi qonun hujjatlariga o‘zgartirish kiritish bo‘yicha O‘zbekiston Respublikasining qonuni loyihasini Vazirlar Mahkamasiga kiritsin;
b) sug‘oriladigan va lalmi yerlar, tog‘ va tog‘oldi hamda cho‘l-yaylov hududlarining tuproq unumdorligini oshirish, suv va boshqa tabiiy resurslardan samarali foydalanish bo‘yicha ilmiy tadqiqotlar olib borilishini;
zamonaviy usullar asosida mamlakat oziq-ovqat ta’minoti barqarorligiga erishish uchun yangi serhosil qishloq xo‘jaligi ekinlari, ularning birlamchi urug‘chiligini yaratish bo‘yicha ilmiy tadqiqotlarni tashkil etilishini va hayotga tatbiq etilishini ta’minlasin;
v) Farmonda ko‘rsatilgan vazifalar ijrosini to‘liq va o‘z vaqtida amalga oshirilishini ta’minlash maqsadida vazirlik tarkibida qo‘shimcha 4 ta shtat birligidan iborat mas’ul boshqarma tashkil etsin.'),
        (1354, 126, '2023-10-09 11:40:53', '2023-10-09 11:40:53', '8. Qoraqalpog‘iston Respublikasi Vazirlar Kengashi va viloyatlar hokimliklari 2020-yil 1-yanvardan boshlab:
Qishloq xo‘jaligi vazirligi, Suv xo‘jaligi vazirligi, Yer resurslari, geodeziya, kartografiya va davlat kadastri davlat qo‘mitasi, Davlat geologiya va mineral resurslar qo‘mitasi bilan birgalikda har yili 1-noyabrga qadar mazkur Farmonning 3-ilovasida tasdiqlangan maydonlarning keyingi yilda qayta tiklanadigan va foydalanishga kiritiladigan yer maydonlari manzilli ro‘yxatini shakllantirib, tasdiqlash uchun O‘zbekiston Respublikasi Vazirlar Mahkamasiga kiritib borsin;
paxta va g‘alla yetishtirish rentabelligi past bo‘lgan maydoni umumiy maydonning 50 foizdan ortiq bo‘lgan tumanlarning barcha ekin maydonlarini irrigatsiya va melioratsiya tarmoqlarini qurish, rekonstruksiya qilish sharti bilan paxta va g‘allani to‘liq qayta ishlashni tashkil etuvchi O‘zbekiston Respublikasi rezidenti bo‘lgan investorlarga berish yuzasidan takliflar kiritsin;
ushbu tumanlarda tashkil etiladigan paxta va g‘allachilik yo‘nalishidagi investorlarga mazkur Farmonning 4-bandida nazarda tutilgan imtiyozlar qo‘llanilsin.'),
        (1355, 126, '2023-10-09 11:40:53', '2023-10-09 11:40:53', '9. O‘zbekiston Respublikasi Vazirlar Mahkamasi bir oy muddatda Hukumat bo‘ysunuvida qishloq xo‘jaligi yerlaridan maqsadli va samarali foydalanish'),
        (1356, 126, '2023-10-09 11:40:53', '2023-10-09 11:40:53', '10. O‘zbekiston Respublikasi Oliy Majlis palatalariga:
Konsepsiya va uni amalga oshirish bo‘yicha “Yo‘l xaritasi”ni o‘z vaqtida va samarali ijro etilishi ustidan parlament va jamoatchilik nazoratini o‘rnatish;
amalga oshirilgan ishlar to‘g‘risida vazirlik va idoralar hamda Qoraqalpog‘iston Respublikasi Vazirlar Kengashi, viloyatlar hokimlarining axborotlarini har chorakda Senat, Qonunchilik palatasi va tegishlicha Qoraqalpog‘iston Respublikasi Jo‘qorg‘i Kengesi hamda xalq deputatlari viloyat Kengashlarining majlislarida eshitib borilishini ta’minlash tavsiya etilsin.'),
        (1552, 141, '2024-05-29 17:38:28', '2024-05-29 17:38:28', '1. Mazkur Nizom O‘zbekiston Respublikasi Qurilish va uy-joy kommunal xo‘jaligi vazirligi faoliyatini qo‘llab-quvvatlash jamg‘armasi (keyingi o‘rinlarda — Jamg‘arma) mablag‘larini shakllantirish va ulardan foydalanish tartibini belgilaydi.'),
        (1357, 126, '2023-10-09 11:40:53', '2023-10-09 11:40:53', '11. O‘zbekiston Respublikasi Bosh vazirining agrar va oziq-ovqat sohalarini rivojlantirish masalalari bo‘yicha maslahatchisi A.J. Vaxabov zimmasiga — Farmonning 3 va 4-ilovalaridagi prognoz ko‘rsatkichlarni, Konsepsiya va uni amalga oshirish bo‘yicha “Yo‘l xaritasi”ni o‘z vaqtida va samarali ijro etilishini monitoring qilish va zaruriy choralarni ko‘rib borish;
O‘zbekiston Respublikasi qishloq xo‘jaligi vaziri J.A. Xodjayev, Qoraqalpog‘iston Respublikasi Vazirlar Kengashi va viloyatlar hokimlari zimmasiga — Konsepsiya va uni amalga oshirish bo‘yicha “Yo‘l xaritasi”ning o‘z vaqtida va samarali ijro etilishini ta’minlash hamda yuzaga keladigan muammolarni bartaraf etish yuzasidan O‘zbekiston Respublikasi Vazirlar Mahkamasiga takliflar kiritish;
O‘zbekiston Respublikasi suv xo‘jaligi vaziri Sh.R. Xamrayev, Qoraqalpog‘iston Respublikasi Vazirlar Kengashi va viloyatlar hokimlari zimmasiga — Farmonning 4-ilovasidagi prognoz ko‘rsatkichlari ijrosini ta’minlash, foydalanishga kiritilgan maydonlarda suv tejovchi texnologiyalarni qo‘llash, irrigatsiya tarmoqlarida suv yo‘qotilishini kamaytirish, yerlarning meliorativ holatini yaxshilash, qo‘shimcha suv omborlarini qurish;
O‘zbekiston Respublikasi investitsiya va tashqi savdo vaziri S.O‘. Umurzoqov zimmasiga — Farmonning 3 va 4-ilovalaridagi vazifalarni amalga oshirish maqsadida xalqaro moliya institutlari va chet el investorlari mablag‘larini jalb qilish bo‘yicha javobgarlik yuklansin.'),
        (1358, 126, '2023-10-09 11:40:53', '2023-10-09 11:40:53', '12. O‘zbekiston Milliy teleradiokompaniyasi'),
        (1359, 126, '2023-10-09 11:40:53', '2023-10-09 11:40:53', '13. O‘zbekiston Respublikasi Qishloq xo‘jaligi vazirligi Adliya vazirligi'),
        (1360, 126, '2023-10-09 11:40:53', '2023-10-09 11:40:53', '14. Mazkur Farmonning ijrosini nazorat qilish O‘zbekiston Respublikasining Bosh vaziri A.N. Aripov'),
        (1361, 127, '2023-10-09 11:42:07', '2023-10-09 11:42:07', 'O‘zbekiston Respublikasi bo‘yicha jami yerlar 44 892,4 ming gektarni tashkil etib, yerlardan foydalanish maqsadi va tartibiga ko‘ra 8 ta toifaga bo‘linadi, jumladan, qishloq xo‘jaligiga mo‘ljallangan yerlar; aholi punktlarining yerlari; sanoat, transport, aloqa, mudofaa va boshqa maqsadlarga mo‘ljallangan yerlar; tabiatni muhofaza qilish, sog‘lomlashtirish va rekreatsiya maqsadlariga mo‘ljallangan yerlar; tarixiy-madaniy ahamiyatga molik yerlar; o‘rmon fondi yerlari; suv fondi yerlari; zaxira yerlar.
Qishloq xo‘jaligiga mo‘ljallangan yerlar unumdor yerlarga taalluqli bo‘lib, umummilliy boylik, qishloq xo‘jaligi mahsulotlarini ishlab chiqarish va mamlakat oziq-ovqat xavfsizligini ta’minlashning asosiy vositasi hisoblanadi.
Qishloq xo‘jaligiga mo‘ljallangan yerlarning umumiy maydoni 20 236,3 ming gektarni, shundan haydaladigan yerlar 3 988,5 ming gektarni, ko‘p yillik daraxtzorlar 383,1 ming gektarni, bo‘z yerlar 76 ming gektarni, pichanzor va yaylovlar 11 028,3 ming gektarni, boshqa yerlar 4 760,4 ming gektarni tashkil qiladi.
Keyingi yillarda mamlakatimizda yer va suv munosabatlarini takomillashtirish, qishloq xo‘jaligiga mo‘ljallangan yer maydonlarini maqbullashtirish va ularni ajratishning soddalashtirilgan tartibini qo‘llash, er-suv resurslaridan foydalanishda zamonaviy bozor mexanizmlari, innovatsion va resurs tejovchi texnologiyalarni joriy qilish, past hosilli paxta va g‘alla maydonlarini qisqartirish hisobiga yuqori daromadli, eksportbop mahsulotlar yetishtirish bo‘yicha tizimli choralar amalga oshirilmoqda.
Shu bilan birga, respublika aholisi sonining yuqori sur’atlar bilan o‘sib borishi, qishloq xo‘jaligi yerlarining boshqa toifaga o‘tkazilishi va global iqlim o‘zgarishi ta’sirining keskinlashuvi oqibatida oxirgi 15 yilda aholi jon boshiga to‘g‘ri keladigan sug‘oriladigan yer maydonlari o‘lchami 24 foizga (0,23 gektardan 0,16 gektargacha), o‘rtacha yillik suv ta’minoti darajasi esa 3 048 metr kubdan 158,9 metr kubgacha qisqardi.
Uzoq yillar davomida qishloq xo‘jaligi yerlaridan nooqilona foydalanish natijasida tuproqning tabiiy unumdorligi va ekinlar hosildorligi pasayib, yetishtirilgan mahsulot sifati yomonlashmoqda, atrof muhit ifloslanishi ortib bormoqda.
Jumladan, sug‘oriladigan ekin yerlaridagi tuproqlarning 93 foizida harakatchan fosfor miqdori, 68,3 foizida almashuvchan kaliy miqdori, 79,3 foizida gumus (chirindi) miqdori o‘rtachadan past darajaga tushib qolgan.
Milliy daromadi O‘zbekiston bilan deyarli bir xil bo‘lgan mamlakatlarda qishloq xo‘jaligi ehtiyojlari uchun davlat budjetining 4-5 foizi yo‘naltiriladi yoki YIMga nisbatan rivojlanayotgan mamlakatlarda 1 foizdan ortiqroqni, daromadi yuqori bo‘lgan davlatlarda esa 1 foizdan kamroqni tashkil etadi.
Qishloq xo‘jaligida o‘rtacha yillik suv sarfi 45 696 mln metr kub yoki iqtisodiyot tarmoqlarida iste’mol qilingan suv resurslarining 90 foizini tashkil etib, yuqoriligicha qolmoqda.
Yer va suv resurslari tobora tanqis bo‘lib borayotgan sharoitda qishloq xo‘jaligi ekinlarini joylashtirishda ekinlarni iqtisodiy samaradorligi va bozor konyunkturasi inobatga olinmasdan hamda intensiv qishloq xo‘jaligi joriy etilmaganligi sababli qishloq xo‘jaligi ishlab chiqarish hajmi pastligicha qolmoqda.
Jumladan, rivojlangan davlatlarda 1 metr kub suv bilan 4-6 AQSh dollarlik mahsulot yetishtirilayotgan bo‘lsa, respublikamizda bu ko‘rsatkich 0,15 AQSh dollarini tashkil etmoqda.
Irrigatsiya va melioratsiya tadbirlarini amalga oshirish yirik kapital qo‘yilmalar talab etishi, ushbu maqsadlar uchun yo‘naltiriladigan budjet mablag‘lari hajmining cheklanganligi, to‘g‘ridan-to‘g‘ri investitsiyalarni, shu jumladan davlat-xususiy sheriklik shartlari asosida jalb qilish masalasiga e’tibor qaratilmayotganligi oqibatida qishloq xo‘jaligi yerlarining foydalanishdan chiqib ketishi, hududlarning resurs va ishlab chiqarish salohiyatidan nooqilona foydalanish kabi holatlar yuzaga kelmoqda, bu esa o‘z navbatida mamlakat oziq-ovqat xavfsizligini ta’minlash va tarmoq eksport salohiyatini oshirishga salbiy ta’sir ko‘rsatmoqda.
Qishloq xo‘jaligida yer va suv resurslaridan samarali foydalanish konsepsiyasi qishloq xo‘jaligi yerlaridan hamda suv va gidroinshootlardan foydalanish samaradorligini oshirish, qishloq xo‘jaligi yerlarining maksimal mahsuldorlik salohiyatiga erishish, qishloq xo‘jaligi mahsulotlari hajmini ko‘paytirish, tuproq unumdorligini tiklash, davlat-xususiy sheriklik doirasidagi loyihalarni amalga oshirish uchun qulay sharoit yaratish, ilg‘or dehqonchilik tizimlarini va qishloq xo‘jaligi ekinlarini yetishtirishning yuqori va intensiv agrotexnologiyalarini joriy qilish bo‘yicha agrotexnik, yerlarning meliorativ holatini yaxshilash, zamonaviy irrigatsiya va melioratsiya texnologiyalari hamda yerlarni sug‘orish usullarini keng joriy etish, ekinlar seleksiyasi va urug‘chiligini, qishloq xo‘jaligi mahsulotlarini chuqur qayta ishlash va sotish, logistika va marketing tizimlarini rivojlantirish, ilm-fan va amaliyot integratsiyasini jadallashtirish maqsadida quyidagi asosiy yo‘nalishlarni o‘z ichiga oladi:'),
        (1362, 127, '2023-10-09 11:42:07', '2023-10-09 11:42:07', '1. Qishloq xo‘jaligi yerlaridan foydalanish samaradorligini oshirish.
Foydalanishdan chiqib ketgan sug‘oriladigan va foydalanishga kiritiladigan hamda o‘rmon fondi yerlarining meliorativ holati, unumdorligi va suv ta’minotini yaxshilash texnologiyasini joriy qilganlik uchun qishloq xo‘jaligi tovar ishlab chiqaruvchilariga imtiyozlar berish tizimini yaratish.
Qishloq xo‘jaligiga mo‘ljallangan lalmi, yaylov va boshqa yerlarni foydalanishga kiritish maqsadida investitsiyaviy shartnoma yoki davlat-xususiy sheriklik shartlari asosida foydalanishga kiritgan, yaroqsiz suv quduqlari, sug‘orish nasoslari, irrigatsiya va melioratsiya tarmoqlarini ta’mirlagan yoki ularni yangidan barpo etgan tadbirkorlik subyektlariga davlat tomonidan kafolatlar berish.
Qishloq xo‘jaligi yer uchastkalarini ajratishning shaffof va tezkor tizimini yaratgan holda yanada takomillashtirish.
Qishloq xo‘jaligi yerlarining aniq hisobi yuritilishini va yangilab borilishini ta’minlash, har bir dala konturi kesimida ma’lumotlar bazasini shakllantirish maqsadida barcha hududlarni aerofotos’yomkadan o‘tkazish.
Davlat yer kadastri ma’lumotlarining haqqoniy va to‘liq shakllantirilishini ta’minlash.
Xalqaro moliya institutlarining grant mablag‘larini jalb qilish hamda investitsiya loyihalari orqali davlat yer kadastrini yuritishda ko‘p maqsadli yer ma’lumotlarining avtomatlashtirilgan yagona tizimi — yergeoportal.uzni ishga tushirish.
Qishloq xo‘jaligi ekinlarini yetishtirish agrotexnikasi, yer va suv resurslaridan foydalanish holatini geoaxborot tizimi orqali monitoring qilish, erni masofadan zondlash tizimlaridan keng foydalanish.
Foydalanilmayotgan yerlar haqidagi ma’lumotlarni viloyat, tuman (shahar) hokimliklari, mutasaddi vazirlik, idora va tashkilotlarning rasmiy veb-saytlarida joylashtirish amaliyotini joriy etish.
Qishloq xo‘jaligi yerlarining tuproq unumdorligi, konturi, ularga ekinlarni joylashtirish, hosildorlikni belgilash to‘g‘risidagi ma’lumotlarni viloyat, tuman (shahar) hokimliklari va manfaatdor tashkilotlarning rasmiy veb-saytlarida joylashtirish amaliyotini joriy etish.
Davlat yer kadastrini yuritishda ko‘p maqsadli yer ma’lumotlarining avtomatlashtirilgan yagona tizimi yergeoportal.uzni yaratish maqsadida Koreya Respublikasi xalqaro “KOICA” jamg‘armasining grant mablag‘lari doirasida investitsiyaviy loyihasini ishlab chiqish.
Qishloq xo‘jaligiga mo‘ljallangan lalmi, yaylov va boshqa foydalanishga kiritilgan yerlarda barpo etiladigan infratuzilma obyektlarini (dala shiypon, mahsulotlar saqlanadigan omborxonalar, sug‘orish inshootlari va boshqalar) qurish uchun yer uchastkalarini ajratish tartibini belgilash.
Foydalanishga kiritiladigan yerlarda barpo etiladigan infratuzilma obyektlari minimal me’yorlarining namunaviy loyihasini ishlab chiqish.
Infratuzilma obyektlarini qurish uchun yer maydonlaridan foydalanish huquqini beruvchi ruxsatnomani Adliya vazirligi tomonidan berish amaliyotini joriy qilish.
Qishloq xo‘jaligiga mo‘ljallangan yer uchastkalarini ikkilamchi ijaraga berish, shuningdek, dehqon xo‘jaliklari va tomorqa yer uchastkalarida meros qilib qoldiriladigan umrbod egalik qilish huquqini saqlab qolgan holda yer uchastkasini vaqtincha foydalanish uchun berish shaklini amaliyotga joriy qilish.
Rentabelligi past bo‘lgan paxta, g‘alla va boshqa ekinlar yer maydonlarini to‘liq xatlovdan o‘tkazib, unumdorligi va foydalanish samaradorligini oshirish bo‘yicha chora-tadbirlar dasturini, shuningdek, ushbu maydonlarda yuqori daromadli ekinlarni joylashtirish bo‘yicha takliflarni ishlab chiqish.
Qishloq xo‘jaligi ekinlarini joylashtirish tizimini tubdan takomillashtirish. Bunda hududlarning tuproq, iqlim sharoitlari va suv bilan ta’minlanganlik darajasi, ichki va tashqi bozorda mahsulotga bo‘lgan talab, daromadliligi asosiy mezon bo‘lishini ta’minlash.
Suv tanqis hududlarda kam suv talab qiladigan, suvsizlikka chidamli va suv to‘plash xususiyatiga ega bo‘lgan ekinlarni joylashtirish.
Tarmoqlarni intensiv rivojlantirish talabidan kelib chiqqan holda qishloq xo‘jaligi ekinlarini ekish sxemalarini takomillashtirish, maydon birligi hisobiga ko‘chat sonini maqbul darajagacha oshirish.
Qishloq xo‘jaligi uchun mo‘ljallangan yerlardan bir mavsumda kamida ikkita hosil olish choralarini ko‘rish, ushbu maqsadga erishish uchun tezpishar ekinlar urug‘i, ko‘chatlar, mineral o‘g‘it, yonilg‘i-moylash materiallari va boshqa turdagi resurslar bilan barqaror ta’minlash tizimini yaratish.
Sug‘oriladigan ekin yerlaridan yanada samarali foydalanish, tuproq unumdorligi va qishloq xo‘jaligi ekinlari hosildorligini oshirish maqsadida ilmiy asoslangan almashlab ekish tizimini joriy qilish.
Xalqaro tajribalarga muvofiq qishloq xo‘jaligining yangi yo‘nalishlarini (Organic, Global G.A.P. va shu kabilar) joriy qilish va rivojlantirish.
Qishloq xo‘jaligi yerlari degradatsiyasining oldini olish va uni keskin kamaytirish hisobiga tuproq unumdorligini oshirish.
Yaylovlardan samarali foydalanish tartibini belgilash, yaylov va lalmi yerlardan foydalanishning ilmiy asoslangan samarali texnologiyalarini ishlab chiqish va joriy etish.
Fermer xo‘jaligi yoki boshqa qishloq xo‘jaligi mahsulotlarini yetishtiruvchi subyektlar tugatilayotganda yoki maqbullashtirilayotganda unga ajratilgan yer maydonida sarflangan xarajatlar (ko‘p yillik daraxtzorlar, tomchilatib sug‘orish tarmoqlari va boshqalar) qiymatini hisoblash va qoplash tartibini belgilash.
Bog‘lar, tokzorlar, baliq hovuzlarini va issiqxonalar tashkil qilishga yer ajratish tartibini belgilash, foydalanishdan chiqib ketgan yerlarda gidroponika, vertikal fermalar usulida issiqxonalar qurishni qo‘llab-quvvatlash.
Suv tanqis, qishloq xo‘jaligi ekinlarini yetishtirish va foydalanishga kiritish imkoniyati qiyin bo‘lgan yerlarda tayyor zamonaviy issiqxonalarni xorijiy investorlar hamda xorijiy moliya institutlarini jalb qilgan holda qurib, tayyor holda lizingga berish tizimini yaratish.
Qishloq xo‘jaligida tuproqdan unumli foydalanish texnologiyalarini joriy etish. Bunda erga ishlov berish va ekinlarni parvarishlashning ilg‘or innovatsiya (Mini till, No till va boshq.) texnologiyalarini amaliyotga izchillik bilan tatbiq etish hisobiga agrotexnik tadbirlar sonini minimallashtirishga alohida e’tibor qaratish.
Qishloq xo‘jaligi mahsulotlari ishlab chiqaruvchilariga sug‘oriladigan yerlarning meliorativ holati, unumdorligi va suv ta’minotini yaxshilash texnologiyalarini joriy qilish bilan bog‘liq xarajatlarini qoplab berish tartibini ishlab chiqish.
Qishloq xo‘jaligiga mo‘ljallangan yerlarning unumdorligi va hosildorligi oshirilishini ta’minlashni davlat tomonidan qo‘llab-quvvatlash mexanizmlarini belgilash.
Yer uchastkalari egalarining tuproq unumdorligini saqlash va oshirishni rag‘batlantiruvchi mexanizmlarni nazarda tutgan holda tabaqalashtirilgan soliq imtiyozlarini joriy qilish.
Qishloq xo‘jaligi uchun mo‘ljallangan yerlardan samarasiz foydalanganlik uchun javobgarlik choralarini kuchaytirish.
Yer va suv resurslaridan samarasiz foydalanishning oldini olishni, yerlarning unumdorligini oshirish va suvni tejovchi agrotexnik tadbirlarning amalga oshirilishini (agrotexnik reglament) huquqiy jihatdan tartibga solish.
Iqtisodiy jihatdan samarasiz, kam hosilli bog‘ va uzumzorlarni yaroqsiz deb topish va ekin yerlar toifasiga o‘tkazish tartibini belgilash.
Har 5 yilda bir marotaba davlat budjeti mablag‘lari hisobidan qishloq xo‘jaligi ekin maydonlarida tuproqning agrokimyoviy tahlillari asosida agrokimyoviy xaritalarni ishlab chiqish.
Shahar va shahar atrofidan chiqayotgan oziq-ovqat, xo‘jalik, chorva hamda boshqa organik chiqindilarni to‘plash hamda zamonaviy texnologiyalarni qo‘llagan holda organik o‘g‘itlar ishlab chiqarishni yo‘lga qo‘yish va qishloq xo‘jaligi mahsulotlari yetishtiruvchilariga yetkazib berish.
O‘rmon fondi yerlaridan foydalanishda davlat-xususiy sheriklik mexanizmini yo‘lga qo‘yish.
Har bir hududning qishloq xo‘jaligi imkoniyatlaridan kelib chiqib, mahalliy va xorijiy kompaniyalarga taqdimot qilish maqsadida “qishloq xo‘jaligi bo‘yicha investitsiya xab”larini yaratish.
Rentabelligi past bo‘lgan paxta va g‘alla maydonlari 50 foizdan yuqori bo‘lgan tumanlardagi barcha ekin maydonlarini irrigatsiya va melioratsiya tarmoqlarini qurish, rekonstruksiya qilish shartlari bilan paxta va g‘allani to‘liq qayta ishlashni tashkil etuvchi klasterlarga berish.
Davlat geologiya va mineral resurslar qo‘mitasi tomonidan qishloq xo‘jaligi ekinlarini sug‘orishga yaroqli bo‘lgan yer osti suvlari zaxirasini aniqlash ishlarini amalga oshirish uchun davlat budjeti hisobidan zarur mablag‘larni ajratish.
Foydalanilmayotgan o‘rmon fondi yerlarida yangi o‘rmonzorlar va dorivor o‘simliklar plantatsiyalarini barpo etish maqsadida foydalanish uchun O‘zbekiston Respublikasining fuqarolariga davlat-xususiy sheriklik shartlari asosida o‘rmon xo‘jaligi organlari tomonidan 50 yilgacha muddatga ijaraga berishni nazarda tutuvchi qonun hujjatlariga o‘zgartirish va qo‘shimchalar kiritish.
Tuproq eroziyasi va degradatsiyasi, shamol va garmseldan muhofaza qilish maqsadida biomeliorotiv tadbirlar qo‘llashni kengaytirish, ixotazorlar barpo etish va qayta tiklash hamda o‘rmon fondi yerlarida cho‘l-ozuqabop o‘simliklar plantatsiyalari va ularni tashqi ekologik omillardan muhofaza qiluvchi yaylovlarni yaratish.
Qayta tiklanadigan va foydalanishga kiritiladigan yer maydonlarining manzilli ro‘yxatini shakllantirish.'),
        (1363, 127, '2023-10-09 11:42:07', '2023-10-09 11:42:07', '2. Suv va gidroinshootlardan foydalanish samaradorligini oshirish, yerlarning meliorativ holatini yaxshilash
2020 — 2030-yillarda suv xo‘jaligini rivojlantirish konsepsiyasini ishlab chiqish.
Suv resurslarini boshqarish va irrigatsiya sektorini rivojlantirish strategiyasini ishlab chiqish.
Suv va suvdan foydalanish sohasidagi qonun hujjatlarini tizimlashtirish va kodifikatsiyalashtirish. Bunda suv resurslarini boshqarish, suvdan foydalanish va uni iste’mol qilish bo‘yicha samarali mexanizmlarni joriy etish.
Qishloq xo‘jaligi ekinlarini sug‘orishda tomchilatib, yomg‘irlatib sug‘orish va boshqa suv tejovchi texnologiyalarni keng qo‘llash, bu borada yer egalari va suv iste’molchilarini rag‘batlantirish mexanizmini kengaytirish.
Qishloq xo‘jaligiga mo‘ljallangan suv resurslarini boshqarish va xizmat ko‘rsatish tizimini takomillashtirish. Bunda suv iste’molchilari uyushmalari faoliyatini qayta ko‘rib chiqish.
Sug‘oriladigan yerlarning meliorativ holatini yaxshilash va barqaror saqlash maqsadida irrigatsiya va melioratsiya obyektlarini qurish va rekonstruksiya qilish hamda ta’mirlash-tiklash ishlarini amalga oshirib borish.
2020 — 2022-yillarga mo‘ljallangan irrigatsiyani rivojlantirish va sug‘oriladigan yerlarning meliorativ holatini yaxshilashga doir davlat dasturini puxta ishlab chiqish va tasdiqlash.
Yengil konstruksiyali plastik lotok va quvurlar ishlab chiqarish sanoatini tashkil etish hamda nasos agregatlari ishlab chiqarishni rivojlantirish.
Tuman irrigatsiya bo‘limlari xodimlari va fermer xo‘jaliklari rahbarlarini sug‘orishda va sho‘r yuvishda suvdan samarali foydalanish hamda suvni tejovchi texnologiyalarni qo‘llash bo‘yicha ko‘nikma va bilimlarini oshirish.
Suv omborlari tarmog‘ini kengaytirish hisobidan lalmi yerlarni foydalanishga kiritish. Bunda asosiy e’tiborni sel suv omborlari va sun’iy suv havzalarini qurishga qaratish.
Suv xo‘jaligi vazirligi tizimidagi nasos stansiyalarida elektr energiyasidan foydalanish samaradorligini oshirish.
Suv iste’moli sohasida bozor mexanizmlarini hamda suv xo‘jaligi obyektlarini ekspluatatsiya qilishda davlat-xususiy sheriklik tamoyillarini bosqichma-bosqich joriy etish.
Suvni yetkazib berish bo‘yicha davlat tomonidan qilinayotgan xarajatlarni suv iste’molchilari tomonidan qoplash tartibini belgilash.
Suv resurslarini boshqarish, suvdan tejamkorlik bilan foydalanish va gidrotexnik inshootlarning texnik holatini yaxshilash va xavfsizligini ta’minlashga qaratilgan istiqbolli loyihalar ro‘yxatini shakllantirish va ularni xalqaro moliya institutlarining mablag‘lari hamda xorijiy grantlar hisobidan amalga oshirish.
Suv iste’molchilarining suvdan foydalanish joylarida suvni boshqarish va hisobga olish vositalari bilan bosqichma-bosqich jihozlanishini tashkil etish.
Yer osti zaxira suv resurslaridan samarali foydalanish bo‘yicha texnologiyalarni ishlab chiqish va joriy etish. Qishloq xo‘jaligida yer osti suvlaridan o‘zboshimchalik bilan suv olish va undan isrofgarchilik bilan foydalanish holatlarining oldini olish.
Hozirgi zamon talablaridan kelib chiqib, ilmiy asoslangan tarzda sug‘oriladigan yerlarni gidromodul rayonlarga qayta bo‘lish va tasdiqlash.
Suv va yer resurslaridan tejovchi va ulardan oqilona foydalanishga imkon yaratib beruvchi sug‘orish va meliorativ texnika, mashina va qurilmalarni ishlab chiqarishni tashkil qilish.
Belgilangan suv olish limitlaridan tejab foydalangan, sug‘orishda suvni iqtisod qiluvchi sug‘orish texnologiyalari joriy qilib, suv iste’mol qilayotgan suvdan foydalanuvchilar va suv iste’molchilarini rag‘batlantirish tartibini ishlab chiqish.
Suv iste’molchilari va suvdan foydalanuvchilar tomonidan suv olish tartibini buzganlik uchun javobgarlikni kuchaytirish.
Qishloq xo‘jaligi ekinlarini sug‘orishga yaroqli yer osti suvlari zaxirasini ko‘paytirish uchun izlash, baholash gidrogeologik ishlarini o‘tkazish va sug‘oriladigan yerlarning meliorativ holatini yaxshilash uchun izlanishlar olib borish.
Yer osti suvi iste’molchilarining yagona Davlat statistik hisobot yuritish tartibini tatbiq etish hamda yer osti suvlaridan foydalanishning monitoring tizimini rivojlantirish.'),
        (1364, 127, '2023-10-09 11:42:07', '2023-10-09 11:42:07', '3. Qishloq xo‘jaligi ekinlari seleksiyasi va urug‘chiligini rivojlantirish
Qishloq xo‘jaligi ekinlari seleksiyasi va urug‘chiligi hamda urug‘shunosligi sohasidagi ilmiy tadqiqot ishlari ko‘lamini kengaytirish.
Urug‘chilik sohasida zamonaviy milliy, shu jumladan import o‘rnini bosuvchi hamda yuqori daromadli va eksportbop ekinlar navlarini yaratish mexanizmini takomillashtirish.
Qishloq xo‘jaligi ekinlari urug‘larini yetishtirish tizimini takomillashtirish, shu jumladan sabzavot, poliz, dukkakli, ozuqa va moyli ekinlar urug‘larini yetishtirish va yetkazib berishning samarali mexanizmini yaratish.
Yaratilayotgan mahalliy navlar va xorijiy davlatlardan import qilinayotgan nav va duragaylarni sinov va davlat reyestriga kiritish ishlarini tubdan takomillashtirish.
Jahon bozori konyunkturasini hisobga olgan holda, respublika tuproq-iqlim sharoitlariga mos bo‘lgan noan’anaviy qishloq xo‘jaligi ekinlari seleksiyasi va urug‘chiligini yo‘lga qo‘yish.
Qishloq xo‘jaligi urug‘chiligi yo‘nalishidagi barcha ilmiy tadqiqot muassasalarida yo‘qolib ketish xavfi ostida bo‘lgan mahalliy navlarni qayta tiklash va ular asosida organik (ekologik toza) urug‘chilikni tashkil etish va rivojlantirish bo‘yicha ilmiy-tadqiqot va amaliy ishlarni kengaytirish hamda davlat-xususiy sheriklik shartlari asosida ushbu ishlar natijalarini realizatsiya qilish tadbirlarini amalga oshirish.
Qurg‘oqchilik va sho‘rlanishga bardoshli ekinlar seleksiyasi hamda urug‘chiligini yo‘lga qo‘yish. Azotni to‘plash texnologiyalarini takomillashtirish, donli ekinlarda dukkaklilar genini joriy etish hisobiga azotlashish xususiyatini yaratish, azotni to‘plovchi mikroblar va tashuvchilarni genetik jihatdan yaxshilash.
“In vitro” laboratoriyalari va ko‘chatchilik xo‘jaliklarida yuqori sifat ko‘rsatkichli, suvsizlikka chidamli mevali daraxt hamda tok ko‘chatlari navlarini ko‘paytirish.
Urug‘chilik bo‘yicha xalqaro tashkilotlarga (ISTA, OECD, ISF va h.k.) bosqichma-bosqich a’zo bo‘lish, urug‘chilik sohasida faoliyat yuritayotgan yirik xorijiy kompaniyalar bilan hamkorlik qilish hamda urug‘liklar bo‘yicha taqdim etilayotgan muvofiqlik sertifikatlarining xalqaro darajada tan olinishiga erishish.
Davlat-xususiy sheriklik shartlari asosida urug‘chilik klasterlarini tashkil qilish, ularni davlat tomonidan qo‘llab-quvvatlashning samarali mexanizmlarini joriy etish.'),
        (1365, 127, '2023-10-09 11:42:07', '2023-10-09 11:42:07', '4. Qishloq xo‘jaligi mahsulotlarini chuqur qayta ishlash va sotish, logistika va marketing tizimlarini rivojlantirish.
Hududlar ixtisoslashuvi, ichki va tashqi bozorlardagi mahsulotlarga bo‘lgan talabni hisobga olgan holda investitsiyalar, shu jumladan to‘g‘ridan-to‘g‘ri xorijiy investitsiyalar ishtirokida zamonaviy ulgurji-taqsimot markazlarini (ORS) tashkil etish.
Qishloq xo‘jaligi mahsulotlarini yetishtirish, qayta ishlash va tayyor mahsulotlarni ishlab chiqarish jarayonini o‘z ichiga oladigan klasterlarni jadal rivojlantirish.
Qishloq xo‘jaligi va oziq-ovqat mahsulotlarining sifati, xavfsizligi, yetkazib berish shartlari darajasini oshirishni ta’minlash.
Qishloq xo‘jaligi mahsulotlarini yetishtirish, qayta ishlash va eksport qilishda logistika markazlari, agrosanoat komplekslari faoliyatini rivojlantirish.
Rivojlangan xorijiy mamlakatlar tajribasi, sohada tatbiq etilgan zamonaviy texnika va texnologiyalar asosida xalqaro talablarga mos keladigan qishloq xo‘jaligi mahsulotlarini yig‘ib olish, saqlash, tashish va kutilmagan ziyonlarni qoplash bo‘yicha davlat standartlarini ishlab chiqish.
Qishloq xo‘jaligi mahsulotlari eksport hajmini oshirishning samarali mexanizmini joriy etish.
Yuqori malakali marketologlar tayyorlash sifatini oshirish, qishloq xo‘jaligi mahsulotlari ichki va tashqi bozorlarida marketing tadqiqotlari olib borish ko‘lamini kengaytirishni qo‘llab-quvvatlash choralarini qo‘llash.'),
        (1402, 136, '2024-05-29 17:30:50', '2024-05-29 17:30:50', '28. Bosh boshqarma:
tuman (shahar) hududiy bo‘linmalari bilan hamkorlikda ularga yuklangan vazifalar va funksiyalarning sifatli va o‘z vaqtida bajarilishini ta’minlash;
tuman (shahar) bo‘linmalarining faoliyati bilan bog‘liq prognoz ko‘rsatkichlariga erishishni ta’minlash;
Vazirlikka kiritiladigan hujjatlar loyihalari va takliflarni sifatli tayyorlash;
tumanlar (shaharlar)da aniqlangan muammolarni chuqur tahlil qilish va umumlashtirish asosida viloyatda sohani rivojlantirish bo‘yicha takliflar tayyorlash;
sohani rivojlantirish bo‘yicha dasturlar, tadbirlar rejalari, “yo‘l xaritalari” va boshqa dasturiy hujjatlarni samarali amalga oshirish uchun javob beradi.'),
        (1366, 127, '2023-10-09 11:42:07', '2023-10-09 11:42:07', '5. Yer va suv resurslaridan samarali foydalanishda ilm-fan va amaliyot integratsiyasini jadallashtirish.
Yer va suv munosabatlari bo‘yicha tor ixtisoslikdagi mutaxassislar tayyorlash bo‘yicha yangi o‘quv yo‘nalishlari (geodeziya, kartografiya va h.k.) va fakultetlar, shu jumladan yetakchi xorijiy oliy ta’lim muassasalari bilan qo‘shma fakultetlar ochish.
Ilmiy tadqiqot mavzularini hududlar va qishloq xo‘jaligining muammolari yechimlaridan kelib chiqqan holda shakllantirish va natijalarini tijoratlashtirish.
Tuproq unumdorligini oshirish, tuproq eroziyasi va degradatsiyasining oldini olishga qaratilgan ilmiy tadqiqotlarni olib borishni jadallashtirish.
Qishloq xo‘jaligi yo‘nalishidagi ilmiy tadqiqotlarni olib borish, innovatsion ishlanmalarni ishlab chiqish va joriy etishda davlat-xususiy sheriklik mexanizmini, xususiy sektorning ishtirokini rag‘batlantirishning samarali mexanizmlarini keng joriy qilish.
Xorijiy davlatlarning qishloq xo‘jaligi ekinlari serhosil navlari va chorva mollarining mahsuldor zotlari hamda turlarini mahalliylashtirish borasidagi ilmiy-amaliy faoliyat hamda hamkorlikni tizimli tashkil etish.
Soha bo‘yicha doktorantura va tayanch doktorantura orqali ilmiy darajali yuksak malakali ilmiy xodimlarni tayyorlashni kengaytirish, ularga muammoli va dolzarb mavzular bo‘yicha ilmiy tadqiqot ishlarini olib borishlari uchun barcha shart-sharoitlarni yaratish.
Ilmiy tadqiqotlar natijasida “Aqlli va raqamli qishloq xo‘jaligi” texnologiyasini joriy etishga, uning texnik vositalari va texnologiya uskunalarini ishlab chiqarishda mahalliylashtirishga erishish. Bunda:
“Aqlli va raqamli qishloq xo‘jaligi” texnologiyasini joriy etishda xalqaro moliya institutlarining mablag‘lari va grantlarini jalb etish;
“Aqlli va raqamli qishloq xo‘jaligi” texnologiyalarini joriy etish vazifalaridan kelib chiqqan holda qishloq xo‘jaligi yo‘nalishidagi oliy ta’lim muassasalarida mutaxassis kadrlarni tayyorlashni yo‘lga qo‘yish.
Ilmiy-tadqiqot institutlarining moddiy-texnika bazasini mustahkamlash, ilmiy laboratoriyalarini modernizatsiya qilish, zamonaviy jihozlar bilan ta’minlash va ta’mirlash.
Sug‘orish zonalarini, mintaqaning suv bilan ta’minlanganligini hisobga olgan holda qishloq xo‘jaligi ekinlarining sug‘orish texnika va texnologiyalarini takomillashtirish bo‘yicha tavsiyalar ishlab chiqish.
Jahon amaliyotini o‘rgangan holda noan’anaviy sug‘orish texnologiyalarini ishlab chiqish va tatbiq etish.'),
        (55, 5, null, null, '27. Тез, тўхтовсиз ҳаракат мавжуд ва йўл ҳаракати бошқариладиган магистраль кўчаларнинг қизил чизиғидан узоқлик масофаси шовқин даражасига қараб ҚМҚ 2.01.08- 96 “Шовқиндан сақланиш” га мувофиқ қабул қилиниши лозим.'),
        (56, 5, null, null, '28. Биринчи қаватида хонадонлар бўлган турар жой уйларини турар жой кўчаларининг қизил чизи-ғидан камида 3 м узоқликда жой-лаштириш лозим. Қизил чизиқ бўйича ичига жамоат хоналари қурилган ёки улар қўшиб қурилган турар жой уйларини жойлаштириш мумкин.'),
        (47, 5, null, null, '21.2. Ижтимоий-амалий ва аралаш зоналарда жойлашган ишлаб чиқариш объектлари участкалари чегарасидан турар-жой ва жамоат биноларигача, шунингдек мактабгача ва умумтаълим, соғлиқни сақлаш ва дам олиш муассасалари участкаларининг чегараларигача бўлган масофа 50 м дан кам бўлмаган ҳолда қабул қилиниши зарур.'),
        (44, 5, null, null, '21*. Турар-жой бинолари қурилишини амалга оширишда шаҳарсозлик, демографик, табиий-иқлимий ва бошқа шароитларга мувофиқ ҳолда турар-жой биноларининг қуйидаги турлари қўлланиши керак: кам қаватли, ҳовлили, кам қаватли зич, ўрта қаватли (4-5 қават), баланд қаватли (9-12-16 қават). Бинолар турини танлашда қўшимча ҳудудларга бўлган эҳтиёжларнинг олдини олувчи ёки ушбу эҳтиёжларни қисқартирувчи энг самарали ер захира ларидан фойдаланишни кўзда тутиш зарур.'),
        (45, 5, null, null, '21.1. Турар-жой бинолари, турар-жой ва жамоат, шунингдек саноат бинолари ўртасидаги масофалар қуёш нурининг тушиши (инсоляция) ва ёритилганликни ҳисоблашлар асосида, асосий турар-жой бинолари ва хонадонлари ёнидаги ҳовлиларнинг кўриниши чегаралари, шунингдек ёнғинга қарши талабларга мувофиқ тарзда қабул  қилиниши зарур (1-илова). Кўп хонадонли турар-жой  уйларининг узун томонлари ўртасидаги масофалар (хоналарнинг яққол  кўринмаслик талабларидан келиб  чиққан ҳолда) қуйидагича қабул  қилиниши лозим: баландлиги 2-3 қават бўлган  турар-жой уйлари учун - 15 м кам  бўлмаган; 4-5 қаватлилар учун - 25-30 м  кам бўлмаган; яшаш хоналарида деразалари  бўлган ушбу биноларнинг узун  томонлари ва ён томонлари  учун - 10 м дан кам бўлмаган. 9 ва ундан кўпроқ қаватли бинолар учун узун томонлари ўртасидаги масофа - 40-45 м дан кам
бўлмаган; яшаш хоналарида деразалари  бўлган ушбу биноларнинг ён  томонлари учун - 20-25 м дан кам  бўлмаган. Изоҳ. Реконструкция ва бошқа мураккаб шаҳарсозлик шароитларда  кўрсатилган масофалар инсоляция, ёритилганлик меъёрлари, ёнғинга қарши  талабларга, шунингдек турар-жой хоналари (истиқомат хоналари ва ошхоналари)  деразаларидан яққол кўринмасликни таъминланишига риоя қилган ҳолд  қисқартирилиши мумкин. Зилзилалиги 8 балл ва ундан  юқори бўлган майдонларда тураржой биноларининг узун томонлари  орасидаги масофалар энг катта бино  икки баравар баландлигидан кам  бўлмаслиги зарур. Изоҳ. Мавжуд мавзеларнинг кўп  хонадонли бинолари оралиғида, махсус  бино ва иншоотлардан (спорт майдонлари,  болалар ўйин майдончалари, кийим-кечак қуритиш майдончалари, автомобилларнинг вақтинчалик тўхташ жойлари) ташқари, ҳар қандай бошқа мақсадли объектларни жойлаштиришга йўл қўйилмайди. Шахсий турар-жой бинолари ва  боғ-дала ҳовли қурилмалари жойлашган туманларда турар-жой бинолари (хоналар, ошхоналар ва айвонлар) деразаларидан қўшни ер  участкаларида жойлашган уй ва хўжалик бинолари деворларигача  бўлган масофа 6 м дан кам бўлмаслиги (яққол кўрмаслик талабларини,  шунингдек қўшни ер участкаларини  пана қилмаслик талабларига риоя  қилган ҳолда) зарур, ҳайвонлар ва  паррандалар боқиладиган саройларгача масофа эса санитариягигиеник талабларга мувофиқ  аниқланади. Участка чегарасидан бўлган  масофа қуйидагилардан кам  бўлмаслиги зарур: турар-жой биноси  деворигача 3 м, хўжалик  биноларигача – 1 м.  Марказлашган канализация  тармоғи бўлмаган ҳолатларда,  ҳожатхонадан қўшни бино деворигача масофа 12 м дан, сув таъминоти  манбаи (қудуқ)гача эса - 25м дан кам  бўлмаган тарзда қабул қилиниши лозим. Изоҳ. Чегарадош томорқалардаги турар-жой бинолари, шунингдек хўжалик  қурилмаларини, уй эгаларининг ўзаро  келишувига биноан, ёнғин хавфсизлиги ва  зилзилабардошлик талабларини ҳисобга  олган ҳолда, бирлаштиришга рухсат  этилади'),
        (1367, 128, '2023-12-25 10:50:06', '2023-12-25 10:50:06', '1. 166-бандга келтирилган изоҳ қуйидаги хатбоши билан тўлдирилсин: “Велосипед йўллари қурилаётган ёки реконструкция қилинаётган йўллар бўйлаб участкаларда'),
        (1368, 128, '2023-12-25 10:50:06', '2023-12-25 10:50:06', 'Батафсил режалаштириш лойиҳаларини'),
        (1369, 130, '2023-12-25 10:53:03', '2023-12-25 10:53:03', '1. 22-банди қуйидаги таҳрирдаги хатбоши билан тўлдирилсин: “Ер участкасида турар-жой биноларини қуришда томнинг нишаблигини ёмғир ва қор эришилан ҳосил бўладиган сувларнинг қўшни участкасига тушмайдиган қилиб йўналтириш лозим”.'),
        (1370, 130, '2023-12-25 10:53:03', '2023-12-25 10:53:03', '2. 36*-бандда: қуйидаги таҳрирдаги бешинчи хатбоши билан тўлдирилсин: “Томда ёмғир ва қор эришидан ҳосил бўладиган сувларнинг қўшни ер участкасига оқишини ташкил этилишига йўл қўйилмайди”; бешинчи хатбоши олтинчи хатбоши деб ҳисоблансин.'),
        (1445, 137, '2024-05-29 17:31:43', '2024-05-29 17:31:43', '8. Kengashga O‘zbekiston Respublikasi Bosh vazirining o‘rinbosari raislik qiladi, uning vakolatiga quyidagilar kiradi:
Kengash majlisining kun tartibini belgilash va majlisni chaqirish;
Kengash tomonidan tuzilgan ishchi guruhlar faoliyatini nazorat qilish;
Kengash tomonidan qabul qilingan qarorlarning bajarilishi yuzasidan nazoratni tashkil qilish;
Kengashga yuklangan vazifalarni bajarish bo‘yicha Kengash a’zolarining faoliyatini nazorat qilish;
Kengash a’zolari zimmasiga yuklangan vazifalarni bajarish bo‘yicha ishlarini muvofiqlashtirish;
Kengash a’zolarining shaxsiy tarkibini o‘zgartirish bo‘yicha O‘zbekiston Respublikasi Vazirlar Mahkamasiga takliflar kiritish;
Kengashning samarali ishlashini ta’minlash bo‘yicha boshqa vakolatlarni amalga oshirish.'),
        (1371, 132, '2023-12-25 10:57:42', '2023-12-25 10:57:42', '1. 20.4-бандда: қуйидаги таҳрирдаги тўққизинчи ва ўнинчи хатбошилар билан тўлдирилсин: “Шаҳар ҳудудида қуриладиган турар жой биносининг умумий майдони (ер устидаги барча қаватлар майдонларининг тўплами) ушбу турар жой биносини қуриш учун ажратилган ер участкаси майдонининг уч баробаридан ошмаслиги лозим. Турар жой биноси умумий майдонининг ер участкаси майдонига нисбати (УМН) ва турар жой биноси қурилиш майдонининг ер участкаси майдонига нисбати (ҚМН) кўрсаткичлари шаҳар бош режасида белгиланади.”; тўққизинчи хатбоши тегишли равишда ўн биринчи хатбоши деб ҳисоблансин.'),
        (1372, 132, '2023-12-25 10:57:42', '2023-12-25 10:57:42', '2. 21.1-бандда: қуйидаги таҳрирдаги иккинчи – тўртинчи хатбошилар билан тўлдирилсин: “Уч ва ундан юқори кўп қаватли турар-жой бинолари ва барча ётоқ ўринлари мавжуд бўлган бинолар орасидаги масофалар мазкур шаҳарсозлик нормалари ва қоидаларининг 17-иловасида келтирилган ўлчамлардан кам бўлмаслиги лозим. Маъмурий биноларда мазкур шаҳарсозлик нормалари ва қоидаларининг 17-иловасида белгиланган масофалар 6 метрдан кам бўлмаган ҳолда икки баробаргача қисқартирилиши мумкин. Иккита бино орасидаги масофани ҳисоблаш учун йўналиш коэффициенти (kй ) мазкур шаҳарсозлик нормалари ва қоидаларининг 18-иловасига мувофиқ ҳисобланиши лозим.”; иккинчи – ўн тўртинчи хатбошилар тегишли равишда бешинчи – ўн еттинчи хатбошилар деб ҳисоблансин; бешинчи – ўн учинчи хатбошилар чиқариб ташлансин; ўн тўртинчи –ўн еттинчи хатбошилар тегишли равишда бешинчи –саккизинчи хатбошилар деб ҳисоблансин.'),
        (1373, 132, '2023-12-25 10:57:42', '2023-12-25 10:57:42', '3. 183-банд қуйидаги таҳрирда баён этилсин: “Турар жой, жамоат ва дам олиш масканларида автотураргоҳларни ҳисоблашнинг нормалари мазкур шаҳарсозлик нормалари ва қоидаларининг 19-иловасига мувофиқ белгиланиши керак.”.'),
        (1374, 132, '2023-12-25 10:57:42', '2023-12-25 10:57:42', '4. Мазкур ўзгартириш ва қўшимчаларнинг 1–3-иловаларига мувофиқ таҳрирда 17–19-иловалар билан тўлдирилсин.'),
        (1383, 136, '2024-05-29 17:30:50', '2024-05-29 17:30:50', '9. Vazirlik tizimiga quyidagilar kiradi:
markaziy apparat;
Qoraqalpog‘iston Respublikasi Qurilish va uy-joy kommunal xo‘jaligi vazirligi, viloyatlar Qurilish va uy-joy kommunal xo‘jaligi bosh boshqarmalari, Toshkent shahar Qurilish bosh boshqarmasi va Toshkent shahar Uy-joy kommunal xo‘jaligi bosh boshqarmasi (keyingi o‘rinlarda — Bosh boshqarma);
tuman (shahar) qurilish va uy-joy kommunal xo‘jaligi bo‘limlari, Toshkent shahrida — tuman qurilish bo‘limlari va uy-joy kommunal xo‘jaligi bo‘limlari;
Qurilish va uy-joy kommunal xo‘jaligi sohasida nazorat qilish inspeksiyasi va hududiy inspeksiyalar;
Shaharsozlik hujjatlarini ekspertiza qilish Respublika markazi davlat muassasasi va uning hududiy boshqarmalari;
Axborot-kommunikatsiya texnologiyalarini rivojlantirish Respublika markazi davlat muassasasi;
Urbanizatsiya va shaharsozlik hujjatlarini ishlab chiqish Respublika markazi davlat muassasasi;
Kompetensiyani rivojlantirish instituti davlat muassasasi (Arxitektorlar muhandislar oliy maktabi, Qayta tayyorlash va malaka oshirish markazi, Malakani baholash markazi, hududiy filiallar);
Texnik me’yorlash va standartlashtirish ilmiy-tadqiqot instituti davlat muassasasi;
“O‘zbekiston bunyodkori” Nashriyot uyi MChJ;
“O‘zbekiston arxitekturasi va qurilishi” jurnali tahririyati MChJ;
Qoraqalpog‘iston Respublikasi, viloyatlar va Toshkent shahri Qurilish sohasida davlat xaridlarini tashkil etish konsalting markazlari davlat muassasalari;
Issiqlik ta’minoti obyektlari bo‘yicha injiniring kompaniyalari;
Issiqlik ta’minoti davlat tashkilotlari;
“Kommunal ta’minot markazi” davlat muassasasi;
“ToshuyjoyLITI” AJ;
“O‘zog‘irsanoatloyiha” AJ;
“O‘zshaharsozlikLITI” bosh loyiha instituti davlat muassasasi;
“ToshkentboshplanLITI” bosh loyiha instituti davlat muassasasi;
“Qishloq qurilish loyiha” bosh loyiha instituti davlat muassasasi;
“Kommunal loyiha ilmiy-tadqiqot instituti” davlat muassasasi;
Loyiha ofisi;
Infratuzilmani rivojlantirish injiniring kompaniyasi davlat muassasasi.'),
        (1384, 136, '2024-05-29 17:30:50', '2024-05-29 17:30:50', '10. Quyidagilar Vazirlikning asosiy vazifalari hisoblanadi:
shaharsozlik faoliyati sohasida yagona davlat siyosatini amalga oshirish, sohada davlat siyosatining asosiy yo‘nalishlari bo‘yicha takliflar tayyorlash, shuningdek, soha faoliyatini rivojlantirishning uzoq va o‘rta muddatli dasturlarni va normativ-huquqiy hujjatlarini ishlab chiqishda, hududlarni ijtimoiy-iqtisodiy rivojlantirish dasturlarini tayyorlashda ishtirok etish;
normativ-huquqiy hujjatlar, shaharsozlik normalari va qoidalari hamda standartlarni ishlab chiqishni tashkil etish hamda tasdiqlashni amalga oshirish;
shaharsozlik faoliyatining barcha bosqichlari raqamlashtirilishini ta’minlash, yuridik va jismoniy shaxslarga ko‘rsatiladigan qurilish va uy-joy kommunal xo‘jaligi sohasida davlat xizmatlarini soddalashtirish va takomillashtirib borish;
respublika hududini rivojlantirishni rejalashtirish bosh sxemasini, Qoraqalpog‘iston Respublikasi va viloyatlar hududlarini rivojlantirishni rejalashtirish sxemalarini, aglomeratsiyalarni rivojlantirish strategiyasi, shaharlar (shaharchalar) bosh rejalari va qishloq aholi punktlarining hududlarini shaharsozlik jihatidan rivojlantirish sxemalarini ishlab chiqish va tasdiqlashni tashkil qilish, shuningdek, ularga rioya etilishi yuzasidan davlat arxitektura nazoratini amalga oshirish;
loyiha tashkilotlari faoliyatini muvofiqlashtirish va monitoringini yuritish, yakka tartibdagi, namunaviy, takroran qo‘llaniladigan eksperimental loyihalarni va energiya tejamkor loyiha yechimlari ishlab chiqishni tashkil etish, ular faoliyatini litsenziyalash hamda shaharsozlik hujjatlarining ekspertiza qilinishini ta’minlash;
pudrat bozorini rivojlantirish, jumladan qurilish-pudrat tashkilotlarining ayrim faoliyatlarini litsenziyalash va ulush kiritish asosida quriladigan ko‘p xonadonli uylarni ro‘yxatga olishni muvofiqlashtirish, qurilish va uy-joy kommunal sohasida ruxsat etish va xabardor qilish tartibini muvofiqlashtirish;
maqsadli dasturlarga kiritilgan obyektlarni monitoring qilish, loyiha va qurilish-pudrat tashkilotlari faoliyatida “Shaffof qurilish” milliy axborot tizimida davlat xaridlari hamda reyting tizimini yuritish;
obyektlar qurilishi jarayonida mualliflik, texnik va ichki nazorat olib borilishi ustidan davlat nazorati o‘rnatilishini ta’minlash, shuningdek, respublika hududida qurilayotgan bino va inshootlarning seysmik xavfsizligi ta’minlanishini shaharsozlik normalari va qoidalariga muvofiqligi baholanishini ta’minlash;
uy-joy kommunal xo‘jaligi sohasida yagona davlat siyosatini yuritish va tarmoqlararo muvofiqlashtirishni amalga oshirish;
ko‘p kvartirali uylarni boshqarish, shu jumladan, ko‘p kvartirali uy-joy fondini mukammal va joriy ta’mirlash ishlarini, eski va avariya holatidagi uy-joylar buzilishini tashkil etishni muvofiqlashtirish hamda renovatsiya dasturlarini shakllantirish;
hududlarni obodonlashtirish sohasi muammolarini o‘rganish va metodologik ta’minlash;
ichimlik suvi ta’minoti va oqova suvlarni chiqarib yuborish sohasida yagona davlat siyosatini amalga oshirish, rivojlantirish, davlat nazoratini ta’minlash, sohada xizmat ko‘rsatuvchilar faoliyatini muvofiqlashtirish;
issiqlik ta’minoti sohasini kompleks rivojlantirish, yagona texnik va tarif siyosatini yuritish;
uy-joy kommunal xo‘jaligi sohasida (uy-joy fondi, ichimlik suvi ta’minoti va oqova suvni chiqarish xizmatlari hamda issiqlik ta’minoti) davlat budjeti, xalqaro moliya institutlari, xorijiy hukumat moliya tashkilotlarining mablag‘lari hamda davlat-xususiy sheriklik hisobidan investitsiya loyihalarini amalga oshirish;
qurilish va uy-joy kommunal xo‘jaligi sohasida kadrlarni mahalliy va xorijiy yetakchi ta’lim muassasalarida tayyorlash, qayta tayyorlash, malakasini oshirish va kompetensiyasini baholash ishlarini tashkil etish hamda sertifikatlash jarayonlarini muvofiqlashtirish.'),
        (1385, 136, '2024-05-29 17:30:50', '2024-05-29 17:30:50', '11. Vazirlik o‘ziga yuklangan vazifalarga muvofiq quyidagi funksiyalarni amalga oshiradi:
a) shaharsozlik faoliyati sohasida yagona davlat siyosatini amalga oshirish, sohada davlat siyosatining asosiy yo‘nalishlari bo‘yicha takliflar tayyorlash, shuningdek, soha faoliyatini rivojlantirishning uzoq va o‘rta muddatli dasturlarni va normativ-huquqiy hujjatlarini ishlab chiqishda, hududlarni ijtimoiy-iqtisodiy rivojlantirish dasturlarini tayyorlashda ishtirok etish sohasida:
soha faoliyatida uzoq muddatli strategiyalarni belgilaydi;
shaharsozlik sohasida natijadorlikni ta’minlash, resurslarni hisobga olish va boshqarish tizimini takomillashtirish, obyektlarni loyihalash, qurish va foydalanishni raqamlashtirish borasida ilmiy yechimlarni ishlab chiqadi;
yuridik va jismoniy shaxslarga shaharsozlik faoliyati sohasida byurokratik to‘siqlarni bartaraf etishga qaratilgan zarur choralar ko‘radi;
qurilish sohasida ilg‘or texnologiyalar va innovatsiyalarni joriy etish yuzasidan takliflar tayyorlaydi;
sohani rivojlantirishga qaratilgan uzoq va o‘rta muddatli dasturlar, yo‘l xaritalari, normativ-huquqiy hujjatlar loyihalarini ishlab chiqadi va takliflar kiritadi;
hududlarni ijtimoiy-iqtisodiy jihatdan rivojlantirish dasturlaridagi obyektlarni loyihalashdan boshlab ularni foydalanishga topshirishgacha bo‘lgan bosqichlarda energiya samaradorlikka erishishni ta’minlashga qaratilgan choralar ko‘radi;
O‘zbekiston Respublikasi Prezidenti va Vazirlar Mahkamasining qarorlariga muvofiq infratuzilmani rivojlantirish loyihalari yuzasidan buyurtmachi xizmatlari faoliyatining amalga oshirilishini tashkil etadi va muvofiqlashtiradi;
b) normativ-huquqiy hujjatlar, shaharsozlik normalari va qoidalari hamda standartlarni ishlab chiqishni tashkil etish hamda tasdiqlashni amalga oshirish sohasida:
o‘z vakolatlari doirasida shaharsozlik faoliyati sohasidagi normativ-huquqiy hujjatlar, shuningdek, shaharsozlik normalari va qoidalari loyihalari ishlab chiqilishini tashkil etadi hamda tasdiqlaydi;
shaharsozlik normalari va qoidalarida innovatsion texnologiyalarni joriy qilishdagi muammolarni o‘rganib boradi;
qurilish sohasidagi xalqaro normalar, qoidalar va standartlarni o‘rganish va tahlil qilish asosida milliy normativ hujjatlarni ular bilan uyg‘unlashtirib boradi;
binolar, inshootlar va ularning alohida elementlariga bo‘lgan xavfsizlik talablari, shuningdek, bino va inshootlarning energiya samaradorligini oshirish bo‘yicha chora-tadbirlar ishlab chiqadi;
seysmik xavfsizlik ta’minlash borasida shaharsozlik normalari va qoidalarini xorijiy mamlakatlar tajribasini hisobga olgan holda muntazam ravishda takomillashtiradi;
xorijiy va xalqaro tashkilotlar, konsultantlar va mutaxassislar bilan hamkorlik qilish hamda ularni milliy normativ hujjatlar bazasini takomillashtirishga jalb qiladi;
shaharsozlik normalari va qoidalarini ishlab chiqilishi bo‘yicha belgilangan tartibda buyurtmachi bo‘ladi;
shaharsozlik normalari va qoidalarining davlat fondini shakllantirishda ishtirok etadi;
shaharsozlikda ilmiy-texnika taraqqiyoti muammolarini maqbul tarzda hal etish uchun ilmiy-texnika va texnik kengashlar faoliyatini tashkil etadi;
qonunchilik hujjatlarida belgilangan tartibda qurilish materiallarini olib kelish, ishlab chiqarish va realizatsiya qilishda muvofiqlikni baholash sohasida davlat tomonidan tartibga solishni amalga oshiradi;
texnik reglamentlarning ishlab chiqilishini ta’minlash, ularga o‘zgartish va qo‘shimchalar kiritish, shuningdek mazkur reglamentlarni bekor qilish bo‘yicha takliflar tayyorlash;
v) shaharsozlik faoliyatining barcha bosqichlari raqamlashtirilishini ta’minlash, yuridik va jismoniy shaxslarga ko‘rsatiladigan qurilish va uy-joy kommunal xo‘jaligi sohasida davlat xizmatlarini soddalashtirish va takomillashtirib borish sohasida:
obyektlar qurilish jarayonini elektron monitoring qilishni yo‘lga qo‘yadi va takomillashtirib boradi;
qurilish sohasiga axborot tizimlarini joriy etish ishlarini takomillashtirish orqali inson omilini kamaytirish, byurokratik to‘siqlar va tizimli muammolarni aniqlash, ularni bartaraf etish yuzasidan takliflar ishlab chiqadi;
qurilish sohasiga zamonaviy axborot-kommunikatsiya texnologiyalarini joriy etish bo‘yicha dasturlar ishlab chiqilishini tashkil etadi va ijro etilishini monitoring qiladi;
qurilish sohasiga axborot tizimlarini joriy etishda mas’ul tashkilotlar faoliyatini muvofiqlashtiradi va hamkorligini ta’minlaydi;
joriy etilayotgan axborot tizimlarining joylarda samarali ishlashi va qo‘llanilishining yagona amaliyotini ta’minlaydi;
sohada ko‘rsatilayotgan davlat xizmatlarini rivojlantirish va soddalashtirish bo‘yicha takliflar kiritib boradi;
hududiy bo‘linmalar faoliyatini tahlil qiladi, ular tomonidan amalga oshiriladigan davlat xizmatlarini takomillashtirib boradi;
g) respublika hududini rivojlantirishni rejalashtirish bosh sxemasi, Qoraqalpog‘iston Respublikasi va viloyatlar hududlarini rivojlantirishni rejalashtirish sxemalari, aglomeratsiyalarni rivojlantirish strategiyasi, shaharlar (shaharchalar) bosh rejalari va qishloq aholi punktlarining hududlarini shaharsozlik jihatidan rivojlantirish sxemalarini ishlab chiqish va tasdiqlashni tashkil qilish, shuningdek, ularga rioya etilishi yuzasidan davlat arxitektura nazoratini amalga oshirish sohasida:
respublika hududini rivojlantirishni rejalashtirish bosh sxemasini, Qoraqalpog‘iston Respublikasi va viloyatlar hududlarini rivojlantirishni rejalashtirish sxemalarini va aglomeratsiyalarni rivojlantirish strategiyasini vazirlik va idoralar bilan birgalikda ishlab chiqishni tashkil etadi;
shaharlarning, shaharchalarning bosh rejalari hamda Toshkent shahrining markaziy qismlari va viloyatlarning ma’muriy markazlari, shuningdek, madaniy meros obyektlariga ega bo‘lgan shaharlarni batafsil rejalashtirish loyihalarini tasdiqlaydi (tasdiqlanishi Vazirlar Mahkamasining va boshqa tashkilotlarning vakolatlari jumlasiga kiritilgan shaharsozlik hujjatlari bundan mustasno);
qishloq aholi punktlarining hududlarini shaharsozlik jihatidan rivojlantirish sxemalarini ishlab chiqishni va tasdiqlashni tashkil qiladi;
shaharsozlik sohasida ilmiy-tadqiqot ishlari, aholi punktlarining shaharsozlik hujjatlarini ishlab chiqish bo‘yicha buyurtmachi funksiyalarini bajaradi;
umumdavlat ahamiyatiga molik alohida tartibga solinadigan shaharsozlik obyektlari uchun chegaralar belgilash haqida Vazirlar Mahkamasiga takliflar kiritadi;
davlat shaharsozlik kadastrini yuritadi, aholi punktlarining shaharsozlik hujjatlariga rioya etilishi yuzasidan davlat-arxitektura nazoratini olib boradi;
aholi punktlarining shaharsozlik hujjatlariga Qoraqalpog‘iston Respublikasi Vazirlar Kengashi, viloyatlar va Toshkent shahar, tuman va shahar hokimliklari hamda boshqa tashkilotlar tomonidan amal qilishi yuzasidan davlat-arxitektura nazoratini amalga oshiradi hamda qonunbuzilish holatlari aniqlangan taqdirda, aybdor shaxslarga nisbatan qonunchilik hujjatlarida belgilangan tartibda tegishli choralari qo‘llash bo‘yicha taklif kiritadi;
mahalliy davlat hokimiyati organlariga va maxsus vakolatli davlat organlarining hududiy bo‘linmalariga shaharsozlik to‘g‘risidagi qonun hujjatlarini buzgan holda qabul qilingan qarorlarni bekor qilish haqida taqdimnomalar kiritish;
shaharsozlik faoliyatini amalga oshirishga doir litsenziyalarning va ruxsat beruvchi hujjatlarning amal qilishini to‘xtatib turish, tugatish yoki ularni bekor qilish hamda o‘zboshimchalik bilan qurilgan imoratlarni bartaraf etish to‘g‘risidagi masalani hal etish uchun tegishli davlat organlari va tashkilotlarga taqdimnomalar yuborish;
aholi punktlarining shaharsozlik hujjatlariga amal qilinishi yuzasidan monitoring yuritadi;
shaharsozlik to‘g‘risidagi qonunchilik hujjatlarining buzilishiga oid ma’muriy huquqbuzarliklar to‘g‘risidagi ishlarni ko‘rib chiqadi;
aholi punktlarining shaharsozlik hujjatlaridan barcha foydalanishini ta’minlash uchun vazirlik saytida ularning ochiq grafik qismini e’lon qilib borilishini ta’minlaydi;
d) loyiha tashkilotlari faoliyatini muvofiqlashtirish va ularning ayrim faoliyat turlarini litsenziyalash hamda monitoringini yuritish, yakka tartibdagi, namunaviy, takroran qo‘llaniladigan eksperimental loyihalarni va energiya tejamkor loyiha yechimlari ishlab chiqishni tashkil etish hamda shaharsozlik hujjatlarini ekspertiza qilinishini ta’minlash sohasida:
loyiha tashkilotlari faoliyatini muvofiqlashtiradi va monitoringini yuritadi, yakka tartibdagi, namunaviy, takroran qo‘llaniladigan eksperimental loyihalar va energiya tejamkor loyiha yechimlarini ishlab chiqishni tashkil etadi;
qonunchilik hujjatlarida belgilangan tartibda shaharsozlik faoliyatining ayrim faoliyat turlarini amalga oshirish uchun belgilangan tartibda litsenziyalar beradi;
litsenziyalar reyestrining doimiy yuritilishi va unga tegishli o‘zgartirishlar kiritishni amalga oshiradi;
o‘z vakolatlari doirasida litsenziya talablari va shartlariga rioya qilinishini nazorat qiladi;
belgilangan tartibda litsenziya va akkreditatsiya talablariga rioya qilmagan loyiha va qurilish-pudrat tashkilotlari litsenziya va akkreditatsiya guvohnomalari amal qilishini to‘xtatadi va bekor qiladi;
litsenziatlar tomonidan litsenziya talablari va shartlari bajarilishi to‘g‘risidagi axborot bilan to‘ldirib boriladigan markazlashtirilgan reyestrni (bazasini) yuritadi;
yuridik shaxslarni shaharsozlik hujjatlarini ekspertizadan o‘tkazish yuzasidan akkreditatsiyadan o‘tkazadi;
shaharsozlik hujjatlari, bino va inshootlarni qurish, rekonstruksiya qilish, mukammal ta’mirlash, modernizatsiya qilish, qayta jihozlash bo‘yicha ishlab chiqilgan loyiha-smeta hujjatlarini ekspertizadan o‘tkazadi;
e) pudrat bozorini rivojlantirish, jumladan qurilish-pudrat tashkilotlarining ayrim faoliyat turlarini litsenziyalash va ulush kiritish asosida quriladigan ko‘p xonadonli uylarni ro‘yxatga olishni muvofiqlashtirish, ishlab chiqariladigan qurilish materiallari bazalarini shakllantirish sohasida:
qurilish materiallari, konstruksiyalari va buyumlari hamda qurilish-pudrat tashkilotlari to‘g‘risidagi ma’lumotlarning axborot bazasini shakllantiradi;
qurilish-pudrat tashkilotlarining ayrim faoliyat turlari uchun litsenziyalar beradi;
energiya samarador qurilish materiallari, konstruksiyalari va buyumlarini qo‘llash bo‘yicha takliflar kiritadi;
ulush kiritish asosida uy-joy quruvchilarni elektron ro‘yxatga kiritadi va shartnomalarni hisobga qo‘yadi va mazkur faoliyatni takomillashtirish choralarini ko‘radi;
j) maqsadli dasturlarga kiritilgan obyektlarni monitoring qilish, loyiha va qurilish-pudrat tashkilotlari faoliyatida “Shaffof qurilish” milliy axborot tizimida davlat xaridlari hamda reyting tizimini yuritish sohasida:
maqsadli dasturlarga kiritilgan obyektlarni yer maydonlari ajratilishidan boshlab, “Shaffof qurilish” milliy axborot tizimida tanlov savdolari (tender) o‘tkazilishi, loyiha hujjatlari ishlab chiqilishi, ekspertizadan o‘tkazilishi, qurilish-montaj ishlarini boshlash uchun obyektni ro‘yxatdan o‘tkazilishi va qurib bitkazilgan obyektlarni foydalanishga ruxsat berishgacha bo‘lgan jarayonlar monitoringini yuritadi;
qurilish-pudrat, loyiha-qidiruv tashkilotlari hamda qurilish bilan bog‘liq klasterlarning elektron reytingi tizimini shakllantiradi va yuritadi;
reyting mezon talablarini sohani bosqichma-bosqich rivojlantirish asosida belgilab, undagi qatnashchilarni xalqaro bozorga chiqarish maqsadida, reyting talablari asosida ularni tayyorlaydi;
reyting tizimini qurilish obyektlari hamda qurilish-montaj ishlarining ixtisosligidan kelib chiqqan holda sohaviy shakllanishini hamda metodologik ta’minlaydi;
reytingga kirgan tashkilotlarning soni va turi statistikasini yuritadi;
reyting mezonlarini belgilashda ishtirok etadi, ularni elektron, inson omilisiz hamda integratsiya yo‘li bilan avtomatik ravishda olinishini ta’minlaydi;
qurilish sohasidagi “Shaffof qurilish” milliy axborot tizimida inson omilisiz elektron tanlov savdolari (tender) tizimi yuritilishini ta’minlaydi, uning shartlarini belgilaydi hamda tizimni ishlash amaliyotidan kelib chiqqan holda takomillashtirib boradi;
autsorsing xizmatini ko‘rsatuvchi buyurtmachi tashkilotlarining bozori shakllangandan keyin ularning reytingini shakllantiradi, ularni davlat xaridlari uchun jalb etish bo‘yicha inson omilisiz elektron tanlov savdolari (tender) tizimini yaratadi va rivojlantirish yuzasidan takliflar kiritadi;
z) obyektlar qurilishi jarayonida mualliflik, texnik va ichki nazorat olib borilishi ustidan davlat nazorati o‘rnatilishini ta’minlash, shuningdek, respublika hududida qurilayotgan bino va inshootlarning seysmik xavfsizligi ta’minlanishini shaharsozlik normalari va qoidalari talablariga muvofiqligini baholash sohasida:
buyurtmachining texnik, loyiha tashkilotining mualliflik va qurilish-pudrat tashkilotining ichki nazorat tizimining sifatli olib borilishini nazorat qilinishini ta’minlash;
obyektlarda bajariladigan qurilish-montaj ishlarining shaharsozlik to‘g‘risidagi qonunchilik hujjatlari va shaharsozlik normalari va qoidalari talablariga rioya etilishi bo‘yicha davlat nazorati amalga oshirilishini ta’minlaydi va muvofiqlashtiradi;
shaharsozlik to‘g‘risidagi qonunchilik hujjatlarining buzilishiga oid ma’muriy huquqbuzarliklar to‘g‘risidagi ishlarni ko‘rib chiqadi;
shaharsozlikka oid qonunchilik, shaharsozlik normalari va qoidalarini takomillashtirishga doir takliflar kiritadi;
qurilishi tugallangan obyektlarni foydalanishga qabul qilish ishlarida ishtirok etadi;
qurilayotgan bino va inshootlarning seysmik xavfsizligi ta’minlanishini shaharsozlik normalari va qoidalari talablariga muvofiqligi baholanishini va nazorat qilinishini ta’minlaydi;
elektron texnik pasportlar asosida bino va inshootlarni seysmik jihatdan zaifligi bo‘yicha tasniflashda, shuningdek, bino va inshootlarning instrumental-texnik tekshiruvi hamda ularning zilzilabardoshligini oshirishda ishtirok etadi;
seysmik faol zonalarda qurilishi rejalashtirilayotgan barcha turdagi bino va inshootlarning, shuningdek, ko‘p qavatli turar joylarning seysmik jihatdan zaifligini baholash uchun zarur bo‘lgan axborotni o‘z ichiga oladigan elektron texnik pasportlarni shakllantiradi;
qurilayotgan bino va inshootlarning zilzilabardoshligini ta’minlash, shuningdek, mavjud bino va inshootlarning zilzilabardoshligini oshirish uchun zarur choralar ko‘rish bo‘yicha takliflar va tavsiyalar tayyorlaydi;
i) uy-joy kommunal xo‘jaligi sohasida yagona davlat siyosatini yuritish va tarmoqlararo muvofiqlashtirishni amalga oshirish sohasida:
jismoniy va yuridik shaxslarning uy-joy kommunal xizmatlarga bo‘lgan talabini o‘rganishni tashkil qiladi, iste’mol ehtiyojlari, uy-joy kommunal xo‘jaligini rivojlantirish istiqbollarini inobatga oladigan ijtimoiy-iqtisodiy prognozlarni tuzadi;
uy-joy kommunal xo‘jaligi sohasini rivojlantirishning ustuvor yo‘nalishlarini belgilaydi va shu asosda tarmoq va tarmoqlararo hamkorlikni shakllantiradi;
iste’molchilarni uy-joy kommunal xizmatlar bilan ta’minlash darajasini oshirish, energiya tejaydigan zamonaviy texnologiyalarni joriy qilish hisobga olinib, ularning sifatini yaxshilashga qaratilgan dasturlarni ishlab chiqishda ishtirok etadi;
ko‘p kvartirali uy-joy fondi hamda issiqlik ta’minoti sohasida qurilish, rekonstruksiya, kapital ta’mirlash va modernizatsiya qilishga doir dasturlar, shuningdek, hududlarning kommunal infratuzilmasini yanada rivojlantirish strategiyasini ishlab chiqadi;
Vazirlik tizimidagi tashkilotlarning aholi, ijtimoiy soha obyektlari va iqtisodiyot tarmoqlariga uy-joy kommunal xo‘jaligi barqarorligini ta’minlashga doir ishlarini tashkil etadi;
Vazirlik tizimidagi uy-joy kommunal xo‘jaligi sohasi obyektlarining barqaror faoliyatini, respublika hududida uy-joy kommunal xo‘jaligiga oid normativ talablarga rioya etilishini ta’minlaydi;
mahalliy ijro etuvchi hokimiyat organlari va fuqarolarning o‘zini o‘zi boshqarish organlari bilan ko‘p kvartirali uylarning saqlanishini ta’minlash, uy-joy kommunal xo‘jaligi obyektlarining barqaror ishlashi, shuningdek, uy-joy kommunal xo‘jaligi sohasini rivojlantirish istiqbollari masalasi yuzasidan o‘zaro hamkorlik qiladi;
Vazirlik tizimidagi issiqlik ta’minoti tashkilotlari faoliyati samaradorligining iqtisodiy normativlarini (ko‘rsatkichlarini) tasdiqlaydi, yagona iqtisodiy, texnikaviy va hisobdorlik siyosatini yuritadi, tashkilotlar ishlari samaradorligini atroflicha tahlil qilish jarayoniga metodologik rahbarlik qiladi;
mahalliy ijro etuvchi hokimiyat organlari bilan hamkorlikda uy-joy kommunal xo‘jaligi sohasida xo‘jalik mexanizmi va boshqaruv tuzilmasini takomillashtirish, uy-joy kommunal xo‘jaligi tashkilotlari faoliyatini yaxshilash bo‘yicha iqtisodiy vositalar va rag‘batlantirish ta’sirini kuchaytirish hamda aholiga xo‘jaligi madaniyatini oshirish choralarini ko‘radi;
respublika aholisini uy-joy kommunal xo‘jaligi tashkilotlari faoliyati to‘g‘risida muntazam xabardor qiladi, jismoniy shaxslar, shu jumladan yakka tartibdagi tadbirkorlar va yuridik shaxslarning Vazirlik vakolatiga kiritilgan masalalarga taalluqli murojaatlarini qonunchilikda belgilangan tartibda ko‘rib chiqadi va zarur choralar ko‘radi, jismoniy shaxslar va yuridik shaxslarning vakillarini qabul qiladi, ularning murojaatlari bilan bog‘liq ishlar holatini tahlil qiladi;
k) ko‘p kvartirali uylarni boshqarish, shu jumladan, ko‘p kvartirali uy-joy fondini mukammal va joriy ta’mirlash ishlarini, eski va avariya holatidagi uy-joylar buzilishini tashkil etishni muvofiqlashtirish hamda renovatsiya dasturlarini shakllantirish sohasida:
ko‘p kvartirali uylarni boshqarish sohasida yagona davlat siyosatini olib boradi;
ko‘p kvartirali uy-joy fondini saqlash va ulardan foydalanish tizimini kompleks rivojlantirish va takomillashtirish bo‘yicha takliflar ishlab chiqadi;
uy-joy kommunal xo‘jaligi sohasini boshqarish borasida ko‘p kvartirali uy-joy fondidan foydalanish va ularga xizmat ko‘rsatish bozorini rivojlantirish yuzasidan chora-tadbirlar ishlab chiqadi;
ko‘p kvartirali uylardan va ko‘p kvartirali uylarga tutash yer uchastkalaridan foydalanish hamda ularni saqlash bo‘yicha tizimli ishlarni samarali tashkil qilishda ishtirok etadi;
ko‘p kvartirali uylarning texnik holati monitoringini o‘tkazish bo‘yicha ishlarni tashkil etadi;
ko‘p kvartirali uylarni boshqarish organlari reyestrini shakllantiradi va yuritadi;
ko‘p kvartirali uylar mulkdorlarining majburiy badallari yoki to‘lovlari miqdorini belgilashda xarajatlarning tarkibini aniqlash tartibini tasdiqlaydi;
ko‘p kvartirali uylar mulkdorlarining majburiy badallari yoki to‘lovlarining eng kam miqdorini belgilaydi;
ko‘p kvartirali uylarni boshqarish sohasida tizimli monitoringning amalga oshirilishini ta’minlaydi;
ko‘p kvartirali uylarni mavsumiy foydalanishga o‘z vaqtida va sifatli tayyorlanishini tashkil qilishda ishtirok etadi;
novatorlik faoliyati va zamonaviy innovatsion texnologiyalarni joriy etishga ko‘maklashadi;
ko‘p kvartirali uylarni boshqarish organlarining hisobi va ularning faoliyati bo‘yicha hisobotlarni yuritish uchun axborot tizimlari hamda axborot resurslarini joriy etadi;
ko‘p kvartirali uylarni boshqarish organlarining ko‘p kvartirali uylardagi joylar mulkdorlarining huquqlari va qonuniy manfaatlari bilan bog‘liq faoliyati to‘g‘risidagi axborotni, shuningdek, ko‘p kvartirali uylarni boshqarish organlarining reyestrini vazirlikning rasmiy veb-saytiga joylashtiradi va yangilab turadi;
ko‘p kvartirali uylarni boshqarish bo‘yicha axborot va maslahat yordami ko‘rsatadi;
ko‘p kvartirali uylarni boshqarish organlarining faoliyatiga huquqiy hamda uslubiy jihatdan ko‘maklashadi;
uy-joy kommunal xo‘jaligi sohasidagi yagona billing tizimini yuritadi;
rivojlangan xorijiy mamlakatlarning ilg‘or tajribasini inobatga olgan holda ko‘p kvartirali uylarni boshqarishning zamonaviy shakllari va usullarini ishlab chiqadi;
boshqarish organlarining mansabdor shaxslari va boshqaruvchilari hamda mutaxassislarini malakasini oshirishni tashkil etadi hamda ularga malaka sertifikatini beradi;
ko‘p kvartirali uylarni boshqarish organlari reytingini yuritadi;
ko‘p kvartirali uy-joy fondini rekonstruksiya qilish va ta’mirlash bo‘yicha loyiha-smeta hujjatlarini ishlab chiqish va bu boradagi ishlarni amalga oshirishga doir manzilli ro‘yxatlarni tasdiqlaydi;
ko‘p kvartirali uy-joy fondini ta’mirlash, obodonlashtirish va undan foydalanish sharoitlarini yaxshilash bo‘yicha dasturlarni amalga oshirish doirasida jalb qilingan buyurtmachilar, loyiha va pudrat tashkilotlari ishlarini muvofiqlashtiradi hamda monitoringini yuritadi;
ko‘p kvartirali uyda energiyani tejash va energetika samaradorligini oshirish bo‘yicha tadbirlarni belgilaydi;
ko‘p kvartirali uylarni rekonstruksiya qilish, ulardan foydalanish, saqlash va ta’mirlash, ularning texnik va foydalanish holatini yaxshilash masalasida joylardagi ijro hokimiyati organlari faoliyatiga ko‘maklashadi;
ko‘p kvartirali uy-joy fondining eskirish va avariya holati monitoringini yuritadi va ular asosida renovatsiya dasturlarini ishlab chiqadi;
l) hududlarni obodonlashtirish sohasi muammolarini o‘rganish va metodologik ta’minlash sohasida:
aholi punktlari hududlarini obodonlashtirish sohasida normativ-huquqiy baza, standartlar, shaharsozlik normalari va qoidalarini ishlab chiqadi va yangilaydi;
obodonlashtirish sohasida davlat mablag‘laridan foydalanish samaradorligi va ko‘rsatiladigan xizmatlar sifatini oshirishga qaratilgan davlat-xususiy sheriklik mexanizmlarini bosqichma-bosqich joriy qilish bo‘yicha chora-tadbirlar ishlab chiqadi;
obodonlashtirish sohasiga yo‘naltirilgan innovatsion yechimlarni joriy qiladi;
obodonlashtirish sohasi xodimlariga munosib mehnat sharoitlarini yaratish, ularni moddiy rag‘batlantirish va ijtimoiy muhofaza qilish darajasini oshirish bo‘yicha takliflarni tayyorlaydi;
m) ichimlik suvi ta’minoti va oqova suvlarni chiqarib yuborish sohasida yagona davlat siyosatini amalga oshirish, rivojlantirish, davlat nazoratini ta’minlash, sohada xizmat ko‘rsatuvchilar faoliyatini muvofiqlashtirish sohasida:
ichimlik suvi ta’minoti va oqova suvlarni chiqarib yuborish sohasidagi dasturlar, strategiyalar hamda konsepsiyalarni ishlab chiqishda ishtirok etadi;
aholining ichimlik suvi ta’minoti va oqova suvlarni chiqarib yuborish bo‘yicha xizmatlar bilan qamrab olinishini monitoring qiladi;
aholi punktlarini ichimlik suvi ta’minoti va oqova suvlarni chiqarib yuborish bo‘yicha xizmatlar bilan ta’minlash uchun davlat ijtimoiy buyurtmalarini beradi;
ichimlik suvi ta’minoti va oqova suvlarni chiqarib yuborish bo‘yicha xizmatlar ko‘rsatilishida xususiy sektorning ishtirokini kengaytirish, shu jumladan davlat-xususiy sheriklik asosida kengaytirish bo‘yicha chora-tadbirlar ishlab chiqadi;
ichimlik suvi ta’minoti va oqova suvlarni chiqarib yuborish sohasidagi normativ-huquqiy hujjatlar loyihalarini ishlab chiqadi;
ichimlik suvi ta’minoti va oqova suvlarni chiqarib yuborish sohasidagi tarif siyosatini shakllantirishga ko‘maklashadi;
O‘zbekiston Respublikasidagi ichimlik suvi ta’minoti va oqova suvlarni chiqarib yuborish tizimlarini rivojlantirishning bosh sxemasi ishlab chiqilishida ishtirok etadi;
ichimlik suvi ta’minoti va oqova suvlarni chiqarib yuborish sohasidagi ilmiy tadqiqotlarni tashkil etadi hamda muvofiqlashtiradi;
ichimlik suvi ta’minoti va oqova suvlarni chiqarib yuborish sohasida xodimlarga bo‘lgan ehtiyojni tahlil qiladi hamda ularni tayyorlashda ishtirok etadi;
davlat ijtimoiy buyurtmalari asosida faoliyat yuritayotgan ichimlik suvi ta’minoti tashkilotlari reyestrini yuritadi va uni o‘zining rasmiy veb-saytida joylashtiradi, ular faoliyati samaradorligining asosiy mezonlarini belgilaydi va baholaydi;
ichimlik suvi va oqova suvlarni tozalash inshootlarini texnologik auditdan o‘tkazish hamda suvni tejash bo‘yicha zarur choralarni belgilashda ishtirok etadi;
ichimlik suvi ta’minoti tashkilotlari uchun suvning texnologik jihatdan sarflanishini va yo‘qotilishini hisob-kitob qilish tartibi, shuningdek, ularning yo‘l qo‘yiladigan normalarini (limitlarini) belgilaydi;
davlat ijtimoiy buyurtmalari asosida faoliyat yuritayotgan ichimlik suvi ta’minoti tashkilotlari tomonidan ijro etilishi majburiy bo‘lgan hujjatlarni o‘z vakolatlari doirasida qabul qiladi;
ichimlik suvi ta’minoti va oqova suvlarni chiqarib yuborish obyektlarini qurish hamda rekonstruksiya qilishga doir aniq yo‘naltirilgan dasturlarni shakllantirishda ishtirok etadi;
xalqaro moliya institutlari ishtirokida amalga oshiriladigan ichimlik suvi ta’minoti va oqova suvlarni chiqarib yuborish sohasidagi loyihalar bo‘yicha loyihaoldi hamda loyiha hujjatlarini ishlab chiqish, kelishib olish va tasdiqlash choralarini ko‘radi, shuningdek, loyihalarni moliyalashtirishni amalga oshiradi;
davlat ijtimoiy buyurtmalari asosida faoliyat yuritayotgan ichimlik suvi ta’minoti va oqova suv chiqarish sohasidagi ekspluatatsiya va xo‘jaligini kengaytirish faoliyatini muvofiqlashtiradi;
ichimlik suvi ta’minoti va oqova suvlarni chiqarib yuborish sohasida davlat nazoratini tashkil etadi va muvofiqlashtiradi;
n) issiqlik ta’minoti sohasini kompleks rivojlantirish, yagona texnik va tarif siyosatini yuritish sohasida:
issiqlik ta’minoti sohasidagi dasturlar, strategiyalar hamda konsepsiyalarni ishlab chiqadi va monitoringini yuritadi;
issiqlik energiyasini ishlab chiqarish va texnologik jarayonlar samaradorligini oshirish, belgilangan talablarga muvofiq uning sifatini ta’minlash, ishlab chiqarish jarayoniga energiya va suvni tejaydigan texnologiya hamda uskunalarni joriy qilishga qaratilgan chora-tadbirlarni belgilaydi;
respublika hududida issiqlik ta’minoti obyektlarini loyihalashtirish, qurish, modernizatsiya va rekonstruksiya qilishda yagona siyosat yuritadi;
issiqlik ta’minoti obyektlari qurilishi bo‘yicha buyurtmachi funksiyalarining bajarilishini tashkil etadi;
issiqlik ta’minoti obyektlarini o‘z vaqtida ishga tushirish bo‘yicha manzilli dasturlar ishlab chiqadi, kelishadi va tasdiqlaydi;
issiqlik ta’minoti sohasida yagona tarif siyosatini yuritadi;
issiqlik ta’minoti tashkilotlari reyestrini yuritadi va uni o‘zining rasmiy veb-saytida joylashtiradi, ular faoliyati samaradorligining asosiy mezonlarini belgilaydi va baholaydi;
issiqlik ta’minoti korxonalarini energo auditdan o‘tkazishni tashkil etadi;
issiqlik ta’minoti tizimida issiqlik energiyasining sarflanishi va yo‘qotilishini hisob-kitob qilish tartibini, shuningdek, ularning yo‘l qo‘yiladigan normalarini (limitlarini) belgilaydi;
issiqlik ta’minoti sohasidagi loyihalarning ro‘yobga chiqarilishi monitoringini mahalliy davlat hokimiyati vakillik organlari bilan hamkorlikda amalga oshiradi;
yangi quriladigan ko‘p kvartirali uylar, ijtimoiy soha va boshqa obyektlarni sifatli va uzluksiz issiqlik energiyasi bilan ta’minlash yuzasidan takliflar kiritadi;
issiqlik energiyasini ishlab chiqarish, yetkazib berish va iste’mol qilish talablarini o‘z ichiga olgan issiqlik ta’minoti tizimida standartlashtirish va metrologiya bo‘yicha yagona siyosatni amalga oshiradi, bunda barcha turdagi energiya sarfini hisoblash zamonaviy o‘lchov vositalarini o‘rganish, joriy etish va ulardan foydalanish bo‘yicha chora-tadbirlarni ishlab chiqadi;
o) uy-joy kommunal xo‘jaligi sohasida (uy-joy fondi, ichimlik suvi ta’minoti va oqova suvni chiqarish xizmatlari hamda issiqlik ta’minoti) davlat budjeti, xalqaro moliya institutlari, xorijiy hukumat moliya tashkilotlarining mablag‘lari hamda davlat-xususiy sheriklik hisobidan investitsiya loyihalarini amalga oshirish sohasida:
Vazirlikka yuklangan vazifalar doirasida investitsiya siyosatini shakllantirish va amalga oshirishni muvofiqlashtiradi;
Davlat budjeti mablag‘lari va chet el investitsiyalarini jalb etish uchun mas’ul vakolatli davlat organlari bilan o‘zaro hamkorlik qiladi;
xalqaro moliya institutlari, xorijiy hukumat moliya tashkilotlari hamda donor mamlakatlar bilan ularni uy-joy kommunal xo‘jaligi sohasiga oid investitsiya loyihalarini amalga oshirishga jalb qilish bo‘yicha istiqbolli va joriy hamkorlik takliflarini o‘rganadi, umumlashtiradi va dasturlarni tayyorlaydi;
investitsiya loyihalarining loyiha-smeta hujjatlari, texnik-iqtisodiy asoslar va boshqa loyihalarga oid hisob-kitoblarni xalqaro moliya institutlari va xorijiy hukumat moliya tashkilotlari kreditlarini jalb etgan holda, o‘z vaqtida ishlab chiqilishi bo‘yicha choralar ko‘radi;
davlat-xususiy sheriklikni joriy etish va rivojlantirish bo‘yicha hamda sohaga xususiy sektorni jalb qilish bo‘yicha takliflar ishlab chiqadi;
uy-joy kommunal xo‘jaligi sohasini rivojlantirish uchun taqdim etiladigan xalqaro va xorijiy hukumat moliya institutlarining texnik ko‘mak (grant) mablag‘laridan foydalanishning ustuvor yo‘nalishlarini belgilaydi;
O‘zbekiston Respublikasi Hukumati nomidan qonunchilikda belgilangan holatlarda xorijiy investorlar bilan investitsiya shartnomalari tuzadi;
p) qurilish va uy-joy kommunal xo‘jaligi sohasida xodimlarni mahalliy va xorijiy yetakchi ta’lim muassasalarida tayyorlash, qayta tayyorlash, malakasini oshirish va kompetensiyasini baholash ishlarini tashkil etish hamda sertifikatlash jarayonlarini muvofiqlashtirish sohasida:
ilg‘or xorijiy tajribalarni inobatga olgan holda qurilish va uy-joy kommunal xo‘jaligi sohasiga oid tegishli yo‘nalishlarda xodimlarni maqsadli tayyorlash, qayta tayyorlash va malakasini oshirishni tashkil etadi hamda o‘quv jarayonlari uchun yangi malaka talablari, o‘quv rejalari va dasturlarini ishlab chiqishda ishtirok etadi;
qurilish va uy-joy kommunal xo‘jaligi sohasida kasbiy malaka va bilimlarni rivojlantirish bo‘yicha tarmoq kengashi faoliyatini yuritadi;
qurilish va uy-joy kommunal xo‘jaligi sohasida mutaxassislarni qayta tayyorlash va malakasini oshirish, malakani baholash va sertifikatlash ishlarini amalga oshiradi;
raqamli texnologiyalar asosida mutaxassislar to‘g‘risidagi tahliliy ma’lumotlar hamda sertifikatlangan mutaxassislar reyestrini yuritadi;
qurilish va uy-joy kommunal xo‘jaligi sohasiga tegishli bo‘lgan kasblar bo‘yicha kasbiy standartlarni ishlab chiqish va joriy etish bo‘yicha faoliyatni amalga oshiradi;
malakani baholash va sertifikatlash ishlariga mahalliy hamda xorijiy ekspertlarni keng jalb etadi;
soha mutaxassislari va ta’lim oluvchilarni nufuzli mahalliy hamda xorijiy tashkilotlarda stajirovka va amaliyotini tashkil etishda ko‘maklashadi;
sohadagi yosh xodimlarning bilimi, iqtidorini yanada oshirish uchun ilmiy-amaliy konferensiya va ko‘rik tanlovlar o‘tkazadi;
vazirlik va idoralar bilan tegishli vazifa hamda topshiriqlarni o‘zaro hamkorlikda amalga oshirishni tashkil etadi.'),
        (1386, 136, '2024-05-29 17:30:50', '2024-05-29 17:30:50', '12. Vazirlik o‘ziga yuklangan vazifalar va funksiyalarni bevosita'),
        (1400, 136, '2024-05-29 17:30:50', '2024-05-29 17:30:50', '26. Vazirlik:
unga yuklangan vazifalar va funksiyalarning lozim darajada va samarali bajarilishi, shuningdek, tegishli sohada davlat siyosati amalga oshirilishi;
tegishli sohadagi faoliyatning asosiy ko‘rsatkichlari parametrlariga va belgilangan indikatorlarga erishishning so‘zsiz ta’minlanishi;
joriy etilayotgan innovatsiyalar, g‘oyalar va texnologiyalar, shuningdek, Vazirlik tomonidan O‘zbekiston Respublikasi Prezidenti Administratsiyasi va Vazirlar Mahkamasiga kiritiladigan normativ-huquqiy hujjatlar va boshqa hujjatlar loyihalarini, shuningdek, Vazirlik tomonidan qabul qilinadigan normativ-huquqiy hujjatlarni amalga oshirish sifati, yakuniy natijalari va oqibatlari;
tegishli sohani rivojlantirish dasturlari, chora-tadbirlar rejalari, “yo‘l xaritalari” va boshqa hujjatlar samarali amalga oshirilishi hamda hududlardagi muammoli masalalar hal etilishi;
fuqarolar va tegishli sohadagi tadbirkorlik subyektlarining huquqlar va qonuniy manfaatlariga rioya etilishi va ularning himoya qilinishi ta’minlanishi, sohada raqamlashtirishni ta’minlash va korrupsion omillarni bartaraf etish uchun javob beradi.'),
        (1401, 136, '2024-05-29 17:30:50', '2024-05-29 17:30:50', '27. Bosh boshqarma o‘ziga yuklangan vazifalar va funksiyalarni bajarish uchun quyidagi huquqlarga ega:
a) shaharsozlik faoliyati sohasida:
davlat organlari va tashkilotlarining hududiy bo‘linmalaridan, mahalliy davlat hokimiyati organlaridan o‘z vakolatiga kiruvchi masalalarni hal etishga doir ma’lumotlarni belgilangan tartibda so‘rash va olish;
davlat organlari va tashkilotlarining hududiy bo‘linmalari, mahalliy davlat hokimiyati organlariga o‘z vakolatiga kiruvchi masalalar bo‘yicha takliflarni ko‘rib chiqish uchun kiritish;
davlat organlari va tashkilotlarining hududiy bo‘linmalari, mahalliy davlat hokimiyati organlariga arxitektura va qurilishni rivojlantirish maqsadida zarur sharoitlarni yaratishda yo‘l qo‘yilgan kamchiliklarni bartaraf etish to‘g‘risida majburiy ko‘rib chiqish uchun taqdimnomalar kiritish;
o‘z vakolatiga kiruvchi masalalarni hal etish uchun shartnoma asosida tashkilotlar, olimlar va mutaxassislar, shu jumladan, chet el olimlari va mutaxassislarini jalb etish;
obyektlar loyihalarini shaharsozlik to‘g‘risidagi qonunchilik, shuningdek, shaharsozlik normalari va qoidalari talablariga muvofiqligini ko‘rib chiqish, ularni kelishishda ijobiy yoki salbiy xulosalar berish;
shaharsozlik faoliyati subyektlaridan shaharsozlik to‘g‘risidagi qonunchilik hujjatlariga hamda shaharsozlik normalari va qoidalariga rioya etilishini talab qilish;
moliyalashtirilishi markazlashtirilgan manbalar hisobiga amalga oshirilayotgan qurilish obyektlari bo‘yicha buyurtmachilar, loyihachilar va pudratchilar tomonidan bajarilishi monitoringini olib borish;
o‘z faoliyatida shaharsozlik hujjatlarini ishlab chiquvchilar va ilmiy-tadqiqot tashkilotlari bilan o‘zaro ijodiy hamkorlikni amalga oshirish;
Vazirlikka shaharsozlik to‘g‘risidagi qonunchilik hujjatlarini shaharsozlik normalari va qoidalari talablarini buzgan holda ishlayotgan shaharsozlik faoliyati subyektlarining qurilish sohasidagi hamda arxitektura-shaharsozlik hujjatlarini yaratish faoliyati huquqini beruvchi litsenziyalarning amal qilishini to‘xtatib turish yoki bekor qilish to‘g‘risida takliflar kiritish;
olimlar hamda vazirliklar, idoralar, ta’lim va boshqa muassasalarning yuqori malakali mutaxassislarini ilmiy va ekspert ishlari, maslahatlar o‘tkazish uchun, arxitektura va qurilish sohasining dolzarb masalalarini ishlab chiqish uchun Vazirlik bilan kelishgan holda xorijiy mutaxassislarni taklif etish;
o‘z vakolatlari doirasida konferensiyalar, seminarlar, simpoziumlar, ko‘rgazmalar, yarmarkalar va boshqa tadbirlarni o‘tkazish, shuningdek, boshqa tashkilotlar tomonidan o‘tkaziladigan shunday tadbirlarda ishtirok etish;
mintaqada arxitektura va qurilishni rivojlantirishga yo‘naltirilgan tadbirlarni birgalikda ishlab chiqish maqsadida xalqaro, xorijiy va respublika tashkilotlari bilan hamkorlikni belgilangan tartibda yo‘lga qo‘yish;
b) uy-joy kommunal xo‘jaligi sohasida:
tashkilotlardan o‘z vakolatiga kiradigan masalalar bo‘yicha hujjatlar, statistika ma’lumotlar, tahliliy materiallar, xulosa va boshqa axborotlarni so‘rab oladi;
tashkilotlardan ichimlik suvi ta’minoti, oqova suvlarni chiqarib yuborish va issiqlik ta’minoti obyektlarini qurish, rekonstruksiya qilish, mukammal va joriy ta’mirlash, ko‘p kvartirali uy-joy fondini mukammal va joriy ta’mirlash, ko‘p kvartirali uy-joy fondiga tutash hududlarni obodonlashtirish bo‘yicha dasturlar loyihalarini ishlab chiqish uchun zarur hisob-kitoblar, hisobot ma’lumotlari va boshqa materiallarni oladi;
ko‘p kvartirali uylarni boshqarish organlarining elektron reyestridan boshqarish organlarini chiqarish bo‘yicha “Kommunal ta’minot markazi” davlat muassasasiga ko‘rib chiqish uchun takliflar kiritadi;
vazirlik va viloyat hokimligiga ko‘rib chiqish uchun bosh boshqarma vakolatiga kiradigan masalalar yuzasidan takliflar kiritadi;
tashkilotlar va fuqarolarning o‘zini o‘zi boshqarish organlariga ko‘rib chiqilishi shart bo‘lgan uy-joy kommunal xo‘jaligi sohasidagi qonunbuzarliklarni bartaraf etish to‘g‘risida taqdimnomalar yuboradi;
tashkilotlar rahbarlarini uy-joy kommunal xo‘jaligi sohasidagi qonunchilik talablari va normativ hujjatlarni bajarmagani yoki tegishli ravishda bajarmagani uchun belgilangan tartibda javobgarlikka tortish to‘g‘risida ko‘rib chiqish uchun vazirlikka, viloyat hokimligiga yoki boshqa tegishli organlarga takliflar kiritadi;
viloyat hokimligiga ko‘rib chiqish uchun takliflar kiritadi;
bosh boshqarma va uning tarkibiga kiradigan tashkilotlarning moddiy-texnika bazasini yaxshilash choralarini ko‘radi;
bosh boshqarma tizimida budjet mablag‘laridan oqilona foydalanadi, boshqarma tarkibiga kiradigan korxonalarning moliyaviy va xo‘jalik faoliyatini tekshiradi va taftish qiladi.
Bosh boshqarma qonunchilik hujjatlariga muvofiq boshqa huquqlarga ham ega bo‘lishi mumkin.'),
        (1387, 136, '2024-05-29 17:30:50', '2024-05-29 17:30:50', '13. Qoraqalpog‘iston Respublikasi Qurilish va uy-joy kommunal xo‘jaligi vazirligi va viloyatlar Qurilish va uy-joy kommunal xo‘jaligi bosh boshqarmalarining vazifalari quyidagilar hisoblanadi:
a) shaharsozlik faoliyati sohasidagi vazifalari:
hududda Vazirlikka yuklangan vazifalarni samarali bajarish;
shaharsozlik faoliyati yo‘nalishlari bo‘yicha takliflarni tayyorlash, hududlarni ijtimoiy-iqtisodiy rivojlantirishning tarmoq va hududiy dasturlarini tayyorlashda ishtirok etish;
yuqori samarali va energiyani tejaydigan loyiha yechimlari va qurilish materiallari, zamonaviy qurilish texnologiyalari va innovatsiya loyihalarini tatbiq etishda ko‘maklashish;
aholi punktlarining shaharsozlik hujjatlarini ishlab chiquvchilar va mutaxassislarning malakasini doimiy ravishda oshirish va ularni qayta tayyorlashni tashkil qilishda ishtirok etish;
tegishlicha shaharlar va tumanlar hokimlari, prokuratura, ichki ishlar va soliq xizmati hududiy organlari rahbarlari boshqaradigan kompleks ijtimoiy-iqtisodiy rivojlanish sektorlari (keyingi o‘rinlarda kompleks ijtimoiy-iqtisodiy rivojlanish sektorlari deb ataladi) bilan hamkorlik qilish;
shaharsozlik faoliyati sohasida xodimlarni tayyorlash, qayta tayyorlash va malakasini oshirish, qurilish-pudrat tashkilotlarini malakali quruvchi, loyihachi va boshqa mutaxassislar bilan ta’minlashda ishtirok etish;
b) uy-joy kommunal xo‘jaligi sohasidagi vazifalari:
Vazirlikning uy-joy kommunal xo‘jaligi sohasidagi yagona davlat siyosatini hudud miqyosida tatbiq etish va tarmoqlararo muvofiqlashtirishni amalga oshirish;
ko‘p kvartirali uylarni boshqaruv organlari faoliyatini muvofiqlashtirish va ko‘p kvartirali uylarning texnik holatini monitoring qilish;
ko‘p xonadonli uy-joy fondini mukammal va joriy ta’mirlash ishlarini monitoring qilish;
ko‘p kvartirali uy-joy fondini saqlash, ulardan foydalanish va umumiy mulkni saqlashga qaratilgan majburiy badallar miqdorini aniqlash yuzasidan boshqaruv organlariga uslubiy ko‘maklashish;
ichimlik suv va oqova suvlarni chiqarib yuborish obyektlarini, issiqlik ta’minoti tizimlarini modernizatsiya va rekonstruksiya qilish dasturlari amalga oshirilishini monitoring qilish;
issiqlik ta’minoti tizimida, jumladan qayta tiklanadigan energiya manbalaridan foydalanib, energiya va resursni tejaydigan yangi texnologiya va uskunalarni joriy qilish bo‘yicha kompleks tadbirlarni amalga oshirish;
markaziy qozonxonalar, issiqlik tarmoqlarini modernizatsiya va rekonstruksiya qilish borasida dasturlarni amalga oshirish va monitoringini yuritish;
rivojlangan xorijiy mamlakatlarning ilg‘or tajribasini inobatga olgan holda uy-joy kommunal xo‘jaligi sohasida normativ-huquqiy bazani takomillashtirish bo‘yicha takliflar kiritish;
ichimlik suv ta’minoti va oqova suvlarni chiqarib yuborish xizmatlari, issiqlik ta’minoti bo‘yicha ko‘rsatiladigan xizmatlarga tarif siyosatini shakllantirish hamda uy-joy kommunal xo‘jaligi sohasi tashkilotlarining iqtisodiy barqarorligini mustahkamlash yuzasidan takliflar ishlab chiqish;
uy-joy kommunal xo‘jaligi sohasining hududdagi tashkilotlari xodimlarini tayyorlash, qayta tayyorlash va malakalarini oshirish bo‘yicha ehtiyojni o‘rganish, shu yuzasidan takliflar kiritish;
bosh boshqarma va uning tashkiliy tuzilmasidagi tashkilotlarda axborot kommunikatsiya texnologiyalari faoliyatini yo‘lga qo‘yish va barqarorligini ta’minlash;
ichimlik suvi ta’minoti va oqova suvlarni chiqarib yuborish sohasini rivojlantirish va tartibga solishda ishtirok etish, sohada xizmat ko‘rsatuvchilar faoliyatiga ko‘maklashish;
bosh boshqarma va uning tashkiliy tuzilmasidagi tashkilotlarda balans va moliyaviy hisobotlarni qabul qilish, tahlil qilish hamda Vazirlikka taqdim qilish.'),
        (1388, 136, '2024-05-29 17:30:50', '2024-05-29 17:30:50', '14. Qoraqalpog‘iston Respublikasi Qurilish va uy-joy kommunal xo‘jaligi vazirligi, viloyatlar Qurilish va uy-joy kommunal xo‘jaligi bosh boshqarmalarining o‘ziga yuklangan asosiy vazifalarga muvofiq quyidagi funksiyalarni amalga oshiradi:
a) shaharsozlik faoliyati sohasidagi funksiyalari:
kelishilgan loyiha hujjatlarining fasad, kichik arxitektura shakllari hamda boshqa arxitekturaviy yechimlaridan jismoniy va yuridik shaxslar tomonidan chekinish sodir etilganda mazkur holatlarga nisbatan chora ko‘rish uchun to‘plangan materiallarni vazirlikning tegishli bo‘linmasiga, mahalliy hokimiyat organlari hamda huquqni muhofaza qiluvchi organlarga taqdim etib boradi;
qonunchilik hujjatlarida belgilangan holatlarda va tartibda qurilishi tugallangan obyektlarni foydalanishga qabul qilish komissiyalari ishida ishtirok etadi;
Qoraqalpog‘iston Respublikasi va viloyatlar hududini rivojlantirishni rejalashtirish sxemalari, aglomeratsiyalarni rivojlantirish strategiyalari, umumdavlat va mahalliy ahamiyatga molik alohida tartibga solinadigan shaharsozlik faoliyati obyektlarining shaharsozlik hujjatlari, aholi punktlarining shaharsozlik hujjatlari ishlab chiqilishi, kelishilishi va tasdiqlanishida ishtirok etadi;
maqsadli dasturlarga kiritilgan qurilish obyektlarining loyiha topshiriqlarini kelishadi;
obyektlarning joylashuvi bo‘yicha qurilish chizig‘ini belgilaydi;
umumdavlat va mahalliy ahamiyatga molik alohida tartibga solinadigan shaharsozlik faoliyati obyektlari uchun chegaralar belgilash haqida takliflar kiritadi;
bosh rejalar mavjud bo‘lmagan hududlarda hamda bosh reja ishlab chiqilayotgan davrda, shuningdek, batafsil rejalashtirish loyihalarida nazarda tutilmagan obyektlar joylashtirilishini hududiy arxitektura-shaharsozlik kengashlarida ko‘rib chiqadi va kelishadi;
o‘z vakolatlari doirasida aholi punktlarining shaharsozlik hujjatlarini ishlab chiqish va tasdiqlashni tashkil qilishda ishtirok etish;
tegishli hududlarda shaharsozlik faoliyati obyektlarini joylashtirish va muhandislik, transport va ijtimoiy infratuzilmalarni rivojlantirish masalalari bo‘yicha takliflar kiritish;
aholi punktlarining shaharsozlik hujjatlari asosida (mavjud bo‘lganda) shaharsozlik faoliyati obyektlari, bino-inshootlar, muhandislik va transport infratuzilmasi obyektlari qurilishi yoki rekonstruksiyasi, shuningdek, hududlarni obodonlashtirishni loyihalashtirish uchun arxitektura-rejalashtirish topshiriqlarini ishlab chiqadi;
aholi punktlarining shaharsozlik hujjatlarining amalga oshirilishi yuzasidan monitoring olib boradi;
davlat shaharsozlik kadastri yuritilishida ishtirok etadi;
loyiha hujjatlarining arxitektura qismini saqlashga doir elektron arxivni yuritadi;
shaharsozlik faoliyati sohasida davlat siyosatining asosiy yo‘nalishlari bo‘yicha takliflar tayyorlashda qatnashadi;
o‘z vakolatiga kiruvchi masalalar bo‘yicha hududiy maqsadli dasturlar, ijtimoiy-iqtisodiy rivojlantirish prognozlarining shaharsozlik bo‘limlarini (qismlarini) ishlab chiqishda va amalga oshirishda qatnashadi;
aholini shaharsozlik faoliyati to‘g‘risida muntazam ravishda xabardor qilib boradi;
shaharsozlik normalari va qoidalarini takomillashtirish yuzasidan takliflar kiritadi;
shaharsozlik faoliyati, shaharsozlik hujjatlarini ishlab chiqish, pudrat ishlari va qurilish materiallari bozorini rivojlantirish, qurilish-montaj ishlariga yangi texnika va zamonaviy texnologiyalarni qo‘llash masalalari bo‘yicha xalqaro ilmiy-texnik va iqtisodiy hamkorlikni amalga oshirishda tegishli hududdagi tashkilotlarga amaliy yordam ko‘rsatadi, xorijiy hamkorlar bilan aloqalar o‘rnatishda ko‘maklashadi;
aholi punktlarining shaharsozlik hujjatlari asosida hududlardagi yerlardan samarali foydalanish, shuningdek, tegishli hududda muhandislik, transportga oid va ijtimoiy infratuzilmani rivojlantirish bo‘yicha takliflar kiritadi;
shaharsozlik faoliyati obyektlarini joylashtirish hamda shahar, shaharcha va qishloq (ovul)ning chegaralarini belgilash to‘g‘risida takliflar kiritadi;
yuqori samarali va energiyani tejaydigan loyiha yechimlari, qurilish materiallari, zamonaviy qurilish texnologiyalari va innovatsion loyihalarni amaliyotga tatbiq etishda ishtirok etadi;
tegishli hududdagi shaharsozlik faoliyati obyektlari bo‘yicha loyihalashtirish ishlarini va kompleks muhandislik qidiruvlari, shuningdek, shaharsozlik hujjatlarini ishlab chiquvchilarning mualliflik nazoratini muvofiqlashtirib boradi;
bosh boshqarma huzuridagi hududiy Arxitektura-shaharsozlik kengashi faoliyatini tashkil etadi;
belgilangan tartibda bino va inshootlar hamda boshqa obyektlar loyihalarining arxitektura qismini kelishib beradi yoki asoslantirgan holda rad etadi;
shaharsozlik hujjatlarini ishlab chiquvchilar va obyektlar qurilishi bo‘yicha qurilish-pudrat tashkilotlarining reyting baholarini aks ettiruvchi ko‘rsatkichlar tizimini joriy etishda ishtirok etadi;
qurilishda islohotlarni chuqurlashtirish, shuningdek, pudrat ishlari bozorini rivojlantirish bo‘yicha takliflarni Vazirlikka kiritadi;
qurilish industriyasi korxonalari to‘g‘risidagi ma’lumotlar bazasi, shuningdek, qurilish materiallari, konstruksiyalari va buyumlari to‘g‘risidagi ma’lumotlarning axborot bazasini shakllantirishda ishtirok etadi;
sohaga kirib kelayotgan zamonaviy texnika va texnologiyalarni inobatga olgan holda qisqa muddatli malaka oshirish kurslarining dasturlarini ishlab chiqish va amaliyotga joriy etishda ishtirok etadi;
sohaga oid faoliyat yuritayotgan mutaxassislar malakasini oshirish va sertifikatlash choralarini ko‘rish yuzasidan takliflar kiritadi;
nazariy bilimlar va amaliy ko‘nikmalarni shakllantirish maqsadida hududlarda amalga oshirilayotgan qurilish obyektlarida ko‘chma mashg‘ulotlar tashkil etishda ko‘maklashadi;
soha mutaxassislarining ishlab chiqarish amaliyotlarini o‘tash bazasi sifatida ularni loyiha va pudrat tashkilotlariga joylashtirishga ko‘maklashadi;
arxitektura va qurilish rivojlanishi holati monitoringini olib boradi, mintaqada kelajakda ularni yanada rivojlantirish bo‘yicha prognozlarni shakllantiradi;
vazirliklar, idoralar va boshqa tashkilotlar hududiy bo‘linmalarining arxitektura va qurilish sohasidagi ishlarini muvofiqlashtiradi;
belgilangan tartibda tasdiqlangan arxitektura va qurilish sohasidagi normativ-huquqiy hujjatlarning ijrosini ta’minlaydi;
arxitektura va qurilish sohasida o‘zini ko‘rsatgan xodimlarni, shuningdek, arxitektura va qurilishni rivojlantirishga ko‘maklashgan boshqa shaxslarni mukofotlash haqida taklif tayyorlaydi va belgilangan tartibda taqdim etadi;
b) uy-joy kommunal xo‘jaligi sohasidagi funksiyalari:
jismoniy va yuridik shaxslarning uy-joy kommunal xizmatlarga bo‘lgan talabini o‘rganadi va o‘rnatilgan tartibda Vazirlikka taqdim qiladi;
iste’molchilarga uy-joy kommunal xizmatlar ko‘rsatish sifat darajasini oshirish va energiya tejaydigan zamonaviy texnologiyalarni joriy qilish yuzasidan takliflar kiritadi;
ko‘p xonadonli uy-joy fondi, ichimlik suvi ta’minoti va oqova suvlarni chiqarib yuborish xizmatlari, issiqlik ta’minoti sohasida qurilish, rekonstruksiya, kapital ta’mirlash va modernizatsiya qilishga doir dasturlarni ishlab chiqishda ishtirok etadi;
hududiy uy-joy kommunal xo‘jaligi tizimiga rahbarlik qiladi, uy-joy kommunal xo‘jaligi obyektlaridan foydalanadigan tashkilotlar faoliyatiga ko‘maklashadi;
ko‘p xonadonli uy-joy fondi, issiqlik ta’minoti, ichimlik suvi ta’minoti va oqova suvlarni chiqarib yuborish tizimlarining texnik-iqtisodiy holati ko‘rsatkichlarini o‘rganadi va monitoring qiladi;
ko‘p kvartirali uylardan va ko‘p kvartirali uylarga tutash yer uchastkalaridan foydalanish hamda ularni saqlash bo‘yicha tizimli ishlarni samarali tashkil qilishda ishtirok etadi;
ko‘p kvartirali uylarni mavsumiy foydalanishga o‘z vaqtida va sifatli tayyorlash ishlarini tashkil qilishda ishtirok etadi;
ko‘p kvartirali uylarni boshqarish organlariga novatorlik faoliyatini va zamonaviy innovatsion texnologiyalarni joriy etishga ko‘maklashish;
ko‘p kvartirali uylarni boshqarish organlari faoliyatining texnik-iqtisodiy ko‘rsatkichlarini tahlil qilib boradi hamda Vazirlikka taqdim qilib boradi;
uy-joy kommunal xo‘jaligi sifatini oshirish, ularning turlarini kengaytirish, ishlab chiqarishning moddiy va energiya iste’molini kamaytirish va bu sohani takomillashtirish yuzasidan takliflar ishlab chiqadi;
mahalliy ijro hokimiyati organlari va fuqarolarning o‘zini o‘zi boshqarish organlari bilan uy-joy mulkdorlari shirkatlarining ko‘p xonadonli uylarning saqlanishini ta’minlash, uy-joy kommunal xo‘jaligi obyektlarining barqaror ishlashi masalalari yuzasidan o‘zaro hamkorligini yo‘lga qo‘yadi;
hudud aholisini uy-joy kommunal xo‘jaligi tashkilotlarining faoliyati to‘g‘risida muntazam xabardor qiladi, jismoniy shaxslar, shu jumladan yakka tartibdagi tadbirkorlar va yuridik shaxslarning bosh boshqarma vakolatiga kiritilgan masalalarga taalluqli murojaatlarini qonunchilikda belgilangan tartibda ko‘rib chiqadi va zarur choralar ko‘radi:
ko‘p xonadonli uy-joy fondini ta’mirlash, obodonlashtirish va undan foydalanish ko‘p xonadonli uylarni boshqarish va boshqaruvni amalga oshiradigan tashkilotlarni nazorat qilish tizimining faoliyat ko‘rsatishi, uy-joy munosabatlari ishtirokchilarining huquq va majburiyatlari to‘g‘risida aholini axborot bilan ta’minlaydi;
ko‘p xonadonli uy-joy fondining turar joylarini yashash uchun yaroqsiz deb tan olish tadbirlarini belgilangan tartibda amalga oshirishda ishtirok etadi hamda ko‘p xonadonli uy-joy fondining avariya holati monitoringini olib boradi;
ko‘p xonadonli uy-joy fondini kuz-qish mavsumida barqaror ishlashga tayyorlash borasida mahalliy ijro hokimiyati organlari, fuqarolarning o‘zini o‘zi boshqarish organlari va xususiy uy-joy mulkdorlari shirkatlari faoliyatiga ko‘maklashadi;
ichimlik suvi ta’minoti va oqova suvlarni chiqarib yuborish sohasidagi dasturlar, strategiyalar hamda konsepsiyalarni ishlab chiqish uchun aniq takliflarni shakllantirish va ularni amalga oshirishda ishtirok etish;
aholining ichimlik suvi ta’minoti va oqova suvlarni chiqarib yuborish bo‘yicha xizmatlar bilan qamrab olinishini monitoring qilish;
aholi punktlarini ichimlik suvi ta’minoti va oqova suvlarni chiqarib yuborish bo‘yicha xizmatlar bilan ta’minlash uchun davlat ijtimoiy buyurtmalari uchun takliflarni shakllantirish;
ichimlik suvi ta’minoti va oqova suvlarni chiqarib yuborish bo‘yicha xizmatlar ko‘rsatilishida xususiy sektorning ishtirokini kengaytirish bo‘yicha choralar ko‘rish, shu jumladan davlat-xususiy sheriklik asosida kengaytirish choralarini ko‘rish;
ichimlik suvi ta’minoti va oqova suvlarni chiqarib yuborish sohasidagi normativ-huquqiy hujjatlar loyihalarini ishlab chiqishda ishtirok etish;
ichimlik suvi ta’minoti va oqova suvlarni chiqarib yuborish sohasidagi tarif siyosatini shakllantirishga ko‘maklashish uchun hududdagi tariflarni iqtisodiy va texnik tomondan tahlil qilish;
ichimlik suvi ta’minoti va oqova suvlarni chiqarib yuborish sohasidagi ilmiy tadqiqotlarni amalga oshirishga ko‘maklashish;
ichimlik suvi ta’minoti va oqova suvlarni chiqarib yuborish sohasida xodimlarga bo‘lgan ehtiyojni tahlil qilish hamda ularni tayyorlash bo‘yicha takliflarni shakllantirish;
ichimlik suvi ta’minoti tashkilotlari faoliyati samaradorligining asosiy mezonlarini bajarilishini baholashda ishtirok etish;
ichimlik suvi va oqova suvlarni tozalash inshootlarini texnologik auditdan o‘tkazish bo‘yicha takliflarni shakllantirish hamda suvni tejash bo‘yicha zarur choralarni ko‘rish;
ichimlik suvi ta’minoti tashkilotlari uchun suvning texnologik jihatdan sarflanishini va yo‘qotilishini yo‘l qo‘yiladigan normalarini (limitlarini) belgilashda ishtirok etish;
ichimlik suvi ta’minoti va oqova suvlarni chiqarib yuborish obyektlarini qurish hamda rekonstruksiya qilishga doir aniq yo‘naltirilgan dasturlarni shakllantirishda ishtirok etish;
davlat ijtimoiy buyurtmasi asosida ichimlik suvi ta’minoti va oqova suvlarni chiqarib yuborish sohasidagi loyihalarning ro‘yobga chiqarilishi monitoringini mahalliy davlat hokimiyati vakillik organlari bilan hamkorlikda amalga oshirish;
issiqlik ta’minoti tizimida, jumladan qayta tiklanadigan energiya manbalaridan foydalanib, energiya va resursni tejaydigan yangi texnologiya va uskunalarni joriy qilish bo‘yicha takliflar ishlab chiqadi;
obyektlarda energiya samaradorligini ta’minlaydigan mahalliy qozonxonalar qurish, shuningdek, xonadonlar ichida alohida issiqlik ta’minoti tizimlarini o‘rnatish orqali markazlashtirilmagan issiqlik ta’minoti tizimini rivojlantirish bo‘yicha takliflarni tayyorlaydi;
jismonan va ma’nan eskirgan, samaradorligi past qozonxona agregatlari, nasos uskunalari, ishdan chiqqan magistral va taqsimlash issiqlik tarmoqlari modernizatsiya, rekonstruksiya qilinishi hamda almashtirilishida qatnashadi;
energiya resurslarini ishlab chiqarish va iste’mol qilishni normativ hisobga olishni yo‘lga qo‘yish, iste’molchilarning issiqlik ta’minoti xizmatlari uchun kelib tushadigan mablag‘lar va to‘lovlarini avtomatlashtirilgan hisobga olish tizimi joriy qilinishini tashkil etadi;
uy-joy kommunal xo‘jaligi sohasi uchun xodimlarni tayyorlash, qayta tayyorlash va ularning malakasini oshirishga ehtiyojni o‘rganadi, uy-joy kommunal xo‘jaligi sohasi uchun xodimlar tayyorlashni, xorijiy mamlakatlarda tajriba orttirish uchun nomzodlarni jalb qilish ishlarini tashkil qiladi;
tegishli davlat organlari bilan hamkorlikda boshqarma va uning tashkiliy tuzilmasidagi tashkilotlarda korrupsiyani oldini olishga qaratilgan ishlarni tashkillashtiradi.'),
        (1389, 136, '2024-05-29 17:30:50', '2024-05-29 17:30:50', '15. Bosh boshqarma qonunchilik hujjatlariga muvofiq boshqa funksiyalarni ham amalga oshirishi mumkin.'),
        (1390, 136, '2024-05-29 17:30:50', '2024-05-29 17:30:50', '16. Toshkent shahar Qurilish bosh boshqarmasining vazifalari va funksiyalari etib quyidagilar hisoblanadi:
hududda Vazirlikka yuklangan vazifalarni samarali bajarish;
o‘z vakolatlari doirasida aholi punktlarining shaharsozlik hujjatlarini ishlab chiqish va tasdiqlashni tashkil qilishda ishtirok etish;
shaharsozlik faoliyati yo‘nalishlari bo‘yicha takliflarni tayyorlash, hududlarni ijtimoiy-iqtisodiy rivojlantirishning tarmoq va hududiy dasturlarini tayyorlashda ishtirok etish;
hududlarda shaharsozlik faoliyati obyektlarini joylashtirish va muhandislik, transport va ijtimoiy infratuzilmalarni rivojlantirish masalalari bo‘yicha takliflar kiritish;
yuqori samarali va energiyani tejaydigan loyiha yechimlari va qurilish materiallari, zamonaviy qurilish texnologiyalari va innovatsiya loyihalarini tatbiq etishda ko‘maklashish;
aholi punktlarining shaharsozlik hujjatlarini ishlab chiquvchilar va mutaxassislarning malakasini doimiy ravishda oshirish va ularni qayta tayyorlashni tashkil qilishda ishtirok etish;
tumanlar hokimlari, prokuratura, ichki ishlar va soliq xizmati hududiy organlari rahbarlari boshqaradigan kompleks ijtimoiy-iqtisodiy rivojlanish sektorlari (keyingi o‘rinlarda kompleks ijtimoiy-iqtisodiy rivojlanish sektorlari deb ataladi) bilan hamkorlik qilish;
shaharsozlik faoliyati sohasida xodimlarni tayyorlash, qayta tayyorlash va malakasini oshirish, mintaqadagi qurilish-pudrat tashkilotlarini yuqori malakali quruvchilar, loyihachilar va boshqa mutaxassislar bilan ta’minlashni tashkil qilishda ishtirok etish.
Normativ-huquqiy hujjatlarda shaharsozlik faoliyati sohasida Toshkent shahar Qurilish bosh boshqarmasiga alohida vazifalar yuklatilishi mumkin.'),
        (1391, 136, '2024-05-29 17:30:50', '2024-05-29 17:30:50', '17. Bosh boshqarma o‘ziga yuklangan asosiy vazifalarga muvofiq quyidagi funksiyalarni amalga oshiradi:
kelishilgan loyiha hujjatlarining fasad, kichik arxitektura shakllari hamda boshqa arxitekturaviy yechimlaridan jismoniy va yuridik shaxslar tomonidan chekinish sodir etilganda mazkur holatlarga nisbatan chora ko‘rish uchun to‘plangan materiallarni vazirlikning tegishli bo‘linmasiga, mahalliy ijro hokimiyati organlari hamda huquqni muhofaza qiluvchi organlarga taqdim etib boradi;
qonunchilik hujjatlarida belgilangan holatlarda va tartibda qurilishi tugallangan obyektlarni foydalanishga qabul qilish komissiyalari ishida ishtirok etadi;
aholi punktlarining bosh rejalari, batafsil rejalashtirish loyihalari hamda boshqa shaharsozlik hujjatlari ishlab chiqilishi, kelishilishi va tasdiqlanishida ishtirok etadi;
maqsadli dasturlarga kiritilgan qurilish obyektlarining loyiha topshiriqlarini kelishadi;
obyektlarning joylashuvi bo‘yicha qurilish chizig‘ini belgilaydi;
umumdavlat va mahalliy ahamiyatga molik alohida tartibga solinadigan shaharsozlik faoliyati obyektlari uchun chegaralar belgilash haqida takliflar kiritadi;
bosh rejalar mavjud bo‘lmagan hududlarda hamda bosh reja ishlab chiqilayotgan davrda, shuningdek, batafsil rejalashtirish loyihalarida nazarda tutilmagan obyektlar joylashtirilishini hududiy arxitektura-shaharsozlik kengashida ko‘rib chiqadi va kelishadi;
aholi punktlarining shaharsozlik hujjatlari asosida (mavjud bo‘lganda) shaharsozlik faoliyati obyektlari, bino-inshootlar, muhandislik va transport infratuzilmasi obyektlari qurilishi yoki rekonstruksiyasi, shuningdek, hududlarni obodonlashtirishni loyihalashtirish uchun arxitektura-rejalashtirish topshiriqlarini ishlab chiqadi;
aholi punktlarining shaharsozlik hujjatlarining amalga oshirilishi yuzasidan monitoring olib boradi;
davlat shaharsozlik kadastri yuritilishida ishtirok etadi;
loyiha hujjatlarining arxitektura qismini saqlashga doir elektron arxivni yuritadi;
